import{_ as p}from"./_plugin-vue_export-helper.cdc0426e.js";import{o,c as t,f as r}from"./app.cb44c984.js";const n={},s=r('<h1 id="\u5218\u6BC5" tabindex="-1"><a class="header-anchor" href="#\u5218\u6BC5" aria-hidden="true">#</a> \u5218\u6BC5</h1><blockquote><p>\u5B81\u8FDC\u5C06\u519B\u80E1\u7C53\u8A00\u4E8E\u88D5\u66F0\uFF1A\u201C\u516C\u8C13\u5218\u536B\u519B\u7EC8\u80FD\u4E3A\u516C\u4E0B\u4E4E\uFF1F\u201D\uFF08\u5218\uFF09\u88D5\u9ED8\u7136\u3002</p></blockquote><p><strong>\u300A\u8D44\u6CBB\u901A\u9274\xB7\u5377\u4E00\u4E00\u516D\xB7\u664B\u7EAA\u4E09\u5341\u516B\u300B</strong></p><blockquote><p><strong>\u5218\u6BC5</strong></p></blockquote><p>\u66FE\u7ECF\u7684\u597D\u5144\u5F1F\u597D\u6218\u53CB\u4E3A\u4EC0\u4E48\u6700\u540E\u4F1A\u53CD\u76EE\u6210\u4EC7\uFF1F\u8FD9\u4E2A\u95EE\u9898\u5982\u679C\u6709\u6807\u51C6\u7B54\u6848\u7684\u8BDD\uFF0C\u90A3\u4E48\u5218\u6BC5\u7684\u6545\u4E8B\u5E94\u8BE5\u5C31\u662F\u5BF9\u8FD9\u4E2A\u7B54\u6848\u7684\u771F\u5B9E\u5199\u7167\u4E86\u3002</p><p><strong>\u4E00\u5207\uFF0C\u4ECE\u5218\u6BC5\u7684\u51FA\u8EAB\u8BF4\u8D77\u3002</strong></p><p>\u5F88\u591A\u5E74\u540E\uFF0C<code>\u6853\u7384</code>\u7BE1\u4F4D\uFF0C<code>\u5218\u88D5</code>\u8D77\u5175\u8BA8\u4F10\u3002\u542C\u5230\u8FD9\u4E2A\u6D88\u606F\uFF0C\u6853\u7384\u76F8\u5F53\u60F6\u6050\uFF0C\u867D\u7136\u6709\u4EBA\u4EE5\u5218\u88D5\u65E0\u6CD5\u6210\u4E8B\u6765\u5B89\u6170\u6853\u7384\uFF0C\u4F46\u6853\u7384\u8BF4\uFF1A</p><blockquote><p>\u5218\u88D5\u662F\u8FD9\u4E16\u95F4\u7684\u67AD\u96C4\uFF01\u4ED6\u7684\u624B\u4E0B\u4E2D\uFF0C<strong>\u5218\u6BC5\u5BB6\u65E0\u79EF\u84C4\uFF0C\u8DDF\u4EBA\u8D4C\u94B1\u5374\u6562\u4E00\u63B7\u767E\u4E07</strong>\uFF1B\u4F55\u65E0\u5FCC\uFF0C\u53C8\u4E0E\u4ED6\u7684\u8205\u8205\u5218\u7262\u4E4B\u7C7B\u4F3C\u3002\u8FD9\u4E9B\u4EBA\u90FD\u80FD\u88AB\u5218\u88D5\u6536\u4E3A\u4E0B\u5C5E\uFF0C\u5218\u88D5\u600E\u4E48\u53EF\u80FD\u4E00\u4E8B\u65E0\u6210\uFF01</p></blockquote><p>\u5982\u679C\u4F60\u8FD8\u8BB0\u5F97\u5218\u88D5\u65E9\u5E74\u6CA1\u6709\u53D1\u8FF9\u65F6\uFF0C\u4E5F\u6709\u8FD9\u4E48\u4E00\u4E2A\u559C\u6B22\u8D4C\u94B1\u7684\u7231\u597D\uFF0C\u90A3\u4E48\u5927\u7EA6\u5C31\u80FD\u77E5\u9053\u5218\u6BC5\u4E3A\u4EC0\u4E48\u8DDF\u5218\u88D5\u610F\u6C14\u76F8\u6295\u4E86\u3002</p><p>\u4E24\u4EBA\u6709\u7740\u5171\u540C\u7684\u7231\u597D\uFF0C\u6216\u8005\u8BF4\uFF0C\u5C31\u662F\u201C<code>\u8D4C\u53CB</code>\u201D\u3002</p><p><strong>\u4E4B\u540E\uFF0C\u5728\u5218\u88D5\u8D77\u5175\u53CD\u6297\u6853\u7384\u7684\u8FC7\u7A0B\u4E2D\uFF0C\u5218\u6BC5\u662F\u6700\u4E3A\u91CD\u8981\u7684<code>\u76DF\u53CB</code>\u4E4B\u4E00\u3002</strong></p><p>\u5F53\u521D\u6853\u6C0F\u51E0\u4E4E\u5DF2\u7ECF\u5360\u636E\u4E86\u4E1C\u664B\u671D\u91CE\u6240\u6709\u91CD\u8981\u7684\u804C\u4F4D\uFF0C\u800C\u79BB\u9996\u90FD\u5EFA\u5EB7\u6700\u4E3A\u91CD\u8981\u7684\u4E24\u652F\u519B\u4E8B\u529B\u91CF\uFF0C\u5206\u522B\u9A7B\u624E\u5728\u4EAC\u53E3\uFF08\u4ECA\u6C5F\u82CF\u9547\u6C5F\uFF09\u4E0E\u5E7F\u9675\uFF08\u4ECA\u6C5F\u82CF\u626C\u5DDE\uFF09\u3002\u5176\u4E2D\uFF0C\u5218\u88D5\u81EA\u5DF1\u5728\u4EAC\u53E3\uFF0C\u5E7F\u9675\u5219\u4EA4\u7ED9\u5218\u6BC5\u3002</p><p>\u5218\u6BC5\u4E0D\u8FB1\u4F7F\u547D\uFF0C\u987A\u5229\u5B8C\u6210\u4EFB\u52A1\uFF0C\u63A7\u5236\u4E86\u5E7F\u9675\u7684\u519B\u961F\uFF0C\u5E76\u968F\u5373\u6E21\u6C5F\u5357\u4E0B\uFF0C\u4E0E\u5218\u88D5\u5175\u5408\u4E00\u5904\uFF0C\u5171\u540C\u653B\u6253\u5EFA\u5EB7\uFF0C\u8D76\u8D70\u6853\u7384\u3002</p><p>\u6B64\u5F79\u5E76\u672A\u51FB\u6740\u6853\u7384\uFF0C\u56E0\u4E3A\u6853\u7384\u5728\u5175\u8D25\u4E4B\u524D\u5DF2\u7ECF\u5F03\u57CE\u800C\u8D70\uFF0C\u800C\u4E14\u4E34\u884C\u524D\uFF0C\u8FD8\u628A\u900A\u4F4D\u7684\u664B\u5B89\u5E1D\u4E5F\u4E00\u8D77\u5E26\u8D70\u4E86\u3002\u4E49\u519B\u8FDB\u5165\u5EFA\u5EB7\u540E\uFF0C\u5218\u88D5\u8981\u7559\u5728\u57CE\u4E2D\u4E3B\u6301\u5927\u5C40\uFF0C\u8FFD\u51FB\u6853\u7384\u7684\u91CD\u4EFB\u81EA\u7136\u5C31\u843D\u5728\u4E86\u5218\u6BC5\u8EAB\u4E0A\u3002</p><p>\u6700\u7EC8\uFF0C\u664B\u5B89\u5E1D\u88AB\u5218\u6BC5\u6210\u529F\u8425\u6551\u56DE\u6765\u3002</p><blockquote><p><strong>\u8FD9\u5F53\u4E2D\u51FA\u4E86\u70B9\u5C0F\u63D2\u66F2</strong>\uFF1A</p></blockquote><p>\u6853\u7384\u9000\u5230\u6C5F\u9675\uFF0C\u5218\u6BC5\u4E00\u8DEF\u8FFD\u51FB\u81F3\u6B64\uFF0C\u6210\u529F\u51FB\u8D25\u6853\u7384\u3001\u89E3\u6551\u664B\u5B89\u5E1D\u3002\u4E0D\u4E45\u540E\uFF0C\u6853\u7384\u88AB\u6740\uFF0C\u5218\u6BC5\u4EE5\u4E3A\u5927\u5C40\u5DF2\u5B9A\uFF0C\u4FBF\u653E\u677E\u4E86\u8B66\u60D5\uFF0C\u4F46\u4E0D\u66FE\u60F3\u6853\u6C0F\u52BF\u529B\u4ECD\u5728\uFF0C\u4ED6\u4EEC\u8D81\u7740\u5218\u6BC5\u653E\u677E\u9632\u5907\uFF0C\u7ADF\u7136\u518D\u6B21\u653B\u5165\u6C5F\u9675\u57CE\uFF0C\u91CD\u65B0\u63B3\u8D70\u4E86\u664B\u5B89\u5E1D\u3002</p><p>\u5218\u6BC5\u56E0\u6B64\u88AB\u77ED\u6682\u514D\u5B98\uFF0C\u4F46\u4ED6\u6700\u7EC8\u8FD8\u662F\u5C06\u664B\u5B89\u5E1D\u6551\u4E86\u51FA\u6765\u3002</p><p>\u4E8B\u540E\uFF0C\u4E1C\u664B\u671D\u5EF7\u8BBA\u529F\u884C\u8D4F\uFF0C\u5218\u6BC5\u83B7\u5C01\u516C\u7235\u3002</p><p>\u7531\u4E8E\u6DF1\u5F97\u5218\u88D5\u4FE1\u4EFB\uFF0C\u5728\u6B64\u540E\u7684\u51E0\u5E74\u95F4\uFF0C\u5218\u6BC5\u4E00\u76F4\u90FD\u5E26\u5175\u9547\u5B88\u5730\u65B9\uFF0C\u5E76\u4E14\u6709\u524A\u5E73\u53DB\u4E71\u7684\u8BB0\u5F55\u3002</p><p>\u76F4\u5230<code>\u5362\u5FAA</code>\u8D77\u5175\u4F5C\u4E71\u3002</p><p>\u5362\u5FAA\u6B64\u6B21\u4F5C\u4E71\u5175\u52BF\u6D69\u5927\uFF0C\u751A\u81F3\u51FB\u6740\u4E86<code>\u4F55\u65E0\u5FCC</code>\u2014\u2014\u4F55\u65E0\u5FCC\u662F\u4E0E\u5218\u88D5\u3001\u5218\u6BC5\u540C\u65F6\u8D77\u4E8B\u7684\u5C06\u9886\u3002</p><p>\u9762\u5BF9\u7740\u5982\u6B64\u5371\u6025\u5F62\u52BF\uFF0C\u4E0D\u8BBA\u4ECE\u8EAB\u4EFD\u6216\u8005\u5730\u4F4D\u6765\u8BBA\uFF0C\u5218\u6BC5\u90FD\u5E94\u8BE5\u65E0\u53EF\u63A8\u5378\u5730\u62C5\u8D1F\u8D77\u5E73\u53DB\u7684\u8D23\u4EFB\u3002\u4F46\u53EF\u60DC\u7684\u662F\uFF0C\u8FD9\u6B21\u4ED6\u5374\u6CA1\u6709\u4E4B\u524D\u7684\u597D\u8FD0\u6C14\u4E86\uFF0C\u5927\u519B\u540C\u6837\u8D25\u4E8E\u5362\u5FAA\u3002</p><p><strong>\u5218\u88D5\u65E0\u6CD5\uFF0C\u53EA\u5F97\u81EA\u5DF1\u4EB2\u81EA\u51FA\u5F81\uFF0C\u6362\u5218\u6BC5\u56DE\u671D\u5EF7\u4E3B\u6301\u653F\u4E8B</strong>\u3002</p><p>\u6B64\u540E\uFF0C\u5218\u88D5\u51FB\u8D25\u5362\u5FAA\uFF0C\u7EE7\u7EED\u56DE\u5230\u671D\u4E2D\u4E3B\u6301\u653F\u4E8B\u3002\u4ED6\u867D\u7136\u5C06\u5218\u6BC5\u964D\u4E86\u804C\uFF0C\u4F46\u5E76\u672A\u6DF1\u7A76\u5176\u5931\u8D25\u7684\u8D23\u4EFB\uFF0C\u800C\u4F9D\u65E7\u8BA9\u4ED6\u9547\u5B88\u5730\u65B9\u3002</p><p><strong>\u53EF\u662F\uFF0C\u5218\u6BC5\u6B64\u65F6\u7684\u5FC3\u6001\u65E9\u5DF2\u5927\u53D8</strong>\u3002</p><p>\u8FFD\u51FB\u6853\u7384\u3001\u8FCE\u56DE\u664B\u5B89\u5E1D\uFF0C\u5218\u88D5\u6D3E\u5218\u6BC5\u4E3B\u6301\uFF1B\u5218\u6BC5\u4E0E\u5362\u5FAA\u4F5C\u6218\u5931\u8D25\uFF0C\u5218\u88D5\u4EB2\u81EA\u6362\u56DE\u5218\u6BC5\uFF0C\u5E76\u8BA9\u5218\u6BC5\u4EE3\u66FF\u4ED6\u5728\u671D\u4E2D\u4E3B\u4E8B\u2026\u2026\u5728\u8FD9\u4E00\u7CFB\u5217\u7684\u5B89\u6392\u4E2D\uFF0C\u53EF\u4EE5\u6E05\u6670\u770B\u51FA\uFF0C<strong>\u5218\u88D5\u5BF9\u4E8E\u5218\u6BC5\u76F8\u5F53\u4FE1\u4EFB\uFF0C\u8FD9\u4E0D\u4EC5\u4EC5\u662F\u5BF9\u5218\u6BC5\u529F\u52B3\u7684\u5956\u52B1\uFF0C\u66F4\u662F\u628A\u5218\u6BC5\u5F53\u6210\u81EA\u5DF1\u7684<code>\u526F\u624B</code>\u6765\u770B\u5F85\u3002</strong></p><p>\u4F46\u5218\u6BC5\u7684<code>\u81EA\u6211\u5B9A\u4F4D</code>\u5374\u5E76\u975E\u5982\u6B64\u3002<strong>\u4ED6\u5E76\u4E0D\u8BA4\u53EF\u8FD9\u79CD\u5173\u7CFB\uFF0C\u53CD\u800C\u8BA4\u4E3A\u81EA\u5DF1\u4E0E\u5218\u88D5\u5E94\u8BE5\u662F<code>\u5E76\u9A7E\u9F50\u9A71</code>\u7684\u3002</strong></p><p>\u4E5F\u5C31\u662F\u8BF4\uFF0C\u5218\u6BC5\u5E76\u4E0D\u8BA4\u4E3A\u81EA\u5DF1\u548C\u5218\u88D5\u662F\u4E00\u4E3B\u4E00\u526F\u7684\uFF0C\u800C\u5E94\u8BE5\u662F\u201C\u53CC\u5BE1\u5934\u6A21\u5F0F\u201D\u3002</p><p>\u4ED6\u8FD9\u4E48\u60F3\uFF0C\u5176\u5B9E\u4E5F\u6709\u4ED6\u81EA\u5DF1\u7684\u9053\u7406\u3002\u9996\u5148\uFF0C\u5F53\u5E74\u8D77\u4E8B\u65F6\uFF0C\u5218\u88D5\u5360\u636E\u4EAC\u53E3\uFF0C\u4ED6\u5360\u636E\u5E7F\u9675\uFF0C\u4E24\u4EBA\u529F\u4E1A\u76F8\u5F53\u3002\u5176\u6B21\uFF0C\u5218\u88D5\u540E\u6765\u6536\u590D\u5EFA\u5EB7\uFF0C\u4ED6\u4E5F\u8FCE\u56DE\u4E86\u664B\u5B89\u5E1D\uFF0C\u8FD9\u53C8\u662F\u5DEE\u4E0D\u591A\u7684\u529F\u52B3\u3002</p><p>\u53EF\u5982\u4ECA\u5462\uFF1F</p><p>\u5728\u5218\u88D5\u9762\u524D\uFF0C\u5218\u6BC5\u603B\u5F52\u77EE\u7740\u4E00\u5934\uFF0C\u8FD9\u81EA\u7136\u4EE4\u4ED6\u6068\u6068\u4E0D\u5DF2\u3002</p><p>\u800C\u4E14\uFF0C\u5218\u6BC5\u5E76\u4E0D\u6EE1\u610F\u5218\u88D5\u5BF9\u4ED6\u7684\u5B89\u6392\u3002</p><p>\u5218\u88D5\u628A\u6301\u671D\u653F\u4EE5\u540E\uFF0C\u5218\u6BC5\u51E0\u4E4E\u4E00\u76F4\u90FD\u5728\u5730\u65B9\u9547\u5B88\uFF0C\u8FD9\u5F88\u660E\u663E\u662F\u5218\u88D5\u51FA\u4E8E\u4FE1\u4EFB\u548C\u5B89\u5168\u7684\u8003\u8651\u3002</p><p><strong>\u4F46\u662F\uFF0C\u5218\u6BC5\u7684\u76EE\u6807\u5374\u662F\u5728\u671D\u4E2D\u4E3A\u5B98\u3002</strong></p><p>\u7531\u4E8E\u4E24\u4EBA\u5730\u4F4D\u4F7F\u7136\uFF0C\u5218\u6BC5\u53EA\u80FD\u63A5\u53D7\u5B89\u6392\u3002\u4F46\u4E0D\u7BA1\u5728\u79C1\u4E0B\u91CC\u8FD8\u662F\u5728\u516C\u5F00\u573A\u5408\uFF0C\u5218\u6BC5\u90FD\u5E76\u4E0D\u63A9\u9970\u4ED6\u5BF9\u5218\u88D5\u7684\u8FD9\u79CD\u4E0D\u6EE1\u3002</p><p>\u5362\u5FAA\u88AB\u51FB\u8D25\u540E\uFF0C\u5218\u6BC5\u83B7\u4EFB\u6C5F\u5DDE\u90FD\u7763\uFF0C\u4E0D\u4E45\u8F6C\u4EFB\u8346\u5DDE\u3002\u8FD9\u672C\u662F\u4E2A\u6B63\u5E38\u7684\u8C03\u52A8\uFF0C\u4F46\u5218\u6BC5\u786C\u751F\u751F\u628A\u4E8B\u60C5\u641E\u5F97\u8BA9\u4EBA\u7591\u5FC3\u56DB\u8D77\uFF1A<strong>\u4ED6\u5728\u8D74\u4EFB\u65F6\uFF0C\u7ADF\u7136\u5C06\u6C5F\u5DDE\u7684\u58EB\u5352\u4E00\u4E07\u591A\u4EBA\u5E26\u5230\u4E86\u8346\u5DDE\uFF0C\u5B9B\u82E5\u79C1\u5175\u3002</strong></p><p>\u800C\u4E14\u5230\u4E86\u8346\u5DDE\u4EE5\u540E\uFF0C\u5218\u6BC5\u4F9D\u7136\u4E0D\u6D88\u505C\uFF0C\u53C8\u4E0A\u4E66\u8BF7\u6C42\u671D\u5EF7\uFF0C\u5C06\u4EA4\u5DDE\u4E0E\u5E7F\u5DDE\u4E5F\u5F52\u7531\u4ED6\u7BA1\u8F96\u2014\u2014\u5218\u88D5\u4F9D\u7136\u540C\u610F\u4E86\u8FD9\u79CD\u8BF7\u6C42\u3002</p><p><strong>\u5230\u540E\u6765\uFF0C\u4E24\u4EBA\u7684\u4E0D\u5BF9\u4ED8\uFF0C\u7EC8\u4E8E\u9010\u6E10\u53D1\u5C55\u5230\u5F53\u9762\u90FD\u4E0D\u7559\u60C5\u9762\u7684\u5730\u6B65\u3002</strong></p><p>\u67D0\u6B21\uFF0C\u5218\u6BC5\u56DE\u4EAC\uFF0C\u53BB\u5218\u88D5\u7684\u4E1E\u76F8\u5E9C\u4F5C\u5BA2\u3002\u5F53\u5E74\u7684\u7A77\u5144\u5F1F\uFF0C\u5982\u4ECA\u90FD\u6210\u4E86\u6743\u503E\u5929\u4E0B\u7684\u4EBA\u7269\uFF0C\u5F7C\u6B64\u4E4B\u95F4\uFF0C\u9887\u611F\u604D\u7136\u3002</p><p>\u6240\u5E78\u6614\u65E5\u60C5\u8C0A\u5C1A\u5728\uFF0C\u4E0D\u77E5\u662F\u8C01\u63D0\u8BAE\uFF0C\u8FD9\u4E9B\u671D\u4E2D\u5927\u4F6C\u4EEC\u7ADF\u7136\u5728\u5218\u88D5\u7684\u4E1E\u76F8\u5E9C\u91CC\u5F00\u59CB\u63B7\u9AB0\u5B50\u8D4C\u535A\u3002</p><p>\u6C14\u6C1B\u610F\u60F3\u4E0D\u5230\u7684\u597D\uFF0C\u8D4C\u6CE8\u4E5F\u8D8A\u6765\u8D8A\u5927\uFF0C\u8FBE\u5230\u4E0A\u767E\u4E07\u94B1\uFF0C\u5176\u4ED6\u4EBA\u4E8E\u662F\u7EB7\u7EB7\u9000\u51FA\uFF0C\u8D4C\u684C\u4E4B\u4E0A\uFF0C\u53EA\u7559\u4E0B\u4E86\u5218\u6BC5\u4E0E\u5218\u88D5\u3002</p><p>\u5F53\u65F6\u7684\u9AB0\u5B50\u53EB\u6A17\u84B2\uFF08\u97F3\u201C\u521D\u8461\u201D\uFF09\uFF0C\u4E94\u679A\u540C\u63B7\uFF0C\u5168\u9ED1\u4E3A\u6700\u9AD8\u5F69\uFF0C\u79F0\u201C\u5362\u201D\uFF1B\u56DB\u9ED1\u4E00\u767D\u6B21\u4E4B\uFF0C\u79F0\u201C\u96C9\u201D\u3002\u5218\u6BC5\u5148\u63B7 \uFF0C\u5F97\u201C\u96C9\u201D\uFF0C\u5927\u559C\uFF0C\u81EA\u4EE5\u4E3A\u5F97\u80DC\uFF0C\u4FBF\u8DDF\u65C1\u4EBA\u8BF4\uFF0C\u6211\u4E0D\u662F\u4E0D\u60F3\u63B7\u51FA\u201C\u5362\u201D\uFF0C\u53EA\u4E0D\u8FC7\u4E0D\u613F\u610F\u7F62\u4E86\u3002</p><p>\u7136\u540E\u8F6E\u5230\u5218\u88D5\u3002</p><p>\u5218\u88D5\u5728\u5FC3\u4E2D\u76F8\u5F53\u9119\u5937\u5218\u6BC5\u7684\u4E0D\u5BA2\u6C14\uFF0C\u4ED6\u62FF\u8D77\u6A17\u84B2\u540E\u8BF4\uFF0C\u90A3\u5C31\u8BA9\u8001\u5144\u6211\u6765\u8BD5\u8BD5\u5427\u3002</p><p>\u4E00\u63B7\uFF0C\u51FA\u201C\u5362\u201D\u3002</p><p>\u5218\u6BC5\u7684\u8138\u987F\u65F6\u5C31\u57AE\u4E86\u4E0B\u6765\uFF0C\u4ED6\u5F53\u7740\u4F17\u4EBA\u7684\u9762\u5BF9\u5218\u88D5\u8BF4\uFF0C\u6211\u65E9\u5C31\u77E5\u9053\u4F60\u4E0D\u4F1A\u76F8\u8BA9\u4E8E\u6211\uFF01\u8A00\u7F62\uFF0C\u544A\u8F9E\u800C\u53BB\u3002</p><p>\u6700\u540E\uFF0C\u4E24\u4EBA\u7EC8\u4E8E\u5175\u620E\u76F8\u89C1\u3002</p><p>\u5218\u6BC5\u81EA\u4ECE\u4E0A\u6B21\u8D25\u4E8E\u5362\u5FAA\u4EE5\u540E\uFF0C\u5DF2\u7ECF\u77E5\u9053\u9760\u519B\u529F\u4E0E\u5218\u88D5\u5206\u5EAD\u6297\u793C\u662F\u4E0D\u53EF\u80FD\u7684\uFF0C<strong>\u56E0\u6B64\u8F6C\u800C\u7ED3\u4EA4\u671D\u58EB</strong>\u3002</p><p>\u5728\u8FD9\u65B9\u9762\u4E0A\uFF0C\u4ED6\u7684\u4F18\u52BF\u5C31\u5F3A\u592A\u591A\u4E86\uFF1A\u5218\u88D5\u672C\u6765\u5C31\u6CA1\u6709\u4EC0\u4E48\u5B66\u95EE\uFF0C\u800C\u4E14\u4E5F\u4E0D\u597D\u5B66\u3002\u76F8\u6BD4\u8D77\u6765\uFF0C\u5112\u96C5\u597D\u5B66\u7684\u5218\u6BC5\u66F4\u52A0\u80FD\u88AB\u5EFA\u5EB7\u671D\u4E2D\u7684\u58EB\u5927\u592B\u6240\u8BA4\u53EF\u3002</p><p>\u4E00\u6765\u4E8C\u53BB\u4E4B\u95F4\uFF0C\u53CC\u65B9\u52FE\u8FDE\u751A\u5BC6\uFF0C\u7EC8\u4E8E\u5F15\u8D77\u4E86\u5218\u88D5\u7684\u6000\u7591\u4E0E\u8B66\u60D5\u3002</p><p>\u5218\u6BC5\u7ED3\u4EA4\u671D\u58EB\uFF0C\u76EE\u7684\u81EA\u4E0D\u5F85\u8A00\uFF1B<strong>\u4F46\u671D\u5EF7\u4E2D\u9AD8\u95E8\u5927\u65CF\u7684\u58EB\u5B50\u4E5F\u4E3B\u52A8\u7ED3\u4EA4\u5218\u6BC5\uFF0C\u8FD9\u5C31\u6709\u70B9\u4E0D\u90A3\u4E48\u7EAF\u7CB9\u4E86</strong>\u3002\u5C24\u5176\u662F\u5EFA\u5EB7\u671D\u5EF7\u59CB\u7EC8\u90FD\u60F3\u628A\u5218\u88D5\u8D76\u8D70\uFF0C\u53EA\u4E0D\u8FC7\u4E00\u76F4\u6CA1\u6709\u6210\u529F\u3002\u5982\u4ECA\u8FD9\u5E2E\u671D\u81E3\u5982\u6B64\u52FE\u7ED3\u5218\u6BC5\uFF0C\u76EE\u7684\u4F55\u5728\uFF0C\u4E00\u76EE\u4E86\u7136\u3002</p><p>\u5218\u88D5\u518D\u4E5F\u65E0\u6CD5\u89C6\u82E5\u65E0\u7779\u3002</p><p>\u67D0\u6B21\uFF0C\u5218\u6BC5\u56DE\u4EAC\u53E3\u626B\u5893\uFF0C\u5218\u88D5\u4E0E\u4E4B\u76F8\u89C1\u3002\u4E24\u4EBA\u4F1A\u9762\u540E\uFF0C\u6709\u4EBA\u5BF9\u5218\u88D5\u8BF4\uFF0C\u60A8\u89C9\u5F97\u536B\u5C06\u519B\uFF08\u5218\u6BC5\u65F6\u4EFB\u536B\u5C06\u519B\uFF09\u80FD\u4E45\u5C45\u60A8\u4E4B\u4E0B\u5417\uFF1F</p><p>\u5218\u88D5\u9ED8\u7136\uFF0C\u8FC7\u4E86\u5F88\u4E45\uFF0C\u53EA\u662F\u95EE\u5BF9\u65B9\u60F3\u6CD5\u3002</p><p>\u5BF9\u65B9\u8BF4\uFF0C\u653B\u57CE\u7565\u5730\uFF0C\u653B\u5FC5\u80DC\u6218\u5FC5\u53D6\uFF0C\u8FD9\u662F\u5218\u6BC5\u4E0D\u5982\u60A8\u7684\u5730\u65B9\u3002\u4F46\u81F3\u4E8E\u6D89\u53CA\u5230\u7ECF\u53F2\u5B50\u96C6\uFF0C\u4E0E\u540D\u58EB\u4EEC\u8BD7\u6B4C\u5531\u548C\uFF0C\u5218\u6BC5\u4E5F\u7B97\u662F\u91CC\u624B\u884C\u5BB6\uFF0C\u5EFA\u5EB7\u671D\u5EF7\u91CC\u7684\u90A3\u4E9B\u767D\u9762\u7F19\u7EC5\u65E0\u4E0D\u5F52\u5FC3\u4E0E\u4ED6\u3002\u53EA\u6015\u4ED6\u7EC8\u7A76\u4E0D\u80AF\u5728\u60A8\u4E4B\u4E0B\uFF0C\u60A8\u8FD8\u662F\u8981\u65E9\u505A\u6253\u7B97\u554A\u3002</p><p>\u4E0D\u4E45\u540E\uFF0C\u5218\u6BC5\u75C5\u91CD\u3002\u58EB\u65CF\u4EEC\u62C5\u5FC3\u5218\u6BC5\u4E00\u65E6\u75C5\u901D\uFF0C\u65E0\u4EBA\u80FD\u6297\u8861\u5218\u88D5\uFF0C\u4FBF\u9F13\u52A8\u5218\u6BC5\u4E0A\u4E66\u671D\u5EF7\uFF0C\u4EFB\u547D\u5218\u6BC5\u4E4B\u5F1F\u5218\u7C53\u4E3A\u526F\u624B\u3002</p><p>\u5218\u88D5\u5047\u610F\u540C\u610F\uFF0C\u547D\u5218\u7C53\u5165\u671D\u53D7\u547D\uFF0C\u4F46\u5374\u8D81\u673A\u6263\u62BC\u5218\u7C53\uFF0C\u540C\u65F6\u4E0B\u8BCF\u7533\u65A5\u5218\u6BC5\u3002\u4E4B\u540E\uFF0C\u5218\u88D5\u53C8\u4EB2\u81EA\u5E26\u5175\u6EAF\u6D41\u800C\u4E0A\uFF0C\u653B\u6253\u5218\u6BC5\u3002</p><p>\u5218\u6BC5\u6700\u7EC8\u5175\u8D25\u81EA\u6740\u3002</p><p>\u4ECE\u5218\u6BC5\u7684\u4EBA\u751F\u8F68\u8FF9\u6765\u770B\uFF0C\u4ED6\u4EBA\u751F\u7684\u540E\u534A\u6BB5\u51E0\u4E4E\u90FD\u4E0E\u5218\u88D5\u8054\u7CFB\u5728\u4E86\u4E00\u8D77\uFF0C\u5305\u62EC\u4ED6\u7684\u6700\u7EC8\u88AB\u6740\uFF0C\u4E5F\u4E0E\u4ED6\u8DDF\u5218\u88D5\u76F8\u5904\u4E2D\u7684\u95EE\u9898\u6709\u7740\u76F4\u63A5\u5173\u7CFB\u3002</p><p>\u5982\u679C\u6211\u4EEC\u4ECE\u4E00\u4E2A\u6BD4\u8F83\u6D45\u663E\u7684\u89C6\u89D2\u6765\u770B\u5F85\uFF0C\u90A3\u4E48\u5218\u6BC5\u53EF\u4EE5\u88AB\u8BA4\u4E3A\u662F \u201C\u5378\u78E8\u6740\u9A74\u201D\u4E86\u2014\u2014\u8054\u7CFB\u4E0B\u4E00\u7BC7\u6211\u4EEC\u5C06\u8981\u63D0\u5230\u7684\u738B\u9547\u6076\uFF0C\u5218\u88D5\u7684\u8FD9\u4E9B\u4E3E\u52A8\u4F3C\u4E4E\u5C31\u662F\u5728\u201C\u8BDB\u6740\u529F\u81E3\u201D\u3002</p><p>\u4F46\u4E8B\u5B9E\u5374\u5E76\u975E\u5982\u6B64\u3002</p><p>\u53EA\u8981\u7A0D\u4E00\u6DF1\u7A76\u4E24\u4EBA\u7684\u76F8\u5904\uFF0C\u4F60\u5C31\u4F1A\u53D1\u73B0\uFF0C<strong>\u6B63\u662F\u56E0\u4E3A\u5218\u6BC5\u5728\u4E0E\u5218\u88D5\u7684\u76F8\u5904\u4E2D\u5904\u7F6E\u5931\u5F53\uFF0C\u624D\u4F1A\u6700\u7EC8\u9020\u6210\u81EA\u5DF1\u88AB\u6740\u7684\u7ED3\u5C40\u3002</strong></p><p>\u4E24\u4EBA\u5F53\u5E74\u80FD\u8D70\u5230\u4E00\u8D77\uFF0C\u662F\u56E0\u4E3A\u6027\u60C5\u76F8\u6295\uFF0C\u76EE\u7684\u76F8\u540C\uFF0C\u6709\u7740\u5171\u540C\u7684\u654C\u4EBA\u2014\u2014\u6853\u7384\u3002\u8FD9\u79CD\u65F6\u5019\uFF0C\u81EA\u7136\u80FD\u52E0\u529B\u540C\u5FC3\uFF0C\u5171\u8C0B\u5927\u8BA1\u3002</p><p>\u4F46\u662F\uFF0C\u5F53\u5171\u540C\u7684\u654C\u4EBA\u6D88\u5931\u4EE5\u540E\uFF0C\u4E24\u4EBA\u4E4B\u95F4\u7684\u5206\u6B67\u5C31\u5F00\u59CB\u9010\u6E10\u6210\u4E3A\u5F71\u54CD\u5F7C\u6B64\u5173\u7CFB\u7684\u4E3B\u8981\u56E0\u7D20\u3002</p><p>\u5218\u6BC5\u8BA4\u4E3A\u81EA\u5DF1\u4E0E\u5218\u88D5\u4E4B\u95F4\uFF0C\u4E0D\u7BA1\u662F\u5728\u5730\u4F4D\u4E0A\uFF0C\u8FD8\u662F\u5728\u80FD\u529B\u4E0A\uFF0C\u90FD\u662F\u5E73\u5206\u79CB\u8272\u7684\u3002<strong>\u57FA\u4E8E\u8FD9\u79CD\u903B\u8F91\uFF0C\u4ED6\u8BA4\u4E3A\u5F7C\u6B64\u53CC\u65B9\u5E94\u8BE5\u662F\u4E00\u79CD\u201C\u53CC\u5DE8\u5934\u201D\u6A21\u5F0F\u3002</strong></p><p>\u4F46\u5218\u88D5\u7684\u770B\u6CD5\u5374\u5E76\u975E\u5982\u6B64\u3002\u4ECE\u59CB\u81F3\u7EC8\uFF0C\u5728\u5218\u88D5\u773C\u91CC\uFF0C\u5218\u6BC5\u90FD\u662F\u4E00\u4E2A\u53EF\u4EE5\u4FE1\u5F97\u8FC7\u7684<code>\u4E0B\u5C5E</code>\u2014\u2014\u5173\u952E\u5728\u4E8E\uFF0C\u662F\u4E0B\u5C5E\u3002</p><p>\u6240\u4EE5\u5218\u88D5\u5BF9\u5218\u6BC5\u76F8\u5F53\u7684\u5BBD\u5BB9\uFF0C\u5218\u6BC5\u6253\u4E86\u51E0\u6B21\u8D25\u4ED7\uFF0C\u5218\u88D5\u90FD\u6CA1\u6709\u4E8B\u6DF1\u7A76\uFF0C\u6B63\u662F\u57FA\u4E8E\u8FD9\u79CD\u6001\u5EA6\u3002\u5728\u5218\u88D5\u770B\u6765\uFF0C\u5218\u6BC5\u662F\u4E2A\u503C\u5F97\u4FE1\u4EFB\u7684\u4EBA\uFF0C\u5728\u654C\u4EBA\u6EE1\u5E03\u7684\u671D\u5C40\u4E2D\uFF0C\u4ED6\u7684\u5B58\u5728\u6709\u7740\u7279\u6B8A\u7684\u610F\u4E49\u3002\u5218\u88D5\u65E2\u7136\u60F3\u8981\u63A7\u5236\u671D\u5C40\uFF0C\u5C31\u5FC5\u987B\u6709\u4FE1\u5F97\u8FC7\u7684\u4EBA\u652F\u6301\uFF0C\u800C\u5218\u6BC5\u53C8\u662F\u8FD9\u4E9B\u4FE1\u5F97\u8FC7\u7684\u4EBA\u91CC\uFF0C\u6700\u503C\u5F97\u76F8\u4FE1\u7684\u4EBA\u4E4B\u4E00\u3002</p><p>\u4F60\u770B\uFF0C\u4E24\u4EBA\u5BF9\u5F7C\u6B64\u5173\u7CFB\u8BA4\u8BC6\u7684\u5DEE\u8DDD\uFF0C\u5C31\u6709\u8FD9\u4E48\u5DE8\u5927\u3002</p><p><strong>\u800C\u8BA4\u8BC6\u4E0A\u7684\u9E3F\u6C9F\uFF0C\u5FC5\u7136\u4F1A\u5F15\u53D1\u5173\u7CFB\u4E0A\u7684\u53D8\u52A8\u3002</strong></p><p>\u4E4B\u524D\uFF0C\u5218\u88D5\u57FA\u4E8E\u5BF9\u5218\u6BC5\u7684\u4FE1\u4EFB\uFF0C\u4E00\u76F4\u4EE5\u5FCD\u8BA9\u4E3A\u4E3B\u3002\u4E4B\u540E\uFF0C<strong>\u771F\u6B63\u7834\u574F\u4E86\u8FD9\u79CD\u4FE1\u4EFB\u7684\uFF0C\u662F\u5218\u6BC5\u4E0E\u671D\u5EF7\u9AD8\u95E8\u58EB\u65CF\u4E4B\u95F4\u7684\u4EA4\u5F80\u3002</strong></p><p>\u5218\u88D5\u51FA\u81EA\u5BD2\u95E8\uFF0C\u8D77\u81EA\u5317\u5E9C\u666E\u901A\u58EB\u5352\u800C\u6700\u7EC8\u6267\u638C\u671D\u653F\uFF0C\u8FD9\u65E2\u4E0D\u7B26\u5408\u4E1C\u664B\u8FD1\u767E\u5E74\u6765\u7684\u653F\u6CBB\u4F20\u7EDF\uFF0C\u53C8\u4FB5\u5360\u4E86\u58EB\u65CF\u7684\u65E2\u6709\u5229\u76CA\u3002\u56E0\u6B64\uFF0C\u5218\u88D5\u624D\u4F1A\u4E0D\u4E3A\u9AD8\u95E8\u5927\u9600\u6240\u63A5\u53D7\uFF0C\u58EB\u65CF\u4EEC\u59CB\u7EC8\u5728\u8C0B\u5212\u5C06\u5176\u8D76\u51FA\u671D\u5EF7\u3002</p><p>\u8FD9\u4E2A\u8C0B\u5212\uFF0C\u66FE\u7ECF\u5728\u5218\u88D5\u7684\u8C0B\u58EB\u5218\u7A46\u4E4B\u7684\u5E72\u6D89\u4E0B\u5931\u8D25\u8FC7\u4E00\u6B21\u3002\u4F46\u662F\uFF0C\u58EB\u65CF\u4EEC\u5E76\u672A\u653E\u5F03\uFF0C\u800C\u662F\u8F6C\u4E0E\u5218\u6BC5\u63A5\u8FD1\uFF0C\u5E0C\u671B\u901A\u8FC7\u5218\u6BC5\u6765\u8D76\u8D70\u5218\u88D5\u3002</p><p><strong>\u6B63\u662F\u5728\u8FD9\u6837\u7684\u60C5\u51B5\u4E0B\uFF0C\u5218\u6BC5\u9762\u5BF9\u7740\u58EB\u65CF\u7684\u9760\u62E2\uFF0C\u4E3B\u52A8\u793A\u597D\u4E86\u3002</strong></p><p>\u8FD9\u5F53\u4E2D\uFF0C\u5218\u6BC5\u6709\u7740\u81EA\u5DF1\u7684\u73B0\u5B9E\u8003\u8651\uFF1A\u65E2\u7136\u5728\u6218\u573A\u4E0A\u7684\u8868\u73B0\u5E76\u4E0D\u80FD\u652F\u6301\u4ED6\u4E0E\u5218\u88D5\u65BD\u884C\u201C\u5BE1\u5934\u6A21\u5F0F\u201D\uFF0C\u90A3\u4E48\u5728\u671D\u5802\u4E4B\u4E0A\u4E89\u53D6\u5230\u58EB\u65CF\u7684\u652F\u6301\uFF0C\u672A\u5C1D\u4E0D\u662F\u4E00\u6761\u6377\u5F84\u3002</p><p>**\u53EF\u8FD9\u771F\u6B63\u72AF\u4E86\u5218\u88D5\u7684\u5FCC\u8BB3\u3002**\u81F3\u6B64\uFF0C\u5218\u88D5\u5BF9\u5218\u6BC5\u518D\u65E0\u4FE1\u4EFB\u53EF\u8A00\u3002</p><p>\u7ED3\u4EA4\u58EB\u65CF\u8FD9\u4EF6\u4E8B\uFF0C\u5728\u5218\u88D5\u548C\u5218\u6BC5\u7684\u773C\u4E2D\u662F\u4E0D\u4E00\u6837\u7684\uFF1A\u5218\u6BC5\u4EE5\u4E3A\uFF0C\u8FD9\u5C31\u662F\u81EA\u5DF1\u8DDF\u5218\u88D5\u91CD\u6784\u5E73\u7B49\u5173\u7CFB\u7684\u4E3E\u63AA\u3002\u4F46\u5728\u5218\u88D5\u770B\u6765\uFF0C\u5218\u6BC5\u8FD9\u662F\u5728\u8DDF\u81EA\u5DF1\u7684\u654C\u4EBA\u9760\u8FD1\uFF0C\u90A3\u610F\u5473\u7740\u7684\u662F\u80CC\u53DB\u3002</p><p>\u53CC\u65B9\u7EC8\u4E8E\u53CB\u5C3D\u3002</p>',78),e=[s];function c(g,d){return o(),t("div",null,e)}const _=p(n,[["render",c],["__file","44.html.vue"]]);export{_ as default};
