import{_ as p}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as e,c as t,f as a}from"./app.52cb12a8.js";const c={},o=a('<h1 id="\u7B2C44\u96C6-\u5468\u79C9\u6606\u91CD\u4F24\u9A86\u58EB\u5BBE" tabindex="-1"><a class="header-anchor" href="#\u7B2C44\u96C6-\u5468\u79C9\u6606\u91CD\u4F24\u9A86\u58EB\u5BBE" aria-hidden="true">#</a> \u7B2C44\u96C6\uFF1A\u5468\u79C9\u6606\u91CD\u4F24\u9A86\u58EB\u5BBE</h1><p>\u5468\u79C9\u4E49\u5B89\u6170\u7740\u5468\u79C9\u6606\uFF0C\u5144\u5F1F\u4FE9\u7684\u624B\u63E1\u5728\u4E00\u8D77\uFF0C\u5468\u79C9\u4E49\u629A\u6478\u7740\u5468\u79C9\u6606\u7684\u5934\uFF0C\u4E5F\u5FCD\u4E0D\u4F4F\u6F78\u7136\u6CEA\u4E0B\u3002\u5468\u79C9\u6606\u4E5F\u5DF2\u7ECF\u51B3\u5B9A\u8981\u8BA9\u90D1\u5A1F\u53BB\u7F8E\u56FD\uFF0C\u540C\u65F6\u4E5F\u4F1A\u901A\u77E5\u9A86\u58EB\u5BBE\uFF0C\u4F5C\u4E3A\u7537\u4EBA\uFF0C\u4ED6\u5E94\u8BE5\u575A\u5F3A\u3002</p><p>\u7531\u4E8E\u90D1\u5A1F\u662F\u6960\u6960\u7684\u4EB2\u751F\u6BCD\u4EB2\uFF0C\u6240\u4EE5\u624B\u7EED\u4E0B\u6765\u7684\u5F88\u5FEB\uFF0C\u5979\u4E5F\u5F88\u5FEB\u53BB\u7F8E\u56FD\u3002\u9A86\u58EB\u5BBE\u53CD\u800C\u4E00\u65F6\u8FC7\u4E0D\u53BB\uFF0C\u5F6D\u6C34\u751F\u51FA\u4E3B\u610F\u7ED9\u90D1\u5A1F\u4E00\u7B14\u94B1\uFF0C\u8BA9\u5979\u5C06\u6960\u6960\u7684\u9057\u4F53\u51B7\u51BB\uFF0C\u7B49\u7740\u9A86\u58EB\u5BBE\u53BB\u89C1\u6960\u6960\u6700\u540E\u4E00\u9762\u3002</p><p>\u8521\u6653\u5149\u7ED9\u5468\u84C9\u505A\u4E86\u996D\u83DC\uFF0C\u5979\u6839\u672C\u5403\u4E0D\u4E0B\u53BB\u3002\u8521\u6653\u5149\u5C31\u6765\u529D\u8BF4\u73A5\u73A5\uFF0C\u56E0\u4E3A\u73A5\u73A5\u4E0D\u5403\u996D\u5468\u84C9\u4E5F\u4E0D\u5403\u996D\u3002\u8521\u6653\u5149\u8BB2\u8FF0\u4E86\u81EA\u5DF1\u5BF9\u5468\u84C9\u7684\u7231\uFF0C\u5F53\u5E74\u4ED6\u7231\u7740\u5468\u84C9\uFF0C\u53EF\u662F\u77E5\u9053\u5468\u84C9\u7231\u7740\u51AF\u5316\u6210\uFF0C\u4E3A\u4E86\u6210\u5168\u5468\u84C9\u8FD8\u5E2E\u7740\u6253\u63A9\u62A4\uFF0C\u56E0\u4E3A\u4ED6\u5E0C\u671B\u81EA\u5DF1\u7231\u7684\u4EBA\u5E78\u798F\u3002\u8521\u6653\u5149\u76F8\u4FE1\u6960\u6960\u5BF9\u73A5\u73A5\u7684\u7231\u4E5F\u662F\u5982\u6B64\uFF0C\u73A5\u73A5\u5E94\u8BE5\u4E3A\u4E86\u90A3\u4E9B\u7231\u81EA\u5DF1\u7684\u4EBA\u8FC7\u5F97\u66F4\u597D\u3002</p><p>\u5468\u79C9\u6606\u6765\u673A\u573A\u9001\u90D1\u5A1F\uFF0C\u5F6D\u6C34\u751F\u4E5F\u5C06\u94B1\u4EA4\u7ED9\u4E86\u90D1\u5A1F\uFF0C\u9A86\u58EB\u5BBE\u8D23\u602A\u5468\u79C9\u6606\u6CA1\u6709\u65E9\u70B9\u5C06\u5B69\u5B50\u7ED9\u4ED6\uFF0C\u5982\u679C\u65E9\u7ED9\u4E86\u8FD9\u79CD\u4E8B\u5C31\u4E0D\u4F1A\u53D1\u751F\u3002\u9A86\u58EB\u5BBE\u8BA4\u4E3A\u5982\u679C\u65E9\u70B9\u7ED9\u4ED6\u7684\u8BDD\uFF0C\u4ED6\u4F1A\u5B89\u6392\u597D\u6960\u6960\u7684\u4E00\u5207\uFF0C\u9A86\u58EB\u5BBE\u6307\u8D23\u5468\u79C9\u6606\u548C\u90D1\u5A1F\u90FD\u592A\u81EA\u79C1\u4E86\u3002\u5468\u79C9\u6606\u4E5F\u8D23\u602A\u9A86\u58EB\u5BBE\u5C31\u7ED9\u4E86\u90D1\u5A1F\u6BCD\u5B50\u4E00\u5E74\u7684\u94B1\uFF0C\u540E\u6765\u5C31\u4E0D\u95FB\u4E0D\u95EE\uFF0C\u4ECE\u76D1\u72F1\u51FA\u6765\u5341\u5E74\u6D88\u5931\u4E0D\u89C1\uFF0C\u5982\u679C\u9A86\u58EB\u5BBE\u80FD\u6709\u513F\u5B50\u4E00\u5B9A\u4E0D\u4F1A\u6765\u627E\u6960\u6960\u3002\u9A86\u58EB\u5BBE\u8BA4\u4E3A\u81EA\u5DF1\u81EA\u4ECE\u89C1\u4E86\u6960\u6960\uFF0C\u4E5F\u8BC1\u660E\u6960\u6960\u559C\u6B22\u548C\u4ED6\u5728\u4E00\u8D77\uFF0C\u9A86\u58EB\u5BBE\u627F\u8BA4\u6960\u6960\u7231\u90D1\u5A1F\uFF0C\u559C\u6B22\u90D1\u5A1F\uFF0C\u542C\u90D1\u5A1F\u7684\u3002\u4F46\u662F\u90D1\u5A1F\u6CA1\u6709\u6587\u5316\uFF0C\u6CA1\u6709\u89C1\u8BC6\uFF0C\u957F\u5927\u4E4B\u540E\u6960\u6960\u4E0D\u4F1A\u542C\u90D1\u5A1F\u7684\u8BDD\uFF0C\u56E0\u4E3A\u90D1\u5A1F\u6CA1\u6709\u6587\u5316\u4E5F\u6CA1\u6709\u5927\u9053\u7406\u53EF\u4EE5\u8BB2\uFF0C\u4E00\u5B9A\u662F\u5468\u79C9\u6606\u8BF4\u4E86\u4EC0\u4E48\u963B\u6B62\u4E86\u6960\u6960\u53BB\u89C1\u4ED6\u3002</p><p>\u5468\u79C9\u6606\u548C\u9A86\u58EB\u5BBE\u5728\u673A\u573A\u5916\u5C31\u5927\u6253\u51FA\u624B\uFF0C\u4E24\u4EBA\u90FD\u5E26\u7740\u6EE1\u8154\u7684\u6124\u6012\uFF0C\u8C01\u7684\u624B\u4E0B\u4E5F\u4E0D\u7559\u60C5\u3002\u5468\u79C9\u6606\u4E00\u811A\u5C06\u9A86\u58EB\u5BBE\u8E39\u5012\uFF0C\u540E\u8111\u52FA\u78D5\u5728\u4E86\u77F3\u58A9\u4E0A\uFF0C\u5468\u79C9\u6606\u4E5F\u88AB\u8B66\u5BDF\u5E26\u8D70\u4E86\uFF0C\u800C\u6B64\u65F6\u90D1\u5A1F\u7684\u98DE\u673A\u521A\u521A\u8D77\u98DE\u3002</p><p>\u8521\u6653\u5149\u9001\u5468\u84C9\u548C\u73A5\u73A5\u79BB\u5F00\uFF0C\u53EE\u5631\u5468\u84C9\u7167\u987E\u597D\u5979\u4EEC\u81EA\u5DF1\uFF0C\u800C\u4ED6\u4E5F\u4F1A\u7167\u987E\u597D\u5BB6\uFF0C\u5468\u84C9\u5E0C\u671B\u5E26\u7740\u73A5\u73A5\u53BB\u81EA\u5DF1\u5E74\u8F7B\u65F6\u5019\u594B\u6597\u8FC7\u7684\u5730\u65B9\uFF0C\u8BA9\u73A5\u73A5\u6563\u6563\u5FC3\u3002</p><p>\u5468\u79C9\u6606\u5728\u62D8\u7559\u6240\u7740\u6025\u60F3\u8981\u77E5\u9053\u9A86\u58EB\u5BBE\u7684\u60C5\u51B5\uFF0C\u4F46\u662F\u8B66\u5BDF\u5C40\u7684\u4EBA\u5374\u5E76\u672A\u544A\u8BC9\uFF0C\u53EA\u662F\u8BE2\u95EE\u5468\u79C9\u6606\u5BB6\u91CC\u7684\u8054\u7CFB\u65B9\u5F0F\uFF0C\u5468\u79C9\u6606\u53EA\u80FD\u6447\u6447\u5934\u3002</p><p>\u5468\u79C9\u4E49\u5E26\u4EBA\u6765\u770B\u4E00\u5757\u9EC4\u91D1\u5730\u6BB5\u7684\u5730\uFF0C\u4ECE\u8096\u5BB6\u6CB3\u90A3\u91CC\u5F97\u77E5\u8FD9\u5757\u5730\u8981\u5EFA\u9020\u4E00\u4E2A\u5C0F\u82B1\u56ED\uFF0C\u5468\u79C9\u4E49\u89C9\u5F97\u6709\u4E9B\u53EF\u60DC\uFF0C\u623F\u5730\u4EA7\u5F00\u53D1\u5546\u8001\u677F\u5ED6\u7B80\u4F0D\u6545\u610F\u96C7\u4EBA\u8D77\u54C4\u60F3\u8981\u963B\u6B62\u5468\u79C9\u4E49\u5C06\u5730\u6539\u7528\u9014\u3002</p><p>\u8521\u6653\u5149\u627E\u4E86\u5F8B\u5E08\u6765\u770B\u5468\u79C9\u6606\uFF0C\u4E86\u89E3\u4E86\u5F53\u65F6\u6253\u67B6\u7684\u60C5\u51B5\uFF0C\u73B0\u5728\u7684\u9A86\u58EB\u5BBE\u5728\u533B\u9662\u8FD8\u4F24\u5F97\u4E0D\u8F7B\uFF0C\u5468\u79C9\u6606\u5E0C\u671B\u7792\u7740\u90D1\u5A1F\u8FD9\u4EF6\u4E8B\uFF0C\u4ED6\u53EA\u5E0C\u671B\u90D1\u5A1F\u77E5\u9053\u4ED6\u4E00\u5207\u90FD\u597D\u3002\u90D1\u5A1F\u5E26\u7740\u6960\u6960\u7684\u9057\u7269\u56DE\u6765\uFF0C\u90DD\u51AC\u6885\u5C06\u90D1\u5A1F\u63A5\u56DE\u6765\uFF0C\u90DD\u51AC\u6885\u5C06\u5468\u79C9\u6606\u73B0\u5728\u7684\u60C5\u51B5\u544A\u8BC9\u4E86\u90D1\u5A1F\u3002\u90D1\u5A1F\u5012\u662F\u663E\u5F97\u5F88\u9547\u9759\uFF0C\u53EA\u8981\u5468\u79C9\u6606\u5B89\u5168\u5C31\u597D\uFF0C\u4E8B\u60C5\u90FD\u53EF\u4EE5\u6162\u6162\u89E3\u51B3\u3002</p><p>\u5468\u84C9\u5E26\u7740\u73A5\u73A5\u6765\u5230\u8D35\u5DDE\uFF0C\u8FD9\u91CC\u4E5F\u662F\u73A5\u73A5\u5C0F\u65F6\u5019\u751F\u6D3B\u8FC7\u7684\u5730\u65B9\uFF0C\u770B\u7740\u9752\u8349\u904D\u5730\uFF0C\u5468\u84C9\u5E0C\u671B\u80FD\u8BA9\u73A5\u73A5\u5FC3\u60C5\u597D\u8D77\u6765\uFF0C\u53EF\u73A5\u73A5\u4F9D\u7136\u662F\u9634\u4E91\u5BC6\u5E03\u3002</p><p>\u5468\u79C9\u4E49\u4E5F\u77E5\u9053\u4E86\u5468\u79C9\u6606\u7684\u4E8B\u60C5\uFF0C\u62DC\u6258\u59DA\u7ACB\u677E\u597D\u597D\u6253\u542C\u9A86\u58EB\u5BBE\u90A3\u8FB9\u7684\u60C5\u51B5\uFF0C\u59DA\u7ACB\u677E\u6253\u7B97\u7ACB\u529F\u8D4E\u7F6A\uFF0C\u4E5F\u5C31\u614C\u5FD9\u7B54\u5E94\u4E0B\u6765\u3002\u5468\u79C9\u4E49\u53BB\u89C1\u4E86\u8BB0\u8005\u6BDB\u6653\uFF0C\u8FD9\u5757\u9EC4\u91D1\u5730\u6BB5\u56E0\u4E3A\u7701\u91CC\u9886\u5BFC\u6709\u719F\u4EBA\uFF0C\u6240\u4EE5\u624D\u80FD\u7528\u4F4E\u4EF7\u7684\u62C6\u8FC1\u6B3E\u62FF\u4E0B\u6765\u76D6\u5546\u8D38\u57CE\uFF0C\u767E\u59D3\u7EB7\u7EB7\u4E0D\u6EE1\u3002\u4F46\u8FD9\u4EF6\u4E8B\u8FD8\u662F\u88AB\u538B\u4E0B\u6765\uFF0C\u6BDB\u6653\u5DF2\u7ECF\u91C7\u8BBF\u4E86\u6240\u6709\u7684\u62C6\u8FC1\u6237\uFF0C\u6709\u8DB3\u591F\u7684\u8BC1\u636E\u8BF4\u660E\uFF0C\u5468\u79C9\u4E49\u5FC3\u4E2D\u6709\u6570\uFF0C\u611F\u8C22\u6BDB\u6653\u7684\u4ED7\u4E49\u6267\u8A00\u3002</p><p>\u9A86\u58EB\u5BBE\u4E00\u76F4\u660F\u8FF7\u4E0D\u9192\uFF0C\u516C\u53F8\u7684\u90A3\u4E9B\u80A1\u4E1C\u4E5F\u90FD\u98DE\u8FC7\u6765\u5B88\u7740\u533B\u9662\uFF0C\u66F9\u73CA\u4E0D\u77E5\u9053\u8BE5\u5982\u4F55\u5904\u7406\u3002\u66F9\u73CA\u8BA4\u4E3A\u5982\u679C\u9A86\u58EB\u5BBE\u51FA\u4E8B\u7684\u8BDD\u5979\u5C31\u662F\u552F\u4E00\u7684\u7EE7\u627F\u4EBA\u3002\u6C34\u81EA\u6D41\u63D0\u9192\u66F9\u73CA\u9A86\u58EB\u5BBE\u8FD8\u6D3B\u7740\uFF0C\u66F9\u73CA\u5374\u8BA4\u4E3A\u73B0\u5728\u9A86\u58EB\u5BBE\u548C\u690D\u7269\u4EBA\u6CA1\u6709\u533A\u522B\u3002\u66F9\u73CA\u5E0C\u671B\u6C34\u81EA\u6D41\u80FD\u56DE\u53BB\u6DF1\u5733\u4E3B\u6301\u8463\u4E8B\u4F1A\uFF0C\u8D76\u7D27\u5546\u8BAE\u7BA1\u4E8B\u7684\u4EBA\uFF0C\u6C34\u81EA\u6D41\u5374\u5E76\u672A\u8BF4\u8BDD\uFF0C\u6B64\u65F6\u90D1\u5A1F\u6765\u4E86\u3002</p><p>\u90D1\u5A1F\u62C5\u5FC3\u9A86\u58EB\u5BBE\u4E00\u76F4\u8FD9\u6837\u9192\u4E0D\u8FC7\u6765\uFF0C\u66F9\u73CA\u5374\u663E\u5F97\u5F88\u51B7\u6F20\uFF0C\u5C31\u7B97\u9192\u4E0D\u8FC7\u6765\u4E5F\u5C31\u53EA\u80FD\u8FD9\u6837\u3002\u90D1\u5A1F\u5FFD\u7136\u4E0B\u8DEA\u6073\u6C42\u66F9\u73CA\u539F\u8C05\u5468\u79C9\u6606\uFF0C\u66F9\u73CA\u5374\u8868\u73B0\u5F97\u6BEB\u4E0D\u5728\u610F\uFF0C\u4E5F\u4E0D\u4F1A\u8BA9\u90D1\u5A1F\u51FA\u94B1\u3002\u90D1\u5A1F\u4E0D\u5728\u4E4E\u51FA\u94B1\uFF0C\u53EF\u4EE5\u8FD8\uFF0C\u9A86\u58EB\u5BBE\u4E0D\u662F\u8F7B\u4F24\uFF0C\u662F\u91CD\u4F24\uFF0C\u4E0D\u80FD\u79C1\u4E86\uFF0C\u79C9\u6606\u8FD9\u5C5E\u4E8E\u8FC7\u5931\u4F24\u4EBA\uFF0C\u90D1\u5A1F\u5E0C\u671B\u53EA\u8981\u80FD\u8BA9\u5468\u79C9\u6606\u5C11\u5224\u5211\u5C31\u884C\u3002\u800C\u66F9\u73CA\u5374\u8868\u793A\u6C34\u81EA\u6D41\u80FD\u4F5C\u8BC1\u5C31\u662F\u8FC7\u5931\u4F24\u4EBA\uFF0C\u5979\u4E5F\u4E0D\u4F1A\u771F\u7684\u8BA1\u8F83\u3002\u6C34\u81EA\u6D41\u4EB2\u81EA\u9001\u90D1\u5A1F\u51FA\u6765\uFF0C\u7B54\u5E94\u4F1A\u53BB\u6CD5\u5EAD\u4E3A\u5468\u79C9\u6606\u4F5C\u8BC1\uFF0C\u90D1\u5A1F\u518D\u4E09\u8981\u6C42\u6C34\u81EA\u6D41\u544A\u8BC9\u66F9\u73CA\uFF0C\u5468\u79C9\u6606\u4E0D\u662F\u6545\u610F\u6253\u4F24\u7684\uFF0C\u6C34\u81EA\u6D41\u4E5F\u90FD\u7B54\u5E94\u4E86\u3002</p><p>\u90DD\u51AC\u6885\u6B63\u5728\u7ED9\u75C5\u4EBA\u770B\u75C5\u7684\u65F6\u5019\uFF0C\u90D1\u5A1F\u6765\u4E86\u3002</p>',15),r=[o];function _(n,s){return e(),t("div",null,r)}const h=p(c,[["render",_],["__file","44.html.vue"]]);export{h as default};
