import{_ as p}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as e,c as t,f as a}from"./app.21634df6.js";const c={},o=a('<h1 id="\u7B2C45\u96C6-\u5468\u79C9\u4E49\u4E3A\u6295\u8D44\u559D\u4F4F\u9662" tabindex="-1"><a class="header-anchor" href="#\u7B2C45\u96C6-\u5468\u79C9\u4E49\u4E3A\u6295\u8D44\u559D\u4F4F\u9662" aria-hidden="true">#</a> \u7B2C45\u96C6\uFF1A\u5468\u79C9\u4E49\u4E3A\u6295\u8D44\u559D\u4F4F\u9662</h1><p>\u6709\u4E2A\u7537\u4EBA\u540D\u53EB\u738B\u695A\u5FB7\uFF0C\u59BB\u5B50\u56E0\u4E3A\u6765\u533B\u9662\u627E\u8FC7\u90DD\u51AC\u6885\u770B\u75C5\uFF0C\u4F46\u56E0\u4E3A\u5F53\u65F6\u60C5\u51B5\u7279\u6B8A\uFF0C\u90DD\u51AC\u6885\u5EFA\u8BAE\u8BA9\u624B\u672F\u751F\uFF0C\u53EF\u662F\u738B\u695A\u5FB7\u4E0D\u542C\u8BDD\uFF0C\u6700\u7EC8\u5BFC\u81F4\u4E86\u59BB\u5B50\u4E27\u547D\u3002\u4E3A\u6B64\u738B\u695A\u5FB7\u95F9\u5230\u533B\u9662\u627E\u90DD\u51AC\u6885\uFF0C\u53EF\u662F\u6BCF\u6B21\u90FD\u6CA1\u89C1\u5230\uFF0C\u770B\u5230\u90D1\u5A1F\u5728\u529E\u516C\u5BA4\u91CC\u7B49\u7740\u5C31\u51B2\u8FC7\u53BB\u6307\u8D23\u3002\u6070\u597D\u90DD\u51AC\u6885\u8FC7\u6765\u89C1\u90D1\u5A1F\uFF0C\u88AB\u738B\u695A\u5FB7\u7ED9\u6253\u4E86\u4E00\u987F\uFF0C\u6709\u75C5\u4EBA\u611F\u8C22\u90DD\u51AC\u6885\u6551\u4E86\u5BB6\u4EBA\uFF0C\u51FA\u624B\u963B\u6B62\u4E86\u738B\u695A\u5FB7\u7684\u884C\u4E3A\u3002\u738B\u695A\u5FB7\u77E5\u9053\u90DD\u51AC\u6885\u662F\u5468\u79C9\u4E49\u7684\u8001\u5A46\uFF0C\u8FD8\u9A82\u5468\u79C9\u4E49\u5C31\u4E0D\u662F\u597D\u4EBA\u3002</p><p>\u90D1\u5A1F\u4E5F\u4E3A\u90DD\u51AC\u6885\u611F\u5230\u5FC3\u75BC\uFF0C\u5C06\u4ECA\u5929\u548C\u6C34\u81EA\u6D41\u8C08\u8BDD\u7684\u4E8B\u60C5\u544A\u8BC9\u4E86\u90DD\u51AC\u6885\uFF0C\u5E0C\u671B\u5979\u4E0D\u8981\u592A\u62C5\u5FC3\u3002\u90DD\u51AC\u6885\u56DE\u53BB\u7684\u65F6\u5019\u5E26\u7740\u6EE1\u6EE1\u59D4\u5C48\uFF0C\u90DD\u6BCD\u4ECE\u65C1\u5B89\u6170\uFF0C\u90DD\u51AC\u6885\u4E5F\u5F00\u59CB\u57CB\u6028\u5468\u79C9\u4E49\u73B0\u5728\u7684\u60C5\u51B5\u60F9\u4E86\u4F17\u6012\uFF0C\u90DD\u6BCD\u89C9\u5F97\u5468\u79C9\u4E49\u6240\u4F5C\u7684\u4E00\u5207\u90FD\u662F\u5BF9\u7684\uFF0C\u90DD\u51AC\u6885\u66F4\u59D4\u5C48\uFF0C\u89C9\u5F97\u6BCD\u4EB2\u73B0\u5728\u504F\u5411\u5468\u79C9\u4E49\u90FD\u8D85\u8FC7\u4E86\u5979\uFF0C\u90DD\u6BCD\u5FCD\u4E0D\u4F4F\u7B11\u4E86\u3002</p><p>\u5468\u79C9\u4E49\u627E\u4E86\u62C6\u8FC1\u7684\u767E\u59D3\u8FC7\u6765\u5F00\u4E86\u5EA7\u8C08\u4F1A\uFF0C\u60F3\u8981\u4E86\u89E3\u4E00\u4E0B\u4E8B\u60C5\u7684\u524D\u56E0\u540E\u679C\uFF0C\u5927\u5BB6\u90FD\u5E0C\u671B\u80FD\u5F97\u5230\u653F\u5E9C\u7684\u5E2E\u52A9\uFF0C\u5468\u79C9\u4E49\u8FD9\u8FB9\u4E5F\u627F\u8BFA\u4F1A\u7ED9\u51FA\u89E3\u7B54\u65B9\u6848\u3002\u5468\u79C9\u4E49\u8981\u6C42\u8C03\u67E5\u6E05\u695A\u6240\u6709\u7684\u767E\u59D3\u5B9E\u9645\u60C5\u51B5\uFF0C\u4E00\u4E2A\u539F\u5219\u7EDD\u5BF9\u4E0D\u80FD\u6709\u4EBA\u9732\u5BBF\u8857\u5934\u3002</p><p>\u90B5\u656C\u6587\u56E0\u4E3A\u5468\u79C9\u6606\u7684\u4E8B\u60C5\u6765\u627E\u5468\u79C9\u4E49\uFF0C\u76EE\u524D\u5468\u79C9\u6606\u7684\u6848\u5B50\u5373\u5C06\u5F00\u5EAD\u4E86\uFF0C\u54EA\u6015\u662F\u5224\u4E86\u4E00\u5929\u7684\u5211\u7F5A\uFF0C\u5BF9\u4E8E\u5468\u79C9\u6606\u6765\u8BF4\u4E5F\u662F\u72AF\u7F6A\u3002\u800C\u5468\u79C9\u6606\u662F\u51FA\u7248\u793E\u7684\u7F16\u5236\uFF0C\u5728\u90A3\u91CC\u662F\u4E0D\u5141\u8BB8\u6709\u4EBA\u6709\u6848\u5E95\u7684\uFF0C\u90B5\u656C\u6587\u8BA4\u4E3A\u5468\u79C9\u6606\u662F\u597D\u4EBA\uFF0C\u4ED6\u4E0D\u5FCD\u5FC3\u770B\u7740\u5468\u79C9\u6606\u51FA\u4E8B\uFF0C\u624D\u4F1A\u5728\u4E03\u5341\u5C81\u7684\u5E74\u9F84\u5750\u4E86\u4E00\u5929\u4E00\u591C\u7684\u706B\u8F66\u6765\u627E\u5468\u79C9\u4E49\u3002\u56E0\u4E3A\u5730\u65B9\u4E0A\u4E5F\u4E0D\u60F3\u62C5\u8D23\u4EFB\uFF0C\u5E0C\u671B\u80FD\u8BA9\u4E0A\u8FB9\u6709\u4EBA\u53D1\u8BDD\uFF0C\u53EF\u5468\u79C9\u4E49\u8BA4\u4E3A\u8FD9\u662F\u5F87\u79C1\u575A\u51B3\u62D2\u7EDD\u4E86\uFF0C\u90B5\u656C\u6587\u5BF9\u6B64\u975E\u5E38\u5931\u671B\u3002</p><p>\u5ED6\u7B80\u4F0D\u6309\u7167\u627E\u4EBA\u6536\u4E70\u5468\u79C9\u4E49\uFF0C\u8FD8\u7279\u610F\u4ECE\u5B8B\u79D8\u4E66\u90A3\u91CC\u6253\u542C\u5230\u5468\u79C9\u4E49\u5176\u5B9E\u4EC0\u4E48\u90FD\u4E0D\u559C\u6B22\uFF0C\u4EFB\u4F55\u7231\u597D\u90FD\u6CA1\u6709\u3002\u5ED6\u7B80\u4F0D\u7279\u610F\u8BF7\u5BA2\u5403\u996D\uFF0C\u4F46\u662F\u5468\u79C9\u4E49\u4F9D\u7136\u53EA\u6709\u4E00\u4E2A\u6761\u4EF6\uFF0C\u5FC5\u987B\u89E3\u51B3\u95EE\u9898\uFF0C\u62C6\u8FC1\u6B3E\u9879\u7684\u95EE\u9898\u662F\u552F\u4E00\u89E3\u51B3\u7684\u91CD\u70B9\u95EE\u9898\u3002</p><p>\u6B63\u5F53\u90D1\u5A1F\u79EF\u6781\u51C6\u5907\u4E3A\u5468\u79C9\u6606\u8FA9\u62A4\u7684\u65F6\u5019\uFF0C\u90DD\u51AC\u6885\u5374\u4F20\u6765\u6D88\u606F\uFF0C\u9A86\u58EB\u5BBE\u5DF2\u7ECF\u6B7B\u4E86\uFF0C\u8FD9\u6B21\u4E0D\u4F1A\u91CD\u65B0\u5224\u4E86\uFF0C\u800C\u4E14\u5468\u79C9\u6606\u4E5F\u4F1A\u88AB\u51FA\u7248\u793E\u9664\u540D\u4E86\u3002\u90DD\u51AC\u6885\u56E0\u4E3A\u8FD9\u4EF6\u4E8B\u5FC3\u91CC\u4E0D\u8212\u670D\uFF0C\u4E5F\u89C9\u5F97\u5468\u79C9\u4E49\u4E0D\u80AF\u5E2E\u5FD9\uFF0C\u53EF\u662F\u90DD\u6BCD\u5374\u8BA4\u4E3A\u53EA\u8981\u662F\u653F\u5E9C\u5B98\u5458\u5C31\u5E94\u8BE5\u4EE5\u8EAB\u4F5C\u5219\uFF0C\u5468\u79C9\u4E49\u6CA1\u6709\u9519\u3002</p><p>\u62C6\u8FC1\u6B3E\u8865\u4E86\u4E4B\u540E\uFF0C\u5ED6\u7B80\u4F0D\u7EA6\u7740\u5468\u79C9\u4E49\u53BB\u770B\u5730\uFF0C\u8B66\u65B9\u7A81\u7136\u4EE5\u884C\u8D3F\u7F6A\u6293\u6355\u4E86\u5ED6\u7B80\u4F0D\uFF0C\u5ED6\u7B80\u4F0D\u8D23\u602A\u5468\u79C9\u4E49\u53EA\u60F3\u4ECE\u4ED6\u624B\u91CC\u9A97\u53D6\u62C6\u8FC1\u6B3E\uFF0C\u4ECE\u6765\u5C31\u6CA1\u60F3\u8FC7\u9879\u76EE\u7ED9\u4ED6\u505A\u3002\u5468\u79C9\u4E49\u4E5F\u4E0D\u89E3\u91CA\uFF0C\u53EA\u662F\u8BA9\u5ED6\u7B80\u4F0D\u5148\u53BB\u63A5\u53D7\u8C03\u67E5\uFF0C\u4EE5\u540E\u7684\u4E8B\u60C5\u4EE5\u540E\u8BF4\u3002\u7B49\u5ED6\u7B80\u4F0D\u88AB\u6293\u4E4B\u540E\uFF0C\u5468\u79C9\u4E49\u5B89\u6392\u4EBA\u7EE7\u7EED\u627E\u5F00\u53D1\u5546\u3002</p><p>\u5468\u84C9\u9047\u5230\u4E86\u4EE5\u524D\u63A5\u66FF\u5979\u5728\u5C71\u6751\u6559\u4E66\u7684\u9F9A\u660E\u6167\uFF0C\u5F97\u77E5\u9F9A\u660E\u6167\u73B0\u5728\u5DF2\u7ECF\u4E0D\u6559\u4E66\u4E86\uFF0C\u5A46\u5BB6\u60F3\u8981\u513F\u5B50\uFF0C\u5979\u5C31\u8FDE\u7EED\u751F\u4E86\u4E09\u4E2A\u5B69\u5B50\uFF0C\u73B0\u5728\u5C5E\u4E8E\u8D85\u751F\uFF0C\u800C\u4E14\u56E0\u4E3A\u5BB6\u5EAD\u6761\u4EF6\u4E0D\u597D\uFF0C\u5B69\u5B50\u4EEC\u4E5F\u6CA1\u6709\u8BFB\u4E66\u3002\u5468\u84C9\u770B\u7740\u9F9A\u660E\u6167\u9886\u7740\u4E09\u4E2A\u5B69\u5B50\uFF0C\u5FC3\u4E2D\u662F\u767E\u611F\u4EA4\u96C6\u3002\u5F53\u5468\u84C9\u548C\u73A5\u73A5\u6765\u5230\u4EE5\u524D\u6559\u5BA4\u7684\u5730\u65B9\uFF0C\u5374\u53D1\u73B0\u8FD9\u91CC\u65E9\u5C31\u53D8\u6210\u4E86\u522B\u4EBA\u7684\u5BB6\uFF0C\u4E5F\u4E0D\u662F\u6559\u5BA4\u4E86\uFF0C\u60F3\u8D77\u8FC7\u53BB\u79CD\u79CD\u5728\u8FD9\u91CC\u5EA6\u8FC7\uFF0C\u5468\u84C9\u773C\u775B\u91CC\u5145\u6EE1\u4E86\u6CEA\u6C34\uFF0C\u4EE5\u524D\u5927\u5BB6\u90FD\u5728\u8FD9\u91CC\u8BFB\u4E66\uFF0C\u5979\u6BCF\u5929\u5347\u8D77\u4E94\u661F\u7EA2\u65D7\uFF0C\u73B0\u5728\u65D7\u6746\u8FD8\u5728\uFF0C\u53EF\u662F\u6CA1\u4EBA\u5347\u56FD\u65D7\u4E86\u3002</p><p>\u5468\u79C9\u4E49\u56DE\u53BB\u5411\u90DD\u6BCD\u548C\u90DD\u51AC\u6885\u8BF4\u4E86\u54C8\u9633\u5E02\u73B0\u5728\u7684\u60C5\u51B5\uFF0C\u5982\u679C\u90A3\u4E2A\u5730\u76AE\u88AB\u5EFA\u9020\u8D77\u6765\uFF0C\u8FD8\u80FD\u89E3\u51B3\u4EBA\u5458\u5C31\u4E1A\u95EE\u9898\uFF0C\u4F46\u662F\u7ECF\u6D4E\u662F\u4E00\u5927\u96BE\u5173\uFF0C\u7701\u5385\u8D22\u653F\u4E5F\u6CA1\u6709\u90A3\u4E48\u591A\u7684\u94B1\u3002</p><p>\u90ED\u660E\u5F2F\u548C\u5468\u79C9\u4E49\u8C08\u6295\u8D44\uFF0C\u90ED\u660E\u5F2F\u5982\u5B9E\u8868\u8FBE\u516C\u53F8\u7684\u5176\u4ED6\u80A1\u4E1C\u90FD\u4E0D\u540C\u610F\u6295\u8D44\u7684\u4E8B\u60C5\uFF0C\u4ED6\u662F\u51B2\u7740\u5468\u79C9\u4E49\u6765\u6295\u8D44\u7684\uFF0C\u4E00\u676F\u9152\u4E94\u5341\u4E07\u7684\u6295\u8D44\u3002\u5468\u79C9\u4E49\u63D0\u51FA\u8BA9\u90ED\u660E\u5F2F\u4EE5\u4E00\u676F\u9152\u6295\u8D44\u4E00\u767E\u4E07\u7684\u6761\u4EF6\uFF0C\u8C08\u4E0B\u4E86\u6295\u8D44\uFF0C\u53EF\u6700\u540E\u4E5F\u5C06\u81EA\u5DF1\u559D\u5F97\u9001\u53BB\u4E86\u533B\u9662\u6025\u6551\u3002</p><p>\u9F9A\u660E\u6167\u89C9\u5F97\u8F9C\u8D1F\u4E86\u5468\u84C9\uFF0C\u7279\u610F\u6765\u62DB\u5F85\u6240\u9053\u6B49\uFF0C\u5468\u84C9\u80FD\u7406\u89E3\u9F9A\u660E\u6167\u7684\u4E0D\u5BB9\u6613\uFF0C\u4F46\u4E5F\u63D0\u9192\u9F9A\u660E\u6167\u8FD8\u662F\u5E94\u8BE5\u62BD\u65F6\u95F4\u597D\u597D\u8BFB\u4E66\uFF0C\u5C06\u6765\u624D\u80FD\u6709\u673A\u4F1A\u8D70\u51FA\u53BB\u3002\u73A5\u73A5\u770B\u7740\u9F9A\u660E\u6167\u79BB\u5F00\u7684\u80CC\u5F71\uFF0C\u6709\u4E9B\u4E0D\u7406\u89E3\uFF0C\u65E2\u7136\u5728\u8FD9\u91CC\u751F\u6D3B\u5145\u5B9E\u8FD8\u5FEB\u4E50\u4E3A\u4EC0\u4E48\u8981\u51FA\u53BB\uFF0C\u5468\u84C9\u82E6\u7B11\uFF0C\u5728\u8FD9\u91CC\u5FEB\u4E50\u548C\u5145\u5B9E\u90FD\u662F\u771F\u7684\uFF0C\u4F46\u662F\u60F3\u8981\u51FA\u53BB\u5B66\u4E60\u66F4\u591A\u7684\u77E5\u8BC6\u4E5F\u662F\u771F\u7684\uFF0C\u4EBA\u751F\u5145\u6EE1\u4E86\u77DB\u76FE\u3002\u73A5\u73A5\u5FFD\u7136\u7406\u89E3\u4E86\u5468\u84C9\uFF0C\u5C06\u6BCD\u4EB2\u6402\u5165\u4E86\u6000\u4E2D\u3002</p><p>\u5F53\u5468\u79C9\u4E49\u9192\u6765\u7684\u65F6\u5019\u53D1\u73B0\u90DD\u51AC\u6885\u5C31\u5750\u5728\u65C1\u8FB9\u3002</p>',13),r=[o];function _(n,s){return e(),t("div",null,r)}const h=p(c,[["render",_],["__file","45.html.vue"]]);export{h as default};
