import{_ as o}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as e,c,f as d}from"./app.cd90e563.js";const p="/myNotes/assets/img/base/640(22).jpeg",t={},s=d('<h1 id="\u56FD\u8FD0" tabindex="-1"><a class="header-anchor" href="#\u56FD\u8FD0" aria-hidden="true">#</a> \u56FD\u8FD0</h1><blockquote><p>\u4E5D\u6708\uFF0C\u592A\u5C09\u88D5\u81F3\u957F\u5B89\uFF0C\u2026\u2026\u6536\u79E6\u5F5D\u5668\u3001\u6D51\u4EEA\u3001\u571F\u572D\u3001\u8BB0\u91CC\u9F13\u3001\u6307\u5357\u8F66\u9001\u8BE3\u5EFA\u5EB7\u3002\u5176\u9980\u91D1\u7389\u3001\u7F2F\u5E1B\u3001\u73CD\u5B9D\uFF0C\u7686\u4EE5\u9881\u8D50\u5C06\u58EB\u3002\u9001\u59DA\u6CD3\u81F3\u5EFA\u5EB7\uFF0C\u65A9\u4E8E\u5E02\u3002</p></blockquote><p><strong>\u300A\u8D44\u6CBB\u901A\u9274\xB7\u5377\u4E00\u4E00\u516B\xB7\u664B\u7EAA\u56DB\u5341\u300B</strong></p><p><strong>\u4EE5<code>\u6155\u5BB9\u5782</code>\u4E4B\u6B7B\u4E3A\u754C\uFF0C\u4E4B\u540E\u7684\u4E94\u5341\u5E74\uFF0C\u662F\u5341\u516D\u56FD\u65F6\u671F\u6700\u4E3A\u6DF7\u4E71\u7684\u534A\u4E2A\u4E16\u7EAA\u3002</strong></p><p>\u5728\u6700\u591A\u7684\u65F6\u5019\uFF0C\u8FD9\u7247\u53E4\u8001\u7684\u571F\u5730\u4E0A\u5927\u7EA6\u6709\u5341\u4E2A\u5F7C\u6B64\u72EC\u7ACB\u7684\u52BF\u529B\u540C\u65F6\u5B58\u5728\u3002\u7687\u5E1D\u4EEC\u51D1\u4E24\u684C\u9EBB\u5C06\u8FD8\u80FD\u5269\u4E24\u4E2A\u66FF\u8865\uFF0C\u8FD9\u6837\u7684\u573A\u9762\u60F3\u60F3\u4E5F\u662F\u58EE\u89C2\u3002</p><p>\u6240\u5E78\u8FD9\u79CD\u5C40\u9762\u5E76\u6CA1\u6709\u6301\u7EED\u5F88\u4E45\uFF0C\u516C\u5143439\u5E74\uFF0C<strong>\u968F\u7740\u5317\u9B4F\u592A\u6B66\u5E1D<code>\u62D3\u62D4\u7118</code>\u6D88\u706D\u5317\u51C9\uFF0C\u91CD\u65B0\u7EDF\u4E00\u4E2D\u56FD\u7684\u5317\u65B9</strong>\uFF0C\u5929\u4E0B\u518D\u6B21\u56DE\u5230\u4E86\u5357\u5317\u4E8C\u5206\u7684\u5C40\u9762\u3002</p><p>\u524D\u79E6\u5D29\u6E83\u4EE5\u540E\uFF0C\u4EE5\u6F7C\u5173\u4E3A\u754C\uFF0C\u540E\u71D5\u4E0E\u540E\u79E6\u5206\u636E\u4E1C\u897F\u3002</p><p>\u4E4B\u540E\uFF0C\u540E\u71D5\u968F\u7740<code>\u6155\u5BB9\u5782</code>\u7684\u75C5\u901D\u800C\u4E00\u8E76\u4E0D\u632F\uFF0C\u906D\u5230\u4E86<code>\u5317\u9B4F</code>\u7684\u4E0D\u65AD\u653B\u51FB\uFF0C\u56FD\u571F\u4E00\u5206\u4E3A\u4E8C\uFF0C\u5357\u5317\u5206\u522B\u79F0\u5E1D\uFF0C\u662F\u4E3A\u201C<code>\u5357\u71D5</code>\u201D\u4E0E\u201C<code>\u5317\u71D5</code>\u201D\u3002</p><p><img src="'+p+'" alt="demo" loading="lazy"></p><p>\u5357\u71D5\u7684\u5F00\u56FD\u541B\u4E3B<code>\u6155\u5BB9\u5FB7</code>\uFF0C\u662F\u524D\u71D5\u6587\u660E\u5E1D\u6155\u5BB9\u769D\u7684\u513F\u5B50\u3001\u540E\u71D5\u7687\u5E1D\u6155\u5BB9\u5782\u7684\u5F1F\u5F1F\u3002\u4ED6\u539F\u672C\u662F\u540E\u71D5\u7684\u8303\u9633\u738B\uFF0C\u5728\u5317\u9B4F\u653B\u5165\u540E\u71D5\u7684\u9996\u90FD\u4E2D\u5C71\u4EE5\u540E\uFF0C\u56E0\u4E3A\u540E\u71D5\u7684\u56FD\u571F\u88AB\u5207\u5272\u4E3A\u5357\u5317\u4E24\u90E8\uFF0C\u4E4B\u95F4\u8054\u7CFB\u4E2D\u65AD\uFF0C\u6155\u5BB9\u5FB7\u4E8E\u662F\u4FBF\u4EE5\u5357\u90E8\u7684\u56FD\u571F\u4E3A\u6839\u57FA\u79F0\u5E1D\u3002\u5176\u56FD\u571F\u8303\u56F4\uFF0C\u6BD4\u4E4B\u5982\u4ECA\u7684\u5C71\u4E1C\u7701\u8981\u7565\u5C0F\u3002</p><p>\u5F53<code>\u6853\u7384</code>\u5728\u4E1C\u664B\u9884\u8C0B\u7BE1\u4F4D\u65F6\uFF0C\u5357\u71D5\u8FD8\u6709\u8FC7\u5185\u90E8\u8BA8\u8BBA\uFF1A\u8981\u4E0D\u8981\u8D81\u4E1C\u664B\u653F\u5C40\u4E0D\u7A33\u5357\u4E0B\uFF1F\u53BB\u4E86\u5E94\u8BE5\u5C31\u80FD\u6709\u6240\u6536\u83B7\uFF0C\u6700\u5C11\u4E5F\u5E94\u8BE5\u80FD\u83B7\u5F97\u4E1C\u664B\u7684\u6C5F\u5317\u4E4B\u5730\uFF08\u4ECA\u6C5F\u82CF\u3001\u5B89\u5FBD\u4F4D\u4E8E\u957F\u6C5F\u4EE5\u5317\u7684\u5730\u57DF\uFF09\u3002\u6155\u5BB9\u5FB7\u81EA\u5DF1\u662F\u610F\u6B32\u4E00\u6218\u7684\uFF0C\u4F46\u6700\u7EC8\u5374\u56E0\u671D\u8BAE\u6CA1\u6709\u7EDF\u4E00\u610F\u89C1\u800C\u4F5C\u7F62\u3002</p><p>\u6B64\u65F6\u6155\u5BB9\u5FB7\u5DF2\u7ECF\u662F\u516D\u5341\u516B\u5C81\u9AD8\u9F84\uFF0C\u4ED6\u4E8E\u4E24\u5E74\u540E\u75C5\u901D\uFF0C\u56E0\u819D\u4E0B\u65E0\u5B50\uFF0C\u7531\u5176\u4F84<code>\u6155\u5BB9\u8D85</code>\u7EE7\u4F4D\u3002</p><p>\u4E00\u573A\u5341\u516D\u56FD\u65F6\u671F\u5DF2\u7ECF\u6F14\u70C2\u4E86\u7684\u5BAB\u5EF7\u5185\u6597\u5927\u5267\u968F\u5373\u54C4\u95F9\u4E0A\u6F14\uFF0C\u6155\u5BB9\u8D85\u6491\u5230\u4E86\u6700\u540E\u83B7\u80DC\uFF0C\u6155\u5BB9\u5FB7\u7559\u4E0B\u7684\u6267\u653F\u73ED\u5E95\u5219\u88AB\u626B\u8361\u4E00\u7A7A\u3002</p><p>\u5185\u90E8\u6E05\u7406\u7ED3\u675F\u540E\uFF0C\u6155\u5BB9\u5FB7\u4FBF\u5F00\u59CB\u6253\u5916\u90E8\u7684\u4E3B\u4E49\uFF0C\u7ECF\u5E38\u6D3E\u5175\u9A9A\u6270\u62A2\u63A0\u4E1C\u664B\u56FD\u571F\u3002\u4E1C\u664B\u6B64\u65F6\u5DF2\u662F<code>\u5218\u88D5</code>\u6267\u653F\uFF0C\u4ED6\u4E5F\u6E05\u7406\u4E86\u671D\u4E2D\u7684\u53CD\u5BF9\u52BF\u529B\u3002</p><p>\u5218\u88D5\u53EF\u4E0D\u662F\u4EC0\u4E48\u8F6F\u67FF\u5B50\uFF0C\u4ED6\u662F\u90A3\u79CD\u4F60\u4E0D\u60F9\u4ED6\uFF0C\u4ED6\u90FD\u4F1A\u6765\u63CD\u4F60\u7684\u4E3B\uFF0C\u5982\u4ECA\u773C\u89C1\u4E00\u4E2A\u855E\u5C14\u5C0F\u56FD\u5C45\u7136\u90FD\u6B3A\u8D1F\u5230\u5BB6\u95E8\u53E3\u4E86\uFF0C\u600E\u4E48\u53EF\u80FD\u5FCD\u6C14\u541E\u58F0\uFF1F\u5218\u88D5\u5F53\u5373\u7387\u5175\u5317\u4F10\uFF0C\u534A\u5E74\u540E\u4FBF\u653B\u706D\u4E86\u5357\u71D5\u3002</p><p>\u81F3\u6B64\uFF0C<strong>\u9C9C\u5351\u6155\u5BB9\u6C0F\u9000\u51FA\u5386\u53F2\u7684\u821E\u53F0</strong>\u3002</p><p>\u88AB\u5218\u88D5\u6302\u4E0A\u9ED1\u540D\u5355\u4E0A\u7684\u56FD\u5BB6\u4E0D\u6B62\u5357\u71D5\u4E00\u4E2A\u3002\u706D\u4EA1\u5357\u71D5\u4EE5\u540E\uFF0C\u5218\u88D5\u53C8\u76EF\u4E0A\u4E86\u5272\u636E\u6210\u90FD\u5E73\u539F\u7684<code>\u8C2F\u8700</code>\u3002</p><p>\u8FD9\u4E2A\u6240\u8C13\u7684\u201C\u56FD\u5BB6\u201D\uFF0C\u5176\u5B9E\u662F\u4ECE\u4E1C\u664B\u5206\u79BB\u51FA\u53BB\u7684\u5272\u636E\u52BF\u529B\u3002\u5F53\u5E74\u6853\u7384\u7BE1\u4F4D\uFF0C\u76CA\u5DDE\u523A\u53F2\u6BDB\u74A9\u53CD\u6297\uFF0C\u4F46\u4E3A\u5C5E\u4E0B<code>\u8C2F\u7EB5</code>\u6240\u6740\u3002\u6B64\u540E\uFF0C\u8C2F\u7EB5\u4FBF\u81EA\u79F0\u6210\u90FD\u738B\uFF0C\u52FE\u7ED3\u5317\u65B9\u7684\u540E\u79E6\uFF0C\u5272\u636E\u4E00\u65B9\u3002</p><p>\u5BF9\u4E8E\u4EFB\u4F55\u4E00\u4E2A\u5357\u65B9\u653F\u6743\u800C\u8A00\uFF0C\u56DB\u5DDD\u76C6\u5730\u4E0D\u5728\u63A7\u5236\u4E4B\u5185\u90FD\u662F\u5DE8\u5927\u7684\u5A01\u80C1\uFF0C\u8FD9\u610F\u5473\u7740\u957F\u6C5F\u5929\u9669\u7684\u9632\u5B88\u51FA\u73B0\u4E86\u5DE8\u5927\u6F0F\u6D1E\u3002\u56E0\u6B64\uFF0C\u5218\u88D5\u5728\u6E05\u7406\u4E86\u5357\u71D5\u7684\u5371\u673A\u4EE5\u540E\uFF0C\u4FBF\u817E\u51FA\u624B\u6765\u65F6\u89E3\u51B3\u5F31\u5C0F\u7684\u8C2F\u8700\u3002</p><p>\u6B64\u6218\u5218\u88D5\u5E76\u6CA1\u6709\u4EB2\u4E34\u524D\u7EBF\uFF0C\u53EA\u662F\u6D3E\u4E86\u76CA\u5DDE\u523A\u53F2<code>\u6731\u9F84\u77F3</code>\u4E3B\u6301\uFF0C\u5F88\u5FEB\u5C31\u706D\u4EA1\u4E86\u8C2F\u8700\uFF0C\u5C06\u6210\u90FD\u5E73\u539F\u91CD\u65B0\u7EB3\u5165\u4E1C\u664B\u7684\u7EDF\u6CBB\u3002\u5F53\u7136\uFF0C\u6B64\u65F6\u7684\u6731\u9F84\u77F3\u8FD8\u4E0D\u77E5\u9053\uFF0C\u51E0\u5E74\u4EE5\u540E\uFF0C\u4ED6\u5C06\u4F1A\u5728\u5173\u4E2D\u4E3A<code>\u8D6B\u8FDE\u52C3\u52C3</code>\u6240\u6740\u3002</p><p>\u800C\u8D6B\u8FDE\u52C3\u52C3\u548C\u4ED6\u7684\u201C<strong>\u80E1\u590F</strong>\u201D\uFF0C\u4E0E\u5176\u8BF4\u662F\u653F\u6743\uFF0C\u8FD8\u4E0D\u5982\u8BF4\u66F4\u50CF\u662F\u4E00\u4E2A\u76D7\u8D3C\u56E2\u4F19\u3002\u8D6B\u8FDE\u52C3\u52C3\u672C\u662F\u540E\u79E6\u7684\u5C06\u9886\uFF0C\u9A7B\u5B88\u5728\u8FB9\u754C\u9632\u5907\u5317\u9B4F\u7684\u4FB5\u88AD\u3002\u540E\u6765\u5317\u9B4F\u4E0E\u540E\u79E6\u548C\u4EB2\uFF0C\u8D6B\u8FDE\u52C3\u52C3\u4FBF\u4E0D\u518D\u81E3\u670D\uFF0C\u51B3\u5B9A\u81EA\u7ACB\u3002</p><p>\u540E\u79E6\u7684\u5669\u68A6\u7531\u6B64\u5F00\u59CB\u3002\u7531\u4E8E\u8D6B\u8FDE\u52C3\u52C3\u519B\u4E8B\u80FD\u529B\u5F3A\u608D\uFF0C\u540E\u79E6\u5728\u4ED6\u7684\u4FB5\u6270\u4E0B\u65E5\u6E10\u8870\u843D\u3002</p><p><img src="'+p+'" alt="demo" loading="lazy"></p><p>\u800C\u66F4\u4E3A\u540E\u4E16\u6240\u77E5\u7684\uFF0C\u8FD8\u6709\u8FD9\u4F4D\u8D6B\u8FDE\u52C3\u52C3\u6240\u4FEE\u7B51\u7684<code>\u7EDF\u4E07\u57CE</code>\uFF1A\u4FEE\u7B51\u65F6\uFF0C\u5148\u8BA9\u5DE5\u5320\u592F\u5B9E\u57CE\u5899\uFF0C\u7136\u540E\u518D\u547D\u58EB\u5175\u4EE5\u94C1\u9525\u63D2\u5165\u3002\u5982\u679C\u80FD\u63D2\u5165\u4E00\u5BF8\uFF0C\u5219\u6740\u6389\u4FEE\u7B51\u7684\u5DE5\u5320\uFF0C\u4E00\u8D77\u7B51\u5165\u5899\u4E2D\u3002\u5982\u6B64\uFF0C\u82B1\u4E86\u516D\u5E74\u65F6\u95F4\uFF0C\u7EDF\u4E07\u57CE\u624D\u4FEE\u7B51\u5B8C\u6210\u3002</p><p>\u8D6B\u8FDE\u52C3\u52C3\u7684\u751F\u6027\u55DC\u6740\u7531\u6B64\u53EF\u89C1\u4E00\u6591\uFF0C\u4ED6\u7A0D\u6709\u4E0D\u987A\u5FC3\u4FBF\u4EE5\u6740\u4EBA\u4E3A\u4E50\uFF0C\u800C\u4E14\u662F\u53D8\u7740\u82B1\u6837\u7684\u6740\u4EBA\uFF0C\u4EE5\u6EE1\u8DB3\u81EA\u5DF1\u7684\u53D8\u6001\u7231\u597D\u3002<strong>\u4F46\u6B8B\u66B4\u5982\u6B64\uFF0C\u4ED6\u4F9D\u7136\u80FD\u51ED\u501F\u81EA\u5DF1\u7684\u519B\u4E8B\u80FD\u529B\u7EF4\u6301\u7EDF\u6CBB</strong>\uFF0C\u4E0D\u65AD\u9A9A\u6270\u540E\u79E6\uFF0C\u7EC8\u4E8E\u8BA9\u8FD9\u4E2A\u66FE\u7ECF\u5F3A\u76DB\u4E00\u65F6\u7684\u56FD\u5BB6\u8D70\u5230\u6CA1\u843D\u7684\u8FB9\u7F18\u3002</p><p>**\u6B64\u65F6\u540E\u79E6\u5DF2\u7ECF\u7ACB\u56FD\u4E8C\u5341\u4F59\u5E74\uFF0C<code>\u59DA\u5174</code>\u662F\u540E\u79E6\u7684\u7B2C\u4E8C\u4F4D\u7EDF\u6CBB\u8005\u3002**\u4ED6\u884C\u4E8B\u98CE\u683C\u4E0E\u82FB\u575A\u7C7B\u4F3C\uFF0C\u9887\u4E3A\u9075\u5FAA\u5112\u5BB6\u7684\u4EC1\u653F\u4F20\u7EDF\u3002</p><p>\u5728\u59DA\u5174\u5F53\u653F\u7684\u65E9\u671F\uFF0C\u540E\u79E6\u9010\u6E10\u58EE\u5927\uFF0C\u4F46\u5230\u4E86\u4ED6\u5728\u4F4D\u7684\u665A\u671F\uFF0C\u80E1\u4EBA\u653F\u6743\u7684\u7F3A\u70B9\u5374\u518D\u4E00\u6B21\u7206\u53D1\u51FA\u6765\uFF1A\u59DA\u5174\u8FD8\u6CA1\u6709\u6B7B\uFF0C\u4ED6\u7684\u51E0\u4E2A\u513F\u5B50\u4E4B\u95F4\u7684\u6597\u4E89\u5C31\u5DF2\u7ECF\u6108\u53D1\u6FC0\u70C8\uFF0C\u751A\u81F3\u662F\u5175\u620E\u76F8\u89C1\u4E86\u3002</p><p>\u59DA\u5174\u6CA1\u6709\u80FD\u529B\u5236\u6B62\u8FD9\u4E00\u5207\uFF0C\u53EA\u80FD\u662F\u9760\u7740\u81EA\u5DF1\u6B8B\u5B58\u7684\u5A01\u671B\uFF0C\u538B\u5236\u4F4F\u77DB\u76FE\u7684\u5927\u7206\u53D1\u3002</p><p><strong>\u800C\u968F\u7740\u59DA\u5174\u4E00\u6B7B\uFF0C\u540E\u79E6\u7684\u8870\u843D\u4E0D\u53EF\u907F\u514D</strong>\u3002\u8FD9\u4E00\u6B21\uFF0C\u62A2\u5148\u800C\u4E0A\u7684\u81EA\u7136\u53C8\u662F\u5218\u88D5\uFF1A\u5728\u59DA\u5174\u6B7B\u540E\u4E00\u5E74\uFF0C\u5218\u88D5\u5373\u4FBF\u653B\u5165\u4E86\u957F\u5B89\u3002</p><p>\u540E\u79E6\u706D\u4EA1\u3002</p><p><strong>\u5728\u8FD9\u534A\u4E2A\u4E16\u7EAA\u7684\u524D\u4E8C\u5341\u591A\u5E74\u91CC\uFF0C<code>\u5317\u9B4F</code>\u662F\u76F8\u5BF9\u6BD4\u8F83\u4F4E\u8C03\u7684\u4E00\u4E2A\u3002</strong></p><p>\u5728<code>\u62D3\u8DCB\u73EA</code>\u51FB\u8D25\u540E\u71D5\uFF0C\u8FDB\u5165\u4E2D\u539F\u4EE5\u540E\uFF0C\u5317\u9B4F\u65E9\u4E9B\u5E74\u5E76\u6CA1\u6709\u4EC0\u4E48\u5927\u52A8\u4F5C\u3002\u4E00\u6765\uFF0C\u4ED6\u4EEC\u8981\u6D88\u5316\u7A81\u7136\u62A2\u5360\u7684\u5927\u91CF\u9886\u5730\uFF0C\u5728\u8FD9\u4E9B\u571F\u5730\u4E0A\u5F62\u6210\u6709\u6548\u6CBB\u7406\u3002\u4E8C\u6765\uFF0C\u4F9D\u7136\u8FD8\u662F\u80E1\u4EBA\u653F\u6743\u5185\u90E8\u7684\u95EE\u9898\uFF1A\u62D3\u8DCB\u73EA\u665A\u5E74\u55DC\u6740\uFF0C\u6700\u7EC8\u88AB\u5176\u6B21\u5B50\u5F11\u6740\uFF0C\u800C\u540E\u4FBF\u662F\u4E00\u573A\u5185\u4E71\u3002\u6700\u7EC8\uFF0C\u592A\u5B50<code>\u62D3\u8DCB\u55E3</code>\u7EE7\u4F4D\uFF0C\u662F\u4E3A\u9B4F\u660E\u5143\u5E1D\u3002</p><p><strong>\u62D3\u8DCB\u55E3\u79F0\u4E0D\u4E0A\u6709\u96C4\u624D\u5927\u7565\uFF0C\u4F46\u5B88\u571F\u6709\u4F59</strong>\u3002\u5728\u4ED6\u5728\u4F4D\u671F\u95F4\uFF0C\u5317\u9B4F\u628A\u7EDD\u5927\u90E8\u5206\u7CBE\u529B\u90FD\u653E\u5728\u4E86\u5E94\u5BF9\u5317\u65B9\u7684<code>\u67D4\u7136</code>\u5165\u4FB5\u4E0A\u3002</p><p>\u5F53\u4E2D\uFF0C<strong>\u4E0E\u5218\u88D5\u4EC5\u6709\u8FC7\u4E00\u6B21\u4EA4\u950B\uFF0C\u53D1\u751F\u5728\u5218\u88D5\u5317\u4F10\u7684\u65F6\u5019</strong>\uFF1A\u5218\u88D5\u4ECE\u5F6D\u57CE\u51FA\u5175\uFF0C\u6EAF\u9EC4\u6CB3\u800C\u4E0A\uFF0C\u51C6\u5907\u653B\u6253\u540E\u79E6\u7684\u6838\u5FC3\u5730\u5E26\u2014\u2014\u5173\u4E2D\u3002\u4F46\u5F53\u65F6\u9EC4\u6CB3\u5728\u5317\u9B4F\u7684\u5883\u5185\uFF0C\u5218\u88D5\u56E0\u6B64\u6D3E\u4EBA\u501F\u9053\u3002</p><p>\u9488\u5BF9\u662F\u5426\u8981\u501F\u9053\uFF0C\u5317\u9B4F\u671D\u5EF7\u5C55\u5F00\u4E86\u4E89\u8BBA\u3002\u5F88\u591A\u4EBA\u90FD\u4E0D\u540C\u610F\uFF0C\u56E0\u4E3A\u540E\u79E6\u4E0E\u5317\u9B4F\u5DF2\u7ECF\u7ED3\u59FB\u3002\u53CD\u5BF9\u8005\u8BA4\u4E3A\uFF0C\u5982\u679C\u501F\u9053\u7ED9\u654C\u4EBA\uFF0C\u662F\u6709\u8FDD\u4EC1\u4E49\u7684\u4E3E\u52A8\u3002</p><p>\u4F46\u535A\u58EB\u796D\u9152<code>\u5D14\u6D69</code>\u5374\u4E0D\u4EE5\u4E3A\u7136\u3002\u4ED6\u7684\u770B\u6CD5\u662F\uFF0C\u5982\u4ECA\u540E\u79E6\u56FD\u529B\u8870\u5F31\uFF0C\u5218\u88D5\u5FD7\u5728\u5FC5\u5F97\u3002\u5317\u9B4F\u5982\u679C\u4E0D\u501F\u9053\uFF0C\u90A3\u4E48\u5FC5\u7136\u4F1A\u62DB\u81F4\u5218\u88D5\u7684\u653B\u6253\uFF0C\u8FD9\u5B8C\u5168\u5C31\u662F\u5728\u4EE3\u540E\u79E6\u53D7\u8FC7\uFF0C\u8FD9\u79CD\u4E70\u5356\u4E0D\u5212\u7B97\u3002</p><p>\u4F46\u62D3\u8DCB\u55E3\u5374\u6CA1\u6709\u542C\u53D6\u8FD9\u4E2A\u610F\u89C1\uFF0C\u800C\u662F\u6D3E\u5175\u6CBF\u6CB3\u9A7B\u5B88\uFF0C\u5E76\u4E14\u4E00\u8DEF\u968F\u5218\u88D5\u897F\u8FDB\u3002\u4E4B\u540E\uFF0C\u5218\u88D5\u679C\u7136\u5982\u5D14\u6D69\u6240\u6599\uFF0C\u8C03\u8F6C\u67AA\u5934\uFF0C\u5148\u89E3\u51B3\u4E86\u5317\u9B4F\u7684\u519B\u961F\uFF0C\u7136\u540E\u624D\u5F15\u5175\u897F\u8FDB\u3002</p><p>\u62D3\u8DCB\u55E3\u8FD9\u624D\u5F00\u59CB\u4F69\u670D\u5D14\u6D69\u7684\u5148\u89C1\u4E4B\u660E\uFF0C\u4ED6\u9759\u4E0B\u5FC3\u6765\uFF0C\u54A8\u8BE2\u5D14\u6D69\u7684\u770B\u6CD5\uFF1A\u5982\u4ECA\u8D81\u7740\u5218\u88D5\u7684\u4E3B\u529B\u5728\u5173\u4E2D\uFF0C\u6211\u4EEC\u653B\u6253\u4E1C\u664B\u7684\u9752\u5F90\u4E00\u5E26\u5982\u4F55\uFF1F</p><p>\u5D14\u6D69\u8BF4\uFF0C\u4E0D\u597D\u3002\u5982\u4ECA\u67D4\u7136\u5728\u5317\uFF0C\u965B\u4E0B\u4E0D\u80FD\u4EB2\u7387\u5927\u519B\u5357\u5F81\uFF0C\u800C\u5982\u679C\u59D4\u6D3E\u4ED6\u4EBA\uFF0C\u53C8\u201C\u867D\u6709\u7CBE\u5175\uFF0C\u672A\u7779\u826F\u5C06\u201D\uFF0C\u56E0\u6B64\u8FD8\u662F\u4E0D\u8981\u6253\u8FD9\u4E2A\u4E3B\u610F\u4E86\u3002</p><p>\u4F46\u62D3\u8DCB\u55E3\u542C\u540E\u4ECD\u4E0D\u6B7B\u5FC3\uFF0C\u4F9D\u65E7\u6D3E\u4EBA\u5357\u4E0B\uFF0C\u60F3\u8981\u4F3A\u673A\u5174\u5175\uFF0C\u76F4\u5230\u6700\u7EC8\u6CA1\u6709\u627E\u5230\u673A\u4F1A\u624D\u4F5C\u7F62\u3002</p><p>\u51E0\u5E74\u4EE5\u540E\uFF0C\u62D3\u8DCB\u55E3\u75C5\u901D\uFF0C\u7531\u592A\u5B50<code>\u62D3\u62D4\u7118</code>\u7EE7\u4F4D\u2014\u2014<strong>\u8FD9\u4F4D\uFF0C\u5C31\u662F\u540E\u6765\u7EDF\u4E00\u4E86\u5317\u65B9\u7684\u5317\u9B4F\u592A\u6B66\u5E1D\u62D3\u62D4\u7118\u3002</strong></p><p><strong>\u6211\u4EEC\u540E\u4E16\u5728\u770B\u5386\u53F2\u7684\u65F6\u5019\uFF0C\u773C\u5149\u5F80\u5F80\u662F<code>\u7EBF\u6027</code>\u7684</strong>\uFF1A\u603B\u662F\u4E00\u4E2A\u4E8B\u4EF6\u63A5\u7740\u53E6\u4E00\u4E2A\u4E8B\u4EF6\u6B21\u7B2C\u53D1\u751F\uFF0C\u7136\u540E\u5C31\u53D8\u6210\u4E86\u6211\u4EEC\u6700\u7EC8\u4E86\u89E3\u5230\u7684\u5386\u53F2\u3002</p><p><strong>\u4F46\u8EAB\u5728\u5386\u53F2\u4E4B\u4E2D\uFF0C\u611F\u5B98\u5374\u5E76\u975E\u5982\u6B64</strong>\u3002\u5F53\u662F\u65F6\uFF0C\u5317\u9B4F\u548C\u4E1C\u664B\uFF08\u5218\u5B8B\uFF09\u5E76\u4E0D\u77E5\u9053\u81EA\u5DF1\u80FD\u591F\u575A\u6301\u5230\u6700\u540E\uFF0C\u540E\u79E6\u8FD9\u6837\u7684\u56FD\u5BB6\u4E5F\u60F3\u4E0D\u5230\u81EA\u5DF1\u6700\u7EC8\u5C06\u53D8\u6210\u522B\u4EBA\u7684\u57AB\u811A\u77F3\u3002</p><p>\u90A3\u8FD9\u5176\u4E2D\u6709\u4EC0\u4E48\u5FC5\u7136\u89C4\u5F8B\u5417\uFF1F</p><p>\u5982\u679C\u62E8\u5F00\u5386\u53F2\u7684\u8FF7\u96FE\u6765\u770B\uFF0C\u5176\u5B9E\u4E5F\u8FD8\u662F\u80FD\u53D1\u73B0\u4E9B\u8BB8\u7AEF\u502A\u7684\uFF1A\u53EA\u8981\u770B\u4E00\u4E0B\u5728\u8FD9\u51E0\u5341\u5E74\u91CC\uFF0C<strong>\u5404\u56FD\u7684\u6CE8\u610F\u529B<code>\u805A\u7126</code>\u5728\u4F55\u5904</strong>\uFF0C\u4E5F\u5C31\u5927\u81F4\u53EF\u4EE5\u770B\u51FA\u4ED6\u4EEC\u540E\u6765\u7684\u5DEE\u522B\u4E86\u3002</p><p>\u5357\u71D5\u56FD\u529B\u5F31\u5C0F\uFF0C\u968F\u7740\u6155\u5BB9\u5FB7\u665A\u5E74\u8F7B\u6613\u653E\u5F03\u5357\u4E0B\u653B\u664B\u7684\u52A8\u8BAE\uFF0C\u57FA\u672C\u4E0A\u4E5F\u5C31\u4E0D\u53EF\u80FD\u518D\u83B7\u5F97\u66F4\u597D\u7684\u58EE\u5927\u673A\u4F1A\u4E86\u3002\u800C\u968F\u7740\u6155\u5BB9\u5FB7\u901D\u53BB\uFF0C\u5357\u71D5\u7684\u56FD\u653F\u6BCF\u51B5\u6108\u4E0B\uFF0C\u56FD\u529B\u65E5\u6E10\u8870\u843D\uFF0C\u88AB\u706D\u53EA\u4F1A\u8D8A\u6765\u8D8A\u662F\u4E00\u4E2A\u65F6\u95F4\u95EE\u9898\u3002</p><p>\u540E\u79E6\u5728\u59DA\u5174\u4E3A\u5E1D\u7684\u65E9\u671F\uFF0C\u9887\u6709\u4E00\u756A\u5174\u76DB\u4E4B\u8C61\uFF0C\u4F46\u81EA\u4ECE\u9519\u7528\u8D6B\u8FDE\u52C3\u52C3\u4EE5\u540E\u5C31\u5F00\u59CB\u8D70\u4E0B\u5761\u8DEF\u3002\u800C\u59DA\u5174\u5230\u4E86\u665A\u5E74\uFF0C\u5728\u7EE7\u4F4D\u95EE\u9898\u4E0A\u7684\u5904\u7406\u53C8\u5E76\u4E0D\u59A5\u5F53\uFF0C\u751A\u81F3\u8FD8\u672A\u6B7B\u4E4B\u65F6\uFF0C\u671D\u5EF7\u5185\u5C31\u5DF2\u7ECF\u6697\u6D41\u6D8C\u52A8\uFF0C\u5200\u5175\u76F8\u89C1\u3002</p><p>\u5916\u6709\u654C\u60A3\uFF0C\u5185\u6709\u52A8\u4E71\uFF0C\u540E\u79E6\u5176\u5B9E\u4E5F\u6CE8\u5B9A\u514D\u4E0D\u4E86\u88AB\u6D88\u706D\u7684\u547D\u8FD0\u3002\u8FD9\u4E00\u70B9\uFF0C\u5218\u88D5\u770B\u5230\u4E86\uFF0C\u5D14\u6D69\u4E5F\u770B\u5230\u4E86\u3002\u65E0\u975E\u53EA\u662F\u5218\u88D5\u5148\u4E0B\u624B\u4E3A\u5F3A\uFF0C\u5317\u9B4F\u5F53\u65F6\u53D7\u5236\u4E8E\u67D4\u7136\uFF0C\u65E0\u529B\u51FA\u624B\u800C\u5DF2\u3002</p><p>\u81F3\u4E8E\u8D6B\u8FDE\u52C3\u52C3\u7684\u80E1\u590F\uFF0C\u5219\u66F4\u662F\u4E00\u4E2A\u6781\u54C1\u7684\u5B58\u5728\u3002\u4E0E\u5176\u8BF4\u5B83\u662F\u4E00\u4E2A\u653F\u6743\uFF0C\u8FD8\u4E0D\u5982\u8BF4\u90A3\u662F\u4E00\u4F19\u5C71\u8D3C\u3002\u5373\u4FBF\u8D6B\u8FDE\u52C3\u52C3\u540E\u6765\u79F0\u5E1D\u3001\u5EFA\u56FD\u53F7\u3001\u5206\u5C01\u5B98\u540F\uFF0C\u4E5F\u4F9D\u7136\u6539\u53D8\u4E0D\u4E86\u4ED6\u4EEC\u662F\u4E00\u4F19\u76D7\u8D3C\u7684\u672C\u8D28\u3002</p><p>\u800C\u4E00\u4E2A\u6CA1\u6709\u4EF7\u503C\u89C2\u3001\u4EE5\u62A2\u63A0\u4E3A\u751F\uFF0C\u6CA1\u6709\u6B63\u5E38\u793E\u4F1A\u6CBB\u7406\u7ED3\u6784\u3001\u9189\u5FC3\u4E8E\u6784\u5EFA\u9632\u5FA1\u5DE5\u4E8B\u7684\u7EC4\u7EC7\uFF0C\u5B83\u552F\u4E00\u501A\u4ED7\u7684\uFF0C\u4E5F\u5C31\u662F\u8D6B\u8FDE\u52C3\u52C3\u7684\u519B\u4E8B\u80FD\u529B\u4E86\u3002\u5F53\u8FD9\u4E2A\u519B\u4E8B\u5F3A\u4EBA\u4E00\u65E6\u4E0D\u5728\u4E86\uFF0C\u8FD9\u4E2A\u201C\u56FD\u5BB6\u201D\u4E5F\u5C31\u8D70\u5230\u4E86\u5934\u3002\u8D6B\u8FDE\u52C3\u52C3\u6B7B\u540E\u4E0D\u4E45\uFF0C\u80E1\u590F\u5373\u88AB\u5317\u9B4F\u6D88\u706D\u3002</p><p>\u8FD9\u65F6\u5019\uFF0C\u4E0D\u59A8\u518D\u6765\u770B\u770B\u5218\u88D5\u8FD9\u4E9B\u5E74\u90FD\u5728\u5E72\u4EC0\u4E48\u3002</p><p>\u81EA\u4ECE\u8D77\u5175\u51FB\u8D25\u6853\u7384\u4EE5\u540E\uFF0C\u5218\u88D5\u4FBF\u6267\u638C\u4E1C\u664B\u671D\u653F\u3002\u968F\u5373\uFF0C\u8D81\u7740\u5357\u71D5\u5185\u4E71\uFF0C\u4ED6\u51FA\u5175\u5317\u4E0A\uFF0C\u653B\u706D\u5357\u71D5\u3002</p><p>\u800C\u540E\uFF0C\u56FD\u5185\u5148\u540E\u7ECF\u5386\u4E86\u5362\u5FAA\u3001\u5218\u6BC5\u3001\u53F8\u9A6C\u4F11\u4E4B\u4F5C\u4E71\uFF0C\u5218\u88D5\u90FD\u9010\u4E00\u5E73\u5B9A\u3002</p><p>\u5728\u6C5F\u5357\u5927\u5B9A\u4EE5\u540E\uFF0C\u5218\u88D5\u53C8\u6CBF\u6C5F\u800C\u4E0A\uFF0C\u524A\u5E73\u4E86\u5341\u51E0\u5E74\u524D\u5728\u6853\u7384\u7BE1\u4F4D\u65F6\u671F\u5360\u636E\u8700\u4E2D\u72EC\u7ACB\u7684\u8C2F\u8700\u3002</p><p>\u518D\u7136\u540E\uFF0C\u5219\u662F\u8D81\u7740\u540E\u79E6\u59DA\u5174\u75C5\u901D\uFF0C\u51FA\u5175\u5317\u4E0A\uFF0C\u8FDB\u5165\u5173\u4E2D</p><p>\u5317\u9B4F\u53C8\u662F\u53E6\u5916\u4E00\u756A\u60C5\u5F62\u3002\u5728\u62D3\u8DCB\u55E3\u5728\u4F4D\u671F\u95F4\uFF0C\u5317\u9B4F\u51E0\u4E4E\u6CA1\u6709\u53C2\u4E0E\u4E2D\u539F\u7684\u4E89\u9738\uFF0C\u800C\u662F\u628A\u4E3B\u8981\u7CBE\u529B\u653E\u5728\u4E86\u4E0E\u57DF\u5916\u67D4\u7136\u7684\u6597\u4E89\u4E0A\u3002</p><p>\u7B49\u5230\u5218\u88D5\u53BB\u4E16\u540E\uFF0C\u62D3\u8DCB\u55E3\u7EC8\u4E8E\u7B49\u5230\u4E86\u4E00\u4E2A\u673A\u4F1A\uFF0C\u5357\u4E0B\u653B\u5B8B\uFF0C\u5360\u636E\u4E86\u4E0D\u5C11\u571F\u5730\u3002\u540C\u65F6\uFF0C\u62D3\u8DCB\u55E3\u8FD8\u63D0\u62D4\u4E86\u5D14\u6D69\uFF0C\u5C06\u8FD9\u4F4D\u6770\u51FA\u7684\u8C0B\u58EB\u7559\u7ED9\u4E86\u81EA\u5DF1\u7684\u513F\u5B50\u3002</p><p><strong>\u5728<code>\u540C\u4E00\u6BB5\u65F6\u95F4</code>\u5185</strong>\uFF0C\u5404\u56FD\u56E0\u4E3A\u56FD\u60C5\u4E0D\u540C\u3001\u6267\u653F\u8005\u6027\u60C5\u4E0D\u540C\u3001\u8FFD\u6C42\u7684\u76EE\u6807\u4E0D\u540C\u800C\u505A\u7740\u4E0D\u540C\u7684\u4E8B\uFF0C<strong>\u5E76\u6700\u7EC8\u8D70\u5411\u4E86\u4E0D\u4E00\u6837\u7684\u7ED3\u5C40</strong>\u3002</p><p>\u5218\u88D5\u6267\u653F\u4EE5\u540E\uFF0C\u5176\u5B9E\u662F\u7ECF\u5386\u4E86\u4E00\u4E2A\u6BD4\u8F83\u957F\u7684\u5185\u90E8\u7A33\u56FA\u671F\u7684\u3002</p><p>\u5728\u8FD9\u6BB5\u65F6\u95F4\u5185\uFF0C\u4E1C\u664B\u6240\u9762\u5BF9\u7684\u56FD\u9645\u73AF\u5883\u76F8\u5BF9\u6BD4\u8F83\u548C\u5E73\uFF0C\u6CA1\u6709\u91CD\u5927\u7684\u5916\u654C\u5165\u4FB5\uFF0C\u6CA1\u6709\u7ED9\u4E1C\u664B\u9020\u6210\u5DE8\u5927\u7684\u5916\u90E8\u538B\u529B\u3002\u5426\u5219\uFF0C\u5218\u88D5\u5FC5\u7136\u6349\u895F\u89C1\u8098\uFF0C\u80FD\u5426\u4ECE\u5BB9\u7684\u89E3\u51B3\u56FD\u5185\u53CD\u5BF9\u52BF\u529B\u95EE\u9898\u5C1A\u4E14\u662F\u4E2A\u672A\u77E5\u6570\u3002</p><p>\u518D\u628A\u65F6\u95F4\u5F80\u524D\u63A8\u4E00\u70B9\uFF0C\u82FB\u575A\u5F53\u5E74\u5357\u4E0B\u4F10\u664B\uFF0C\u78B0\u5230\u7684\u662F\u8C22\u5B89\u5728\u671D\u3001\u8C22\u7384\u7ECF\u8425\u5317\u5E9C\u5175\u3001\u6853\u51B2\u9547\u5B88\u8346\u5DDE\u7684\u5C40\u9762\u3002\u8FD9\u4E00\u5E74\u662F\u664B\u592A\u5143\u516B\u5E74\uFF08\u516C\u5143383\u5E74\uFF09\uFF0C\u82FB\u575A\u5982\u679C\u518D\u665A\u6765\u4E24\u5E74\uFF0C\u7B49\u5230\u8FD9\u4E09\u4EBA\u90FD\u5148\u540E\u53BB\u4E16\uFF0C\u90A3\u4E48\u4ED6\u7684\u7EDF\u4E00\u5927\u4E1A\u5F88\u6709\u53EF\u80FD\u5C31\u53EF\u4EE5\u5B9E\u73B0\u3002</p><p>\u4ECE\u8FD9\u4E2A\u89D2\u5EA6\u6765\u770B\uFF0C\u82FB\u575A\u7684\u8FD0\u6C14\u7740\u5B9E\u4E0D\u597D\u3002</p><p>\u82FB\u575A\u9762\u5BF9\u7684\uFF0C\u5982\u679C\u662F\u5185\u90E8\u6597\u4E89\u6FC0\u70C8\u7684\u4E1C\u664B\uFF0C\u90A3\u4E48\u4F10\u664B\u5F88\u6709\u53EF\u80FD\u5C31\u4F1A\u6210\u529F\u3002\u800C\u6709\u4E86\u8FD9\u573A\u7EDF\u4E00\u4E4B\u6218\u7684\u6210\u529F\uFF0C\u5185\u90E8\u7684\u6C11\u65CF\u77DB\u76FE\u5C31\u4E0D\u4F1A\u88AB\u6FC0\u5316\uFF0C\u524D\u79E6\u4E5F\u4E0D\u4F1A\u7ACB\u5373\u5C31\u5D29\u6E83\u2014\u2014\u751A\u81F3\u53EF\u80FD\u5728\u90A3\u4E00\u5E74\uFF0C\u82FB\u575A\u5C31\u80FD\u7EDF\u4E00\u5357\u5317\u3002</p><p>\u540C\u7406\uFF0C\u62D3\u62D4\u7118\u5982\u679C\u65E9\u751F\u4E09\u5341\u5E74\uFF0C\u90A3\u4E48\u5728\u5218\u88D5\u6E05\u7406\u4E1C\u664B\u5185\u90E8\u53CD\u5BF9\u52BF\u529B\u65F6\uFF0C\u5317\u9B4F\u5C31\u80FD\u8D81\u865A\u5357\u4E0B\u3002\u5982\u6B64\u4E00\u6765\uFF0C\u5218\u88D5\u5185\u5916\u4EA4\u56F0\uFF0C\u4E0D\u89C1\u5F97\u5C31\u80FD\u59A5\u5584\u5E94\u5BF9\uFF0C\u5F88\u6709\u53EF\u80FD\u4F1A\u548C\u6853\u7384\u4E00\u6837\u88AB\u6740\u3002</p><p>\u53C8\u6216\u8005\uFF0C\u5728\u5218\u88D5\u5317\u4F10\u5173\u4E2D\u6210\u529F\u540E\uFF0C\u5218\u7A46\u4E4B\u6CA1\u6709\u75C5\u901D\uFF0C\u800C\u662F\u7EE7\u7EED\u5728\u5EFA\u5EB7\u4E3A\u5218\u88D5\u4E3B\u6301\u653F\u52A1\uFF0C\u90A3\u4E48\u5218\u88D5\u5C31\u80FD\u7EE7\u7EED\u5317\u4F10\u3002\u5F53\u65F6\u7684\u5317\u65B9\u5218\u88D5\u5C1A\u65E0\u654C\u624B\uFF0C\u5F88\u53EF\u80FD\u4ED6\u4E5F\u5C31\u4E00\u7EDF\u5929\u4E0B\u4E86\u3002</p>',65),r=[s];function n(g,a){return e(),c("div",null,r)}const l=o(t,[["render",n],["__file","47.html.vue"]]);export{l as default};
