import{_ as p}from"./_plugin-vue_export-helper.cdc0426e.js";import{o,c as t,f as r}from"./app.1218f0f6.js";const n={},s=r('<h1 id="\u8463\u5353\u8FDB\u4EAC" tabindex="-1"><a class="header-anchor" href="#\u8463\u5353\u8FDB\u4EAC" aria-hidden="true">#</a> \u8463\u5353\u8FDB\u4EAC</h1><blockquote><p>\u4F55\u8FDB\u53EC\u5353\u4F7F\u5C06\u5175\u8BE3\u4EAC\u5E08\u3002\u4F8D\u5FA1\u53F2\u90D1\u6CF0\u8C0F\u66F0\uFF1A\u201C......\u201D\u5C1A\u4E66\u5362\u690D\u4EA6\u8A00\u4E0D\u5B9C\u53EC\u5353\uFF0C\u8FDB\u6BD5\u4E0D\u4ECE\u3002\u6CF0\u4E43\u5F03\u5B98\u53BB\uFF0C\u8C13\u8340\u6538\u66F0\uFF1A\u201C\u4F55\u516C\u672A\u6613\u8F85\u4E5F\u3002\u201D</p></blockquote><p><strong>\u300A\u8D44\u6CBB\u901A\u9274 \xB7 \u5377\u4E94\u5341\u4E5D \xB7 \u6C49\u7EAA\u4E94\u5341\u4E00\u300B</strong></p><p>**\u628A\u6C49\u7075\u5E1D\u57CB\u4E86\u4EE5\u540E\uFF0C\u4F55\u8FDB\u548C\u5BA6\u5B98\u4EEC\u7684\u6597\u4E89\u8FDB\u5165\u4E86\u80F6\u7740\u72B6\u6001\u3002**\u4E4B\u524D\u8E47\u7855\u8BBE\u8BA1\u8981\u5728\u5BAB\u91CC\u6740\u4ED6\uFF0C\u867D\u7136\u540E\u6765\u88AB\u8BC6\u7834\u4E86\uFF0C\u4F46\u4F55\u8FDB\u8FD8\u662F\u88AB\u5413\u5F97\u4E09\u9B42\u4E03\u9B44\u4E22\u4E86\u4E00\u534A\uFF0C\u751A\u81F3\u4E8E\u8FDE\u7075\u5E1D\u7684\u846C\u793C\u90FD\u6CA1\u6562\u53C2\u52A0\uFF0C\u751F\u6015\u53BB\u516B\u5B9D\u5C71\u5F00\u8FFD\u60BC\u4F1A\u7684\u65F6\u5019\uFF0C\u522B\u4EBA\u53CD\u5012\u628A\u81EA\u5DF1\u7ED9\u70E7\u4E86\u3002</p><p>**\u4E00\u65F6\u65E0\u63AA\uFF0C\u4F55\u8FDB\u4FBF\u95EE\u8BA1\u4E8E\u8881\u7ECD\u3002**\u8881\u7ECD\u5F88\u4E0D\u559C\u6B22\u5BA6\u5B98\uFF0C\u81EA\u7136\u662F\u79EF\u6781\u5730\u64BA\u6387\u7740\u4F55\u8FDB\u6740\u5149\u6240\u6709\u7684\u5BA6\u5B98\u3002\u4E24\u4EBA\u4E00\u756A\u8BAE\u5B9A\uFF0C\u4F55\u8FDB\u5C31\u51C6\u5907\u8FDB\u5BAB\u53BB\u7ED9\u59B9\u59B9\u4F55\u592A\u540E\u505A\u5DE5\u4F5C\u3002</p><p>\u4F46\u4F55\u592A\u540E\u4E0D\u540C\u610F\u3002\u4E00\u6765\uFF0C\u5979\u89C9\u5F97\u628A\u5BA6\u5B98\u5168\u6740\u6389\u52A8\u9759\u592A\u5927\uFF1B\u4E8C\u6765\uFF0C\u5979\u4E0D\u6B62\u4F55\u8FDB\u4E00\u4E2A\u54E5\u54E5\uFF0C\u4ED6\u7684\u53E6\u4E00\u4E2A\u54E5\u54E5\u4F55\u82D7\u4EE5\u53CA\u4ED6\u4EEC\u7684\u5988\u6536\u4E86\u5BA6\u5B98\u7684\u597D\u5904\uFF0C\u6781\u529B\u53CD\u5BF9\u4F55\u8FDB\u7684\u8BA1\u5212\u3002</p><p>\u4F55\u8FDB\u81EA\u5DF1\u662F\u4E2A\u6CA1\u6CE8\u610F\u7684\u8D27\uFF0C\u65E2\u7136\u592A\u540E\u59B9\u59B9\u4E0D\u540C\u610F\uFF0C\u90A3\u5C31\u6298\u4E2D\u4E00\u4E0B\u5427\uFF1A\u5148\u6740\u6389\u9996\u6076\uFF0C\u5904\u6B7B\u51E0\u4E2A\u6700\u4E3A\u8DCB\u6248\u7684\u5BA6\u5B98\u3002</p><p>\u4F46\u8881\u7ECD\u53C8\u4E0D\u540C\u610F\u4E86\uFF0C\u8FD9\u4E9B\u4EBA\u91CE\u706B\u70E7\u4E0D\u5C3D\u6625\u98CE\u5439\u53C8\u751F\uFF0C\u6740\u4E86\u8FD9\u4E00\u6279\uFF0C\u8FD8\u4F1A\u6709\u4E0B\u4E00\u6CE2\u50F5\u5C38\u7EE7\u7EED\u63A5\u8FD1\u7684\uFF01</p><p>\u60F3\u60F3\u4E5F\u5BF9\uFF0C\u4F55\u8FDB\u53C8\u5F00\u59CB\u8FF7\u832B\u4E86\u3002\u8FD9\u65F6\uFF0C\u667A\u591A\u661F\u8881\u7ECD\u53C8\u7ED9\u4ED6\u51FA\u4E86\u4E2A\u4E3B\u610F\uFF1A<strong>\u6211\u4EEC\u53EF\u4EE5\u53EC\u5730\u65B9\u7684\u5B9E\u529B\u6D3E\u8FDB\u4EAC\uFF0C\u5A01\u80C1\u592A\u540E\uFF0C\u770B\u5979\u6562\u4E0D\u4ECE\u547D\uFF01</strong></p><p>\u4F55\u8FDB\u89C9\u5F97\u8FD9\u4E2A\u529E\u6CD5\u7B80\u76F4\u592A\u5999\u4E86\uFF0C\u5F53\u5373\u5BA3\u53EC\u8463\u5353\u5E26\u5175\u8FDB\u4EAC\u3002</p><p>\u96BE\u9053\u5C31\u6CA1\u6709\u4EBA\u8868\u793A\u53CD\u5BF9\u5417\uFF1F</p><p><strong>\u5F53\u7136\u6709\uFF0C\u800C\u4E14\u8FD8\u4E0D\u5C11\u3002</strong></p><p>\u6BD4\u5982\u4E3B\u8584\u9648\u7433\u5C31\u8BA4\u4E3A\uFF0C\u60A8\u5802\u5802\u5927\u5C06\u519B\uFF0C\u505A\u4E8B\u4E0D\u8D70\u6B63\u9053\uFF0C\u53CD\u5012\u662F\u5C3D\u60F3\u8FD9\u4E9B\u5751\u8499\u62D0\u9A97\u7684\u5C0F\u4F0E\u4FE9.</p><p>\u6B64\u5916\uFF0C\u66F9\u64CD\u3001\u90D1\u6CF0\u3001\u5362\u690D\u7B49\u4EBA\u4E5F\u7EB7\u7EB7\u529D\u8BF4\u4F55\u8FDB\uFF0C<code>\u4F46\u7686\u65E0\u6548\u679C</code>\u3002</p><p>\u8463\u5353\u4E00\u63A5\u5230\u547D\u4EE4\uFF0C\u4E00\u53CD\u5E38\u6001\uFF0C\u9A6C\u4E0A\u51FA\u53D1\u3002</p><p>\u4E3A\u4EC0\u4E48\u8BF4\u8463\u5353\u662F\u4E00\u53CD\u5E38\u6001\u5462\uFF1F<strong>\u56E0\u4E3A\u8463\u5353\u5DF2\u7ECF\u5F88\u4E45\u6CA1\u6709\u542C\u4ECE\u671D\u5EF7\u7684\u653F\u4EE4\u4E86\u3002</strong></p><p>\u7075\u5E1D\u6D3B\u7740\u7684\u65F6\u5019\uFF0C\u773C\u89C1\u8463\u5353\u62E5\u5175\u81EA\u91CD\uFF0C\u5C3E\u5927\u4E0D\u6389\uFF0C\u4FBF\u53EC\u8463\u5353\u5165\u671D\u4E3A\u5B98\uFF0C\u4EE5\u524A\u5176\u5175\u6743\u3002\u4F46\u8463\u5353\u5374\u4E0A\u4E86\u4E00\u5C01\u594F\u6298\uFF0C\u62D2\u7EDD\u63A5\u53D7\u3002\u5BF9\u6B64\uFF0C\u671D\u5EF7\u6BEB\u65E0\u529E\u6CD5\u3002</p><p>\u5230\u4E86\u7075\u5E1D\u75C5\u91CD\u7684\u65F6\u5019\uFF0C\u671D\u5EF7\u53C8\u4E0B\u4E86\u4E00\u5C01\u8BCF\u4E66\uFF0C\u4EFB\u547D\u8EAB\u5728\u51C9\u5DDE\u7684\u8463\u5353\u4E3A\u5E76\u5DDE\u7267\uFF0C\u540C\u65F6\u5C06\u9EBE\u4E0B\u58EB\u5175\u4EA4\u7ED9\u7687\u752B\u5D69\u6307\u6325\u3002\u4F46\u8463\u5353\u6839\u672C\u4E0D\u542C\uFF0C\u5E26\u7740\u90E8\u961F\u5C31\u5230\u4E86\u5E76\u5DDE\u8D74\u4EFB\u3002</p><p>**\u5F53\u65F6\u5C31\u6709\u4EBA\u5BF9\u8463\u5353\u7684\u884C\u4E3A\u6DF1\u8868\u5FE7\u8651\uFF0C\u5E76\u529D\u7687\u752B\u5D69\u7387\u5175\u653B\u6253\u8463\u5353\uFF0C\u4EE5\u7EDD\u540E\u60A3\u3002**\u4ECE\u4E8B\u540E\u770B\u6765\uFF0C\u8FD9\u7EDD\u5BF9\u662F\u4E00\u756A\u5B9A\u56FD\u5B89\u90A6\u7684\u8001\u6210\u8C0B\u56FD\u4E4B\u8A00\uFF0C\u4F46\u5728\u5F53\u65F6\uFF0C\u7687\u752B\u5D69\u5374\u89C9\u5F97\uFF1A\u8463\u5353\u6297\u4EE4\u4E0D\u5C0A\uFF0C\u671D\u5EF7\u90FD\u6CA1\u4EC0\u4E48\u52A8\u9759\uFF0C\u6211\u6025\u543C\u543C\u7684\u53BB\u6253\u4ED6\uFF0C\u8FD9\u4E0D\u662F\u843D\u4EBA\u53E3\u5B9E\uFF0C\u7ED9\u81EA\u5DF1\u627E\u9EBB\u70E6\u4E48\uFF1F\u4E8E\u662F\u62D2\u7EDD\u4E86\u3002</p><p>\u5C31\u8FD9\u6837\uFF0C\u5927\u5BB6\u4F19\u773C\u7741\u7741\u5730\u770B\u7740\u8463\u5353\u628A\u4E00\u652F\u8FB9\u7586\u90E8\u961F\u5E26\u5230\u4E86\u4E2D\u539F\u3002\u5E76\u5DDE\u7267\u6CBB\u6240\u5728\u592A\u539F\uFF0C\u5927\u5BB6\u53EF\u4EE5\u5728\u5730\u56FE\u4E0A\u91CF\u4E00\u4E0B\uFF0C\u5230\u6D1B\u9633\u53EA\u6709\u4E09\u767E\u591A\u516C\u91CC\u3002</p><p>\u8463\u5353\u4E09\u6B65\u5E76\u4F5C\u4E24\u6B65\u5730\u5F80\u6D1B\u9633\u8D76\uFF0C\u4E2D\u9014\u987A\u4FBF\u8FD8\u4E0A\u4E86\u4E00\u5C01\u594F\u6298\uFF0C\u5185\u5BB9\u81EA\u7136\u662F\u4EC0\u4E48\u5BA6\u5B98\u7978\u56FD\uFF0C\u6211\u613F\u4E0D\u8F9E\u8F9B\u82E6\uFF0C\u809D\u8111\u6D82\u5730\uFF0C\u8FDB\u4EAC\u62EF\u6551\u7687\u4E0A\u548C\u592A\u540E\uFF01\u5F53\u7136\uFF0C\u8FD9\u662F\u6211\u4E00\u4E2A\u81E3\u5B50\u7684\u672C\u5206\uFF0C\u4F60\u4EEC\u4E5F\u4E0D\u7528\u5BF9\u6211\u592A\u591A\u611F\u8C22\uFF01</p><p>\u6536\u5230\u4E0A\u4E66\u4EE5\u540E\uFF0C\u4F55\u592A\u540E\u548C\u4EB2\u4FE1\u4EEC\u624D\u77E5\u9053\u8463\u5353\u5DF2\u7ECF\u5728\u62AB\u661F\u6234\u6708\u7684\u5F80\u4EAC\u57CE\u91CC\u8D76\u4E86\u3002\u4F55\u8FDB\u7684\u5F1F\u5F1F\u4F55\u82D7\u8D76\u7D27\u82E6\u53E3\u5A46\u5FC3\u5730\u529D\u4F55\u8FDB\uFF0C\u8BF4\u6211\u4EEC\u672C\u6765\u5C31\u53EA\u662F\u5357\u9633\u7684\u5C60\u592B\uFF0C\u662F\u9760\u4E86\u5BA6\u5B98\u7684\u5E2E\u5FD9\u624D\u5F97\u4EE5\u6709\u4ECA\u5929\u7684\u5730\u4F4D\uFF0C\u8F7B\u6613\u7FFB\u8138\uFF0C\u6015\u662F\u8986\u6C34\u96BE\u6536\u554A\uFF01</p><p>\u4F55\u8FDB\u4E00\u542C\uFF0C\u679C\u7136\u53C8\u72B9\u8C6B\u4E86\uFF0C\u4FBF\u6D3E\u4E86\u4E2A\u4EBA\uFF0C\u53BB\u62E6\u8463\u5353\u3002</p><p>\u4F46\u6CA1\u60F3\u5230\u521A\u628A\u8463\u5353\u62E6\u4E0B\u6765\uFF0C\u4F55\u8FDB\u8FD9\u8FB9\u4E3B\u610F\u53C8\u53D8\u4E86\u3002\u539F\u56E0\u662F\u8881\u7ECD\u5F97\u77E5\u4E86\u4F55\u8FDB\u7684\u72B9\u8C6B\uFF0C\u4E8E\u662F\u8DD1\u8FC7\u6765\u6050\u5413\u4ED6\uFF0C\u8BF4\u5F53\u65AD\u4E0D\u65AD\u5FC5\u53D7\u5176\u4E71\uFF01\u4E8B\u60C5\u90FD\u5230\u8FD9\u79CD\u5730\u6B65\u4E86\uFF0C\u5BA6\u5B98\u600E\u4E48\u53EF\u80FD\u8FD8\u4F1A\u8BB2\u548C\uFF0C\u4E0D\u662F\u4F60\u6B7B\u5C31\u662F\u6211\u4EA1\uFF01</p><p>\u4F55\u8FDB\u4E00\u60F3\u4E5F\u5BF9\uFF0C\u5C31\u53C8\u6D3E\u4E86\u4E00\u4E2A\u4EBA\u53BB\u50AC\u8463\u5353\u3002</p><p>\u51E0\u5929\u4E4B\u95F4\uFF0C\u8463\u5353\u5148\u540E\u6536\u5230\u4E24\u5C01\u5185\u5BB9\u622A\u7136\u76F8\u53CD\u7684\u547D\u4EE4\uFF0C\u9AD8\u5EA6\u6000\u7591\u6D1B\u9633\u57CE\u91CC\u5DF2\u7ECF\u53D1\u751F\u4E86\u91CD\u5927\u53D8\u6545\u3002\u8FD9\u53EF\u771F\u662F\u673A\u4E0D\u53EF\u5931\u554A\uFF01<strong>\u8463\u5353\u4E22\u4E0B\u6B65\u5175\uFF0C\u5E26\u7740\u4E09\u5343\u9A91\u5175\u6025\u5954\u5230\u4E86\u6D1B\u9633\u3002</strong></p><p>\u7740\u6025\u7684\u4E0D\u6B62\u8463\u5353\u4E00\u4E2A\u4EBA\u3002</p><p>\u773C\u770B\u7740\u8463\u5353\u5373\u5C06\u8FDB\u4EAC\uFF0C<strong>\u8881\u7ECD\u89C9\u5F97\u5927\u4E8B\u5C06\u5B9A\uFF0C\u5F00\u59CB\u7740\u624B\u51C6\u5907\u76F8\u5173\u4E8B\u5B9C</strong>\uFF0C\u751A\u81F3\u4E8E\u901A\u77E5\u5730\u65B9\uFF1A\u9884\u5907\u597D\u6293\u6355\u5BA6\u5B98\u4EEC\u7684\u5BB6\u5C5E\u5427\uFF01</p><p>\u540C\u65F6\uFF0C\u4ED6\u64BA\u6387\u7740\u4F55\u8FDB\u518D\u6B21\u8FDB\u5BAB\u529D\u544A\u4F55\u592A\u540E\uFF0C\u8BF7\u6C42\u8BDB\u6740\u5BA6\u5B98\u3002</p><p>\u4E8B\u6001\u4ECE\u6B64\u6025\u8F6C\u76F4\u4E0B\u3002</p><p>\u4F55\u8FDB\u5BF9\u4F55\u592A\u540E\u529D\u8BEB\u672A\u679C\uFF0C\u53CD\u5012\u662F\u88AB\u5BA6\u5B98\u4EEC\u5077\u542C\u5230\u4E86\u5BF9\u8BDD\u7684\u5185\u5BB9\u3002\u4E00\u756A\u5BC6\u8C0B\u4E4B\u540E\uFF0C\u5BA6\u5B98\u4EEC\u51B3\u5B9A\u5148\u53D1\u5236\u4EBA\u3002</p><p>\u8FD9\u91CC\u6211\u4EEC\u8981\u56DE\u5FC6\u4E00\u4E0B<strong>\u7B2C\u4E8C\u6B21\u515A\u9522\u4E4B\u7978\uFF1A</strong></p><p>\u5F53\u65F6\uFF0C\u7AA6\u6B66\u51FA\u5BAB\u4EE5\u540E\uFF0C\u5BA6\u5B98\u628A\u63E1\u673A\u4F1A\uFF0C\u88F9\u631F\u7AA6\u592A\u540E\u4E0E\u7075\u5E1D\uFF0C\u53D1\u8BCF\u4E66\u5230\u5BAB\u5916\uFF0C\u58F0\u79F0\u7AA6\u6B66\u9020\u53CD\uFF0C\u53F7\u4EE4\u519B\u961F\u8D77\u5175\u52E4\u738B\u3002</p><p>\u7AA6\u6B66\u505A\u5927\u5C06\u519B\u7684\u65F6\u65E5\u5C1A\u77ED\uFF0C\u53EA\u80FD\u773C\u7741\u7741\u770B\u7740\u519B\u961F\u88AB\u5BA6\u5B98\u9A97\u8D70\uFF0C\u800C\u81EA\u5DF1\u8EB2\u5728\u81EA\u5DF1\u4F84\u5B50\u7684\u5175\u8425\u91CC\uFF0C\u505A\u7684\u5168\u662F\u65E0\u8C13\u7684\u53CD\u6297\u3002\u4ED6\u7684\u201C\u6218\u53CB\u201D\u2014\u2014\u592A\u5085\u9648\u8543\u4E5F\u65E0\u80FD\u4E3A\u529B\uFF0C\u867D\u7136\u5E26\u7740\u81EA\u5DF1\u7684\u5B66\u751F\u548C\u5B98\u5C5E\u516B\u5341\u591A\u4EBA\u52A0\u5165\u4E86\u6218\u6597\uFF0C\u4F46\u662F\u676F\u6C34\u8F66\u85AA\uFF0C\u5F88\u5FEB\u4FBF\u88AB\u6740\u3002</p><p>\u8FD9\u6B21\uFF0C\u5BA6\u5B98\u4EEC\u8FD8\u60F3\u7528\u8FD9\u4E2A\u5957\u8DEF\u3002</p><p>\u7406\u8BBA\u4E0A\u6765\u8BF4\uFF0C\u5BA6\u5B98\u4EEC\u6210\u529F\u7684\u51E0\u7387\u5F88\u5927\u3002\u56E0\u4E3A\u867D\u7136\u58EB\u5927\u592B\u5728\u671D\u5802\u4E4B\u4E0A\u5360\u636E\u4E86\u7EDD\u5BF9\u4F18\u52BF\uFF0C\u4F46\u7687\u5E1D\u548C\u592A\u540E\u53EF\u5168\u5728\u5BAB\u91CC\u3002\u53EA\u8981\u8BCF\u4E66\u4E00\u53D1\uFF0C\u6D1B\u9633\u57CE\u7684\u519B\u961F\u5C31\u57FA\u672C\u4E0A\u80FD\u5168\u90E8\u593A\u8FC7\u6765\u3002\u800C\u53EA\u8981\u63A7\u5236\u4E86\u519B\u961F\uFF0C\u4E00\u7FA4\u6587\u4EBA\u8FD8\u80FD\u6709\u4EC0\u4E48\u51FA\u606F\u3002</p><p>\u552F\u4E00\u7684\u95EE\u9898\u5728\u4E8E\u4F55\u8FDB\u3002\u4F55\u8FDB\u79EF\u7834\u9EC4\u5DFE\u4E4B\u529F\uFF0C\u5728\u519B\u961F\u4E2D\u6709\u6781\u5927\u7684\u5F71\u54CD\u529B\u3002<strong>\u4F46\u662F\uFF01</strong> \u504F\u504F\u5C31\u662F\u8FD9\u4E48\u5DE7\uFF0C\u8FD9\u4E2A\u65F6\u5019\uFF0C\u4F55\u8FDB\u5C45\u7136\u521A\u597D\u5728\u5BAB\u91CC\u3002\u597D\u673A\u4F1A\u5343\u8F7D\u96BE\u9022\uFF0C\u5BA6\u5B98\u4EEC\u4E8E\u662F\u76F4\u63A5\u6740\u4E86\u4F55\u8FDB\uFF0C\u7136\u540E\u4F2A\u9020\u8BCF\u4E66\uFF0C\u8FDB\u884C\u4EBA\u4E8B\u4EFB\u547D\u3002</p><p>\u53EA\u662F\u8FD9\u5C01\u8BCF\u4E66\u5F15\u8D77\u4E86\u5C1A\u4E66\u4EEC\u7684\u6000\u7591\uFF0C\u4ED6\u4EEC\u8981\u6C42\u89C1\u5230\u4F55\u8FDB\u672C\u4EBA\uFF0C\u4E00\u95EE\u771F\u4F2A\u3002\u5BF9\u6B64\uFF0C\u5BA6\u5B98\u4EEC\u7684\u56DE\u5E94\u5145\u6EE1\u4E86\u884C\u4E3A\u827A\u672F\u7684\u98CE\u683C\uFF1A\u4ED6\u4EEC\u9694\u7740\u5BAB\u5899\uFF0C\u628A\u4F55\u8FDB\u7684\u4EBA\u5934\u4E22\u4E86\u51FA\u53BB\uFF0C\u8BF4\uFF0C\u4F55\u8FDB\u56E0\u8C0B\u53CD\u88AB\u6740\u4E86\u3002</p><p>**\u4F46\u6B64\u65F6\u548C\u7075\u5E1D\u521D\u5E74\u65E9\u5DF2\u4E0D\u540C\u3002**\u5BAB\u5916\uFF0C\u4F55\u8FDB\u7684\u90E8\u5C5E\u95FB\u77E5\u8FD9\u4E00\u6D88\u606F\u540E\uFF0C\u968F\u5373\u4E0E\u8881\u672F\u5408\u5175\uFF0C\u653B\u6253\u7687\u5BAB\u3002\u6CA1\u591A\u4E45\uFF0C\u8881\u7ECD\u4E5F\u7387\u5175\u8D76\u5230\uFF0C\u653B\u7834\u5BAB\u95E8\uFF0C\u5C06\u7559\u5728\u5BAB\u91CC\u7684\u5BA6\u5B98\u5168\u90E8\u8BDB\u6740\u3002\u671F\u95F4\u6709\u5C0F\u80A1\u5BA6\u5B98\u8D81\u4E71\u5E26\u7740\u5C11\u5E1D\u5218\u8FA9\u548C\u9648\u7559\u738B\u5218\u534F\u9003\u8D70\uFF0C\u4F46\u6700\u7EC8\u8D70\u6295\u65E0\u8DEF\uFF0C\u5BA6\u5B98\u9996\u9886\u5F20\u8BA9\u81EA\u6740\uFF0C\u7FA4\u81E3\u62A4\u9001\u7740\u5218\u8FA9\u4E0E\u5218\u534F\u56DE\u4EAC\u3002</p><p><strong>\u8DEF\u4E0A\uFF0C\u4ED6\u4EEC\u78B0\u5230\u4E86\u8D76\u6765\u7684\u8463\u5353\u3002</strong></p><p>\u5176\u540E\u7684\u6545\u4E8B\uFF0C\u5C31\u548C\u300A\u4E09\u56FD\u6F14\u4E49\u300B\u4E2D\u7684\u5DEE\u4E0D\u591A\u4E86\uFF1A\u8463\u5353\u62A4\u9001\u5C11\u5E1D\u56DE\u4EAC\uFF0C\u5176\u540E\uFF0C\u79C1\u4E0B\u91CC\u4E70\u901A\u5415\u5E03\uFF0C\u523A\u6B7B\u4E01\u539F\uFF08\u4E01\u539F\u65F6\u4EFB\u6267\u91D1\u543E\uFF0C\u76F8\u5F53\u4E8E\u5982\u4ECA\u7684\u5317\u4EAC\u536B\u620D\u533A\u53F8\u4EE4\u5458\uFF09\u3002\u968F\u5373\uFF0C<strong>\u8463\u5353\u5E9F\u5218\u8FA9\u4E3A\u5F18\u519C\u738B\uFF0C\u7ACB\u5218\u534F\u4E3A\u5E1D\uFF0C\u662F\u4E3A\u6C49\u732E\u5E1D\u3002</strong></p><p>\u56E0\u5E9F\u7ACB\u65F6\u7684\u4E00\u756A\u4E89\u6267\uFF0C\u4E3A\u9632\u6B62\u8463\u5353\u52A0\u5BB3\uFF0C\u8881\u7ECD\u9003\u5F80\u5180\u5DDE\u3001\u8881\u672F\u51FA\u5954\u5357\u9633\u3001\u66F9\u64CD\u56DE\u5230\u9648\u7559\u3002</p><p>\u662F\u5E74\u4E3A\u4E2D\u5E73\u516D\u5E74\uFF08\u516C\u5143189\u5E74\uFF09\u3002\u6B21\u5E74\uFF08\u521D\u5E73\u5143\u5E74\uFF0C\u516C\u5143190\u5E74\uFF09\uFF0C\u201C\u6625\uFF0C\u6B63\u6708\uFF0C<strong>\u5173\u4E1C\u5DDE\u90E1\u7686\u8D77\u5175\u4EE5\u8BA8\u8463\u5353</strong>\u201D\u3002</p><p>\u56DE\u6EAF\u8463\u5353\u8FDB\u4EAC\u7684\u6574\u4E2A\u8FC7\u7A0B\uFF0C<code>\u8881\u7ECD\u8D77\u5230\u4E86\u4E3E\u8DB3\u8F7B\u91CD\u7684\u4F5C\u7528</code>\u3002\u4F55\u8FDB\u6027\u683C\u4F18\u67D4\u5BE1\u65AD\uFF0C\u57FA\u672C\u4E0A\u9047\u5230\u91CD\u8981\u7684\u51B3\u7B56\u65F6\uFF0C\u90FD\u662F\u5728\u88AB\u8881\u7ECD\u63A8\u7740\u8D70\u3002</p><p>\u8881\u7ECD\u5F53\u65F6\u867D\u7136\u5927\u7EA6\u4E0D\u5230\u56DB\u5341\u5C81\uFF0C\u4F46\u56E0\u51FA\u8EAB\u9AD8\u8D35\uFF0C\u52C7\u4E8E\u4EFB\u4E8B\uFF0C\u5DF2\u7ECF\u505A\u5230\u4E86\u53F8\u96B6\u6821\u5C09\u3002\u4F4D\u9AD8\u6743\u91CD\uFF0C\u81EA\u7136\u5BF9\u671D\u5EF7\u62B1\u6709\u5DE8\u5927\u7684\u8D23\u4EFB\u611F\uFF0C\u8FD9\u5E94\u8BE5\u662F\u8881\u7ECD\u79EF\u6781\u63A8\u52A8\u4F55\u8FDB\u94F2\u9664\u5BA6\u5B98\u7684\u4E3B\u8981\u539F\u56E0\u3002</p><p>\u4F46\u4E24\u4EBA\u60F3\u7684\u8FD9\u4E2A\u529E\u6CD5\uFF0C\u5374\u5B9E\u5728\u662F\u4E0D\u600E\u4E48\u6837\u3002</p><p>\u4F55\u592A\u540E\u4E0D\u540C\u610F\u8BDB\u6740\u5BA6\u5B98\uFF0C\u4E24\u4EBA\u7ADF\u7136\u60F3\u51FA\u4E86\u53EC\u5730\u65B9\u519B\u9600\u8FDB\u4EAC\u7684\u660F\u62DB\u3002\u5728\u4E1C\u6C49\u7684\u653F\u6CBB\u4F53\u7CFB\u4E2D\uFF0C\u592A\u540E\u4E8B\u5B9E\u4E0A\u62E5\u6709\u6700\u7EC8\u6743\u5A01\uFF0C\u4F55\u8FDB\u548C\u8881\u7ECD\u6B63\u662F\u56E0\u4E3A\u5B9E\u5728\u8FC8\u4E0D\u8FC7\u8FD9\u9053\u574E\uFF0C\u6240\u4EE5\u624D\u51FA\u6B64\u4E0B\u7B56\uFF0C\u4ED6\u4EEC\u5E0C\u671B\u7528\u8463\u5353\u6765\u5236\u8861\u4F55\u592A\u540E\u3002\u4F46\u4ED6\u4EEC\u5374\u4F3C\u4E4E\u6CA1\u6709\u60F3\u8FC7\uFF0C\u65E2\u7136\u592A\u540E\u8FD9\u79CD\u6700\u7EC8\u6743\u5A01\u90FD\u80FD\u591F\u81E3\u670D\u4E8E\u8463\u5353\uFF0C\u90A3\u4E48\uFF0C\u4ED6\u4EEC\u5C06\u6765\u53C8\u9760\u4EC0\u4E48\u6765\u5236\u8861\u8463\u5353\u5462\uFF1F\u8BF4\u5F97\u518D\u76F4\u767D\u70B9\uFF0C<strong>\u65E2\u7136\u8463\u5353\u80FD\u591F\u80C1\u8FEB\u4F55\u592A\u540E\uFF0C\u90A3\u8C01\u80FD\u6765\u80C1\u8FEB\u8463\u5353\u5462\uFF1F</strong></p><p>**\u4E24\u4EBA\u5BF9\u4E8E\u81EA\u5DF1\u7684\u80FD\u529B\uFF0C\u90FD\u8FC7\u4E8E\u6258\u5927\u3002**\u4ED6\u4EEC\u770B\u4E2D\u4E86\u8463\u5353\u624B\u4E0B\u519B\u961F\u7684\u5B9E\u529B\uFF0C\u5374\u5FFD\u7565\u4E86\u8463\u5353\u8FD9\u4E9B\u519B\u961F\u7684\u7531\u6765\uFF1A\u98DE\u626C\u8DCB\u6248\u7684\u8463\u5353\uFF0C\u4E0D\u6B63\u662F\u56E0\u4E3A\u65E9\u5C31\u4E0D\u542C\u671D\u5EF7\u7684\u53F7\u4EE4\uFF0C\u624D\u80FD\u6709\u8FD9\u4E48\u591A\u7684\u79C1\u5175\u5417\uFF1F<strong>\u5BF9\u4E8E\u8FD9\u79CD\u5FC3\u6000\u5F02\u5FD7\u7684\u5B9E\u529B\u6D3E\uFF0C\u4F60\u4EEC\u7ADF\u7136\u4F1A\u89C9\u5F97\u81EA\u5DF1\u80FD\u53EC\u4E4B\u5373\u6765\u6325\u4E4B\u5373\u53BB\uFF0C\u7A76\u7ADF\u662F\u4ECE\u54EA\u91CC\u6765\u7684\u81EA\u4FE1\uFF01</strong></p><p>\u4F55\u8FDB\u548C\u8881\u7ECD\u4E5F\u662F\u540C\u7406\uFF0C<strong>\u8EAB\u5904\u9669\u5C40\uFF0C\u5374\u81EA\u89C9\u5F62\u52BF\u7686\u5728\u638C\u63E1\u4E4B\u4E2D\u3002\u76F4\u5230\u5927\u4E8B\u4E34\u5934\uFF0C\u624D\u53D1\u73B0\u539F\u6765\u53EA\u662F\u81EA\u5DF1\u7684\u4E00\u53A2\u60C5\u613F\u3002</strong></p><p>\u6211\u4EEC\u56DE\u5934\u518D\u6765\u770B\u770B\u4F55\u8FDB\u548C\u8881\u7ECD\u7684\u8C0B\u5212\u3002</p><p><strong>\u76EE\u7684</strong>\uFF1A\u4F55\u592A\u540E\u540C\u610F\u8BDB\u6740\u5BA6\u5B98\u3002</p><p><strong>\u624B\u6BB5</strong>\uFF1A\u53EC\u8463\u5353\u8FDB\u4EAC\uFF0C\u80C1\u8FEB\u4F55\u592A\u540E\u3002</p><blockquote><p>\u5982\u679C\u60F3\u8981\u8FBE\u6210\u9884\u671F\u76EE\u6807\uFF0C\u9700\u8981\u7684\u6761\u4EF6\u6709\uFF1A</p></blockquote><p>1\u3001\u8463\u5353\u613F\u610F\u5949\u8BCF\u8FDB\u4EAC\uFF1B 2\u3001\u5BA6\u5B98\u5728\u6B64\u671F\u95F4\u5750\u4EE5\u5F85\u6BD9\uFF1B 3\u3001\u4F55\u592A\u540E\u9762\u5BF9\u519B\u4E8B\u5A01\u80C1\u9009\u62E9\u59A5\u534F\uFF1B 4\u3001\u8463\u5353\u8FBE\u6210\u76EE\u7684\u540E\u5949\u547D\u64A4\u9000\u3002</p>',54),e=[s];function g(c,a){return o(),t("div",null,e)}const l=p(n,[["render",g],["__file","5.html.vue"]]);export{l as default};
