import{_ as p}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as e,c as t,f as a}from"./app.eee000b7.js";const c={},o=a('<h1 id="\u7B2C53\u96C6-\u5468\u79C9\u6606\u5F00\u4FDD\u6D01\u516C\u53F8" tabindex="-1"><a class="header-anchor" href="#\u7B2C53\u96C6-\u5468\u79C9\u6606\u5F00\u4FDD\u6D01\u516C\u53F8" aria-hidden="true">#</a> \u7B2C53\u96C6\uFF1A\u5468\u79C9\u6606\u5F00\u4FDD\u6D01\u516C\u53F8</h1><p>\u5468\u79C9\u4E49\u548C\u5468\u79C9\u6606\u8D70\u4E86\u4E00\u8DEF\uFF0C\u4EE5\u524D\u8FD9\u91CC\u7684\u80E1\u540C\u4EE5\u4EC1\u4E49\u793C\u667A\u4FE1\u4E3A\u540D\uFF0C\u53EF\u73B0\u5728\u5230\u5904\u90FD\u662F\u79C1\u642D\u4E71\u5EFA\uFF0C\u5468\u79C9\u4E49\u611F\u6168\u4E07\u5343\u3002\u5468\u79C9\u6606\u89C9\u5F97\u90FD\u662F\u751F\u6D3B\u6240\u8FEB\uFF0C\u5927\u5BB6\u5F97\u6709\u5730\u65B9\u4F4F\uFF0C\u767E\u59D3\u6709\u82E6\u96BE\u8A00\u3002\u5468\u79C9\u4E49\u96BE\u8FC7\u81EA\u5DF1\u7ED9\u5468\u5BB6\u4E22\u4EBA\u4E86\uFF0C\u5B98\u505A\u7684\u5F88\u5927\uFF0C\u53EF\u662F\u5149\u5B57\u7247\u8FD8\u662F\u8001\u6837\u5B50\u3002\u4E4B\u6240\u4EE5\u9009\u62E9\u4E0B\u96E8\u5929\u51FA\u6765\uFF0C\u5468\u79C9\u4E49\u5C31\u662F\u60F3\u770B\u770B\u6700\u5DEE\u7684\u65F6\u5019\u80FD\u5DEE\u5230\u4EC0\u4E48\u5730\u6B65\uFF0C\u8BA9\u81EA\u5DF1\u4E0D\u518D\u6709\u7406\u7531\u7EE7\u7EED\u89C6\u800C\u4E0D\u89C1\u3002\u5468\u79C9\u4E49\u4E0B\u72E0\u5FC3\u8981\u6539\u53D8\u8FD9\u91CC\u3002</p><p>\u5468\u79C9\u6606\u559D\u591A\u4E86\uFF0C\u88AB\u5468\u79C9\u4E49\u9001\u56DE\u6765\uFF0C\u90D1\u5A1F\u63D0\u51FA\u4E86\u4E00\u4E2A\u4E00\u76F4\u90FD\u60F3\u95EE\u5F97\u95EE\u9898\u3002\u5F53\u5E74\u4E3A\u4EC0\u4E48\u4E0D\u5E2E\u5468\u79C9\u6606\uFF0C\u5468\u79C9\u4E49\u544A\u8BC9\u90D1\u5A1F\u4ED6\u867D\u7136\u5F53\u5B98\uFF0C\u4F46\u662F\u4ED6\u4E0D\u80FD\u60F3\u5E72\u5565\u5C31\u5E72\u5565\uFF0C\u800C\u4E14\u4ED6\u7BA1\u4E0D\u4E86\u3002\u4F46\u5982\u679C\u6709\u4E00\u5929\u5FC5\u987B\u5144\u5F1F\u4FE9\u6B7B\u4E00\u4E2A\uFF0C\u4ED6\u4F1A\u662F\u53BB\u6B7B\u7684\u90A3\u4E00\u4E2A\uFF0C\u90D1\u5A1F\u7684\u5FC3\u7ED3\u6253\u5F00\u4E86\uFF0C\u8FD8\u4E13\u95E8\u7ED9\u5468\u79C9\u4E49\u62FF\u4E86\u4E00\u4E2A\u624B\u7535\u7B52\u7167\u4EAE\u3002</p><p>\u521A\u51FA\u80E1\u540C\u53E3\uFF0C\u5468\u79C9\u4E49\u5C31\u88AB\u4E24\u4E2A\u4EBA\u6309\u7740\u62A2\u4E86\u624B\u673A\u94B1\u5305\u548C\u96E8\u9774\u3002\u90DD\u51AC\u6885\u60F3\u8BA9\u4ED6\u62A5\u8B66\uFF0C\u53EF\u662F\u5468\u79C9\u4E49\u89C9\u5F97\u5149\u5B57\u7247\u4F1A\u56E0\u6B64\u4E22\u4EBA\uFF0C\u56DE\u60F3\u4EE5\u524D\u7761\u89C9\u4E0D\u63D2\u95E8\uFF0C\u73B0\u5728\u5374\u6CBB\u5B89\u8FD9\u4E48\u5DEE\u3002\u90DD\u51AC\u6885\u89C9\u5F97\u5982\u679C\u4E0D\u62A5\u6848\u5C31\u4F1A\u52A9\u957F\u6B6A\u98CE\u90AA\u6C14\u3002\u5468\u79C9\u4E49\u5374\u542C\u51FA\u90A3\u4E24\u4E2A\u4EBA\u5E74\u9F84\u4E0D\u5927\uFF0C\u8BF4\u4E0D\u5B9A\u8BA4\u8BC6\uFF0C\u67E5\u4E0B\u53BB\u4E5F\u4E0D\u597D\uFF0C\u90FD\u662F\u751F\u6D3B\u6240\u8FEB\u3002\u5468\u79C9\u4E49\u6253\u7B97\u79C1\u5E95\u4E0B\u627E\u4EBA\u67E5\u67E5\uFF0C\u67E5\u51FA\u6765\u8BAD\u65A5\u4E00\u4E0B\u5F97\u4E86\u3002</p><p>\u8521\u6653\u5149\u4E5F\u63A5\u5230\u4E86\u9A6C\u4E3D\u4E3D\u7535\u8BDD\uFF0C\u90A3\u90E8\u300A\u6211\u4EEC\u8FD9\u4EE3\u513F\u5973\u300B\u8FD8\u662F\u7ED9\u4E86\u8521\u6653\u5149\u5BFC\u6F14\uFF0C\u8521\u6653\u5149\u610F\u5473\u6DF1\u957F\u770B\u7740\u5468\u84C9\uFF0C\u5174\u594B\u4E4B\u60C5\u6EA2\u4E8E\u8A00\u8868\u3002</p><p>\u5468\u79C9\u4E49\u548C\u5468\u84C9\u5546\u8BAE\u4E86\u5468\u79C9\u6606\u7684\u4E8B\u60C5\uFF0C\u4ED6\u89C9\u5F97\u5468\u79C9\u6606\u60F3\u8981\u505A\u70B9\u4E8B\uFF0C\u4E0D\u4EC5\u4EC5\u662F\u56E0\u4E3A\u60F3\u505A\u751F\u610F\uFF0C\u662F\u60F3\u8BC1\u660E\u4ED6\u4F9D\u7136\u662F\u90A3\u4E2A\u8BA9\u5927\u5BB6\u4F9D\u8D56\u7684\u5468\u79C9\u6606\u3002\u4ED6\u9700\u8981\u7684\u662F\u7CBE\u795E\u652F\u67F1\u3002\u8521\u6653\u5149\u542C\u8BF4\u4E4B\u540E\u7ACB\u5373\u53BB\u501F\u94B1\u7ED9\u5468\u79C9\u6606\uFF0C\u5468\u84C9\u521A\u9001\u4E86\u94B1\u79BB\u5F00\uFF0C\u90DD\u51AC\u6885\u53C8\u9001\u6765\u94B1\u3002\u5468\u79C9\u6606\u5FC3\u91CC\u975E\u5E38\u611F\u52A8\uFF0C\u4F46\u5468\u84C9\u9001\u4E86\u7684\u94B1\u5DF2\u7ECF\u8D85\u51FA\u4E86\u4E24\u4E07\uFF0C\u5BF9\u4E8E\u4EB2\u4EBA\u7684\u652F\u6301\u4ED6\u90FD\u8BB0\u5728\u5FC3\u91CC\uFF0C\u6709\u4E86\u8FD9\u4E9B\u94B1\uFF0C\u4FDD\u6D01\u516C\u53F8\u4E5F\u5F00\u59CB\u4E86\u3002</p><p>\u5409\u6625\u5E02\u9886\u5BFC\u73ED\u5B50\u4E5F\u5546\u8BAE\u4E0B\u4E00\u6B65\u5F00\u53D1\u7684\u533A\u57DF\uFF0C\u5973\u533A\u957F\u90CE\u7389\u8BA4\u4E3A\u9996\u5F53\u5176\u51B2\u5E94\u8BE5\u662F\u5149\u5B57\u7247\uFF0C\u73B0\u5728\u4E0D\u7BA1\u54EA\u91CC\u6539\u9020\u90FD\u9700\u8981\u94B1\uFF0C\u4F46\u8FD9\u4E2A\u4E0D\u662F\u6700\u96BE\u5F97\uFF0C\u6700\u96BE\u7684\u5C31\u662F\u5982\u4F55\u8BA9\u767E\u59D3\u4FE1\u670D\u3002</p><p>\u4E54\u6625\u71D5\u548C\u66F9\u5FB7\u5B9D\u7AD9\u5728\u697C\u4E0A\u770B\u7740\u5468\u79C9\u6606\u6210\u7ACB\u4E86\u516C\u53F8\uFF0C\u540E\u6094\u5F53\u521D\u6CA1\u6709\u628A\u94B1\u501F\u7ED9\u5468\u79C9\u6606\uFF0C\u5C24\u5176\u662F\u73B0\u5728\u5468\u79C9\u4E49\u8FD8\u6210\u4E86\u4ED6\u4EEC\u7684\u7236\u6BCD\u5B98\u3002\u66F9\u5FB7\u5B9D\u63D0\u8BAE\u5047\u88C5\u4E0D\u77E5\u9053\u7ED9\u9001\u8FC7\u53BB\u94B1\uFF0C\u4F46\u4E54\u6625\u71D5\u5374\u8BA4\u4E3A\u73B0\u5728\u9001\u8FC7\u53BB\u522B\u4EBA\u5C31\u4F1A\u8BF4\u662F\u56E0\u4E3A\u5468\u79C9\u4E49\u56DE\u6765\u5F53\u5B98\u4E86\u624D\u4E0A\u6746\u5B50\u5DF4\u7ED3\u3002\u66F9\u5FB7\u5B9D\u8D23\u602A\u4E54\u6625\u71D5\u5F53\u65F6\u5C31\u662F\u5934\u53D1\u957F\u89C1\u8BC6\u77ED\uFF0C\u4E24\u4EBA\u5546\u8BAE\u4EE5\u540E\u60F3\u529E\u6CD5\u5F25\u8865\uFF0C\u4E5F\u731C\u60F3\u5468\u79C9\u4E49\u56DE\u6765\u80AF\u5B9A\u8981\u6539\u9020\u68DA\u6237\u533A\u3002</p><p>\u5468\u79C9\u4E49\u7684\u68C0\u67E5\u62A5\u544A\u88AB\u90DD\u51AC\u6885\u53D1\u73B0\uFF0C\u90DD\u51AC\u6885\u6012\u4E0D\u53EF\u904F\u8BAD\u65A5\u5468\u79C9\u4E49\u9690\u7792\u60C5\u51B5\uFF0C\u5468\u79C9\u4E49\u4E0D\u6EE1\u90DD\u51AC\u6885\u73B0\u5728\u7684\u813E\u6C14\u8BF4\u53D1\u706B\u5C31\u53D1\u706B\u3002\u59DA\u7ACB\u677E\u6253\u7535\u8BDD\u7ED9\u5468\u79C9\u4E49\uFF0C\u63D0\u9192\u4ED6\u4EE5\u540E\u63A5\u53D7\u91C7\u8BBF\u7684\u65F6\u5019\u8EAB\u8FB9\u4E0D\u8981\u51FA\u73B0\u5973\u4EBA\uFF0C\u514D\u5F97\u88AB\u6709\u5FC3\u4EBA\u5229\u7528\u3002\u5468\u79C9\u4E49\u8FD9\u624D\u60F3\u8D77\u90DD\u51AC\u6885\u53EF\u80FD\u56E0\u4E3A\u8FD9\u751F\u6C14\uFF0C\u672C\u60F3\u597D\u597D\u54C4\u54C4\u3002\u53EF\u662F\u6CA1\u60F3\u5230\u90DD\u51AC\u6885\u53D1\u4E86\u66F4\u5927\u813E\u6C14\uFF0C\u8FD8\u81EA\u79F0\u6709\u66F4\u5E74\u671F\uFF0C\u6454\u4E86\u9065\u63A7\u5668\u3002</p><p>\u4E54\u6625\u71D5\u7ED9\u5468\u79C9\u6606\u6253\u7535\u8BDD\uFF0C\u544A\u8BC9\u4E86\u9F9A\u7EF4\u5219\u88AB\u6293\u8D77\u6765\u4E86\u3002\u5E38\u8FDB\u6B65\u8DD1\u6765\u627E\u5468\u79C9\u6606\uFF0C\u56E0\u4E3A\u90BB\u5C45\u8BA4\u5B9A\u4E86\u9F9A\u7EF4\u5219\u662F\u4E8E\u8679\u7684\u540E\u53F0\u3002\u5F53\u5E74\u4E24\u5BB6\u5C31\u56E0\u4E3A\u76D6\u623F\u5B50\u7684\u4E8B\u60C5\u751F\u6C14\u5435\u67B6\u3002\u9F9A\u7EF4\u5219\u5F53\u65F6\u8FD8\u8BA9\u8D76\u8D85\u62C6\u4E86\u623F\u5B50\uFF0C\u4F46\u662F\u540E\u6765\u5347\u804C\u4E4B\u540E\u53C8\u8BA9\u8D76\u8D85\u76D6\u8D77\u6765\u3002\u90BB\u5C45\u89C9\u5F97\u73B0\u5728\u9F9A\u7EF4\u5219\u4E0B\u53F0\u4E86\uFF0C\u5C31\u975E\u8981\u62C6\u4E86\u4E8E\u8679\u642D\u5EFA\u7684\u623F\u5B50\uFF0C\u5468\u79C9\u6606\u607C\u6012\u90BB\u5C45\u80E1\u6405\u86EE\u7F20\uFF0C\u547D\u4EE4\u4ED6\u5C06\u4E8E\u8679\u7684\u623F\u5B50\u7ED9\u76D6\u4E0A\uFF0C\u8FD8\u81EA\u79F0\u662F\u4E8E\u8679\u7684\u540E\u53F0\u3002\u90BB\u5C45\u5FC3\u6709\u4E0D\u6EE1\uFF0C\u53EF\u662F\u4E0D\u6562\u4E0D\u505A\uFF0C\u5634\u4E0A\u8BBD\u523A\u5468\u79C9\u6606\u5C31\u662F\u4ED7\u7740\u5468\u79C9\u4E49\u5406\u4E94\u559D\u516D\uFF0C\u6CA1\u6709\u5468\u79C9\u4E49\u4ED6\u5565\u4E5F\u4E0D\u662F\u3002\u90D1\u5A1F\u6124\u6012\u8D70\u8FC7\u53BB\uFF0C\u4E00\u5DF4\u638C\u6253\u5728\u4E86\u90BB\u5C45\u7684\u8138\u4E0A\uFF0C\u63D0\u9192\u4ED6\u4EE5\u540E\u8BF4\u8BDD\u6CE8\u610F\u70B9\uFF0C\u90BB\u5C45\u88AB\u90D1\u5A1F\u7684\u6C14\u52BF\u9707\u6151\u4E00\u53E5\u8BDD\u4E5F\u4E0D\u6562\u518D\u8BF4\u3002\u90D1\u5A1F\u79C1\u4E0B\u4E5F\u8D23\u602A\u5468\u79C9\u6606\u4E0D\u8BE5\u90A3\u4E48\u8BF4\uFF0C\u5BF9\u5468\u79C9\u4E49\u5F71\u54CD\u4E0D\u597D\u3002</p><p>\u5468\u79C9\u6606\u62FF\u7740\u4E24\u4E07\u6765\u8FD8\u7ED9\u5468\u84C9\uFF0C\u5176\u5B83\u7684\u5206\u671F\u8FD8\uFF0C\u5468\u84C9\u5374\u4E0D\u80AF\u8981\uFF0C\u8FD8\u8BF4\u4E86\u8FD9\u4E9B\u5E74\u7167\u987E\u73A5\u73A5\uFF0C\u4EB2\u4EBA\u4E4B\u95F4\u672C\u6765\u5C31\u4E0D\u9700\u8981\u8BA1\u8F83\u90A3\u4E48\u591A\u3002\u5468\u79C9\u6606\u548C\u5927\u5BB6\u5546\u8BAE\uFF0C\u65E2\u7136\u5468\u84C9\u4E0D\u8BA9\u8FD8\u94B1\uFF0C\u5C31\u628A\u94B1\u62FF\u51FA\u6765\u7ED9\u5E38\u8FDB\u6B65\u4E70\u8033\u8717\uFF0C\u8BA9\u4ED6\u80FD\u542C\u89C1\u5927\u5BB6\u7684\u58F0\u97F3\u3002\u6234\u4E0A\u8033\u8717\u4E4B\u540E\uFF0C\u8FDB\u6B65\u80FD\u542C\u89C1\u5927\u5BB6\u8BF4\u8BDD\uFF0C\u8FD8\u80FD\u53EB\u51FA\u5927\u5BB6\u7684\u540D\u5B57\uFF0C\u5927\u5BB6\u90FD\u6FC0\u52A8\u7684\u773C\u775B\u91CC\u6EA2\u6EE1\u6CEA\u6C34\u3002</p><p>\u6B63\u5728\u642C\u5BB6\u7684\u65F6\u5019\uFF0C\u56FD\u5E86\u5FFD\u7136\u5F97\u4E86\u8170\u95F4\u76D8\u7A81\u51FA\u4F4F\u9662\uFF0C\u5468\u84C9\u62C5\u5FC3\u957F\u6B64\u4EE5\u5F80\u5468\u79C9\u6606\u4E5F\u4F1A\u5F97\u75C5\uFF0C\u56E0\u6B64\u8BA9\u73A5\u73A5\u7ED9\u5B89\u6392\u5DE5\u4F5C\u3002\u73A5\u73A5\u62C5\u5FC3\u5468\u79C9\u6606\u81EA\u5C0A\u5FC3\u53D7\u4F24\uFF0C\u6253\u7B97\u627E\u4EBA\u8BF4\u8BF4\u8BD5\u8BD5\u3002</p><p>\u5468\u79C9\u6606\u6765\u533B\u9662\u770B\u56FD\u5E86\uFF0C\u5411\u9633\u4E5F\u6765\u533B\u9662\uFF0C\u63D0\u51FA\u8BA9\u5468\u79C9\u6606\u5E2E\u4E2A\u5FD9\u3002</p>',13),r=[o];function _(n,s){return e(),t("div",null,r)}const h=p(c,[["render",_],["__file","53.html.vue"]]);export{h as default};
