import{_ as p}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as e,c as t,f as a}from"./app.b5163196.js";const c={},o=a('<h1 id="\u7B2C54\u96C6-\u9648\u8DEF\u738B\u65ED\u5144\u5F1F\u548C\u89E3" tabindex="-1"><a class="header-anchor" href="#\u7B2C54\u96C6-\u9648\u8DEF\u738B\u65ED\u5144\u5F1F\u548C\u89E3" aria-hidden="true">#</a> \u7B2C54\u96C6\uFF1A\u9648\u8DEF\u738B\u65ED\u5144\u5F1F\u548C\u89E3</h1><p>\u738B\u65ED\u56DE\u6765\u4E86\uFF0C\u90B1\u5CA9\u5374\u548C\u67EF\u603B\u53BB\u4E86\u6DF1\u5733\u3002\u665A\u4E0A\uFF0C\u738B\u65ED\u5E26\u7740\u4E00\u8EAB\u75B2\u60EB\u56DE\u5BB6\uFF0C\u53C8\u542C\u8BF4\u6D77\u5916\u4ED3\u7684\u5206\u9500\u5546\u96C6\u4F53\u62D2\u7EDD\u63D0\u8D27\uFF0C\u738B\u65ED\u5341\u5206\u6025\u8E81\u3002\u90B1\u5CA9\u5728\u9152\u5E97\u7A81\u7136\u89C9\u5F97\u8EAB\u4F53\u4E0D\u9002\uFF0C\u6323\u7740\u6700\u540E\u4E00\u70B9\u529B\u6C14\u62E8\u901A\u4E86\u738B\u65ED\u7684\u7535\u8BDD\uFF0C\u8C01\u77E5\u738B\u65ED\u521A\u521A\u597D\u6CA1\u6709\u63A5\u5230\u3002</p><p>\u738B\u65ED\u4E0B\u697C\u770B\u5230\u90B1\u5CA9\u7684\u6765\u7535\uFF0C\u5FD9\u7ED9\u90B1\u5CA9\u6253\u4E86\u8FC7\u53BB\uFF0C\u90B1\u5CA9\u5DF2\u7ECF\u4ECE\u660F\u8FF7\u4E2D\u9192\u6765\uFF0C\u5979\u4EC0\u4E48\u4E5F\u6CA1\u6709\u8BF4\uFF0C\u53EA\u8BF4\u81EA\u5DF1\u662F\u56E0\u4E3A\u601D\u5FF5\u738B\u65ED\u624D\u6253\u7684\u7535\u8BDD\u3002</p><p>\u5DE7\u59D1\u6DF1\u591C\u52A0\u73ED\uFF0C\u5929\u5B9D\u6765\u7ED9\u5979\u9001\u591C\u5BB5\uFF0C\u5DE7\u59D1\u8BA9\u4ED6\u56DE\u5BB6\u4F11\u606F\u3002\u521A\u5173\u4E0A\u95E8\uFF0C\u53C8\u542C\u89C1\u6709\u4EBA\u6572\u95E8\uFF0C\u5F00\u95E8\u4E00\u770B\uFF0C\u7ADF\u7136\u662F\u9648\u5927\u5149\u3002</p><p>\u9648\u5927\u5149\u8BE2\u95EE\u5DE7\u59D1\u7684\u8FD1\u51B5\uFF0C\u8FD8\u8BF4\u81EA\u5DF1\u5728\u5916\u9762\u6F02\u6CCA\u591A\u5E74\uFF0C\u60F3\u56DE\u6765\u4E86\u3002\u4ED6\u5C06\u81EA\u5DF1\u8FD9\u4E48\u591A\u5E74\u7684\u79EF\u84C4\u90FD\u4EA4\u7ED9\u4E86\u5DE7\u59D1\u4F5C\u4E3A\u8865\u507F\u3002</p><p>\u90B1\u5CA9\u5E26\u738B\u65ED\u6765\u5230\u4E86\u9648\u8DEF\u7684\u7814\u7A76\u4E2D\u5FC3\uFF0C\u9648\u8DEF\u4E0D\u5728\uFF0C\u8521\u6653\u4E39\u8BF7\u5F20\u5B66\u7ED9\u4ED6\u4EEC\u8BB2\u89E3\u4ED6\u4EEC\u7684\u7814\u7A76\u6210\u679C\u3002\u5F20\u5B66\u4E0D\u7518\u5FC3\u542C\u8521\u6653\u4E39\u6307\u6325\uFF0C\u8521\u6653\u4E39\u6C14\u7684\u62D4\u6389\u4E86\u5F20\u5B66\u7684\u7535\u6E90\u3002</p><p>\u5F20\u5B66\u6C14\u7684\u53D1\u4FE1\u606F\u5411\u9648\u8DEF\u544A\u72B6\u3002</p><p>\u738B\u65ED\u60CA\u8BB6\u7684\u53D1\u73B0\u9648\u8DEF\u7684\u7814\u7A76\u6B63\u597D\u80FD\u7528\u6765\u89E3\u51B3\u6D77\u5916\u4ED3\u7684\u95EE\u9898\u3002\u4ED6\u9080\u8BF7\u9648\u8DEF\u5411\u5168\u516C\u53F8\u7684\u4EBA\u4ECB\u7ECD\u4ED6\u7684\u6653\u4E39\u7CFB\u7EDF\u3002</p><p>\u516C\u53F8\u7684\u8463\u4E8B\u4EEC\u8BA4\u4E3A\u9648\u8DEF\u7684\u7CFB\u7EDF\u592A\u8FC7\u4E8E\u8D85\u524D\uFF0C\u90B1\u5CA9\u5374\u8BA4\u4E3A\u4ED6\u4EEC\u73B0\u5728\u9700\u8981\u7684\u5C31\u662F\u8FD9\u79CD\u8D85\u524D\u7684\u610F\u8BC6\u3002</p><p>\u6653\u4E39\u7CFB\u7EDF\u8BA9\u6D77\u5916\u4ED3\u7684\u6D41\u91CF\u4E00\u4E2A\u6708\u7FFB\u4E86\u4E09\u500D\u3002\u962E\u6587\u96C4\u5F97\u77E5\u5341\u5206\u60CA\u8BB6\u3002\u540E\u6765\u5F97\u77E5\u8FD9\u4E2A\u7CFB\u7EDF\u662F\u738B\u65ED\u7684\u5F1F\u5F1F\u9648\u8DEF\u5F00\u53D1\u7684\u3002</p><p>\u9648\u8DEF\u7684\u516C\u53F8\u83B7\u5F97\u4E86\u4E00\u5343\u4E07\u7684\u878D\u8D44\uFF0C\u9648\u8DEF\u5341\u5206\u9AD8\u5174\uFF0C\u6FC0\u52A8\u7684\u62B1\u8D77\u4E86\u8521\u6653\u4E39\u3002</p><p>\u5E26\u8D70\u8FDC\u7A0B\u76D1\u63A7\u529F\u80FD\u7684\u533B\u7597\u8BBE\u5907\u7EC8\u4E8E\u7814\u53D1\u6210\u529F\uFF0C\u738B\u65ED\u8FD9\u624D\u5F97\u77E5\u7F8E\u6B27\u96C6\u56E2\u4E00\u76F4\u4E3A\u90B1\u5CA9\u7814\u7A76\u6CBB\u7597\u65B9\u6848\u3002\u90B1\u5CA9\u79BB\u5F00\u540E\u8FD9\u4E9B\u6CBB\u7597\u4E5F\u5C31\u7EC8\u6B62\u4E86\u3002</p><p>\u738B\u65ED\u54C4\u7740\u90B1\u5CA9\u6234\u4E0A\u4E86\u65B0\u7684\u533B\u7597\u8BBE\u5907\u3002\u65E0\u8BBA\u662F\u5DE5\u4F5C\u8FD8\u662F\u5F00\u8F66\uFF0C\u738B\u65ED\u4E00\u76F4\u5173\u6CE8\u7740\u90B1\u5CA9\u7684\u72B6\u51B5\u3002\u4E00\u5929\u665A\u4E0A\uFF0C\u4E0B\u7740\u5927\u96E8\uFF0C\u738B\u65ED\u7A81\u7136\u53D1\u73B0\u90B1\u5CA9\u7684\u6570\u636E\u51FA\u4E86\u95EE\u9898\u504F\u504F\u8F66\u53C8\u5728\u96E8\u4E2D\u7184\u706B\u3002\u5FC3\u6025\u5982\u711A\u7684\u738B\u65ED\u53EA\u80FD\u5192\u96E8\u8DD1\u4E86\u56DE\u53BB\u3002\u597D\u5728\u90B1\u5CA9\u6CA1\u4E8B\uFF0C\u5979\u628A\u8BBE\u5907\u563F\u4E86\u5934\u75BC\u7684\u8D75\u59E8\u5E26\u7740\u3002\u738B\u65ED\u51B3\u5B9A\u4E0D\u60DC\u4E00\u5207\u548C\u7F8E\u6B27\u5408\u4F5C\u3002</p><p>\u738B\u65ED\u8BF7\u9648\u5927\u5149\u559D\u9152\uFF0C\u5E76\u5411\u9648\u5927\u5149\u9053\u6B49\uFF0C\u4ED6\u77E5\u9053\u9648\u5927\u5149\u5F53\u5E74\u72AF\u4E0B\u7684\u9519\uFF0C\u591A\u5C11\u662F\u7531\u4E8E\u4ED6\u5484\u5484\u76F8\u903C\u7684\u539F\u56E0\u3002\u540C\u65F6\uFF0C\u4ED6\u6073\u8BF7\u9648\u5927\u5149\u5E2E\u52A9\u4ED6\u7BA1\u7406\u6D77\u5916\u4ED3\u3002</p><p>\u9648\u5927\u5149\u5F00\u8F66\u6765\u5230\u9648\u6C5F\u6CB3\u7684\u95E8\u53E3\uFF0C\u8FDC\u8FDC\u7684\u5411\u9648\u6C5F\u6CB3\u97A0\u4E86\u4E00\u8EAC\uFF0C\u8F6C\u8EAB\u79BB\u5F00\u3002</p><p>\u6768\u96EA\u4E5F\u56DE\u6765\u4E86\uFF0C\u5979\u6765\u770B\u671B\u7389\u73E0\u548C\u6C5F\u6CB3\u4E09\u4EBA\u5982\u4ECA\u65E9\u5DF2\u653E\u4E0B\u5F53\u5E74\u7684\u5FC3\u7ED3\uFF0C\u4EE5\u5144\u59B9\u76F8\u79F0\u3002\u4E09\u4EBA\u804A\u5929\u804A\u7684\u5341\u5206\u5F00\u5FC3\uFF0C\u6768\u96EA\u5FCD\u4E0D\u4F4F\u843D\u4E0B\u6CEA\u6765\u3002</p><p>\u962E\u6587\u96C4\u7EA6\u738B\u65ED\u5728\u4E1C\u6B27\u7684\u6D77\u5916\u4ED3\u76F8\u89C1\uFF0C\u962E\u6587\u96C4\u8BD5\u56FE\u6210\u4E3A\u5CA9\u65ED\u96C6\u56E2\u7684\u6700\u5927\u80A1\u4E1C\uFF0C\u738B\u65ED\u575A\u51B3\u8BA4\u4E3A\u4E0D\u53EF\u80FD\uFF0C\u962E\u6587\u96C4\u4EE5\u5F53\u5E74\u7684\u7ED1\u67B6\u6848\u5A01\u80C1\u738B\u65ED\uFF0C\u738B\u65ED\u5374\u544A\u8BC9\u962E\u6587\u96C4\uFF0C\u4ED6\u65E9\u6709\u9632\u8303\u7684\u529E\u6CD5\u3002</p>',17),r=[o];functi