import{_ as e}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as p,c as t,f as a}from"./app.cb44c984.js";const c={},o=a('<h1 id="\u7B2C54\u96C6-\u5149\u5B57\u7247\u8981\u5F00\u53D1" tabindex="-1"><a class="header-anchor" href="#\u7B2C54\u96C6-\u5149\u5B57\u7247\u8981\u5F00\u53D1" aria-hidden="true">#</a> \u7B2C54\u96C6\uFF1A\u5149\u5B57\u7247\u8981\u5F00\u53D1</h1><p>\u5411\u9633\u81EA\u79F0\u670B\u53CB\u5F00\u4E86\u4E2A\u7269\u6D41\u516C\u53F8\uFF0C\u9700\u8981\u53F8\u673A\uFF0C\u5E0C\u671B\u5468\u79C9\u6606\u80FD\u53BB\u5E2E\u5FD9\uFF0C\u73B0\u5728\u56FD\u5E86\u75C5\u4E86\u4E5F\u4E0D\u80FD\u518D\u642C\u5BB6\u3002\u56FD\u5E86\u8BA4\u4E3A\u662F\u597D\u4E8B\uFF0C\u529D\u8BF4\u5468\u79C9\u6606\u8FC7\u53BB\uFF0C\u5411\u9633\u8BA9\u56FD\u5E86\u597D\u597D\u517B\u75C5\uFF0C\u4EE5\u540E\u597D\u4E86\u4E5F\u8003\u9A7E\u8BC1\u8FC7\u53BB\u3002</p><p>\u5F6D\u5FC3\u751F\u7EA6\u89C1\u4E86\u5468\u79C9\u4E49\uFF0C\u4E5F\u8868\u793A\u4F1A\u6295\u8D44\u6539\u9020\u68DA\u6237\u533A\uFF0C\u5468\u79C9\u4E49\u56E0\u6B64\u53C8\u548C\u5F6D\u5FC3\u751F\u559D\u4E86\u4E00\u70B9\u9152\u3002\u5468\u79C9\u4E49\u56DE\u53BB\u7684\u65F6\u5019\u95EE\u90DD\u51AC\u6885\u7A76\u7ADF\u600E\u4E48\u4E86\uFF1F\u90DD\u51AC\u6885\u5982\u5B9E\u8BF4\u4E86\u81EA\u5DF1\u8F9E\u804C\u60F3\u8981\u7167\u987E\u5468\u79C9\u4E49\uFF0C\u7ED3\u679C\u5BFC\u81F4\u5979\u6BCF\u5929\u53BB\u7ED9\u8001\u5E72\u90E8\u4F53\u68C0\uFF0C\u5979\u62C5\u5FC3\u624B\u672F\u5200\u518D\u4E5F\u65E0\u6CD5\u62FF\u8D77\u6765\u4E86\u3002\u5468\u79C9\u4E49\u8D23\u602A\u90DD\u51AC\u6885\u4E0D\u548C\u4ED6\u5546\u91CF\uFF0C\u90DD\u51AC\u6885\u607C\u6012\u5468\u79C9\u4E49\u56DE\u6765\u4E5F\u6CA1\u5546\u91CF\uFF0C\u5BB6\u91CC\u4E00\u5207\u90FD\u56F4\u7ED5\u5468\u79C9\u4E49\u8F6C\u60A0\u3002\u5979\u505A\u4E86\u81EA\u5DF1\u8BE5\u505A\u7684\u4E00\u5207\uFF0C\u53EF\u662F\u552F\u72EC\u6CA1\u6709\u81EA\u5DF1\uFF0C\u5979\u4E5F\u89C9\u5F97\u662F\u5DE5\u4F5C\u5C97\u4F4D\u4E0A\u7684\u4F7C\u4F7C\u8005\uFF0C\u53EF\u662F\u5728\u5468\u79C9\u4E49\u8FD9\u91CC\u5979\u5C31\u662F\u65E0\u6761\u4EF6\u4ED8\u51FA\u7684\u4EBA\uFF0C\u5E94\u8BE5\u5E94\u4EFD\u3002\u5468\u79C9\u4E49\u4E5F\u6709\u81EA\u5DF1\u7684\u59D4\u5C48\uFF0C\u4ED6\u4F4F\u5728\u90DD\u51AC\u6885\u5BB6\u91CC\u4E5F\u6709\u5BC4\u4EBA\u7BF1\u4E0B\u7684\u611F\u89C9\uFF0C\u4ED6\u4E5F\u6068\u81EA\u5DF1\u65E0\u80FD\uFF0C\u6240\u4EE5\u624D\u6CA1\u80FD\u8BA9\u7236\u6BCD\u4E34\u6B7B\u524D\u90FD\u6CA1\u89C1\u8FC7\u4EB2\u5BB6\u3002</p><p>\u90DD\u51AC\u6885\u6CA1\u60F3\u5230\u5468\u79C9\u4E49\u6709\u8FD9\u4E48\u591A\u59D4\u5C48\u548C\u57CB\u6028\uFF0C\u76F4\u63A5\u6311\u660E\u5468\u79C9\u4E49\u5FC3\u91CC\u6700\u5927\u7684\u59D4\u5C48\u5C31\u662F\u6CA1\u80FD\u6709\u4E2A\u5B69\u5B50\u3002\u770B\u89C1\u5468\u79C9\u4E49\u6C89\u9ED8\uFF0C\u90DD\u51AC\u6885\u66F4\u662F\u8BC1\u5B9E\u4E86\u5FC3\u91CC\u7684\u731C\u6D4B\uFF0C\u4E00\u6C14\u4E4B\u4E0B\u63D0\u51FA\u79BB\u5A5A\uFF0C\u5468\u79C9\u4E49\u4E5F\u8D4C\u6C14\u7B54\u5E94\u4E86\u3002</p><p>\u5468\u79C9\u6606\u5F00\u8F66\u56DE\u6765\u53D1\u73B0\u5E38\u8FDB\u6B65\u548C\u4E8E\u8679\u4E5F\u53BB\u8F66\u961F\u6253\u626B\u536B\u751F\u4E86\uFF0C\u8FD8\u7ED9\u7F34\u7EB3\u53CC\u9669\u3002\u56E0\u6B64\u731C\u5230\u53EF\u80FD\u662F\u548C\u5468\u84C9\u6709\u5173\uFF0C\u7279\u610F\u53BB\u627E\u4E86\u5468\u84C9\uFF0C\u5468\u84C9\u4E5F\u627F\u8BA4\u4E86\u662F\u73A5\u73A5\u7684\u610F\u601D\u3002\u5979\u672C\u6765\u60F3\u76F4\u63A5\u8BF4\uFF0C\u53EF\u662F\u73A5\u73A5\u975E\u8981\u7167\u987E\u5468\u79C9\u6606\u7684\u81EA\u5C0A\u5FC3\uFF0C\u624D\u8FD9\u4E48\u5B89\u6392\u7684\u3002\u5468\u79C9\u6606\u8868\u793A\u4EE5\u540E\u4F1A\u7763\u4FC3\u4E8E\u8679\u548C\u8FDB\u6B65\u4E00\u8D77\u5E72\u597D\uFF0C\u4E0D\u7ED9\u73A5\u73A5\u4E22\u4EBA\u3002\u5468\u79C9\u6606\u77E5\u9053\u8FD9\u662F\u73A5\u73A5\u6765\u62A5\u6069\u4E86\uFF0C\u5FC3\u91CC\u611F\u52A8\uFF0C\u4ECE\u5C0F\u73A5\u73A5\u662F\u5728\u8EAB\u8FB9\u957F\u5927\u7684\u3002</p><p>\u90DD\u51AC\u6885\u6765\u5230\u5E02\u59D4\u7ED9\u5468\u79C9\u4E49\u9001\u4E86\u4ED6\u8981\u7684\u4E1C\u897F\uFF0C\u6070\u597D\u770B\u89C1\u90CE\u7389\u548C\u5468\u79C9\u4E49\u8BF4\u8BDD\uFF0C\u5979\u5C31\u8EB2\u7740\u6CA1\u51FA\u6765\uFF0C\u76F4\u5230\u5468\u79C9\u4E49\u8BA9\u53F8\u673A\u9001\u90CE\u7389\u56DE\u53BB\u5979\u624D\u6765\u6572\u95E8\u3002\u90DD\u51AC\u6885\u5DF2\u7ECF\u6536\u62FE\u597D\u4E86\u5468\u79C9\u4E49\u6240\u6709\u7684\u4E1C\u897F\uFF0C\u8BA9\u4ED6\u62BD\u65F6\u95F4\u53BB\u529E\u7406\u79BB\u5A5A\u624B\u7EED\uFF0C\u5979\u4E0D\u60F3\u7EE7\u7EED\u803D\u8BEF\u5468\u79C9\u4E49\u3002</p><p>\u770B\u7740\u90DD\u51AC\u6885\u8981\u79BB\u5F00\uFF0C\u5468\u79C9\u4E49\u5FCD\u4E0D\u4F4F\u53EB\u4F4F\u4E86\u90DD\u51AC\u6885\uFF0C\u4ED6\u627F\u8BA4\u81EA\u5DF1\u5FFD\u89C6\u8FC7\u90DD\u51AC\u6885\uFF0C\u53EF\u662F\u8FD9\u4E9B\u5E74\u90DD\u51AC\u6885\u5C31\u662F\u957F\u5728\u4ED6\u8EAB\u4E0A\u7684\u53E6\u4E00\u534A\u3002\u4ED6\u8981\u5931\u53BB\u90DD\u51AC\u6885\u624D\u77E5\u9053\u5FC3\u91CC\u6709\u591A\u75BC\u3002\u4F46\u5982\u679C\u90DD\u51AC\u6885\u60F3\u79BB\u5A5A\u518D\u7B49\u7B49\uFF0C\u56E0\u4E3A\u4ED6\u68C0\u67E5\u8EAB\u4F53\u662F\u80C3\u764C\uFF0C\u4E0D\u8FC7\u662F\u65E9\u671F\uFF0C\u53EF\u4EE5\u5207\u9664\u4E00\u534A\uFF0C\u4ED6\u8981\u7ED9\u81EA\u5DF1\u4E89\u53D6\u65F6\u95F4\uFF0C\u6539\u9020\u597D\u5149\u5B57\u7247\u3002\u6240\u4EE5\u4ED6\u624D\u4F1A\u4E00\u5929\u5F53\u505A\u4E24\u5929\u7528\uFF0C\u90CE\u7389\u662F\u4E3B\u6293\u5149\u5B57\u7247\u6539\u9020\u7684\u4EBA\uFF0C\u6240\u4EE5\u624D\u8C08\u8BDD\u5F88\u665A\u3002\u6700\u4E3B\u8981\u8FD8\u6709\u90DD\u51AC\u6885\uFF0C\u5982\u679C\u4ED6\u6B7B\u4E86\uFF0C\u90DD\u51AC\u6885\u548B\u529E\uFF0C\u6240\u4EE5\u4ED6\u4E5F\u5FC5\u987B\u6CBB\u75C5\u3002\u90DD\u51AC\u6885\u5931\u58F0\u75DB\u54ED\uFF0C\u5468\u79C9\u4E49\u5B89\u6170\u7740\u90DD\u51AC\u6885\uFF0C\u5E0C\u671B\u90DD\u51AC\u6885\u80FD\u4E0D\u544A\u8BC9\u4EFB\u4F55\u4EBA\uFF0C\u4ED6\u4E0D\u60F3\u6210\u4E3A\u4EFB\u4F55\u4EBA\u7684\u8D1F\u62C5\u3002\u4F46\u5728\u8FD9\u4E2A\u65F6\u5019\uFF0C\u4ED6\u5E0C\u671B\u90DD\u51AC\u6885\u80FD\u662F\u90A3\u4E2A\u966A\u7740\u4ED6\u8BA9\u7740\u4ED6\u7684\u4EBA\u3002\u90DD\u51AC\u6885\u6CEA\u5982\u96E8\u4E0B\uFF0C\u6376\u6253\u7740\u5468\u79C9\u4E49\uFF0C\u8D23\u602A\u5468\u79C9\u4E49\u4E0D\u542C\u8BDD\uFF0C\u5BFC\u81F4\u4E86\u4ECA\u5929\u7684\u7ED3\u679C\u3002\u770B\u7740\u5FC3\u75DB\u6B32\u7EDD\u7684\u90DD\u51AC\u6885\uFF0C\u5468\u79C9\u4E49\u5C06\u5979\u62C9\u5165\u6000\u4E2D\uFF0C\u8F7B\u8F7B\u5B89\u6170\u7740\u3002</p><p>\u5468\u806A\u544A\u8BC9\u5468\u79C9\u6606\u4ED6\u6709\u5973\u53CB\u4E86\uFF0C\u5468\u79C9\u6606\u5F00\u5FC3\uFF0C\u4E00\u7728\u773C\u5468\u806A\u90FD\u957F\u5927\u4E86\u3002\u5149\u5B57\u7247\u5DF2\u7ECF\u5F00\u59CB\u6D4B\u91CF\u4E86\uFF0C\u5F88\u5FEB\u5C31\u8981\u5927\u5200\u9614\u65A7\u7684\u6539\u9020\u4E86\uFF0C\u4E54\u6625\u71D5\u5230\u5904\u60F3\u8981\u6253\u542C\u6D88\u606F\u3002</p><p>\u5468\u79C9\u6606\u770B\u89C1\u6709\u4EBA\u7ED9\u987E\u961F\u957F\u94B1\uFF0C\u987E\u961F\u957F\u89E3\u91CA\u662F\u8FD8\u94B1\u3002\u5468\u79C9\u6606\u63D0\u51FA\u5E0C\u671B\u80FD\u4ECB\u7ECD\u53F8\u673A\u6765\u5E72\u6D3B\uFF0C\u4F46\u662F\u987E\u961F\u957F\u5374\u8868\u793A\u6765\u53EA\u80FD\u5E72\u88C5\u5378\u5DE5\u3002\u5468\u79C9\u6606\u5C06\u8FD9\u4E2A\u4E8B\u544A\u8BC9\u4E86\u56FD\u5E86\uFF0C\u4F46\u5E0C\u671B\u56FD\u5E86\u8FD8\u662F\u597D\u597D\u517B\u517B\uFF0C\u6BD5\u7ADF\u4F53\u529B\u6D3B\u4E0D\u9002\u5408\u4E86\u3002\u5468\u79C9\u6606\u79BB\u5F00\u4EE5\u540E\uFF0C\u5434\u5029\u8D23\u602A\u56FD\u5E86\u6CA1\u6709\u6253\u542C\u4E00\u4E0B\u5F00\u53D1\u7684\u4E8B\uFF0C\u56FD\u5E86\u8BA4\u4E3A\u5468\u79C9\u6606\u80FD\u8BF4\u7684\u65E9\u5C31\u8BF4\u4E86\uFF0C\u4E0D\u80FD\u8BF4\u7684\u5565\u4E5F\u4E0D\u4F1A\u8BF4\u3002\u8FD8\u53EE\u5631\u5434\u5029\u4E0D\u8981\u5B66\u522B\u7684\u957F\u820C\u5987\u3002</p><p>\u770B\u5230\u4E86\u516C\u544A\u6539\u9020\u62C6\u8FC1\u7684\u4E8B\uFF0C\u8857\u574A\u90BB\u5C45\u90FD\u8DD1\u5230\u4E86\u5468\u79C9\u6606\u5BB6\u91CC\uFF0C\u62DC\u6258\u5468\u79C9\u6606\u53BB\u6253\u542C\u4E00\u4E0B\u4F1A\u642C\u53BB\u54EA\u91CC\u3002\u7ED3\u679C\u5F97\u77E5\u8981\u642C\u53BB\u738B\u5BB6\u5C6F\uFF0C\u5468\u79C9\u6606\u8BA4\u4E3A\u642C\u53BB\u90A3\u91CC\u4E0D\u884C\uFF0C\u90A3\u5C31\u662F\u519C\u6751\u3002\u53EF\u5468\u79C9\u4E49\u8BA4\u4E3A\uFF0C\u73B0\u5728\u79D8\u5BC6\u5FC5\u987B\u5B88\u4F4F\uFF0C\u642C\u8FC7\u53BB\u7684\u6D88\u606F\u4F20\u51FA\u53BB\u4E00\u5B9A\u4F1A\u70B8\u9505\u3002\u5468\u79C9\u4E49\u6253\u7B97\u8981\u642C\u53BB\u4E4B\u524D\u5C06\u738B\u5BB6\u5C6F\u5EFA\u8BBE\u4E00\u5207\u5B8C\u6BD5\u4E4B\u540E\u5E26\u7740\u5927\u5BB6\u53BB\u53C2\u89C2\uFF0C\u8FD9\u6837\u624D\u884C\u3002\u5468\u79C9\u6606\u56DE\u53BB\u540E\u548C\u90D1\u5A1F\u8BF4\u4E86\u8FD9\u4EF6\u4E8B\uFF0C\u4EE5\u540E\u5982\u679C\u6709\u4EBA\u95EE\u5C31\u8BF4\u770B\u516C\u544A\u3002</p><p>\u516C\u544A\u4E0B\u6765\u7684\u65F6\u5019\uFF0C\u5927\u5BB6\u53C8\u805A\u5230\u4E00\u8D77\u6765\u5230\u5468\u79C9\u6606\u5BB6\u91CC\uFF0C\u4E54\u5927\u5A76\u5FF5\u53E8\u7740\u5468\u5FD7\u521A\u517B\u4E86\u597D\u513F\u5B50\uFF0C\u4ED6\u4EEC\u90FD\u80FD\u4EAB\u53D7\u5230\u8FD9\u4E2A\u597D\u5F85\u9047\u3002</p><p>\u4E00\u8F86\u8F86\u5361\u8F66\u4ECE\u5149\u5B57\u7247\u8FC7\u53BB\uFF0C\u6325\u821E\u7740\u9A86\u6C0F\u96C6\u56E2\u7684\u65D7\u5B50\u3002\u56FD\u5E86\u548C\u8FDB\u6B65\u90FD\u8DDF\u7740\u8F66\uFF0C\u4E00\u8DEF\u53BB\u4E86\u738B\u5BB6\u5C6F\uFF0C\u8FD9\u8BA9\u56FD\u5E86\u5FC3\u751F\u7591\u7AA6\u3002\u4E54\u6625\u71D5\u4E3A\u4E86\u60F3\u8981\u591A\u5360\u70B9\uFF0C\u5F00\u59CB\u5728\u4E54\u5927\u5A76\u5BB6\u91CC\u76D6\u623F\u5B50\uFF0C\u665A\u4E0A\u90FD\u53C8\u8DD1\u53BB\u4E86\u5468\u79C9\u6606\u5BB6\u91CC\uFF0C\u6709\u4EBA\u8D23\u602A\u5468\u79C9\u6606\u6709\u6D88\u606F\u4E0D\u900F\u6F0F\u3002\u56FD\u5E86\u633A\u8EAB\u800C\u51FA\u7EF4\u62A4\u5468\u79C9\u6606\uFF0C\u51E1\u662F\u60F3\u8981\u4E3A\u96BE\u5468\u79C9\u6606\u7684\u90FD\u5148\u4ECE\u4ED6\u8FD9\u91CC\u8FC7\u53BB\u3002</p>',12),r=[o];function _(n,s){return p(),t("div",null,r)}const h=e(c,[["render",_],["__file","54.html.vue"]]);export{h as default};
