import{_ as p}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as e,c as t,f as a}from"./app.52cb12a8.js";const c={},o=a('<h1 id="\u7B2C55\u96C6-\u5468\u79C9\u6606\u5E26\u5934\u642C\u53BB\u65B0\u533A" tabindex="-1"><a class="header-anchor" href="#\u7B2C55\u96C6-\u5468\u79C9\u6606\u5E26\u5934\u642C\u53BB\u65B0\u533A" aria-hidden="true">#</a> \u7B2C55\u96C6\uFF1A\u5468\u79C9\u6606\u5E26\u5934\u642C\u53BB\u65B0\u533A</h1><p>\u90BB\u5C45\u5927\u718A\u5435\u56B7\u7740\uFF0C\u8D23\u602A\u5468\u79C9\u6606\u5C31\u8BE5\u544A\u8BC9\u5927\u5BB6\u5B9E\u8BDD\uFF0C\u800C\u4E0D\u662F\u85CF\u7740\u6396\u7740\uFF0C\u4E54\u5927\u5A76\u4E5F\u770B\u4E0D\u4E0B\u53BB\u4E86\uFF0C\u8D23\u602A\u5927\u718A\u4E0D\u8BE5\u4E3A\u96BE\u5468\u79C9\u6606\u3002\u90D1\u5A1F\u4E0A\u524D\u4E00\u6B65\uFF0C\u4E0B\u4E86\u9010\u5BA2\u4EE4\uFF0C\u8981\u6C42\u6240\u6709\u4EBA\u4EE5\u540E\u6765\u505A\u5BA2\u968F\u65F6\u6B22\u8FCE\uFF0C\u4F46\u60F3\u8981\u95F9\u4E8B\u8BF7\u79BB\u5F00\u3002\u5468\u806A\u4E0D\u6EE1\u8FD9\u4E9B\u4EBA\u7684\u884C\u4E3A\uFF0C\u89C9\u5F97\u5C31\u662F\u5201\u6C11\uFF0C\u4E5F\u7EC8\u4E8E\u4F53\u4F1A\u5230\u4E86\u5468\u79C9\u4E49\u7684\u4E0D\u5BB9\u6613\u3002</p><p>\u623F\u5B50\u843D\u6210\u4E4B\u540E\uFF0C\u5927\u5BB6\u8DDF\u7740\u5927\u5DF4\u8F66\u6765\u770B\u623F\u5B50\uFF0C\u4F46\u662F\u7531\u4E8E\u5730\u533A\u6BD4\u8F83\u8FDC\uFF0C\u8D2D\u7269\u548C\u4E0A\u5B66\u4E0A\u73ED\u90FD\u4E0D\u65B9\u4FBF\uFF0C\u56E0\u6B64\u5F88\u591A\u4EBA\u90FD\u4E0D\u613F\u610F\u642C\u3002\u90D1\u5A1F\u5411\u5468\u79C9\u6606\u63D0\u51FA\u642C\u5BB6\uFF0C\u516C\u544A\u4E0A\u8BF4\u4E86\uFF0C\u8C01\u5148\u642C\u5BB6\u4F18\u5148\u9009\u62E9\uFF0C\u6253\u7B97\u4EE5\u540E\u80FD\u5F00\u4E2A\u996D\u5E97\u6216\u8005\u8D85\u5E02\u3002\u90D1\u5A1F\u77E5\u9053\u5468\u79C9\u6606\u4E5F\u60F3\u642C\u5BB6\uFF0C\u4E0D\u662F\u56E0\u4E3A\u5730\u65B9\uFF0C\u800C\u662F\u56E0\u4E3A\u5468\u79C9\u4E49\u3002</p><p>\u5468\u79C9\u4E49\u4E0D\u7406\u89E3\u4E3A\u4EC0\u4E48\u90A3\u4E9B\u4EBA\u4E0D\u613F\u610F\u642C\u5BB6\uFF0C\u90FD\u60F3\u79BB\u5F00\u90A3\u4E2A\u8D2B\u7A77\u7684\u5730\u65B9\uFF0C\u53EF\u662F\u65B0\u5730\u65B9\u5EFA\u597D\u4E86\u90FD\u4E0D\u8D70\u4E86\u3002\u5468\u79C9\u6606\u80FD\u7406\u89E3\u6240\u6709\u4EBA\u7684\u5FC3\u601D\uFF0C\u6BD5\u7ADF\u5F88\u591A\u4E0D\u4FBF\u5229\u7684\u5730\u65B9\uFF0C\u4F46\u4ED6\u4E5F\u80FD\u7406\u89E3\u5468\u79C9\u4E49\u4E3A\u4E86\u767E\u59D3\u64CD\u788E\u4E86\u5FC3\u3002\u5468\u79C9\u4E49\u544A\u8BC9\u5468\u79C9\u6606\uFF0C\u5982\u679C\u771F\u6CA1\u6709\u4EBA\u642C\u8FC7\u53BB\uFF0C\u5230\u65F6\u5019\u4E0B\u4E00\u6279\u7684\u5F00\u53D1\u5546\u5C31\u5F15\u4E0D\u8FC7\u6765\uFF0C\u8FD9\u6837\u5C31\u771F\u7684\u6210\u4E86\u70C2\u5C3E\u697C\u3002</p><p>\u5468\u79C9\u6606\u5E26\u7740\u5168\u5BB6\u4EBA\uFF0C\u4F9D\u4F9D\u4E0D\u820D\u642C\u79BB\u4E86\u5149\u5B57\u7247\uFF0C\u5927\u718A\u53C8\u6765\u770B\u70ED\u95F9\uFF0C\u89C9\u5F97\u8FD9\u662F\u5468\u79C9\u6606\u60F3\u8981\u5E2E\u5468\u79C9\u4E49\u7684\u5FD9\uFF0C\u5426\u5219\u4ED6\u4E5F\u4E0D\u60F3\u642C\u5BB6\u3002\u90CE\u7389\u5728\u5468\u79C9\u6606\u642C\u5BB6\u8FD9\u5929\u5E26\u7740\u5A92\u4F53\u6765\u91C7\u8BBF\uFF0C\u5468\u79C9\u6606\u5728\u73B0\u573A\u505A\u4E86\u4E00\u756A\u6F14\u8BF4\uFF0C\u58F0\u60C5\u5E76\u8302\u8BB2\u8FF0\u4E86\u65B0\u5C0F\u533A\u7684\u597D\u5904\uFF0C\u5468\u79C9\u4E49\u548C\u90DD\u51AC\u6885\u770B\u7740\u7535\u89C6\u7A81\u7136\u773C\u775B\u91CC\u95EA\u70C1\u6CEA\u5149\uFF0C\u4ED6\u4EEC\u77E5\u9053\u8FD9\u662F\u5468\u79C9\u6606\u5728\u5E2E\u5FD9\u3002\u5468\u79C9\u6606\u5C06\u6960\u6960\u548C\u7236\u6BCD\u7684\u9057\u50CF\u90FD\u5E26\u6765\u4E86\u65B0\u5BB6\uFF0C\u5E0C\u671B\u4ED6\u4EEC\u80FD\u770B\u770B\u65B0\u5730\u65B9\uFF0C\u4E5F\u7531\u8877\u89C9\u5F97\u7236\u6BCD\u751F\u4E86\u4E00\u7FA4\u7684\u597D\u5B69\u5B50\u3002</p><p>\u5434\u5029\u548C\u56FD\u5E86\u63D0\u51FA\u5047\u79BB\u5A5A\uFF0C\u7136\u540E\u6BCF\u4EBA\u53BB\u6392\u53F7\u4E70\u4E24\u5957\u623F\u5B50\uFF0C\u8FD9\u6837\u8FD8\u80FD\u7ED9\u513F\u5B50\u5927\u521A\u51C6\u5907\u4E00\u5957\u7ED3\u5A5A\u7528\u7684\u3002\u56FD\u5E86\u63D0\u9192\u5434\u5029\uFF0C\u5982\u679C\u79BB\u5A5A\u4E86\uFF0C\u5C31\u662F\u771F\u7684\uFF0C\u4ED6\u6B63\u597D\u53BB\u627E\u5C0F\u59D1\u5A18\uFF0C\u5434\u5029\u53C8\u6C14\u5F97\u6376\u6253\u56FD\u5E86\u3002</p><p>\u4E8E\u8679\u6765\u627E\u90D1\u5A1F\uFF0C\u90D1\u5A1F\u529D\u8BF4\u4E8E\u8679\u8D76\u7D27\u62A5\u540D\u4E70\u623F\u5B50\uFF0C\u751F\u6015\u665A\u4E86\u5C31\u6CA1\u4E86\uFF0C\u5982\u679C\u65E0\u623F\u6237\u53EF\u80FD\u9700\u8981\u591A\u4EA4\u94B1\uFF0C\u90D1\u5A1F\u8FD8\u51C6\u5907\u4E86\u94B1\u7ED9\u4E8E\u8679\u7528\u3002\u4E8E\u8679\u5374\u544A\u8BC9\u90D1\u5A1F\u5979\u6237\u53E3\u4E0D\u5728\u8FD9\u91CC\uFF0C\u4F4F\u7684\u662F\u90D1\u5A1F\u7684\u623F\u5B50\uFF0C\u5E0C\u671B\u80FD\u8BA9\u90D1\u5A1F\u5C06\u623F\u5B50\u8BA9\u7ED9\u5979\uFF0C\u5979\u53EF\u4EE5\u7ED9\u90D1\u5A1F\u94B1\u3002\u56E0\u4E3A\u516C\u544A\u4E0A\u4E5F\u8BF4\u660E\u4E86\uFF0C\u592B\u5987\u4FE9\u90FD\u6709\u623F\u5B50\u7684\u53EA\u80FD\u6309\u7167\u4E00\u4E2A\u6765\u7B97\u3002</p><p>\u90D1\u5A1F\u652F\u652F\u543E\u543E\u8868\u793A\uFF0C\u58F0\u79F0\u4E0D\u77E5\u9053\u7A76\u7ADF\u81EA\u5DF1\u7684\u623F\u5B50\u662F\u5426\u6709\u623F\u4EA7\u8BC1\uFF0C\u5982\u679C\u6CA1\u6709\u5C31\u6CA1\u6CD5\u8FC7\u6237\uFF0C\u4E8E\u8679\u62DC\u6258\u90D1\u5A1F\u53BB\u95EE\u95EE\u3002\u90D1\u5A1F\u5230\u8857\u9053\u8F6C\u4E86\u4E00\u5708\uFF0C\u53C8\u5230\u623F\u7BA1\u6240\u95EE\u4E86\u4E00\u5708\uFF0C\u6309\u7167\u9053\u7406\u6765\u8BF4\u4ED6\u4EEC\u5E94\u8BE5\u662F\u90A3\u91CC\u7684\u4EBA\uFF0C\u4F46\u662F\u9700\u8981\u6709\u8BC1\u660E\u3002\u90D1\u5A1F\u5C31\u7167\u7740\u8857\u574A\u90BB\u5C45\u8BC1\u660E\u623F\u5B50\u662F\u5979\u7684\uFF0C\u4E54\u5927\u5A76\u723D\u5FEB\u7684\u7B54\u5E94\u4E86\u3002\u4E54\u5927\u5A76\u8BA4\u4E3A\u90D1\u5A1F\u80AF\u5B9A\u662F\u4E3A\u4E86\u5B69\u5B50\u624D\u60F3\u62FF\u4EA7\u6743\u7684\uFF0C\u5982\u679C\u8FC7\u6237\u7ED9\u4E86\u5468\u806A\uFF0C\u8FD9\u6837\u5C31\u80FD\u518D\u62FF\u4E00\u5957\u623F\u5B50\u3002</p><p>\u90D1\u5A1F\u529E\u59A5\u4E86\u623F\u5B50\u7684\u4E8B\u60C5\u4E4B\u540E\uFF0C\u53C8\u5F00\u59CB\u96BE\u4E3A\u60C5\u8BE5\u5982\u4F55\u5BF9\u5F85\u4E8E\u8679\u4E86\uFF0C\u5979\u4E4B\u524D\u4E00\u76F4\u8BA9\u4E8E\u8679\u4F4F\u7740\u623F\u5B50\uFF0C\u53EF\u662F\u6CA1\u60F3\u5230\u4E8E\u8679\u7ADF\u7136\u80FD\u5F00\u53E3\u8981\u623F\u5B50\u3002\u5468\u79C9\u6606\u4E5F\u4ECE\u5411\u9633\u90A3\u91CC\u542C\u8BF4\u4E86\u4E00\u5957\u623F\u5B50\u4EF7\u503C\u4E8C\u5341\u4E07\uFF0C\u4ED6\u80FD\u6253\u591A\u5C11\u5E74\u7684\u5DE5\u624D\u80FD\u8D5A\u5230\u8FD9\u4E9B\u94B1\u3002\u90D1\u5A1F\u60F3\u8981\u5C06\u623F\u5B50\u7ED9\u5468\u806A\uFF0C\u5468\u79C9\u6606\u53C8\u89C9\u5F97\u6CA1\u6CD5\u5BF9\u5F85\u4E8E\u8679\u3002\u90D1\u5A1F\u5FFD\u7136\u4F24\u5FC3\u54ED\u8D77\u6765\uFF0C\u5468\u6BCD\u762B\u75EA\u7684\u65F6\u5019\u4E8E\u8679\u603B\u662F\u5E2E\u5FD9\u53BB\u6309\u6469\uFF0C\u6709\u65F6\u5019\u7D2F\u5230\u7761\u7740\u4E86\uFF0C\u624B\u8FD8\u5728\u6309\u6469\uFF0C\u4E3A\u6B64\u90D1\u5A1F\u89C9\u5F97\u81EA\u5DF1\u5BF9\u4E0D\u8D77\u4E8E\u8679\u3002</p><p>\u7ECF\u8FC7\u4E86\u601D\u7D22\u4E4B\u540E\uFF0C\u90D1\u5A1F\u548C\u5468\u79C9\u6606\u627E\u5468\u806A\u8C08\uFF0C\u90D1\u5A1F\u5C31\u60F3\u7740\u80FD\u548C\u513F\u5B50\u4F4F\u5728\u5BF9\u95E8\uFF0C\u4F46\u53C8\u89C9\u5F97\u5BF9\u4E0D\u8D77\u4E8E\u8679\uFF0C\u5468\u806A\u8D5E\u540C\u7ED9\u4E8E\u8679\uFF0C\u4ED6\u89C9\u5F97\u4E8E\u8679\u5DF2\u7ECF\u4E94\u5341\u591A\u4E86\uFF0C\u60F3\u8981\u4E70\u623F\u5B50\u4E0D\u53EF\u80FD\u4E86\uFF0C\u800C\u4ED6\u8FD8\u5E74\u8F7B\uFF0C\u4EE5\u540E\u8FD8\u6709\u53D1\u5C55\u3002\u5468\u806A\u77E5\u9053\u5982\u679C\u8FD9\u4EF6\u4E8B\u5B9A\u4E0D\u4E0B\u6765\u7236\u6BCD\u4E5F\u96BE\u8FC7\uFF0C\u56E0\u6B64\u5C31\u723D\u5FEB\u50AC\u4FC3\u7236\u6BCD\u53BB\u529E\uFF0C\u5468\u79C9\u6606\u5FCD\u4E0D\u4F4F\u79F0\u8D5E\u8C01\u5BB6\u751F\u4E86\u8FD9\u4E48\u4E2A\u597D\u513F\u5B50\u3002</p><p>\u90D1\u5A1F\u6765\u627E\u4E8E\u8679\uFF0C\u4E8E\u8679\u672C\u6765\u90FD\u4E0D\u62B1\u5E0C\u671B\u7684\uFF0C\u5373\u4FBF\u90D1\u5A1F\u4E0D\u7ED9\u5979\uFF0C\u5979\u4E5F\u53EF\u4EE5\u7406\u89E3\u3002\u90D1\u5A1F\u5374\u63D0\u51FA\u53BB\u8FC7\u6237\uFF0C\u4E8E\u8679\u773C\u542B\u70ED\u6CEA\u62E5\u62B1\u90D1\u5A1F\u3002</p><p>\u65B0\u516C\u544A\u53C8\u4E0B\u6765\u4E86\uFF0C\u53EA\u8981\u662F\u529E\u7406\u79BB\u5A5A\u624B\u7EED\u4E3A\u591A\u5206\u623F\u7684\u90FD\u4E0D\u7B97\u8865\u507F\u8303\u56F4\uFF0C\u8FD9\u8BA9\u4E54\u6625\u71D5\u5F88\u751F\u6C14\uFF0C\u89C9\u5F97\u653F\u5E9C\u5C31\u662F\u60F3\u529E\u6CD5\u6765\u5BF9\u4ED8\u4ED6\u4EEC\uFF0C\u56E0\u6B64\u575A\u51B3\u4E0D\u80AF\u642C\u5BB6\uFF0C\u4F46\u662F\u5374\u529D\u8BF4\u4E54\u5927\u5A76\u8D76\u7D27\u642C\u8D70\uFF0C\u4E0D\u8981\u5728\u8FD9\u91CC\u53D7\u7F6A\u4E86\uFF0C\u5979\u662F\u6253\u7B97\u5BF9\u6297\u5230\u5E95\u7684\u3002\u5468\u79C9\u4E49\u4E5F\u8BF4\u4E86\u4E8E\u8679\u4E70\u623F\u5B50\u7684\u4E8B\u60C5\uFF0C\u5982\u679C\u4E8E\u8679\u60F3\u8981\u548C\u90D1\u5A1F\u4E00\u6837\u7684\u623F\u5B50\uFF0C\u5C31\u9700\u8981\u8865\u5DEE\u4EF7\uFF0C\u56E0\u4E3A\u4E8E\u8679\u592A\u5E73\u80E1\u540C\u7684\u623F\u5B50\u7684\u5E73\u7C73\u6BD4\u90D1\u5A1F\u548C\u5468\u79C9\u6606\u7684\u8981\u5C0F\u3002</p><p>\u4E54\u6625\u71D5\u4E0D\u6EE1\u81EA\u5DF1\u7684\u76EE\u7684\u6CA1\u6709\u8FBE\u5230\uFF0C\u7279\u610F\u6765\u627E\u90D1\u5A1F\uFF0C\u4E0D\u6EE1\u90D1\u5A1F\u53EF\u4EE5\u8FC7\u6237\u623F\u5B50\u7ED9\u4E8E\u8679\uFF0C\u4E3A\u4EC0\u4E48\u5979\u5C31\u4E0D\u80FD\u6709\u623F\u5B50\u3002\u4E54\u6625\u71D5\u5E0C\u671B\u80FD\u8BA9\u5468\u79C9\u6606\u53BB\u627E\u5468\u79C9\u4E49\u529E\u59A5\u8FD9\u4EF6\u4E8B\uFF0C\u5426\u5219\u5C31\u8D56\u5728\u90D1\u5A1F\u5BB6\u91CC\u4E0D\u8D70\u4E86\u3002</p>',13),r=[o];function _(n,s){return e(),t("div",null,r)}const h=p(c,[["render",_],["__file","55.html.vue"]]);export{h as default};
