import{_ as p}from"./_plugin-vue_export-helper.cdc0426e.js";import{o,c as e,f as t}from"./app.21634df6.js";const c={},r=t('<h1 id="\u5218\u4E49\u9686" tabindex="-1"><a class="header-anchor" href="#\u5218\u4E49\u9686" aria-hidden="true">#</a> \u5218\u4E49\u9686</h1><blockquote><p>\u5E1D\u6027\u4EC1\u539A\u606D\u4FED\uFF0C\u52E4\u4E8E\u4E3A\u653F\uFF0C\u5B88\u6CD5\u800C\u4E0D\u5CFB\uFF0C\u5BB9\u7269\u800C\u4E0D\u5F1B\u3002......\u540E\u4E4B\u8A00\u653F\u6CBB\u8005\uFF0C\u7686\u79F0\u5143\u5609\u7109\u3002</p></blockquote><p><strong>\u300A\u8D44\u6CBB\u901A\u9274\xB7\u5377123\xB7\u5B8B\u7EAA\u4E94\u300B</strong></p><p>\u4ED6\u5584\u731C\u5FCC\uFF0C\u5148\u540E\u5904\u6B7B\u4E86\u81EA\u5DF1\u7684\u8F85\u653F\u5927\u81E3\u3001\u56FD\u5BB6\u680B\u6881\uFF0C\u751A\u81F3\u4E8E\u81F3\u4EB2\u5144\u5F1F\uFF1B\u4ED6\u597D\u5927\u559C\u529F\uFF0C\u5728\u4F4D\u4E09\u5341\u5E74\u95F4\u4E09\u6B21\u53D1\u8D77\u5317\u4F10\uFF0C\u4F46\u90FD\u5F92\u52B3\u65E0\u529F\uFF1B\u6700\u7EC8\uFF0C\u4ED6\u6B7B\u4E8E\u653F\u53D8\uFF0C\u88AB\u81EA\u5DF1\u7684\u592A\u5B50\u6240\u5F11\u3002</p><p>\u5982\u679C\u53EA\u770B\u4EE5\u4E0A\u8FD9\u4E9B\u5185\u5BB9\uFF0C\u90A3\u4E48\u4F60\u5BF9\u8FD9\u4F4D\u7687\u5E1D\u7684\u8BC4\u4EF7\uFF0C\u53EF\u80FD\u76F8\u5F53\u4E00\u822C\u3002\u4ED6\u4E0D\u8BBA\u662F\u4F5C\u4E3A\u8FD8\u662F\u80FD\u529B\uFF0C\u6700\u591A\u4E5F\u5C31\u662F\u4E2A\u4E2D\u5E73\u5EB8\u4E3B\u7684\u6837\u5B50\u3002</p><p>\u7136\u800C\u4E8B\u5B9E\u5374\u662F\uFF0C**\u82E5\u8BBA\u6CBB\u56FD\uFF0C\u6587\u5E1D\u6210\u7EE9\u6590\u7136\u3002**\u4ED6\u5728\u4F4D\u4E09\u5341\u5E74\u95F4\uFF0C\u6240\u521B\u4E0B\u7684\u201C<code>\u5143\u5609\u4E4B\u6CBB</code>\u201D\u6781\u53D7\u53F2\u5BB6\u79F0\u9882\u3002</p><p>\u8FD9\u91CC\u4F3C\u4E4E\u5C31\u4EA7\u751F\u4E86\u4E00\u4E2A\u77DB\u76FE\uFF1A\u5218\u4E49\u9686\u672C\u4EBA\u6709\u8BF8\u591A\u7F3A\u70B9\uFF0C\u884C\u4E8B\u884C\u6743\u4E5F\u6740\u4F10\u72E0\u5389\uFF1B\u4F46\u540C\u65F6\uFF0C\u4ED6\u7684\u7EDF\u6CBB\u5374\u53C8\u662F\u4E0D\u53EF\u591A\u5F97\u7684\u5178\u8303\uFF0C\u5C24\u5176\u662F\u5728\u5357\u5317\u671D\u52A8\u8361\u6DF7\u4E71\u7684\u65F6\u4EE3\u80CC\u666F\u4E4B\u4E0B\uFF0C\u8FD9\u79CD\u6210\u5C31\u5C31\u66F4\u663E\u73CD\u8D35\u96BE\u5F97\u3002</p><p>\u53F2\u4E66\u5BF9\u4E8E\u5143\u5609\u4E4B\u6CBB\u7684\u8BC4\u4EF7\uFF1A</p><blockquote><p>\u4E09\u5341\u5E74\u95F4\uFF0C\u56DB\u5883\u4E4B\u5185\uFF0C\u664F\u5B89\u65E0\u4E8B\uFF0C\u6237\u53E3\u8543\u606F\uFF1B\u51FA\u79DF\u4F9B\u5FAD\uFF0C\u6B62\u4E8E\u5C81\u8D4B\uFF0C\u6668\u51FA\u66AE\u5F52\uFF0C\u81EA\u4E8B\u800C\u5DF2\u3002\u95FE\u960E\u4E4B\u5185\uFF0C\u8BB2\u8BF5\u76F8\u95FB\uFF1B\u58EB\u6566\u64CD\u5C1A\uFF0C\u4E61\u803B\u8F7B\u8584\u3002\u6C5F\u5DE6\u98CE\u4FD7\uFF0C\u4E8E\u65AF\u4E3A\u7F8E\u3002\u540E\u4E4B\u8A00\u653F\u6CBB\u8005\uFF0C\u7686\u79F0\u5143\u5609\u7109\u3002</p></blockquote><p>\u8FD9\u91CC\u9762\uFF0C\u5305\u542B\u4E86\u8FD9\u4E48\u51E0\u4E2A\u5185\u5BB9\uFF1A</p><p>\u9996\u5148\uFF0C\u767E\u59D3\u5B89\u5C45\u4E50\u4E1A\uFF0C\u6237\u53E3\u7A33\u5B9A\u589E\u957F\uFF1B</p><p>\u5176\u6B21\uFF0C\u7A0E\u8D4B\u6C34\u5E73\u4E0D\u9AD8\uFF0C\u800C\u4E14\u6CA1\u6709\u82DB\u6350\u6742\u7A0E\uFF1B</p><p>\u7B2C\u4E09\uFF0C\u5FB7\u80B2\u6559\u5316\u826F\u597D\uFF0C\u767E\u59D3\u77E5\u803B\u5B88\u793C\u3002</p><p>\u800C\u4E14\uFF0C\u8FD9\u573A\u76DB\u4E16\u8FD8\u4E0D\u662F\u4E00\u822C\u7684\u597D\uFF0C\u662F\u597D\u6210\u4E86\u5386\u53F2\u4E2D\u7684\u9AD8\u6C34\u5E73\u6807\u6746\uFF1A</p><blockquote><p><strong>\u540E\u4E16\u79F0\u9882\uFF0C\u591A\u4EE5\u5143\u5609\u4E4B\u6CBB\u4E3A\u4F8B\u3002</strong></p></blockquote><p>\u5177\u4F53\u7684\u505A\u6CD5\u5176\u5B9E\u76F8\u5BF9\u6BD4\u8F83\u7B80\u5355\uFF0C\u4E3B\u8981\u7684\u5C31\u4E24\u6761\uFF1A</p><p>\u7B2C\u4E00\uFF0C\u6574\u987F\u540F\u6CBB\uFF1B</p><p>\u7B2C\u4E8C\uFF0C\u529D\u8BFE\u519C\u6851\u3002</p><p>\u6574\u987F\u540F\u6CBB\uFF0C\u5C31\u662F\u7EA6\u675F\u5B98\u5458\u7684\u8D2A\u6B32\u3002\u53EA\u8981\u7EA6\u675F\u4F4F\u5B98\u5458\u7684\u8D2A\u6B32\uFF0C\u4E5F\u5C31\u7EA6\u675F\u4F4F\u4E86\u5B98\u5458\u5BF9\u666E\u901A\u767E\u59D3\u7684\u4FB5\u5BB3\u3002</p><p>\u529D\u8BFE\u519C\u6851\uFF0C\u5219\u662F\u9F13\u52B1\u767E\u59D3\u8015\u79CD\u3002\u4E0D\u5728\u6B63\u5E38\u7684\u5C81\u8D4B\u4E4B\u5916\u5F81\u7A0E\u4EE5\u53CA\u5F81\u53D1\u5FAD\u5F79\uFF0C\u4FBF\u53EF\u6700\u5927\u7A0B\u5EA6\u5730\u8BA9\u767E\u59D3\u4F11\u517B\u751F\u606F\u3002</p><p>\u6B64\u5916\uFF0C\u518D\u52A0\u4E0A\u6301\u7EED\u7684\u9053\u5FB7\u6559\u5316\uFF0C\u901A\u8FC7\u51E0\u5341\u5E74\u5B89\u5B9A\u548C\u5E73\u7684\u653F\u6CBB\u73AF\u5883\uFF0C\u81EA\u7136\u5C31\u80FD\u5F62\u6210\u767E\u59D3\u5B89\u5C45\u4E50\u4E1A\u7684\u592A\u5E73\u5149\u666F\u3002</p><p><strong>\u6240\u8C13\u7684\u201C\u76DB\u4E16\u201D\uFF0C\u770B\u8D77\u6765\u5C31\u662F\u8FD9\u4E48\u7684\u7B80\u5355\u4E14\u5E73\u6DE1\u65E0\u5947\u3002</strong></p><p>\u4E5F\u5C31\u662F\u8BF4\uFF0C\u541B\u738B\u53EA\u8981\u80FD\u7EA6\u675F\u81EA\u5DF1\u7684\u6B32\u671B\uFF0C\u4E0D\u641C\u522E\u6C11\u8102\u6C11\u818F\uFF0C\u4EE5\u5FB7\u80B2\u6559\u5316\u4E16\u4EBA\uFF0C\u5C31\u80FD\u4F7F\u6C11\u98CE\u6566\u539A\u3002</p><p>\u5B8B\u6587\u5E1D\u6240\u505A\u7684\uFF0C\u5176\u5B9E\u5C31\u662F\u8FD9\u4E9B\u3002</p><p>\u81F3\u4E8E\u4ED6\u672C\u4EBA\u662F\u5426\u80FD\u5584\u5F85\u671D\u81E3\uFF0C\u6301\u5E73\u516C\u6B63\u2026...\u4F60\u4F1A\u53D1\u73B0\uFF0C\u4E0E\u4E4B\u5173\u8054\u5E76\u4E0D\u5927\u3002</p><p>\u800C\u8FD9\uFF0C\u4E5F\u5C31\u662F\u6211\u4EEC\u89C2\u5BDF\u4E00\u4E2A\u7687\u5E1D\u597D\u574F\u4E0E\u5426\u7684\u7B2C\u4E00\u4E2A\u5207\u5165\u70B9\uFF1A<strong>\u5728\u8BC4\u4EF7\u4E00\u4E2A\u7687\u5E1D\u7684\u65F6\u5019\uFF0C\u6211\u4EEC\u7684\u7740\u773C\u70B9\u5E94\u8BE5\u662F<code>\u671D\u653F</code>\uFF0C\u8FD8\u662F<code>\u56FD\u653F</code>\uFF1F</strong></p><p>\u4F60\u5982\u679C\u7740\u773C\u4E8E\u5177\u4F53\u7684\u653F\u6CBB\u6597\u4E89\u6216\u8005\u671D\u5802\u5185\u5E55\uFF0C\u770B\u5230\u7684\u5C31\u53EA\u53EF\u80FD\u662F\u52FE\u5FC3\u6597\u89D2\uFF0C\u5C14\u865E\u6211\u8BC8\u3002\u4F46\u4F60\u5982\u679C\u628A\u89C6\u89D2\u6269\u5927\u5230\u6574\u4E2A\u56FD\u5BB6\u7684\u56FD\u8BA1\u6C11\u751F\uFF0C\u5C31\u770B\u5230\u6CBB\u7406\u80FD\u529B\u4E0E\u6A21\u5F0F\u7684\u5B8F\u5927\u547D\u9898\u3002</p><p>\u4E24\u8005\u5B70\u8F7B\u5B70\u91CD\uFF1F</p><p>\u5F53\u7136\u662F\u540E\u8005\u3002</p><p>\u5B8B\u6587\u5E1D\u56FA\u7136\u6027\u683C\u731C\u5FCC\u82DB\u5BDF\uFF0C\u4F46\u4E5F\u6B63\u662F\u5728\u4ED6\u7684\u6CBB\u4E0B\uFF0C\u767E\u59D3\u5B89\u5C45\u4E50\u4E1A\u3002\u671D\u5802\u4E4B\u4E0A\u7684\u653F\u6CBB\u6597\u4E89\uFF0C\u56FA\u7136\u53EF\u80FD\u60CA\u5FC3\u52A8\u9B44\uFF0C\u53EF\u80FD\u5C14\u865E\u6211\u8BC8\uFF0C\u4F46\u4E5F\u5C31\u662F\u51E0\u5BB6\u51E0\u59D3\u7684\u5174\u8870\u8363\u8FB1\u3002\u53EF\u5143\u5609\u4E4B\u6CBB\u6240\u8FBE\u5230\u7684\u653F\u901A\u4EBA\u548C\u7684\u5C40\u9762\uFF0C\u5374\u662F\u5728\u51E0\u5341\u5E74\u95F4\uFF0C\u8BA9\u6570\u767E\u4E07\u767E\u59D3\u4EAB\u53D7\u5230\u4E86\u5BCC\u8DB3\u7684\u751F\u6D3B\u3002</p><p><strong>\u5B8B\u6587\u5E1D\u7684\u731C\u5FCC\u4E0E\u6740\u4F10\uFF0C\u662F\u201C\u79C1\u5FB7\u201D\uFF0C\u4F46\u5176\u6CBB\u56FD\u7684\u6210\u5C31\uFF0C\u5374\u662F\u201C\u516C\u5229\u201D\u3002</strong></p><p>\u5728\u79C1\u5FB7\u4E0A\uFF0C\u4ED6\u8FDC\u4E0D\u5982\u5386\u4EE3\u5E1D\u738B\u4E2D\u7684\u4F7C\u4F7C\u8005\uFF0C\u4E2A\u6027\u4E0E\u884C\u4E3A\u9887\u6709\u53EF\u6307\u6458\u4E4B\u5904\u3002\u4F46\u662F\uFF0C\u4ED6\u7684\u8FD9\u4E9B\u201C\u95EE\u9898\u201D\u5374\u57FA\u672C\u4E0A\u6CA1\u6709\u5BF9\u56FD\u653F\u9020\u6210\u592A\u5927\u7684\u8D1F\u9762\u5F71\u54CD\u3002</p><p>\u4ED6\u5728\u4F4D\u4E09\u5341\u5E74\u95F4\uFF0C\u9664\u4E86\u7B2C\u4E8C\u6B21\u5143\u5609\u5317\u4F10\u5916\uFF0C\u56FD\u5185\u57FA\u672C\u4E0A\u65E0\u52A8\u4E71\u3001\u65E0\u6A2A\u5F81\u66B4\u655B\uFF0C\u653F\u6CBB\u5B89\u5B9A\uFF0C\u540F\u6CBB\u6E05\u660E\u2014\u2014\u8FD9\u5C31\u662F\u516C\u5229\u3002</p><p>\u6587\u5E1D\u4E0D\u662F\u9053\u5FB7\u4E0A\u7684\u5B8C\u4EBA\uFF0C\u8FDC\u8FDC\u8FBE\u4E0D\u5230\u53E4\u4EE3\u5112\u751F\u6240\u5E0C\u671B\u7684\u201C\u5185\u5723\u5916\u738B\u201D\uFF0C\u4F46\u65E0\u7591\uFF0C\u4ED6\u662F\u4E00\u4E2A\u5408\u683C\u7684\u7687\u5E1D\u3002</p><p>\u800C\u7B2C\u4E8C\u4E2A\u70B9\uFF0C\u6211\u4EEC\u5219\u9700\u8981\u4ECE\u5143\u5609\u5317\u4F10\u7684\u60E8\u6DE1\u7ED3\u679C\u6765\u5207\u5165\u3002</p><p>\u6587\u5E1D\u56FA\u7136\u6709\u5143\u5609\u4E4B\u6CBB\u7684\u6210\u5C31\uFF0C\u4F46\u5BF9\u4ED6\u7684\u201C\u7586\u573A\u65E0\u80FD\u201D\uFF0C\u53C8\u8BE5\u5982\u4F55\u8BC4\u4EF7\u5462\uFF1F</p><p>\u8FD9\u4E2A\u95EE\u9898\u5927\u800C\u5316\u4E4B\u7684\u6362\u4E00\u4E2A\u95EE\u6CD5\uFF0C\u5176\u5B9E\u5C31\u662F\uFF1A<strong>\u597D\u7687\u5E1D\u5E94\u8BE5\u8FFD\u6C42<code>\u6B66\u529F</code>\u6781\u76DB\uFF0C\u8FD8\u662F<code>\u6587\u6CBB</code>\u660C\u9686\uFF1F</strong></p><p>\u5728\u540E\u4E16\u4EBA\u770B\u6765\uFF0C\u5F00\u7586\u62D3\u571F\u7684\u7687\u5E1D\u624D\u66F4\u8BA9\u4EBA\u94A6\u4F69\u3002\u81F3\u4E8E\u6B66\u529F\u4E0D\u8DB3\u7684\uFF0C\u4E0D\u8BBA\u662F\u671D\u4EE3\u8FD8\u662F\u7687\u5E1D\uFF0C\u90FD\u6781\u5BB9\u6613\u4E3A\u540E\u4E16\u6240\u8BDF\u75C5\u2014\u2014\u6BD4\u5982\u5B8B\u671D\u3002</p><p>\u79E6\u7687\u6C49\u6B66\u561B\uFF0C\u7C7B\u4F3C\u4E8E\u6C49\u6B66\u5E1D\u8FD9\u6837\u7684\uFF0C\u624D\u7B97\u5F97\u4E0A\u662F\u96C4\u624D\u5927\u7565\u7684\u4E00\u4EE3\u5927\u5E1D\uFF1B\u81F3\u4E8E\u6C49\u6587\u5E1D\u8FD9\u6837\u5BF9\u5308\u5974\u59D4\u66F2\u6C42\u5168\u7684\uFF0C\u600E\u4E48\u7740\u4E5F\u603B\u5F52\u5DEE\u4E00\u70B9\u3002</p><p>\u8FD9\u4E2A\u7C7B\u6BD4\uFF0C\u5373\u4FBF\u653E\u5728\u5218\u5B8B\u65F6\u671F\u4E5F\u80FD\u6210\u7ACB\uFF1A<strong>\u5B8B\u6B66\u5E1D<code>\u5218\u88D5</code></strong>\uFF0C\u5BF9\u5185\u524A\u5E73\u53DB\u4E71\uFF0C\u5BF9\u5916\u5317\u4F10\u4E2D\u539F\uFF0C\u4E00\u751F\u5F81\u6218\uFF0C\u51E0\u65E0\u8D25\u7EE9\uFF0C\u6B66\u529F\u8D6B\u8D6B\uFF0C\u5357\u671D\u4E0D\u505A\u7B2C\u4E8C\u4EBA\u60F3\u3002\u81F3\u4E8E<strong>\u5B8B\u6587\u5E1D<code>\u5218\u4E49\u9686</code></strong>\uFF0C\u597D\u5927\u559C\u529F\uFF0C\u5143\u5609\u8349\u8349\uFF0C\u65E0\u529F\u800C\u8FD4\u3002</p><p>\u6240\u4EE5\uFF0C\u6B66\u5E1D\u8FD8\u662F\u8981\u5F3A\u4E8E\u6587\u5E1D\u7684\u3002</p><p>\u4F46\u662F\uFF0C\u8FD9\u662F\u540E\u4E16\u4EBA\u5BF9\u7740\u53F2\u7C4D\u58A8\u8FF9\u624D\u4F1A\u53D1\u51FA\u7684\u6307\u70B9\u4E0E\u611F\u6168\u3002\u4F60\u82E5\u662F\u5F53\u65F6\u4E4B\u4EBA\uFF0C\u5C24\u5176\u662F\u666E\u901A\u767E\u59D3\uFF0C\u90A3\u4E48\uFF0C\u4F60\u5F88\u53EF\u80FD\u4F1A\u7ED9\u51FA\u76F8\u53CD\u7684\u7B54\u6848\u3002</p><p>\u5F53\u4F60\u4E0D\u8FC7\u662F\u4E00\u4ECB\u8349\u6C11\uFF0C\u4F60\u662F\u613F\u610F\u751F\u6D3B\u5728\u6B66\u529F\u8D6B\u8D6B\u7684\u6C49\u6B66\u5E1D\u65F6\u4EE3\uFF0C\u8FD8\u662F\u613F\u610F\u6295\u751F\u5230\u6587\u6CBB\u660C\u9686\u7684\u5B8B\u4EC1\u5B97\u65F6\u4EE3\uFF1F</p><p>\u53EF\u80FD\u5927\u591A\u6570\u4EBA\u90FD\u4F1A\u9009\u62E9\u540E\u8005\u3002</p><p>\u4F60\u8BFB\u53F2\u65F6\uFF0C\u56FA\u7136\u89C9\u5F97\u6A2A\u626B\u585E\u5317\u3001\u5C01\u72FC\u5C45\u80E5\u7684\u58EE\u4E3E\u8BA9\u4EBA\u70ED\u8840\u6F8E\u6E43\uFF0C\u60F3\u60F3\u90FD\u4F1A\u6FC0\u52A8\u4E0D\u5DF2\u3002\u4F46\u518D\u4E00\u60F3\uFF0C\u8FD9\u6837\u7684\u4E30\u529F\u4F1F\u7EE9\uFF0C\u90FD\u662F\u7528\u201C\u6237\u53E3\u51CF\u534A\u3001\u5929\u4E0B\u9A9A\u7136\u201D\u6362\u6765\u7684\u3002\u800C\u5F53\u4F60\u53EA\u662F\u4E00\u4E2A\u751F\u6D3B\u5728\u90A3\u4E2A\u65F6\u4EE3\u7684\u666E\u901A\u4EBA\uFF0C\u90A3\u4E48\u4F60\u53EF\u80FD\u8DDF\u8FD9\u4E9B\u5F6A\u70B3\u53F2\u518C\u7684\u529F\u4E1A\u90FD\u4E0D\u642D\u754C\uFF0C\u5927\u6982\u7387\u53EA\u4F1A\u53D8\u6210\u6B66\u5E1D\u96C4\u624D\u5927\u7565\u7684\u201C\u4EE3\u4EF7\u201D\u2014\u2014\u7B80\u79F0\u70AE\u7070\u3002</p><p>\u76F8\u53CD\uFF0C\u5BF9\u666E\u901A\u4EBA\u800C\u8A00\uFF0C\u5B8B\u4EC1\u5B97\u5728\u4F4D\u65F6\u671F\u53EF\u80FD\u624D\u662F\u5E1D\u5236\u65F6\u4EE3\u7684\u5929\u5802\u3002\u4E14\u4E0D\u8BF4\u4E1C\u4EAC\u57CE\u91CC\u7684\u65E0\u9650\u7E41\u534E\uFF0C\u5355\u8BF4\u6574\u4F53\u4E0A\u7684\u653F\u6CBB\u6E05\u660E\uFF0C\u5C31\u610F\u5473\u7740\u516C\u5E73\u6B63\u4E49\u4E5F\u662F\u666E\u901A\u4EBA\u53EF\u53BB\u8FFD\u6C42\u7684\u76EE\u6807\u3002\u4F60\u53EA\u8981\u4E0D\u662F\u751F\u6D3B\u5728\u897F\u590F\u8FB9\u754C\uFF0C\u90A3\u4E48\u5F81\u5FAD\u5F79\u7684\u4E8B\u57FA\u672C\u4E0A\u4E5F\u5C31\u8DDF\u4F60\u6CA1\u6709\u5173\u7CFB\uFF0C\u4F60\u5B8C\u5168\u53EF\u4EE5\u5F00\u5F00\u5FC3\u5FC3\u7684\u8FC7\u81EA\u5DF1\u7684\u5C0F\u65E5\u5B50\u3002</p><p>\u8FD9\uFF0C\u4E5F\u624D\u662F\u5143\u5609\u4E4B\u6CBB\u6240\u95EA\u8000\u7684\u65E0\u4E0A\u5149\u8F89\uFF1A<strong>\u5728\u6C49\u672B\u4EE5\u6765\u8FD1\u56DB\u767E\u5E74\u7684\u6218\u4E71\u4E2D\uFF0C\u6B64\u65F6\u7684\u8001\u767E\u59D3\uFF0C\u53EF\u80FD\u5DF2\u7ECF\u662F\u6700\u4E3A\u5E78\u8FD0\u7684\u7FA4\u4F53\u3002</strong></p><p>\u5C24\u5176\uFF0C\u662F\u8FD9\u4E2A\u65F6\u4EE3\u5927\u90FD\u662F\u4EE5\u4E71\u4E16\uFF0C\u6216\u8005\u8BF4\u8870\u4E16\u7684\u6837\u5B50\u51FA\u73B0\u7684\uFF1A\u8981\u4E48\u671D\u653F\u8150\u8D25\uFF0C\u5404\u7EA7\u5B98\u540F\u6A2A\u5F81\u66B4\u655B\u3001\u76D8\u5265\u767E\u59D3\uFF1B\u8981\u4E48\u7687\u5E1D\u597D\u5927\u559C\u529F\u3001\u6EE5\u7528\u6C11\u529B\uFF1B\u6216\u8005\u662F\u56FD\u5BB6\u5E38\u5E74\u6218\u4E89\uFF0C\u767E\u59D3\u75B2\u4E8E\u5E94\u4ED8\uFF1B\u6216\u8005\u662F\u653F\u5C40\u52A8\u8361\uFF0C\u671D\u4EE4\u5915\u6539\u3002</p><p>\u5143\u5609\u4E4B\u6CBB\u8FD9\u77ED\u77ED\u7684\u51E0\u5341\u5E74\uFF0C\u5BF9\u65F6\u4EBA\u6765\u8BF4\uFF0C\u5DF2\u7ECF\u662F\u5F25\u8DB3\u73CD\u8D35\u7684\u597D\u65F6\u4EE3\u3002</p><p>\u5F53\u4F60\u7AD9\u5728\u540E\u4E16\u770B\u5386\u53F2\uFF0C\u4F60\u662F\u4F53\u4F1A\u4E0D\u5230\u5F53\u65F6\u4EBA\u7684\u5207\u8EAB\u611F\u53D7\u7684\uFF1A</p><p>\u5F81\u53D1\u5FAD\u5F79\uFF0C\u5728\u540E\u4E16\u4EBA\u770B\u6765\uFF0C\u53EA\u662F\u4E00\u573A\u5927\u6218\u80CC\u540E\u6B63\u5E38\u7684\u540E\u52E4\u51C6\u5907\uFF0C\u4F46\u5BF9\u4E8E\u88AB\u5F81\u53D1\u7684\u767E\u59D3\u6765\u8BF4\uFF0C\u5374\u53EF\u80FD\u5C31\u610F\u5473\u7740\u59BB\u79BB\u5B50\u6563\uFF0C\u5BA2\u6B7B\u4ED6\u4E61\u2014\u2014\u675C\u752B\u5728\u300A\u5175\u8F66\u884C\u300B\u300A\u77F3\u58D5\u540F\u300B\u91CC\uFF0C\u5DF2\u7ECF\u751F\u52A8\u7684\u63CF\u5199\u8FC7\u666E\u901A\u767E\u59D3\u7684\u8FD9\u79CD\u5883\u5730\u3002</p><p>\u6A2A\u5F81\u66B4\u655B\uFF0C\u5728\u540E\u4E16\u4EBA\u770B\u6765\uFF0C\u53EA\u662F\u653F\u6CBB\u9ED1\u6697\u7684\u4E00\u4E2A\u5177\u4F53\u7F29\u5F71\u3002\u4F46\u8BBE\u8EAB\u5904\u5730\u7684\u60F3\u4E00\u4E0B\uFF0C\u5982\u679C\u6709\u5B98\u540F\u5230\u4F60\u5BB6\u91CC\uFF0C\u62A2\u8D70\u4E86\u4F60\u660E\u5929\u7684\u53E3\u7CAE\uFF0C\u800C\u4F60\u5374\u6B32\u54ED\u65E0\u6CEA\u7533\u8BC9\u65E0\u95E8\uFF0C\u90A3\u4F1A\u662F\u600E\u6837\u7684\u4E00\u79CD\u7EDD\u671B\uFF01</p><p>\u540E\u4E16\u7684\u4EBA\uFF0C\u5173\u6CE8\u70B9\u6C38\u8FDC\u90FD\u662F\u767D\u7389\u4E30\u7891\u4E0A\u90A3\u4E9B\u4EE4\u4EBA\u6FC0\u6602\u7684\u4E30\u529F\u4F1F\u7EE9\uFF0C\u53EA\u6709\u7B49\u5230\u81EA\u5DF1\u5207\u5207\u5B9E\u5B9E\u7684\u751F\u6D3B\u5230\u4E86\u90A3\u4E2A\u65F6\u4EE3\uFF0C\u7740\u5E03\u8863\u3001\u98DF\u8349\u6839\uFF0C\u624D\u80FD\u660E\u767D\u5927\u4EBA\u7269\u7684\u7406\u60F3\u548C\u62B1\u8D1F\u5176\u5B9E\u79BB\u81EA\u5DF1\u5F88\u8FDC\u3002</p><p>\u771F\u6B63\u4E0E\u4F60\u76F8\u5173\u7684\uFF0C\u4E0D\u8FC7\u662F\u6751\u91CC\u7684\u957F\u8001\u4EEC\u662F\u5426\u80FD\u6301\u5E73\u5904\u4E8B\uFF0C\u4E61\u91CC\u7684\u91CC\u6B63\u5728\u6536\u7A0E\u65F6\u80FD\u5426\u5C11\u8D2A\u5C11\u5360\uFF0C\u53BF\u8859\u91CC\u7684\u6355\u5FEB\u4EEC\u4F1A\u4E0D\u4F1A\u7ECF\u5E38\u5230\u5BB6\u91CC\u6765\u5403\u62FF\u5361\u8981\u3002</p><p>\u4EBA\u5728\u7CBE\u795E\u5883\u754C\u4E2D\u5F9C\u5F89\u65F6\uFF0C\u90FD\u559C\u6B22\u5B8F\u5927\uFF0C\u4F46\u56DE\u5230\u4E86\u771F\u5B9E\u4E16\u754C\u4E2D\u751F\u5B58\uFF0C\u5374\u5F97\u5173\u6CE8\u7EC6\u8282\u3002\u8BFB\u53F2\u65F6\uFF0C\u5C3D\u53EF\u4EE5\u53CD\u590D\u9882\u626C\u90A3\u4E9B\u626C\u6211\u56FD\u5A01\u7684\u65F6\u523B\uFF0C\u53BB\u559C\u6B22\u201C\u540D\u72AF\u5F3A\u6C49\u8005\uFF0C\u867D\u8FDC\u5FC5\u8BDB\u201D\u7684\u96F7\u9706\u6C14\u52BF\uFF0C\u4F46\u56DE\u5230\u771F\u5B9E\u7684\u751F\u6D3B\u4E2D\uFF0C\u5374\u65E0\u4E0D\u613F\u5929\u4E0B\u65E0\u4E8B\uFF0C\u56FD\u6CF0\u6C11\u5B89\u3002</p><p>\u51ED\u541B\u83AB\u8BDD\u5C01\u4FAF\u4E8B\uFF0C\u4E00\u5C06\u529F\u6210\u4E07\u9AA8\u67AF\u3002\u8BFB\u53F2\u4E66\u65F6\uFF0C\u5F88\u591A\u4EBA\u90FD\u4F1A\u8C6A\u60C5\u4E07\u4E08\u5730\u89C9\u5F97\u81EA\u5DF1\u662F\u90A3\u4E2A\u201C\u5C06\u201D\uFF0C\u4F46\u6B8A\u4E0D\u77E5\uFF0C\u4F60\u6211\u4E4B\u8F88\uFF0C\u53EA\u662F\u201C\u9AA8\u201D\u800C\u5DF2\u3002</p><p>\u53EF\u601C\u65E0\u5B9A\u6CB3\u8FB9\u9AA8\uFF0C\u72B9\u662F\u6DF1\u95FA\u68A6\u91CC\u4EBA\u3002</p>',57),n=[r];function s(d,a){return o(),e("div",null,n)}const l=p(c,[["render",s],["__file","56.html.vue"]]);export{l as default};
