import{_ as e}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as c,c as d,d as t,w as s,f as p,r,a as n}from"./app.e2de568c.js";const a="/myNotes/assets/img/base/1666841084700.jpg",g="/myNotes/assets/img/base/1666841311323.jpg",i="/myNotes/assets/img/base/1666841527389.jpg",l="/myNotes/assets/img/base/1666850857137.jpg",m={},_=p('<h1 id="\u66B4\u541B" tabindex="-1"><a class="header-anchor" href="#\u66B4\u541B" aria-hidden="true">#</a> \u66B4\u541B</h1><blockquote><p>\u7678\u9149\uFF0C\u5E1D\u81EA\u5E05\u7FBD\u6797\u5175\u8BA8\u4E49\u606D\uFF0C\u6740\u4E4B\uFF0C\u5E76\u5176\u56DB\u5B50\u3002\u65AD\u7EDD\u4E49\u606D\u652F\u4F53\uFF0C\u5206\u88C2\u80A0\u80C3\uFF0C\u6311\u53D6\u773C\u775B\uFF0C\u4EE5\u871C\u6E0D\u4E4B\uFF0C\u8C13\u4E4B\u201C\u9B3C\u76EE\u7CBD\u201D\u3002</p></blockquote><p><strong>\u300A\u8D44\u6CBB\u901A\u9274\xB7\u5377\u4E00\u4E09\u96F6\xB7\u5B8B\u7EAA\u5341\u4E8C\u300B</strong></p><p>\u81EA\u4ECE**\u5B8B\u6587\u5E1D<code>\u5218\u4E49\u9686</code><strong>\u6B7B\u540E\uFF0C\u4E00\u76F4\u5230</strong>\u6881\u6B66\u5E1D<code>\u8427\u884D</code>**\u4E4B\u524D\uFF0C\u5357\u671D\u5728\u8FD9\u4E94\u5341\u5E74\u91CC\uFF0C\u53EF\u5199\u7684\u4E8B\u60C5\u5C31\u53EA\u6709\u4E00\u4EF6\uFF1A</p>',4),h=n("b",null,"\u6740\u622E",-1),u=p('<p>\u8FD9\u4E9B\u53CD\u590D\u4E0A\u6F14\u7684\u6B8B\u6740\uFF0C\u5168\u90E8\u90FD\u53D1\u751F\u5728\u7687\u65CF\u5185\u90E8\uFF1A\u7687\u4F4D\u4F20\u627F\u65F6\u8981\u6740\uFF0C\u7687\u4F4D\u6CA1\u6709\u4F20\u627F\u65F6\u4E5F\u8981\u6740\u3002\u8F93\u4E86\u7684\u81EA\u7136\u5BB6\u7834\u4EBA\u4EA1\uFF0C\u4F46\u8D62\u4E86\u7684\u4E5F\u4E0D\u7528\u5FD7\u5F97\u610F\u6EE1\uFF0C\u56E0\u4E3A\u53EF\u80FD\u8FC7\u4E0D\u4E86\u591A\u5C11\u5E74\uFF0C\u4F9D\u7136\u8FD8\u662F\u4E2A\u5BB6\u7834\u4EBA\u4EA1\u7684\u7ED3\u5C40\u3002</p><p>\u6B64\u65F6\u5317\u65B9\u5DF2\u7ECF\u5728\u8FDB\u884C\u7740\u81EA\u4E94\u80E1\u4E71\u534E\u4EE5\u6765\u5C31\u6301\u7EED\u4E0D\u65AD\u7684\u6C11\u65CF\u878D\u5408\uFF0C\u800C\u4E14\u8FD8\u5353\u6709\u6210\u6548\u3002\u4F46\u5357\u671D\u5374\u59CB\u7EC8\u6DF1\u9677\u4E8E\u81EA\u76F8\u6B8B\u6740\u4E4B\u4E2D\uFF0C\u4E0D\u53EF\u81EA\u62D4\u3002</p><p>\u8BDD\u8BF4\u8D77\u6765\uFF0C\u5218\u5B8B\u7684\u7687\u4F4D\u4F20\u627F\uFF0C\u4F3C\u4E4E\u4ECE\u6765\u5C31\u6CA1\u6709\u6B63\u5E38\u8FC7\u3002</p><p>\u5F00\u56FD\u7684**\u5B8B\u6B66\u5E1D<code>\u5218\u88D5</code>**\u5728\u6B7B\u65F6\uFF0C\u7ED9\u4ED6\u7684\u592A\u5B50<code>\u5218\u4E49\u82FB</code>\u8BBE\u4E86\u56DB\u4E2A\u8F85\u653F\u5927\u81E3\u3002\u7136\u800C\u624D\u8FC7\u4E86\u4E00\u5E74\u591A\u65F6\u95F4\uFF0C\u8FD9\u56DB\u4E2A\u8F85\u653F\u5927\u81E3\u5C31\u628A\u4ED6\u7684\u513F\u5B50\u7ED9\u6740\u4E86\u2014\u2014\u8FD8\u4E0D\u662F\u6740\u4E86\u4E00\u4E2A\uFF0C\u662F\u4E00\u6B21\u5C31\u6740\u4E86\u4FE9\uFF0C**\u5B8B\u5C11\u5E1D<code>\u5218\u4E49\u7B26</code>**\u548C\u4ED6\u7684\u5F1F\u5F1F<code>\u5218\u4E49\u771F</code>\u90FD\u6B7B\u4E8E\u5200\u4E0B\u3002</p><p>\u4E4B\u540E\uFF0C\u4E09\u513F\u5B50<code>\u5218\u4E49\u9686</code>\u987A\u52BF\u8865\u4F4D\uFF0C\u662F\u4E3A<strong>\u5B8B\u6587\u5E1D</strong>\u3002\u5B8B\u6587\u5E1D\u5728\u4F4D30\u5E74\uFF0C\u5148\u662F\u9664\u6389\u4E86\u4ED6\u7239\u7559\u4E0B\u7684\u8FD9\u56DB\u4E2A\u8F85\u653F\u5927\u81E3\uFF0C\u7136\u540E\u53C8\u9010\u6E10\u758F\u8FDC\u5E76\u903C\u6B7B\u4E86\u4E00\u76F4\u5E2E\u7740\u81EA\u5DF1\u5904\u7406\u671D\u653F\u7684\u5F1F\u5F1F\u3001\u5F6D\u57CE\u738B<code>\u5218\u4E49\u5EB7</code>\u3002</p><p>\u6700\u7EC8\uFF0C\u8FD9\u4F4D\u521B\u4E0B\u201C\u5143\u5609\u4E4B\u6CBB\u201D\u7684\u7687\u5E1D\u88AB\u81EA\u5DF1\u7684\u592A\u5B50<code>\u5218\u52AD</code>\u6240\u6740\u3002\u4F46\u5218\u52AD\u81EA\u5DF1\u4E5F\u6CA1\u6D3B\u8FC7\u591A\u4E45\uFF0C\u4ED6\u5F11\u7236\u7EE7\u4F4D\u540E\uFF0C\u5F1F\u5F1F\u6B66\u9675\u738B<code>\u5218\u9A8F</code>\u4E0D\u4E45\u5C31\u8D77\u5175\u53CD\u6297\u5E76\u53D6\u5F97\u6210\u529F\uFF0C\u5218\u52AD\u6700\u7EC8\u88AB\u6740\u3002</p><p><code>\u5218\u9A8F</code>\u4E4B\u540E\u79F0\u5E1D\uFF0C\u662F\u4E3A<strong>\u5B8B\u5B5D\u6B66\u5E1D</strong>\u3002\u4ED6\u5728\u4F4D\u7684\u65F6\u95F4\u4E0D\u7B97\u77ED\uFF0C\u5927\u7EA6\u6709\u5341\u4E00\u5E74\u3002\u4F46\u671F\u95F4\u4ED6\u5728\u7687\u4F4D\u4E0A\u4E5F\u5750\u5F97\u5E76\u4E0D\u5B89\u7A33\uFF0C\u4ED6\u7684\u53D4\u7236\u5357\u90E1\u738B<code>\u5218\u4E49\u5BA3</code>\u3001\u5F1F\u5F1F\u7ADF\u9675\u738B<code>\u5218\u8BDE</code>\u5148\u540E\u90FD\u5411\u7687\u4F4D\u53D1\u8D77\u8FC7\u51B2\u51FB\uFF0C\u53EA\u4E0D\u8FC7\u6700\u7EC8\u90FD\u88AB\u5E73\u5B9A\u3002</p><p><img src="'+a+'" alt="demo" loading="lazy"></p><p>**\u5728\u5B8B\u5B5D\u6B66\u5E1D\u5218\u9A8F\u5728\u4F4D\u7684\u8FD9\u5341\u4E00\u5E74\u91CC\uFF0C\u771F\u6B63\u60E8\u7684\uFF0C\u5176\u5B9E\u662F\u5927\u81E3\u4EEC\u3002**\u5BF9\u4E8E\u4ED6\u4EEC\u6765\u8BF4\uFF0C\u5B8C\u5168\u662F\u5669\u68A6\u4E00\u573A\u3002</p><p>\u9996\u5148\uFF0C\u5B8B\u5B5D\u6B66\u5E1D\u5218\u9A8F\u5B9E\u5728\u662F\u592A\u559C\u6B22\u7ED9\u81E3\u4E0B\u4EEC\u8D77\u5916\u53F7\u4E86\u3002\u6BD4\u5982\uFF0C<code>\u738B\u7384\u8C1F</code>\u88AB\u53EB\u505A\u201C\u8001\u4F27\u201D\uFF0C\u201C\u4F27\u201D\u662F\u5F53\u65F6\u5357\u65B9\u4EBA\u5BF9\u5317\u6765\u6C49\u4EBA\u7684\u8511\u79F0\u3002</p><p>\u738B\u7384\u8C1F\u51FA\u81EA\u592A\u539F\u738B\u6C0F\uFF0C\u8FD9\u539F\u672C\u662F\u5357\u671D\u7684\u9876\u7EA7\u58EB\u65CF\u3002\u4F46\u65E0\u5948\u6B64\u65F6\u5DF2\u7ECF\u4E0D\u662F\u51E0\u5341\u5E74\u524D\u58EB\u65CF\u5171\u6CBB\u7684\u65F6\u4EE3\u4E86\uFF0C\u5982\u4ECA\u7687\u6743\u4E00\u679D\u72EC\u5927\uFF0C\u738B\u7384\u8C1F\u53EA\u80FD\u662F\u4F4E\u5934\u63A5\u53D7\u8FD9\u4E2A\u5145\u6EE1\u4FAE\u8FB1\u7684\u5916\u53F7\u3002</p><p>\u800C\u4E14\u8FD9\u8FD8\u4E0D\u7B97\u662F\u6700\u96BE\u542C\u7684\u3002\u4EC6\u5C04<code>\u5218\u79C0\u4E4B</code>\u88AB\u53EB\u6210\u4E86\u201C\u8001\u60AD\u201D\uFF0C\u8FD9\u5927\u7EA6\u662F\u56E0\u4E3A\u4ED6\u541D\u556C\uFF1B<code>\u989C\u5E08\u4F2F</code>\u5219\u559C\u83B7\u201C\u9F74\u201D\u7684\u79F0\u53F7\uFF0C\u7FFB\u8BD1\u6210\u73B0\u4EE3\u6C49\u8BED\u5C31\u662F\u201C\u9F85\u7259\u54E5\u54E5\u201D\uFF1B\u81F3\u4E8E\u5176\u4ED6\u4EBA\uFF0C\u201C\u77ED\u3001\u957F\u3001\u80A5\u3001\u7626\uFF0C\u7686\u6709\u79F0\u76EE\u201D\u3002</p><p>\u53E4\u5F80\u4ECA\u6765\uFF0C\u4F60\u5C31\u6CA1\u89C1\u8FC7\u8FD9\u4E48\u5634\u6B20\u4E0D\u5E84\u91CD\u7684\u7687\u5E1D\u3002</p><p>\u800C\u5982\u679C\u4EC5\u4EC5\u662F\u8FD9\u79CD\u79F0\u8C13\u4E0A\u7684\u4FAE\u8FB1\uFF0C\u90A3\u4E5F\u8FD8\u662F\u5C0F\u4E8B\u3002\u771F\u6B63\u53EF\u6015\u7684\uFF0C\u8FD8\u5728\u4E8E\u5218\u5B8B\u7687\u5BA4\u5BB6\u4F20\u7684\u731C\u5FCC\u7EDD\u5B66\uFF0C\u5230\u4E86\u5218\u9A8F\u8FD9\u91CC\u88AB\u8FDB\u4E00\u6B65\u53D1\u626C\u5149\u5927\u3002\u5728\u4ED6\u7684\u6CBB\u4E0B\uFF0C\u80FD\u6D3B\u4E0B\u6765\u5B8C\u5168\u5C31\u662F\u4E00\u79CD\u5E78\u8FD0\u3002</p><p><code>\u989C\u7AE3</code>\u65E9\u5E74\u95F4\u8DDF\u968F\u5B5D\u6B66\u5E1D\u4E00\u8D77\u8D77\u5175\u53CD\u6297\u5218\u52AD\uFF0C\u540E\u6765\u529F\u6210\uFF0C\u6069\u5BA0\u65E0\u4EBA\u80FD\u6BD4\uFF0C\u4F46\u5374\u56E0\u4E3A\u8BAA\u8C24\u671D\u5EF7\u88AB\u6740\u3002\u4E00\u6837\u88AB\u6740\u7684\uFF0C\u8FD8\u6709\u51FA\u81EA\u4E8E\u7405\u740A\u738B\u6C0F\u7684<code>\u738B\u50E7\u8FBE</code>\u3002</p><p><code>\u6C88\u6000\u6587</code>\u5219\u56E0\u4E3A\u4E0D\u7ED9\u5B5D\u6B66\u5E1D\u9762\u5B50\uFF0C\u4E0D\u613F\u610F\u5728\u9152\u5BB4\u4E0A\u966A\u9152\u800C\u88AB\u5FCC\u6068\u3002\u8FD9\u4EFD\u6068\u610F\u662F\u6700\u6DF1\u7684\uFF0C\u5B5D\u6B66\u5E1D\u4E0D\u4EC5\u6740\u4E86\u6C88\u6000\u6587\uFF0C\u540E\u6765\u8FD8\u4F9D\u7136\u89C9\u5F97\u4E0D\u6EE1\u610F\uFF0C\u53C8\u6740\u4E86\u4ED6\u7684\u4E09\u4E2A\u513F\u5B50\u3002</p><p>\u56E0\u6B64\u5230\u5B5D\u6B66\u5E1D\u6B7B\u7684\u65F6\u5019\uFF0C\u5927\u81E3\u4EEC\u7B80\u76F4\u5F00\u5FC3\u5F97\u4E0D\u8981\u4E0D\u8981\u7684\u3002\u5C31\u8FDE\u5B5D\u6B66\u5E1D\u81EA\u5DF1\u9009\u51FA\u6765\u7684\u6258\u5B64\u5927\u81E3\uFF1A\u592A\u5BB0<code>\u5218\u4E49\u606D</code>\uFF08\u5B5D\u6B66\u5E1D\u81EA\u5DF1\u7684\u53D4\u53D4\uFF09\u3001<code>\u989C\u5E08\u4F2F</code>\uFF08\u9F85\u7259\u54E5\u54E5\uFF09\u4EE5\u53CA<code>\u67F3\u5143\u666F</code>\u7B49\u4EBA\uFF0C\u90FD\u559C\u60A6\u6EA2\u4E8E\u8A00\u8868\uFF0C\u7ADF\u7136\u65E5\u591C\u4E0D\u505C\u5730\u559D\u9152\u4F5C\u4E50\u3002</p><p>\u5B8B\u5B5D\u6B66\u5E1D\u7684\u6B7B\uFF0C\u5BF9\u4E8E\u53C2\u52A0\u4ED6\u7684\u846C\u793C\u7684\u4EBA\u6765\u8BF4\uFF0C\u5B8C\u5168\u5C31\u662F\u4E00\u573A\u201C\u544A\u522B\u65E7\u65F6\u4EE3\xB7\u8FCE\u63A5\u65B0\u672A\u6765\u201D\u7684\u6B22\u4E50\u4E3B\u9898\u76DB\u5178\u3002</p><p>\u628A\u8FD9\u79CD\u6FC0\u52A8\u7684\u5FC3\u60C5\u4E00\u4E0D\u5C0F\u5FC3\u5C31\u5C55\u9732\u51FA\u6765\u4E86\u7684\uFF0C\u8FD8\u6709\u5B5D\u6B66\u5E1D\u7684\u592A\u5B50<code>\u5218\u5B50\u4E1A</code>\u3002</p><p>\u4E5F\u4E0D\u80FD\u8BF4\u5218\u5B50\u4E1A\u662F\u628A\u9AD8\u5174\u5199\u5728\u4E86\u8138\u4E0A\uFF0C\u603B\u5F52\u662F\u81EA\u5DF1\u7684\u8001\u5B50\u6B7B\u4E86\uFF0C\u5FC3\u91CC\u5C31\u7B97\u518D\u5F00\u5FC3\uFF0C\u4E5F\u77E5\u9053\u4E0D\u80FD\u7B11\u51FA\u58F0\u6765\u3002\u4F46\u4ED6\u8138\u4E0A\u7684\u8868\u60C5\u5B9E\u5728\u4E0D\u592A\u6B63\u5E38\uFF0C\u85CF\u90FD\u85CF\u4E0D\u4F4F\uFF0C\u8FD8\u662F\u88AB\u4EBA\u4E00\u773C\u5C31\u7ED9\u770B\u4E86\u51FA\u6765\uFF1A\u540F\u90E8\u5C1A\u4E66<code>\u8521\u5174\u5B97</code>\u5728\u628A\u7389\u73BA\u4EA4\u7ED9\u5218\u5B50\u4E1A\u65F6\uFF0C\u770B\u5230\u5BF9\u65B9\u201C<strong>\u50B2\u60F0\u65E0\u621A\u5BB9</strong>\u201D\u3002</p><p>\u8FD9\u79CD\u4E0D\u5B5D\u7684\u884C\u4E3A\uFF0C\u5728\u4EE5\u5112\u5BB6\u4E3A\u4E3B\u7684\u4F20\u7EDF\u4EF7\u503C\u89C2\u91CC\u518D\u6076\u52A3\u4E0D\u8FC7\u3002\u6070\u5982\u300A\u5DE6\u4F20\u300B\u6709\u4E4C\u9E26\u5634\u5F0F\u7684\u9884\u8A00\u4F20\u7EDF\u4E00\u822C\uFF0C\u8521\u5174\u5B97\u4E5F\u8BF4\u4E86\u4E00\u53E5\u7C7B\u4F3C\u7684\u8BDD\uFF0C\u201C<strong>\u5BB6\u56FD\u4E4B\u7978\uFF0C\u5176\u5728\u6B64\u4E4E\uFF01</strong>\u201D</p><p>\u5218\u5B50\u4E1A\u6B64\u65F6\u53EA\u670916\u5C81\uFF0C\u56E0\u6B64\u5B5D\u6B66\u5E1D\u7ED9\u4ED6\u7559\u4E86\u4E94\u4E2A\u987E\u547D\u5927\u81E3\u3002\u5927\u81E3\u4EEC\u5404\u6709\u5206\u5DE5\uFF0C\u4F46\u5728\u8FD9\u91CC\uFF0C\u6211\u4EEC\u5C31\u4E0D\u4E00\u4E00\u4ECB\u7ECD\u4E86\uFF0C\u56E0\u4E3A\u8FD9\u51E0\u4E2A\u4EBA\u9664\u4E86\u738B\u7384\u8C1F\uFF08\u4E5F\u5C31\u662F\u201C\u8001\u4F27\u201D\u540C\u5FD7\uFF09\uFF0C\u5176\u4ED6\u7684\u90FD\u6D3B\u4E0D\u957F\u3002\u4ED6\u4EEC\u5C06\u5728\u4E00\u5E74\u4E4B\u5185\uFF0C\u88AB\u5218\u5B50\u4E1A\u5168\u90E8\u6740\u6389\u3002</p><p>\u88AB\u6740\u7684\u76F4\u63A5\u8BF1\u56E0\uFF0C\u662F\u5218\u3001\u989C\u3001\u67F3\u4E09\u4E2A\u4EBA\u7684party\u5F00\u5F97\u592A\u8FC7\u5206\u4E86\u3002\u540C\u4E3A\u6258\u5B64\u5927\u81E3\u7684<code>\u6C88\u5E86\u4E4B</code>\u8DDF\u8FD9\u51E0\u4E2A\u4EBA\u4E0D\u5BF9\u4ED8\uFF0C\u4E8E\u662F\u4FBF\u4E0A\u594F\u4E86\u5218\u5B50\u4E1A\u3002\u5218\u5B50\u4E1A\u95FB\u8BAF\u540E\u4E8C\u8BDD\u6CA1\u8BF4\uFF0C\u5E26\u5175\u5C31\u6740\u5230\u4E86<code>\u5218\u4E49\u606D</code>\u7684\u5BB6\u91CC\u3002</p><p>\u5218\u4E49\u606D\u662F\u5218\u5B50\u4E1A\u7684\u7237\u7237\u8F88\uFF0C\u4F46\u5218\u5B50\u4E1A\u538B\u6839\u5C31\u6CA1\u628A\u4EBA\u5BB6\u5F53\u56DE\u4E8B\uFF0C\u81EA\u4E2A\u513F\u5E26\u7740\u7FBD\u6797\u519B\u5C31\u6740\u4E86\u5218\u4E49\u606D\u548C\u4ED6\u7684\u56DB\u4E2A\u513F\u5B50\u3002</p><p>\u8FD9\u8FD8\u4E0D\u89E3\u6C14\uFF0C\u5218\u5B50\u4E1A\u53C8\u628A\u5218\u4E49\u606D\u7684\u56DB\u80A2\u7ED9\u5272\u4E86\u4E0B\u6765\uFF0C\u809A\u5B50\u5228\u5F00\uFF0C\u53D6\u51FA\u80A0\u80C3\uFF0C\u6316\u6389\u773C\u775B\uFF0C\u5728\u773C\u775B\u548C\u809A\u5B50\u91CC\u704C\u4E0A\u871C\uFF0C\u79F0\u4E4B\u4E3A\u201C<strong>\u9B3C\u76EE\u7CBD</strong>\u201D\u3002</p><p>\u81F3\u6B64\uFF0C\u5218\u4E49\u606D\u4E00\u8109\u7B97\u662F\u88AB\u6740\u5F97\u5E72\u5E72\u51C0\u51C0\u3002\u4ED6\u672C\u6765\u662F\u6709\u5341\u4E8C\u4E2A\u513F\u5B50\u7684\uFF0C\u4F46\u4E4B\u524D\u56E0\u4E3A\u53CD\u5BF9\u5218\u52AD\u79F0\u5E1D\uFF0C\u5DF2\u7ECF\u5168\u90E8\u88AB\u5218\u52AD\u6740\u6389\u4E86\u3002\u5982\u4ECA\u8FD9\u56DB\u4E2A\u662F\u540E\u751F\u7684\uFF0C\u73B0\u5728\u53C8\u88AB\u4E00\u9505\u7AEF\u4E86\u3002</p><p><img src="'+g+'" alt="demo" loading="lazy"></p><p>\u6740\u4E86\u5218\u4E49\u606D\u8FD8\u4E0D\u7B97\u5B8C\uFF0C\u5218\u5B50\u4E1A\u968F\u5373\u53C8\u4E0B\u8BCF\u8981\u6C42<code>\u67F3\u5143\u666F</code>\u89D0\u89C1\u3002\u67F3\u5143\u666F\u81EA\u77E5\u4E0D\u514D\uFF0C\u4FBF\u8F9E\u522B\u6BCD\u4EB2\uFF0C\u6574\u7406\u597D\u671D\u670D\u89D0\u89C1\u3002\u4ED6\u4EBA\u624D\u8D70\u5230\u534A\u8DEF\uFF0C\u5C31\u78B0\u5230\u4E86\u524D\u6765\u6740\u4ED6\u7684\u58EB\u5175\u3002\u8DDF\u968F\u4ED6\u4E00\u540C\u88AB\u6740\u7684\uFF0C\u8FD8\u6709\u4ED6\u7684\u516B\u4E2A\u513F\u5B50\u3001\u516D\u4E2A\u5F1F\u5F1F\u4EE5\u53CA\u4F84\u5B50\u4EEC\u3002</p><p>\u540C\u6837\u88AB\u53EC\u89C1\u7684\uFF0C\u8FD8\u6709<code>\u989C\u5E08\u4F2F</code>\u3002\u4E00\u6837\u5730\uFF0C\u5175\u58EB\u4EEC\u4E5F\u662F\u5728\u8DEF\u4E0A\u5835\u4E86\u4EBA\uFF0C\u7136\u540E\u6740\u6389\u4E86\u989C\u5E08\u4F2F\u548C\u4ED6\u7684\u516D\u4E2A\u513F\u5B50\u3002</p><p>\u4EC5\u4EC5\u662F\u56E0\u4E3A\u5218\u4E49\u606D\u5728\u5B5D\u6B66\u5E1D\u6B7B\u540E\u559D\u9152\u4F5C\u4E50\uFF0C\u6240\u4EE5\u5C31\u8FD9\u4E48\u6740\u4EBA\uFF1F\u4E66\u8BFB\u81F3\u6B64\u7684\u65F6\u5019\uFF0C\u4F60\u662F\u5F88\u96BE\u7406\u89E3\u5218\u5B50\u4E1A\u4E3A\u4EC0\u4E48\u4F1A\u6709\u90A3\u4E48\u5927\u7684\u6068\u7684\u3002</p><p>\u800C\u4E00\u5F00\u59CB\u4E0A\u594F\u63ED\u53D1\u7684<code>\u6C88\u5E86\u4E4B</code>\uFF0C\u4E5F\u4E0D\u662F\u4EC0\u4E48\u8D62\u5BB6\u3002\u4E0D\u4E45\u4E4B\u540E\uFF0C\u4ED6\u5C31\u56E0\u4E3A\u529D\u8BEB\u5218\u5B50\u4E1A\u614E\u6740\uFF0C\u800C\u88AB\u5218\u5B50\u4E1A\u7ED9\u6BD2\u6B7B\u4E86\u3002</p><p>\u5F53\u65F6\u6C88\u5E86\u4E4B\u5DF2\u7ECF\u662F\u516B\u5341\u5C81\u9AD8\u9F84\u3002\u4ED6\u5728\u5218\u88D5\u65F6\u4EE3\u5373\u53C2\u519B\uFF0C\u53C2\u52A0\u8FC7\u5143\u5609\u5317\u4F10\uFF0C\u8BA8\u4F10\u8FC7\u86EE\u65CF\u3002\u5230\u5B5D\u6B66\u5E1D\u65F6\u671F\uFF0C\u56E0\u4E3A\u5148\u540E\u5E2E\u52A9\u5B5D\u6B66\u5E1D\u8BDB\u706D\u524D\u592A\u5B50\u5218\u52AD\uFF0C\u5E73\u5B9A\u9C81\u723D\u3001\u5218\u8BDE\u7684\u53DB\u4E71\u800C\u771F\u6B63\u53D1\u8FF9\u3002</p><p>\u53EF\u4EE5\u8BF4\uFF0C\u5218\u5B50\u4E1A\u4ECE\u4ED6\u8001\u5B50\u5B5D\u6B66\u5E1D\u90A3\u91CC\u7EE7\u627F\u6765\u7684\u6C5F\u5C71\uFF0C\u6709\u4E00\u5927\u534A\u662F\u6C88\u5E86\u4E4B\u5E2E\u7740\u6253\u4E0B\u6765\u7684\u3002\u4F46\u5373\u4FBF\u5982\u6B64\uFF0C\u6C88\u5E86\u4E4B\u4E5F\u4F9D\u7136\u4E3A\u81EA\u5DF1\u62DB\u6765\u4E86\u6740\u8EAB\u4E4B\u7978\uFF0C\u6CA1\u80FD\u5584\u7EC8\u3002</p><p>\u800C\u4E14\uFF0C\u5218\u5B50\u4E1A\u4E0D\u6B62\u662F\u5BF9\u6258\u5B64\u5927\u81E3\u5982\u6B64\uFF0C\u5BF9\u81EA\u5DF1\u7684\u4EB2\u5988\uFF0C\u4ED6\u4E5F\u6BEB\u4E0D\u5BA2\u6C14\u3002</p><p>\u738B\u592A\u540E\u75C5\u91CD\uFF0C\u6D3E\u4EBA\u53BB\u558A\u5218\u5B50\u4E1A\u2014\u2014\u4F46\u5218\u5B50\u4E1A\u5374\u4E0D\u53BB\uFF0C\u4ED6\u5ACC\u75C5\u4EBA\u623F\u95F4\u91CC\u9B3C\u591A\u3002\u738B\u592A\u540E\u542C\u540E\u6C14\u5F97\u5927\u9A82\uFF1A\u4F60\u4EEC\u53BB\u7ED9\u6211\u53D6\u4E00\u628A\u5200\u6765\uFF0C\u6211\u8981\u770B\u770B\u6211\u7684\u809A\u5B50\u91CC\u662F\u4EC0\u4E48\u4E1C\u897F\uFF0C\u4E3A\u4EC0\u4E48\u4F1A\u751F\u51FA\u8FD9\u79CD\u4E0D\u5B5D\u5B50\u6765\uFF01</p><p>\u5200\uFF0C\u81EA\u7136\u662F\u6CA1\u6709\u4EBA\u4F1A\u62FF\u7ED9\u738B\u592A\u540E\u7684\u3002\u4F46\u6700\u7EC8\uFF0C\u738B\u592A\u540E\u4E5F\u6CA1\u89C1\u5230\u5979\u5B9D\u8D1D\u513F\u5B50\u7684\u6700\u540E\u4E00\u9762\u3002</p><p>\u9664\u4E86\u51F6\u6B8B\u66B4\u8650\u5916\uFF0C<strong>\u5218\u5B50\u4E1A\u6700\u4EE4\u4EBA\u53D1\u6307\u7684\uFF0C\u8FD8\u5728\u4E8E\u4ED6\u6709\u4E00\u4E9B\u53D8\u6001\u7684\u7231\u597D\u3002</strong></p><p>\u6BD4\u5982\uFF0C\u4ED6\u66FE\u7ECF\u53EC\u96C6\u8BF8\u738B\u7684\u5983\u5B50\u3001\u516C\u4E3B\u6392\u6210\u4E00\u6392\uFF0C\u8BA9\u8EAB\u8FB9\u7684\u4F8D\u536B\u4FAE\u8FB1\u5979\u4EEC\uFF0C\u201C\u5F3A\u5DE6\u53F3\u4F7F\u8FB1\u4E4B\u201D\u3002</p><p>\u8FD9\u4E48\u53D8\u6001\u7684\u884C\u4E3A\uFF0C\u5F53\u7136\u4F1A\u62DB\u81F4\u53CD\u6297\uFF0C\u5357\u5E73\u738B<code>\u5218\u94C4</code>\uFF08\u5218\u5B50\u4E1A\u4E4B\u53D4\uFF09\u7684\u738B\u5983<code>\u6C5F\u6C0F</code>\u5C31\u6297\u62D2\u4E0D\u4ECE\u3002\u53EF\u8FD9\u5374\u60F9\u5F97\u5218\u5B50\u4E1A\u5927\u6012\uFF0C\u4ED6\u4E0D\u4EC5\u4EB2\u81EA\u97AD\u6253\u6C5F\u6C0F\uFF0C\u4E4B\u540E\u8FD8\u4F9D\u7136\u89C9\u5F97\u4E0D\u89E3\u6C14\uFF0C\u53C8\u6740\u4E86\u6C5F\u6C0F\u7684\u4E09\u4E2A\u513F\u5B50\u3002</p><p>\u4E0D\u8FC7\uFF0C\u5218\u5B50\u4E1A\u4E5F\u4E0D\u662F\u5BF9\u6240\u6709\u4EB2\u621A\u90FD\u8FD9\u6837\uFF0C\u5BF9\u4EB2\u59D0\u59D0<strong>\u5C71\u9634\u516C\u4E3B</strong>\uFF0C\u4ED6\u5C31\u5F88\u5BBD\u5BB9\u3002</p><p>\u5C71\u9634\u516C\u4E3B\u8DDF\u5218\u5B50\u4E1A\u8BF4\uFF0C\u4F60\u6709\u540E\u5BAB\u4E07\u4EBA\uFF0C\u6211\u5C31\u9A78\u9A6C\u4E00\u4E2A\uFF0C\u5927\u5BB6\u90FD\u662F\u5148\u5E1D\u6240\u751F\uFF0C\u4E3A\u4EC0\u4E48\u8FD9\u4E48\u4E0D\u516C\u5E73\uFF01</p><p>\u5218\u5B50\u4E1A\u89C9\u5F97\u59D0\u59D0\u7684\u8FD9\u756A\u8BDD\u8BF4\u5F97\u5F88\u6709\u9053\u7406\uFF0C\u968F\u5373\u4FBF\u8D4F\u4E86\u4E09\u5341\u4E2A\u7F8E\u5C11\u5E74\u8FC7\u53BB\u3002</p><p>\u53EF\u8FD9\u4F4D\u5C71\u9634\u516C\u4E3B\u4E5F\u4E0D\u662F\u4E2A\u597D\u4F3A\u5019\u7684\u4E3B\u513F\u3002\u6CA1\u8FC7\u591A\u4E45\uFF0C\u5979\u7684\u65B0\u9C9C\u52B2\u5C31\u8FC7\u4E86\u3002\u8F6C\u773C\uFF0C\u5979\u53C8\u770B\u4E0A\u4E86\u540F\u90E8\u90CE<code>\u891A\u6E0A</code>\u3002</p><p>\u891A\u6E0A\u5728\u4F8D\u5949\u4E86\u5341\u5929\u4EE5\u540E\uFF0C\u4EE5\u6B7B\u81EA\u8A93\uFF0C\u8FD9\u624D\u5F97\u4EE5\u9003\u8131\u9B54\u722A\u3002\u53F2\u4E66\u4E0A\u7684\u539F\u8BDD\u662F\uFF0C\u201C<strong>\u6E0A\u4F8D\u516C\u4E3B\u5341\u65E5\uFF0C\u5907\u89C1\u903C\u8FEB\uFF0C\u4EE5\u6B7B\u81EA\u8A93</strong>\u201D\u3002</p><p>\u8FD9\u8FD8\u6CA1\u5B8C\uFF0C\u968F\u540E\uFF0C\u5218\u5B50\u4E1A\u53C8\u76EF\u4E0A\u4E86\u4ED6\u7684\u4E09\u4E2A\u53D4\u53D4\uFF1A\u6E58\u4E1C\u738B<code>\u5218\u5F67</code>\u3001\u5EFA\u5B89\u738B<code>\u5218\u4F11\u4EC1</code>\u3001\u5C71\u9633\u738B<code>\u5218\u4F11\u7950</code>\u3002</p><p><img src="'+i+'" alt="demo" loading="lazy"></p><p>\u8FD9\u4E09\u4E2A\u4EBA\u7684\u5171\u540C\u7279\u70B9\uFF0C\u662F\u80D6\uFF0C\u5C24\u5176\u662F\u5218\u5F67\u3002\u56E0\u6B64\u5218\u5B50\u4E1A\u7528\u7AF9\u7B3C\u628A\u4ED6\u4EEC\u88C5\u8D77\u6765\u79F0\u91CD\uFF0C\u7136\u540E\u53C8\u5145\u5206\u53D1\u626C\u5BB6\u4F20\u7EDD\u5B66\u7ED9\u4ED6\u4EEC\u8D77\u5916\u53F7\uFF1A\u6700\u80D6\u7684\u5218\u5F67\u83B7\u5F97\u4E86\u201C\u732A\u738B\u201D\u79F0\u53F7\uFF0C\u5218\u4F11\u4EC1\u88AB\u53EB\u505A\u201C\u6740\u738B\u201D\uFF0C\u5218\u4F11\u7950\u5219\u662F\u201C\u8D3C\u738B\u201D\u3002</p><p>\u8D77\u4E86\u5916\u53F7\u8FD8\u4E0D\u7B97\u5B8C\uFF0C\u63A5\u7740\u8FD8\u8981\u8650\u5F85\u4E00\u756A\u2014\u2014\u4EE5\u4E0B\u6587\u5B57\uFF0C\u51FA\u81EA\u6B63\u53F2\uFF0C\u4E0D\u662F\u675C\u64B0\uFF1A</p><blockquote><p>\u4EE5\u6728\u69FD\u76DB\u996D\uFF0C\u5185\u8BF8\u6742\u98DF\uFF0C\u6405\u4EE4\u548C\u5408\uFF0C\u6398\u5730\u4E3A\u5751\u9631\uFF0C\u5B9E\u4E4B\u4EE5\u6CE5\u6C34\uFF0C\u88F8\u592A\u5B97\u5185\u5751\u4E2D\uFF0C\u548C\u69FD\u98DF\u7F6E\u524D\uFF0C\u4EE4\u592A\u5B97\u4EE5\u53E3\u5C31\u69FD\u4E2D\u98DF\uFF0C\u7528\u4E4B\u4E3A\u6B22\u7B11\u3002</p></blockquote><p>\u5BF9\u4E8E\u4E09\u738B\uFF0C\u5218\u5B50\u4E1A\u4E0D\u4EC5\u968F\u610F\u6BB4\u6253\u51CC\u8FB1\uFF0C\u8FD8\u547D\u4EBA\u7528\u6728\u69FD\u76DB\u996D\uFF0C\u518D\u5728\u91CC\u9762\u62CC\u70B9\u6742\u98DF\uFF0C\u653E\u5728\u4E00\u4E2A\u6CE5\u5751\u65C1\u3002\u4E4B\u540E\uFF0C\u8BA9\u201C\u732A\u738B\u201D\u5218\u5F67\u8131\u5149\u4E86\u8DF3\u5230\u6CE5\u5751\u91CC\uFF0C\u628A\u5934\u57CB\u8FDB\u6728\u69FD\u91CC\u5403\u4E1C\u897F\u3002</p><p>\u4F69\u5947\u5BB6\u90FD\u4E0D\u6562\u8FD9\u4E48\u73A9\u3002</p><p>\u751A\u81F3\u4E8E\u8FD9\u6837\u7684\u4FAE\u8FB1\u90FD\u53EA\u662F\u5C0F\u4E8B\u4E86\uFF0C\u5218\u5F67\u8FD8\u5DEE\u70B9\u88AB\u6740\u3002\u67D0\u6B21\uFF0C\u5218\u5F67\u53C8\u60F9\u6012\u4E86\u5218\u5B50\u4E1A\uFF0C\u88AB\u5218\u5B50\u4E1A\u5265\u5149\u4E86\u8863\u670D\uFF0C\u624B\u811A\u7ED1\u5728\u4E00\u6839\u6728\u68CD\u4E0A\uFF0C\u8BF4\u662F\u8981\u62AC\u5230\u53A8\u623F\u53BB\u6740\u732A\u3002</p><p>\u5218\u5F67\u5413\u5F97\u809D\u80C6\u4FF1\u88C2\uFF0C\u5371\u6025\u65F6\u523B\uFF0C\u5E78\u4E8F\u201C\u6740\u738B\u201D\u5218\u4F11\u4EC1\u51FA\u6765\u5E2E\u4E86\u4E00\u628A\u3002\u5218\u4F11\u4EC1\u5BF9\u5218\u5B50\u4E1A\u8BF4\uFF0C\u4ECA\u5929\u4E0D\u9002\u5408\u6740\u732A\u3002</p><p>\u5218\u5B50\u4E1A\u95EE\u4E3A\u4EC0\u4E48\uFF1F\u5218\u4F11\u4EC1\u7B11\u7740\u56DE\u7B54\u5230\uFF0C\u767E\u59D3\u5BB6\u6740\u732A\uFF0C\u90FD\u662F\u56E0\u4E3A\u6709\u559C\u4E8B\u3002\u5982\u4ECA\u7687\u592A\u5B50\u9A6C\u4E0A\u5C31\u8981\u964D\u751F\u4E86\uFF0C\u5230\u65F6\u5019\u518D\u6740\u624D\u540D\u6B63\u8A00\u987A\u561B\uFF01</p><p>\u5218\u5B50\u4E1A\u89C9\u5F97\u6709\u9053\u7406\uFF0C\u4FBF\u628A\u7ED1\u5728\u6728\u68CD\u4E0A\u7684\u201C\u732A\u738B\u201D\u4EA4\u7ED9\u4E86\u5EF7\u5C09\u3002\u8FC7\u4E86\u4E00\u591C\u4E4B\u540E\uFF0C\u5218\u5B50\u4E1A\u6C14\u6D88\u4E86\uFF0C\u624D\u53C8\u628A\u5218\u5F67\u7ED9\u653E\u4E86\u51FA\u6765\u3002</p><p>\u66B4\u8650\u72C2\u6096\u5982\u6B64\uFF0C\u5218\u5B50\u4E1A\u5DF2\u7ECF\u662F\u4F17\u6028\u6240\u5F52\u3002**\u800C\u5728\u5F53\u7687\u5E1D\u7684\u8FD9\u4E00\u5E74\u91CC\uFF0C\u4ED6\u65E2\u6CA1\u6709\u60F3\u8FC7\u5982\u4F55\u64CD\u7EB5\u671D\u653F\uFF0C\u4E5F\u6CA1\u6709\u60F3\u8FC7\u5982\u4F55\u63A7\u5236\u7981\u519B\u3002**\u5982\u4ECA\u65E2\u7136\u5DF2\u7ECF\u7ED3\u6028\u5982\u6B64\uFF0C\u90A3\u4E48\u81EA\u7136\u6B7B\u671F\u5C06\u81F3\u3002</p><p>\u4EC5\u4EC5\u662F\u6536\u4E70\u4E86\u51E0\u4E2A\u4F8D\u536B\uFF0C<code>\u5218\u5F67</code>\u5C31\u6740\u6389\u4E86\u5218\u5B50\u4E1A\uFF0C\u968F\u540E\u767B\u57FA\uFF0C\u662F\u4E3A<strong>\u5B8B\u660E\u5E1D</strong>\u2014\u2014\u5F53\u7136\uFF0C\u4ED6\u4E5F\u662F\u6709\u4E0D\u5F97\u4EE5\u7684\u82E6\u8877\u7684\u3002\u4ED6\u5982\u679C\u518D\u4E0D\u6740\u5218\u5B50\u4E1A\uFF0C\u53EA\u6015\u54EA\u5929\u771F\u7684\u5C31\u4F1A\u88AB\u5F53\u6210\u732A\u7ED9\u6740\u6389\u4E86\u3002</p><p>\u5B8B\u660E\u5E1D\u5218\u5F67\u4E0A\u4F4D\u540E\uFF0C\u4E00\u5171\u5F53\u4E86\u516B\u5E74\u7687\u5E1D\u3002</p><p>\u4F46\u662F\uFF0C\u5218\u5B8B\u738B\u671D\u7684\u8840\u8165\u6740\u622E\u5374\u5E76\u6CA1\u6709\u56E0\u6B64\u800C\u7EC8\u7ED3\u3002\u76F8\u53CD\uFF0C\u5B97\u5BA4\u5185\u7684\u6740\u622E\u4EFF\u4F5B\u88AB\u4E0B\u4E86\u8BC5\u5492\u4E00\u822C\uFF0C\u8FDB\u5165\u5230\u4E00\u4E2A\u65B0\u9AD8\u5EA6\u3002</p><p>\u5218\u5F67\u4F5C\u4E3A\u4E00\u540D\u6210\u5E74\u4EBA\uFF0C\u5F53\u7136\u4E0D\u4F1A\u50CF\u5341\u51E0\u5C81\u7684\u5218\u5B50\u4E1A\u4E00\u6837\uFF0C\u6027\u60C5\u53E4\u602A\u800C\u53D8\u6001\u3002\u4ED6\u4E3B\u8981\u662F\u76EF\u7740\u5B97\u5BA4\u4EEC\u6740\uFF0C\u7531\u4E8E\u6709\u81EA\u5DF1\u7684\u5148\u4F8B\u5728\u6B64\uFF0C\u56E0\u6B64\u5218\u5F67\u5728\u6267\u653F\u7684\u65E9\u671F\uFF0C\u89C9\u5F97\u5927\u81E3\u4EEC\u90FD\u65E0\u6240\u8C13\uFF0C\u5BF9\u7687\u4F4D\u6709\u5A01\u80C1\u7684\uFF0C\u8FD8\u662F\u59D3\u5218\u7684\u5B97\u4EB2\u4EEC\u3002</p><p>\u6BD4\u5982\u5728\u5218\u5B50\u4E1A\u88AB\u6740\u540E\uFF0C\u664B\u5B89\u738B<code>\u5218\u5B50\u52CB</code>\u5C31\u5728\u5BFB\u9633\u79F0\u5E1D\u4E86\u3002\u867D\u7136\u8FD9\u79CD\u4E00\u56FD\u4E24\u5E1D\u7684\u5C40\u9762\u5E76\u6CA1\u6709\u7EF4\u6301\u5F88\u4E45\uFF0C\u51E0\u4E2A\u6708\u4EE5\u540E\uFF0C\u5218\u5B50\u52CB\u7684\u79F0\u5E1D\u5C31\u4EE5\u5931\u8D25\u800C\u544A\u7EC8\u3002\u4F46\u4E3A\u7EDD\u540E\u60A3\uFF0C<strong>\u6B64\u540E\uFF0C\u5B8B\u660E\u5E1D\u8FD8\u662F\u5C06\u5B5D\u6B66\u5E1D\u5218\u9A8F\u768428\u4E2A\u513F\u5B50\u9646\u7EED\u5168\u90E8\u6740\u5149\u3002</strong></p><p><img src="'+l+'" alt="demo" loading="lazy"></p><p>\u4F46\u9976\u662F\u5982\u6B64\uFF0C\u5218\u5F67\u4E5F\u8FD8\u4E0D\u653E\u5FC3\uFF0C\u4ED6\u53C8\u76EF\u4E0A\u4E86\u81EA\u5DF1\u7684\u5144\u5F1F\u4EEC\u3002\u4E3A\u4E86\u8BA9\u513F\u5B50\u80FD\u591F\u5373\u4F4D\uFF0C\u4ED6\u51B3\u5B9A\u5BF9\u5F1F\u5F1F\u4EEC\u5C55\u5F00\u72FC\u4EBA\u6740\u3002</p><p>**\u5218\u5F67\u4E00\u5171\u6709\u5144\u5F1F\u5341\u4E5D\u4EBA\uFF0C\u5230\u4ED6\u5373\u4F4D\u65F6\uFF0C\u5305\u62EC\u4ED6\u81EA\u5DF1\u5728\u5185\uFF0C\u5DF2\u7ECF\u53EA\u5B58\u4E03\u4EBA\u3002**\u5269\u4E0B\u7684\u5341\u4E8C\u4E2A\uFF0C\u8981\u4E48\u5DF2\u7ECF\u88AB\u6740\uFF0C\u8981\u4E48\u5DF2\u7ECF\u5916\u9003\u2014\u2014\u4ED6\u7684\u4E5D\u54E5<code>\u5218\u6636</code>\uFF0C\u4E4B\u524D\u56E0\u4E3A\u524D\u5E9F\u5E1D\u5218\u5B50\u4E1A\u7684\u731C\u5FCC\uFF0C\u5DF2\u7ECF\u6295\u5954\u5230\u4E86\u5317\u9B4F\u3002</p><p>\u4E00\u4E2A\u7687\u5B50\uFF0C\u5728\u81EA\u5DF1\u7684\u56FD\u5BB6\u5B9E\u5728\u65E0\u6CD5\u751F\u5B58\uFF0C\u7ADF\u7136\u53EA\u6709\u6295\u9760\u654C\u56FD\u624D\u80FD\u5F97\u4EE5<strong>\u5BFF\u7EC8</strong>\uFF0C\u8FD9\u5B9E\u5728\u662F\u9ED1\u8272\u5E7D\u9ED8\u3002</p><p>\u800C\u5218\u5F67\u5728\u505A\u7687\u5E1D\u7684\u516B\u5E74\u5185\uFF0C\u53C8\u9646\u7EED\u6740\u6389\u4E86\u5269\u4E0B\u7684\u4E94\u4E2A\u5144\u5F1F\u3002\u5230\u4ED6\u6B7B\u7684\u65F6\u5019\uFF0C\u5C31\u53EA\u6709<code>\u5218\u4F11\u8303</code>\u4E00\u4E2A\u4EBA\u8FD8\u6D3B\u7740\u4E86\u3002\u5218\u5F67\u662F\u8BA4\u4E3A\u6B64\u4EBA\u987D\u52A3\u624D\u80FD\uFF0C\u6CA1\u6709\u5A01\u80C1\uFF0C\u624D\u56E0\u6B64\u653E\u8FC7\u4E86\u4ED6\u3002</p><p>\u4F46\u5C31\u662F\u8FD9\u4E48\u4E00\u4E2A\u65E0\u5FB7\u65E0\u624D\u7684\u738B\u7237\uFF0C\u5C45\u7136\u4E5F\u6709\u5F53\u7687\u5E1D\u7684\u5FC3\u601D\u3002\u540E\u6765\uFF0C\u5218\u4F11\u8303\u4E5F\u56E0\u8C0B\u53CD\u88AB\u6740\u3002</p><p>\u6709\u65F6\u5019\uFF0C\u4F60\u4E0D\u5F97\u4E0D\u611F\u6168\u62A5\u5E94\u771F\u7684\u5F88\u65E0\u5E38\u3002\u5B8B\u6587\u5E1D\u5218\u4E49\u9686\u867D\u7136\u6027\u683C\u731C\u5FCC\uFF0C\u4F46\u5728\u4ED6\u6CBB\u4E0B\uFF0C\u767E\u59D3\u5B89\u5C45\u4E50\u4E1A\uFF0C\u5143\u5609\u4E4B\u6CBB\u53EF\u4EE5\u8BF4\u662F\u9B4F\u664B\u4EE5\u6765\u6700\u597D\u7684\u793E\u4F1A\u5C40\u9762\u3002**\u53EF\u8FD9\u79CD\u751F\u6C11\u4E4B\u5FB7\uFF0C\u5374\u4F3C\u4E4E\u5E76\u6CA1\u6709\u7ED9\u4ED6\u79EF\u6512\u8D77\u591A\u5C11\u798F\u62A5\u3002**\u4ED6\u768419\u4E2A\u513F\u5B50\u4E2D\uFF0C\u9664\u4E86\u5218\u6636\u5728\u5317\u9B4F\u5386\u4EFB\u9AD8\u5B98\uFF0C\u76F4\u81F3\u516D\u5341\u4E8C\u5C81\u65F6\u75C5\u901D\u5916\uFF0C<strong>\u5176\u4ED6\u7684\u2014\u2014\u54EA\u6015\u5305\u62EC\u4E86\u505A\u8FC7\u7687\u5E1D\u7684\u90A3\u4E09\u4E2A\uFF0C\u90FD\u6CA1\u6709\u4E00\u4E2A\u6D3B\u8FC7\u56DB\u5341\u5C81\u3002</strong></p><p>\u968F\u7740\u5B8B\u6587\u5E1D\u7684\u513F\u5B50\u4EEC\u88AB\u5C60\u6740\u5E72\u51C0\uFF0C\u5218\u5F67\u7EC8\u4E8E\u4E0D\u518D\u89C9\u5F97\u5B97\u5BA4\u662F\u5A01\u80C1\u3002</p><p><strong>\u53EF\u5218\u5B8B\u738B\u671D\u4E5F\u57FA\u672C\u4E0A\u8D70\u5230\u4E86\u672B\u8DEF\u3002</strong></p><p>\u5B8B\u660E\u5E1D\u5218\u5F67\u5728\u6B7B\u65F6\uFF0C\u89C9\u5F97\u552F\u4E00\u6709\u5A01\u80C1\u7684\u662F\u5927\u81E3<code>\u8427\u9053\u6210</code>\u3002\u4F46\u6700\u7EC8\uFF0C\u8427\u9053\u6210\u8FD8\u662F\u8EB2\u8FC7\u731C\u5FCC\u6D3B\u4E86\u4E0B\u6765\u3002</p><p>\u4E4B\u540E\uFF0C\u4FBF\u662F\u6743\u81E3\u7BE1\u4F4D\u7684\u6807\u51C6\u6A21\u677F\u88AB\u518D\u4E00\u6B21\u91CD\u73B0\uFF1A\u8427\u9053\u6210\u5EFA\u7ACB\u519B\u529F\uFF0C\u6536\u4E70\u5927\u81E3\uFF0C\u5E9F\u7ACB\u7687\u5E1D\uFF0C\u6700\u7EC8\u7985\u4F4D\uFF0C\u53F2\u79F0<code>\u5357\u9F50</code>\u3002</p><p>\u8427\u9053\u6210\u5F53\u7687\u5E1D\u7684\u65F6\u95F4\u66F4\u77ED\uFF0C\u53EA\u6709\u56DB\u5E74\u3002</p><p>\u4E34\u7EC8\u524D\uFF0C\u4ED6\u529D\u8BEB\u592A\u5B50\u8BF4\uFF0C\u6211\u4E4B\u6240\u4EE5\u80FD\u53D6\u4EE3\u5218\u6C0F\u7684\u7687\u4F4D\uFF0C\u5C31\u662F\u56E0\u4E3A\u4ED6\u4EEC\u81EA\u76F8\u6B8B\u6740\u4E4B\u6545\uFF0C\u6240\u4EE5\u4F60\u4EEC\u5C06\u6765\u4E00\u5B9A\u4E0D\u8981\u91CD\u8E48\u8986\u8F99\u554A\uFF01</p><p>\u53EF\u8FD9\u79CD\u8BDD\uFF0C\u7B80\u76F4\u5C31\u662F\u5728\u7ED9\u513F\u5B59\u4EEC\u7ACBflag\u3002</p><p>\u8427\u9053\u6210\u6B7B\u540E\uFF0C\u7EE7\u4F4D\u7684<code>\u8427\u8CFE</code>\u56E0\u4E3A\u8DDF\u7740\u7236\u4EB2\u95EF\u8FC7\u4E1A\uFF0C\u660E\u767D\u5176\u4E2D\u7684\u51F6\u9669\uFF0C\u6240\u4EE5\u4E00\u5207\u8FD8\u7B97\u987A\u5F53\u3002\u4F46\u968F\u7740\u8427\u8CFE\u4E00\u6B7B\uFF0C\u5357\u9F50\u4E5F\u518D\u6B21\u8D70\u4E0A\u4E86\u5218\u5B8B\u5F53\u5E74\u7684\u8001\u8DEF\u3002\u800C\u4E14\uFF0C<strong>\u8FD9\u4E2A\u671D\u4EE3\u7684\u6301\u7EED\u65F6\u95F4\u4E4B\u77ED\uFF0C\u8FD8\u521B\u4E0B\u4E86\u5357\u671D\u4E4B\u6700\uFF0C\u4E00\u5171\u53EA\u670924\u5E74\u3002</strong></p><p>\u8FD9\u662F\u4E2A\u4EC0\u4E48\u6982\u5FF5\uFF1F</p><p>\u540C\u4E00\u65F6\u671F\u7684\u5317\u65B9\uFF0C\u5927\u540D\u9F0E\u9F0E\u7684**\u5317\u9B4F\u5B5D\u6587\u5E1D<code>\u5143\u5B8F</code>**\u5728\u5373\u4F4D\u65F6\uFF0C\u5357\u671D\u8FD8\u662F\u5B8B\u660E\u5E1D\u5218\u5F67\u5728\u4F4D\u7684\u3002\u53EF\u516B\u5E74\u8FC7\u53BB\u4EE5\u540E\uFF0C\u5218\u5B8B\u5C31\u53D8\u6210\u4E86\u5357\u9F50\u3002\u5230\u5B5D\u6587\u5E1D\u6B7B\u540E\u7684\u7B2C\u4E09\u5E74\uFF0C\u5357\u9F50\u53C8\u53D8\u6210\u4E86\u6881\u3002</p><p>\u5357\u671D\u7684\u653F\u6CBB\u8D25\u574F\uFF0C**\u662F\u4ECE\u4E00\u4E2A\u6709\u7F3A\u9677\u7684\u5236\u5EA6\u5F00\u59CB\u7684\uFF0C\u7136\u540E\uFF0C\u5728\u4E00\u4EE3\u4EE3\u4E0D\u52A0\u8282\u5236\u7684\u6597\u4E89\u4E2D\u4E0D\u65AD\u7A81\u7834\u4E0B\u9650\uFF0C\u5F62\u6210\u6076\u6027\u5FAA\u73AF\u3002**\u6700\u7EC8\uFF0C\u6210\u5C31\u4E86\u4E00\u4E2A\u5F02\u59D3\u6B66\u5C06\u7684\u5D1B\u8D77\uFF0C\u7EC8\u81F3\u671D\u4EE3\u66F4\u66FF\u3002</p><p>\u5176\u4EE4\u4EBA\u77A0\u76EE\u7684\u5730\u65B9\uFF0C\u5728\u4E8E\u7ECF\u8FC7\u4E00\u4EE3\u53C8\u4E00\u4EE3\u7684\u7A81\u7834\u4EE5\u540E\uFF0C\u6597\u4E89\u53D8\u5F97\u8D8A\u6765\u8D8A\u6B8B\u9177\uFF0C\u8D8A\u6765\u8D8A\u9A87\u4EBA\u542C\u95FB\u3002\u6700\u7EC8\uFF0C\u4E00\u4E2A\u738B\u671D\u7684\u751F\u673A\u4E0E\u80FD\u91CF\u5728\u6597\u4E89\u4E2D\u6D88\u8017\u6B86\u5C3D\u3002</p><p>\u8FD9\u79CD\u4E00\u4EE3\u4EE3\u7684\u8F6E\u56DE\uFF0C\u6B8A\u65E0\u53EF\u8BB0\u4E4B\u5904\u3002\u770B\u7B2C\u4E00\u6BB5\u65F6\uFF0C\u53EF\u80FD\u8FD8\u4F1A\u6709\u4E00\u70B9\u730E\u5947\u4E4B\u5FC3\uFF0C\u60CA\u5F02\u4E8E\u8FD9\u79CD\u6B8B\u6740\u7684\u65E0\u4E0B\u9650\uFF0C\u60CA\u53F9\u4E00\u58F0\u201C\u600E\u4F1A\u5982\u6B64\uFF01\u201D</p><p>\u4F46\u770B\u5230\u7B2C\u4E8C\u6B21\u65F6\u5C31\u9EBB\u6728\u4E86\uFF0C\u201C\u53C8\u662F\u5982\u6B64\u201D\u3002</p><p>\u7B49\u5230\u4E86\u7B2C\u4E09\u6B21\uFF0C\u5C31\u53EA\u4F1A\u662F\u201C\u53EA\u80FD\u5982\u6B64\u201D\u4E86\u3002</p><p>\u56E0\u4E3A\u5F53\u5357\u65B9\u5982\u9B3C\u6253\u5899\u4E00\u822C\uFF0C\u59CB\u7EC8\u90FD\u9677\u5728\u5185\u90E8\u6B8B\u6740\u4E2D\u65E0\u6CD5\u9003\u79BB\u65F6\uFF0C<strong>\u5317\u65B9\u5DF2\u7ECF\u5728\u7740\u529B\u89E3\u51B3\u5F02\u65CF\u8FDB\u5165\u4E2D\u539F\u4EE5\u540E\u7684\u6C11\u65CF\u878D\u5408\u95EE\u9898\u4E86\u3002</strong></p><p>\u4ECE\u5B8B\u6B66\u5E1D\u5218\u88D5\u7BE1\u664B\u5F00\u59CB\u5F80\u540E\u7B97\uFF0C\u5728\u63A5\u4E0B\u6765\u7684\u8FD9\u4E00\u767E\u4E94\u5341\u5E74\u4E2D\uFF0C\u5357\u5317\u5C06\u6E10\u884C\u6E10\u8FDC\uFF0C\u7EC8\u81F3\u5206\u9053\u626C\u9573\u3002</p><h2 id="\u5E1D\u738B\u5FC3\u672F" tabindex="-1"><a class="header-anchor" href="#\u5E1D\u738B\u5FC3\u672F" aria-hidden="true">#</a> \u5E1D\u738B\u5FC3\u672F</h2><p>\u5B5D\u6B66\u5E1D\u6B8B\u9177\u731C\u5FCC\uFF0C\u4F46\u5E76\u4E0D\u660F\u8069\u3002</p><p>\u636E\u53F2\u4E66\u8BB0\u8F7D\uFF0C\u5B5D\u6B66\u5E1D\u6574\u5929\u90FD\u559D\u5F97\u9169\u914A\u5927\u9189\uFF0C\u5F88\u5C11\u6709\u9192\u7740\u7684\u65F6\u5019\uFF0C\u7ECF\u5E38\u662F\u5750\u7740\u5750\u7740\u5C31\u7761\u7740\u4E86\u3002\u4F46\u5982\u679C\u6B64\u65F6\u6709\u5916\u95F4\u594F\u4E8B\uFF0C\u4ED6\u5374\u80FD\u9A6C\u4E0A\u5C31\u9192\u8FC7\u6765\uFF0C\u4E00\u70B9\u90FD\u770B\u4E0D\u51FA\u559D\u8FC7\u9152\u7684\u6837\u5B50\u3002\u56E0\u6B64\uFF0C\u81E3\u4E0B\u4EEC\u4E0D\u6562\u6709\u4E1D\u6BEB\u61C8\u6020\uFF0C\u4E0D\u6562\u4F01\u56FE\u81EA\u5DF1\u80FD\u8499\u6DF7\u8FC7\u5173\u3002</p><p>\u8FD9\u5C31\u662F\u5E1D\u738B\u5FC3\u672F\u3002</p><p>\u65E9\u5728\u6218\u56FD\u65F6\u671F\uFF0C\u300A\u97E9\u975E\u5B50\u300B\u5C31\u5199\u4E86\u5E1D\u738B\u5FC3\u672F\u5E94\u8BE5\u5982\u4F55\u5E94\u7528\uFF0C<strong>\u4E0D\u662F\u8BF4\u8981\u8BA9\u7687\u5E1D\u660E\u5BDF\u79CB\u6BEB\uFF0C\u6D1E\u6089\u4E07\u7269</strong>\u2014\u2014\u8FD9\u65E2\u4E0D\u53EF\u80FD\uFF0C\u4E5F\u4E0D\u5B9E\u9645\u3002\u800C\u662F\u8BF4\u7687\u5E1D\u82E5\u60F3\u8981\u638C\u63E1\u6743\u529B\uFF0C<strong>\u5173\u952E\u6838\u5FC3\u5176\u5B9E\u5728\u4E8E\uFF0C\u8981\u8BA9\u81E3\u4E0B\u731C\u4E0D\u900F\u3002</strong></p><p>\u4F5C\u4E3A\u7687\u5E1D\uFF0C\u4F60\u54EA\u6015\u518D\u660E\u5BDF\u79CB\u6BEB\uFF0C\u81E3\u4E0B\u53EA\u8981\u6478\u6E05\u4E86\u4F60\u7684\u8FD9\u4E2A\u80FD\u529B\uFF0C\u4E5F\u90FD\u53EF\u4EE5\u987A\u7740\u4F60\u7684\u601D\u8DEF\u731C\u5230\u4F60\u60F3\u8981\u5F97\u51FA\u7684\u7ED3\u8BBA\u3002\u800C\u5982\u679C\u518D\u9000\u51E0\u6B65\uFF0C\u4F60\u8FD8\u660F\u8069\u5982\u664B\u60E0\u5E1D\uFF0C\u90A3\u4E48\u5C31\u66F4\u597D\u8499\u9A97\u4E86\u3002</p><p>\u6240\u4EE5\uFF0C\u4E0D\u60F3\u8BA9\u81E3\u4E0B\u8499\u853D\u7684\u5173\u952E\uFF0C\u5176\u5B9E\u5728\u4E8E\u4E0D\u80FD\u8BA9\u4ED6\u5BDF\u89C9\u5230\u81EA\u5DF1\u7684\u5957\u8DEF\u3002</p><p>\u82F1\u660E\u56FA\u7136\u8981\u82F1\u660E\uFF0C\u4F46\u4E5F\u4E0D\u80FD\u6C38\u8FDC\u82F1\u660E\uFF1B\u660F\u8069\u4E5F\u65E0\u59A8\u660F\u8069\uFF0C\u4F46\u5076\u5C14\u4E5F\u8981\u5723\u660E\u51E0\u6B21\u3002\u8FD9\u79CD\u8F93\u51FA\u80FD\u529B\u7684\u968F\u673A\u6027\u548C\u4E0D\u786E\u5B9A\u6027\uFF0C\u624D\u771F\u7684\u80FD\u8BA9\u4F60\u7684\u81E3\u4E0B\u731C\u4E0D\u900F\u4F60\u3002</p><p>\u5B5D\u6B66\u5E1D\u5C31\u5F88\u6E05\u695A\u7684\u638C\u63E1\u4E86\u8FD9\u4E00\u6280\u80FD\uFF0C\u4F60\u8BF4\u6211\u559D\u9189\u4E86\uFF0C\u6211\u6CA1\u6709\uFF0C\u4F46\u6211\u660E\u660E\u53C8\u9189\u4E86\u3002\u8FD9\u65F6\u5019\u4F60\u5982\u679C\u60F3\u8D81\u7740\u6211\u559D\u9189\u4E86\uFF0C\u5C31\u4F01\u56FE\u8499\u853D\u6211\uFF0C\u8FD9\u662F\u4F11\u60F3\u3002\u770B\u4E0A\u53BB\u6211\u662F\u9189\u4E86\uFF0C\u4F46\u4F60\u6839\u672C\u4E0D\u77E5\u9053\u6211\u662F\u4E0D\u662F\u771F\u7684\u9189\u4E86\uFF0C\u6211\u53EF\u4EE5\u9189\uFF0C\u4E5F\u53EF\u4EE5\u4E0D\u9189\u3002</p><p>\u7B80\u79F0\u201C\u859B\u5B9A\u8C14\u7684\u9189\u201D\u3002</p><p>\u4F60\u4E0D\u77E5\u9053\u6211\u54EA\u6B21\u662F\u771F\u7684\u9189\u4E86\uFF0C\u54EA\u6B21\u6CA1\u9189\uFF0C\u6240\u4EE5\u53EA\u80FD\u6BCF\u6B21\u90FD\u8001\u8001\u5B9E\u5B9E\u7684\u505A\u597D\u672C\u4EFD\u5DE5\u4F5C\uFF0C\u4E0D\u641E\u4EC0\u4E48\u82B1\u82B1\u80A0\u5B50\u5E7A\u86FE\u5B50\u3002</p><p>\u5426\u5219\uFF0C\u6211\u53EF\u662F\u5F88\u559C\u6B22\u6740\u4EBA\u7684\u54E6\u3002</p>',97);function f(b,y){const o=r("center");return c(),d("div",null,[_,t(o,null,{default:s(()=>[h]),_:1}),u])}const x=e(m,[["render",f],["__file","58.html.vue"]]);export{x as default};
