import{_ as p}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as e,c as t,f as a}from"./app.52cb12a8.js";const c={},o=a('<h1 id="\u7B2C58\u96C6-\u5468\u79C9\u6606\u90D1\u5A1F\u7275\u624B\u4E00\u751F" tabindex="-1"><a class="header-anchor" href="#\u7B2C58\u96C6-\u5468\u79C9\u6606\u90D1\u5A1F\u7275\u624B\u4E00\u751F" aria-hidden="true">#</a> \u7B2C58\u96C6\uFF1A\u5468\u79C9\u6606\u90D1\u5A1F\u7275\u624B\u4E00\u751F</h1><p>\u5927\u5BB6\u5531\u7740\u300A\u670B\u53CB\u300B\u559D\u7740\u676F\u4E2D\u7684\u9152\uFF0C\u4E8E\u8679\u3001\u5434\u5029\u548C\u90D1\u5A1F\u76F8\u4E92\u6402\u7740\u5F00\u6000\u5927\u7B11\uFF0C\u66F9\u5FB7\u5B9D\u548C\u4E54\u6625\u71D5\u4E0D\u81EA\u89C9\u7684\u5F80\u524D\u8D70\uFF0C\u900F\u8FC7\u6A71\u7A97\u770B\u7740\u91CC\u9762\u7684\u60C5\u5F62\uFF0C\u5F80\u4E8B\u4E00\u5E55\u5E55\u6D6E\u73B0\u5728\u8111\u6D77\u4E2D\uFF0C\u4E54\u6625\u71D5\u548C\u66F9\u5FB7\u5B9D\u6CEA\u5982\u96E8\u4E0B\uFF0C\u4E54\u6625\u71D5\u81EA\u89C9\u6CA1\u6709\u8138\u8FDB\u53BB\u542B\u6CEA\u79BB\u5F00\u3002</p><p>\u4E54\u6625\u71D5\u60F3\u8981\u628A\u623F\u5B50\u7ED9\u725B\u725B\uFF0C\u725B\u725B\u4E5F\u8868\u793A\u5973\u53CB\u4E0D\u8981\u623F\u5B50\uFF0C\u53EA\u8981\u4E24\u4EBA\u5728\u4E00\u8D77\u5C31\u884C\uFF0C\u73B0\u5728\u767B\u8BB0\u5C31\u53EF\u4EE5\u4E86\u3002\u725B\u725B\u4E5F\u77E5\u9053\u4E86\u66F9\u5FB7\u5B9D\u4E3E\u62A5\u4FE1\u7684\u4E8B\u60C5\uFF0C\u8BA4\u4E3A\u7236\u6BCD\u4FE9\u5C31\u662F\u592A\u7CBE\u4E86\uFF0C\u592A\u4F1A\u7B97\u8BA1\u3002\u5728\u725B\u725B\u770B\u6765\u4E8E\u8679\u662F\u771F\u7684\u592A\u96BE\u4E86\uFF0C\u53EF\u662F\u4E54\u6625\u71D5\u5C31\u662F\u5AC9\u5992\u4EBA\u5BB6\u3002\u4E54\u6625\u71D5\u4E5F\u610F\u8BC6\u5230\u81EA\u5DF1\u7684\u9519\u8BEF\uFF0C\u725B\u725B\u544A\u8BC9\u4E54\u6625\u71D5\u4ED6\u5728\u6BA1\u4EEA\u9986\u5DE5\u4F5C\u88AB\u4EBA\u77A7\u4E0D\u8D77\uFF0C\u4F46\u662F\u4ED6\u5C31\u8BA4\u4E3A\u81EA\u5DF1\u662F\u4E00\u8EAB\u6B63\u6C14\uFF0C\u5C31\u597D\u6BD4\u5F53\u5E74\u7684\u4E54\u6625\u71D5\uFF0C\u8C8C\u7F8E\u5982\u82B1\u5374\u53EF\u4EE5\u53BB\u7ED9\u4EBA\u4FEE\u811A\u3002\u4ED6\u50CF\u6781\u4E86\u4E54\u6625\u71D5\uFF0C\u4E54\u6625\u71D5\u88AB\u725B\u725B\u8BF4\u5F97\u843D\u6CEA\u3002</p><p>\u5468\u79C9\u4E49\u56DE\u5230\u5BB6\u91CC\uFF0C\u5144\u5F1F\u59D0\u59B9\u65E9\u5DF2\u7B49\u5019\u5728\u5BB6\uFF0C\u6B22\u8FCE\u4ED6\u7684\u56DE\u5F52\u3002</p><p>\u4E54\u6625\u71D5\u548C\u66F9\u5FB7\u5B9D\u6765\u627E\u56FD\u5E86\uFF0C\u5E0C\u671B\u80FD\u53C2\u52A0\u725B\u725B\u7684\u5A5A\u793C\uFF0C\u56FD\u5E86\u4E0D\u6EE1\u4E8C\u4EBA\u7684\u6240\u4F5C\u6240\u4E3A\uFF0C\u5C06\u66F9\u5FB7\u5B9D\u548C\u4E54\u6625\u71D5\u8BAD\u65A5\u4E86\u4E00\u987F\uFF0C\u4E8C\u4EBA\u60BB\u60BB\u79BB\u5F00\u3002\u56DE\u53BB\u4E4B\u540E\uFF0C\u66F9\u5FB7\u5B9D\u548C\u4E54\u6625\u71D5\u5F00\u59CB\u51C6\u5907\u8BF7\u67EC\u548C\u793C\u7269\uFF0C\u66F9\u5FB7\u5B9D\u8FD8\u7279\u610F\u63D0\u524D\u6F14\u8BB2\u4E86\u4E00\u6BB5\u65B0\u5A5A\u795D\u798F\u3002\u4F46\u60F3\u5230\u4E0D\u4F1A\u6709\u4EBA\u6765\u4E86\uFF0C\u4E54\u6625\u71D5\u9EEF\u7136\u795E\u4F24\uFF0C\u53EF\u6CA1\u60F3\u5230\u6572\u95E8\u58F0\u54CD\u8D77\uFF0C\u6765\u7684\u4EBA\u6B63\u662F\u5468\u79C9\u6606\u548C\u90D1\u5A1F\u3002</p><p>\u725B\u725B\u7ED3\u5A5A\u8FD9\u5929\uFF0C\u5F88\u70ED\u95F9\uFF0C\u90D1\u5A1F\u548C\u5468\u79C9\u6606\u8FD8\u6709\u5927\u5BB6\u90FD\u6765\u795D\u8D3A\uFF0C\u4E54\u6625\u71D5\u8FD8\u662F\u548C\u4EE5\u524D\u4E00\u6837\uFF0C\u62B1\u7740\u5468\u79C9\u6606\u9001\u4E0A\u4E86\u4E00\u4E2A\u8138\u988A\u543B\uFF0C\u90D1\u5A1F\u7AD9\u5728\u4E00\u8FB9\u7B11\u770B\u4E8C\u4EBA\u3002</p><p>\u5434\u5029\u548C\u56FD\u5E86\u8981\u53BB\u6DF1\u5733\u627E\u513F\u5B50\u5927\u521A\uFF0C\u5927\u521A\u5728\u804C\u8BFB\u4E86\u7814\u7A76\u751F\uFF0C\u5728\u5355\u4F4D\u5F88\u53D7\u5668\u91CD\uFF0C\u73B0\u5728\u975E\u8981\u7236\u6BCD\u8FC7\u53BB\uFF0C\u5434\u5029\u5634\u4E0A\u4E0D\u820D\u670B\u53CB\uFF0C\u4F46\u6EE1\u8138\u6D0B\u6EA2\u9A84\u50B2\u548C\u5E78\u798F\u3002\u4E8E\u8679\u611F\u8C22\u6240\u6709\u4EBA\u5BF9\u81EA\u5DF1\u7684\u5E2E\u52A9\uFF0C\u4E5F\u76F8\u4FE1\u8D76\u8D85\u5982\u679C\u6CC9\u4E0B\u6709\u77E5\u4E5F\u4F1A\u611F\u8C22\u5927\u5BB6\u7684\u5E2E\u52A9\uFF0C\u5468\u79C9\u6606\u79F0\u8D5E\u4E8E\u8679\u517B\u4E86\u4E00\u4E2A\u597D\u513F\u5B50\uFF0C\u5B59\u80DC\u90A3\u4E48\u5E74\u8F7B\u5C31\u505A\u4E86\u6559\u6388\u3002\u6BCF\u4E2A\u4EBA\u7684\u5B69\u5B50\u90FD\u6709\u4E86\u51FA\u606F\uFF0C\u53EA\u8981\u6839\u6B63\uFF0C\u65E0\u8BBA\u662F\u5426\u8003\u4E0A\u5927\u5B66\uFF0C\u76F8\u4FE1\u90FD\u5DEE\u4E0D\u4E86\u3002</p><p>\u90DD\u51AC\u6885\u548C\u5468\u79C9\u4E49\u7ED9\u5468\u79C9\u6606\u7684\u5B59\u5B50\u8D77\u540D\u5B57\uFF0C\u56E0\u4E3A\u5468\u79C9\u6606\u8BF4\u8981\u8BA9\u5B59\u5B50\u7BA1\u5468\u79C9\u4E49\u592B\u5987\u53EB\u7237\u7237\u5976\u5976\uFF0C\u90DD\u51AC\u6885\u89C9\u5F97\u8FD9\u662F\u5728\u5F25\u8865\u4E24\u4EBA\u7684\u9057\u61BE\uFF0C\u5468\u79C9\u4E49\u7B11\u8A00\u5E94\u8BE5\u53D6\u4E00\u4E2A\u7B80\u5355\u7684\u540D\u5B57\uFF0C\u8981\u4E0D\u7136\u5468\u79C9\u6606\u8BE5\u4E0D\u4E50\u610F\u4E86\uFF0C\u5C0F\u65F6\u5019\u5468\u79C9\u6606\u56E0\u4E3A\u540D\u5B57\u590D\u6742\u8FD8\u975E\u8981\u548C\u5468\u79C9\u4E49\u6362\u540D\u5B57\u3002</p><p>\u5468\u84C9\u7528\u4E86\u4E09\u5E74\u65F6\u95F4\u5199\u4E86\u4E00\u90E8\u56DB\u5341\u96C6\u7684\u7535\u89C6\u5267\u53D1\u7ED9\u4E86\u4E00\u4E2A\u5F71\u89C6\u516C\u53F8\uFF0C\u4F46\u662F\u6CA1\u60F3\u5230\u515C\u515C\u8F6C\u8F6C\u7ADF\u7136\u5230\u4E86\u8521\u6653\u5149\u8FD9\u91CC\uFF0C\u8521\u6653\u5149\u8BA4\u4E3A\u5267\u672C\u4E0D\u9519\uFF0C\u4F46\u662F\u56E0\u4E3A\u7B2C\u4E00\u6B21\u5199\u7ED3\u6784\u6709\u5F88\u591A\u95EE\u9898\u3002\u4F46\u8521\u6653\u5149\u613F\u610F\u63A5\u4E0B\u6765\uFF0C\u603B\u89C9\u5F97\u6709\u4F3C\u66FE\u76F8\u8BC6\u7684\u611F\u89C9\uFF0C\u5468\u84C9\u8FD9\u624D\u8BF4\u51FA\u8FD9\u662F\u5979\u5199\u7684\u3002\u5468\u84C9\u5E0C\u671B\u8521\u6653\u5149\u80FD\u5982\u5B9E\u8BF4\u51FA\u770B\u6CD5\uFF0C\u5979\u5B81\u53EF\u88AB\u62D2\u7EDD\u4E5F\u4E0D\u8981\u65BD\u820D\uFF0C\u8521\u6653\u5149\u8BA4\u4E3A\u5267\u672C\u8DDD\u79BB\u4ED6\u7684\u8981\u6C42\u8FD8\u5DEE\u7740\u4E00\u622A\u5B50\uFF0C\u4F46\u662F\u4ED6\u8981\u597D\u597D\u5E2E\u7740\u5468\u84C9\u6539\u6539\uFF0C\u4F5C\u4E3A\u81EA\u5DF1\u7684\u5C01\u7BB1\u4E4B\u4F5C\u3002</p><p>\u5468\u79C9\u4E49\u9000\u4F11\u4E86\uFF0C\u548C\u90DD\u51AC\u6885\u53BB\u4E86\u5F53\u5E74\u63D2\u961F\u7684\u5730\u65B9\uFF0C\u56DE\u5FC6\u5F80\u6614\uFF0C\u624B\u62C9\u624B\u8D70\u8FC7\u5F53\u5E74\u7684\u540A\u6865\u3002\u5F53\u5E74\u7684\u5730\u65B9\u73B0\u5728\u5DF2\u7ECF\u6210\u4E3A\u4E86\u65C5\u6E38\u666F\u70B9\uFF0C\u6070\u597D\u59DA\u7ACB\u677E\u4E5F\u5728\u8FD9\u91CC\uFF0C\u900F\u8FC7\u73BB\u7483\u7A97\u770B\u5230\u4E86\u5916\u9762\u6B23\u8D4F\u7F8E\u666F\u7684\u5468\u79C9\u4E49\u548C\u90DD\u51AC\u6885\uFF0C\u59DA\u7ACB\u677E\u98A4\u6296\u7740\u8FC7\u53BB\u547C\u5524\u7740\u4E8C\u4EBA\uFF0C\u9000\u4F11\u4E86\u90FD\u89C9\u5F97\u5BC2\u5BDE\u4E86\u3002</p><p>\u5468\u79C9\u4E49\u548C\u59DA\u7ACB\u677E\u804A\u5929\uFF0C\u59DA\u7ACB\u677E\u597D\u5947\u8FD9\u4E9B\u5E74\u5468\u79C9\u4E49\u662F\u5982\u4F55\u505A\u5230\u4ECE\u653F\u51E0\u5341\u5E74\u90FD\u80FD\u5E72\u5E72\u51C0\u51C0\u7684\u3002\u5468\u79C9\u4E49\u8868\u793A\u81EA\u5DF1\u6015\u6B7B\u4E0D\u7791\u76EE\uFF0C\u4ED6\u5BB3\u6015\u6CA1\u6709\u8138\u53BB\u89C1\u7239\u5A18\uFF0C\u5BB3\u6015\u540C\u5B66\u6233\u810A\u6881\u9AA8\uFF0C\u5468\u79C9\u4E49\u5FFD\u7136\u529D\u8BF4\u59DA\u7ACB\u677E\u53BB\u81EA\u9996\uFF0C\u59DA\u7ACB\u677E\u6C89\u9ED8\u4E86\u3002\u5F53\u770B\u89C1\u90DD\u51AC\u6885\u7ED9\u5468\u79C9\u4E49\u62FF\u6765\u4E00\u76D2\u5B50\u836F\u7684\u65F6\u5019\uFF0C\u59DA\u7ACB\u677E\u4EE5\u4E3A\u662F\u5468\u79C9\u4E49\u517B\u751F\uFF0C\u5374\u6CA1\u60F3\u5230\u5468\u79C9\u4E49\u54C8\u54C8\u4E00\u7B11\u544A\u8BC9\u59DA\u7ACB\u677E\u4ED6\u662F\u80C3\u764C\u665A\u671F\u3002\u5468\u79C9\u4E49\u544A\u8BC9\u59DA\u7ACB\u677E\u5BB6\u91CC\u4EBA\u4E5F\u90FD\u77E5\u9053\u8FD9\u4EF6\u4E8B\uFF0C\u60F3\u8981\u5728\u751F\u547D\u7ED3\u675F\u4E4B\u524D\u51FA\u6765\u8D70\u8D70\uFF0C\u5927\u5BB6\u4E5F\u90FD\u540C\u610F\u4E86\u3002\u59DA\u7ACB\u677E\u95FB\u542C\u6B64\u8A00\u5FFD\u7136\u54ED\u4E86\uFF0C\u6765\u5230\u8FD9\u91CC\uFF0C\u6BCF\u6B21\u90FD\u80FD\u60F3\u5230\u6325\u6D12\u9752\u6625\u7684\u65F6\u5019\uFF0C\u59DA\u7ACB\u677E\u4E5F\u65E9\u5C31\u51C6\u5907\u597D\u4E86\u6750\u6599\uFF0C\u53EA\u60F3\u56DE\u53BB\u5C31\u81EA\u9996\uFF0C\u4E24\u4E2A\u8001\u53CB\u63E1\u624B\u9053\u522B\u3002</p><p>\u8521\u6653\u5149\u548C\u5468\u84C9\u4E5F\u4E00\u8D77\u6765\u5230\u4E86\u8D35\u5DDE\u91D1\u575D\u6751\u5C0F\u5B66\uFF0C\u5B66\u751F\u4EEC\u8DF3\u8D77\u4E86\u7BDD\u706B\u821E\uFF0C\u770B\u7740\u5927\u5BB6\u8F7D\u6B4C\u8F7D\u821E\u7684\u6837\u5B50\uFF0C\u5468\u84C9\u50CF\u662F\u5B69\u5B50\u4E00\u822C\u7B11\u4E86\uFF0C\u62C9\u7740\u8521\u6653\u5149\u7684\u624B\u611F\u8C22\u4ED6\uFF0C\u4E24\u4EBA\u4E00\u8D77\u6295\u5165\u5230\u4E86\u8DF3\u821E\u7684\u961F\u4F0D\u4E2D\uFF0C\u6B22\u58F0\u7B11\u8BED\u4E0D\u65AD\u3002</p><p>\u90DD\u51AC\u6885\u548C\u5468\u79C9\u4E49\u5750\u5728\u5927\u5C71\u4E4B\u5DC5\uFF0C\u6B64\u65F6\u7684\u5468\u79C9\u4E49\u73AF\u987E\u56DB\u5468\u725B\u7F8A\u6210\u7FA4\uFF0C\u8EAB\u8FB9\u5750\u7740\u90DD\u51AC\u6885\u3002\u5468\u79C9\u4E49\u5199\u4E86\u4E24\u5C01\u4FE1\uFF0C\u5206\u522B\u7ED9\u4E86\u5468\u84C9\u548C\u5468\u79C9\u6606\uFF0C\u5468\u79C9\u4E49\u4E00\u751F\u6700\u5927\u7684\u5E78\u8FD0\u5C31\u662F\u9047\u5230\u90DD\u51AC\u6885\uFF0C\u5982\u679C\u4ED6\u6709\u4E00\u5929\u4E0D\u5728\u4E86\uFF0C\u5E0C\u671B\u5468\u79C9\u4E49\u548C\u5468\u84C9\u80FD\u4EE3\u66FF\u4ED6\u7167\u987E\u597D\u90DD\u51AC\u6885\u3002\u5468\u79C9\u4E49\u53EE\u5631\u5F1F\u5F1F\u548C\u59B9\u59B9\uFF0C\u8981\u597D\u597D\u751F\u6D3B\uFF0C\u53EF\u4EE5\u4E0D\u505A\u5F88\u6210\u529F\u7684\u4EBA\uFF0C\u4F46\u662F\u5FC5\u987B\u505A\u4E00\u4E2A\u7231\u56FD\u7684\u4EBA\u3002</p><p>\u5468\u79C9\u6606\u6491\u7740\u4F1E\u548C\u90D1\u5A1F\u6F2B\u6B65\u96E8\u4E2D\uFF0C\u4ED6\u671F\u671B\u8FD8\u80FD\u6709\u6765\u751F\uFF0C\u8FD8\u80FD\u548C\u90D1\u5A1F\u5728\u4E00\u8D77\uFF0C\u90D1\u5A1F\u5634\u4E0A\u7B11\u7740\u5468\u79C9\u6606\u60F3\u5F97\u7F8E\uFF0C\u5468\u79C9\u6606\u5634\u89D2\u626F\u51FA\u4E00\u4E1D\u7B11\u610F\uFF0C\u60F3\u60F3\u6765\u751F\u80FD\u5728\u4E00\u8D77\u4ED6\u5C31\u5FC3\u91CC\u7F8E\uFF0C\u90D1\u5A1F\u4F9D\u504E\u5728\u5468\u79C9\u6606\u8EAB\u4E0A\u61A7\u61AC\u672A\u6765\u3002</p>',14),r=[o];function _(n,s){return e(),t("div",null,r)}const h=p(c,[["render",_],["__file","58.html.vue"]]);export{h as default};
