import{_ as p}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as e,c as t,f as a}from"./app.dcc3c1cf.js";const c={},o=a('<h1 id="\u7B2C6\u96C6-\u4E01\u6625\u59B9\u627F\u8BA4\u8BEC\u9677\u4FAF\u8D35\u5E73" tabindex="-1"><a class="header-anchor" href="#\u7B2C6\u96C6-\u4E01\u6625\u59B9\u627F\u8BA4\u8BEC\u9677\u4FAF\u8D35\u5E73" aria-hidden="true">#</a> \u7B2C6\u96C6\uFF1A\u4E01\u6625\u59B9\u627F\u8BA4\u8BEC\u9677\u4FAF\u8D35\u5E73</h1><p>\u6731\u4F1F\u5C06\u533F\u540D\u7535\u8BDD\u539F\u59D4\u544A\u77E5\u6C5F\u9633\uFF0C\u5E76\u67E5\u51FA\u5BF9\u65B9\u662F\u7528\u9547\u4E0A\u7684\u516C\u7528\u7535\u8BDD\u6253\u6765\u3002\u6C5F\u9633\u770B\u5230\u6848\u4EF6\u51FA\u73B0\u8F6C\u673A\uFF0C\u4E8E\u662F\u6253\u7B97\u7EE7\u7EED\u5F7B\u67E5\uFF0C\u6731\u4F1F\u5F88\u8D4F\u8BC6\u6C5F\u9633\u7684\u80C6\u91CF\uFF0C\u51B3\u5B9A\u5E2E\u4ED6\u4E00\u8D77\u627E\u51FA\u771F\u51F6\uFF0C\u8FD8\u4FAF\u8D35\u5E73\u6E05\u767D\u3002</p><p>\u6C5F\u9633\u548C\u6731\u4F1F\u7EC4\u6210\u4E8C\u4EBA\u5C0F\u961F\uFF0C\u4ED6\u4EEC\u4ECE\u201C\u53D7\u5BB3\u4EBA\u201D\u4E01\u6625\u59B9\u5F00\u59CB\u8D70\u8BBF\uFF0C\u7ED3\u679C\u53D1\u73B0\u5C0F\u5356\u90E8\u91CC\u6709\u4E2A\u4E24\u5C81\u5DE6\u53F3\u7684\u7537\u5B69\u53EB\u5979\u5988\u5988\u3002\u7531\u4E8E\u4E01\u6625\u59B9\u5C1A\u672A\u518D\u5A5A\u4E5F\u6CA1\u6709\u9886\u517B\u5B69\u5B50\uFF0C\u6240\u4EE5\u6731\u4F1F\u5BF9\u6B64\u63D0\u51FA\u8D28\u7591\uFF0C\u5BA1\u8BAF\u5C0F\u5B69\u6765\u5386\uFF0C\u4E01\u6625\u59B9\u8868\u793A\u662F\u670B\u53CB\u5BB6\u7684\u5C0F\u5B69\uFF0C\u7D27\u63A5\u53C8\u5728\u6731\u4F1F\u7684\u8981\u6C42\u4E0B\u7ED9\u201C\u5B69\u5B50\u7236\u6BCD\u201D\u6253\u53BB\u7535\u8BDD\u3002</p><p>\u7535\u8BDD\u4E3B\u4EBA\u6B63\u662F\u9EC4\u6BDB\uFF0C\u5F85\u4ED6\u8D76\u6765\u5C0F\u5356\u90E8\u65F6\uFF0C\u6B63\u5DE7\u4E0E\u6C5F\u9633\u7B49\u4EBA\u78B0\u4E2A\u6B63\u9762\u3002\u6731\u4F1F\u5148\u5C06\u9EC4\u6BDB\u62FD\u51FA\u95E8\u9501\u5728\u8F66\u91CC\uFF0C\u5176\u540E\u6C5F\u9633\u62FF\u51FA\u5F55\u97F3\u673A\u8BB0\u5F55\u4E01\u6625\u59B9\u7684\u53E3\u4F9B\uFF0C\u800C\u8FD9\u4E2A\u5F55\u97F3\u673A\u4E5F\u5728\u4E03\u5E74\u540E\u515C\u515C\u8F6C\u8F6C\u6765\u5230\u9648\u660E\u7AE0\u624B\u91CC\uFF0C\u4E13\u6848\u7EC4\u636E\u6B64\u5F97\u5230\u91CD\u8981\u4FE1\u606F\u3002</p><p>\u5F55\u97F3\u673A\u91CC\u7684\u4E01\u6625\u59B9\u5341\u5206\u614C\u5F20\uFF0C\u8D77\u521D\u8FD8\u5728\u6709\u6240\u9690\u7792\uFF0C\u4F46\u56E0\u6731\u4F1F\u8C0E\u79F0\u9EC4\u6BDB\u62DB\u4F9B\u7F6A\u884C\uFF0C\u4E0D\u4EC5\u5957\u51FA\u5C0F\u5B69\u662F\u9EC4\u6BDB\u4EB2\u751F\u9AA8\u8089\uFF0C\u66F4\u4E0E\u6C5F\u9633\u8FDE\u756A\u653B\u7834\u4E01\u6625\u59B9\u5FC3\u7406\u9632\u7EBF\uFF0C\u8BA9\u5979\u5982\u5B9E\u4EA4\u4EE3\u52FE\u5F15\u4FAF\u8D35\u5E73\u5F3AJ\u7684\u8FC7\u7A0B\u3002</p><p>\u5F53\u521D\u9EC4\u6BDB\u4E3B\u52A8\u627E\u5230\u4E01\u6625\u59B9\uFF0C\u62FF\u51FA\u4E00\u4E07\u5757\u94B1\uFF0C\u751A\u81F3\u7B54\u5E94\u4F1A\u5E26\u4E01\u6625\u59B9\u8FDC\u79BB\u6751\u91CC\u6D41\u8A00\u871A\u8BED\uFF0C\u518D\u5230\u9547\u4E0A\u5F00\u4E2A\u8D85\u5E02\u8FC7\u65E5\u5B50\u3002\u4E01\u6625\u59B9\u6E34\u671B\u5B89\u7A33\u751F\u6D3B\u5DF2\u4E45\uFF0C\u81EA\u7136\u8F7B\u4FE1\u9EC4\u6BDB\uFF0C\u5E76\u4E14\u4F9D\u7167\u4ED6\u7684\u6559\u5506\uFF0C\u7279\u5730\u7528\u6BDB\u5DFE\u6CBE\u4E0A\u4FAF\u8D35\u5E73\u4F53\u6DB2\u5E26\u8D70\uFF0C\u76F4\u81F3\u4E00\u4E2A\u5C0F\u65F6\u540E\u62A5\u6848\u3002</p><p>\u672C\u6765\u6C5F\u9633\u53EF\u4EE5\u76F4\u63A5\u95EE\u51FA\u4FAF\u8D35\u5E73\u6B7B\u56E0\uFF0C\u600E\u6599\u9EC4\u6BDB\u6323\u8131\u624B\u94D0\u76F4\u63A5\u51B2\u8FDB\u5C0F\u5356\u90E8\u6253\u65AD\u8BE2\u95EE\uFF0C\u4E01\u6625\u59B9\u5BDF\u89C9\u4E0A\u5F53\uFF0C\u987F\u65F6\u7F04\u53E3\u9ED8\u8A00\uFF0C\u6B7B\u6D3B\u4E0D\u80AF\u900F\u9732\u534A\u70B9\u7EC6\u8282\u3002\u6731\u4F1F\u8981\u4E34\u65F6\u6267\u884C\u8DE8\u7701\u516C\u52A1\uFF0C\u6240\u4EE5\u6CA1\u6709\u5C06\u9EC4\u6BDB\u6293\u56DE\u8B66\u5C40\u5BA1\u95EE\uFF0C\u800C\u662F\u8BA9\u6C5F\u9633\u7B49\u4ED6\u56DE\u6765\u518D\u6536\u62FE\u9EC4\u6BDB\u548C\u5C0F\u5BE1\u5987\u3002</p><p>2010\u5E74\uFF0C\u770B\u5B88\u6240\u91CC\u7684\u5F20\u8D85\u5982\u5750\u9488\u6BE1\uFF0C\u8D77\u8EAB\u8BE2\u95EE\u76D1\u7BA1\u5458\u65F6\u95F4\uFF0C\u5F97\u77E5\u5DF2\u8FC7\u665A\u4E0A\u5341\u70B9\u534A\uFF0C\u4E0D\u7531\u76B1\u8D77\u7709\u5934\u3002\u4E13\u6848\u7EC4\u542C\u5B8C\u5F55\u97F3\u7ED3\u5408\u4FAF\u8D35\u5E73\u5C38\u68C0\u62A5\u544A\uFF0C\u9488\u5BF9\u6B64\u4E8B\u5C55\u5F00\u8BA8\u8BBA\uFF0C\u6CD5\u533B\u7ECF\u8FC7\u80BA\u6CE1\u89E3\u5256\u53D1\u73B0\u4FAF\u8D35\u5E73\u5E76\u975E\u6EBA\u6C34\u8EAB\u4EA1\uFF0C\u552F\u4E00\u6B7B\u4EA1\u65F6\u95F4\u662F\u5728\u5F53\u665A\u4E5D\u70B9\u534A\u5DE6\u53F3\u3002</p><p>\u9648\u660E\u7AE0\u8BA4\u4E3A\u4E01\u6625\u59B9\u53E3\u4F9B\u6709\u865A\u6709\u5047\uFF0C\u7591\u70B9\u5728\u4E8E\u4FAF\u8D35\u5E73\u662F\u5728\u5F3AJ\u540E\u6B7B\u8FD8\u662F\u5728\u5F3AJ\u524D\u6B7B\uFF0C\u56E0\u4E3A\u5C04\u7CBE\u548C\u819D\u8DF3\u53CD\u5C04\u90FD\u662F\u810A\u9AD3\u7684\u53CD\u5C04\u52A8\u4F5C\uFF0C\u6B7B\u540E\u53EA\u8981\u6027\u5668\u5B98\u53D7\u5230\u4E00\u5B9A\u5F3A\u5EA6\u7684\u523A\u6FC0\u7167\u6837\u53EF\u4EE5\u5C04\u7CBE\u3002\u4E25\u826F\u5BF9\u6C5F\u9633\u6B7B\u56E0\u5B58\u7591\uFF0C\u4F46\u662F\u6CA1\u6709\u627E\u5230\u4F38\u7F29\u8863\u67B6\uFF0C\u4FBF\u8BA9\u5C0F\u9A6C\u53BB\u62CD\u51E0\u5F20\u8D85\u5BA2\u5385\u91CC\u8001\u6276\u624B\u7684\u7167\u7247\uFF0C\u518D\u53BB\u5546\u573A\u627E\u76F8\u4F3C\u3002</p><p>\u300A\u6C5F\u6F6D\u665A\u62A5\u300B\u56E0\u7FA4\u4F17\u4E3E\u62A5\u4E34\u65F6\u505C\u520A\uFF0C\u6D41\u6D6A\u6C49\u5F00\u59CB\u7740\u624B\u884C\u52A8\uFF0C\u5148\u540E\u5236\u9020\u51FA\u4E24\u8D77\u7206\u70B8\uFF0C\u5F85\u8B66\u65B9\u548C\u5A92\u4F53\u63A5\u5230\u6D88\u606F\u540E\uFF0C\u8FC5\u901F\u8D76\u5F80\u4E8B\u6545\u73B0\u573A\uFF0C\u7531\u4E8E\u5730\u70B9\u6240\u5904\u4E8E\u5361\u6069\u96C6\u56E2\u7684\u70C2\u5C3E\u697C\u76D8\uFF0C\u6240\u4EE5\u5E76\u65E0\u4EFB\u4F55\u4EBA\u5458\u4F24\u4EA1\uFF0C\u4E5F\u6CA1\u6709\u627E\u5230\u8FDD\u7981\u7206\u70B8\u7269\u54C1\u3002</p><p>\u4E25\u826F\u53BB\u627E\u5F20\u8D85\u8BA9\u4ED6\u4F9B\u51FA\u540C\u4F19\uFF0C\u7ED3\u679C\u5F20\u8D85\u63D0\u9192\u4E25\u826F\u5C3D\u5FEB\u6062\u590D\u7167\u7247\u53D1\u5E03\uFF0C\u5426\u5219\u5C06\u65E0\u6CD5\u63A7\u5236\u4E8B\u6001\u8D70\u5411\u3002\u56E0\u4E3A\u8FD9\u680B\u70C2\u5C3E\u697C\u4E5F\u66FE\u91D1\u78A7\u8F89\u714C\uFF0C\u53EF\u60DC\u7A9D\u85CF\u592A\u591A\u6C61\u57A2\uFF0C\u5176\u4E2D\u5305\u542B\u674E\u5EFA\u56FD\u548C\u80E1\u4E00\u6D6A\uFF0C\u4ED6\u4EEC\u966A\u7740\u5B59\u4F20\u798F\u5728\u8FD9\u91CC\u5403\u8FC7\u9C7C\u5B50\u9171\uFF0C\u4E5F\u8C08\u8BBA\u8FC7\u5982\u4F55\u5BF9\u4ED8\u6C5F\u9633\u3002</p><p>\u91CD\u6848\u7EC4\u5F00\u4F1A\u8BA8\u8BBA\uFF0C\u53D1\u73B0\u4E24\u8D77\u7206\u70B8\u95F4\u9694\u65F6\u95F4\u5F88\u77ED\uFF0C\u73B0\u573A\u53EA\u6709\u8BF8\u591A\u94A2\u4E1D\u5E8A\u3001\u8702\u7A9D\u7089\u3001\u7535\u98CE\u6247\u7B49\u65E5\u7528\u5BB6\u5177\u3002\u9644\u8FD1\u6CA1\u6709\u76D1\u63A7\uFF0C\u4F46\u662F\u5468\u56F4\u5C45\u6C11\u4F1A\u5230\u9EBB\u96C0\u6E56\u905B\u5F2F\uFF0C\u636E\u4ED6\u4EEC\u53CD\u6620\u70C2\u5C3E\u697C\u7ECF\u5E38\u6709\u6D41\u6D6A\u6C49\u51FA\u6CA1\u3002\u4E25\u826F\u5C06\u5173\u952E\u4FE1\u606F\u9501\u5B9A\u5728\u6D41\u6D6A\u6C49\u8EAB\u4E0A\uFF0C\u4E8E\u662F\u548C\u4EFB\u73A5\u5A77\u518D\u6B21\u524D\u5F80\u7206\u70B8\u73B0\u573A\u52D8\u67E5\u3002</p><p>\u4EFB\u73A5\u5A77\u770B\u7740\u5730\u9762\u4E0A\u9057\u7559\u7684\u8702\u7A9D\u7089\uFF0C\u4E0D\u7531\u56DE\u60F3\u8D77\u5979\u5C0F\u65F6\u5019\u548C\u7236\u4EB2\u56F4\u7740\u8702\u7A9D\u8DEF\u5403\u996D\u7684\u753B\u9762\u3002\u5F53\u65F6\u7236\u4EB2\u4F5C\u4E3A\u516C\u5B89\u5211\u8B66\uFF0C\u6267\u884C\u4EFB\u52A1\u65F6\u4E0D\u5E78\u53D7\u4F24\uFF0C\u7531\u4E8E\u533B\u9662\u53EA\u6709\u4E00\u4E2A\u624B\u672F\u5BA4\uFF0C\u6240\u4EE5\u4ED6\u628A\u6551\u547D\u673A\u4F1A\u8BA9\u7ED9\u540C\u6837\u9700\u8981\u62A2\u6551\u7684\u5C0F\u5973\u5B69\uFF0C\u76F4\u81F3\u4ECA\u65E5\u4EFB\u73A5\u5A77\u90FD\u4F1A\u901A\u8FC7\u9999\u70DF\u5473\u9053\u6000\u5FF5\u7236\u4EB2\u3002</p><p>\u7206\u70B8\u4E8B\u6545\u53D1\u751F\u540E\uFF0C\u4E25\u793E\u957F\u610F\u8BC6\u5230\u4E8B\u6001\u7684\u4E25\u91CD\u6027\uFF0C\u7ACB\u9A6C\u6062\u590D\u62A5\u7EB8\u520A\u767B\u7EE7\u7EED\u914D\u5408\u8B66\u65B9\u3002\u4E25\u826F\u5206\u6790\u6B64\u6B21\u7206\u70B8\u6848\u4E0E\u5361\u6069\u96C6\u56E2\u6709\u5173\uFF0C\u4ED6\u51B3\u5B9A\u6362\u4E2A\u601D\u8DEF\uFF0C\u6253\u7B97\u627E\u51FA\u4E0B\u843D\u4E0D\u660E\u7684\u6731\u4F1F\uFF0C\u4F46\u6B64\u65F6\u6731\u4F1F\u5C31\u5728\u6C5F\u8C2D\u5E02\u5185\uFF0C\u4ED6\u8863\u886B\u7834\u891B\uFF0C\u6210\u4E3A\u5916\u4EBA\u773C\u4E2D\u7684\u6D41\u6D6A\u6C49\uFF1B\u6210\u4E3A\u9001\u6C34\u5DE5\u5E2E\u8001\u677F\u770B\u5E93\u623F\u3002</p>',14),r=[o];function _(n,s){return e(),t("div",null,r)}const h=p(c,[["render",_],["__file","6.html.vue"]]);export{h as default};
