import{_ as p}from"./_plugin-vue_export-helper.cdc0426e.js";import{o,c as t,f as e}from"./app.1218f0f6.js";const r={},n=e('<h1 id="\u8BF8\u4FAF\u5E76\u8D77" tabindex="-1"><a class="header-anchor" href="#\u8BF8\u4FAF\u5E76\u8D77" aria-hidden="true">#</a> \u8BF8\u4FAF\u5E76\u8D77</h1><blockquote><p>\u6625\uFF0C\u6B63\u6708\uFF0C\u5173\u4E1C\u5DDE\u90E1\u7686\u8D77\u5175\u4EE5\u8BA8\u8463\u5353\uFF0C\u63A8\u6E24\u6D77\u592A\u5B88\u8881\u7ECD\u4E3A\u76DF\u4E3B\u3002\u7ECD\u81EA\u53F7\u8F66\u9A91\u5C06\u519B\uFF0C\u8BF8\u5C06\u7686\u677F\u6388\u5B98\u53F7\u3002 \u7ECD\u4E0E\u6CB3\u5185\u592A\u5B88\u738B\u5321\u5C6F\u6CB3\u5185\uFF0C\u5180\u5DDE\u7267\u97E9\u99A5\u7559\u9134\uFF0C\u7ED9\u5176\u519B\u7CAE\uFF0C\u8C6B\u5DDE\u523A\u53F2\u5B54\u4F37\u5C6F\u988D\u5DDD\uFF0C\u5157\u5DDE\u523A\u53F2\u5218\u5CB1\u3001\u9648\u7559\u592A\u5B88\u5F20\u9088\u3001\u9088\u5F1F\u5E7F\u9675\u592A\u5B88\u8D85\u3001\u4E1C\u90E1\u592A\u5B88\u6865\u7441\u3001\u5C71\u9633\u592A\u5B88\u8881\u9057\u3001\u6D4E\u5317\u76F8\u9C8D\u4FE1\u4E0E\u66F9\u64CD\u4FF1\u5C6F\u9178\u67A3\uFF0C\u540E\u5C06\u519B\u8881\u672F\u5C6F\u9C81\u9633\uFF0C\u4F17\u540D\u6570\u4E07\u3002</p></blockquote><p><strong>\u300A\u8D44\u6CBB\u901A\u9274 \xB7 \u5377\u4E94\u5341\u4E5D \xB7 \u6C49\u7EAA\u4E94\u5341\u4E00\u300B</strong></p><blockquote><p>\u521D\u5E73\u5143\u5E74\uFF08\u516C\u5143190\u5E74\uFF09\u6B63\u6708\uFF0C\u4EE5\u6E05\u541B\u4FA7\u7684\u540D\u4E49\u7ED3\u76DF</p></blockquote><p>\u6E24\u6D77\u592A\u5B88\u8881\u7ECD\u4E3A\u76DF\u4E3B\uFF0C\u8A93\u5C06\u671D\u5EF7\u5978\u8D3C\u8463\u5353\u7FE6\u9664\uFF0C\u4F46\u76DF\u519B\u5728\u6B43\u8840\u540C\u76DF\u4E4B\u540E\uFF0C\u6309\u5175\u4E0D\u52A8\uFF0C\u5E76\u6CA1\u6709\u548C\u8463\u5353\u519B\u6253\u8FC7\u51E0\u573A\u3002</p><p>\u53F2\u4E66\u4E2D\uFF0C<strong>\u548C\u8463\u5353\u6709\u8FC7\u4EA4\u6218\u8BB0\u5F55\u7684\uFF0C\u53EA\u6709\u66F9\u64CD\u548C\u5B59\u575A</strong>\u3002</p><p>\u5728\u8054\u519B\u4E4B\u4E2D\uFF0C\u5E76\u6CA1\u6709\u5B59\u575A\u7684\u540D\u53F7\uFF0C\u8FD9\u662F\u56E0\u4E3A\u5B59\u575A\u662F\u4F5C\u4E3A\u8881\u672F\u7684\u5C06\u9886\u53C2\u6218\u7684\u3002</p><p>\u4F46\u5176\u5B9E\uFF0C\u5B59\u575A\u6210\u540D\u5F88\u65E9\uFF0C\u65E9\u5728\u7075\u5E1D\u5E74\u95F4\uFF0C\u5B59\u575A\u5C31\u56E0\u4E3A\u5E73\u53DB\uFF0C\u88AB\u4EFB\u547D\u4E3A\u957F\u6C99\u592A\u5B88\uFF0C\u5C01\u4E4C\u7A0B\u4FAF\u3002</p><p>\u53EA\u4E0D\u8FC7\u5B59\u575A\u6B66\u4EBA\u51FA\u8EAB\uFF0C\u6240\u4EE5\u4E00\u76F4\u4E0D\u5927\u88AB\u58EB\u5927\u592B\u4EEC\u770B\u5F97\u8D77\uFF1B\u800C\u4E14\u5B59\u575A\u81EA\u5DF1\u751F\u6027\u9C81\u83BD\uFF0C\u66FE\u88AB\u4EBA\u5F53\u67AA\u4F7F\uFF0C\u6740\u4E86\u81EA\u5DF1\u7684\u4E0A\u5B98\uFF0C\u6700\u540E\u5B9E\u5728\u6CA1\u529E\u6CD5\uFF0C\u53EA\u80FD\u6295\u9760\u4E86\u8881\u672F\u3002</p><p>\u5B59\u575A\u548C\u8463\u5353\u7684\u90E8\u961F\u4EA4\u4E86\u51E0\u6B21\u624B\uFF0C\u603B\u4F53\u4E0A\u6765\u8BF4\uFF0C\u80DC\u591A\u8D25\u5C11\u3002\u9664\u4E86\u521A\u5F00\u59CB\u7684\u65F6\u5019\u5403\u8FC7\u4E00\u6B21\u4E8F\uFF0C<strong>\u540E\u6765\u57FA\u672C\u4E0A\u80FD\u79F0\u5F97\u4E0A\u662F\u4F5C\u6218\u52C7\u731B\uFF0C\u6240\u5411\u62AB\u9761\u3002</strong></p><p>\u5BF9\u4E8E\u5B59\u575A\uFF0C\u8463\u5353\u5F88\u662F\u5934\u75BC\u3002\u4E24\u4EBA\u662F\u8001\u76F8\u8BC6\u4E86\uFF0C\u5728\u897F\u51C9\u6218\u573A\u4E0A\u5C31\u66FE\u6709\u8FC7\u63A5\u89E6\u3002\u5F53\u65F6\uFF0C\u4F5C\u4E3A\u65B9\u9762\u5927\u5458\u7684\u8463\u5353\uFF0C\u5BF9\u53EA\u662F\u4E2A\u5C0F\u53C2\u519B\u7684\u5B59\u575A\u9887\u4E3A\u6B23\u8D4F\u3002\u90FD\u8BF4\u8463\u5353\u6709\u8BC6\u4EBA\u4E4B\u540D\uFF0C\u8FD9\u8BDD\u8FD8\u771F\u4E0D\u5047\uFF0C\u5B59\u575A\u8FD9\u4E2A\u5F53\u5E74\u7684\u5C0F\u53C2\u519B\uFF0C\u540E\u6765\u679C\u7136\u628A\u4ED6\u7684\u624B\u4E0B\u6253\u5F97\u6EE1\u5730\u627E\u7259\uFF0C\u8FD8\u6740\u4E86\u90FD\u7763\u534E\u96C4\u3002</p><p>\u773C\u89C1\u522B\u7684\u5C0F\u5F1F\u90FD\u88AB\u63CD\u8DB4\u4E0B\u4E86\uFF0C\u8463\u5353\u5927\u54E5\u51B3\u5B9A\u81EA\u5DF1\u4E0A\u3002\u8FD9\u4E2A\u76F4\u6765\u76F4\u53BB\u7684\u7C97\u4EBA\uFF0C\u8FD9\u6B21\u7ADF\u7136\u5B66\u4F1A\u4E86\u5148\u793C\u540E\u5175\uFF0C\u4ED6\u6D3E\u4EBA\u53BB\u548C\u5B59\u575A\u8BF4\uFF0C\u8981\u4E0D\u6211\u4EEC\u7ED3\u4E2A\u513F\u5973\u4EB2\u5BB6\uFF1F</p><p>\u597D\u5427\uFF0C\u90A3\u5C31\u771F\u5200\u771F\u67AA\u7684\u5E72\u5427\uFF01\u53EA\u53EF\u60DC\uFF0C\u8463\u5353\u4EB2\u81EA\u5E26\u5175\u4E0A\u9635\uFF0C\u5374\u8FD8\u662F\u6253\u4E0D\u8FC7\u5B59\u575A\uFF0C\u53EA\u80FD\u9000\u8D70\u3002\u4E34\u8D70\u524D\uFF0C\u8463\u5353\u628A\u5415\u5E03\u7559\u5728\u4E86\u6D1B\u9633\u3002\u4F46\u5415\u5E03\u8FDE\u4E2A\u57CE\u90FD\u5B88\u4E0D\u4F4F\uFF0C\u88AB\u5B59\u575A\u8D76\u51FA\u4E86\u6D1B\u9633\u57CE\u3002</p><p>\u5B59\u575A\u63A5\u6536\u4E86\u6D1B\u9633\u4EE5\u540E\uFF0C\u6253\u626B\u597D\u5BAB\u57CE\uFF0C\u4FEE\u590D\u4E86\u88AB\u5415\u5E03\u5E26\u5175\u76D7\u8FC7\u7684\u4E1C\u6C49\u7687\u9675\uFF0C\u7136\u540E\u628A\u76DF\u519B\u7684\u5927\u4F6C\u4EEC\u5168\u90FD\u8BF7\u4E86\u8FDB\u6765\u3002</p><p>\u867D\u7136\u8FD9\u65F6\u6C49\u732E\u5E1D\u5DF2\u7ECF\u88AB\u8463\u5353\u52AB\u6301\u5230\u4E86\u957F\u5B89\uFF0C\u867D\u7136\u6D1B\u9633\u57CE\u91CC\u65E9\u5DF2\u51E0\u65E0\u4EBA\u70DF\uFF0C\u4F46\u6536\u590D\u90FD\u57CE\u8FD9\u79CD\u4E8B\uFF0C\u8FD8\u662F\u5F88\u503C\u5F97\u5E86\u795D\u7684\u5440\uFF01</p><p>\u8FD9\u79CD\u4E0D\u601D\u8FDB\u53D6\u7684\u5FC3\u6001\uFF0C\u88AB\u4E00\u4E2A\u4E0E\u4F1A\u8005\u8BB0\u5F55\u5728\u4E86\u8BD7\u4E2D\uFF1A</p><p>\u5173\u4E1C\u6709\u4E49\u58EB\uFF0C\u5174\u5175\u8BA8\u7FA4\u51F6\u3002 \u521D\u671F\u4F1A\u76DF\u6D25\uFF0C\u4E43\u5FC3\u5728\u54B8\u9633\u3002 \u519B\u5408\u529B\u4E0D\u9F50\uFF0C\u8E0C\u8E87\u800C\u96C1\u884C\u3002</p><p>\u8FD9\u4E2A\u4EBA\u5C31\u662F\u66F9\u64CD\uFF0C\u8FD9\u9996\u8BD7\u5C31\u662F\u300A\u5D69\u91CC\u884C\u300B\u3002</p><p>\u66F9\u64CD\u5BF9\u7FA4\u96C4\u7684\u8FD9\u79CD\u6001\u5EA6\u75DB\u5FC3\u75BE\u9996\uFF0C\u51E0\u756A\u529D\u8BEB\u5E94\u8BE5\u4E58\u80DC\u8FFD\u51FB\u3002\u53EF\u65E0\u5948\uFF0C\u5927\u5BB6\u9152\u559D\u9AD8\u4E86\uFF0C\u90FD\u4EE5\u4E3A\u66F9\u64CD\u8BF4\u7684\u4E5F\u662F\u9189\u8BDD\uFF0C\u5E76\u4E0D\u5F53\u771F\u3002</p><p>**\u773C\u89C1\u65E0\u671B\uFF0C\u66F9\u64CD\u4E8E\u662F\u81EA\u884C\u7387\u5175\u8FFD\u51FB\u3002**\u7ED3\u679C\u88AB\u8463\u5353\u7684\u90E8\u5C06\u5F90\u8363\u6253\u4E86\u4E2A\u57CB\u4F0F\uFF0C\u5DEE\u70B9\u6B7B\u5728\u6218\u573A\u4E0A\u3002</p><p>\u66F9\u64CD\u8F93\u5F97\u7070\u5934\u571F\u8138\uFF0C\u53EA\u80FD\u518D\u56DE\u9178\u67A3\uFF08\u8054\u519B\u4E3B\u529B\u5C6F\u5175\u4E8E\u6B64\uFF09\uFF0C\u529B\u529D\u5927\u5BB6\u9F50\u5FC3\u5408\u529B\uFF0C\u4E00\u8D77\u4F10\u8463\u3002\u53EF\u8FD9\u4E2A\u4E8B\u5728\u522B\u4EBA\u770B\u6765\u5C31\u5F88\u641E\u7B11\u4E86\uFF0C\u4F60\u6253\u8D62\u4E86\u56DE\u6765\u561A\u5575\u51E0\u53E5\uFF0C\u6211\u4EEC\u542C\u7740\u4E5F\u5C31\u662F\u4E86\uFF1B\u73B0\u5728\u6253\u8F93\u4E86\u5C41\u6EDA\u5C3F\u6D41\u7684\u56DE\u6765\uFF0C\u8FD8\u6709\u4EC0\u4E48\u8D44\u683C\u778E\u903C\u903C\uFF01</p><p>\u66F9\u64CD\u5927\u5931\u6240\u671B\uFF0C\u5FC3\u7070\u610F\u51B7\u4E4B\u4F59\uFF0C\u79BB\u5F00\u4E86\u9178\u67A3\u3002</p><p>\u66F9\u64CD\u7684\u4E0B\u4E00\u7AD9\u662F\u4E1C\u90E1\uFF0C**\u4ECE\u6B64\u4EE5\u540E\uFF0C\u4ED6\u548C\u8463\u5353\u518D\u65E0\u4EA4\u96C6\u3002**\u5929\u4E0B\u5DF2\u7136\u5927\u4E71\uFF0C\u65E2\u7136\u8BA8\u4F10\u8463\u5353\u4E0D\u53EF\u4E3A\uFF0C\u90A3\u5C31\u4E3A\u81EA\u5DF1\u7684\u5C06\u6765\u6253\u7B97\u5427\uFF01\u6B64\u540E\uFF0C\u66F9\u64CD\u8BA8\u9EC4\u5DFE\u4E8E\u9752\u5156\uFF0C\u8FCE\u732E\u5E1D\u4E8E\u8BB8\u4E0B\uFF0C\u7834\u8881\u7ECD\u4E8E\u5B98\u6E21\u3002\u5F53\u7136\uFF0C\u90A3\u662F\u53E6\u5916\u7684\u4E00\u756A\u6CE2\u6F9C\u58EE\u9614\u4E86\u3002</p><p>\u800C\u6240\u8C13\u7684\u8054\u519B\uFF0C\u5C6F\u5175\u4E8E\u9178\u67A3\u6570\u6708\uFF0C\u7EC8\u4E8E\u4EA7\u751F\u5185\u8BA7\u3002\u5728\u7CAE\u98DF\u5403\u5149\u4EE5\u540E\uFF0C\u7FA4\u96C4\u76F8\u4E92\u503E\u8F67\uFF0C\u6DF7\u4E71\u4E0D\u5DF2\u3002</p><p>\u5B59\u575A\u6B64\u65F6\u76D8\u8E1E\u4E8E\u9C81\u9633\uFF0C\u4F46\u5F88\u5FEB\u8FD9\u91CC\u5C31\u88AB\u8881\u7ECD\u4EFB\u547D\u7684\u8C6B\u5DDE\u523A\u53F2\u4FB5\u593A\uFF0C\u5B59\u575A\u53EA\u5F97\u9000\u8D70\u3002</p><p>\u8F6C\u5E74\uFF0C\u8881\u672F\u4E0E\u5218\u8868\u56E0\u5357\u9633\u7684\u5F52\u5C5E\u5F00\u6218\uFF0C<strong>\u5B59\u575A\u5949\u547D\u5F81\u8BA8\u5218\u8868\uFF0C\u4E2D\u6D41\u77E2\u800C\u6B7B\u3002</strong></p><p>\u66F9\u64CD\u4E0E\u5B59\u575A\uFF0C\u4E24\u4EBA\u9887\u591A\u76F8\u4F3C\u4E4B\u5904\uFF0C\u90FD\u662F\u52C7\u4E8E\u4EFB\u4E8B\uFF0C\u4F5C\u6218\u52C7\u731B\uFF0C\u800C\u4E14\u6B64\u65F6\u8FD8\u90FD\u6000\u7740\u5BF9\u5927\u6C49\u671D\u7684\u8D23\u4EFB\u5FC3\u3002\u4F46\u662F\uFF0C\u518D\u770B\u4E24\u4EBA\u4ECE\u6218\u524D\u5230\u6218\u540E\u7684\u53D8\u5316\uFF0C\u7ED3\u679C\u5374\u5DF2\u7ECF\u9887\u4E3A\u4E0D\u540C\u3002\u8FD9\u5F53\u4E2D\uFF1A</p><blockquote><p><strong>\u4E24\u4EBA\u7684\u6700\u5927\u533A\u522B\uFF0C\u5728\u4E8E\u89C2\u5FF5\u610F\u8BC6\u4E0A\u7684\u4E0D\u540C\u3002</strong></p></blockquote><p>\u4E24\u4EBA\u4E00\u6837\u7684\u53C2\u52A0\u8054\u519B\u8BA8\u4F10\u8463\u5353\uFF0C\u4F46\u7ECF\u5386\u632B\u6298\u4EE5\u540E\uFF0C\u66F9\u64CD\u5DF2\u7ECF\u654F\u9510\u7684\u610F\u8BC6\u5230\u5929\u4E0B\u5927\u4E71\uFF0C\u4E8E\u662F\u653E\u5F03\u4E86\u4E4B\u524D\u7684\u8BBE\u60F3\uFF0C\u8F6C\u800C\u8F6C\u6218\u56DB\u65B9\uFF0C\u79EF\u6512\u5B9E\u529B\uFF1B\u800C\u5B59\u575A\u5462\uFF0C\u7A7A\u8D1F\u4E00\u8EAB\u6B66\u529B\uFF0C\u5374\u4ECE\u59CB\u81F3\u7EC8\u90FD\u5FC3\u7518\u60C5\u613F\u5730\u4F9D\u9644\u5728\u8881\u672F\u624B\u4E0B\u4EFB\u804C\uFF0C\u4E3A\u8881\u672F\u4E1C\u5F81\u897F\u8BA8\uFF0C\u6700\u7EC8\u6B7B\u4E8E\u6218\u573A\u3002</p><p>\u800C\u5982\u679C\u4ED6\u5F53\u65F6\u4E0D\u6B7B\uFF0C\u6211\u4EEC\u751A\u81F3\u53EF\u4EE5\u5047\u8BBE\uFF0C\u4ED6\u53EF\u80FD\u5C06\u6C38\u8FDC\u662F\u8881\u672F\u9EBE\u4E0B\u7684\u4E00\u540D\u5927\u5C06\u3002<strong>\u4ED6\u7684\u5FE0\u8BDA\uFF0C\u56FA\u7136\u8BA9\u6211\u4EEC\u8D5E\u8D4F\uFF1B\u4F46\u4ED6\u5BF9\u65F6\u5C40\u7684\u4E0D\u654F\u611F\uFF0C\u540C\u6837\u8BA9\u6211\u4EEC\u89C9\u5F97\u53EF\u60DC\u3002</strong></p><p>\u56DE\u987E\u5B59\u575A\u77ED\u6682\u7684\u4EBA\u751F\uFF0C\u4ED6\u548C\u5434\u56FD\u7684\u5EFA\u7ACB\uFF0C\u5173\u7CFB\u5E76\u4E0D\u5927\u3002\u4ED6\u5BF9\u4E8E\u5B59\u7B56\u6700\u5927\u7684\u52A9\u529B\uFF0C\u53EF\u80FD\u662F\u7559\u4E0B\u4E86\u4E00\u4E9B\u90E8\u66F2\uFF0C\u8BF8\u5982\u7A0B\u666E\u3001\u9EC4\u76D6\u7B49\u3002\u4F46\u5E78\u597D\uFF0C<strong>\u5B59\u7B56\u7684\u80FD\u529B\u548C\u8FDC\u89C1\u90FD\u5728\u7236\u4EB2\u4E4B\u4E0A</strong>\u3002</p><p>\u9664\u4E86\u4F5C\u6218\u52C7\u731B\u8FD9\u4E2A\u4E24\u4EBA\u90FD\u6709\u7684\u7279\u5F81\u4EE5\u5916\uFF0C\u5B59\u7B56\u76F8\u6BD4\u4E8E\u5B59\u575A\uFF0C\u6700\u5927\u7684\u4F18\u52BF\u5C31\u5728\u4E8E\uFF0C\u5B59\u7B56\u6709\u79EF\u6512\u5B9E\u529B\u7684\u610F\u8BC6\u3002\u5728\u5B59\u575A\u6B7B\u540E\uFF0C\u5B59\u7B56\u8BBE\u6CD5\u8131\u79BB\u4E86\u8881\u672F\u7684\u63A7\u5236\uFF0C\u81EA\u7ACB\u95E8\u6237\u3002</p><p>\u7136\u540E\uFF0C\u624D\u6709\u4E86\u540E\u6765\u7684\u4E09\u5206\u5929\u4E0B\u6709\u5176\u4E00\u3002</p><h2 id="\u53CD\u8463\u8054\u519B" tabindex="-1"><a class="header-anchor" href="#\u53CD\u8463\u8054\u519B" aria-hidden="true">#</a> \u53CD\u8463\u8054\u519B</h2><p>\u53CD\u8463\u540C\u76DF\u80FD\u591F\u6210\u7ACB\uFF0C\u8BF4\u8D77\u6765\uFF0C\u8FD8\u662F\u8463\u5353\u81EA\u5DF1\u642C\u8D77\u77F3\u5934\u7838\u4E86\u81EA\u5DF1\u7684\u811A\u3002</p><p>\u5728\u7387\u5175\u8FDB\u4EAC\u4EE5\u540E\uFF0C\u8463\u5353\u6DF1\u77E5\u81EA\u5DF1\u7C97\u4EBA\u4E00\u4E2A\uFF0C\u58EB\u5927\u592B\u4E0D\u5927\u53EF\u80FD\u770B\u5F97\u8D77\uFF0C\u56E0\u6B64\u4ED6\u4E13\u95E8\u653E\u4F4E\u59FF\u6001\uFF0C\u523B\u610F\u8BA8\u597D\u58EB\u5927\u592B\u3002</p><p>\u4F46\u6CA1\u60F3\u5230\uFF0C\u8FD9\u4E8B\u513F\u8BF4\u8D77\u6765\u5BB9\u6613\u505A\u8D77\u6765\u96BE\u3002\u5728\u53EC\u96C6\u516C\u537F\u5F00\u4F1A\uFF0C\u8BA8\u8BBA\u5E9F\u7ACB\u7687\u5E1D\u7684\u65F6\u5019\uFF0C\u8463\u5353\u4E00\u65F6\u6CA1\u5FCD\u4F4F\uFF0C\u548C\u8881\u7ECD\u5435\u4E86\u4E00\u67B6\u3002\u8FD9\u4E00\u5435\u4E0D\u8981\u7D27\uFF0C\u5C45\u7136\u4E00\u4E0B\u5B50\u5C31\u5413\u8DD1\u4E86\u597D\u591A\u4EBA\uFF0C\u6BD4\u5982\u8BF4\u8881\u7ECD\u3001\u8881\u672F\u3001\u66F9\u64CD\u3002</p><p>\u8FD9\u8BA9\u8463\u5353\u5934\u75BC\u4E0D\u5DF2\u3002\u5BF9\u5F85\u58EB\u5927\u592B\u548C\u5E26\u5175\u771F\u662F\u5B8C\u5168\u4E0D\u4E00\u6837\uFF0C\u4ED6\u5B9E\u5728\u662F\u65E0\u4ECE\u4E0B\u624B\u3002\u8DDF\u4ED6\u4EEC\u597D\u597D\u8BF4\u8BDD\u5427\uFF0C\u4ED6\u4EEC\u9732\u80F3\u818A\u633D\u8896\u5B50\uFF0C\u4E0A\u6765\u5C31\u8981\u8DDF\u4F60\u5435\uFF1B\u81EA\u5DF1\u4E5F\u79C0\u4E00\u4E0B\u808C\u8089\u5427\uFF0C\u7ED3\u679C\u5C31\u628A\u4EBA\u5168\u7ED9\u5413\u8DD1\u4E86\u3002</p><p>\u8FD9\u65F6\u5019\uFF0C\u6709\u4EBA\u7ED9\u4ED6\u51FA\u4E86\u4E2A\u4E3B\u610F\uFF1A\u8881\u7ECD\u4E0D\u662F\u90FD\u8DD1\u4E86\u561B\uFF0C\u60A8\u5C31\u987A\u6C34\u63A8\u821F\uFF0C\u7ED9\u4ED6\u5C01\u4E2A\u5B98\u5457\uFF01\u8FD9\u5C31\u80FD\u663E\u5F97\u8463\u516C\u60A8\u5BBD\u5B8F\u5927\u91CF\u4E86\u3002\u81F3\u4E8E\u671D\u91CC\u90A3\u4E9B\u53CD\u5BF9\u6D3E\uFF0C\u60A8\u4E5F\u628A\u4ED6\u4EEC\u6D3E\u5230\u5730\u65B9\u4E0A\u53BB\u597D\u4E86\uFF0C\u4E00\u6765\u8FD8\u60A8\u8033\u6839\u4E00\u4E2A\u6E05\u51C0\uFF0C\u4E8C\u6765\u60A8\u7ED9\u4ED6\u4EEC\u52A0\u4E86\u5B98\uFF0C\u4ED6\u4EEC\u611F\u6069\u6234\u5FB7\uFF0C\u81EA\u7136\u5C31\u4E0D\u4F1A\u53CD\u5BF9\u60A8\u4E86\u3002</p><p>\u8463\u5353\u559C\u51FA\u671B\u5916\uFF0C\u5FC3\u60F3\u8BFB\u4E66\u4EBA\u679C\u7136\u9AD8\u660E\uFF0C\u8FD9\u4E48\u597D\u7684\u529E\u6CD5\u6211\u8001\u8463\u5C31\u60F3\u4E0D\u51FA\u6765\uFF01\u4E8B\u60C5\u8BF4\u5E72\u5C31\u5E72\uFF0C\u8463\u5353\u4E8E\u662F\u8FC7\u4E86\u4E00\u628A\u505A\u7687\u5E1D\u7684\u763E\uFF0C\u628A\u597D\u51E0\u4E2A\u7701\u90E8\u7EA7\u5E72\u90E8\u6D3E\u5230\u4E86\u5730\u65B9\u3002</p><p>**\u7ED3\u679C\uFF0C\u8FD9\u4E9B\u4EBA\u4E00\u5230\u5730\u65B9\uFF0C\u5C31\u548C\u8881\u7ECD\u4E32\u8054\uFF0C\u540C\u65F6\u8D77\u5175\u53CD\u5BF9\u8463\u5353\u3002**\u5386\u53F2\u4E0A\u8D6B\u8D6B\u6709\u540D\u7684\u8054\u519B\u8BA8\u8463\u5353\uFF0C\u5C31\u662F\u8FD9\u4E48\u6765\u7684\u3002</p>',41),s=[n];function a(c,d){return o(),t("div",null,s)}const l=p(r,[["render",a],["__file","6.html.vue"]]);export{l as default};
