import{_ as p}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as e,c as t,f as a}from"./app.43858123.js";const c={},o=a('<h1 id="\u7B2C6\u96C6-\u9752\u82CD\u52A9\u529B\u5C0F\u5170\u82B1\u4ED9\u8BD5" tabindex="-1"><a class="header-anchor" href="#\u7B2C6\u96C6-\u9752\u82CD\u52A9\u529B\u5C0F\u5170\u82B1\u4ED9\u8BD5" aria-hidden="true">#</a> \u7B2C6\u96C6\uFF1A\u9752\u82CD\u52A9\u529B\u5C0F\u5170\u82B1\u4ED9\u8BD5</h1><p>\u4E1C\u65B9\u9752\u82CD\u544A\u8BC9\u5C0F\u5170\u82B1\uFF0C\u82E5\u662F\u5979\u6709\u6C42\u4E8E\u81EA\u5DF1\uFF0C\u81EA\u5DF1\u5B9A\u4F1A\u66FF\u5979\u5B8C\u6210\u3002\u5C0F\u5170\u82B1\u501F\u673A\u63D0\u51FA\uFF0C\u8BA9\u4E1C\u65B9\u9752\u82CD\u8D76\u7D27\u56DE\u660A\u5929\u5854\u91CC\u3002\u5BF9\u65B9\u679C\u7136\u62D2\u7EDD\uFF0C\u5C0F\u5170\u82B1\u4E0D\u7531\u5F97\u591A\u60F3\uFF0C\u4ED6\u6216\u8BB8\u662F\u60F3\u7559\u5728\u81EA\u5DF1\u8EAB\u8FB9\u3002\u5C0F\u5170\u82B1\u53EA\u597D\u63D0\u51FA\uFF0C\u81EA\u5DF1\u60F3\u901A\u8FC7\u4ED9\u8003\uFF0C\u5230\u957F\u73E9\u8EAB\u8FB9\u4EFB\u804C\u3002</p><p>\u957F\u73E9\u4E43\u662F\u4E91\u4E2D\u541B\u7684\u5F1F\u5F1F\u3002\u5F53\u5E74\u4E24\u65CF\u5927\u6218\u540E\uFF0C\u6C34\u4E91\u5929\u56DB\u5206\u4E94\u88C2\uFF0C\u957F\u73E9\u7528\u4E86\u4E0D\u5230\u516D\u5343\u5E74\u5C31\u6062\u590D\u4E86\u6240\u6709\u5931\u5730\u3002\u5728\u5C0F\u5170\u82B1\u773C\u4E2D\uFF0C\u82E5\u957F\u73E9\u518D\u65E9\u751F\u4E2A\u51E0\u767E\u5E74\uFF0C\u90A3\u4E1C\u65B9\u9752\u82CD\u4E5F\u5B9A\u4E0D\u662F\u4ED6\u7684\u5BF9\u624B\u3002\u8BF4\u4E86\u8BB8\u4E45\uFF0C\u4E1C\u65B9\u9752\u82CD\u8FD9\u624D\u77E5\u6653\uFF0C\u539F\u6765\u5C0F\u5170\u82B1\u559C\u6B22\u957F\u73E9\u3002</p><p>\u8776\u8863\u5DF2\u7ECF\u8C03\u67E5\u5230\uFF0C\u5077\u8D70\u547D\u7C3F\u7684\u4E43\u662F\u53F8\u547D\u6BBF\u4E00\u682A\u65E0\u540D\u5170\u82B1\u8349\u7CBE\u7075\u3002\u6D77\u5E02\u738B\u4E00\u731C\uFF0C\u5C31\u77E5\u9053\u662F\u90A3\u7EE7\u627F\u4E86\u53F8\u547D\u8863\u94B5\u7684\u5C0F\u5170\u82B1\u3002\u6D77\u5E02\u738B\u4E00\u58F0\u4EE4\u4E0B\uFF0C\u6740\u4E86\u5C0F\u5170\u82B1\u593A\u56DE\u547D\u7C3F\u3002\u4E0D\u8FC7\u957F\u73E9\u8FD1\u65E5\u5728\u7389\u4EAC\uFF0C\u82E5\u53D1\u751F\u4E86\u547D\u6848\uFF0C\u4ED6\u5B9A\u5C06\u5F7B\u67E5\uFF0C\u82E5\u662F\u7275\u8FDE\u6D77\u5E02\u6015\u662F\u4E0D\u59A5\u3002\u6070\u5DE7\u4ED9\u8003\u5373\u5C06\u6765\u4E34\uFF0C\u82E5\u662F\u8FD9\u4E2A\u65F6\u5019\u4E00\u4E2A\u5C0F\u82B1\u7CBE\u795E\u4E0D\u77E5\u9B3C\u4E0D\u89C9\u53D1\u751F\u610F\u5916\uFF0C\u4E5F\u6CA1\u6709\u4EBA\u4F1A\u5728\u610F\u3002</p><p>\u5C0F\u5170\u82B1\u6253\u4E86\u4E00\u4E2A\u76F9\uFF0C\u7741\u5F00\u773C\u775B\u5C31\u770B\u5230\u4E86\u957F\u73E9\uFF0C\u5413\u5F97\u762B\u5012\u5728\u5730\u3002\u5C0F\u5170\u82B1\u5FC3\u4E2D\u96C0\u8DC3\uFF0C\u4E0D\u6562\u76F8\u4FE1\u8FD9\u4E00\u5E55\u3002\u679C\u7136\u4E00\u5207\u90FD\u662F\u5047\u7684\uFF0C\u957F\u73E9\u53EA\u4E0D\u8FC7\u662F\u5927\u5F3A\u5E7B\u5316\u7684\u3002\u6B64\u65F6\uFF0C\u6C34\u4E91\u5929\u6F31\u7389\u6797\uFF0C\u4ED9\u5973\u4E39\u97F3\u6B63\u5728\u4FEE\u70BC\u6CD5\u672F\uFF0C\u53EF\u5979\u5374\u63A7\u5236\u4E0D\u4F4F\u4F7F\u51FA\u6765\u7684\u6CD5\u529B\uFF0C\u81EA\u5DF1\u5012\u53D7\u4E86\u4F24\u3002\u4ED9\u5C0A\u6FA7\u6C85\u8D70\u4E0A\u524D\u6765\uFF0C\u6276\u8D77\u81EA\u5DF1\u7684\u5B9D\u8D1D\u5973\u513F\uFF0C\u6570\u843D\u5979\u4E3A\u4F55\u4E00\u5B9A\u8981\u53BB\u4F3A\u5019\u522B\u4EBA\u3002\u53EF\u4E39\u97F3\u5BF9\u957F\u73E9\u949F\u60C5\u5DF2\u4E45\uFF0C\u4EFB\u7236\u4EB2\u600E\u4E48\u529D\u8BF4\uFF0C\u5979\u4E5F\u4E00\u610F\u5B64\u884C\uFF0C\u4E0D\u80AF\u56DE\u5934\u3002</p><p>\u8FD9\u5929\uFF0C\u4E39\u97F3\u7A81\u7136\u6765\u5230\u53F8\u547D\u6BBF\uFF0C\u60F3\u8981\u770B\u770B\u81EA\u5DF1\u7684\u547D\u7C3F\u3002\u5C0F\u5170\u82B1\u606A\u5B88\u672C\u5206\uFF0C\u4E0D\u80AF\u4EA4\u51FA\u547D\u7C3F\u3002\u53EF\u5BF9\u65B9\u5374\u5A01\u80C1\u5979\uFF0C\u968F\u540E\u7ADF\u7834\u574F\u547D\u7C3F\u6811\u3002\u6811\u5E72\u4E0A\u5FFD\u7136\u73B0\u51FA\u4E00\u4E2A\u6F29\u6DA1\uFF0C\u4E39\u97F3\u4E0A\u524D\u67E5\u770B\uFF0C\u53D1\u73B0\u662F\u957F\u73E9\u7684\u5927\u5A5A\u753B\u9762\u3002\u4EBA\u95F4\u7684\u5927\u5A5A\u4E0A\uFF0C\u65B0\u5A18\u5E76\u975E\u4E39\u97F3\uFF0C\u6C34\u4E91\u5929\u4E0A\u7684\u5927\u5A5A\uFF0C\u65B0\u5A18\u4E5F\u4E0D\u662F\u5979\u3002\u518D\u5F80\u4E0B\u770B\uFF0C\u65B0\u5A18\u7684\u8138\u7EC8\u4E8E\u9732\u51FA\u3002\u53EF\u90A3\u5F20\u8138\u5206\u660E\u5C31\u662F\u8EAB\u65C1\u7684\u5C0F\u5170\u82B1\u3002\u4E39\u97F3\u8FC1\u6012\u4E8E\u5C0F\u5170\u82B1\uFF0C\u5C06\u5979\u72E0\u72E0\u63A8\u5012\u5728\u5730\u3002</p><p>\u4ED9\u8003\u5982\u7EA6\u800C\u81F3\uFF0C\u5C0F\u5170\u82B1\u59D7\u59D7\u6765\u8FDF\uFF0C\u6240\u5E78\u6CA1\u6709\u8FDF\u5230\u3002\u968F\u7740\u4ED9\u541B\u4E09\u751F\u7684\u4E00\u58F0\u201C\u8083\u9759\u201D\uFF0C\u4E91\u4E2D\u541B\u4E0E\u957F\u73E9\u4ED9\u541B\u767B\u573A\u3002\u53F0\u4E0B\u4F17\u4ED9\u5B50\u6324\u7834\u8111\u888B\u90FD\u60F3\u901A\u8FC7\u4ED9\u8003\uFF0C\u6210\u4E3A\u957F\u73E9\u8EAB\u8FB9\u7684\u4F8D\u5973\u3002\u53EF\u6B8A\u4E0D\u77E5\uFF0C\u53F0\u4E0A\u7684\u957F\u73E9\u5FC3\u4E2D\u5728\u60F3\u7740\u53E6\u4E00\u4E2A\u4EBA\u3002\u5F80\u65E5\u4E0E\u5C0F\u5170\u82B1\u76F8\u5904\u7684\u79CD\u79CD\u4E0D\u7531\u5F97\u6D6E\u73B0\u8111\u6D77\uFF0C\u968F\u7740\u4EEA\u5F0F\u5F00\u59CB\uFF0C\u4ED6\u7684\u601D\u7EEA\u624D\u6162\u6162\u62BD\u56DE\u6765\u3002</p><p>\u4F17\u4ED9\u5973\u4EEC\u82E5\u662F\u6536\u670D\u4E86\u6F31\u7389\u6797\u4E2D\u7684\u51F6\u517D\uFF0C\u4FBF\u7B97\u901A\u8FC7\u3002\u4ED9\u5973\u4EEC\u5174\u9AD8\u91C7\u70C8\u4E0A\u53F0\u63A5\u8FC7\u957F\u73E9\u9012\u6765\u7684\u7075\u73E0\uFF0C\u53EA\u662F\u4ED6\u7684\u773C\u7738\u4ECE\u672A\u62AC\u8D77\u8FC7\uFF0C\u76F4\u5230\u5C0F\u5170\u82B1\u4E0A\u53F0\uFF0C\u4ED6\u7684\u773C\u4E2D\u624D\u6709\u4E9B\u62C5\u5FE7\u3002\u4E39\u97F3\u672C\u60F3\u7B2C\u4E00\u4E2A\u8FDB\u5165\u6797\u5B50\uFF0C\u53EF\u5C0F\u5170\u82B1\u5374\u98DE\u5954\u4E0A\u524D\uFF0C\u62A2\u5148\u4E00\u6B65\u3002\u5C0F\u5170\u82B1\u4E5F\u4E0D\u77E5\u9053\u662F\u8C01\u63A8\u4E86\u5979\u4E00\u628A\uFF0C\u53EF\u4E8B\u5230\u5982\u4ECA\uFF0C\u4E5F\u53EA\u80FD\u786C\u7740\u5934\u76AE\u51B2\u5728\u4ED9\u5973\u4EEC\u7684\u524D\u9762\u3002\u4E0E\u6B64\u540C\u65F6\uFF0C\u4E09\u751F\u6325\u624B\uFF0C\u6797\u5B50\u4E2D\u7684\u753B\u9762\u4FBF\u663E\u73B0\u5728\u5927\u6BBF\u3002</p><p>\u867D\u7136\u505A\u8DB3\u4E86\u51C6\u5907\uFF0C\u4E5F\u5341\u5206\u60F3\u8981\u901A\u8FC7\u4ED9\u8003\uFF0C\u53EF\u5C0F\u5170\u82B1\u8FD8\u662F\u63A7\u5236\u4E0D\u4F4F\u7684\u5BB3\u6015\u3002\u8D70\u7740\u8D70\u7740\uFF0C\u5C31\u8D70\u5230\u4E86\u4E00\u5904\u6CA1\u4EBA\u7684\u5730\u65B9\u3002\u53EF\u4E0D\u77E5\u662F\u4E0D\u662F\u592A\u8FC7\u5BB3\u6015\uFF0C\u5C0F\u5170\u82B1\u7ADF\u6CA1\u6709\u53D1\u73B0\u811A\u4E0B\u7684\u51F6\u517D\u3002\u4E1C\u65B9\u9752\u82CD\u548C\u6B87\u9619\u9707\u60CA\u53C8\u65E0\u5948\uFF0C\u751A\u81F3\u6709\u4E9B\u607C\u6012\u3002\u51E0\u7ECF\u5468\u6298\uFF0C\u5C0F\u5170\u82B1\u7EC8\u4E8E\u53D1\u73B0\u4E86\u90A3\u51F6\u517D\u3002\u53EF\u5979\u5B9E\u5728\u592A\u5C0F\u5FC3\u7FFC\u7FFC\uFF0C\u521A\u4E00\u62AC\u624B\uFF0C\u90A3\u51F6\u517D\u5C31\u8DD1\u8FDC\u4E86\u3002</p><p>\u5C0F\u5170\u82B1\u7EC8\u4E8E\u8FFD\u4E0A\u51F6\u517D\u82F1\u62DB\uFF0C\u8FD8\u7ED9\u5B83\u62FF\u4E86\u4ED9\u8349\uFF0C\u60F3\u8981\u5728\u5B83\u653E\u677E\u8B66\u60D5\u7684\u65F6\u5019\u6293\u4E86\u3002\u90FD\u8BF4\u82F1\u62DB\u5341\u5206\u8B66\u60D5\uFF0C\u53EA\u76F8\u4FE1\u751F\u6027\u7EAF\u826F\u4E4B\u4EBA\u3002\u6BBF\u4E0A\u7684\u957F\u73E9\u89C1\u72B6\uFF0C\u4E0D\u7531\u5F97\u66FF\u5C0F\u5170\u82B1\u8BF4\u8BDD\uFF0C\u53EF\u5374\u906D\u5230\u4E86\u4E91\u4E2D\u541B\u7684\u773C\u795E\u8B66\u544A\u3002\u5C31\u5728\u8FD9\u65F6\uFF0C\u51E0\u4E2A\u4ED9\u5973\u7A81\u7136\u56F4\u4F4F\u5C0F\u5170\u82B1\uFF0C\u8981\u628A\u82F1\u62DB\u62A2\u8D70\u3002\u4F17\u4EBA\u65BD\u6CD5\u5BF9\u5C0F\u5170\u82B1\u52A8\u624B\uFF0C\u53EF\u8C01\u77E5\u5979\u7684\u6CD5\u529B\u7ADF\u80FD\u591F\u62B5\u6297\uFF0C\u751A\u81F3\u8FD8\u80FD\u5C06\u5979\u4EEC\u51FB\u5012\u3002</p><p>\u8776\u8863\u6084\u6084\u8D76\u6765\uFF0C\u4E1C\u65B9\u9752\u82CD\u5BDF\u89C9\u795F\u6C14\uFF0C\u53EE\u5631\u6B87\u9619\u7167\u770B\u597D\u5C0F\u5170\u82B1\u540E\uFF0C\u4FBF\u5FAA\u7740\u795F\u6C14\u800C\u53BB\u3002\u53EF\u8776\u8863\u5DF2\u7ECF\u9760\u8FD1\u5C0F\u5170\u82B1\uFF0C\u8FD8\u6253\u7834\u4E86\u4E09\u751F\u7684\u6CD5\u672F\uFF0C\u5C06\u795F\u6C14\u6CE8\u5165\u82F1\u62DB\u4F53\u5185\u3002\u6E29\u987A\u4E56\u5DE7\u7684\u82F1\u62DB\u5FFD\u7136\u4E4B\u95F4\u5C31\u53D8\u5F97\u53EF\u6015\u5F02\u5E38\uFF0C\u5F20\u7740\u8840\u76C6\u5927\u53E3\u8FFD\u7740\u5C0F\u5170\u82B1\u3002\u957F\u73E9\u5B9E\u5728\u62C5\u5FE7\uFF0C\u60F3\u8981\u8D77\u8EAB\u53BB\u5BFB\u5C0F\u5170\u82B1\uFF0C\u53EF\u5374\u88AB\u5BB9\u660A\u62E6\u4F4F\u3002\u5343\u94A7\u4E00\u53D1\u4E4B\u9645\uFF0C\u6B87\u9619\u53CA\u65F6\u51FA\u73B0\uFF0C\u62E6\u4E0B\u4E86\u82F1\u62DB\u3002</p><p>\u5176\u4ED6\u4ED9\u5973\u4E5F\u9047\u5230\u4E86\u53D1\u72C2\u7684\u82F1\u62DB\uFF0C\u4E39\u97F3\u51FA\u624B\u6536\u670D\u53EF\u5374\u88AB\u51FB\u5012\u5728\u5730\u3002\u5C0F\u5170\u82B1\u7A81\u7136\u51FA\u73B0\uFF0C\u6551\u4E0B\u4E39\u97F3\uFF0C\u968F\u540E\u8BD5\u56FE\u6536\u670D\u82F1\u62DB\u3002\u4F17\u4ED9\u5973\u89C1\u72B6\u4E5F\u4E0A\u524D\u5E2E\u5FD9\uFF0C\u53EF\u4ECD\u65E7\u4E0D\u662F\u82F1\u62DB\u7684\u5BF9\u624B\u3002\u5C0F\u5170\u82B1\u77A7\u89C1\u4E86\u4E00\u5904\u6D1E\u7A74\uFF0C\u8D76\u5FD9\u5E26\u7740\u5927\u5BB6\u8EB2\u4E86\u8FDB\u53BB\u3002\u773C\u4E0B\u53EA\u6709\u5C06\u5927\u5BB6\u7684\u4ED9\u73E0\u805A\u96C6\u8D77\u6765\uFF0C\u624D\u6709\u53EF\u80FD\u62B5\u6321\u82F1\u62DB\u3002\u5927\u5BB6\u72B9\u8C6B\u7247\u523B\uFF0C\u8FD8\u662F\u51B3\u5B9A\u62FC\u4E00\u62FC\u3002</p><p>\u5C0F\u5170\u82B1\u5E26\u7740\u5927\u5BB6\u7684\u4ED9\u73E0\u5C0F\u5FC3\u7FFC\u7FFC\u6324\u5230\u5916\u9762\uFF0C\u54ED\u7740\u4E3E\u8D77\u53D7\u4F24\u7684\u624B\uFF0C\u66FF\u4ED9\u5973\u4EEC\u5F15\u5F00\u4E86\u82F1\u62DB\u3002\u4E39\u97F3\u5E26\u7740\u4ED9\u5973\u4EEC\u8FD4\u56DE\u5927\u6BBF\uFF0C\u5C06\u6B64\u4E8B\u6C47\u62A5\u3002\u957F\u73E9\u518D\u4E5F\u5750\u4E0D\u4F4F\uFF0C\u8D76\u5FD9\u8D77\u8EAB\u524D\u5F80\u6F31\u7389\u6797\u3002</p>',13),r=[o];function _(n,s){return e(),t("div",null,r)}const h=p(c,[["render",_],["__file","6.html.vue"]]);export{h as default};
