import{_ as p}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as e,c as t,f as a}from"./app.1226039b.js";const c={},o=a('<h1 id="\u7B2C6\u96C6-\u7389\u73E0\u6C5F\u6CB3\u62CD\u7ED3\u5A5A\u7167" tabindex="-1"><a class="header-anchor" href="#\u7B2C6\u96C6-\u7389\u73E0\u6C5F\u6CB3\u62CD\u7ED3\u5A5A\u7167" aria-hidden="true">#</a> \u7B2C6\u96C6\uFF1A\u7389\u73E0\u6C5F\u6CB3\u62CD\u7ED3\u5A5A\u7167</h1><p>\u9648\u6C5F\u6CB3\u4E58\u673A\u6559\u80B2\u51E0\u4E2A\u5C0F\u5B50\uFF0C\u505A\u751F\u610F\u8981\u52A8\u8111\u5B50\u3002\u539F\u6765\u8FD9\u4E9B\u732A\u76AE\u662F\u7528\u6765\u505A\u732A\u76AE\u624B\u5957\u7528\u7684\u3002</p><p>\u8001\u4EBA\u4EEC\u7EB7\u7EB7\u611F\u53F9\u65F6\u4EE3\u4E0D\u540C\u4E86\uFF0C\u9E21\u6BDB\u6362\u7CD6\u4E5F\u8D5A\u4E0D\u4E86\u51E0\u4E2A\u94B1\u3002\u6CA1\u6709\u6C5F\u6CB3\u5E26\u5927\u5BB6\u6536\u5927\u9EA6\u8D5A\u7684\u591A\u3002\u9648\u91D1\u6C34\u5374\u4F9D\u7136\u56FA\u6267\u5DF1\u89C1\uFF0C\u8BA4\u4E3A\u9E21\u6BDB\u6362\u7CD6\u624D\u662F\u6B63\u7ECF\u751F\u610F\u3002\u9648\u91D1\u6C34\u53D1\u73B0\u5E74\u8F7B\u4EBA\u6CA1\u6709\u56DE\u6765\uFF0C\u51FA\u95E8\u770B\u89C1\u9648\u5927\u5149\uFF0C\u95EE\u4ED6\u4EEC\u53BB\u4E86\u54EA\u91CC\u3002\u5927\u5149\u8BF4\u4ED6\u4EEC\u8D70\u8FDC\u8DEF\u6362\u7CD6\u53BB\u4E86\u3002\u9648\u91D1\u6C34\u770B\u89C1\u6241\u62C5\u91CC\u7684\u732A\u76AE\u624B\u5957\u3002\u4E00\u773C\u770B\u7A7F\u4E86\u4ED6\u4EEC\u7684\u5047\u8BDD\u3002\u9648\u91D1\u6C34\u5341\u5206\u751F\u6C14\u3002</p><p>\u6C5F\u6CB3\u548C\u7389\u73E0\u804A\u5929\uFF0C\u8BF4\u4ED6\u53D1\u73B0\u4E86\u6BD4\u889C\u5B50\u66F4\u8D5A\u94B1\u7684\u751F\u610F\uFF0C\u672C\u60F3\u5728\u7389\u73E0\u9762\u524D\u4E70\u4E2A\u5173\u5B50\u3002\u7389\u73E0\u5374\u4E00\u773C\u770B\u7A7F\u4ED6\u662F\u60F3\u505A\u732A\u76AE\u624B\u5957\u7684\u751F\u610F\u3002\u6C5F\u6CB3\u5927\u547C\u4E0D\u8FC7\u763E\u3002\u6C5F\u6CB3\u672C\u4EE5\u4E3A\u7389\u73E0\u4F1A\u5BF9\u624B\u5957\u751F\u610F\u611F\u5174\u8DA3\u3002\u6CA1\u60F3\u5230\u4E00\u5411\u63D0\u5230\u8D5A\u94B1\u751F\u610F\u5C31\u4E24\u773C\u653E\u5149\u7684\u7389\u73E0\u5374\u6CA1\u6253\u7B97\u63D2\u624B\u3002\u539F\u6765\u7389\u73E0\u60F3\u628A\u8FD9\u95E8\u751F\u610F\u7559\u7ED9\u5927\u5149\u4ED6\u4EEC\uFF0C\u7ED9\u4ED6\u4EEC\u7559\u53E3\u996D\u5403\u3002\u6C5F\u6CB3\u5341\u5206\u611F\u52A8\u3002</p><p>\u7389\u73E0\u95EE\u6C5F\u6CB3\u7684\u8863\u670D\u662F\u8C01\u6D17\u7684\uFF0C\u6C5F\u6CB3\u541E\u541E\u5410\u5410\u4E0D\u6562\u56DE\u7B54\u3002\u7389\u73E0\u5FC3\u91CC\u660E\u767D\uFF0C\u544A\u8BC9\u9648\u6C5F\u6CB3\u4EE5\u540E\u4ED6\u7684\u8863\u670D\u90FD\u8BA9\u5979\u6D17\u3002</p><p>\u7389\u73E0\u5E26\u6C5F\u6CB3\u5750\u4E0A\u4E86\u591C\u73ED\u706B\u8F66\u53BB\u8FDB\u8D27\uFF0C\u539F\u6765\u5979\u5C0F\u65F6\u5019\u5C31\u4F4F\u5728\u889C\u5382\u65C1\u8FB9\u3002\u7389\u73E0\u8FC7\u4E16\u7684\u6BCD\u4EB2\u5F53\u5E74\u5C31\u5728\u889C\u5382\u5DE5\u4F5C\u3002</p><p>\u9648\u5927\u5149\u548C\u5DE7\u59D1\u591C\u91CC\u5077\u5077\u76F8\u4F1A\u3002\u6CA1\u60F3\u5230\u521A\u6CA1\u8BF4\u4E24\u53E5\u8BDD\uFF0C\u5C31\u88AB\u9648\u91D1\u6C34\u6293\u4E2A\u6B63\u7740\u3002\u9648\u91D1\u6C34\u5230\u5904\u5BFB\u627E\u9648\u6C5F\u6CB3\uFF0C\u7535\u8BDD\u6253\u5230\u4E86\u90B1\u82F1\u6770\u90A3\u91CC\u3002\u90B1\u82F1\u6770\u652F\u652F\u543E\u543E\u5C31\u662F\u4E0D\u8BA9\u9648\u6C5F\u6CB3\u4E0B\u6765\u63A5\u7535\u8BDD\u3002\u9648\u91D1\u6C34\u5FC3\u91CC\u6709\u6570\uFF0C\u5341\u5206\u7A9D\u706B\u3002\u8BF4\u4ECA\u5E74\u5C31\u8981\u628A\u5DE7\u59D1\u548C\u6C5F\u6CB3\u7684\u5A5A\u4E8B\u7ED9\u529E\u4E86\u3002\u5DE7\u59D1\u75DB\u54ED\u54C0\u6C42\uFF0C\u9648\u91D1\u6C34\u5374\u4E0D\u4E3A\u6240\u52A8\u3002\u9648\u5927\u5149\u5728\u95E8\u5916\u542C\u5230\u4E86\u6240\u6709\u7684\u8BDD\u3002\u6014\u6014\u5730\u770B\u4E86\u5DE7\u59D1\u4E00\u773C\u3002\u8F6C\u8EAB\u5C31\u8D70\u3002\u5DE7\u59D1\u60F3\u8981\u53BB\u8FFD\u3002\u5374\u88AB\u9648\u91D1\u6C34\u62E6\u4F4F\u3002</p><p>\u7B2C\u4E8C\u5929\u4E00\u5927\u65E9\uFF0C\u6C5F\u6CB3\u548C\u7389\u73E0\u6765\u5230\u889C\u5382\u8FDB\u8D27\uFF0C\u6CA1\u60F3\u5230\u889C\u5B50\u5DF2\u7ECF\u5168\u90E8\u5356\u5B8C\u4E86\uFF0C\u53EA\u5269\u4E0B\u6700\u540E\u51E0\u888B\u5F0F\u7559\u7ED9\u5382\u957F\u4EB2\u621A\u7684\u3002\u7389\u73E0\u5341\u5206\u61CA\u607C\u3002\u4ECA\u5929\u662F\u889C\u5382\u51FA\u65B0\u6B3E\u7684\u65E5\u5B50\uFF0C\u80AF\u5B9A\u7279\u522B\u597D\u5356\uFF0C\u5979\u5DF2\u7ECF\u76FC\u4E86\u597D\u591A\u5929\u4E86\u3002\u7389\u73E0\u773C\u73E0\u4E00\u8F6C\uFF0C\u8BA1\u4E0A\u5FC3\u6765\u3002</p><p>\u4E8C\u4EBA\u7FFB\u5899\u8FDB\u5165\u889C\u5382\uFF0C\u6C5F\u6CB3\u4E0D\u89E3\uFF0C\u53EA\u6709\u8DDF\u4E0A\u3002\u539F\u6765\u7389\u73E0\u5047\u5192\u4E86\u5382\u957F\u4E8C\u59D1\uFF0C\u63D0\u8D70\u4E86\u90A3\u51E0\u888B\u889C\u5B50\uFF0C\u6CA1\u60F3\u5230\u5374\u8FCE\u9762\u9047\u5230\u4E86\u90D1\u5382\u957F\u3002\u4E24\u4EBA\u4E00\u6123\uFF0C\u6C5F\u6CB3\u8131\u53E3\u800C\u51FA\uFF1A\u6211\u662F\u4F60\u4E8C\u59D1\u7236\u3002\u6B63\u5728\u5927\u5BB6\u6123\u795E\u65F6\uFF0C\u4E24\u4EBA\u625B\u8D77\u9EBB\u888B\u5C31\u8DD1\u3002</p><p>\u8DEF\u4E0A\uFF0C\u7389\u73E0\u8981\u6C5F\u6CB3\u966A\u5979\u53BB\u7167\u76F8\u9986\u7167\u5F20\u50CF\uFF0C\u56DE\u5BB6\u8FD8\u8981\u4E0B\u53A8\u505A\u597D\u5403\u7684\u3002\u6C5F\u6CB3\u4E0D\u77E5\u9053\u662F\u4EC0\u4E48\u4ECA\u5929\u662F\u4EC0\u4E48\u597D\u65E5\u5B50\u3002\u7389\u73E0\u4F4E\u5934\u8FDE\u558A\u4E09\u58F0\u201C\u4ED6\u4E8C\u59D1\u7236\u201D\uFF0C\u6C5F\u6CB3\u4E86\u7136\u3002\u5728\u7389\u73E0\u8033\u8FB9\u5E94\u4E86\u58F0\u201C\u8BF6\u201D\u3002\u4E24\u4EBA\u76F8\u89C6\u4E00\u7B11\u3002</p><p>\u53BF\u59D4\u529E\u516C\u697C\uFF0C\u90B1\u82F1\u6770\u56E0\u4E3A\u5411\u9648\u6C5F\u6CB3\u4E70\u5927\u9EA6\u5356\u7ED9\u4E61\u517B\u6B96\u573A\u7684\u4E8B\u60C5\uFF0C\u88AB\u601D\u60F3\u987D\u56FA\u7684\u8001\u9886\u5BFC\u4E25\u5389\u7684\u6279\u8BC4\u3002\u8BF4\u4ED6\u662F\u6295\u673A\u5012\u628A\uFF0C\u6316\u793E\u4F1A\u4E3B\u4E49\u5899\u89D2\u3002\u90B1\u82F1\u6770\u6709\u7406\u8BF4\u4E0D\u6E05\uFF0C\u53EA\u8BF4\u7B49\u8C22\u4E66\u8BB0\u56DE\u6765\u3002\u6CA1\u60F3\u5230\u5374\u542C\u5230\u80CC\u540E\u6709\u4EBA\u8BAE\u8BBA\u8BF4\u4ED6\u662F\u7ED9\u8C22\u4E66\u8BB0\u6DFB\u9EBB\u70E6\uFF0C\u66F4\u6709\u751A\u8005\u8BF4\u4ED6\u662F\u62FF\u4E86\u56DE\u6263\u3002\u90B1\u82F1\u6770\u767E\u53E3\u83AB\u8FA9\u3002</p><p>\u7389\u73E0\u548C\u6C5F\u6CB3\u4E00\u8D77\u6765\u5230\u5E02\u573A\u4E0A\u5356\u889C\u5B50\u3002\u7389\u73E0\u6559\u6C5F\u6CB3\u5406\u559D\u3002\u770B\u5230\u5FC3\u60C5\u90C1\u95F7\u7684\u90B1\u82F1\u6770\uFF0C\u60F3\u8981\u95EE\u95EE\u600E\u4E48\u56DE\u4E8B\uFF0C\u5374\u88AB\u90B1\u82F1\u6770\u51E0\u53E5\u8BDD\u5E26\u8FC7\u3002\u6CA1\u60F3\u5230\u9648\u91D1\u6C34\u6709\u5E26\u7740\u6751\u91CC\u7684\u5E74\u8F7B\u4EBA\u4EEC\u627E\u5230\u7389\u73E0\u7684\u644A\u5B50\u524D\u9762\u6765\u3002\u9648\u91D1\u6C34\u5F53\u4F17\u903C\u7740\u9648\u6C5F\u6CB3\u7B54\u5E94\u5A36\u5DE7\u59D1\u4E3A\u59BB\uFF0C\u8FD8\u8FB1\u9A82\u7389\u73E0\u662F\u6765\u7978\u5BB3\u9648\u5BB6\u6751\u7684\u9B54\u9B3C\u5996\u7CBE\u3002\u9648\u6C5F\u6CB3\u4E3A\u4E86\u7EF4\u62A4\u7389\u73E0\uFF0C\u5F53\u4F17\u627F\u8BA4\u7389\u73E0\u662F\u4ED6\u7684\u5973\u4EBA\u3002\u4ED6\u53EA\u662F\u62FF\u5DE7\u59D1\u5F53\u59B9\u59B9\u3002\u9648\u91D1\u6C34\u6C14\u6025\u653B\u5FC3\uFF0C\u4E00\u4E0B\u5B50\u6655\u5012\u5728\u5730\u3002\u4F17\u4EBA\u8D76\u5FD9\u5C06\u4ED6\u9001\u5230\u533B\u9662\u3002</p><p>\u7389\u73E0\u8F6C\u5934\uFF0C\u5C45\u7136\u5728\u56F4\u89C2\u7684\u4EBA\u7FA4\u4E2D\uFF0C\u770B\u5230\u4E86\u4E00\u4E2A\u719F\u6089\u7684\u4EBA\uFF0C\u8F6C\u8EAB\u8FFD\u4E86\u51FA\u53BB\u3002\u90A3\u4EBA\u7ADF\u662F\u7389\u73E0\u55DC\u8D4C\u6210\u6027\u7684\u7236\u4EB2\u3002\u82B1\u5149\u4E86\u7389\u73E0\u7ED9\u4ED6\u7684\u94B1\uFF0C\u53C8\u6765\u627E\u7389\u73E0\u3002\u7389\u73E0\u7236\u4EB2\u8BF4\u8D77\u4ECA\u5929\u662F\u7389\u73E0\u7684\u751F\u65E5\uFF0C\u60F3\u8981\u611F\u52A8\u7389\u73E0\u3002\u4F46\u7389\u73E0\u60F3\u8D77\u5F80\u4E8B\uFF0C\u5B9E\u5728\u662F\u5FC3\u7070\u610F\u51B7\u3002\u8F6C\u8EAB\u79BB\u5F00\u3002</p><p>\u9648\u6C5F\u6CB3\u5230\u4E86\u533B\u9662\u624D\u77E5\u9053\uFF0C\u9648\u91D1\u6C34\u5F53\u5E74\u4E3A\u4E86\u4ECE\u5B66\u4E60\u73ED\u79BB\u5F00\uFF0C\u4E0D\u60DC\u62FF\u5934\u649E\u5899\uFF0C\u8FD9\u624D\u7559\u4E0B\u4E86\u4E2D\u98CE\u7684\u75C5\u6839\u513F\uFF0C\u4ECA\u5929\u6536\u5230\u4E86\u523A\u6FC0\uFF0C\u518D\u6B21\u590D\u53D1\u3002\u6C5F\u6CB3\u60F3\u5230\u91D1\u6C34\u53D4\u8FD9\u75C5\u7684\u6765\u5386\uFF0C\u5FC3\u4E2D\u4E94\u5473\u9648\u6742\u3002</p><p>\u7389\u73E0\u773C\u770B\u91D1\u6C34\u75C5\u5012\uFF0C\u5FC3\u91CC\u5341\u5206\u4E0D\u5B89\u3002\u5B88\u5728\u533B\u9662\u95E8\u53E3\uFF0C\u6C5F\u6CB3\u8BA9\u5979\u5148\u56DE\u53BB\u3002\u665A\u4E0A\uFF0C\u6C5F\u6CB3\u6765\u5230\u7389\u73E0\u5BB6\uFF0C\u4EA4\u7ED9\u7389\u73E0\u4E00\u5927\u5305\u4E1C\u897F\uFF0C\u7ADF\u7136\u662F\u4E4B\u524D\u5356\u9972\u6599\u6362\u56DE\u6765\u7684\u7CAE\u7968\u3002\u4ED6\u4EBA\u5728\u533B\u9662\uFF0C\u4E0D\u653E\u5FC3\u8FD9\u7B14\u5DE8\u6B3E\u3002\u4E8E\u662F\u4EA4\u5230\u7389\u73E0\u624B\u91CC\uFF0C\u8981\u5979\u4EE3\u4E3A\u4FDD\u7BA1\u3002\u7389\u73E0\u5FC3\u4E2D\u60F6\u6050\uFF0C\u5BB3\u6015\u6C5F\u6CB3\u8981\u79BB\u5F00\u5979\uFF0C\u903C\u6C5F\u6CB3\u53D1\u8A93\u8C01\u90FD\u4E0D\u80FD\u5206\u5F00\u4ED6\u4EEC\u3002\u7389\u73E0\u5C06\u5979\u7684\u540A\u5760\u9001\u7ED9\u4E86\u6C5F\u6CB3\uFF0C\u8BF4\u76F8\u4FE1\u4ED6\u4E00\u8F88\u5B50\u3002</p><p>\u6C5F\u6CB3\u521A\u8D70\uFF0C\u53C8\u6709\u4EBA\u6765\u6572\u95E8\uFF0C\u6CA1\u60F3\u5230\u5374\u662F\u7389\u73E0\u7684\u8D4C\u9B3C\u7236\u4EB2\u3002\u7389\u73E0\u672C\u4E0D\u613F\u7406\u4ED6\uFF0C\u65E0\u5948\u7389\u73E0\u7236\u4EB2\u82E6\u82E6\u54C0\u6C42\u3002\u8BF4\u53EA\u4F4F\u4E00\u665A\u3002\u7389\u73E0\u6700\u7EC8\u653E\u4ED6\u8FDB\u4E86\u95E8\u3002\u4E00\u8FDB\u95E8\u7389\u73E0\u8D76\u7D27\u6536\u597D\u5305\u88F9\uFF0C\u6CA1\u60F3\u5230\u5374\u88AB\u7389\u73E0\u7239\u770B\u5728\u773C\u770B\uFF0C\u8BB0\u5728\u5FC3\u91CC\u3002\u7389\u73E0\u7239\u65E0\u610F\u95F4\u63D0\u8D77\u7389\u73E0\u8FC7\u4E16\u7684\u6BCD\u4EB2\u3002\u7389\u73E0\u7EC8\u4E8E\u5FCD\u8010\u4E0D\u4F4F\uFF0C\u75DB\u54ED\u5931\u58F0\u3002</p>',16),r=[o];function _(n,s){return e(),t("div",null,r)}const h=p(c,[["render",_],["__file","6.html.vue"]]);export{h as default};
