import{_ as p}from"./_plugin-vue_export-helper.cdc0426e.js";import{o,c as t,f as r}from"./app.cd90e563.js";const e={},n=r('<h1 id="\u664B\u7EAA\u516D" tabindex="-1"><a class="header-anchor" href="#\u664B\u7EAA\u516D" aria-hidden="true">#</a> \u664B\u7EAA\u516D</h1><blockquote><p>\u516D\u6708\uFF0C\u4E59\u536F\uFF0C\u9F50\u738B\u518F\u5E05\u4F17\u5165\u6D1B\u9633\uFF0C\u987F\u519B\u901A\u7AE0\u7F72\uFF0C\u7532\u58EB\u6570\u5341\u4E07\uFF0C\u5A01\u9707\u4EAC\u90FD\u3002</p></blockquote><p><strong>\u300A\u8D44\u6CBB\u901A\u9274\u2022\u5377\u516B\u5341\u56DB\u2022\u664B\u7EAA\u516D\u300B</strong></p><blockquote><p>\u897F\u664B\u7684\u516B\u738B\u4E4B\u4E71\uFF0C\u524D\u540E\u6301\u7EED\u8FD1\u4E8C\u5341\u5E74\uFF0C\u5927\u4F53\u4E0A\u53EF\u4EE5\u5206\u4E3A\u4E09\u4E2A\u9636\u6BB5\u3002</p></blockquote><p>\u7B2C\u4E00\u4E2A\u9636\u6BB5\u4EE5\u664B\u6B66\u5E1D\u7559\u4E0B\u7684\u8F85\u653F\u73ED\u5E95\u88AB\u6E05\u6D17\uFF0C\u8D3E\u540E\u767B\u53F0\u6267\u653F\u4E3A\u6536\u573A\u3002\u6B64\u540E\uFF0C\u5929\u4E0B\u5927\u7EA6\u7EF4\u6301\u4E86\u4E03\u516B\u5E74\u7684\u548C\u5E73\u3002</p><p>\u7B2C\u4E8C\u4E2A\u9636\u6BB5\u5219\u8D77\u4E8E\u8D3E\u540E\u5BF9\u592A\u5B50\u7684\u75DB\u4E0B\u6740\u624B\u3002\u4ECA\u5929\uFF0C\u6211\u4EEC\u5C31\u6765\u804A\u804A\u8FD9\u90E8\u5206\u6545\u4E8B\uFF0C\u770B\u770B\u4E71\u4E16\u7684\u201C\u4E71\u201D\u7A76\u7ADF\u662F\u4EE5\u4E00\u79CD\u4EC0\u4E48\u6837\u7684\u65B9\u5F0F\u5448\u73B0\uFF0C\u4EE5\u53CA\u90A3\u4E9B\u5728\u4E71\u4E16\u4E2D\u8D81\u673A\u53D6\u5229\u7684\u4EBA\uFF0C\u6700\u7EC8\u7ED3\u679C\u53C8\u5982\u4F55\u3002</p><p>\u5927\u6DF7\u4E71\u7684\u964D\u4E34\uFF0C\u90FD\u662F\u4ECE\u5C0F\u4EBA\u7269\u5F00\u59CB\u7684\u3002\u6700\u5F00\u59CB\u6311\u4E71\u5C40\u9762\u7684\uFF0C\u662F\u4E00\u4E2A\u53EB<code>\u8D3E\u8C27</code>\u7684\u4EBA\u3002</p><p>\u8D3E\u8C27\u662F\u7687\u540E\u8D3E\u5357\u98CE\u7684\u4F84\u5B50\u3002\u8FD9\u79CD\u8859\u5185\u7684\u552F\u4E00\u7279\u5F81\uFF0C\u5C31\u662F\u56A3\u5F20\u8DCB\u6248\u3002\u800C\u8FD9\u4E2A\u8D3E\u8C27\u53C8\u8981\u72C2\u5F97\u66F4\u8FC7\u5206\u4E00\u70B9\uFF0C\u5C45\u7136\u5C31\u5728\u592A\u5B50\u9762\u524D\u65E0\u793C\u3002</p><p>\u8FD9\u4E00\u4E3E\u52A8\u906D\u5230\u4E86\u592A\u5B50\u7684\u4EB2\u53D4\u53D4\u3001\u6210\u90FD\u738B<code>\u53F8\u9A6C\u9896</code>\u5F53\u573A\u559D\u6B62\uFF0C\u7136\u800C\u8D3E\u540E\u4E13\u6743\uFF0C\u53F8\u9A6C\u9896\u867D\u7136\u8D35\u4E3A\u5F53\u4ECA\u7687\u5E1D\u7684\u4EB2\u5F1F\u5F1F\uFF0C\u4E5F\u5F88\u5FEB\u906D\u5230\u62A5\u590D\uFF1A\u4E0D\u4E45\u5C31\u88AB\u8D76\u51FA\u4E86\u671D\u5EF7\uFF0C\u201C<strong>\u51FA\u9896\u4E3A\u5E73\u5317\u5C06\u519B\uFF0C\u9547\u9134\uFF0C\u8F6C\u9547\u5317\u5927\u5C06\u519B</strong>\u201D\u3002</p><p>\u8FD9\u662F\u975E\u5E38\u5178\u578B\u7684\u8D2C\u5B98\uFF0C\u867D\u7136\u90BA\u57CE\u4E5F\u662F\u7E41\u534E\u4E4B\u5730\uFF0C\u4F46\u4ECE\u9996\u90FD\u6D1B\u9633\u88AB\u8D76\u51FA\u6765\uFF0C\u660E\u663E\u5E26\u7740\u5916\u653E\u7684\u610F\u5473\u3002\u53EA\u4E0D\u8FC7\u53F8\u9A6C\u9896\u6BD5\u7ADF\u662F\u7687\u5E1D\u7684\u4EB2\u5F1F\u5F1F\uFF0C\u5373\u4FBF\u662F\u88AB\u8D76\u51FA\u4E86\u4EAC\u57CE\uFF0C\u7EF4\u6301\u4F53\u9762\u7684\u804C\u4F4D\u4E5F\u8FD8\u662F\u8BE5\u6709\u7684\uFF0C\u56E0\u6B64\uFF0C\u671D\u5EF7\u624D\u4F1A\u7ED9\u4E86\u4E00\u4E2A\u770B\u8D77\u6765\u5F88\u91CD\u8981\u7684\u201C\u90FD\u7763\u90BA\u57CE\u519B\u4E8B\u201D\u3002</p><p>\u5728\u5F53\u65F6\u7684\u8BED\u5883\u4E0B\uFF0C\u4F60\u53EF\u4EE5\u628A\u5B83\u7406\u89E3\u4E3A\u201C<strong>\u5317\u90E8\u6218\u533A\u53F8\u4EE4</strong>\u201D\u3002\u5728\u548C\u5E73\u5E74\u4EE3\uFF0C\u8FD9\u4E2A\u5934\u8854\u4E0D\u8FC7\u662F\u4E00\u5C42\u5149\u9C9C\u7684\u5916\u8863\uFF0C\u542C\u8D77\u6765\u597D\u542C\u800C\u5DF2\u3002</p><p><strong>\u53EA\u6709\u8EAB\u5728\u540E\u4E16\u7684\u6211\u4EEC\u77E5\u9053\uFF0C\u4E71\u4E16\u5C06\u81F3</strong>\u3002</p><p>\u9634\u5DEE\u9633\u9519\u4E4B\u95F4\uFF0C\u8D3E\u8C27\u7684\u8BCB\u6BC1\u7ADF\u7136\u7ED9\u4E86\u53F8\u9A6C\u9896\u4E71\u4E16\u4E89\u96C4\u7684\u6700\u5927\u8D44\u672C\u3002</p><p>\u800C\u8FD9\u53EA\u662F\u8D3E\u8C27\u7ED9\u4E71\u4E16\u5E26\u6765\u7684\u4E00\u4E2A\u5C0F\u63D2\u66F2\u3002</p><p>\u771F\u6B63\u8BA9\u4E71\u4E16\u5F00\u542F\u7684\uFF0C\u662F\u8D3E\u8C27\u5728\u8D3E\u540E\u9762\u524D\u7684\u4E00\u756A\u8C17\u8A00\u3002\u8FD9\u4E9B\u8BDD\uFF0C\u8BA9\u8D3E\u540E\u505A\u51FA\u4E86\u5E9F\u9664\u592A\u5B50<code>\u53F8\u9A6C\u9079</code>\u7684\u4E3E\u52A8\u3002</p><p>\u4E8E\u8D3E\u540E\u800C\u8A00\uFF0C\u5E9F\u9664\u5E76\u5F04\u6B7B\u592A\u5B50\u4E0D\u8FC7\u662F\u4E3E\u624B\u4E4B\u52B3\u3002\u4F46\u8FD9\u5374\u8BA9\u8D3E\u540E\u4EA7\u751F\u4E86\u4E00\u4E2A\u5F88\u5927\u7684\u8BEF\u4F1A\uFF1A\u6700\u5927\u7684\u5A01\u80C1\u5DF2\u88AB\u6D88\u9664\uFF0C\u4EE5\u540E\u53EF\u4EE5\u9AD8\u6795\u65E0\u5FE7\u4E86\u3002</p><p><strong>\u5979\u5FD8\u4E86\u4E1C\u5BAB\u8FD8\u6709\u4E00\u652F\u4E0A\u4E07\u4EBA\u7684\u519B\u961F</strong>\u3002</p><p>\u592A\u5B50\u53F8\u9A6C\u9079\u5F53\u5E74\u7531\u664B\u6B66\u5E1D\u53F8\u9A6C\u708E\u4EB2\u81EA\u9009\u5B9A\u3002\u8FD9\u4E48\u591A\u5E74\u6765\uFF0C\u5927\u664B\u671D\u7684\u653F\u6CBB\u6597\u4E89\u867D\u7136\u4E00\u518D\u7A81\u7834\u4E0B\u9650\uFF0C\u4F46\u201C\u592A\u5B50\u662F\u672A\u6765\u7684\u5929\u4E0B\u4E4B\u4E3B\u201D\u8FD9\u4E00\u5171\u8BC6\u5374\u4E3A\u5404\u65B9\u6240\u63A5\u53D7\u3002</p><p>\u671D\u5EF7\u51FA\u4E8E\u5BF9\u592A\u5B50\u5B89\u5371\u7684\u62C5\u5FC3\uFF0C\u4E00\u76F4\u5728\u4E0D\u65AD\u52A0\u5F3A\u4E1C\u5BAB\u7684\u5B89\u4FDD\u529B\u91CF\u3002\u51E0\u8F6E\u6597\u4E89\u4E0B\u6765\u540E\u3002\u592A\u5B50\u4E00\u7CFB\u4E0D\u4EC5\u672A\u53D7\u7275\u8FDE\uFF0C\u53CD\u800C\u8FD8\u6210\u4E86\u6D1B\u9633\u57CE\u91CC\u4E00\u652F\u4E0D\u53EF\u5FFD\u89C6\u7684\u91CD\u8981\u529B\u91CF\uFF1A\u5B88\u536B\u4E1C\u5BAB\u7684\u519B\u961F\uFF0C\u5DF2\u6709\u4E0A\u4E07\u4EBA\u4E4B\u591A\u3002</p><p>\u8FD9\u652F\u519B\u961F\u7684\u5929\u804C\u4ECE\u6765\u90FD\u662F\u4FDD\u62A4\u592A\u5B50\u3002\u5982\u4ECA\u592A\u5B50\u65E2\u5DF2\u88AB\u6740\uFF0C\u90A3\u4E48\u8FD9\u652F\u519B\u961F\u5B58\u5728\u7684\u552F\u4E00\u610F\u4E49\uFF0C\u5C31\u53EA\u80FD\u662F\u4E3A\u592A\u5B50\u62A5\u4EC7\u4E86\u3002</p><p><strong>\u8C01\u80FD\u4E3A\u592A\u5B50\u62A5\u4EC7\uFF0C\u8C01\u5C31\u6709\u638C\u63E1\u8FD9\u652F\u519B\u961F\u7684\u53EF\u80FD</strong>\u3002</p><p>\u90A3\u8C01\u4F1A\u4E3A\u592A\u5B50\u62A5\u4EC7\u5462\uFF1F\u662F\u5FE0\u8D1E\u6B63\u76F4\u7684\u671D\u4E2D\u5927\u81E3\u5417\uFF1F</p><p>\u5F53\u7136\u4E0D\u662F\uFF0C\u662F\u91CE\u5FC3\u5BB6\u3002</p><p>\u8D75\u738B<code>\u53F8\u9A6C\u4F26</code>\u5C31\u5F88\u6709\u91CE\u5FC3\u3002</p><p>\u4ED6\u662F\u53F8\u9A6C\u61FF\u7684\u513F\u5B50\uFF0C\u4E8E\u5F53\u4ECA\u7687\u5E1D\u53F8\u9A6C\u8877\u800C\u8A00\uFF0C\u5DF2\u7ECF\u662F\u7956\u7236\u8F88\u7684\u4EBA\u7269\u3002\u8FD9\u4F4D\u4F4D\u9AD8\u6743\u91CD\u7684\u4E3B\u513F\u4EBA\u8001\u5FC3\u4E0D\u8001\uFF0C\u867D\u7136\u4E00\u628A\u5E74\u7EAA\u5374\u4E0D\u7518\u5BC2\u5BDE\uFF0C\u60F3\u8981\u8D81\u4E71\u4E0B\u6C34\u6478\u9C7C\u3002</p><p>\u4ED6\u7684\u667A\u5546\u4E0D\u662F\u592A\u591F\uFF0C\u4F46\u8FD9\u5E76\u4E0D\u8981\u7D27\uFF0C\u4ED6\u8EAB\u8FB9\u8FD8\u6709\u4E00\u4F4D\u8D34\u5FC3\u7684\u8C0B\u58EB\u2014\u2014<code>\u5B59\u79C0</code>\uFF0C\u6B63\u597D\u53EF\u4EE5\u5F25\u8865\u4ED6\u5728\u8FD9\u65B9\u9762\u7684\u4E0D\u8DB3\u3002</p><p>\u592A\u5B50\u88AB\u5E9F\u4EE5\u540E\uFF0C\u4E1C\u5BAB\u7684\u519B\u961F\u5F88\u5FEB\u5C31\u627E\u4E0A\u4E86\u53F8\u9A6C\u4F26\uFF0C\u8BF7\u6C42\u62E8\u4E71\u53CD\u6B63\u3002<strong>\u5728\u5B59\u79C0\u7684\u6388\u610F\u4E0B\uFF0C\u53F8\u9A6C\u4F26\u4E00\u65B9\u9762\u7B54\u5E94\uFF0C\u53E6\u4E00\u65B9\u9762\u53C8\u6309\u5175\u4E0D\u52A8\uFF0C\u7B49\u5F85\u65F6\u673A</strong>\uFF1A\u4ED6\u4EEC\u5E76\u4E0D\u662F\u771F\u60F3\u89E3\u6551\u592A\u5B50\uFF0C\u53EA\u662F\u60F3\u5229\u7528\u8FD9\u652F\u519B\u961F\u2014\u2014\u5728\u8FD9\u79CD\u76EE\u7684\u4E0B\uFF0C\u6B7B\u592A\u5B50\u5F53\u7136\u6BD4\u6D3B\u592A\u5B50\u66F4\u6709\u4EF7\u503C\u3002</p><p>\u679C\u7136\uFF0C\u8D3E\u540E\u4E0D\u4E45\u4E4B\u540E\u4FBF\u6740\u4E86\u592A\u5B50\u3002\u8FD9\u8BA9\u53F8\u9A6C\u4F26\u6709\u4E86\u5145\u5206\u7684\u8D77\u5175\u7406\u7531\uFF0C\u4ED6\u81EA\u5DF1\u6B64\u65F6\u638C\u63E1\u7740\u6D1B\u9633\u5317\u519B\uFF0C\u518D\u52A0\u4E0A\u4E1C\u5BAB\u7684\u519B\u961F\uFF0C\u4E24\u652F\u529B\u91CF\u8D81\u8D3E\u540E\u4E0D\u5907\u653B\u5165\u5BAB\u4E2D\uFF0C\u6210\u529F\u593A\u53D6\u4E86\u6743\u529B\u3002</p><p>\u53F8\u9A6C\u4F26\u4E00\u591C\u4E4B\u95F4\u8DC3\u4E0A\u6743\u529B\u7684\u5DC5\u5CF0\u3002</p><p>\u76F4\u5230\u6B64\u65F6\uFF0C\u53F8\u9A6C\u4F26\u7684\u884C\u4E3A\u4F9D\u7136\u5177\u5907\u5408\u6CD5\u6027\uFF0C\u867D\u7136\u4E13\u64C5\u671D\u653F\uFF0C\u4F46\u4E5F\u80FD\u88AB\u5404\u65B9\u63A5\u53D7\u548C\u8BA4\u53EF\u3002</p><p>\u53EF\u8D2A\u5FC3\u4E0D\u8DB3\uFF0C\u51E0\u4E4E\u5DF2\u7ECF\u5F97\u5230\u4E86\u4EBA\u95F4\u6240\u6709\u4E00\u5207\u7684\u53F8\u9A6C\u4F26\u4F9D\u7136\u4E0D\u6EE1\u610F\uFF0C\u4ED6\u8FD8\u60F3\u66F4\u8FDB\u4E00\u6B65\uFF1A</p><p><strong>\u5F53\u7687\u5E1D</strong></p><p>\u6709\u8FD9\u79CD\u5FC3\u601D\u4E5F\u96BE\u602A\uFF0C\u5B88\u7740\u664B\u60E0\u5E1D\u53F8\u9A6C\u8877\u8FD9\u4E48\u4E00\u4E2A\u5F31\u667A\uFF0C\u4EFB\u8C01\u90FD\u6CA1\u6709\u529E\u6CD5\u4EA7\u751F\u5C0A\u656C\u4E4B\u5FC3\u3002\u66F4\u4F55\u51B5\u8FD9\u4E2A\u5F31\u667A\u8FD8\u6709\u7740\u5168\u5929\u4E0B\u6700\u4E3A\u5C0A\u8D35\u7684\u8EAB\u4EFD\uFF0C\u53F8\u9A6C\u4F26\u96BE\u514D\u4F1A\u60F3\uFF0C \u4E3A\u4F55\u6211\u4E0D\u80FD\u62A2\u8FC7\u6765\u8BD5\u8BD5\u5462\uFF1F</p><p>\u8FD9\u79CD\u65F6\u5019\uFF0C\u5F53\u7136\u4F1A\u6709\u4EBA\u8DF3\u51FA\u6765\u8868\u793A\u53CD\u5BF9\u3002\u6BD4\u5982\uFF0C\u4E2D\u62A4\u519B\u3001\u6DEE\u5357\u738B<code>\u53F8\u9A6C\u5141</code>\u2014\u2014\u8FD9\u4E2A\u540D\u5B57\u5927\u5BB6\u4E0D\u9700\u8981\u8BB0\uFF0C\u53EA\u8981\u8BB0\u4F4F\u4ED6\u7684\u8EAB\u4EFD\u662F\u664B\u60E0\u5E1D\u7684\u4EB2\u5F1F\u5F1F\u5C31\u53EF\u4EE5\u4E86\u3002</p><p>\u664B\u6B66\u5E1D\u4E00\u5171\u6709\u4E8C\u5341\u591A\u4E2A\u513F\u5B50\uFF0C\u9664\u4E86\u5F31\u667A\u7684\u664B\u60E0\u5E1D\u53F8\u9A6C\u8877\u548C\u4E00\u4E9B\u65E9\u592D\u7684\u5B69\u5B50\u5916\uFF0C\u5176\u4ED6\u7684\u6216\u591A\u6216\u5C11\u90FD\u8FD8\u662F\u6709\u80FD\u529B\u7684\u3002\u5C31\u50CF\u8FD9\u4F4D\u53F8\u9A6C\u5141\uFF0C\u53F2\u4E66\u8BB0\u8F7D\u4ED6\u201C\u6027\u6C89\u6BC5\uFF0C\u5BBF\u536B\u5C06\u58EB\u7686\u754F\u670D\u4E4B\u201D\uFF0C\u6027\u683C\u6DF1\u6C89\u575A\u6BC5\uFF0C\u5F88\u5F97\u5C06\u58EB\u4E4B\u5FC3\u3002</p><p><strong>\u53F8\u9A6C\u5141\u575A\u51B3\u53CD\u5BF9\u53F8\u9A6C\u4F26\u5F53\u7687\u5E1D</strong>\u3002\u4ED6\u8BA4\u4E3A\u5929\u4E0B\u6BD5\u7ADF\u662F\u6B66\u5E1D\u7684\u5929\u4E0B\uFF0C\u4F60\u53F8\u9A6C\u4F26\u8F88\u5206\u867D\u9AD8\uFF0C\u4E5F\u53EA\u662F\u4E2A\u8FDC\u652F\u5B97\u5BA4\uFF0C\u7687\u5E1D\u7684\u5B9D\u5EA7\u518D\u600E\u4E48\u8F6C\u4E5F\u8F6C\u4E0D\u5230\u4F60\u7684\u5C41\u80A1\u4E0B\u9762\u53BB\u3002</p><blockquote><p>\u4E24\u4EBA\u8FD9\u79CD\u7ACB\u573A\u4E0A\u7684\u5DEE\u522B\uFF0C\u6700\u7EC8\u6F14\u53D8\u6210\u4E86\u519B\u4E8B\u51B2\u7A81\u3002</p></blockquote><p>\u5F53\u65F6\u53F8\u9A6C\u5141\u4EFB\u4E2D\u62A4\u519B\uFF0C\u638C\u7BA1\u7740\u5BAB\u5EF7\u6B66\u88C5\u529B\u91CF\u3002\u53F8\u9A6C\u4F26\u4E8E\u662F\u5C06\u53F8\u9A6C\u5141\u5347\u4E3A\u592A\u5C09\uFF0C\u60F3\u901A\u8FC7\u8FD9\u79CD\u529E\u6CD5\u5265\u593A\u53F8\u9A6C\u5141\u7684\u519B\u6743\u3002</p><p>\u4F46\u53F8\u9A6C\u5141\u62FF\u8FC7\u8BCF\u4E66\u540E\uFF0C\u5374\u53D1\u73B0\u8FD9\u6240\u8C13\u7684\u201C\u5723\u65E8\u201D\u5C45\u7136\u662F\u5B59\u79C0\u81EA\u5DF1\u5199\u7684\u3002\u53F8\u9A6C\u5141\u5927\u6012\uFF0C\u968F\u5373\u53D1\u5175\u56F4\u653B\u53F8\u9A6C\u4F26\u7684\u76F8\u5E9C\u3002</p><p>\u4F46\u5728\u4E71\u519B\u4E4B\u4E2D\uFF0C\u53F8\u9A6C\u5141\u843D\u8D25\u88AB\u6740\u3002</p><p>\u4E00\u65F6\u95F4\uFF0C\u53F8\u9A6C\u4F26\u5FD7\u5F97\u610F\u6EE1\u3002\u4ED6\u78E8\u5200\u970D\u970D\uFF0C<strong>\u903C\u60E0\u5E1D\u53F8\u9A6C\u8877\u9000\u4F4D\u4E3A\u592A\u4E0A\u7687\uFF0C\u81EA\u5DF1\u767B\u57FA\u505A\u4E86\u7687\u5E1D</strong>\u3002</p><p>\u53F8\u9A6C\u4F26\u6B64\u4E3E\uFF0C\u7ED9\u81EA\u5DF1\u62DB\u6765\u4E86\u66F4\u5927\u7684\u53CD\u5BF9\u529B\u91CF\u3002</p><p><strong>\u9996\u5148\u53D1\u96BE\u7684\uFF0C\u662F\u9F50\u738B<code>\u53F8\u9A6C\u518F</code></strong>\u3002</p><p>\u53F8\u9A6C\u518F\u4E4B\u6240\u4EE5\u4F1A\u8DF3\u51FA\u6765\uFF0C\u5176\u5B9E\u4E5F\u662F\u53F8\u9A6C\u4F26\u81EA\u5DF1\u7ED9\u81EA\u5DF1\u6316\u7684\u5751\u3002\u5F53\u521D\u8BDB\u6740\u8D3E\u540E\uFF0C\u53F8\u9A6C\u518F\u4E5F\u53C2\u4E0E\u4E86\u653F\u53D8\u3002\u4F46\u4E8B\u6210\u4EE5\u540E\uFF0C\u53F8\u9A6C\u4F26\u7ED9\u81EA\u5DF1\u7684\u4EB2\u4FE1\u5168\u90FD\u5347\u4E86\u5B98\uFF0C\u5BF9\u53F8\u9A6C\u518F\u8FD9\u4E2A\u76DF\u53CB\u5374\u9887\u4E3A\u5C0F\u6C14\uFF0C\u53EA\u7ED9\u4E86\u4ED6\u4E00\u4E2A\u6E38\u51FB\u5C06\u519B\u7684\u865A\u804C\u3002</p><p>\u53F8\u9A6C\u518F\u5F53\u65F6\u5C31\u5F88\u4E0D\u6EE1\u610F\uFF0C\u53EF\u5B59\u79C0\u5374\u4E1D\u6BEB\u4E0Dcare\uFF0C\u7ADF\u7136\u628A\u53F8\u9A6C\u518F\u8D76\u5230\u4E86\u8BB8\u660C\u3002\u4EB2\u738B\u51FA\u9547\u5730\u65B9\uFF0C\u7167\u4F8B\u662F\u8981\u517C\u519B\u804C\u7684\uFF0C\u8EAB\u5728\u8BB8\u660C\u7684\u53F8\u9A6C\u518F\u5B9E\u9645\u4E0A\u5C31\u53D8\u6210\u4E86\u664B\u671D\u7684\u201C<strong>\u4E2D\u90E8\u6218\u533A\u53F8\u4EE4\u5458</strong>\u201D\u3002</p><p>\u5982\u4ECA\u53F8\u9A6C\u4F26\u7BE1\u4F4D\uFF0C\u53F8\u9A6C\u518F\u89C9\u5F97\u81EA\u5DF1\u62A5\u590D\u7684\u673A\u4F1A\u6765\u4E86\u3002\u4ED6\u6548\u4EFF\u4E00\u767E\u591A\u5E74\u524D\u7684\u8881\u7ECD\uFF0C\u53D1\u6A84\u6587\u8BA8\u4F10\u53F8\u9A6C\u4F26\u3002</p><p><strong>\u90BA\u57CE\u7684\u6210\u90FD\u738B<code>\u53F8\u9A6C\u9896</code>\u6700\u5148\u54CD\u5E94</strong>\u3002\u4ED6\u672C\u6765\u5C31\u662F\u664B\u60E0\u5E1D\u7684\u4EB2\u5F1F\u5F1F\uFF0C\u53C8\u56E0\u4E3A\u5F97\u7F6A\u8D3E\u540E\u515A\u7FBD\u88AB\u8D76\u51FA\u4E86\u6D1B\u9633\u3002\u5982\u4ECA\u60E0\u5E1D\u88AB\u5E9F\uFF0C\u5C31\u7B97\u662F\u51FA\u4E8E\u8D23\u4EFB\u5FC3\uFF0C\u4ED6\u4E5F\u8981\u52A0\u5165\u9F50\u738B\u53F8\u9A6C\u518F\u7684\u8BA8\u4F10\u961F\u4F0D\u3002</p><p>\u6CB3\u95F4\u738B<code>\u53F8\u9A6C\u9899</code>\u6709\u70B9\u53CD\u590D\uFF0C\u4ED6\u672C\u6765\u662F\u62BC\u5B9D\u8D75\u738B\u4F26\u7684\uFF0C\u540E\u6765\u53D1\u73B0\u53CD\u6297\u7684\u961F\u4F0D\u4E0D\u65AD\u58EE\u5927\uFF0C\u624D\u53C8\u6539\u4E3B\u610F\u52A0\u5165\u4E86\u9F50\u738B\u518F\u548C\u6210\u90FD\u738B\u988D\u7684\u9635\u8425\u2014\u2014\u8FD9\u91CC\u8981\u987A\u4FBF\u63D0\u4E00\u53E5\uFF0C\u6CB3\u95F4\u738B\u9899\u6B64\u65F6\u9547\u5B88\u957F\u5B89\uFF0C\u53EF\u4EE5\u7B97\u662F\u201C<strong>\u897F\u90E8\u6218\u533A\u53F8\u4EE4\u5458</strong>\u201D\u3002</p><blockquote><p>\u8FD9\u4F4D\u53F8\u9A6C\u9899\u76F8\u5F53\u5978\u733E\uFF0C\u4ED6\u7684\u519B\u961F\u51FA\u53D1\u540E\u4E00\u76F4\u78E8\u78E8\u8E6D\u8E6D\uFF0C\u8FD8\u6CA1\u8D70\u51FA\u6F7C\u5173\uFF0C\u53F8\u9A6C\u4F26\u548C\u5B59\u79C0\u5C31\u5DF2\u7ECF\u88AB\u9F50\u738B\u518F\u6740\u4E86\uFF0C\u81EA\u5DF1\u5E73\u767D\u6361\u4E86\u4E2A\u5927\u7EA2\u5305\u3002</p></blockquote><p>\u9F50\u738B\u518F\u8DDF\u6210\u90FD\u738B\u988D\u4E8E\u662F\u4E00\u8DEF\u62DB\u5175\u4E70\u9A6C\uFF0C\u51E0\u5341\u4E07\u4EBA\u6D69\u6D69\u8361\u8361\u6740\u5411\u6D1B\u9633\uFF0C\u8D75\u738B\u4F26\u548C\u5B59\u79C0\u5219\u624B\u5FD9\u811A\u4E71\u4ED3\u4FC3\u5E94\u5BF9\uFF0C\u4E24\u4EBA\u53C8\u662F\u5360\u535C\u53C8\u662F\u8BF7\u795E\u95EE\u9053\uFF0C\u5DEB\u5E08\u6765\u4E86\u4E00\u6CE2\u53C8\u4E00\u6CE2\uFF0C\u6068\u4E0D\u5F97\u628A\u5929\u5175\u5929\u5C06\u90FD\u7ED9\u62DB\u6765\u3002</p><p>\u7136\u800C\u524D\u7EBF\u4F20\u6765\u7684\u6D88\u606F\u5374\u8BA9\u53CC\u65B9\u90FD\u5927\u8DCC\u773C\u955C\uFF1A\u9F50\u738B\u518F\u548C\u6210\u90FD\u738B\u988D\u7684\u4E4C\u5408\u4E4B\u4F17\u5B8C\u5168\u6CA1\u6709\u6218\u6597\u529B\uFF0C\u6839\u672C\u6321\u4E0D\u4F4F\u8D75\u738B\u4F26\u6D3E\u51FA\u53BB\u7684\u6B63\u89C4\u519B\u3002</p><p>\u4F46\u4E71\u4E16\u7684\u6ED1\u7A3D\u548C\u8352\u8BDE\u4E5F\u5C31\u5728\u8FD9\u91CC\uFF0C\u8D75\u738B\u4F26\u7684\u6B63\u89C4\u519B\u8D62\u5F97\u4E86\u6218\u6597\u8D62\u4E0D\u4E86\u4EBA\u5FC3\uFF0C<strong>\u524D\u7EBF\u6709\u5C06\u9886\u4E0D\u60F3\u6253\u4ED7\uFF0C\u5C45\u7136\u8DD1\u56DE\u6D1B\u9633\u8DDF\u671D\u5EF7\u8BF4\u8D62\u7684\u662F\u9F50\u738B</strong>\u3002\u4E00\u65F6\u4E4B\u95F4\uFF0C\u6D1B\u9633\u60F6\u6050\u3002</p><p>\u9F50\u738B\u548C\u6210\u90FD\u738B\u7684\u8054\u519B\u7531\u6B64\u7AD9\u7A33\u9635\u811A\uFF0C\u5F00\u59CB\u53CD\u653B\u3002\u8D75\u738B\u4F26\u4E0E\u5B59\u79C0\u5728\u6D1B\u9633\u60F6\u60F6\u4E0D\u53EF\u7EC8\u65E5\u3002\u4E0D\u4E45\u4EE5\u540E\uFF0C\u57CE\u5185\u53D1\u751F\u5175\u53D8\uFF0C\u8D75\u738B\u4F26\u4E0E\u5B59\u79C0\u88AB\u6740\u3002\u9F50\u738B\u518F\u7387\u5148\u8FDB\u5165\u6D1B\u9633\u3002\u664B\u60E0\u5E1D\u53F8\u9A6C\u8877\u590D\u4F4D\u3002</p><p>\u81F3\u6B64\uFF0C\u516B\u738B\u4E4B\u4E71\u7684\u7B2C\u4E8C\u9636\u6BB5\uFF0C\u4EE5\u9F50\u738B\u518F\u4E3A\u9996\u7684\u8054\u519B\u4E2D\u573A\u83B7\u80DC\u800C\u544A\u7EC8\u3002</p><p>\u76F8\u6BD4\u8D77\u4E09\u56FD\u65F6\u4EE3\u7684\u6218\u4E71\uFF0C\u516B\u738B\u4E4B\u4E71\u4E2D\u7684\u4E89\u6597\u4F3C\u4E4E\u4E0D\u5FCD\u5352\u89C6\uFF0C\u6743\u8C0B\u6597\u4E89\u6C34\u5E73\u62D9\u52A3\u4E0D\u8BF4\uFF0C\u5373\u4FBF\u662F\u6218\u573A\u4E89\u80DC\u4E5F\u6BEB\u65E0\u6597\u4E89\u7F8E\u5B66\u53EF\u8A00\u3002</p><p>\u53CC\u65B9\u7684\u6218\u6597\uFF0C\u7B80\u76F4\u5C31\u50CF\u662F\u4E24\u4E2A\u9ED1\u793E\u4F1A\u56E2\u4F53\u5728\u4E92\u780D\uFF0C\u4E00\u987F\u4E71\u5200\u4E0B\u53BB\uFF0C\u8D62\u7684\u4E0D\u77E5\u9053\u662F\u600E\u4E48\u8D62\u7684\uFF0C\u8F93\u7684\u4E0D\u77E5\u9053\u4E3A\u4EC0\u4E48\u8F93\u4E86\u3002</p><p><strong>\u4F46\u771F\u5B9E\u7684\u4E8B\u5B9E\u5374\u662F\uFF0C\u5927\u90E8\u5206\u7684\u4E71\u4E16\u5C31\u662F\u8FD9\u6837\u7684</strong>\u3002</p><p>\u5728\u4E71\u4E16\u4E4B\u4E2D\u6740\u4F10\u7EB5\u6A2A\u7684\uFF0C\u5E76\u4E0D\u4E00\u5B9A\u662F\u90FD\u6709\u6C34\u5E73\u548C\u80FD\u529B\u7684\u3002\u5F88\u591A\u65F6\u5019\uFF0C\u90FD\u53EA\u662F\u4E00\u4E9B\u4F4E\u6C34\u5E73\u7684\u6DF7\u4E71\uFF0C\u65E2\u6CA1\u6709\u4E71\u51FA\u98CE\u683C\uFF0C\u4E5F\u6CA1\u6709\u4E71\u51FA\u6C34\u5E73\u3002</p><p>\u90A3\u4E48\uFF0C\u65E2\u7136\u8FD9\u662F\u4E71\u4E16\u4E2D\u7684\u5E38\u6001\uFF0C\u7A76\u7ADF\u662F\u4EC0\u4E48\u539F\u56E0\u5BFC\u81F4\u4E86\u8FD9\u79CD\u7ED3\u679C\uFF1F</p><blockquote><p>\u8FD9\u91CC\u5176\u5B9E\u7275\u6D89\u5230\u4E00\u4E2A\u5F88\u57FA\u672C\u7684\u95EE\u9898\uFF1A</p><p>\u5F53\u4E00\u4E2A\u4E71\u4E16\u7A81\u7136\u6765\u4E34\u7684\u65F6\u5019\uFF0C\u4EC0\u4E48\u6837\u7684\u4EBA\u4F1A\u7387\u5148\u53C2\u4E0E\u5176\u4E2D\uFF1F</p></blockquote><p>\u6709\u4E24\u79CD\uFF0C<strong>\u7B2C\u4E00\u79CD</strong>\u662F\u6B63\u597D\u5904\u5728\u98CE\u53E3\u6D6A\u5C16\u4E0A\u7684\uFF0C<strong>\u7B2C\u4E8C\u79CD</strong>\u662F\u60F3\u8981\u6D51\u6C34\u6478\u9C7C\u7684\u3002</p><p>\u4ECA\u5929\u6211\u4EEC\u7684\u6587\u7AE0\u63D0\u5230\u4E86\u56DB\u4F4D\u4EB2\u738B\uFF0C\u4ED6\u4EEC\u5C31\u6B63\u597D\u5BF9\u5E94\u7740\u8FD9\u4E24\u79CD\u4EBA\u3002\u8D75\u738B\u4F26\u548C\u6CB3\u95F4\u738B\u9899\u662F\u5E0C\u671B\u6D51\u6C34\u6478\u9C7C\u7684\uFF0C\u9F50\u738B\u518F\u548C\u6210\u90FD\u738B\u988D\u5219\u662F\u5904\u5728\u98CE\u53E3\u6D6A\u5C16\u4E0A\u7684\u3002</p><p>\u4E00\u822C\u6765\u8BF4\uFF0C\u8FD9\u4E24\u79CD\u4EBA\u4F1A\u7387\u5148\u53C2\u4E0E\u7ADE\u4E89\u3002<strong>\u800C\u8FD9\u53C8\u610F\u5473\u7740\uFF0C\u7387\u5148\u5165\u5C40\u7684\uFF0C\u5E76\u4E0D\u89C1\u5F97\u5C31\u662F\u771F\u6B63\u6709\u7ADE\u4E89\u5B9E\u529B\u7684</strong>\u3002</p><p>\u6216\u8005\uFF0C\u8FD9\u4E48\u8868\u8FF0\u66F4\u4E3A\u51C6\u786E\uFF1A</p><p><strong>\u5927\u90E8\u5206\u65F6\u5019\uFF0C\u5728\u7ADE\u4E89\u7684<code>\u521D\u671F</code>\uFF0C\u7ADE\u4E89\u8005\u7684\u6C34\u5E73\u90FD\u662F\u76F8\u5BF9<code>\u4F4E\u4E0B</code>\u7684</strong>\u3002</p><p>\u5728\u4F4E\u6C34\u5E73\u7684\u7ADE\u4E89\u4E2D\uFF0C <code>\u4F4D\u7F6E</code> \u548C <code>\u8FD0\u6C14</code> \u76F8\u5BF9\u91CD\u8981\uFF1A\u53EA\u8981\u80FD\u5361\u4E2A\u597D\u4F4D\u7F6E\uFF0C\u5C31\u610F\u5473\u7740\u4F60\u6709\u4E86\u5148\u53D1\u4F18\u52BF\uFF1B\u5982\u679C\u8FD0\u6C14\u518D\u597D\u4E00\u70B9\uFF0C\u90A3\u4E48\u53EF\u80FD\u4E00\u573A\u80DC\u5229\u5C31\u80FD\u901A\u5403\u5168\u573A\u3002</p><p>\u4E0D\u7BA1\u662F\u8D3E\u540E\u65E9\u5E74\u7684\u501F\u5200\u6740\u4EBA\uFF0C\u593A\u53D6\u6743\u529B\uFF0C\u8FD8\u662F\u5982\u4ECA\u8D75\u738B\u4F26\u7684\u5E9F\u9664\u8D3E\u540E\uFF0C\u4EE5\u53CA\u6700\u7EC8\u8986\u706D\uFF0C\u90FD\u6709\u5F88\u5927\u7684\u5076\u7136\u6210\u5206\u5728\u5176\u4E2D\u3002\u8FD9\u8BA9\u53CC\u65B9\u7684\u6597\u4E89\u770B\u8D77\u6765\u5341\u5206\u5177\u6709\u620F\u5267\u6027\uFF0C\u8D62\u7684\u4EBA\u83AB\u540D\u5176\u5999\uFF0C\u8F93\u7684\u4EBA\u4E0D\u77E5\u6240\u4EE5\u3002</p><p>\u4F46\u95EE\u9898\u662F\uFF0C\u968F\u7740\u7ADE\u4E89\u7684\u65F6\u95F4\u8D8A\u6765\u8D8A\u957F\uFF0C\u9AD8\u6C34\u5E73\u7684\u9009\u624B\u603B\u4F1A\u9010\u6E10\u4E0B\u573A\uFF0C<strong>\u4ED6\u4EEC\u4F1A\u5C06\u6574\u4E2A\u7ADE\u4E89\u6C34\u5E73\u62C9\u5347\u5230\u4E00\u4E2A\u65B0\u9AD8\u5EA6</strong>\u3002\u5230\u4E86\u90A3\u65F6\uFF0C\u5982\u679C\u4F60\u8FD8\u60F3\u7EE7\u7EED\u5728\u8FD9\u4E2A\u5C40\u91CC\u5F85\u4E0B\u53BB\uFF0C\u8FD0\u6C14\u57FA\u672C\u4E0A\u5C31\u5E2E\u4E0D\u4E86\u4F60\u4E86\uFF0C\u4F60\u5FC5\u987B\u5F97\u6709\u4E00\u5B9A\u7684\u80FD\u529B\u3002</p><p>\u4E00\u4E2A\u4F8B\u5B50\u662F\u5386\u4EE3\u738B\u671D\u672B\u5E74\u7684\u6218\u4E71\u3002\u7B2C\u4E00\u6CE2\u5F15\u53D1\u6218\u4E71\u7684\uFF0C\u51E0\u4E4E\u90FD\u5F88\u96BE\u8D70\u5230\u6700\u540E\uFF0C\u4E0D\u7BA1\u662F\u9648\u80DC\u3001\u5434\u5E7F\uFF0C\u8FD8\u662F\u9EC4\u5DE2\u3001\u674E\u81EA\u6210\u3002\u4ED6\u4EEC\u53EA\u662F\u9002\u9022\u5176\u4F1A\uFF0C\u70B9\u71C3\u4E86\u70FD\u706B\uFF0C\u6405\u4E71\u4E86\u5C40\u52BF\u800C\u5DF2\u3002\u7136\u540E\uFF0C\u53EA\u80FD\u7B49\u5F85\u771F\u6B63\u7684\u9AD8\u624B\u4E0B\u573A\u6536\u62FE\u6B8B\u5C40\u3002</p><p>\u4E00\u6837\u7684\u539F\u56E0\uFF0C\u4E3A\u4EC0\u4E48\u5386\u53F2\u4E2D\u7684\u6743\u8C0B\u6597\u4E89\u90FD\u53D1\u751F\u5728\u5E1D\u738B\u5C06\u76F8\u8EAB\u4E0A\uFF0C\u800C\u51E0\u4E4E\u6CA1\u6709\u5C0F\u5B98\u5C0F\u540F\u4EC0\u4E48\u620F\u4EFD\uFF1F</p><p>\u7B54\u6848\u5F88\u660E\u663E\uFF0C\u5C31\u662F\u56E0\u4E3A\u5C0F\u5B98\u540F\u7684\u6597\u4E89\u6C34\u5E73\u592A\u4F4E\u7EA7\u3002\u771F\u6B63\u4F1A\u6597\u80FD\u6597\u7684\uFF0C\u5176\u5B9E\u90FD\u5728\u671D\u5802\u4E4B\u4E0A\uFF0C\u90A3\u4E9B\u624D\u662F\u5343\u9524\u767E\u70BC\u7684\u6218\u6597\u7CBE\u82F1\u3002</p><p>\u8FD9\u6761\u9053\u7406\uFF0C\u4E0D\u4EC5\u9002\u7528\u4E8E\u6743\u8C0B\u8BE1\u8BA1\uFF0C\u4E5F\u9002\u7528\u4E8E\u4EFB\u4F55\u7ADE\u4E89\u5E02\u573A\u3002<strong>\u8FD9\u4E9B\u5E74\u6765\u7684\u5546\u6218\uFF0C\u56DE\u60F3\u8D77\u6765\u6A21\u5F0F\u603B\u76F8\u4F3C</strong>\uFF1A\u6700\u65E9\u7684\u89C6\u9891\u7F51\u7AD9\u5174\u8D77\u6F6E\u6D41\u3001\u5F53\u521D\u7684\u5343\u56E2\u5927\u6218\u3001\u524D\u4E24\u5E74\u7684\u5171\u4EAB\u5355\u8F66......\u90FD\u662F\u4E00\u573A\u7A81\u5982\u5176\u6765\u7684\u7ADE\u4E89\uFF0C\u5404\u8DEF\u9AD8\u624B\u548C\u4F4E\u624B\u7C89\u58A8\u767B\u573A\uFF0C\u7136\u540E\u597D\u4E00\u756A\u70ED\u95F9\uFF0C\u76F4\u5230\u6700\u540E\u5927\u591A\u90FD\u6B7B\u5728\u4E86\u6C99\u6EE9\u4E0A\u3002</p><p>\u8FD8\u662F\u90A3\u53E5\u8BDD\uFF0C\u5982\u679C\u4F60\u5355\u9760\u8FD0\u6C14\u5728\u4E00\u4E2A\u8D4C\u5C40\u4E4B\u4E2D\u8D5A\u5230\u4E86\u7B2C\u4E00\u6876\u91D1\uFF0C<strong>\u90A3\u4E48\u8BF7\u4E0D\u8981\u8FF7\u4FE1\u4F60\u7684\u8FD0\u6C14\uFF0C\u66F4\u4E0D\u8981\u9AD8\u4F30\u4F60\u7684\u80FD\u529B</strong>\uFF0C\u53CA\u65F6\u62BD\u8EAB\u624D\u662F\u6700\u597D\u7684\u9009\u62E9\u3002</p><p>\u5426\u5219\uFF0C\u7B49\u5230\u9AD8\u6C34\u5E73\u5BF9\u624B\u4E0B\u573A\u4E4B\u65F6\uFF0C\u4F60\u5C06\u6B7B\u65E0\u846C\u8EAB\u4E4B\u5730\u3002</p><h2 id="\u6C11\u56FD" tabindex="-1"><a class="header-anchor" href="#\u6C11\u56FD" aria-hidden="true">#</a> \u6C11\u56FD</h2><p>\u6C11\u56FD\u7684\u5C40\u9762\uFF0C\u51E0\u4E4E\u7B26\u5408\u6211\u4EEC\u5728\u6B63\u6587\u5206\u6790\u4E2D\u63D0\u5230\u7684\u4E00\u5207\u6761\u4EF6\uFF1A\u7A81\u7136\u51FA\u73B0\u7684\u6DF7\u4E71\u3001\u5404\u79CD\u5B9E\u529B\u4F4E\u4E0B\u7684\u4EBA\u7269\u7C89\u58A8\u767B\u573A\u3001\u83AB\u540D\u5176\u5999\u7684\u80DC\u8D25......\u7B49\u7B49\u7B49\u7B49\u3002</p><p><strong>\u6C11\u56FD\u65E9\u671F\u7684\u6597\u4E89\u6C34\u5E73\uFF0C\u76F8\u5BF9\u90FD\u6BD4\u8F83\u4F4E\u52A3</strong>\u3002\u4E14\u4E0D\u8BF4\u5982\u77F3\u53CB\u4E09\u3001\u5B59\u6BBF\u82F1\u4E4B\u7C7B\u7684\u519B\u9600\uFF0C\u5373\u4FBF\u662F\u76F4\u3001\u5949\u3001\u7696\uFF0C\u4E5F\u65E0\u975E\u5C31\u662F\u4E09\u4E2A\u6BD4\u8F83\u5927\u7684\u519B\u9600\u800C\u5DF2\u3002</p><p>\u5176\u4E2D\uFF0C\u5434\u4F69\u5B5A\u66FE\u7ECF\u88AB\u8BA4\u4E3A\u662F\u80FD\u591F\u7ED3\u675F\u4E2D\u56FD\u6DF7\u4E71\u7684\u6551\u4E16\u4E3B\uFF0C\u4F46\u4E5F\u51E0\u4E4E\u662F\u4E00\u6218\u800C\u8D25\uFF0C\u518D\u4E5F\u65E0\u6CD5\u7FFB\u8EAB\u3002\u800C\u80FD\u5982\u960E\u9521\u5C71\u4E00\u822C\u4E0D\u4E0B\u8D4C\u684C\uFF0C\u4ECE\u8F9B\u4EA5\u9769\u547D\u575A\u6301\u5230\u5EFA\u56FD\u524D\u7684\uFF0C\u5BE5\u5BE5\u6570\u4EBA\u800C\u5DF2\u3002</p><p>\u5728\u65E9\u671F\u7684\u519B\u9600\u6DF7\u6218\u4E2D\uFF0C\u5404\u5927\u519B\u9600\u4E89\u76F8\u83B7\u53D6\u5916\u56FD\u529B\u91CF\u7684\u652F\u6301\uFF0C\u8FD9\u662F\u4ED6\u4EEC\u6210\u529F\u7684\u6700\u5173\u952E\u56E0\u7D20\u3002\u5373\u4FBF\u5F3A\u608D\u5982\u5949\u7CFB\uFF0C\u5728\u5931\u53BB\u4E86\u65E5\u672C\u4EBA\u7684\u652F\u6301\u540E\uFF0C\u4E5F\u5728\u51E0\u5E74\u4E4B\u95F4\u8FC5\u901F\u8870\u843D\uFF0C\u76F4\u81F3\u9000\u51FA\u4E1C\u5317\uFF0C\u800C\u540E\u6D88\u5931\u5728\u5386\u53F2\u4E2D\u3002</p><p>\u4F46\u968F\u7740\u65F6\u95F4\u8FDB\u5165\u4E8C\u5341\u5E74\u4EE3\u672B\uFF0C\u6C11\u56FD\u7684\u7ADE\u4E89\u5C40\u9762\u5DF2\u7ECF\u5927\u4E0D\u76F8\u540C\u3002<strong>\u5728\u8FD9\u5341\u51E0\u5E74\u95F4\u4E2D\uFF0C\u968F\u7740\u6DF7\u4E71\u5C40\u9762\u7684\u6301\u7EED\u548C\u6DF1\u5165\uFF0C\u53C2\u4E0E\u65B9\u7684\u6597\u4E89\u6C34\u5E73\u5728\u6301\u7EED\u4E0D\u65AD\u7684\u63D0\u9AD8</strong>\uFF0C\u8FD9\u662F\u4E00\u4E2A\u4E0D\u4E89\u7684\u4E8B\u5B9E\u3002</p><blockquote><p>\u4F8B\u8BC1\u5C31\u662F\u6297\u65E5\u6218\u4E89\u3002</p></blockquote><p>\u8FD9\u91CC\u4E0D\u8BA8\u8BBA\u6297\u65E5\u6218\u4E89\u5230\u5E95\u662F\u8C01\u6253\u8D62\u7684\uFF0C\u4F46\u56FD\u5171\u4E24\u515A\u786E\u5B9E\u90FD\u662F\u4ECE\u516B\u5E74\u6297\u6218\u4E2D\u575A\u6301\u4E0B\u6765\u7684\u3002\u800C\u65E5\u672C\u4EBA\u5728\u519B\u4E8B\u4E0A\u7684\u538B\u529B\uFF0C\u4E5F\u8FDC\u6BD4\u5F53\u5E74\u9762\u5BF9\u5949\u7CFB\u7684\u538B\u529B\u8981\u5927\u5F97\u591A\u3002</p><p>\u5DEE\u8DDD\u5BF9\u6BD4\uFF0C\u4E00\u671B\u53EF\u77E5\u3002</p><p>\u82E5\u662F\u848B\u4ECB\u77F3\u6709\u6C11\u56FD\u4E09\u5341\u56DB\u5E74\uFF08\u516C\u51431945\u5E74\uFF09\u7684\u5B9E\u529B\uFF0C\u7A7F\u8D8A\u5230\u6C11\u56FD\u5341\u5E74\uFF08\u516C\u51431921\u5E74\uFF09\uFF0C\u53EA\u6015\u662F\u80FD\u6A2A\u626B\u5929\u4E0B\u7684\uFF0C\u795E\u6321\u6740\u795E\uFF0C\u4F5B\u6321\u6740\u4F5B\u3002</p><blockquote><p>\u66F4\u4E3A\u5F3A\u5927\u7684\u4F8B\u8BC1\uFF0C\u8FD8\u6709\u671D\u9C9C\u6218\u4E89\u3002</p></blockquote><p>1894\u5E74\uFF0C\u7532\u5348\u6218\u4E89\u7206\u53D1\uFF0C\u6E05\u5EF7\u8D25\u4E8E\u65E5\u672C\uFF0C\u4E22\u6389\u4E1C\u4E9A\u9738\u6743\u300260\u5E74\u540E\uFF0C\u671D\u9C9C\u6218\u573A\u505C\u706B\u3002</p><p>\u4EC5\u4EC5\u4E00\u4E2A\u7532\u5B50\uFF0C\u4E2D\u56FD\u5DF2\u7ECF\u53EF\u4EE5\u4E0E\u4E16\u754C\u7B2C\u4E00\u5F3A\u56FD\u5728\u6B63\u9762\u6218\u573A\u4E0A\u4E89\u96C4\u3002</p>',87),s=[n];function c(g,d){return o(),t("div",null,s)}const u=p(e,[["render",c],["__file","6.html.vue"]]);export{u as default};
