import{_ as p}from"./_plugin-vue_export-helper.cdc0426e.js";import{o,c as e,f as c}from"./app.8a7d9ef5.js";const d={},t=c('<h1 id="\u5317\u9B4F\u8FC1\u90FD" tabindex="-1"><a class="header-anchor" href="#\u5317\u9B4F\u8FC1\u90FD" aria-hidden="true">#</a> \u5317\u9B4F\u8FC1\u90FD</h1><blockquote><p>\u9B4F\u4E3B\u81EA\u53D1\u5E73\u57CE\u81F3\u6D1B\u9633\uFF0C\u9716\u96E8\u4E0D\u6B62\u3002\u4E19\u5B50\uFF0C\u8BCF\u8BF8\u519B\u524D\u53D1\u3002\u4E01\u4E11\uFF0C\u5E1D\u620E\u670D\uFF0C\u6267\u97AD\u4E58\u9A6C\u800C\u51FA\u3002</p></blockquote><p><strong>\u300A\u8D44\u6CBB\u901A\u9274\xB7\u5377\u4E00\u4E09\u4E03\xB7\u5B8B\u7EAA\u56DB\u300B</strong></p><p>\u8FC1\u90FD\uFF0C\u4ECE\u6765\u90FD\u662F\u5173\u4E4E\u56FD\u8FD0\u7684\u5927\u4E8B\u3002\u4F46\u6839\u636E\u53F2\u4E66\u8BB0\u8F7D\uFF0C\u5B5D\u6587\u5E1D\u5728\u505A\u8FD9\u4E2A\u51B3\u7B56\u65F6\uFF0C\u51E0\u4E4E\u6CA1\u6709\u8DDF\u4EFB\u4F55\u4EBA\u5546\u91CF\u8FC7\u3002</p><p>\u901A\u8FC7\u53F2\u6599\u7684\u7EC6\u679D\u672B\u8282\uFF0C\u53EF\u4EE5\u53D1\u73B0\u6709\u4E9B\u4EBA\u5BF9\u4E8E\u8FC1\u90FD\u4E00\u4E8B\uFF0C\u662F\u6709\u6240\u9884\u8BA1\u7684\uFF0C\u4F46\u8FD9\u53EA\u662F\u6781\u5C11\u6570\u4EBA\u3002\u800C\u4E14\uFF0C\u8FD9\u4E9B\u4EBA\u5E76\u4E0D\u662F\u671D\u5EF7\u91CD\u81E3\uFF0C\u53EA\u662F\u8DDF\u5B5D\u6587\u5E1D\u5173\u7CFB\u6BD4\u8F83\u4EB2\u5BC6\u7684\u8FD1\u81E3\u3002</p><p>\u4E5F\u5C31\u662F\u8BF4\uFF0C<strong>\u6240\u6709\u7684\u671D\u5EF7\u5927\u81E3\uFF0C\u5BF9\u4E8E\u5B5D\u6587\u5E1D\u8981\u8FC1\u90FD\u7684\u610F\u56FE\uFF0C\u90FD\u662F\u4E0D\u77E5\u9053\u7684\u3002</strong></p><p>\u4F46\u4E0D\u77E5\u9053\u5C31\u5BF9\u4E86\uFF0C\u56E0\u4E3A\u5B5D\u6587\u5E1D\u538B\u6839\u5C31\u6CA1\u6253\u7B97\u8981\u544A\u8BC9\u4ED6\u4EEC\u3002\u5F53\u65F6\uFF0C<strong>\u5B5D\u6587\u5E1D\u5728\u671D\u5802\u4E4B\u4E0A\u5BA3\u5E03\u7684\uFF0C\u662F\u8981\u5357\u5F81\uFF0C\u800C\u4E14\u662F\u8D77\u91CD\u5175\uFF0C\u5FA1\u9A7E\u4EB2\u5F81\u3002</strong></p><p>\u5317\u9B4F\u6BD5\u7ADF\u662F\u9C9C\u5351\u4EBA\u5EFA\u7ACB\u7684\u738B\u671D\uFF0C\u5BF9\u4E8E\u7687\u5E1D\u5FA1\u9A7E\u4EB2\u5F81\u8FD9\u79CD\u4E8B\u5E76\u4E0D\u6392\u65A5\u3002\u5B5D\u6587\u5E1D\u662F\u5317\u9B4F\u7684\u7B2C\u4E03\u4EE3\u7687\u5E1D\uFF0C\u5728\u4ED6\u4E4B\u524D\u7684\u516D\u4EE3\u7687\u5E1D\u51E0\u4E4E\u90FD\u6709\u8FC7\u5FA1\u9A7E\u4EB2\u5F81\u7684\u7ECF\u5386\uFF0C\u800C\u4E14\u6BCF\u4F4D\u7687\u5E1D\u8FD8\u4E0D\u6B62\u51FA\u5F81\u4E86\u4E00\u6B21\u3002</p><p>\u6240\u4EE5\u5F53\u5B5D\u6587\u5E1D\u8BF4\u81EA\u5DF1\u60F3\u8981\u5357\u5F81\u7684\u65F6\u5019\uFF0C\u8C01\u4E5F\u6CA1\u6709\u591A\u60F3\u5176\u4ED6\u7684\u53EF\u80FD\uFF0C\u5927\u5BB6\u90FD\u4EE5\u4E3A\u4ED6\u771F\u7684\u5C31\u662F\u8981\u5357\u5F81\u3002</p><p>\u800C\u5982\u679C\u662F\u5357\u5F81\uFF0C\u90A3\u4E48\u8981\u8003\u8651\u7684\u95EE\u9898\uFF0C\u5C31\u662F\u5355\u7EAF\u7684\u519B\u4E8B\u95EE\u9898\u4E86\u3002\u7FA4\u81E3\u7684\u8BA8\u8BBA\uFF0C\u56E0\u6B64\u57FA\u672C\u4E0A\u4E5F\u56F4\u7ED5\u8FD9\u4E00\u70B9\u5C55\u5F00\u3002\u800C\u5F53\u65F6\u7684\u60C5\u51B5\u662F\uFF0C\u5BF9\u4E8E\u5357\u5F81\u8FD9\u4EF6\u4E8B\uFF0C\u671D\u5802\u4E4B\u4E0A\u51E0\u4E4E\u6CA1\u6709\u4EBA\u53CD\u5BF9\u3002</p><p>\u4E3A\u4EC0\u4E48\uFF1F</p><p>\u8FD9\u5176\u4E2D\u6709\u4E00\u4E2A\u91CD\u8981\u7684\u80CC\u666F\uFF1A\u4E09\u5E74\u524D\uFF0C\u51AF\u592A\u540E\u53BB\u4E16\uFF0C24\u5C81\u7684\u5B5D\u6587\u5E1D\u4EB2\u653F\u3002\u5982\u4ECA\uFF0C\u4E09\u5E74\u4E27\u671F\u521A\u8FC7\uFF0C\u5B5D\u6587\u5E1D\u5C31\u8981\u5357\u5F81\u3002</p><p>**\u6240\u4EE5\u7FA4\u81E3\u7406\u6240\u5E94\u5F53\u5730\u8BA4\u4E3A\uFF0C\u5B5D\u6587\u5E1D\u6B64\u4E3E\u662F\u60F3\u8981\u6811\u7ACB\u5A01\u4FE1\u3002**\u6BD5\u7ADF\u6B64\u65F6\u4ED6\u624D\u53EA\u670927\u5C81\uFF0C\u5E74\u8F7B\u4EBA\u561B\uFF0C\u597D\u51B2\u52A8\u53EF\u4EE5\u7406\u89E3\uFF0C\u60F3\u8981\u6811\u7ACB\u5A01\u4FE1\u7684\u610F\u56FE\u4E5F\u53EF\u4EE5\u7406\u89E3\u3002</p><p>\u800C\u4E14\uFF0C\u6240\u8C13\u5357\u5F81\u4E91\u4E91\uFF0C\u8FD9\u4E2A\u95EE\u9898\u53EF\u5927\u53EF\u5C0F\uFF0C\u5C31\u770B\u4F60\u600E\u4E48\u770B\u4E86\u3002\u8FD9\u4E48\u5927\u7684\u519B\u4E8B\u884C\u52A8\uFF0C\u76EE\u6807\u662F\u4EC0\u4E48\uFF1F\u706D\u4EA1\u5357\u9F50\uFF1F\u80AF\u5B9A\u4E0D\u73B0\u5B9E\u3002\u65E2\u7136\u5B5D\u6587\u5E1D\u662F\u60F3\u8981\u7ACB\u5A01\uFF0C\u90A3\u4E48\u6709\u4E2A\u4E0D\u9519\u7684\u6218\u7EE9\u5C31\u8DB3\u591F\u4E86\uFF0C\u800C\u8FD9\u5BF9\u4E8E\u5317\u9B4F\u8FD9\u79CD\u4E45\u7ECF\u6218\u9635\u7684\u5927\u56FD\u800C\u8A00\uFF0C\u53C8\u4E0D\u662F\u4EC0\u4E48\u96BE\u4E8B\uFF0C\u4E5F\u6CA1\u6709\u591A\u5C11\u98CE\u9669\u3002</p><p>\u8FD9\u79CD\u60C5\u51B5\u4E0B\uFF0C\u7687\u5E1D\u6025\u543C\u543C\u7684\u60F3\u505A\u70B9\u4E8B\uFF0C\u4E8E\u56FD\u65E0\u5BB3\u4E8E\u541B\u6709\u5229\uFF0C\u4F60\u4E00\u4E2A\u5927\u81E3\uFF0C\u4E0D\u4F53\u4F1A\u7687\u5E1D\u51B3\u7B56\u80CC\u540E\u7684\u826F\u82E6\u7528\u5FC3\uFF0C\u53CD\u5012\u8FD8\u7AD9\u51FA\u6765\u6A2A\u52A0\u963B\u6320\uFF0C\u4F60\u89C9\u5F97\u7687\u5E1D\u4F1A\u600E\u4E48\u60F3\u4F60\uFF1F\u4F60\u5C31\u771F\u4E0D\u6015\u7687\u5E1D\u628A\u4F60\u5F53\u9E21\uFF0C\u6740\u4E86\u7ED9\u7334\u770B\uFF1F</p><p>\u56E0\u6B64\uFF0C\u7FA4\u81E3\u7684\u7F04\u9ED8\u663E\u5F97\u5341\u5206\u9ED8\u5951\u800C\u53C8\u6B63\u5E38\u3002\u867D\u7136\u6709\u4E9B\u5927\u81E3\u8179\u8BFD\u7687\u5E1D\u7684\u8FD9\u6B21\u5357\u5F81\u9635\u4ED7\u592A\u5927\uFF0C\u4F46\u8C01\u90FD\u6E05\u695A\uFF0C\u4E0D\u503C\u5F97\u4E3A\u4E86\u8FD9\u70B9\u4E8B\u5C31\u626B\u7687\u5E1D\u7684\u5174\u81F4\u3002</p><p>\u4E0D\u8FC7\uFF0C\u4E5F\u4E0D\u662F\u4E00\u4E2A\u53CD\u5BF9\u7684\u4EBA\u90FD\u6CA1\u6709\uFF0C**\u4EFB\u57CE\u738B<code>\u62D3\u8DCB\u6F84</code>**\u5C31\u7AD9\u4E86\u51FA\u6765\u3002</p><p>\u62D3\u8DCB\u6F84\u662F\u5B5D\u6587\u5E1D\u7684\u5802\u53D4\u7236\uFF0C\u56E0\u4E3A\u5176\u7236\u62D3\u8DCB\u4E91\u6DF1\u53D7\u51AF\u592A\u540E\u7684\u4FE1\u4EFB\uFF0C\u6240\u4EE5\u4ED6\u4EEC\u8FD9\u4E00\u7CFB\u5B97\u5BA4\u66F4\u663E\u4EB2\u8D35\u2014\u2014\u800C\u8FD9\uFF0C\u6B63\u4E5F\u662F\u62D3\u8DCB\u6F84\u6562\u4E8E\u53CD\u5BF9\u7684\u539F\u56E0\u3002</p><p>\u9762\u5BF9\u7740\u62D3\u8DCB\u6F84\u7684\u53CD\u5BF9\uFF0C\u5B5D\u6587\u5E1D\u5F88\u751F\u6C14\uFF0C\u4ED6\u8D28\u95EE\u62D3\u8DCB\u6F84\uFF1A\u793E\u7A37\u662F\u6715\u7684\u793E\u7A37\uFF0C\u8DDF\u4F60\u6709\u4EC0\u4E48\u5173\u7CFB\uFF01</p><p>\u6CA1\u60F3\u5230\u62D3\u8DCB\u6F84\u6BEB\u4E0D\u9000\u7F29\uFF0C\u5F53\u4F17\u9876\u4E86\u56DE\u53BB\uFF1A\u793E\u7A37\u786E\u5B9E\u662F\u965B\u4E0B\u60A8\u7684\u793E\u7A37\uFF0C\u4F46\u6211\u662F\u793E\u7A37\u4E4B\u81E3\uFF0C\u600E\u4E48\u80FD\u660E\u77E5\u5371\u9669\u800C\u4E0D\u51FA\u8A00\u963B\u6B62\uFF01</p><p>\u541B\u81E3\u5BF9\u8BDD\u81F3\u6B64\uFF0C\u518D\u65E0\u8F6C\u571C\u4F59\u5730\uFF0C\u53EA\u5F97\u9000\u671D\u3002</p><p><strong>\u4E0D\u8FC7\u5B5D\u6587\u5E1D\u5E76\u4E0D\u662F\u771F\u7684\u751F\u6C14</strong>\uFF0C\u5728\u6563\u671D\u540E\uFF0C\u4ED6\u628A\u62D3\u8DCB\u6F84\u53EB\u6765\uFF0C\u5355\u72EC\u8DDF\u4ED6\u4ED4\u7EC6\u89E3\u91CA\u4E86\u201C\u5357\u5F81\u201D\u7684\u771F\u6B63\u610F\u56FE\uFF1A</p><p>\u201C\u56FD\u5BB6\u5174\u81EA\u6714\u571F\uFF0C\u5F99\u5C45\u5E73\u57CE\uFF1B\u6B64\u4E43\u7528\u6B66\u4E4B\u5730\uFF0C\u975E\u53EF\u6587\u6CBB\u3002\u4ECA\u5C06\u79FB\u98CE\u6613\u4FD7\uFF0C\u5176\u9053\u8BDA\u96BE\uFF0C\u6715\u6B32\u56E0\u6B64\u8FC1\u5B85\u4E2D\u539F\uFF0C\u537F\u4EE5\u4E3A\u4F55\u5982\uFF1F\u201D\u2014\u2014<code>\u6211\u56FD\u5174\u8D77\u4E8E\u6714\u5317\uFF0C\u56E0\u6B64\u5B9A\u90FD\u5E73\u57CE\u3002\u6B64\u5730\u53EF\u4EE5\u7528\u6B66\uFF0C\u4F46\u96BE\u6587\u6CBB\u3002\u5982\u4ECA\u6211\u60F3\u79FB\u98CE\u6613\u4FD7\uFF0C\u56E0\u6B64\u6253\u7B97\u8FC1\u90FD\u4E2D\u539F\uFF0C\u4F60\u4EE5\u4E3A\u5982\u4F55\uFF1F</code></p><p>\u62D3\u8DCB\u6F84\u7565\u5FAE\u4E00\u60F3\uFF0C\u4FBF\u5DF2\u660E\u767D\u5176\u4E2D\u7684\u542B\u4E49\uFF0C\u968F\u5373\u9644\u548C\uFF1A\u201C\u535C\u5B85\u4E2D\u571F\uFF0C\u4EE5\u7ECF\u7565\u56DB\u6D77\uFF0C\u6B64\u5468\u3001\u6C49\u4E4B\u6240\u4EE5\u5174\u9686\u4E5F\u3002\u201D\u2014\u2014<code>\u4E8E\u4E2D\u539F\u5EFA\u90FD\uFF0C\u8FD9\u662F\u5468\u6C49\u5174\u9686\u7684\u539F\u56E0\u554A\uFF01</code></p><p>\u5B5D\u6587\u5E1D\u53C8\u95EE\uFF1A\u201C\u5317\u4EBA\u4E60\u5E38\u604B\u6545\uFF0C\u5FC5\u5C06\u60CA\u6270\uFF0C\u5948\u4F55\uFF1F\u201D\u2014\u2014<code>\u6211\u65CF\u4EBA\u4E60\u60EF\u604B\u65E7\u751F\u6D3B\u4E8E\u6B64\uFF0C\u80AF\u5B9A\u4F1A\u6709\u53CD\u5BF9\uFF0C\u8FD9\u8BE5\u600E\u4E48\u529E\uFF1F</code></p><p>\u62D3\u8DCB\u6F84\u56DE\u590D\uFF1A\u201C\u975E\u5E38\u4E4B\u4E8B\uFF0C\u6545\u975E\u5E38\u4EBA\u4E4B\u6240\u53CA\u3002\u965B\u4E0B\u65AD\u81EA\u5723\u5FC3\uFF0C\u5F7C\u4EA6\u4F55\u6240\u80FD\u4E3A\uFF01\u201D\u2014\u2014<code>\u8FD9\u79CD\u5927\u4E8B\uFF0C\u60A8\u53EA\u8981\u81EA\u5DF1\u4E0B\u5B9A\u51B3\u5FC3\uFF0C\u522B\u4EBA\u5C31\u6CA1\u6709\u529E\u6CD5\u53CD\u5BF9\uFF01</code></p><p>\u8FC1\u90FD\u4E00\u4E8B\uFF0C\u5C31\u6B64\u51B3\u5B9A\u3002</p><p>\u6B64\u540E\uFF0C\u5728\u671D\u5802\u4E4B\u4E0A\uFF0C\u4EFB\u57CE\u738B\u62D3\u8DCB\u6F84\u4E00\u6539\u5E38\u6001\uFF0C\u5F00\u59CB\u652F\u6301\u5B5D\u6587\u5E1D\u7684\u5357\u5F81\u3002\u7FA4\u81E3\u4E00\u770B\u4EFB\u57CE\u738B\u6001\u5EA6\u8F6C\u53D8\u5982\u6B64\u4E4B\u5FEB\uFF0C\u4E5F\u4E0D\u77E5\u9053\u79C1\u4E0B\u91CC\u8DDF\u5B5D\u6587\u5E1D\u8FBE\u6210\u4E86\u4EC0\u4E48\u79D8\u5BC6\u4EA4\u6613\u3002\u4F46\u65E2\u7136\u5982\u4ECA\u8FDE\u7687\u5BA4\u91CD\u81E3\u90FD\u4E0D\u7275\u5934\u53CD\u5BF9\u4E86\uFF0C\u90A3\u81EA\u5DF1\u81EA\u7136\u5C31\u66F4\u4E0D\u6562\u6A2A\u52A0\u53CD\u5BF9\u4E86\u3002</p><p>\u592A\u548C\u5341\u4E03\u5E74\uFF08\u516C\u5143493\u5E74\uFF09\u4E03\u6708\uFF0C\u5317\u9B4F\u6B63\u5F0F\u51B3\u5B9A\uFF0C\u5357\u5F81\u3002</p><p>\u4F5C\u4E3A\u4E00\u4E2A\u519B\u4E8B\u5E1D\u56FD\uFF0C\u5317\u9B4F\u7684\u884C\u653F\u6548\u7387\u6781\u9AD8\u3002<code>\u516B\u6708\u521D\u4E5D</code>\uFF0C\u5B5D\u6587\u5E1D\u81F3\u51AF\u592A\u540E\u6C38\u56FA\u9675\u8F9E\u9675\uFF1B<code>\u5341\u4E00\u65E5</code>\uFF0C\u5927\u519B\u4ECE\u5E73\u57CE\u51FA\u53D1\u3002</p><p><code>\u4E5D\u6708\u4E8C\u5341\u65E5</code>\uFF0C\u5927\u519B\u6E21\u8FC7\u9EC4\u6CB3\u3002<code>\u4E8C\u5341\u4E8C\u65E5</code>\uFF0C\u5B5D\u6587\u5E1D\u81F3\u6D1B\u9633\u3002<code>\u4E8C\u5341\u56DB\u65E5</code>\uFF0C\u5E1D\u81F3\u592A\u5B66\u89C2\u300A\u77F3\u7ECF\u300B\u3002</p><p>**\u5728\u8FDB\u5165\u6CB3\u5357\u4EE5\u540E\uFF0C\u8FDE\u65E5\u66B4\u96E8\uFF0C\u519B\u58EB\u75B2\u655D\uFF0C\u5F3A\u884C\u8FDB\u519B\u5B9E\u5728\u96BE\u4EE5\u4E3A\u7EE7\u3002**\u4F46\u4ECE\u540D\u4E49\u4E0A\u6765\u8BF4\uFF0C\u6D1B\u9633\u5E76\u4E0D\u662F\u6B64\u884C\u7684\u7EC8\u70B9\uFF0C\u800C\u662F\u4E2D\u9014\u3002\u56E0\u6B64\uFF0C<code>\u4E5D\u6708\u4E8C\u5341\u516B\u65E5</code>\uFF0C\u5B5D\u6587\u5E1D\u4E0B\u8BCF\uFF1A\u5168\u519B\u7EE7\u7EED\u524D\u8FDB\u2014\u2014\u6B64\u4E3E\u5F53\u7136\u662F\u5B5D\u6587\u5E1D\u6545\u610F\u4E3A\u4E4B\u3002\u4F46\u5728\u5FC3\u91CC\uFF0C\u4ED6\u53EF\u80FD\u662F\u5F88\u611F\u8C22\u8FD9\u573A\u53CA\u65F6\u96E8\u7684\u3002</p><p>\u800C\u5230\u4E86<code>\u4E8C\u5341\u4E5D\u65E5</code>\uFF0C\u5B5D\u6587\u5E1D\u620E\u88C5\u51FA\u5E10\uFF0C\u53D1\u73B0\u6587\u6B66\u5927\u81E3\u90FD\u7B49\u5728\u5E10\u5916\u3002</p><p>\u5B5D\u6587\u5E1D\u95EE\uFF1A\u4F60\u4EEC\u8FD9\u662F\u8981\u963B\u6320\u56FD\u5BB6\u5927\u4E8B\u5417\uFF1F\uFF01</p><p>\u5C1A\u4E66<code>\u674E\u51B2</code>\u8BF4\uFF0C\u91CD\u5175\u4EB2\u5F81\u8FD9\u7B49\u56FD\u5BB6\u5927\u4E8B\uFF0C\u5168\u56FD\u4E0A\u4E0B\u90FD\u4E0D\u613F\u610F\uFF0C\u5C31\u965B\u4E0B\u60A8\u4E00\u4E2A\u4EBA\u60F3\u8981\u5982\u6B64\u3002\u6211\u4EEC\u4E5F\u4E0D\u77E5\u9053\u965B\u4E0B\u60A8\u8FD9\u822C\u4E00\u610F\u5B64\u884C\uFF0C\u76EE\u7684\u5230\u5E95\u4F55\u5728\uFF0C\u6240\u4EE5\u4E5F\u6CA1\u6709\u4EC0\u4E48\u8BDD\u80FD\u8BF4\uFF0C\u53EA\u80FD\u662F\u5192\u6B7B\u8BF7\u965B\u4E0B\u4E09\u601D\u3002</p><p>\u5B5D\u6587\u5E1D\u8BF4\uFF0C\u6211\u8981\u7ECF\u8425\u5929\u4E0B\uFF0C\u6DF7\u540C\u57DF\u5185\uFF0C\u4F60\u4EEC\u8FD9\u4E9B\u8150\u5112\u603B\u662F\u6A2A\u52A0\u963B\u6320\uFF0C\u771F\u7684\u4E0D\u6015\u6740\u5934\u4E48\uFF01</p><p>\u7FA4\u81E3\u4F9D\u7136\u82E6\u8C0F\u3002</p><p>\u773C\u770B\u7740\u65F6\u95F4\u548C\u6C14\u6C1B\u90FD\u70D8\u6258\u5F97\u5DEE\u4E0D\u591A\u4E86\uFF0C<strong>\u5B5D\u6587\u5E1D\u7A81\u7136\u53E3\u6C14\u4E00\u8F6C\uFF0C\u6001\u5EA6\u51FA\u73B0\u4E86\u677E\u52A8</strong>\uFF1A\u5509\uFF0C\u6211\u5982\u4ECA\u641E\u4E86\u8FD9\u4E48\u5927\u7684\u52A8\u9759\uFF0C\u7A81\u7136\u5C31\u653E\u5F03\u4E86\uFF0C\u8FD9\u4F1A\u8BA9\u5BF9\u9762\u600E\u4E48\u770B\u6211\uFF0C\u8BA9\u540E\u4E16\u600E\u4E48\u770B\u6211\uFF01</p><p>\u7FA4\u81E3\u9762\u9762\u76F8\u89D1\uFF0C\u4E0D\u77E5\u4F55\u610F\u3002</p><p>\u5B5D\u6587\u5E1D\u63A5\u7740\u53C8\u7EE7\u7EED\u5F80\u540E\u9000\uFF0C\u8BF4\uFF1A\u90A3\u8981\u4E0D\u8FD9\u6837\u5427\uFF0C<strong>\u53CD\u6B63\u6211\u4EEC\u90FD\u5230\u6D1B\u9633\u4E86\uFF0C\u8981\u4E0D\u5C31\u8FC1\u90FD\u5230\u8FD9\u91CC\uFF0C\u522B\u56DE\u5E73\u57CE\u4E86</strong>\u2026\u2026\u597D\u4E86\uFF01\u5C31\u8FD9\u4E48\u4E24\u6761\u8DEF\uFF0C\u8981\u4E48\u7EE7\u7EED\u5357\u5F81\uFF0C\u540C\u610F\u7684\u7AD9\u53F3\u8FB9\uFF1B\u8981\u4E48\u8FC1\u90FD\u4E8E\u6B64\uFF0C\u652F\u6301\u7684\u7AD9\u5DE6\u8FB9\uFF0C\u4F60\u4EEC\u9009\u5427\uFF01</p><p>\u7FA4\u81E3\u8FD8\u5728\u7591\u60D1\uFF0C\u53EA\u89C1\u4EFB\u57CE\u738B\u62D3\u8DCB\u6F84\u201C\u8E6D\u201D\u7684\u4E00\u4E0B\uFF0C\u4E00\u6E9C\u70DF\u5C31\u8DD1\u5230\u4E86\u5DE6\u8FB9\u7AD9\u7740\uFF0C\u65C1\u82E5\u65E0\u4EBA\u3002</p><p>\u5927\u81E3\u4EEC\u4E00\u770B\uFF0C\u5927\u7EA6\u4FBF\u77E5\u9053\u4E86\u5185\u60C5\uFF0C\u6562\u60C5\u7687\u5E1D\u65E9\u5C31\u6253\u5B9A\u4E86\u4E3B\u610F\uFF1A<strong>\u5357\u5F81\u4E0D\u8FC7\u662F\u5E4C\u5B50\uFF0C\u8FC1\u90FD\u624D\u662F\u76EE\u7684\u3002</strong></p><p>\u4E8B\u5DF2\u81F3\u6B64\uFF0C\u5C31\u4E0D\u662F\u53CD\u5BF9\u4E0D\u53CD\u5BF9\u7684\u4E8B\u60C5\u4E86\uFF0C\u800C\u662F\u4E24\u5BB3\u76F8\u6743\u53D6\u5176\u8F7B\u7684\u95EE\u9898\u3002\u518D\u8BF4\u4E86\uFF0C\u5E73\u57CE\u4F5C\u4E3A\u9996\u90FD\u7684\u8BF8\u591A\u7F3A\u70B9\uFF0C\u671D\u4E2D\u5927\u81E3\u65E9\u5DF2\u5FC3\u77E5\u809A\u660E\uFF0C\u8FC1\u90FD\u672A\u5C1D\u4E0D\u662F\u4E00\u4EF6\u597D\u4E8B\u3002</p><p>\u6700\u7EC8\uFF0C\u7FA4\u81E3\u601D\u8651\u534A\u664C\uFF0C\u7EB7\u7EB7\u7AD9\u5230\u5DE6\u8FB9\u8868\u793A\u652F\u6301\u3002</p><p>\u8FC1\u90FD\u4E00\u4E8B\uFF0C\u5C31\u6B64\u51B3\u5B9A\u3002</p><p>\u63A5\u4E0B\u6765\uFF0C\u5C31\u662F\u4E00\u4E9B\u626B\u5C3E\u7684\u4E8B\u4E86\uFF1A\u5B5D\u6587\u5E1D\u6D3E\u4EFB\u57CE\u738B\u62D3\u8DCB\u6F84\u56DE\u5230\u5E73\u57CE\uFF0C\u5C06\u8FC1\u90FD\u7684\u51B3\u5B9A\u544A\u77E5\u7ED9\u9A7B\u7559\u4E8E\u5E73\u57CE\u7684\u738B\u516C\u5927\u81E3\u3002\u8FD9\u4E9B\u4EBA\u5C3D\u7BA1\u518D\u53CD\u5BF9\uFF0C\u4E5F\u65E0\u6D4E\u4E8E\u4E8B\u3002</p><p>\u8FD8\u80FD\u600E\u4E48\u529E\u5462\uFF1F\u8DDF\u4EFB\u57CE\u738B\u8BF4\u5417\uFF1F\u4ED6\u53EA\u662F\u5B5D\u6587\u5E1D\u7684\u4F20\u58F0\u7B52\uFF0C\u518D\u600E\u4E48\u8DDF\u4ED6\u56B7\u56B7\u4E5F\u6CA1\u7528\u3002\u4E0A\u4E66\u7ED9\u5B5D\u6587\u5E1D\uFF1F\u5B5D\u6587\u5E1D\u80AF\u5B9A\u5F53\u6CA1\u770B\u5230\u3002\u53BB\u6D1B\u9633\u529D\u5B5D\u6587\u5E1D\uFF1F\u5B5D\u6587\u5E1D\u4E00\u51C6\u628A\u4F60\u7559\u5728\u6D1B\u9633\uFF0C\u8BA9\u4F60\u518D\u4E5F\u56DE\u4E0D\u4E86\u5E73\u57CE\u3002</p><p>\u4E00\u5E74\u540E\uFF0C\u6D1B\u9633\u8425\u9020\u5B8C\u5DE5\uFF0C<strong>\u5317\u9B4F\u6B63\u5F0F\u8FC1\u90FD\u6D1B\u9633\u3002</strong></p><p>\u5728\u6211\u4EEC\u60EF\u5E38\u610F\u4E49\u4E0A\u6240\u4E86\u89E3\u7684\u5317\u9B4F\u5B5D\u6587\u5E1D\u6539\u9769\u4E2D\uFF0C\u8FC1\u90FD\u6D1B\u9633\u662F\u6807\u5FD7\u6027\u4E8B\u4EF6\u3002\u4F46\u5176\u5B9E\uFF0C\u5B83\u5E76\u4E0D\u662F\u6539\u9769\u7684\u5168\u90E8\u3002\u771F\u6B63\u7684\u6539\u9769\u5185\u5BB9\uFF0C\u662F\u6211\u4EEC\u4E0B\u4E00\u7BC7\u8981\u8BB2\u5230\u7684\u6C49\u5316\u6539\u9769\u3002</p><p><strong>\u8FC1\u90FD\uFF0C\u4EC5\u4EC5\u662F\u5176\u4E2D\u7684\u4E00\u4E2A\u524D\u594F\u548C\u5148\u58F0\u3002</strong></p><blockquote><p><strong>\u5317\u9B4F\u4E3A\u4EC0\u4E48\u8981\u8FC1\u90FD\uFF1F</strong></p></blockquote><p>\u5317\u9B4F\u5B9A\u90FD\u5E73\u57CE\uFF08\u4ECA\u5C71\u897F\u5927\u540C\uFF09\uFF0C\u662F**\u5F00\u56FD\u4E4B\u541B<code>\u9053\u6B66\u5E1D</code>**\u65F6\u671F\u7684\u4E8B\u3002\u5F53\u65F6\uFF0C\u5317\u9B4F\u7684\u56FD\u571F\u57FA\u672C\u4E0A\u8FD8\u5728\u957F\u57CE\u4EE5\u5916\uFF0C\u5E73\u57CE\u4E4B\u4E8E\u5317\u9B4F\uFF0C\u5DF2\u7ECF\u5C31\u662F\u5357\u65B9\u7684\u5927\u57CE\u5E02\u4E86\u2014\u2014\u8FD9\u4E5F\u53EF\u4EE5\u770B\u4F5C\u662F\u9053\u6B66\u5E1D\u79EF\u6781\u8FDB\u53D6\u4E2D\u539F\u7684\u8DF3\u677F\u3002</p><p>\u53C8\u8FC7\u4E86\u51E0\u5341\u5E74\uFF0C\u5317\u9B4F\u7684\u4E09\u4EE3\u76EE**\u592A\u6B66\u5E1D<code>\u62D3\u8DCB\u7118</code>**\u7EDF\u4E00\u4E86\u5317\u65B9\u3002\u6B64\u65F6\u518D\u770B\u5E73\u57CE\uFF0C\u5C31\u7A0D\u5FAE\u6709\u4E00\u70B9\u504F\u79BB\u56FD\u4E2D\u4E86\u3002</p><p>\u4E8B\u5B9E\u4E0A\uFF0C\u5728\u6B64\u4E4B\u524D\uFF0C\u5E73\u57CE\u592A\u9760\u5317\u65B9\u7684\u95EE\u9898\u5C31\u5DF2\u7ECF\u88AB\u8BA8\u8BBA\u8FC7\u3002\u67D0\u5E74\uFF0C\u5E73\u57CE\u4E00\u5E26\u66FE\u9047\u5927\u65F1\uFF0C\u671D\u5EF7\u5F53\u65F6\u5C31\u6709\u8FC7\u8BA8\u8BBA\uFF1A\u662F\u5426\u8981\u8FC1\u90FD\u5230\u90BA\u57CE\uFF08\u5F7C\u65F6\u5317\u9B4F\u5C1A\u672A\u6D88\u706D\u540E\u79E6\uFF0C\u56E0\u6B64\u8FB9\u754C\u7684\u6D1B\u9633\u5E76\u4E0D\u5728\u8003\u8651\u8303\u56F4\u4E4B\u5185\uFF09\u3002</p><p>\u4F46<code>\u5D14\u6D69</code>\u5374\u7ED9\u51FA\u4E86\u53CD\u5BF9\u610F\u89C1\uFF0C\u539F\u56E0\u4E00\u5171\u6709\u4E24\u6761\uFF1A</p><p><strong>\u9996\u5148</strong>\uFF0C\u5F53\u65F6\u5E1D\u56FD\u7684\u4E3B\u8981\u5A01\u80C1\u662F\u5317\u8FB9\u7684\u67D4\u7136\u3002\u67D4\u7136\u5BC7\u8FB9\uFF0C\u5B9A\u90FD\u4E8E\u5E73\u57CE\u80FD\u53CA\u65F6\u54CD\u5E94\u2014\u2014\u8FD9\u5F88\u6709\u70B9\u5929\u5B50\u5B88\u56FD\u95E8\u7684\u610F\u601D\u5728\u91CC\u9762\u3002</p><p><strong>\u5176\u6B21</strong>\uFF0C\u5317\u9B4F\u5F53\u65F6\u7684\u7EDF\u6CBB\u5E76\u4E0D\u7A33\u56FA\u3002\u4E2D\u539F\u5927\u5730\u4E8E\u5317\u9B4F\u800C\u8A00\uFF0C\u66F4\u50CF\u662F\u5360\u9886\u533A\u3002\u5728\u8FD9\u79CD\u903B\u8F91\u4E0B\uFF0C\u4E2D\u539F\u5E7F\u5927\u7684\u6C49\u4EBA\uFF0C\u90FD\u662F\u6F5C\u5728\u7684\u654C\u4EBA\u3002\u5982\u679C\u62D3\u8DCB\u6C0F\u6DF1\u5165\u6C49\u5730\uFF0C\u90A3\u4E48\u5F88\u5BB9\u6613\u4F1A\u88AB\u6C49\u4EBA\u770B\u6E05\u695A\u865A\u5B9E\u3002</p><p>\u7559\u5728\u5E73\u57CE\uFF0C\u65E2\u53EF\u4EE5\u9632\u5907\u67D4\u7136\uFF0C\u53C8\u53EF\u4EE5\u5BF9\u6C49\u5730\u9020\u6210\u8DB3\u591F\u7684\u519B\u4E8B\u538B\u529B\uFF0C\u8FD9\u5C31\u662F\u5F53\u65F6\u4F9D\u7136\u9009\u62E9\u7EE7\u7EED\u5B9A\u90FD\u5728\u5E73\u57CE\u7684\u7406\u7531\u3002</p><p><strong>\u4F46\u8FD9\u5DF2\u7ECF\u662F\u5B5D\u6587\u5E1D\u8FC1\u90FD\u524D\u516B\u5341\u5E74\u524D\u7684\u8BA8\u8BBA\u4E86</strong>\u3002\u5230\u4E86\u5B5D\u6587\u5E1D\u65F6\u671F\uFF0C\u95EE\u9898\u5DF2\u7ECF\u5168\u7136\u4E0D\u662F\u8FD9\u4E9B\u95EE\u9898\uFF0C\u56FD\u90FD\u6B63\u8D8A\u6765\u8D8A\u6210\u4E3A\u5317\u9B4F\u8FD9\u4E2A\u56FD\u5BB6\u524D\u8FDB\u9053\u8DEF\u4E0A\u7684\u969C\u788D\u3002</p><p><strong>\u9996\u5148\uFF0C\u662F\u519B\u4E8B\u76EE\u6807\u7684\u53D8\u5316\u3002</strong></p><p>\u4E4B\u524D\uFF0C\u5317\u9B4F\u7684\u4E3B\u8981\u519B\u4E8B\u5A01\u80C1\u662F\u5317\u65B9\u7684\u67D4\u7136\uFF0C\u5176\u6B21\u662F\u5357\u671D\u3002\u4F46\u73B0\u5728\u770B\u6765\uFF0C\u4E24\u8005\u7684\u987A\u5E8F\u9700\u8981\u5BF9\u8C03\u4E00\u4E0B\u4E86\u3002</p><p>\u5317\u9B4F\u653F\u6743\u968F\u7740\u6C49\u5316\u7A0B\u5EA6\u7684\u9010\u6E10\u52A0\u6DF1\uFF0C\u6B63\u7EDF\u738B\u671D\u7684\u610F\u8BC6\u4E5F\u8D8A\u6765\u8D8A\u5F3A\u70C8\uFF0C\u56E0\u6B64\uFF0C\u6DF7\u540C\u5B87\u5185\uFF0C\u653B\u706D\u5357\u671D\u5C31\u53D8\u6210\u4E86\u4E3B\u8981\u519B\u4E8B\u76EE\u6807\u3002\u800C\u67D4\u7136\uFF0C\u591A\u5E74\u4EE5\u6765\u5728\u5317\u9B4F\u7684\u6253\u51FB\u4E0B\u5DF2\u7ECF\u5F0F\u5FAE\uFF0C\u4E0D\u518D\u662F\u4E3B\u8981\u76EE\u6807\u3002</p><p>\u800C\u5982\u679C\u76EE\u6807\u5728\u5357\u671D\uFF0C\u90A3\u5E73\u57CE\u5C31\u592A\u8FDC\u4E86\u3002</p><p><strong>\u5176\u6B21\uFF0C\u662F\u7269\u8D44\u8FD0\u8F93\u4E0A\u7684\u5B9E\u9645\u56F0\u96BE\u3002</strong></p><p>\u9996\u90FD\u6709\u5927\u91CF\u7684\u519B\u961F\u548C\u975E\u52B3\u52A8\u4EBA\u53E3\uFF0C\u5F88\u96BE\u81EA\u7ED9\u81EA\u8DB3\uFF0C\u9700\u8981\u5185\u5730\u5C06\u5927\u91CF\u7684\u7CAE\u98DF\u8F6C\u8FD0\u81F3\u6B64\u3002\u5982\u4ECA\u968F\u7740\u5317\u9B4F\u5F3A\u5927\uFF0C\u5E73\u57CE\u7684\u4EBA\u53E3\u8D8A\u6765\u8D8A\u591A\uFF0C\u8FD0\u8F93\u91CF\u4E5F\u4E0E\u65E5\u4FF1\u589E\u3002</p><p>\u4F46\u662F\uFF0C\u4E2D\u539F\u5230\u5E73\u57CE\u5374\u8FD8\u6CA1\u6709\u6C34\u8DEF\uFF0C\u53EA\u80FD\u9760\u9646\u8DEF\u8FD0\u8F93\uFF0C\u8FD9\u6781\u5927\u5730\u9650\u5236\u4E86\u5E73\u57CE\u7684\u53D1\u5C55\u3002</p><p>\u540C\u65F6\uFF0C\u5E73\u57CE\u6240\u5728\u7684\u96C1\u5317\u5730\u533A\u5E73\u539F\u9762\u79EF\u6709\u9650\uFF0C\u4EBA\u53E3\u589E\u591A\u4EE5\u540E\uFF0C\u7A0D\u6709\u6B49\u6536\uFF0C\u5373\u9047\u707E\u8352\u3002\u53F2\u4E66\u8BB0\u8F7D\uFF0C\u5230\u4E86\u5317\u9B4F\u4E2D\u671F\uFF0C\u5E73\u57CE\u4E00\u4EE3\u7ECF\u5E38\u6027\u7684\u51FA\u73B0\u9965\u8352\uFF0C\u8FD9\u5E76\u4E0D\u662F\u56E0\u4E3A\u6D41\u5E74\u4E0D\u5229\uFF0C\u800C\u662F\u4EBA\u592A\u591A\u4E86\u3002</p><p>**\u6700\u540E\uFF0C\u5B5D\u6587\u5E1D\u60F3\u63A8\u884C\u6C49\u5316\u6539\u9769\uFF0C\u5E73\u57CE\u5374\u662F\u9C9C\u5351\u4EBA\u7684\u805A\u5C45\u533A\u3002**\u5728\u6B64\u5730\u5C55\u5F00\u64CD\u4F5C\uFF0C\u52BF\u5FC5\u4F1A\u906D\u5230\u5DE8\u5927\u7684\u53CD\u5BF9\u3002</p><p>\u8FC1\u90FD\u4E00\u4E8B\u52BF\u5728\u5FC5\u884C\u3002</p><h2 id="\u71B9\u5E73\u77F3\u7ECF" tabindex="-1"><a class="header-anchor" href="#\u71B9\u5E73\u77F3\u7ECF" aria-hidden="true">#</a> \u71B9\u5E73\u77F3\u7ECF</h2><p>\u81EA\u6C49\u6B66\u5E1D\u7F62\u9EDC\u767E\u5BB6\u4EE5\u6765\uFF0C\u5112\u5B66\u6210\u4E3A\u5BD2\u95E8\u5B66\u58EB\u8FDB\u9636\u7684\u9014\u5F84\u3002\u4F46\u5728\u5F53\u65F6\uFF0C\u5370\u5237\u672F\u5C1A\u672A\u53D1\u660E\uFF0C\u9020\u7EB8\u672F\u53C8\u8FD8\u4E0D\u53D1\u8FBE\uFF0C\u5112\u5BB6\u7ECF\u5178\u7684\u4F20\u64AD\uFF0C\u5168\u9760\u4F20\u6284\u3002</p><p>\u800C\u4F20\u6284\u7684\u8FC7\u7A0B\uFF0C\u53C8\u5F88\u5BB9\u6613\u51FA\u9519\u3002</p><p>\u8FD9\u79CD\u60C5\u51B5\u4E0B\uFF0C\u71B9\u5E73\u77F3\u7ECF\u5E94\u8FD0\u800C\u751F\u3002\u4F60\u53EF\u4EE5\u628A\u5B83\u7406\u89E3\u4E3A\uFF0C\u8FD9\u5C31\u662F\u56FD\u5BB6\u53D1\u5E03\u7684\u7EDF\u4E00\u6559\u6750\u3002</p><p><code>\u4E1C\u6C49\u7075\u5E1D</code>\u71B9\u5E73\u5E74\u95F4\uFF0C\u671D\u5EF7\u547D\u5927\u5112<code>\u8521\u9095</code>\u5C06\u5112\u5B66\u4E03\u4E66\uFF08\u300A\u8BD7\u300B\u300A\u4E66\u300B\u300A\u793C\u300B\u300A\u6613\u300B\u300A\u6625\u79CB\u300B\u300A\u8BBA\u8BED\u300B\u300A\u516C\u7F8A\u4F20\u300B\uFF09\u6284\u523B\u6210\u77F3\u4E66\uFF0C\u5171\u6210\u789146\u5757\uFF0C\u6BCF\u5757\u9AD83\u7C73\u4F59\uFF0C\u5BBD1\u7C73\u591A\uFF0C\u7ACB\u4E8E\u6D1B\u9633\u592A\u5B66\u4E4B\u5916\u3002</p><p>\u7891\u6210\u4E4B\u65E5\uFF0C\u5929\u4E0B\u8F70\u52A8\u3002\u636E\u53F2\u4E66\u8BB0\u8F7D\uFF0C\u201C<strong>\u5176\u89C2\u89C6\u53CA\u6479\u5199\u8005\uFF0C\u8F66\u4E58\u65E5\u5343\u4F59\u4E24\uFF08\u8F86\uFF09\uFF0C\u586B\u585E\u8857\u964C</strong>\u201D\u3002</p><p>\u4F46\u8FD9\u4E9B\u77F3\u7891\u7684\u547D\u8FD0\uFF0C\u5374\u9887\u4E3A\u574E\u5777\u3002</p><p>\u7891\u6210\u540E\u7684\u7B2C\u4E8C\u5E74\u5373\u7206\u53D1\u9EC4\u5DFE\u4E4B\u4E71\uFF0C\u540E\u6765\u7684\u5386\u53F2\uFF0C\u5C31\u662F\u6211\u4EEC\u6240\u719F\u77E5\u7684<code>\u8463\u5353</code>\u4E71\u653F\u3001\u7FA4\u96C4\u8BA8\u8463\u5353\u4E86\u3002\u800C\u8463\u5353\u5728\u64A4\u79BB\u6D1B\u9633\u524D\uFF0C\u70E7\u6BC1\u4E86\u6D1B\u9633\u5BAB\u5E99\u3002\u636E\u53F2\u6599\u8BB0\u8F7D\uFF0C\u5230\u51E0\u5341\u5E74\u540E<code>\u66F9\u4E15</code>\u5728\u4F4D\u65F6\uFF0C\u77F3\u7ECF\u5DF2\u7ECF\u9700\u8981\u4FEE\u8865\u4E86\u3002</p><p>\u800C\u8FD9\u8FD8\u4E0D\u662F\u7EC8\u70B9\uFF0C\u5176\u540E\uFF0C\u8FD9\u4E9B\u77F3\u7ECF\u53C8\u9971\u7ECF\u6218\u4E71\u3002</p><p>\u4EC5\u4EC5\u5728<code>\u5B5D\u6587\u5E1D</code>\u8EAB\u540E\u4E8C\u5341\u5E74\uFF0C\u6D1B\u9633\u57CE\u5185\u5927\u5EFA\u4F5B\u5BFA\uFF0C\u77F3\u7ECF\u88AB\u78E8\u5E73\u7528\u505A\u6750\u6599\u8005\u5C31\u4E0D\u53EF\u80DC\u6570\u3002</p><p>\u53C8\u8FC7\u4E86\u51E0\u5341\u5E74\u540E\uFF0C<code>\u5317\u9B4F</code>\u5206\u88C2\u4E3A<code>\u4E1C\u9B4F</code>\u4E0E<code>\u897F\u9B4F</code>\u3002\u4E1C\u9B4F\u8FC1\u90FD\u90BA\u57CE\uFF0C\u4E3A\u793A\u6B63\u7EDF\uFF0C\u5C06\u77F3\u7ECF\u7531\u6C34\u8DEF\u8FC1\u5F80\u90BA\u57CE\uFF0C\u7ED3\u679C\u534A\u9053\u8239\u8986\uFF0C\u5927\u7EA6\u4E00\u534A\u7684\u77F3\u7ECF\u843D\u5165\u6C34\u4E2D\u3002</p><p>\u5230\u4E86<code>\u968B\u671D</code>\u7EDF\u4E00\u540E\uFF0C\u4E3A\u8425\u5EFA\u65B0\u57CE\uFF0C\u77F3\u7ECF\u88AB\u8FD0\u5F80\u5927\u5174\u2014\u2014\u4F46\u662F\uFF0C\u5374\u662F\u88AB\u7528\u4F5C\u67F1\u5B50\u7684\u57FA\u77F3\u3002</p><p>\u4E4B\u540E\u53C8\u8FC7\u53BB\u4E86\u51E0\u5341\u5E74\u7684\u5149\u9634\uFF0C\u5230<code>\u9B4F\u5F81</code>\u53BB\u641C\u96C6\u65F6\uFF0C\u8FD9\u4E9B\u77F3\u7ECF\u5DF2\u7ECF\u88AB\u7834\u574F\u6B86\u5C3D\u3002</p><p>\u81EA\u5B8B\u4EE5\u6765\uFF0C\u95F4\u6216\u53D1\u73B0\u4E86\u90E8\u5206\u6B8B\u7247\u3002\u76EE\u524D\u5B58\u4E16\u7684\u71B9\u5E73\u77F3\u7ECF\u7684\u6B8B\u7247\u5B57\u6570\uFF0C\u636E\u8BF4\u572810000\u5B57\u4EE5\u4E0A\u3002</p><p>\u5728\u6211\u4EEC\u540E\u4E16\u770B\u6765\uFF0C\u8FD9\u4EF6\u627F\u8F7D\u7740\u534E\u590F\u5343\u5E74\u6587\u8109\u7684\u6587\u7269\uFF0C\u7740\u5B9E\u73CD\u8D35\u3002\u4E0D\u8FC7\u8FD9\u79CD\u611F\u60C5\uFF0C\u4E5F\u5E76\u4E0D\u662F\u4ECA\u65F6\u4ECA\u65E5\u624D\u6709\u3002<strong>\u4E8E\u5B5D\u6587\u5E1D\u800C\u8A00\uFF0C\u5B83\u4EEC\u540C\u6837\u662F\u6587\u7269</strong>\u2014\u2014\u5230\u4ED6\u90A3\u4F1A\u513F\uFF0C\u8FD9\u4E9B\u4E5F\u5DF2\u7ECF\u662F\u4E09\u767E\u5E74\u524D\u7684\u8001\u7269\u4EF6\u4E86\u3002</p><p>\u66F4\u4F55\u51B5\uFF0C\u8FD9\u4E9B\u77F3\u523B\u4E4B\u4E0A\uFF0C\u8FD8\u9971\u542B\u7740\u4ED6\u5BF9\u4E8E\u81EA\u5DF1\u56FD\u5BB6\u4E0E\u6C11\u65CF\u672A\u6765\u7684\u671F\u5F85\u3002</p><p>\u4ED6\u7684\u56FD\u5BB6\u5C06\u8981\u8D70\u5411\u4F55\u65B9\uFF0C\u5728\u8FC1\u90FD\u6210\u529F\u7684\u90A3\u4E00\u523B\uFF0C\u4E00\u5207\u90FD\u5373\u5C06\u8F6C\u5411\u3002\u800C\u90A3\u4E2A\u672A\u6765\u7684\u65B9\u5411\uFF0C\u53C8\u548C\u8FD9\u4E9B\u4E09\u767E\u5E74\u524D\u7684\u77F3\u523B\u6709\u7740\u83AB\u5927\u7684\u5173\u8054\u3002</p>',86),r=[t];function n(s,g){return o(),e("div",null,r)}const i=p(d,[["render",n],["__file","61.html.vue"]]);export{i as default};
