import{_ as p}from"./_plugin-vue_export-helper.cdc0426e.js";import{o,c as e,f as c}from"./app.cb44c984.js";const d={},t=c('<h1 id="\u5317\u9B4F\u8870\u843D" tabindex="-1"><a class="header-anchor" href="#\u5317\u9B4F\u8870\u843D" aria-hidden="true">#</a> \u5317\u9B4F\u8870\u843D</h1><blockquote><p>\u79CB\uFF0C\u4E03\u6708\uFF0C\u7532\u5348\uFF0C\u9B4F\u7ACB\u9AD8\u8D35\u5AD4\u4E3A\u7687\u540E\u3002\u5C1A\u4E66\u4EE4\u9AD8\u8087\u76CA\u8D35\u91CD\u7528\u4E8B\u3002\u8087\u591A\u53D8\u66F4\u5148\u671D\u65E7\u5236\uFF0C\u51CF\u524A\u5C01\u79E9\uFF0C\u6291\u9EDC\u52CB\u4EBA\uFF0C\u7531\u662F\u6028\u58F0\u76C8\u8DEF\u3002</p></blockquote><p><strong>\u300A\u8D44\u6CBB\u901A\u9274\xB7\u5377\u4E00\u56DB\u4E03\xB7\u6881\u7EAA\u4E09\u300B</strong></p><blockquote><p><strong>\u5B5D\u6587\u5E1D\u7684\u6539\u9769\uFF0C\u662F\u5426\u662F\u5BFC\u81F4\u5317\u9B4F\u706D\u4EA1\u7684\u7F6A\u9B41\u7978\u9996\uFF1F</strong></p></blockquote><p>\u5B5D\u6587\u5E1D\u8EAB\u540E\u4E09\u5341\u591A\u5E74\uFF0C\u5317\u9B4F\u5373\u9677\u5165\u5206\u88C2\uFF0C\u6B64\u540E\uFF0C\u671D\u653F\u88AB\u6743\u81E3\u6240\u638C\u63E1\uFF0C\u76F4\u81F3\u706D\u4EA1\u3002\u56E0\u6B64\u770B\u8D77\u6765\uFF0C\u4F3C\u4E4E\u5C31\u662F\u5B5D\u6587\u5E1D\u7684\u6539\u9769\u5BFC\u81F4\u4E86\u5317\u9B4F\u706D\u4EA1\u3002</p><p>\u4F46\u4E8B\u5B9E\u4E0A\uFF0C\u8FD9\u662F\u4E00\u79CD<code>\u9519\u89C9</code>\u3002</p><p>\u4ECE\u5B5D\u6587\u5E1D\u9A7E\u5D29\uFF0C\u5230\u516D\u9547\u4E4B\u4E71\u7206\u53D1\uFF0C\u8FD9\u5176\u4E2D\u4E00\u5171\u7ECF\u5386\u4E86\u4E8C\u5341\u4E94\u5E74\u3002**\u8FD9\u4E8C\u5341\u4E94\u5E74\u95F4\uFF0C\u5317\u9B4F\u653F\u5C40\u6025\u5267\u8D25\u574F\u3002**\u4E5F\u6B63\u662F\u56E0\u4E3A\u8FD9\u4E9B\u5E74\u7684\u8352\u653F\uFF0C\u5317\u9B4F\u4E27\u5931\u4E86\u91CD\u5927\u6218\u7565\u673A\u9047\u3001\u519B\u4E8B\u5B9E\u529B\u906D\u53D7\u5DE8\u5927\u635F\u5931\uFF0C\u671D\u653F\u8D25\u574F\u3001\u5185\u90E8\u77DB\u76FE\u6FC0\u5316\u2026\u2026\u56E0\u6B64\u79CD\u79CD\uFF0C\u516D\u9547\u4E4B\u4E71\uFF0C\u624D\u6700\u7EC8\u6F14\u5316\u4E3A\u5317\u9B4F\u706D\u4EA1\u7684\u5148\u58F0\u3002</p><p>\u592A\u548C\u4E8C\u5341\u4E09\u5E74\uFF08\u516C\u5143499\u5E74\uFF09\uFF0C<strong>\u5B5D\u6587\u5E1D<code>\u5143\u5B8F</code><strong>\u56E0\u75C5\u53BB\u4E16\u3002\u540C\u5E74\uFF0C\u5341\u4E03\u5C81\u7684\u592A\u5B50<code>\u5143\u606A</code>\u7EE7\u4F4D\uFF0C\u662F\u4E3A</strong>\u5BA3\u6B66\u5E1D</strong>\u3002</p><p>\u5BA3\u6B66\u5E1D\u7EE7\u4F4D\u65F6\uFF0C\u6709\u4E2A\u5929\u7136\u7684\u7F3A\u9677\uFF1A\u4ED6\u5F53\u592A\u5B50\u7684\u65F6\u95F4\u592A\u77ED\u4E86\uFF0C\u53EA\u6709\u4E24\u5E74\u65F6\u95F4\u3002</p><p>\u8FD9\u610F\u5473\u7740\uFF0C\u5317\u9B4F\u4F20\u7EDF\u4E0A\u5BF9\u4E8E\u592A\u5B50\u7684\u4E25\u683C\u6559\u80B2\uFF0C\u53EF\u80FD\u5728\u4ED6\u8FD9\u91CC\u5E76\u6CA1\u6709\u8D77\u5230\u591A\u5C11\u4F5C\u7528\u3002</p><p>\u5317\u9B4F\u4ECE\u7B2C\u4E8C\u4EE3\u541B\u4E3B<code>\u660E\u5143\u5E1D</code>\u5F00\u59CB\uFF0C\u5BF9\u592A\u5B50\u7684\u57F9\u517B\u5C31\u662F\u671D\u5EF7\u7684\u5927\u4E8B\u3002\u800C\u4E14\uFF0C\u4ECE\u5317\u9B4F\u51E0\u4EE3\u7687\u5E1D\u7684\u5B9E\u9645\u8868\u73B0\u6765\u770B\uFF0C\u8FD9\u79CD\u6559\u80B2\u662F\u6210\u6548\u5353\u8457\u7684\u3002\u81EA<code>\u592A\u6B66\u5E1D</code>\u3001<code>\u6587\u6210\u5E1D</code>\u3001<code>\u732E\u6587\u5E1D</code>\u81F3<code>\u5B5D\u6587\u5E1D</code>\uFF0C\u4ED6\u4EEC\u90FD\u53EF\u4EE5\u79F0\u5F97\u4E0A\u662F\u5408\u683C\u7684\u541B\u4E3B\u3002</p><p>\u8054\u7CFB\u4ED6\u4EEC\u7684\u9C9C\u5351\u4EBA\u80CC\u666F\uFF0C\u518D\u5BF9\u6BD4\u4E00\u4E0B\u4E94\u80E1\u5341\u516D\u56FD\u65F6\u671F\u5176\u4ED6\u80E1\u4EBA\u5BF9\u4E8E\u7EE7\u627F\u4EBA\u7684\u57F9\u517B\u6A21\u5F0F\uFF0C\u90A3\u4E48\u5317\u9B4F\u7684\u6210\u7EE9\u53EF\u4EE5\u79F0\u5F97\u4E0A\u662F\u4E00\u9A91\u7EDD\u5C18\u4E86\u3002</p><p>\u4F46\u504F\u504F\uFF0C\u5230\u5BA3\u6B66\u5E1D\u8FD9\u91CC\u5374\u51FA\u4E86\u70B9\u610F\u5916\u3002</p><p>\u524D\u592A\u5B50<code>\u5143\u6042</code>\uFF0C\u56E0\u4E3A\u53CD\u5BF9\u5B5D\u6587\u5E1D\u7684\u6C49\u5316\u6539\u9769\u800C\u88AB\u5E9F\u3002\u4E4B\u540E\uFF0C\u5143\u606A\u662F\u4F5C\u4E3A\u4E8C\u5B50\u8865\u4F4D\uFF0C\u624D\u6210\u4E86\u592A\u5B50\u3002\u800C\u4E14\uFF0C\u53EA\u5F53\u4E86\u4E24\u5E74\u592A\u5B50\uFF0C\u4FBF\u7EE7\u4F4D\u6210\u4E86\u7687\u5E1D\u3002</p><p>\u5728\u5165\u4E3B\u4E1C\u5BAB\u4E4B\u524D\uFF0C\u5143\u606A\u53EF\u80FD\u5E76\u6CA1\u6709\u600E\u4E48\u5165\u7236\u4EB2\u7684\u6CD5\u773C\uFF0C\u56E0\u4E3A\u53F2\u6599\u4E2D\u4EC5\u6709\u7684\u7236\u4EB2\u5BF9\u4E8E\u4ED6\u7684\u5938\u8D5E\uFF0C\u5F88\u50CF\u662F\u4E8B\u540E\u8865\u8BB0\u7684\uFF1B\u800C\u4E14\uFF0C\u4ED6\u81EA\u5DF1\u4E5F\u6CA1\u6709\u53C2\u4E0E\u671D\u653F\u7684\u7ECF\u5386\u3002</p><p>\u5F88\u6709\u53EF\u80FD\uFF0C\u5143\u606A\u7684\u7AE5\u5E74\u5C31\u662F\u81EA\u7531\u751F\u957F\u7684\uFF0C\u53EA\u4E0D\u8FC7\u662F\u5230\u4E86\u5341\u4E94\u5C81\u65F6\uFF0C\u5929\u4E0A\u7A81\u7136\u6389\u4E0B\u4E2A\u5927\u9985\u997C\uFF0C\u6B63\u597D\u7838\u5230\u4ED6\u5934\u4E0A\u3002</p><p>\u4F46\u95EE\u9898\u662F\uFF0C**\u5341\u4E94\u5C81\u7684\u4EBA\uFF0C\u5982\u679C\u5DF2\u7ECF\u6709\u4E86\u4E00\u5B9A\u7684\u4E16\u754C\u89C2\uFF0C\u90A3\u4E48\u518D\u60F3\u7EA0\u6B63\u662F\u5F88\u96BE\u7684\u3002**\u8FD9\u4E00\u70B9\uFF0C\u5143\u606A\u5728\u540E\u6765\u7684<code>\u7528\u4EBA</code>\u4E0A\u4F53\u73B0\u5F97\u5C24\u5176\u660E\u663E\uFF1A</p><p>\u4ED6\u7279\u522B\u559C\u6B22\u91CD\u7528\u4E0E\u81EA\u5DF1\u5173\u7CFB\u4EB2\u5BC6\u7684\u4EBA\uFF0C\u800C\u4E0D\u80FD\u50CF\u4ED6\u7684\u7236\u4EB2\u5B5D\u6587\u5E1D\u90A3\u6837\u6062\u5B8F\u5927\u5EA6\uFF0C\u4E0D\u8BA1\u524D\u5ACC\uFF0C\u91CF\u624D\u5F55\u7528\u3002</p><p><strong>\u5916\u621A</strong></p><p>\u5C24\u5176\uFF0C\u662F\u5728\u770B\u5230\u8F85\u653F\u5927\u81E3\u540D\u5355\u4EE5\u540E\uFF0C\u5C31\u66F4\u52A0\u575A\u5B9A\u4E86\u4ED6\u4FE1\u4EFB\u5916\u621A\u7684\u51B3\u5FC3\u3002</p><p>\u5B5D\u6587\u5E1D\u5728\u75C5\u901D\u524D\uFF0C\u66FE\u7ECF\u7ED9\u5BA3\u6B66\u5E1D\u6307\u5B9A\u8FC7\u516D\u4E2A\u8F85\u653F\u5927\u81E3\uFF0C\u8FD9\u5F53\u4E2D\uFF0C<strong>\u5143\u6C0F\u5B97\u5BA4\u5360\u4E86\u56DB\u4E2A\u3002</strong></p><p>\u4F46\u9762\u5BF9\u7740\u8FD9\u4E9B\u81EA\u5BB6\u957F\u8F88\uFF0C\u5BA3\u6B66\u5E1D\u7684\u6001\u5EA6\u5374\u662F\uFF1A</p><blockquote><p><strong>\u9632\u5907</strong></p></blockquote><p>\u7B2C\u4E00\u4E2A\u88AB\u641E\u7684\uFF0C\u662F\u5B5D\u6587\u5E1D\u7684\u5802\u53D4\u7236\u3001\u5BA3\u6B66\u5E1D\u7684\u5802\u53D4\u7956\u7236\u3001\u4EFB\u57CE\u738B<code>\u5143\u6F84</code>\u3002</p><p>\u5143\u6F84\u662F\u5B5D\u6587\u5E1D\u65F6\u671F\u7684\u91CD\u81E3\uFF0C\u5F53\u521D\u5728\u8FC1\u90FD\u4E00\u4E8B\u4E0A\uFF0C\u5B5D\u6587\u5E1D\u6700\u5148\u5C06\u5185\u5FC3\u60F3\u6CD5\u900F\u9732\u51FA\u6765\u7684\u5BF9\u8C61\u5C31\u662F\u4ED6\u3002\u800C\u4E14\u6B64\u540E\uFF0C\u5728\u4E00\u7CFB\u5217\u7684\u6C49\u5316\u6539\u9769\u4E2D\uFF0C\u5143\u6F84\u90FD\u662F\u5B5D\u6587\u5E1D\u7684\u4E3B\u8981\u52A9\u624B\u3002</p><p>\u4F46\u5C31\u662F\u8FD9\u4E48\u91CD\u8981\u7684\u4E00\u4E2A\u4EBA\u7269\uFF0C\u5728\u5BA3\u6B66\u5E1D\u5E74\u95F4\u5374\u6BEB\u65E0\u4F5C\u4E3A\u3002</p><p>\u4ED6\u662F\u516D\u4E2A\u8F85\u653F\u5927\u81E3\u4E2D\u6700\u5148\u88AB\u7F62\u514D\u7684\u4E00\u4F4D\uFF0C\u867D\u7136\u4E4B\u540E\u5C61\u6B21\u590D\u51FA\uFF0C\u4F46\u5F97\u5230\u7684\u90FD\u662F\u523A\u53F2\u4E00\u7C7B\u7684\u5730\u65B9\u5927\u5458\u3002\u5143\u6F84\u7684\u76EE\u6807\u660E\u663E\u4E0D\u5728\u4E8E\u6B64\uFF0C\u4ED6\u5E0C\u671B\u7684\u662F\u80FD\u56DE\u5230\u671D\u4E2D\u518D\u638C\u673A\u67A2\uFF0C\u56E0\u6B64\uFF0C\u53EA\u80FD\u4E00\u518D\u4EE5\u6BCD\u4E27\u8F9E\u804C\uFF0C\u60F3\u8981\u7559\u5728\u6D1B\u9633\u7B49\u5F85\u673A\u4F1A\u3002</p><p>\u4F46\u4ED6\u7B49\u6765\u7684\u5374\u662F\u8D8A\u6765\u8D8A\u6076\u52A3\u7684\u653F\u6CBB\u73AF\u5883\u3002\u540E\u6765\u2014\u2014\u5C24\u5176\u662F\u5728\u5F6D\u57CE\u738B<code>\u5143\u52F0</code>\u88AB\u5BB3\u6B7B\u4EE5\u540E\uFF0C\u4ED6\u53EA\u80FD\u6574\u65E5\u996E\u9152\u4F5C\u4E50\uFF0C\u751F\u6015\u6B65\u5143\u52F0\u7684\u540E\u5C18\u3002\u4E00\u76F4\u5230\u5BA3\u6B66\u5E1D\u53BB\u4E16\uFF0C\u5143\u6F84\u624D\u518D\u6B21\u51FA\u4ED5\u3002</p><p>\u800C\u5F6D\u57CE\u738B<code>\u5143\u52F0</code>\u7684\u906D\u9047\u5C31\u66F4\u52A0\u60B2\u60E8\u4E86\u3002</p><p>\u5143\u52F0\u662F\u5B5D\u6587\u5E1D\u5143\u5B8F\u7684\u5F1F\u5F1F\uFF0C\u5BA3\u6B66\u5E1D\u4EB2\u53D4\u7236\uFF0C\u5E74\u5C11\u65F6\u5373\u6709\u4EE4\u540D\u3002\u6210\u5E74\u540E\uFF0C\u4ED6\u5C3D\u529B\u8F85\u4F50\u5144\u957F\uFF0C\u5B5D\u6587\u5E1D\u5230\u665A\u5E74\u65F6\u751A\u81F3\u8DDF\u4ED6\u8BF4\uFF1A\u5982\u679C\u6211\u7684\u513F\u5B50\u4E0D\u582A\u8F85\u4F50\uFF0C\u90A3\u4F60\u53EF\u4EE5\u53D6\u800C\u4EE3\u4E4B\u3002</p><p>\u5B5D\u6587\u5E1D\u5728\u53BB\u4E16\u65F6\uFF0C\u66FE\u60F3\u6307\u5B9A\u5143\u52F0\u4E3A\u9996\u8F85\uFF0C\u4F46\u5143\u52F0\u89C9\u5F97\u81EA\u5DF1\u79BB\u6743\u529B\u592A\u8FD1\u4E86\uFF0C\u5F88\u5371\u9669\uFF0C\u56E0\u6B64\u7AED\u529B\u63A8\u8F9E\u3002</p><p>\u7B49\u5230\u5B5D\u6587\u5E1D\u53BB\u4E16\u540E\uFF0C\u5BA3\u6B66\u5E1D\u606A\u4E8E\u5F62\u52BF\uFF0C\u8FD8\u662F\u7ED9\u4E86\u5143\u52F0\u4EFB\u547D\uFF0C\u6307\u5B9A\u4ED6\u4E3B\u6301\u653F\u4E8B\u3002\u4F46\u4E24\u5E74\u540E\uFF0C\u5BA3\u6B66\u5E1D\u4EB2\u653F\uFF0C\u5F88\u5FEB\u5C31\u540C\u610F\u4E86\u5143\u52F0\u7684\u8F9E\u5448\u3002</p><p>\u4E4B\u540E\uFF0C\u5143\u52F0\u66FE\u7ECF\u77ED\u6682\u7684\u51FA\u4ED5\uFF0C\u4F46\u4E5F\u53EA\u662F\u5728\u524D\u7EBF\u4F5C\u6218\uFF0C\u800C\u4E0D\u613F\u610F\u63BA\u548C\u671D\u5EF7\u7684\u653F\u6CBB\u6597\u4E89\u3002</p><p>\u5230\u6C38\u5E73\u5143\u5E74\u65F6\uFF0C\u5143\u52F0\u4E09\u5341\u4E94\u5C81\uFF0C\u5DF2\u7ECF\u5728\u534A\u9690\u9000\u4E2D\u6E21\u8FC7\u4E86\u516B\u5E74\u65F6\u5149\uFF0C\u4F46\u547D\u8FD0\u8FD8\u662F\u6CA1\u6709\u9976\u8FC7\u4ED6\uFF1A\u4EAC\u5146\u738B<code>\u5143\u6109</code>\u8D77\u5175\u9020\u53CD\u4F5C\u4E71\uFF0C\u867D\u7136\u540E\u88AB\u5E73\u5B9A\uFF0C\u4F46\u5BA3\u6B66\u5E1D\u5728<code>\u9AD8\u8087</code>\u7684\u86CA\u60D1\u4E0B\uFF0C\u5F00\u59CB\u6000\u7591\u5143\u52F0\u4E5F\u53C2\u4E0E\u5176\u4E2D\u3002</p><p>\u67D0\u65E5\uFF0C\u5BA3\u6B66\u5E1D\u53EC\u5143\u52F0\u7B49\u4EB2\u738B\u5165\u5BAB\u8D74\u5BB4\u3002\u5F53\u65F6\uFF0C\u5143\u52F0\u7684\u738B\u5983\u674E\u6C0F\u6B63\u8981\u751F\u4EA7\uFF0C\u56E0\u6B64\u4ED6\u63A8\u8F9E\u4E0D\u53BB\u3002\u4F46\u4E2D\u4F7F\u51E0\u6B21\u50AC\u4FC3\uFF0C\u5143\u52F0\u4E0D\u5F97\u5DF2\uFF0C\u53EA\u80FD\u5728\u4E0E\u674E\u6C0F\u8BC0\u522B\u540E\uFF0C\u767B\u8F66\u8D74\u5BB4\u3002</p><p>\u8F66\u81F3\u5BAB\u95E8\uFF0C\u725B\u8E1F\u8E70\u4E0D\u8FDB\u3002\u4E2D\u4F7F\u518D\u4E09\u50AC\u4FC3\uFF0C\u5143\u52F0\u6B65\u5165\u5BAB\u4E2D\u3002</p><p>\u5BB4\u540E\uFF0C\u5143\u52F0\u88AB\u6BD2\u6740\u4E8E\u5BAB\u5185\u3002\u6B21\u65E5\uFF0C\u5C38\u4F53\u88AB\u8FD0\u56DE\u738B\u5E9C\uFF0C\u7406\u7531\u662F\u9189\u6B7B\u3002</p><p>\u53F2\u8F7D\uFF0C\u674E\u5983\u53F7\u54ED\uFF0C\u8A00\u66F0\uFF1A\u201C\u9AD8\u8087\u6789\u7406\u6740\u4EBA\uFF0C\u5929\u9053\u6709\u7075\uFF0C\u6C5D\u5B89\u5F97\u826F\u6B7B\uFF01\u201D</p><p>\u8FD9\u4E2A<code>\u9AD8\u8087</code>\uFF0C<strong>\u5373\u662F\u5BA3\u6B66\u5E1D\u5E74\u95F4\u8D25\u574F\u671D\u653F\u7684\u7F6A\u9B41\u7978\u9996\u3002</strong></p><p><strong>\u9AD8\u8087\u51FA\u8EAB\u4E8E<code>\u5916\u621A</code></strong>\uFF0C\u662F\u5BA3\u6B66\u5E1D\u7684\u4EB2\u8205\u8205\u3002\u5BA3\u6B66\u5E1D\u7684\u6BCD\u4EB2\u6B7B\u5F97\u8E4A\u8DF7\uFF0C\u56E0\u6B64\u5BA3\u6B66\u5E1D\u5BF9\u4E8E\u8FD9\u4E2A\u8205\u8205\u683C\u5916\u7167\u987E\uFF0C\u5E76\u9010\u6E10\u5728\u7167\u987E\u4E4B\u5916\uFF0C\u8FDB\u4E00\u6B65\u5F00\u53D1\u51FA\u4E86\u8205\u8205\u7684\u65B0\u6280\u80FD\uFF1A</p><blockquote><p><strong>\u6392\u65A5\u5B97\u738B</strong></p></blockquote><p>\u5728\u7687\u5E1D\u770B\u6765\uFF0C\u5B97\u5BA4\u4EEC\u79BB\u7687\u4F4D\u592A\u8FD1\uFF0C\u5E76\u4E0D\u5B89\u5168\uFF1B\u5916\u621A\u662F\u5F3A\u70C8\u4F9D\u9644\u4E8E\u81EA\u5DF1\u7684\uFF0C\u4ED6\u4EEC\u624D\u6700\u597D\u7528\u3002</p><p>\u5982\u4ECA\u5F6D\u57CE\u738B\u5143\u52F0\u5DF2\u6B7B\uFF0C\u4EFB\u57CE\u738B\u5143\u6F84\u5DF2\u88AB\u6392\u6324\u51FA\u5C40\uFF0C\u5269\u4E0B\u7684\u51E0\u4E2A\u738B\u7237\u5728\u5BA3\u6B66\u5E1D\u6216\u660E\u6216\u6697\u7684\u6697\u793A\u548C\u5A01\u80C1\u4E0B\uFF0C\u4E5F\u90FD\u9010\u6E10\u6DE1\u51FA\u4E86\u671D\u653F\u3002\u4F46\u987A\u5229\u4EB2\u653F\u7684\u5BA3\u6B66\u5E1D\u81EA\u5DF1\u5E76\u6CA1\u6709\u591A\u5C11\u6267\u653F\u80FD\u529B\u548C\u7ECF\u9A8C\uFF0C\u6240\u4EE5\u5728\u56FD\u5BB6\u5927\u4E8B\u4E0A\uFF0C\u4ED6\u57FA\u672C\u90FD\u662F\u9760\u51E0\u4E2A\u4EB2\u4FE1\u548C\u5916\u621A\u9AD8\u8087\u6307\u5BFC\u3002</p><p>\u8FD9\u6837\u7684\u73AF\u5883\uFF0C\u7ED9\u5B97\u5BA4\u4EEC\u5E26\u6765\u4E86\u5DE8\u5927\u7684\u4E0D\u5B89\u5168\u611F\u3002\u56E0\u4E3A\u5BB3\u6015\u88AB\u5BA3\u6B66\u5E1D\u8EAB\u8FB9\u7684\u8FD9\u4E9B\u5BB5\u5C0F\u6784\u9677\uFF0C\u5B5D\u6587\u5E1D\u4E4B\u5F1F\u3001\u5BA3\u6B66\u5E1D\u4E4B\u53D4\u3001\u54B8\u9633\u738B<code>\u5143\u79A7</code>\u51B3\u5B9A\u8C0B\u53CD\uFF0C\u4F46\u4E8B\u60C5\u8D25\u9732\uFF0C\u5143\u79A7\u88AB\u6740\u3002</p><p>\u53EF\u8FD9\u53CD\u8FC7\u6765\u53C8\u52A0\u91CD\u4E86\u5BA3\u6B66\u5E1D\u5BF9\u4E8E\u5B97\u5BA4\u7684\u4E0D\u4FE1\u4EFB\uFF0C\u4ED6\u7EE7\u7EED\u5411\u7740\u5B97\u5BA4\u624B\u8D77\u5200\u843D\u2014\u2014\u800C\u8FD9\u628A\u5200\uFF0C\u6B63\u662F\u8205\u8205\u9AD8\u8087\u3002</p><p>\u6B64\u540E\uFF0C\u6298\u5728\u9AD8\u8087\u624B\u4E0A\u7684\uFF0C\u5148\u540E\u6709\u5B5D\u6587\u5E1D\u4E4B\u5F1F\u3001\u5317\u6D77\u738B<code>\u5143\u8BE6</code>\uFF0C\u88AB\u6740\uFF1B\u5B5D\u6587\u5E1D\u4E4B\u5B50\u3001\u4EAC\u5146\u738B<code>\u5143\u6109</code>\uFF0C\u903C\u53CD\u88AB\u6740\uFF1B\u5B5D\u6587\u5E1D\u4E4B\u5F1F\u3001\u5F6D\u57CE\u738B<code>\u5143\u52F0</code>\uFF0C\u6BD2\u6740\uFF1B\u5E76\u4E14\uFF0C\u4ED6\u8FD8\u4E0E\u5B5D\u6587\u5E1D\u4E4B\u5B50\u3001\u6E05\u6CB3\u738B<code>\u5143\u603F</code>\u5F53\u4F17\u53D1\u751F\u8FC7\u4E89\u5435\u3002</p><p>\u6B64\u5916\uFF0C**\u9AD8\u8087\u6253\u51FB\u7684\u53E6\u4E00\u4E2A\u76EE\u6807\u7FA4\u4F53\uFF0C\u662F\u4F4D\u9AD8\u6743\u91CD\uFF0C\u4F46\u4E0D\u5F52\u987A\u4E8E\u81EA\u5DF1\u7684\u4EBA\u3002**\u6BD4\u5982\uFF0C\u5EA6\u652F\u5C1A\u4E66<code>\u5143\u5321</code>\u5C31\u662F\u5178\u578B\u3002</p><p>\u5BA3\u6B66\u5E1D\u548C\u9AD8\u8087\u5728\u5904\u7F6E\u4E86\u51E0\u4F4D\u5B97\u5BA4\u4EE5\u540E\uFF0C\u5B97\u5BA4\u5664\u82E5\u5BD2\u8749\uFF0C\u53EA\u6709\u8FDC\u652F\u5143\u5321\u662F\u4E2A\u4F8B\u5916\u3002\u5143\u5321\u5BF9\u9AD8\u8087\u53CD\u611F\u81F3\u6781\uFF0C\u66FE\u7ECF\u4E00\u5EA6\u505A\u597D\u4E86\u68FA\u6750\uFF0C\u51C6\u5907\u5192\u6B7B\u8FDB\u8C0F\uFF0C\u8C0F\u540E\u5373\u81EA\u6740\uFF0C\u4EE5\u6FC0\u8D77\u4F17\u6012\u3002</p><p>\u4F46\u5143\u5321\u6700\u7EC8\u6CA1\u6709\u673A\u4F1A\u8FD9\u4E48\u505A\u3002\u9AD8\u8087\u5728\u5F97\u77E5\u4E86\u5143\u5321\u7684\u8C0B\u5212\u540E\uFF0C\u5728\u67D0\u6B21\u671D\u8BAE\u4E2D\u6545\u610F\u6FC0\u6012\u5143\u5321\uFF0C\u5143\u5321\u5F53\u5EAD\u6307\u8D23\u9AD8\u8087\u5982\u8D75\u9AD8\u4E00\u822C\u6307\u9E7F\u4E3A\u9A6C\u3002</p><p>\u5FA1\u53F2\u4E2D\u4E1E\u968F\u5373\u4E0A\u594F\uFF0C\u8BF4\u5143\u5321\u8FB1\u9A82\u5BB0\u76F8\uFF0C\u5E94\u8BE5\u5904\u4EE5\u6781\u5211\u3002\u5BA3\u6B66\u5E1D\u5012\u662F\u8FD8\u6CA1\u6709\u90A3\u4E48\u4E0D\u660E\u4E8B\u7406\uFF0C\u4F46\u5143\u5321\u4E5F\u56E0\u6B64\u88AB\u964D\u804C\u3002</p><p>\u603B\u4E4B\uFF0C\u51E1\u662F\u4F4D\u9AD8\u6743\u91CD\u7684\u6216\u8005\u79BB\u7687\u4F4D\u8FD1\u7684\uFF0C\u90FD\u6210\u4E86\u6253\u51FB\u5BF9\u8C61\u3002</p><p>\u800C\u4E14\uFF0C\u8FD9\u4F4D\u9AD8\u8087\u4E0D\u53EA\u662F\u7687\u5BA4\u5B50\u5F1F\u7684\u7C89\u788E\u673A\uFF0C\u8FD8\u662F\u98CE\u6C14\u6076\u5316\u7684\u59CB\u4F5C\u4FD1\u8005\u3002\u53F2\u4E66\u8BB0\u8F7D\uFF0C\u592A\u548C\u5E74\u95F4\u6E05\u6B63\u7684\u653F\u6CBB\u98CE\u6C14\uFF0C\u5230\u5BA3\u6B66\u5E1D\u5E74\u95F4\u5DF2\u7ECF\u53D8\u5F97\u7ED3\u515A\u8425\u79C1\uFF0C\u8150\u5316\u5815\u843D\uFF0C\u810F\u810F\u4E0D\u582A\u3002</p><p>\u8FD9\u4E00\u5207\u7684\u80CC\u540E\uFF0C\u7F6A\u9B41\u7978\u9996\u6B63\u662F\u9AD8\u8087\u3002</p><p>\u671D\u653F\u4E4B\u5916\uFF0C\u5BA3\u6B66\u5E1D\u4E00\u671D\u5728\u6218\u573A\u4E0A\u7684\u8868\u73B0\uFF0C\u5C31\u66F4\u4E0D\u5982\u4EBA\u610F\u4E86\u3002</p><p>\u4ECE\u4E1C\u664B\u5EFA\u7ACB\u5230\u9648\u706D\u4EA1\uFF0C<strong>\u4F34\u968F\u7740\u5357\u5317\u53CC\u65B9\u5728\u5B9E\u529B\u5DEE\u8DDD\u4E0A\u7684\u53D8\u52A8\uFF0C\u8FB9\u754C\u4E5F\u5728\u6CBF\u7740\u6DEE\u6CB3\u5357\u5317\u4E0D\u65AD\u53D8\u52A8\u3002</strong></p><p>\u5357\u671D\u8FDB\u5165\u5317\u65B9\u7684\u6781\u9650\uFF0C\u662F\u5728**\u5B8B\u6B66\u5E1D<code>\u5218\u88D5</code>**\u65F6\u671F\uFF0C\u66FE\u7ECF\u5230\u8FBE\u8FC7\u5C71\u4E1C\u534A\u5C9B\u4E00\u5E26\u3002\u4F46\u662F\uFF0C\u968F\u7740\u5143\u5609\u5317\u4F10\u5931\u8D25\uFF0C**\u5317\u9B4F<code>\u592A\u6B66\u5E1D</code>**\u4E00\u5EA6\u7A81\u5165\u5230\u4E86\u957F\u6C5F\u5317\u5CB8\uFF0C\u867D\u7136\u540E\u6765\u64A4\u56DE\uFF0C\u4F46\u6B64\u540E<code>\u5218\u5B8B</code>\u5B9E\u529B\u5927\u635F\u3002</p><p>\u5230<code>\u5B8B\u660E\u5E1D</code>\u65F6\u671F\uFF0C\u4E5F\u5C31\u662F\u5317\u9B4F<code>\u732E\u6587\u5E1D</code>\u65F6\u671F\uFF0C\u56E0\u5218\u5B8B\u5728\u6218\u4E89\u4E2D\u5927\u8D25\uFF0C\u5357\u671D\u5728\u6DEE\u6CB3\u4EE5\u5317\u7684\u571F\u5730\u5168\u90E8\u4E27\u5931\uFF0C\u53CC\u65B9\u6CBF\u6DEE\u6CB3\u5BF9\u5CD9\u3002</p><p>\u6211\u4EEC\u5728\u8BB2\u5B5D\u6587\u5E1D\u65F6\u66FE\u7ECF\u63D0\u8FC7\uFF0C\u8FC1\u90FD\u6D1B\u9633\u4EE5\u540E\uFF0C\u5B5D\u6587\u5E1D\u7684\u91CD\u5FC3\u4E00\u76F4\u90FD\u653E\u5728\u6218\u4E89\u4E0A\u2014\u2014\u8FD9\u4E9B\u6218\u4E89\u7684\u7126\u70B9\uFF0C\u5C31\u662F\u5BF9\u4E8E\u6DEE\u6CB3\u5357\u5CB8\u7684\u4E89\u593A\uFF0C\u53EA\u4E0D\u8FC7\u6218\u679C\u4E0D\u5C3D\u5982\u4EBA\u610F\u3002</p><p>\u5982\u4ECA\u5BA3\u6B66\u5E1D\u7EE7\u4F4D\uFF0C\u7EE7\u7EED\u4E0E\u5357\u65B9\u5728\u6B64\u53AE\u6740\u3002<strong>\u7EC8\u5BA3\u6B66\u5E1D\u4E00\u671D\uFF0C\u4E0E<code>\u5357\u6881</code>\u4E89\u593A\u6DEE\u6CB3\u6D41\u57DF\u7684\u6218\u4E89\u4E00\u5171\u6301\u7EED\u4E86\u4E03\u5E74</strong>\u3002\u7B2C\u4E00\u9636\u6BB5\uFF0C\u662F\u5317\u9B4F\u4E3B\u52A8\u51FA\u51FB\uFF1B\u7B2C\u4E8C\u9636\u6BB5\uFF0C\u5219\u7531\u5357\u6881\u6311\u8D77\u3002</p><p>\u5F53\u65F6\uFF0C\u5317\u9B4F\u5076\u7136\u5728\u56DB\u5DDD\u4E00\u5E26\u53D6\u5F97\u4F18\u52BF\uFF0C\u653B\u5165\u6210\u90FD\u6307\u65E5\u53EF\u5F85\u3002\u5357\u6881\u6551\u63F4\u4E0D\u53CA\uFF0C\u53EA\u80FD\u5728\u4E1C\u7EBF\u6269\u5927\u6218\u4E89\u89C4\u6A21\uFF0C\u4EE5\u6B64\u6765\u7275\u5236\u9B4F\u519B\uFF0C\u9632\u6B62\u5176\u652F\u63F4\u897F\u7EBF\u3002</p><p>\u4F46\u5BA3\u6B66\u5E1D\u7684\u6700\u7EC8\u9009\u62E9\uFF0C\u5374\u662F\u653E\u5F03\u652F\u63F4\u897F\u7EBF\uFF0C\u503E\u91CD\u5175\u4E8E\u4E1C\u7EBF\u3002</p><p>\u7ED3\u679C\uFF0C\u5927\u8D25\u800C\u5F52\u3002</p><p>\u5728\u6B64\u6218\u4E2D\uFF0C<strong>\u5317\u9B4F\u4E27\u5931\u5175\u529B\u4E8C\u5341\u4F59\u4E07\uFF0C\u5931\u53BB\u4E86\u81EA\u592A\u6B66\u5E1D\u4EE5\u6765\u4E03\u5341\u5E74\u5BF9\u5357\u65B9\u7684\u519B\u4E8B\u4F18\u52BF\u3002</strong></p><p>\u81F3\u4E8E\u897F\u7EBF\u7684\u5C40\u9762\uFF0C\u5219\u53C8\u662F\u4E00\u756A\u627C\u8155\u75DB\u60DC\u3002</p><p>\u6881\u5929\u76D1\u56DB\u5E74\uFF0C\u5317\u9B4F\u6B63\u59CB\u4E8C\u5E74\uFF08\u516C\u5143505\u5E74\uFF09\uFF0C\u6881\u76CA\u5DDE\u5883\u5185\u53D1\u751F\u5185\u4E71\uFF0C\u90E8\u5206\u5C06\u9886\u5411\u5317\u9B4F\u7EB3\u964D\uFF0C\u5317\u9B4F\u4E8E\u662F\u6D3E\u5C1A\u4E66<code>\u5211\u5CE6</code>\u524D\u53BB\u63A5\u6536\u3002</p><p>\u5F53\u65F6\u671D\u5EF7\u6B63\u5728\u4E1C\u7EBF\u7528\u5175\uFF0C\u56E0\u6B64\u60F3\u7740\u5211\u5CE6\u6B64\u53BB\uFF0C\u4E5F\u65E0\u975E\u5C31\u662F\u7ED9\u6881\u56FD\u5236\u9020\u70B9\u6DF7\u4E71\u3002\u4F46\u6CA1\u6599\u5230\uFF0C\u5211\u5CE6\u7ADF\u7136\u505A\u51FA\u4E86\u4E00\u7BC7\u5927\u6587\u7AE0\uFF1A</p><p><strong>\u5E74\u521D\uFF0C\u5211\u5CE6\u51FA\u5175\uFF1B\u5230\u4E86\u5E74\u4E2D\uFF0C\u5373\u5DF2\u653B\u4E0B<code>\u5251\u9601</code>\u3002</strong></p><p>\u5251\u9601\u5386\u6765\u662F\u6210\u90FD\u7684\u5317\u65B9\u95E8\u6237\u3002\u5F53\u5E74\u66F9\u9B4F\u706D\u8700\uFF0C\u949F\u4F1A\u7387\u5927\u519B\u800C\u6765\uFF0C\u4FBF\u662F\u88AB\u59DC\u7EF4\u963B\u4E8E\u5251\u9601\u4E4B\u5916\u3002\u9093\u827E\u662F\u4ECE\u9634\u5E73\u5C0F\u9053\u7ED5\u8FC7\u5251\u9601\uFF0C\u624D\u5230\u8FBE\u4E86\u6210\u90FD\u57CE\u4E0B\u3002</p><p>\u5982\u4ECA\uFF0C\u5317\u9B4F\u5F62\u52BF\u5927\u597D\uFF0C\u4E0D\u53EA\u5211\u5CE6\u6240\u90E8\u5DF2\u653B\u5165\u5251\u9601\uFF0C\u6DAA\u57CE\u4E5F\u5DF2\u7ECF\u88AB\u5317\u9B4F\u519B\u961F\u5305\u56F4\uFF0C\u65AD\u7EDD\u4E86\u6881\u56FD\u519B\u961F\u7684\u6551\u63F4\u4E4B\u8DEF\u3002<strong>\u4E00\u65E6\u80FD\u653B\u4E0B\u76CA\u5DDE\uFF0C\u5317\u9B4F\u5BF9\u5357\u65B9\u7684\u4F18\u52BF\u5C31\u5C06\u66F4\u8FDB\u4E00\u6B65\u3002</strong></p><p>\u80DC\u5229\u6765\u5F97\u5982\u6B64\u7A81\u7136\uFF0C\u5211\u5CE6\u56E0\u6B64\u4E0A\u4E66\uFF0C\u5E0C\u671B\u671D\u5EF7\u80FD\u6D3E\u7ED9\u4ED6\u4E24\u4E07\u63F4\u519B\uFF0C\u8FD9\u6837\u4ED6\u5C31\u80FD\u8FDB\u5360\u76CA\u5DDE\u3002</p><p>\u4F46\u671D\u5EF7\u7ADF\u7136\u62D2\u7EDD\u4E86\u8FD9\u4E2A\u63D0\u8BAE\u3002</p><p>\u4E5F\u6B63\u662F\u5728\u540C\u65F6\uFF0C\u6881\u56FD\u56E0\u65E0\u6CD5\u652F\u63F4\u6210\u90FD\uFF0C\u4FBF\u5728\u4E1C\u7EBF\u53D1\u52A8\u653B\u52BF\uFF0C\u4F01\u56FE\u7F13\u89E3\u897F\u7EBF\u7684\u519B\u4E8B\u538B\u529B\u3002</p><p>\u6700\u7EC8\uFF0C\u5F97\u4E0D\u5230\u63F4\u519B\u7684\u653B\u8700\u519B\u961F\u64A4\u51FA\u4E86\u8700\u4E2D\uFF0C<strong>\u5317\u9B4F\u6C38\u8FDC\u5931\u53BB\u4E86\u4ECE\u4E0A\u6E38\u5BF9\u5357\u671D\u9020\u6210\u538B\u529B\u7684\u673A\u4F1A\u3002</strong></p><p>\u800C\u9664\u4E86\u4EE5\u4E0A\u8FD9\u4E9B\u56E0\u80FD\u529B\u4E0D\u8DB3\u800C\u5BFC\u81F4\u7684\u4E71\u5C40\u5916\uFF0C\u5BA3\u6B66\u5E1D\u7684<code>\u8FD0\u6C14</code>\u4E5F\u4E0D\u5927\u597D\u3002</p><p>\u5317\u9B4F\u5728<code>\u9053\u6B66\u5E1D</code>\u5F00\u56FD\u4E4B\u521D\uFF0C\u4E3A\u4E86\u9632\u6B62\u5176\u4ED6\u90E8\u843D\u63D2\u624B\u62D3\u8DCB\u6C0F\u5185\u90E8\u4E8B\u52A1\uFF0C\u8BBE\u7ACB\u4E86\u201C<code>\u7ACB\u5B50\u6740\u6BCD</code>\u201D\u7684\u6B8B\u5FCD\u5236\u5EA6\u3002\u4EE5\u4FDD\u8BC1\u5728\u4E0E\u5176\u4ED6\u90E8\u843D\u8054\u59FB\u7684\u540C\u65F6\uFF0C\u4E0D\u8BA9\u5176\u4ED6\u90E8\u843D\u6709\u5E72\u6D89\u672C\u65CF\u4E8B\u52A1\u7684\u63D0\u4F9B\u53EF\u80FD\u2014\u2014\u5C24\u5176\uFF0C\u662F\u5728\u5C11\u4E3B\u5E74\u5E7C\u4E4B\u65F6\u3002</p><p>\u5982\u4ECA\u968F\u7740\u65F6\u95F4\u63A8\u79FB\uFF0C\u62D3\u8DCB\u6C0F\u7684\u5317\u9B4F\u5DF2\u7ECF\u8D8A\u6765\u8D8A\u5F3A\u5927\uFF0C\u5176\u4ED6\u90E8\u843D\u4E5F\u5DF2\u7ECF\u7EB7\u7EB7\u6CA1\u843D\u3002\u5728\u8FD9\u79CD\u60C5\u51B5\u4E0B\uFF0C\u518D\u7EF4\u6301\u8FD9\u4E2A\u6B8B\u5FCD\u7684\u5236\u5EA6\uFF0C\u53D8\u5F97\u6BEB\u65E0\u610F\u4E49\u3002</p><p>\u4F46\u662F\uFF0C\u6CA1\u6709\u610F\u4E49\u7684\u5236\u5EA6\uFF0C\u5374\u4E0D\u610F\u5473\u7740\u4E00\u5B9A\u80FD\u591F\u88AB\u9769\u9664\u3002\u201C\u5B50\u8D35\u6BCD\u6B7B\u201D\u518D\u600E\u4E48\u8BF4\u4E5F\u662F\u7956\u5236\uFF0C\u867D\u7136\u5DF2\u7ECF\u6CA1\u6709\u4EFB\u4F55\u4EBA\u53D7\u76CA\u4E8E\u8FD9\u9879\u5236\u5EA6\uFF0C\u4F46\u8FD9\u9876\u5927\u5E3D\u5B50\u5C31\u662F\u5B83\u7684\u4FDD\u62A4\u4F1E\u3002</p><p>\u8FD8\u5728\u5B5D\u6587\u5E1D\u65F6\uFF0C\u5C31\u6709\u5F88\u591A\u4EBA\u5E0C\u671B\u80FD\u591F\u9769\u9664\u8FD9\u4E00\u5236\u5EA6\u3002\u518C\u7ACB\u5E9F\u592A\u5B50\u5143\u6042\u4E4B\u65F6\uFF0C\u5B5D\u6587\u5E1D\u5E0C\u671B\u80FD\u514D\u9664\u5176\u6BCD\u6797\u6C0F\u4E4B\u6B7B\u3002\u4F46\u5F53\u65F6\u7531\u4E8E<code>\u51AF\u592A\u540E</code>\u4E0D\u540C\u610F\uFF0C\u6700\u7EC8\u8FD8\u662F\u5C06\u6797\u6C0F\u8D50\u6B7B\uFF0C\u8FFD\u8D60\u7687\u540E\u3002</p><p>\u5982\u4ECA\u5230\u5BA3\u6B66\u5E1D\u8FD9\u91CC\uFF0C\u5728\u592A\u5B50<code>\u5143\u8BE9</code>\u88AB\u518C\u7ACB\u65F6\uFF0C\u8FD9\u4E2A\u95EE\u9898\u53C8\u4E00\u6B21\u88AB\u63D0\u4E86\u51FA\u6765\u3002\u6B64\u65F6\u5BA3\u6B66\u5E1D\u5BF9\u671D\u653F\u5DF2\u7ECF\u80FD\u591F\u5B8C\u5168\u638C\u63A7\uFF0C\u4E5F\u4E0D\u518D\u60E7\u6015\u6709\u4EC0\u4E48\u53CD\u5BF9\u529B\u91CF\u501F\u673A\u751F\u4E8B\u4E86\uFF0C\u4E8E\u662F\u4FBF\u987A\u52BF\u5E9F\u9664\u4E86\u8FD9\u9879\u5236\u5EA6\u3002</p><p>\u4E5F\u5C31\u662F\u8BF4\uFF0C\u5317\u9B4F\u4E00\u767E\u4E94\u5341\u5E74\u4EE5\u6765\uFF0C\u592A\u5B50\u5143\u8BE9\u7684\u751F\u6BCD<code>\u80E1\u6C0F</code>\uFF0C\u662F\u552F\u4E00\u4ECE\u8FD9\u9879\u6B8B\u5FCD\u5236\u5EA6\u4E2D\u9003\u51FA\u751F\u5929\u7684\u4EBA\u3002</p><p>\u7136\u800C\u4E16\u4E8B\u5C31\u662F\u8FD9\u4E48\u65E0\u5E38\uFF0C\u5728\u5BA3\u6B66\u5E1D\u53BB\u4E16\u540E\uFF0C\u592A\u5B50\u5143\u8BE9\u7EE7\u4F4D\uFF0C\u8FD9\u4F4D\u80E1\u6C0F\u5F97\u4EE5\u542C\u653F\uFF0C\u662F\u4E3A<code>\u80E1\u592A\u540E</code>\u3002\u6B63\u662F\u8FD9\u4F4D\u80E1\u592A\u540E\uFF0C\u7528\u81EA\u5DF1\u7684\u540E\u534A\u751F\u7CBE\u5F69\u6F14\u7ECE\u4E86\u4E00\u4E2A\u7978\u4E71\u671D\u653F\u7684\u5973\u4E3B\u662F\u600E\u4E48\u7ED9\u4E00\u4E2A\u56FD\u5BB6\u5E26\u6765\u65E0\u7A77\u707E\u96BE\u7684\u2014\u2014\u8FD9\u6070\u6070\u662F\u5F53\u5E74\u9053\u6B66\u5E1D\u5728\u521B\u8BBE\u8FD9\u9879\u5236\u5EA6\u65F6\uFF0C\u5FC3\u5FC3\u5FF5\u5FF5\u60F3\u8981\u907F\u514D\u7684\u60C5\u51B5\u3002</p><p>\u5BA3\u6B66\u5E1D\u60F3\u8981\u653E\u98DE\u4E00\u53EA\u548C\u5E73\u9E3D\uFF0C\u4E0D\u60F3\u624B\u4E00\u6325\u653E\u51FA\u7684\u5374\u662F\u4E00\u4E2A\u5973\u5DEB\u5E08\u3002</p><p>\u4ED6\u4EE5\u4E3A\u81EA\u5DF1\u65BD\u7684\u662F\u5584\u653F\uFF0C\u6B8A\u4E0D\u77E5\u4EB2\u624B\u57CB\u4E0B\u7684\uFF0C\u5374\u662F\u5BFC\u81F4\u5317\u9B4F\u8FC5\u901F\u8870\u843D\u7684\u79CD\u5B50\u3002</p><p>\u8FD9\u4F4D\u80E1\u6C0F\uFF0C\u5168\u79F0\u5BA3\u6B66\u7075\u7687\u540E\u80E1\u6C0F\uFF0C\u6B7B\u540E\u88AB\u8FFD\u8C25\u4E3A\u201C\u7075\u201D\u2014\u2014\u597D\u796D\u9B3C\u795E\u66F0\u7075\uFF0C\u53D6\u5176\u6267\u653F\u671F\u95F4\uFF0C\u6D1B\u9633\u57CE\u5927\u4FEE\u4F5B\u5BFA\u4E4B\u610F\u3002</p><p>\u603B\u63FD\u5BA3\u6B66\u5E1D\u7684\u4E00\u751F</p><p>\u300A\u9B4F\u4E66\u300B\u5199\u5230\u6B64\uFF0C\u505A\u4E86\u4E00\u53E5\u8BC4\u4EF7\uFF1A\u201C\u4E34\u671D\u6E0A\u9ED8\uFF0C\u7AEF\u4E25\u82E5\u795E\uFF0C\u6709\u4EBA\u541B\u4E4B\u91CF\u77E3\u3002\u201D\u2014\u2014\u770B\u4E0A\u53BB\u662F\u5728\u5938\uFF0C\u5176\u5B9E\u662F\u5728\u8BBD\u523A\u5BA3\u6B66\u5E1D\u4E0D\u4EB2\u653F\u4E8B\uFF0C\u758F\u8FDC\u5927\u81E3\u3002</p><p>\u800C\u63A5\u4E0B\u6765\u5C31\u5199\u5F97\u5F88\u4E0D\u5BA2\u6C14\u4E86\uFF0C\u201C\u800C\u5BBD\u4EE5\u6444\u4E0B\uFF0C\u4ECE\u5BB9\u4E0D\u65AD\uFF0C\u592A\u548C\u4E4B\u98CE\u66FF\u77E3\u3002\u201D\u2014\u2014\u4EE5\u5BBD\u5F85\u4E0B\uFF0C\u4ECE\u5BB9\u5F97\u8FC7\u4E86\u5934\uFF0C\u592A\u548C\u5E74\u95F4\u7684\u826F\u597D\u98CE\u6C14\u5C31\u8FD9\u4E48\u7ED9\u6BC1\u4E86\u3002</p>',87),r=[t];function n(s,g){return o(),e("div",null,r)}const _=p(d,[["render",n],["__file","63.html.vue"]]);export{_ as default};
