import{_ as p}from"./_plugin-vue_export-helper.cdc0426e.js";import{o,c as e,f as c}from"./app.1226039b.js";const t={},d=c('<h1 id="\u5317\u9B4F\u7684\u8463\u5353" tabindex="-1"><a class="header-anchor" href="#\u5317\u9B4F\u7684\u8463\u5353" aria-hidden="true">#</a> \u5317\u9B4F\u7684\u8463\u5353</h1><blockquote><p>\u5E9A\u5B50\uFF0C\u8363\u9063\u9A91\u6267\u592A\u540E\u53CA\u5E7C\u4E3B\uFF0C\u9001\u81F3\u6CB3\u9634\u3002\u592A\u540E\u5BF9\u8363\u591A\u6240\u9648\u8BF4\uFF0C\u8363\u62C2\u8863\u800C\u8D77\uFF0C\u6C89\u592A\u540E\u53CA\u5E7C\u4E3B\u4E8E\u6CB3\u3002</p></blockquote><p><strong>\u300A\u8D44\u6CBB\u901A\u9274\xB7\u5377\u4E00\u4E94\u4E8C\xB7\u6881\u7EAA\u516B\u300B</strong></p><p><strong>\u5C14\u6731\u8363\u662F\u5317\u9B4F\u706D\u4EA1\u8FDB\u7A0B\u4E2D\u7684\u5173\u952E\u4EBA\u7269\u3002</strong></p><p>\u6070\u5982\u8463\u5353\u5165\u6D1B\u9633\u6572\u54CD\u4E86\u4E1C\u6C49\u738B\u671D\u7684\u4E27\u949F\u4E00\u822C\uFF0C\u5317\u9B4F\u4E5F\u662F\u4EE5\u5C14\u6731\u8363\u8FDB\u5165\u6D1B\u9633\u4E3A\u6807\u5FD7\uFF0C\u5BA3\u544A\u4E86\u5B9E\u9645\u4E0A\u7684\u706D\u4EA1\u3002</p><p>\u867D\u7136\u6B64\u540E\u5317\u9B4F\u4EE5\u53CA\u4E1C\u897F\u9B4F\u53C8\u65AD\u65AD\u7EED\u7EED\u7684\u5B58\u5728\u4E86\u8FD1\u4E09\u5341\u5E74\uFF0C\u4F46\u90A3\u5DF2\u7ECF\u662F\u6743\u81E3\u4E13\u653F\u7684\u65F6\u4EE3\uFF0C\u5143\u6C0F\u7687\u5E1D\u4EEC\u4E0D\u8FC7\u53EA\u662F\u5080\u5121\u800C\u5DF2\u3002</p><p>\u800C\u4E14\uFF0C\u5C14\u6731\u8363\u8FD8\u6709\u53E6\u4E00\u4E2A\u4E0E\u8463\u5353\u7684\u76F8\u4F3C\u4E4B\u5904\uFF1A**\u4ED6\u4EEC\u867D\u7136\u4EB2\u624B\u5F00\u542F\u4E86\u5F53\u65F6\u7684\u4E71\u5C40\uFF0C\u4F46\u5374\u90FD\u6CA1\u80FD\u575A\u6301\u5230\u6700\u540E\u3002**\u5C14\u6731\u8363\u628A\u6301\u671D\u653F\u7684\u65F6\u95F4\u53EA\u6709\u7EA6\u4E09\u5E74\uFF0C\u5176\u5174\u4E5F\u52C3\uFF0C\u5176\u4EA1\u4E5F\u5FFD\u3002</p><p>\u65E9\u5728\u5C14\u6731\u8363\u53D1\u8FF9\u524D\u4E00\u767E\u591A\u5E74\uFF0C\u5C14\u6731\u6C0F\u5C31\u5DF2\u7ECF\u5728\u53F2\u7C4D\u7684\u8BB0\u8F7D\u4E2D\u51FA\u73B0\u8FC7\u4E86\u3002\u90A3\u65F6\u8FD8\u662F\u5317\u9B4F\u5F00\u56FD\u4E4B\u541B<code>\u9053\u6B66\u5E1D</code>\u65F6\u671F\uFF0C\u5C14\u6731\u6C0F\u7684\u5148\u7956\u5C14<code>\u6731\u7FBD\u5065</code>\u56E0\u8DDF\u968F\u9053\u6B66\u5E1D\u51FA\u5F81<code>\u540E\u71D5</code>\u800C\u83B7\u5C01\u79C0\u5BB9\u5DDD\uFF0C\u4E5F\u5C31\u662F\u5982\u4ECA\u7684\u5C71\u897F\u5FFB\u5DDE\u4E00\u5E26\u3002</p><p>\u5F53\u65F6\u5317\u9B4F\u7684\u9996\u90FD\u8FD8\u5728\u5E73\u57CE\uFF0C\u4F60\u53EA\u8981\u7FFB\u5F00\u5730\u56FE\uFF0C\u5C31\u53EF\u4EE5\u770B\u5230\u8FD9\u7247\u5730\u533A\u6B63\u597D\u5728\u5E73\u57CE\u7684\u5357\u65B9\u3002\u8FD9\u65F6\u5019\u4F60\u5C31\u80FD\u770B\u51FA\u6765\u4E86\uFF0C<strong>\u9053\u6B66\u5E1D\u5C06\u5C14\u6731\u6C0F\u5206\u5C01\u4E8E\u6B64\uFF0C\u5176\u5B9E\u662F\u7528\u4ED6\u4EEC\u6765\u770B\u95E8\u7684\u3002</strong></p><p>\u5F53\u65F6\uFF0C\u5317\u9B4F\u5C1A\u662F\u4EE5\u5F02\u65CF\u5165\u4E3B\u4E2D\u539F\u7684\u5FC3\u6001\u6765\u7EDF\u6CBB\u6C49\u5730\uFF0C\u56E0\u6B64\u6709\u8FD9\u4E48\u4E00\u652F\u5951\u80E1\u4EBA\u9A7B\u624E\u4E8E\u4E2D\u539F\u901A\u5F80\u5E73\u57CE\u7684\u8DEF\u4E0A\uFF0C\u5728\u5FC3\u7406\u4E0A\u7684\u5B89\u5168\u611F\u4F1A\u591A\u51FA\u51E0\u5206\u3002</p><p>\u800C\u4E5F\u6B63\u662F\u57FA\u4E8E\u6B64\uFF0C\u9053\u6B66\u5E1D\u665A\u5E74\u867D\u7136\u81F4\u529B\u4E8E\u201C\u79BB\u6563\u8BF8\u90E8\u201D\uFF0C\u4F46\u5374\u504F\u504F\u653E\u8FC7\u4E86\u5C14\u6731\u6C0F\u8FD9\u4E2A\u5C0F\u5C0F\u7684\u5951\u80E1\u90E8\u843D\u3002</p><p>\u6B64\u540E\uFF0C<strong>\u5C14\u6731\u6C0F\u5E2E\u62D3\u8DCB\u6C0F\u5B88\u95E8\uFF0C\u4E00\u5B88\u5C31\u662F\u4E0A\u767E\u5E74\uFF0C\u76F4\u5230<code>\u5B5D\u6587\u5E1D</code>\u8FC1\u90FD\u3002</strong></p><p>\u5B5D\u6587\u5E1D\u8FC1\u90FD\uFF0C\u662F\u5317\u9B4F\u5386\u53F2\u4E0A\u7684\u91CD\u5927\u4E8B\u4EF6\uFF0C\u6B64\u4E3E\u6781\u4E3A\u6DF1\u523B\u5730\u6539\u53D8\u4E86\u5317\u9B4F\u7684\u653F\u6CBB\u751F\u6001\u3002\u800C\u4E5F\u6B63\u662F\u8FD9\u6B21\u53D8\u9769\uFF0C\u7ED9\u5C14\u6731\u6C0F\u5BB6\u65CF\u7684\u4E0A\u5347\uFF0C\u5E26\u6765\u4E86\u7B2C\u4E00\u6B21\u91CD\u5927\u673A\u9047\u3002</p><p>\u6D1B\u9633\u5DF2\u7ECF\u5730\u5904\u4E2D\u539F\u519C\u8015\u6587\u660E\u7684\u6838\u5FC3\u5730\u5E26\uFF0C\u9C9C\u5351\u8D35\u65CF\u4EEC\u5728\u5230\u4E86\u6D1B\u9633\u4EE5\u540E\uFF0C\u624D\u53D1\u73B0\u4EE5\u524D\u968F\u5904\u53EF\u89C1\u7684\u9A6C\u5339\u5728\u8FD9\u91CC\u7ADF\u7136\u6210\u4E86\u7A00\u7F55\u7269\u4EF6\uFF1A\u6D1B\u9633\u5468\u56F4\uFF0C\u662F\u6CA1\u6709\u7267\u573A\u7684\u3002</p><p>\u800C\u79BB\u6D1B\u9633\u6700\u8FD1\u7684\u9A6C\u573A\uFF0C\u5C31\u662F\u5C14\u6731\u6C0F\u7684\u79C0\u5BB9\u5DDD\u4E86\u3002\u5C14\u6731\u6C0F\u4E5F\u654F\u9510\u53C8\u53CA\u65F6\u5730\u53D1\u73B0\u4E86\u8FD9\u4E2A\u673A\u9047\uFF0C\u6B64\u540E\uFF0C\u4ED6\u4EEC\u4E0D\u4EC5\u501F\u6B64\u6362\u56DE\u4E86\u8D4F\u8D50\u65E0\u6570\uFF0C\u800C\u4E14\u5B98\u4F4D\u8FD8\u8282\u8282\u6500\u5347\uFF0C\u4E00\u8DC3\u6210\u4E3A\u6D1B\u9633\u9AD8\u7EA7\u805A\u4F1A\u4E2D\u5C0A\u8D35\u7684\u5BA2\u4EBA\u3002</p><p>\u5E78\u798F\u6765\u5F97\u5C31\u662F\u8FD9\u4E48\u7A81\u7136\uFF0C\u4E00\u767E\u591A\u5E74\u524D\u7684\u5C14\u6731\u6C0F\u4F30\u8BA1\u505A\u68A6\u90FD\u6CA1\u60F3\u5230\uFF0C\u62D3\u8DCB\u6C0F\u5F53\u5E74\u4EE5\u94C1\u9A91\u7ACB\u56FD\uFF0C\u9A6C\u5339\u5927\u6982\u662F\u6700\u5145\u88D5\u7684\u6218\u7565\u7269\u8D44\u4E86\uFF0C\u5982\u4ECA\u65F6\u79FB\u4E16\u6613\uFF0C\u4ED6\u4EEC\u5BB6\u65CF\u7A81\u7136\u5F97\u5230\u5982\u6B64\u5BCC\u8D35\uFF0C\u7ADF\u7136\u9760\u7684\u662F\u9A6C\u5339\u3002</p><p>\u800C\u5F53<code>\u516D\u9547\u4E4B\u4E71</code>\u7206\u53D1\uFF0C\u5C14\u6731\u6C0F\u4E5F\u7EC8\u4E8E\u4F20\u5230\u4E86\u5C14\u6731\u8363\u7684\u624B\u91CC\u3002\u6B64\u65F6\uFF0C\u5C14\u6731\u6C0F\u8FCE\u6765\u4E86\u7B2C\u4E8C\u6B21\u98DE\u8DC3\u7684\u673A\u9047\u3002</p><p>\u773C\u89C1\u7740\u5929\u4E0B\u5927\u4E71\uFF0C\u5C14\u6731\u8363\u5E76\u4E0D\u6EE1\u8DB3\u4E8E\u5728\u5C71\u6C9F\u6C9F\u91CC\u517B\u4E00\u8F88\u5B50\u9A6C\uFF0C\u4ED6\u6563\u5C3D\u5BB6\u8D22\uFF0C\u96C6\u805A\u5B9E\u529B\u3002\u8FD9\u79CD\u86F0\u4F0F\uFF0C\u5728\u4E0D\u4E45\u4E4B\u540E\u5F88\u5FEB\u5C31\u6D3E\u4E0A\u4E86\u7528\u573A\u3002</p><p>\u5F53\u65F6\u5317\u9B4F\u5883\u5185\u5DF2\u7ECF\u72FC\u70DF\u56DB\u8D77\uFF0C\u4F46\u671D\u5EF7\u5374\u5E94\u5BF9\u4E4F\u529B\uFF0C\u5C24\u5176\u662F\u5728\u516D\u9547\u4E4B\u4E71\u7206\u53D1\u540E\uFF0C\u7ADF\u7136\u8FD8\u9700\u8981\u501F\u52A9\u5BBF\u654C\u67D4\u7136\u7684\u529B\u91CF\u624D\u80FD\u5E73\u53DB\u3002\u800C\u5728\u6B64\u524D\u540E\u4E00\u7CFB\u5217\u7684\u5E73\u53DB\u4E2D\uFF0C<strong>\u5C14\u6731\u8363\u51ED\u501F\u81EA\u5DF1\u51FA\u8272\u7684\u519B\u4E8B\u5B9E\u529B\uFF0C\u4EE5\u53CA\u51E0\u4EE3\u4EBA\u79EF\u84C4\u6362\u6765\u7684\u5F3A\u5927\u5B9E\u529B\uFF0C\u5C55\u9732\u5934\u89D2\uFF0C\u7ADF\u7136\u6210\u4E86\u5317\u9B4F\u5883\u5185\u6700\u5927\u7684\u5B9E\u529B\u6D3E\u3002</strong></p><p>\u968F\u7740\u5B9E\u529B\u4E0D\u65AD\u589E\u52A0\uFF0C\u5C14\u6731\u8363\u7684\u91CE\u5FC3\u4E5F\u5728\u4E0D\u65AD\u81A8\u80C0\u3002\u6B66\u6CF0\u5143\u5E74\uFF08\u516C\u5143528\u5E74\uFF09\uFF0C\u5C14\u6731\u8363\u53CD\u590D\u4E0A\u4E66\u671D\u5EF7\uFF0C\u5E0C\u671B\u80FD\u7387\u519B\u7FFB\u8D8A\u592A\u884C\u5C71\uFF0C\u8FDB\u5165\u6CB3\u5317\u2014\u2014\u867D\u7136\u660E\u9762\u4E0A\u7684\u76EE\u7684\u662F\u53BB\u5E73\u5B9A\u6CB3\u5317\u7684\u53DB\u4E71\uFF0C\u4F46\u771F\u5B9E\u7684\u60F3\u6CD5\uFF0C\u5176\u5B9E\u662F\u60F3\u8981\u501F\u673A\u6269\u5927\u81EA\u5DF1\u7684\u52BF\u529B\u8303\u56F4\u3002</p><p>\u4E0D\u8FC7\u6B64\u65F6\u671D\u5EF7\u867D\u7136\u52BF\u5F31\uFF0C\u4F46\u6BD5\u7ADF\u8FD8\u6709\u6743\u5A01\u5728\u3002<strong><code>\u5B5D\u660E\u5E1D</code>\u660E\u4EE4\u7981\u6B62\u5C14\u6731\u8363\u8D8A\u5883</strong>\uFF0C\u52C9\u529B\u5C06\u5176\u52BF\u529B\u63A7\u5236\u5728\u4E86\u592A\u884C\u5C71\u4EE5\u897F\u3001\u9EC4\u6CB3\u4EE5\u5317\u7684\u8303\u56F4\u4E4B\u5185\u3002</p><p>\u53EF\u662F\uFF01</p><p>\u5C31\u5728\u4E0D\u4E45\u4EE5\u540E\uFF0C\u5B5D\u660E\u5E1D\u5374\u4E00\u6539\u524D\u6001\uFF0C\u53CD\u800C\u547D\u4EE4\u5C14\u6731\u8363\u5357\u4E0B\u5165\u4EAC\u3002</p><p>\u56E0\u4E3A\u5B5D\u660E\u5E1D\u53D7\u5230\u4E86<code>\u80E1\u592A\u540E</code>\u7684\u5DE8\u5927\u5A01\u80C1\u3002</p><p>\u4EBA\u65E0\u8FDC\u8651\u5FC5\u6709\u8FD1\u5FE7\uFF0C\u6B64\u65F6\u5B5D\u660E\u5E1D\u5DF2\u7ECF\u65E0\u6687\u987E\u53CA\u5C14\u6731\u8363\u5750\u5927\u7684\u5371\u5BB3\u4E86\uFF0C\u5BF9\u4ED6\u6765\u8BF4\uFF0C\u773C\u524D\u7684\u5A01\u80C1\u624D\u662F\u5F53\u52A1\u4E4B\u6025\uFF1A\u4ED6\u53D7\u5230\u4E86\u80E1\u592A\u540E\u7684\u5A01\u80C1\uFF0C\u8FD9\u79CD\u5A01\u80C1\u4E4B\u5927\uFF0C\u5DF2\u7ECF\u5230\u4E86\u8BA9\u4ED6\u5750\u7ACB\u4E0D\u5B89\u7684\u5730\u6B65\u3002</p><p>\u800C\u4E14\u4E4B\u540E\u5386\u53F2\u7684\u53D1\u5C55\uFF0C\u4E5F\u8BC1\u660E\u4E86\u5B5D\u660E\u5E1D\u7684\u8FD9\u79CD\u62C5\u5FE7\u5E76\u975E\u675E\u4EBA\u5FE7\u5929\u3002<strong>\u5C3D\u7BA1\u5C14\u6731\u8363\u5728\u63A5\u8BCF\u540E\u4FBF\u8FC5\u901F\u5357\u4E0B\uFF0C\u4F46\u5C1A\u672A\u8FDB\u4EAC\uFF0C\u5B5D\u660E\u5E1D\u5373\u88AB\u6BCD\u4EB2\u80E1\u592A\u540E\u7ED9\u6BD2\u6B7B\u3002</strong></p><p>\u53EF\u8FD9\u5BF9\u5C14\u6731\u8363\u800C\u8A00\uFF0C\u5374\u610F\u5473\u7740\u66F4\u5927\u7684\u5929\u8D50\u826F\u673A\u3002</p><p>\u51E0\u6708\u540E\uFF0C\u5C14\u6731\u8363\u8FDB\u5165\u6D1B\u9633\uFF0C\u62E8\u4E71\u53CD\u6B63\u3002\u80E1\u592A\u540E\u773C\u89C1\u4E0D\u654C\uFF0C\u4E3B\u52A8\u843D\u53D1\u4E3A\u5C3C\uFF0C\u5165\u4F5B\u5BFA\u907F\u96BE\u800C\u672A\u679C\u3002<strong>\u5C14\u6731\u8363\u5C06\u80E1\u592A\u540E\u6C89\u4E8E\u9EC4\u6CB3\u4E4B\u4E2D\uFF0C\u5E76\u62E5\u7ACB\u957F\u4E50\u738B<code>\u5143\u5B50\u6538</code>\u4E3A\u5E1D\uFF0C\u662F\u4E3A<code>\u5B5D\u5E84\u5E1D</code>\u3002</strong></p><p>\u63A5\u4E0B\u6765\uFF0C\u5C14\u6731\u8363\u7684\u4F5C\u4E3A\u8BA9\u4EBA\u77A0\u76EE\u7ED3\u820C\uFF1A</p><p>\u5C14\u6731\u8363\u5728\u6740\u6389\u80E1\u592A\u540E\u540E\uFF0C\u4EE5\u796D\u5929\u4E3A\u540D\uFF0C<strong>\u5C06\u6587\u6B66\u5B98\u5458\u8FD1\u4E24\u5343\u4EBA\u8BD3\u9A97\u81F3\u9EC4\u6CB3\u8FB9</strong>\u3002\u4ED6\u5F53\u4F17\u65A5\u8D23\u767E\u5B98\u52A9\u7EA3\u4E3A\u8650\uFF0C\u7136\u540E\uFF0C<strong>\u628A\u8FD9\u4E9B\u4EBA\u5168\u90E8\u6740\u6389\uFF0C\u53F2\u79F0\u6CB3\u9634\u4E4B\u53D8</strong>\u3002</p><p>\u81F3\u6B64\uFF0C\u5C14\u6731\u8363\u8BA4\u4E3A\u81EA\u5DF1\u5DF2\u7ECF\u6D88\u706D\u6389\u4E86\u79F0\u5E1D\u7684\u6240\u6709\u53CD\u5BF9\u529B\u91CF\uFF0C\u4FBF\u51C6\u5907\u5E9F\u6389\u5B5D\u5E84\u5E1D\uFF0C\u81EA\u7ACB\u4E3A\u5E1D\u3002\u8FD9\u4E2A\u65F6\u5019\uFF0C\u7ADF\u7136\u8FD8\u662F\u5317\u9B4F\u6D41\u4F20\u4E86\u4E00\u767E\u591A\u5E74\u7684\u653F\u6CBB\u4F20\u7EDF\u62E6\u4F4F\u4E86\u5C14\u6731\u8363\uFF1A\u5F53\u65F6\uFF0C\u518C\u7ACB\u592A\u5B50\u3001\u7687\u540E\uFF0C\u9700\u201C\u94F8\u91D1\u4EBA\u201D\u6765\u7948\u7977\uFF0C\u7ED3\u679C\u5C14\u6731\u8363\u201C\u56DB\u94F8\u4E0D\u5C31\u201D\u3002</p><p>\u4E0A\u5929\u8B66\u793A\u5982\u6B64\uFF0C\u5C14\u6731\u8363\u53EA\u80FD\u662F\u653E\u5F03\uFF0C\u8F6C\u56DE\u5934\u7EE7\u7EED\u8D70\u81EA\u5DF1\u7684<code>\u6743\u81E3</code>\u4E4B\u8DEF\u3002</p><p>\u5728\u6D1B\u9633\u7684\u5C40\u9762\u57FA\u672C\u4E0A\u5B89\u5B9A\u4EE5\u540E\uFF0C\u5C14\u6731\u8363\u4FBF\u542F\u7A0B\u56DE\u5230\u664B\u9633\uFF0C\u9065\u63A7\u671D\u653F\u3002</p><p>\u6B64\u65F6\u7684\u5317\u9B4F\uFF0C\u5DF2\u7ECF\u5185\u5FE7\u5916\u60A3\u4E0D\u65AD\uFF1A</p><p>\u5728\u6D1B\u9633\uFF0C\u56E0\u5C14\u6731\u8363\u5927\u5F00\u6740\u6212\uFF0C\u671D\u5EF7\u4E3A\u4E4B\u4E00\u7A7A\uFF1B\u5730\u65B9\u4E0A\uFF0C\u6CB3\u5317\u3001\u5173\u4E2D\u3001\u9752\u5F90\u4E00\u5E26\u5747\u6709\u53DB\u4E71\uFF0C\u72FC\u70DF\u56DB\u8D77\uFF1B\u540C\u65F6\uFF0C\u5317\u6D77\u738B<code>\u5143\u98A2</code>\u5728\u5C14\u6731\u8363\u5165\u6D1B\u540E\uFF0C\u6295\u9760\u6881\u671D\uFF0C\u6B64\u65F6\u6B63\u5728\u88AB\u6881\u671D\u5C06\u9886<code>\u9648\u5E86\u4E4B</code>\u62A4\u9001\u5317\u5F52\u3002</p><p>\u8FD9\u79CD\u5C40\u9762\uFF0C\u5C14\u6731\u8363\u7ADF\u7136\u9760\u7740\u81EA\u5DF1\u60CA\u4EBA\u7684\u519B\u4E8B\u5B9E\u529B\uFF0C\u786C\u6491\u4E86\u4E0B\u6765\u3002</p><blockquote><p>\u8FD9\u5F53\u4E2D\uFF0C\u53D1\u6325\u4E86\u51B3\u5B9A\u6027\u4F5C\u7528\u7684\u6218\u6597\u6709\u4E24\u573A\uFF1A</p></blockquote><p>**\u7B2C\u4E00\u573A\u662F\u5728\u6CB3\u5317\u51FB\u8D25<code>\u845B\u8363</code>\u3002**\u81F3\u6B64\uFF0C\u516D\u9547\u4E4B\u4E71\u4EE5\u53CA\u540E\u7EED\u884D\u751F\u51FA\u7684\u53DB\u4E71\u88AB\u5168\u90E8\u5E73\u5B9A\u2014\u2014\u867D\u7136\uFF0C\u6B64\u65F6\u7684\u5317\u9B4F\u5DF2\u7ECF\u662F\u4E00\u7247\u72FC\u85C9\u3002</p><p><strong>\u7B2C\u4E8C\u573A\uFF0C\u5219\u662F\u51FB\u8D25\u4E86<code>\u9648\u5E86\u4E4B</code>\u9001<code>\u5143\u98A2</code>\u5317\u5F52\u7684\u519B\u961F</strong>\u2014\u2014\u5173\u4E8E\u9648\u5E86\u4E4B\u7684\u4E8B\u8FF9\uFF0C\u6211\u4EEC\u4F1A\u5728\u5C06\u6765\u7684\u6587\u7AE0\u4E2D\u63D0\u5230\u3002</p><p>\u7ECF\u8FC7\u8FD9\u4E24\u6B21\u6218\u4E89\uFF0C<strong>\u5C14\u6731\u8363\u5B8C\u5168\u63A7\u5236\u4F4F\u4E86\u5317\u9B4F</strong>\u3002\u8FD9\u4E0E\u5F53\u5E74\u7684\u8463\u5353\u5012\u4E0D\u76F8\u7C7B\uFF0C\u8463\u5353\u5F53\u521D\u867D\u7136\u63A7\u5236\u4F4F\u4E86\u671D\u5EF7\uFF0C\u4F46\u5730\u65B9\u8BF8\u4FAF\u5374\u90FD\u89C6\u4ED6\u4E3A\u4EC7\u96E0\u3002\u5982\u4ECA\u5219\u4E0D\u540C\uFF0C\u5317\u9B4F\u5DF2\u7ECF\u5B8C\u5168\u662F\u5C14\u6731\u8363\u7684\u56CA\u4E2D\u4E4B\u7269 \u3002</p><p>\u56E0\u6B64\uFF0C\u5728\u79BB\u5F00\u6D1B\u9633\u4E24\u5E74\u591A\u4EE5\u540E\uFF0C\u5C14\u6731\u8363\u518D\u6B21\u5165\u6D1B\u3002</p><p>\u800C\u6B64\u65F6\uFF0C\u7559\u7ED9\u5B5D\u5E84\u5E1D\u7684\uFF0C\u4E5F\u5C31\u53EA\u6709\u4E00\u6761\u8DEF\u4E86\u3002</p><p>\u5C14\u6731\u8363\u5728\u8FDB\u5165\u6D1B\u9633\u4EE5\u524D\uFF0C\u66FE\u6709\u5F88\u591A\u4EBA\u529D\u4ED6\u653E\u5F03\u6B64\u884C\uFF0C\u4F46\u5C14\u6731\u8363\u5374\u4E0D\u4E3A\u6240\u52A8\uFF0C<strong>\u4ED6\u8BA4\u4E3A\u81EA\u5DF1\u7ACB\u5927\u529F\u4E8E\u56FD\uFF0C\u7406\u6240\u5E94\u5F53\u4F1A\u5F97\u5230\u6D1B\u9633\u9AD8\u5C42\u7684\u7ADE\u76F8\u819C\u62DC\u3002</strong></p><p>\u4F46\u73B0\u5B9E\u4E0E\u4ED6\u7684\u6240\u6599\u5374\u622A\u7136\u76F8\u53CD\u3002</p><p>\u5EFA\u660E\u5143\u5E74\u4E5D\u6708\uFF08\u516C\u5143530\u5E74\uFF09\uFF0C\u5C14\u6731\u8363\u5728\u89D0\u89C1\u5B5D\u5E84\u5E1D\u65F6\uFF0C\u88AB\u5B5D\u5E84\u5E1D\u6240\u6740\uFF1A</p><p><strong>\u624B\u5203\u3002</strong></p><p>\u5230\u8FD9\u91CC\uFF0C\u6211\u4EEC\u4E0D\u59A8\u518D\u6765\u68B3\u7406\u4E00\u4E0B\u6574\u4E2A\u65F6\u95F4\u7EBF\uFF08\u4EE5\u516C\u5143\u8BA1\uFF09\uFF1A</p><blockquote><p>524\u5E74</p></blockquote><p>\u516D\u9547\u4E4B\u4E71\u7206\u53D1\u3002</p><blockquote><p>526\u5E74</p></blockquote><p><code>6\u6708</code>\uFF0C\u516D\u9547\u4E4B\u4E71\u88AB\u5E73\u5B9A\uFF0C\u671D\u5EF7\u8FC1\u516D\u9547\u9547\u6C11\u4E8C\u5341\u4F59\u4E07\u4EBA\u81F3\u6CB3\u5317\u4E09\u5DDE\u5C31\u98DF\u3002</p><p><code>\u4E24\u4E2A\u6708\u540E</code>\uFF0C\u516D\u9547\u4E4B\u6C11\u4E8E\u6CB3\u5317\u590D\u53CD\u3002</p><blockquote><p>528\u5E74</p></blockquote><p><code>2\u6708</code>\uFF0C\u80E1\u592A\u540E\u6BD2\u6740\u5B5D\u660E\u5E1D\u3002</p><p><code>4\u6708</code>\uFF0C\u5C14\u6731\u8363\u5165\u6D1B\u9633\uFF0C\u5B5D\u5E84\u5E1D\u5143\u5B50\u6538\u5728\u5C14\u6731\u8363\u7684\u62E5\u62A4\u4E0B\u7EE7\u4F4D\uFF0C\u80E1\u592A\u540E\u88AB\u6740\u3002</p><p><code>\u5F53\u6708</code>\uFF0C\u5C14\u6731\u8363\u6740\u6587\u6B66\u767E\u5B98\u4E24\u5343\u4F59\u4EBA\u3002</p><p><code>5\u6708</code>\uFF0C\u5C14\u6731\u8363\u8FD4\u56DE\u664B\u9633\u3002</p><p><code>9\u6708</code>\uFF0C\u5C14\u6731\u8363\u51FA\u5F81\u6CB3\u5317\uFF0C\u845B\u8363\u5175\u8D25\u88AB\u64D2\uFF0C\u5E76\u4E8E\u6B21\u6708\u88AB\u6740\u3002</p><p><code>10\u6708</code>\uFF0C\u5357\u6881\u9648\u5E86\u4E4B\u9001\u51FA\u9003\u6881\u671D\u7684\u5317\u6D77\u738B\u5143\u98A2\u5317\u5F52\u3002</p><blockquote><p>529\u5E74</p></blockquote><p><code>5\u6708</code>\uFF0C\u5143\u98A2\u653B\u5165\u6D1B\u9633\uFF0C\u5B5D\u5E84\u5E1D\u5317\u9003\u3002</p><p><code>6\u6708</code>\uFF0C\u5C14\u6731\u8363\u51FB\u8D25\u9648\u5E86\u4E4B\uFF0C\u5143\u98A2\u88AB\u6740\u3002</p><blockquote><p>530\u5E74</p></blockquote><p><code>9\u6708</code>\uFF0C\u5C14\u6731\u8363\u518D\u6B21\u5165\u6D1B\uFF0C\u4E0D\u4E45\u540E\u88AB\u5B5D\u5E84\u5E1D\u624B\u5203\u3002</p><p>\u4EE5\u7B2C\u4E00\u6B21\u79BB\u5F00\u6D1B\u9633\u4E3A\u6807\u5FD7\uFF0C\u53EF\u4EE5\u5F88\u660E\u663E\u7684\u770B\u51FA\uFF0C\u5C14\u6731\u8363\u5728\u79F0\u5E1D\u4E0D\u6210\u540E\uFF0C\u5F80\u4E0B\u9000\u4E86\u4E00\u6B65\uFF0C\u91CD\u65B0\u5F00\u59CB\u8D70\u6743\u81E3\u7BE1\u4F4D\u7684\u8DEF\uFF1A\u79EF\u6512\u519B\u529F\uFF0C\u6269\u5145\u5B9E\u529B\uFF0C\u63D0\u5347\u5B98\u7235\u3002</p><p>**\u800C\u5E73\u5B9A\u845B\u8363\u548C\u51FB\u9000\u9648\u5E86\u4E4B\uFF0C\u4E8E\u5317\u9B4F\u800C\u8A00\uFF0C\u7B97\u5F97\u4E0A\u662F\u518D\u9020\u4E4B\u529F\u3002**\u53EF\u4EE5\u8FD9\u4E48\u8BF4\uFF0C\u5982\u679C\u6CA1\u6709\u5C14\u6731\u8363\uFF0C\u90A3\u4E48\u5317\u9B4F\u5FC5\u7136\u4F1A\u5728\u5185\u5916\u5939\u51FB\u4E4B\u4E0B\u8D70\u5411\u5D29\u6E83\u3002</p><p>\u6240\u4EE5\uFF0C\u5C14\u6731\u8363\u624D\u4F1A\u5929\u771F\u7684\u4EE5\u4E3A\uFF0C\u5F53\u4ED6\u4E8C\u8FDB\u6D1B\u9633\uFF0C\u8FCE\u63A5\u4ED6\u7684\u5C06\u662F\u9C9C\u82B1\u548C\u638C\u58F0\u3002</p><p>\u4F46\u4ED6\u5374\u5FFD\u7565\u4E86\u4E00\u4E2A\u8981\u547D\u7684\u95EE\u9898\uFF0C\u867D\u7136\u4ED6\u6709\u5927\u529F\u4E8E\u671D\u5EF7\uFF0C\u4F46\u8FD9\u5BF9\u6D1B\u9633\u7684\u738B\u516C\u5927\u81E3\u800C\u8A00\u5374\u662F\u4E00\u573A\u4E0D\u6298\u4E0D\u6263\u7684\u707E\u96BE\uFF1A<strong>\u4ED6\u7684\u52BF\u529B\u8D8A\u5927\uFF0C\u52CB\u8D35\u4EEC\u7684\u5B89\u5168\u5C31\u8D8A\u96BE\u5F97\u4EE5\u4FDD\u8BC1</strong>\u2014\u2014\u4E0D\u53EA\u662F\u5BF9\u5B5D\u5E84\u5E1D\u5143\u5B50\u6538\u800C\u8A00\uFF0C\u8FD8\u5305\u62EC\u6240\u6709\u7684\u738B\u516C\u5927\u81E3\uFF0C\u6BD5\u7ADF\u4ED6\u5C14\u6731\u8363\u662F\u6709\u524D\u79D1\u7684\uFF0C\u8C01\u77E5\u9053\u4ED6\u8FD9\u6B21\u4F1A\u4E0D\u4F1A\u518D\u5EA6\u5927\u5F00\u6740\u6212\uFF1F</p><p>\u4ED7\u7740\u81EA\u5DF1\u7684\u6B66\u529B\uFF0C\u5C14\u6731\u8363\u5176\u5B9E\u65E9\u5C31\u5DF2\u7ECF\u628A\u6240\u6709\u4EBA\u90FD\u8D76\u5230\u4E86\u5BF9\u7ACB\u9762\u4E0A\u53BB\uFF0C\u628A\u81EA\u5DF1\u53D8\u6210\u4E86\u6D1B\u9633\u9AD8\u5C42\u7684\u773C\u4E2D\u9489\u8089\u4E2D\u523A\u3002</p><p><strong>\u800C\u591A\u4E00\u4E2A\u654C\u4EBA\uFF0C\u5C31\u662F\u591A\u4E00\u4EFD\u5371\u9669\u3002</strong></p><p>\u751A\u81F3\u4E8E\uFF0C\u5C31\u8FDE\u4ED6\u4EB2\u81EA\u9009\u5B9A\u6276\u6301\u7684\u5B5D\u5E84\u5E1D\u5143\u5B50\u6538\uFF0C\u4E5F\u90FD\u575A\u5B9A\u5730\u7AD9\u5230\u4E86\u4ED6\u7684\u5BF9\u7ACB\u9762\u3002\u56E0\u4E3A\u5143\u5B50\u6538\u77E5\u9053\uFF0C\u8FD9\u4E9B\u5E74\u4ED6\u4E4B\u6240\u4EE5\u8FD8\u80FD\u5F85\u5728\u7687\u4F4D\u4E0A\uFF0C\u65E0\u975E\u662F\u56E0\u4E3A\u5C14\u6731\u8363\u7684\u201C\u91D1\u4EBA\u672A\u5C31\u201D\u3002\u5982\u4ECA\u773C\u770B\u7740\u5C14\u6731\u8363\u7684\u6743\u52BF\u5DF2\u7ECF\u8D8A\u6765\u8D8A\u5927\uFF0C\u81EA\u5DF1\u5982\u679C\u518D\u4E0D\u52A8\u624B\uFF0C\u90A3\u4E48\u53EF\u80FD\u5C31\u771F\u7684\u8981\u88AB\u5C14\u6731\u8363\u7528\u5B8C\u6254\u6389\u4E86\u3002</p><p>\u8F7B\u654C\u5165\u5BAB\u7684\u5C14\u6731\u8363\uFF0C\u547D\u8FD0\u5176\u5B9E\u65E9\u5DF2\u88AB\u6CE8\u5B9A\u3002</p><p>**\u592A\u591A\u5730\u70AB\u8000\u548C\u4F7F\u7528\u6B66\u529B\uFF0C\u53EA\u4F1A\u628A\u6240\u6709\u7684\u4EBA\u90FD\u903C\u5230\u81EA\u5DF1\u7684\u5BF9\u7ACB\u9762\u53BB\u3002**\u6B64\u540E\uFF0C\u4F60\u6BCF\u884C\u4E00\u6B65\u90FD\u5C06\u51F6\u9669\u4E07\u5206\uFF0C\u56E0\u4E3A\u6240\u6709\u4EBA\u90FD\u662F\u4F60\u7684\u654C\u4EBA\u3002</p><p>\u751A\u81F3\u4E8E\uFF0C\u90A3\u4E9B\u4E0E\u4F60\u5E76\u65E0\u51B2\u7A81\u7684\u4EBA\uFF0C\u5728\u4F60\u6709\u4E86\u8FD9\u4E48\u591A\u7684\u654C\u4EBA\u4EE5\u540E\uFF0C\u4E5F\u90FD\u4F1A\u53D8\u6210\u4F60\u6F5C\u5728\u7684\u654C\u4EBA\u2014\u2014\u4E0D\u53EA\u662F\u56E0\u4E3A\u5154\u6B7B\u72D0\u60B2\uFF0C<strong>\u66F4\u56E0\u4E3A\u4ED6\u53EA\u8981\u80FD\u628A\u4F60\u6740\u4E86\uFF0C\u5C31\u4F1A\u5F97\u5230\u6700\u5927\u591A\u6570\u4EBA\u7684\u62E5\u62A4\u548C\u652F\u6301\u3002</strong></p><p>\u8BF4\u767D\u4E86\uFF0C\u5230\u5904\u9732\u51FA\u7360\u7259\u9707\u6151\u4ED6\u4EBA\uFF0C\u8FD9\u79CD\u884C\u4E3A\u5E26\u6765\u7684\u5B89\u5168\u611F\u53EA\u662F\u4E00\u79CD\u81EA\u6211\u9676\u9189\u7684\u865A\u5E7B\uFF0C\u5B9E\u9645\u4E0A\uFF0C\u8FD9\u5374\u662F\u4E00\u4E2A\u8BA9\u6240\u6709\u4EBA\u90FD\u8D70\u5728\u4E00\u8D77\uFF0C\u53D8\u6210\u4F60\u7684\u654C\u4EBA\u7684\u8FC7\u7A0B\u3002</p><p>\u5C14\u6731\u8363\u6B7B\u540E\uFF0C\u53F2\u4E66\u4E2D\u7684\u8BB0\u5F55\u5982\u4E0B\uFF1A</p><blockquote><p><strong>\u4E8E\u662F\u5185\u5916\u559C\u566A\uFF0C\u58F0\u6EE1\u6D1B\u9633\u57CE\uFF0C\u767E\u50DA\u5165\u8D3A\u3002</strong></p></blockquote><p>\u4E8B\u540E\u6765\u770B\uFF0C\u5C14\u6731\u8363\u5C60\u6740\u6D1B\u9633\u7684\u738B\u516C\u8D35\u65CF\uFF0C\u662F\u4E00\u4E2A\u5F88\u8822\u7684\u51B3\u5B9A\u3002</p><p>\u5728\u8FD9\u91CC\uFF0C\u6211\u4EEC\u4E0D\u59A8\u7ED3\u5408\u5C14\u6731\u8363\u7684\u6210\u957F\u8FC7\u7A0B\u6765\u505A\u4E00\u4E2A\u731C\u60F3\uFF0C\u8BD5\u7740\u89E3\u91CA\u4E00\u4E0B\u4ED6\u7684\u8FD9\u79CD\u4E0D\u7406\u667A\u884C\u4E3A\u3002</p><p><strong>\u5C14\u6731\u8363\u7684\u5168\u90E8\u751F\u5B58\u73AF\u5883\uFF0C\u63D0\u4F9B\u7ED9\u4ED6\u7684\u6559\u80B2\u90FD\u662F\u201C\u5F3A\u8005\u4E3A\u738B\u201D</strong>\uFF0C\u4E0D\u7BA1\u662F\u80E1\u4EBA\u90E8\u843D\u751F\u6D3B\u4E2D\u7684\u5F31\u8089\u5F3A\u98DF\uFF0C\u8FD8\u662F\u6253\u730E\u65F6\u83B7\u5F97\u7684\u9163\u7545\u6DCB\u6F13\uFF0C\u53C8\u6216\u8005\u662F\u5728\u56DB\u65B9\u5E73\u53DB\u65F6\u7684\u8086\u610F\u6740\u622E......\u8FD9\u4E9B\u7ECF\u5386\uFF0C\u5176\u5B9E\u90FD\u5728\u4E00\u6B21\u6B21\u5730\u5F3A\u5316\u4ED6\u5FC3\u4E2D\u7684\u4E00\u79CD\u4FE1\u5FF5\uFF1A\u53EA\u6709\u5B9E\u529B\uFF0C\u624D\u662F\u51B3\u5B9A\u4E00\u5207\u7684\u6839\u672C\uFF1B\u53EA\u6709\u6740\u622E\uFF0C\u624D\u80FD\u9707\u6151\u5F97\u4F4F\u5BF9\u624B\u3002</p><p>\u53EF\u4EE5\u8FD9\u4E48\u8BF4\uFF0C\u5728\u8FDB\u5165\u6D1B\u9633\u4E4B\u524D\uFF0C\u5C14\u6731\u8363\u7684\u6574\u4E2A\u4EBA\u751F\u90FD\u5904\u5728\u4E00\u79CD\u91CE\u86EE\u4E16\u754C\u4E4B\u4E2D\uFF0C\u5F3A\u4E0E\u5F31\u7684\u5206\u91CE\uFF0C\u53EA\u5728\u4E8E\u624B\u4E2D\u7684\u5200\u3002</p><p>\u4F46\u6587\u660E\u4E16\u754C\u7684\u76F8\u5904\u4E4B\u9053\u5374\u5E76\u975E\u5982\u6B64\u3002</p><p>\u6D1B\u9633\u5B98\u573A\u867D\u7136\u9A84\u5962\u6DEB\u9038\uFF0C\u867D\u7136\u5BE1\u5EC9\u9C9C\u803B\uFF0C\u4F46\u6743\u529B\u5C42\u5374\u81EA\u6709\u4E00\u5957\u81EA\u5DF1\u7684\u4EA4\u5F80\u65B9\u5F0F\u3002\u4F60\u60F3\u7BE1\u4F4D\uFF0C\u8FD9\u4E0D\u662F\u4E0D\u53EF\u4EE5\uFF0C\u4F46\u5F97\u4E00\u6B65\u4E00\u6B65\u6765\u3002\u524D\u9762\u6709\u592A\u591A\u7684\u7ECF\u9A8C\u4F60\u4E0D\u5B66\uFF0C\u4E00\u4E0A\u6765\u5C31\u5BF9\u4EBA\u52A8\u5200\u5B50\uFF0C\u6700\u540E\u5931\u8D25\u4E86\uFF0C\u602A\u8C01\u5462\uFF1F</p><p>\u5C14\u6731\u8363\u4EE5\u4E3A\u628A\u8FD9\u4E9B\u4EBA\u6740\u6389\uFF0C\u5C31\u80FD\u4E00\u4E86\u767E\u4E86\u3002<strong>\u6B8A\u4E0D\u77E5\u4ED6\u53EA\u662F\u5728\u4EE5\u4ED6\u81EA\u5DF1\u7684\u91CE\u86EE\u65B9\u5F0F\uFF0C\u6765\u5BF9\u6297\u4E00\u4E2A\u5B8C\u5168\u6CA1\u6709\u63A5\u89E6\u8FC7\u7684\u5F02\u6B21\u5143\u4E16\u754C\u3002</strong></p><p>\u5C31\u50CF\u4E00\u5934\u91CE\u725B\u731B\u5730\u624E\u8FDB\u4E86\u74F7\u5668\u5E97\uFF0C\u4F60\u5F53\u7136\u53EF\u4EE5\u6A2A\u51B2\u76F4\u649E\uFF0C\u4F46\u53EA\u8981\u4F60\u8FD8\u60F3\u7EE7\u7EED\u7ECF\u8425\u8FD9\u5BB6\u74F7\u5668\u5E97\uFF0C\u90A3\u5C31\u5F97\u987E\u53CA\u74F7\u5668\u4EEC\u7684\u611F\u53D7\u3002\u82E5\u8FD8\u60F3\u8981\u74F7\u5668\u4EEC\u7EE7\u7EED\u8DDF\u4F60\u73A9\uFF0C\u5373\u4FBF\u5FC3\u91CC\u518D\u600E\u4E48\u6076\u5FC3\u8FD9\u4E9B\u74F7\u5668\uFF0C\u4E5F\u5F97\u5BF9\u5B83\u4EEC\u8F7B\u62FF\u8F7B\u653E\uFF0C\u987A\u7740\u74F7\u5668\u5E97\u7684\u6446\u653E\u683C\u5C40\uFF0C\u6162\u6162\u6B23\u8D4F\u3002</p><p>\u5B9E\u529B\u8BDA\u7136\u662F\u4E00\u5207\u7684\u57FA\u7840\uFF0C\u6740\u622E\u4E5F\u786E\u662F\u8BC1\u660E\u5B9E\u529B\u7684\u6700\u597D\u529E\u6CD5\uFF0C\u4F46\u662F\uFF0C<strong>\u4E00\u5473\u5730\u7528\u5F20\u7259\u821E\u722A\u6765\u663E\u9732\u81EA\u5DF1\u7684\u5B9E\u529B\uFF0C\u9760\u6B66\u529B\u548C\u6740\u622E\u53BB\u9707\u6151\u522B\u4EBA\uFF0C\u6362\u4E0D\u6765\u5C0A\u91CD\uFF0C\u4E5F\u6362\u4E0D\u6765\u5B89\u5168\uFF0C\u53EA\u80FD\u6362\u6765\u522B\u4EBA\u5BF9\u4F60\u7684\u540C\u4EC7\u654C\u5FFE\u3002</strong></p><p>\u5C14\u6731\u8363\u7684\u95EE\u9898\uFF0C\u5C31\u5728\u4E8E\u4ED6\u5E26\u7740\u4E00\u79CD\u76F2\u76EE\u7684\u81EA\u4FE1\u8FDB\u5165\u4E86\u6D1B\u9633\u7684\u5B98\u573A\uFF0C\u65E2\u4E0D\u9075\u5B88\u5BF9\u624B\u7684\u89C4\u5219\uFF0C\u4E5F\u4E0D\u5C0A\u91CD\u5BF9\u624B\u7684\u8BC9\u6C42\uFF0C\u6700\u7EC8\uFF0C\u906D\u5230\u53CD\u566C\u3002</p>',87),r=[d];function n(s,g){return o(),e("div",null,r)}const a=p(t,[["render",n],["__file","65.html.vue"]]);export{a as default};
