import{_ as p}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as e,c as t,f as a}from"./app.2ed33847.js";const c={},o=a('<h1 id="\u7B2C7\u96C6-\u9A86\u7236\u5077\u8D70\u5DE8\u989D\u94B1\u8D22" tabindex="-1"><a class="header-anchor" href="#\u7B2C7\u96C6-\u9A86\u7236\u5077\u8D70\u5DE8\u989D\u94B1\u8D22" aria-hidden="true">#</a> \u7B2C7\u96C6\uFF1A\u9A86\u7236\u5077\u8D70\u5DE8\u989D\u94B1\u8D22</h1><p>\u9648\u91D1\u6C34\u7EC8\u4E8E\u9192\u6765\uFF0C\u521A\u521A\u9192\u6765\u5C31\u544A\u8BEB\u6C5F\u6CB3\u4E0D\u8981\u5A36\u7389\u73E0\u8FD9\u6837\u7684\u5973\u4EBA\uFF0C\u8BF4\u7389\u73E0\u5FC3\u91CE\u3002\u9648\u6C5F\u6CB3\u4E0D\u613F\u548C\u521A\u521A\u9192\u8FC7\u6765\u7684\u9648\u91D1\u6C34\u53D1\u751F\u51B2\u7A81\uFF0C\u501F\u53E3\u8BF4\u8981\u53BB\u627E\u533B\u751F\uFF0C\u79BB\u5F00\u4E86\u75C5\u623F\u3002</p><p>\u521A\u8D70\u5230\u533B\u751F\u529E\u516C\u5BA4\uFF0C\u5C31\u542C\u5230\u4E24\u4F4D\u60F3\u8981\u6765\u770B\u671B\u9648\u91D1\u6C34\u7684\u9547\u653F\u5E9C\u7684\u540C\u5FD7\uFF0C\u5728\u8BA8\u8BBA\u8BF4\u4E0D\u8981\u544A\u8BC9\u9648\u91D1\u6C34\u8FD9\u4EF6\u4E8B\u60C5\uFF0C\u8FD8\u8BF4\u8FD9\u4EF6\u4E8B\u60C5\u7275\u626F\u5230\u4E86\u9648\u6C5F\u6CB3\u3002\u6C5F\u6CB3\u62E6\u4F4F\u4ED6\u4EEC\uFF0C\u95EE\u51FA\u4E86\u4E8B\u60C5\u7684\u7ECF\u8FC7\u3002</p><p>\u6C5F\u6CB3\u542C\u8BF4\u4E86\u4E8B\u60C5\u7684\u539F\u59D4\uFF0C\u9A91\u4E0A\u81EA\u884C\u8F66\u8F6C\u5934\u51B2\u5411\u53BF\u59D4\u62DB\u5F85\u6240\u90B1\u82F1\u6770\u7684\u4F4F\u5904\uFF0C\u8DEF\u4E0A\uFF0C\u7389\u73E0\u770B\u5230\u6C5F\u6CB3\u7ECF\u8FC7\uFF0C\u8FFD\u4E86\u4E0A\u53BB\u3002</p><p>\u6C5F\u6CB3\u6765\u5230\u90B1\u82F1\u6770\u7684\u4F4F\u5904\uFF0C\u90B1\u82F1\u6770\u8FD8\u5047\u88C5\u4EC0\u4E48\u4E8B\u90FD\u6CA1\u6709\u53D1\u751F\u3002\u5374\u88AB\u6C5F\u6CB3\u6293\u4F4F\u6B63\u5728\u5199\u505C\u804C\u68C0\u67E5\u3002\u9648\u6C5F\u6CB3\u5927\u4E3A\u4E0D\u89E3\uFF0C\u60F3\u8981\u627E\u9886\u5BFC\u7406\u8BBA\u3002\u5374\u88AB\u90B1\u82F1\u6770\u62E6\u4F4F\u3002\u4E8B\u60C5\u88AB\u8DDF\u4E0A\u6765\u7684\u7389\u73E0\u770B\u5728\u773C\u91CC\u3002\u7389\u73E0\u56DE\u5BB6\u62FF\u8D70\u4E86\u6C5F\u6CB3\u5BC4\u5B58\u5728\u5979\u90A3\u513F\u7684\u7CAE\u7968\u3002\u5C06\u644A\u5B50\u4EA4\u7ED9\u7236\u4EB2\u770B\u7BA1\u5C31\u79BB\u5F00\u4E86\u3002</p><p>\u6C5F\u6CB3\u53BB\u627E\u7389\u73E0\uFF0C\u624D\u77E5\u9053\u7389\u73E0\u4E0B\u5348\u4E00\u76F4\u6CA1\u56DE\u6765\uFF0C\u53EA\u89C1\u5230\u4E86\u8EBA\u5728\u644A\u5B50\u65C1\u8FB9\u6253\u778C\u7761\u7684\u7389\u73E0\u7239\u3002\u7389\u73E0\u7239\u542C\u51AF\u59D0\u5938\u8D77\u6C5F\u6CB3\u662F\u4E49\u4E4C\u6700\u6709\u672C\u4E8B\u7684\u4EBA\u3002\u5341\u5206\u611F\u5174\u8DA3\u3002</p><p>\u7389\u73E0\u5F88\u665A\u90FD\u6CA1\u6709\u56DE\u6765\uFF0C\u5979\u4E3A\u4E86\u5E2E\u6C5F\u6CB3\u6362\u7CAE\u7968\uFF0C\u8DD1\u904D\u4E86\u5468\u8FB9\u7684\u6751\u9547\uFF0C\u6700\u540E\u4E00\u4E2A\u5E97\u8001\u677F\u6307\u70B9\u5979\u53BB\u5B81\u6CE2\u6362\u7CAE\u7968\uFF0C\u8BF4\u90A3\u8FB9\u53EF\u4EE5\u6362\u7684\u591A\u4E00\u70B9\u3002\u53EA\u662F\u53BB\u5B81\u6CE2\u7684\u8F66\u7968\u8981\u4E94\u5757\u94B1\uFF0C\u7389\u73E0\u60F3\u4E86\u53C8\u60F3\uFF0C\u5341\u5206\u820D\u4E0D\u5F97\u3002</p><p>\u6C5F\u6CB3\u5728\u7389\u73E0\u5BB6\u5DE6\u7B49\u53F3\u7B49\uFF0C\u7B49\u4E0D\u5230\u7389\u73E0\u56DE\u6765\uFF0C\u53C8\u542C\u5230\u7389\u73E0\u7239\u5728\u8033\u65C1\u5439\u5618\u4ED6Du\u535A\u8D62\u94B1\u7684\u201C\u5149\u8F89\u5386\u53F2\u201D\u3002\u5341\u5206\u4E0D\u8010\u70E6\u3002\u53C8\u542C\u7684\u7389\u73E0\u7239\u65E0\u610F\u95F4\u8BF4\u51FA\u4ED6Du\u535A\u7684\u94B1\u662F\u5973\u513F\u5E78\u5E78\u82E6\u82E6\u8D5A\u56DE\u6765\u7684\u8840\u6C57\u94B1\uFF0C\u706B\u5192\u4E09\u4E08\u3002\u6309\u8010\u4E0D\u4F4F\uFF0C\u63EA\u8D77\u8863\u9886\u8D28\u95EE\u7389\u73E0\u7239\u8FD8\u914D\u4E0D\u914D\u505A\u4E00\u4E2A\u7236\u4EB2\u3002</p><p>\u7389\u73E0\u4E3A\u4E86\u7701\u4E0B\u706B\u8F66\u7968\u94B1\uFF0C\u8D81\u7740\u6DF1\u591C\uFF0C\u5077\u5077\u6252\u4E0A\u4E86\u706B\u8F66\u3002</p><p>\u7389\u73E0\u8F9B\u82E6\u4E86\u4E00\u591C\u7EC8\u4E8E\u6362\u56DE\u4E86\u94B1\u3002\u7389\u73E0\u7239\u773C\u770B\u7740\u5973\u513F\u628A\u80CC\u5305\u9501\u8FDB\u4E86\u67DC\u5B50\u91CC\uFF0C\u773C\u73E0\u5B50\u8F6C\u6765\u8F6C\u53BB\uFF0C\u4E0D\u77E5\u5728\u52A8\u4EC0\u4E48\u5FC3\u601D\u3002</p><p>\u665A\u4E0A\uFF0C\u8D81\u7389\u73E0\u7761\u7740\uFF0C\u7389\u73E0\u7239\u5077\u5077\u4ECE\u7389\u73E0\u6795\u5934\u4E0B\u5077\u51FA\u4E86\u94A5\u5319\uFF0C\u6253\u5F00\u4E86\u67DC\u5B50\u4E0A\u7684\u9501\u3002</p><p>\u8C22\u4E66\u8BB0\u56DE\u6765\u4E86\uFF0C\u53BF\u59D4\u4E3E\u884C\u4F1A\u8BAE\uFF0C\u8BA8\u8BBA\u9648\u6C5F\u6CB3\u548C\u90B1\u82F1\u6770\u7684\u4E8B\u60C5\u3002\u9648\u91D1\u6C34\u542C\u8BF4\uFF0C\u6025\u5306\u5306\u7684\u4ECE\u533B\u9662\u91CC\u8D76\u8FC7\u6765\uFF0C\u8BF4\u54EA\u6015\u662F\u5411\u53BF\u91CC\u7684\u9886\u5BFC\u4E0B\u8DEA\uFF0C\u4E5F\u8981\u628A\u9648\u6C5F\u6CB3\u5E26\u51FA\u6765\u3002</p><p>\u7389\u73E0\u6025\u5306\u5306\u8D76\u6765\u7ED9\u6C5F\u6CB3\u9001\u94B1\uFF0C\u6B63\u597D\u542C\u8BF4\u6C5F\u6CB3\u548C\u82F1\u6770\u8981\u88AB\u6279\u6597\u3002\u8D76\u7D27\u51B2\u5411\u4F1A\u573A\uFF0C\u6B63\u9047\u5230\u4E86\u88AB\u62E6\u5728\u95E8\u5916\u7684\u9648\u91D1\u6C34\u3002\u9648\u91D1\u6C34\u4E0D\u89E3\u7389\u73E0\u6765\u5E72\u4EC0\u4E48\u3002\u7389\u73E0\u4E0D\u613F\u5728\u4E0E\u4ED6\u8D77\u51B2\u7A81\uFF0C\u8F6C\u5934\u4E0D\u8BF4\u8BDD\u79BB\u5F00\u3002</p><p>\u90B1\u82F1\u6770\u5F53\u4F17\u5FF5\u4E86\u81EA\u5DF1\u7684\u68C0\u67E5\uFF0C\u5374\u88AB\u8C22\u4E66\u8BB0\u4E00\u987F\u6279\u8BC4\u3002\u800C\u9648\u6C5F\u6CB3\u7684\u4ED7\u4E49\u6267\u8A00\u5374\u5F97\u5230\u4E86\u8C22\u4E66\u8BB0\u7684\u8868\u6F14\u3002\u8C22\u4E66\u8BB0\u6124\u6168\uFF0C\u8C01\u60F3\u8FC7\u771F\u6B63\u7684\u89E3\u51B3\u95EE\u9898\uFF0C\u8C01\u60F3\u8FC7\u8BA9\u8001\u767E\u59D3\u5B9E\u5B9E\u5728\u5728\u7684\u5BCC\u8D77\u6765\u3002\u5927\u5BB6\u8FD9\u624D\u77E5\u9053\uFF0C\u8FD9\u6B21\u5927\u4F1A\u7684\u76EE\u7684\uFF0C\u5E76\u4E0D\u662F\u8981\u6279\u8BC4\u4ED6\u4EEC\uFF0C\u76F8\u53CD\uFF0C\u662F\u8981\u80AF\u5B9A\u548C\u9F13\u52B1\u4ED6\u4EEC\u3002</p><p>\u540C\u65F6\uFF0C\u8C22\u4E66\u8BB0\u8FD8\u5E26\u6765\u4E86\u4E00\u4E2A\u632F\u594B\u4EBA\u5FC3\u7684\u597D\u6D88\u606F\uFF0C\u4ECE\u4ECA\u4EE5\u540E\uFF0C\u4E2A\u4F53\u5DE5\u5546\u6237\u53EF\u4EE5\u7684\u5230\u7ECF\u8425\u8BB8\u53EF\u8BC1\uFF0C\u53EF\u4EE5\u8FDB\u884C\u5DE5\u5546\u767B\u8BB0\u3002\u4E5F\u5C31\u662F\u8BF4\uFF0C\u4E2A\u4EBA\u7684\u7ECF\u8425\u884C\u4E3A\uFF0C\u662F\u5408\u6CD5\u7684\uFF0C\u5149\u660E\u6B63\u5927\u7684\u3002\u518D\u4E5F\u4E0D\u7528\u62C5\u5FC3\u4F1A\u88AB\u6263\u4E0A\u4E00\u9876\u6295\u673A\u5012\u628A\u7684\u5E3D\u5B50\u4E86\u3002</p><p>\u8FD9\u4E2A\u6D88\u606F\uFF0C\u5927\u5927\u7684\u632F\u594B\u4E86\u5927\u5BB6\u3002\u7389\u73E0\u9AD8\u5174\u6781\u4E86\uFF0C\u60F3\u8981\u51B2\u8FDB\u4F1A\u573A\u7ED9\u9648\u6C5F\u6CB3\u9001\u94B1\u53BB\u3002\u6CA1\u60F3\u5230\u5230\u4E86\u95E8\u53E3\u7FFB\u51FA\u5305\u88B1\uFF0C\u53D1\u73B0\u5979\u8F9B\u8F9B\u82E6\u82E6\u4E00\u665A\u4E0A\u6362\u56DE\u7684\u94B1\u7ADF\u7136\u53D8\u6210\u4E86\u6728\u5934\u548C\u7816\u5757\u513F\u3002\u7389\u73E0\u4EFF\u4F5B\u660E\u767D\u4E86\u4EC0\u4E48\uFF0C\u8F6C\u8EAB\u5411\u5916\u8DD1\u53BB\u3002\u9648\u91D1\u6C34\u4EE5\u4E3A\u5979\u8981\u628A\u94B1\u5377\u8D70\uFF0C\u5FD9\u558A\u4EBA\u53BB\u6293\u5979\u3002\u81EA\u5DF1\u5219\u51B2\u8FDB\u4F1A\u573A\uFF0C\u5927\u58F0\u51B2\u53F0\u4E0A\u558A\u9053\uFF1A\u9A86\u7389\u73E0\u628A\u94B1\u5377\u8D70\u4E86\u3002</p><p>\u7389\u73E0\u8DD1\u56DE\u5BB6\uFF0C\u6253\u5F00\u67DC\u5B50\uFF0C\u53EA\u5269\u4E0B\u4E86\u4E00\u5F20\u7EB8\u6761\u3002\u7389\u73E0\u7239\u5E26\u8D70\u4E86\u94B1\uFF0C\u8981\u5979\u518D\u7ED9\u4ED6\u4E00\u6B21\u673A\u4F1A\u53BB\u8D5A\u5927\u94B1\u3002\u7389\u73E0\u8E72\u5728\u5730\u4E0A\uFF0C\u4EC0\u4E48\u8BDD\u4E5F\u8BF4\u4E0D\u51FA\u6765\u3002</p><p>\u4E8B\u60C5\u4F20\u56DE\u4E86\u9648\u5BB6\u6751\uFF0C\u5927\u5BB6\u90FD\u5341\u5206\u62C5\u5FC3\u3002\u9648\u91D1\u6C34\u4E3A\u4E86\u6C5F\u6CB3\uFF0C\u6682\u65F6\u7684\u538B\u4F4F\u4E86\u5927\u5BB6\uFF0C\u4E0D\u8BA9\u5927\u5BB6\u7BA1\u9648\u6C5F\u6CB3\u8981\u94B1\u3002\u4ED6\u8D81\u673A\u529D\u8BF4\u4ED6\u79BB\u5F00\u7389\u73E0\u3002\u6C5F\u6CB3\u5374\u4E0D\u76F8\u4FE1\u7389\u73E0\u4F1A\u505A\u8FD9\u6837\u7684\u4E8B\u60C5\u3002</p>',18),r=[o];function _(n,s){return e(),t("div",null,r)}const h=p(c,[["render",_],["__file","7.html.vue"]]);export{h as default};
