import{_ as p}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as e,c as t,f as a}from"./app.b5163196.js";const c={},o=a('<h1 id="\u7B2C7\u96C6-\u5C0F\u5170\u82B1\u593A\u5F97\u9B41\u9996" tabindex="-1"><a class="header-anchor" href="#\u7B2C7\u96C6-\u5C0F\u5170\u82B1\u593A\u5F97\u9B41\u9996" aria-hidden="true">#</a> \u7B2C7\u96C6\uFF1A\u5C0F\u5170\u82B1\u593A\u5F97\u9B41\u9996</h1><p>\u5C31\u5728\u82F1\u62DB\u6251\u4E0A\u6765\u7684\u4E00\u77AC\uFF0C\u5C0F\u5170\u82B1\u8D76\u5FD9\u62A4\u4F4F\u4E86\u8111\u888B\uFF0C\u53EF\u5374\u6CA1\u6709\u7B49\u6765\u82F1\u62DB\u7684\u653B\u51FB\u3002\u5C0F\u5170\u82B1\u7741\u5F00\u773C\u775B\uFF0C\u5C31\u770B\u5230\u4E86\u4E1C\u65B9\u9752\u82CD\u3002\u4E1C\u65B9\u9752\u82CD\u6076\u72E0\u72E0\u5730\u63D0\u5251\u4E0A\u524D\uFF0C\u60F3\u8981\u89E3\u51B3\u4E86\u82F1\u62DB\uFF0C\u53EF\u5C0F\u5170\u82B1\u5374\u5C06\u4ED6\u7684\u5251\u593A\u8D70\u3002\u5C0F\u5170\u82B1\u7F13\u7F13\u4E0A\u524D\uFF0C\u66FF\u82F1\u62DB\u7597\u4F24\uFF0C\u968F\u540E\u5C06\u5B83\u6536\u8FDB\u4E86\u4ED9\u73E0\u91CC\u3002\u8776\u8863\u6084\u6084\u6765\u5230\u65C1\u8FB9\uFF0C\u4E1C\u65B9\u9752\u82CD\u5BDF\u89C9\u540E\u4E00\u6325\u624B\uFF0C\u8776\u8863\u4FBF\u53D8\u6210\u8774\u8776\u9003\u8D70\u4E86\u3002</p><p>\u4E1C\u65B9\u9752\u82CD\u62C5\u5FC3\u5C0F\u5170\u82B1\u592A\u611F\u60C5\u7528\u4E8B\uFF0C\u8FDF\u65E9\u4F1A\u9047\u5230\u5371\u9669\uFF0C\u4FBF\u63D0\u51FA\u5C06\u5979\u7684\u60C5\u6839\u8FDE\u6839\u62D4\u8D77\u3002\u5C0F\u5170\u82B1\u53CD\u9A73\u9053\uFF0C\u82E5\u662F\u6CA1\u6709\u4E86\u611F\u60C5\uFF0C\u90A3\u5C31\u548C\u6B8B\u75BE\u4E00\u6837\u3002\u800C\u4E5F\u6B63\u662F\u56E0\u4E3A\u6709\u4E86\u611F\u60C5\uFF0C\u5979\u548C\u90A3\u4E9B\u4ED9\u5973\u4EEC\u624D\u80FD\u8054\u5408\u8D77\u6765\u62B5\u6297\u82F1\u62DB\u3002\u8FD9\u4E00\u756A\u8BDD\u4E0D\u7531\u5F97\u8BA9\u4E1C\u65B9\u9752\u82CD\u9677\u5165\u56DE\u5FC6\uFF0C\u4ED6\u8FD8\u8BB0\u5F97\u81EA\u5DF1\u88AB\u56F0\u5728\u718A\u718A\u70C8\u706B\u4E2D\uFF0C\u8EAB\u65C1\u65E0\u4EBA\u5728\u610F\u3002</p><p>\u89C1\u4E1C\u65B9\u9752\u82CD\u6709\u4E9B\u53D1\u6123\uFF0C\u5C0F\u5170\u82B1\u8F7B\u8F7B\u62CD\u62CD\u4ED6\u7684\u80A9\u8180\uFF0C\u529D\u6170\u4ED6\u8BF4\u8FD9\u4E16\u4E0A\u8FD8\u662F\u6709\u5F88\u591A\u597D\u4EBA\u7684\u3002\u8BF4\u7740\uFF0C\u8FDC\u5904\u4F20\u6765\u547C\u558A\u5979\u7684\u58F0\u97F3\u3002\u5C0F\u5170\u82B1\u8D76\u5FD9\u8BA9\u4E1C\u65B9\u9752\u82CD\u79BB\u5F00\uFF0C\u53EF\u5BF9\u65B9\u5374\u6709\u4E9B\u62B5\u89E6\u3002\u5C31\u5728\u957F\u73E9\u51FA\u73B0\u7684\u524D\u4E00\u523B\uFF0C\u4E1C\u65B9\u9752\u82CD\u6D88\u5931\u4E0D\u89C1\u4E86\u3002\u89C1\u5230\u5C0F\u5170\u82B1\uFF0C\u957F\u73E9\u8D76\u5FD9\u4E0A\u524D\u8BE2\u95EE\u3002\u89C1\u5979\u5E76\u65E0\u5927\u788D\uFF0C\u957F\u73E9\u8FD9\u624D\u653E\u4E0B\u5FC3\u6765\u3002</p><p>\u5C0F\u5170\u82B1\u548C\u4F17\u4E0A\u4ED9\u6C47\u62A5\u60C5\u51B5\uFF0C\u53EF\u6FA7\u6C85\u4F3C\u4E4E\u5E76\u4E0D\u76F8\u4FE1\u3002\u957F\u73E9\u5728\u4E00\u65C1\u66FF\u5C0F\u5170\u82B1\u8BF4\u8BDD\uFF0C\u53EF\u53C8\u62C5\u5FC3\u81EA\u5DF1\u7684\u5FC3\u610F\u88AB\u4EBA\u5BDF\u89C9\u3002\u800C\u5BDF\u89C9\u5230\u4ED6\u5FC3\u610F\u7684\uFF0C\u7B2C\u4E00\u4E2A\u5C31\u662F\u5BB9\u660A\u3002\u5728\u5BB9\u660A\u7684\u89E3\u56F4\u4E0B\uFF0C\u8FD9\u4EF6\u4E8B\u60C5\u624D\u6682\u65F6\u544A\u4E00\u6BB5\u843D\u3002</p><p>\u70BC\u5316\u795F\u6C14\u9700\u8981\u6D88\u8017\u5927\u91CF\u4EA1\u8005\u5143\u795E\uFF0C\u4E14\u70BC\u5316\u795F\u6C14\u9700\u8981\u7684\u6CD5\u5668\u5316\u9B42\u9F0E\u4E5F\u65E9\u4E8E\u4E0A\u53E4\u65F6\u671F\u6D88\u5931\u4E0D\u89C1\u3002\u8FD9\u795F\u6C14\u7EDD\u8FF9\u4E09\u754C\u6570\u5341\u4E07\u5E74\uFF0C\u5982\u4ECA\u5374\u7A81\u7136\u51FA\u73B0\u5728\u6C34\u4E91\u5929\uFF0C\u53C8\u7A81\u7136\u51FA\u73B0\u5728\u82F1\u62DB\u8EAB\u4E0A\uFF0C\u957F\u73E9\u5982\u4F55\u4E5F\u60F3\u4E0D\u660E\u767D\u3002\u6FA7\u6C85\u5DF2\u7ECF\u5C01\u9501\u4E86\u6574\u4E2A\u4ED9\u754C\uFF0C\u60F3\u5FC5\u67E5\u4E0B\u53BB\u5B9A\u80FD\u627E\u5230\u86DB\u4E1D\u9A6C\u8FF9\u3002</p><p>\u5C0F\u5170\u82B1\u6536\u5230\u4FE1\u4EF6\uFF0C\u5F97\u77E5\u81EA\u5DF1\u4E0D\u4EC5\u901A\u8FC7\u4E86\u4ED9\u8003\uFF0C\u8FD8\u62D4\u5F97\u4E86\u9B41\u9996\u3002\u53EF\u662F\u60F3\u5230\u4ECA\u5929\u7684\u4E8B\u60C5\uFF0C\u5C0F\u5170\u82B1\u53EA\u89C9\u5F97\u540E\u6015\uFF0C\u62C5\u5FC3\u8FD9\u4E9B\u4E8B\u60C5\u662F\u5927\u5F3A\u505A\u7684\u3002\u60F3\u5230\u8FD9\u91CC\uFF0C\u5979\u8D76\u5FD9\u53BB\u627E\u5927\u5F3A\u5174\u5E08\u95EE\u7F6A\u3002\u8FD9\u672C\u5C31\u4E0D\u662F\u4E1C\u65B9\u9752\u82CD\u505A\u7684\uFF0C\u4ED6\u81EA\u7136\u4E0D\u4F1A\u627F\u8BA4\u3002\u53EF\u5C0F\u5170\u82B1\u5DF2\u7ECF\u660F\u4E86\u5934\uFF0C\u7740\u6025\u62C9\u7740\u4ED6\u53BB\u81EA\u9996\u3002\u5728\u540C\u5FC3\u5492\u7684\u4F5C\u7528\u4E0B\uFF0C\u4E1C\u65B9\u9752\u82CD\u53EA\u80FD\u4EFB\u7531\u5C0F\u5170\u82B1\u62C9\u7740\u3002\u65E0\u5948\uFF0C\u4E1C\u65B9\u9752\u82CD\u53EA\u597D\u548C\u5979\u89E3\u91CA\uFF0C\u81EA\u5DF1\u53BB\u6F31\u7389\u6797\uFF0C\u53EA\u662F\u4E3A\u4E86\u5E2E\u5979\u901A\u8FC7\u4ED9\u8003\u3002\u5C0F\u5170\u82B1\u542C\u5230\u8FD9\u756A\u8BDD\uFF0C\u7EC8\u4E8E\u60F3\u660E\u767D\u4ECA\u5929\u7684\u5DE7\u5408\u662F\u600E\u4E48\u56DE\u4E8B\u4E86\u3002\u8FD9\u6CD5\u5B50\u679C\u7136\u594F\u6548\uFF0C\u5C0F\u5170\u82B1\u518D\u4E5F\u4E0D\u62C9\u7740\u4ED6\u53BB\u81EA\u9996\u4E86\uFF0C\u751A\u81F3\u8FD8\u62C5\u5FC3\u4ED6\u88AB\u4EBA\u6293\u4E86\u53BB\u3002</p><p>\u53EF\u5C0F\u5170\u82B1\u8FD8\u662F\u95F7\u95F7\u4E0D\u4E50\uFF0C\u5979\u539F\u4EE5\u4E3A\u662F\u81EA\u5DF1\u6CD5\u529B\u89C1\u957F\uFF0C\u53EF\u6CA1\u60F3\u5230\u5230\u5934\u6765\u53EA\u662F\u8096\u60F3\u3002\u5C0F\u5170\u82B1\u60F3\u8981\u53BB\u627E\u4E09\u751F\u53D6\u6D88\u81EA\u5DF1\u7684\u6210\u7EE9\uFF0C\u5979\u4E0D\u60F3\u56E0\u4E3A\u4F5C\u5F0A\u5230\u957F\u73E9\u8EAB\u8FB9\u3002\u8BF4\u8D77\u8FD8\u60F3\u4E0D\u60F3\u5927\u5F3A\u56DE\u5230\u660A\u5929\u5854\uFF0C\u5C0F\u5170\u82B1\u5FC3\u91CC\u6709\u4E9B\u52A8\u6447\u3002\u4ECE\u524D\u5E08\u7236\u4E0D\u5728\u7684\u65F6\u5019\uFF0C\u5C0F\u5170\u82B1\u5DF2\u7ECF\u4E60\u60EF\u4E86\u4E00\u4E2A\u4EBA\uFF0C\u53EF\u73B0\u5728\u5927\u5F3A\u6765\u4E86\uFF0C\u5979\u5BB3\u6015\u81EA\u5DF1\u4F1A\u6162\u6162\u4E60\u60EF\u4ED6\u7684\u5B58\u5728\uFF0C\u7B49\u5230\u4ED6\u79BB\u5F00\u65F6\uFF0C\u5979\u53C8\u53D8\u6210\u4E86\u4E00\u4E2A\u5B64\u72EC\u7684\u4EBA\u3002</p><p>\u5BB9\u660A\u5C06\u53D7\u4F24\u7684\u8776\u8863\u5E26\u56DE\u4E86\u7559\u82B3\u9601\u3002\u8776\u8863\u8BA4\u9519\uFF0C\u53EF\u5B9E\u5728\u4E0D\u77E5\u51FA\u624B\u7684\u7A76\u7ADF\u662F\u4F55\u4EBA\u3002\u5BB9\u660A\u5439\u7BAB\u6000\u5FF5\u5E08\u7236\u8D64\u5730\u5973\u5B50\uFF0C\u4E0D\u66FE\u60F3\u5E08\u7236\u7ADF\u771F\u7684\u51FA\u73B0\u5728\u4ED6\u7684\u8EAB\u8FB9\u3002\u53EA\u662F\u4ED6\u6E05\u695A\uFF0C\u9762\u524D\u7684\u4EBA\u5E76\u975E\u5E08\u7236\uFF0C\u800C\u662F\u5E7B\u5316\u800C\u6765\u7ED9\u4ED6\u4E0B\u836F\u7684\u3002\u4ED6\u8BA9\u5973\u5B50\u6536\u56DE\u773C\u6CEA\uFF0C\u968F\u540E\u4E00\u62AC\u624B\u626D\u65AD\u4E86\u5979\u7684\u8116\u5B50\u3002</p><p>\u56E0\u4E3A\u5728\u8776\u8863\u8EAB\u4E0A\u53D1\u73B0\u4E86\u4E1A\u706B\uFF0C\u5BB9\u660A\u4E0D\u7531\u5F97\u6000\u7591\u5230\u4E1C\u65B9\u9752\u82CD\u8EAB\u4E0A\u3002\u5F85\u4ED6\u5230\u4E86\u660A\u5929\u5854\uFF0C\u66F4\u52A0\u786E\u5B9A\u81EA\u5DF1\u7684\u731C\u6D4B\u3002\u601D\u6765\u60F3\u53BB\uFF0C\u5BB9\u660A\u731C\u6D4B\u8BB8\u662F\u90A3\u65E5\u6389\u5165\u660A\u5929\u5854\u7684\u4ED9\u5973\u4FEE\u590D\u4E86\u4E1C\u65B9\u9752\u82CD\u7684\u5143\u795E\uFF0C\u8FD9\u624D\u8BA9\u4ED6\u9003\u4E86\u51FA\u53BB\u3002\u53EF\u8FD9\u4E09\u754C\u4E4B\u4E2D\uFF0C\u53EA\u6709\u606F\u5C71\u795E\u5973\u80FD\u591F\u4FEE\u590D\u5143\u795E\u3002\u542C\u4E86\u6628\u65E5\u53D1\u751F\u7684\u4E8B\u60C5\uFF0C\u5BB9\u660A\u4E0D\u7531\u5F97\u6000\u7591\u5C0F\u5170\u82B1\u5C31\u662F\u606F\u5C71\u795E\u5973\u3002</p><p>\u5C0F\u5170\u82B1\u6765\u5230\u5927\u6BBF\uFF0C\u9047\u5230\u4E86\u4E39\u97F3\u3002\u4E0D\u8FC7\u8FD9\u6B21\uFF0C\u4E39\u97F3\u4E0D\u50CF\u4E4B\u524D\u90A3\u6837\u5BF9\u5C0F\u5170\u82B1\u9488\u950B\u76F8\u5BF9\u4E86\u3002\u542C\u95FB\u5C0F\u5170\u82B1\u8981\u53BB\u627E\u4E09\u751F\u53D6\u6D88\u4ED9\u8003\u6210\u7EE9\uFF0C\u957F\u73E9\u8D76\u5FD9\u529D\u6170\u5979\u8BF4\uFF0C\u8FD9\u6B21\u6210\u7EE9\u662F\u81EA\u5DF1\u8BF4\u4E86\u7B97\uFF0C\u5979\u4E0D\u5FC5\u53D6\u6D88\u3002\u5C0F\u5170\u82B1\u8FD8\u60F3\u5C06\u8424\u77F3\u8FD8\u7ED9\u957F\u73E9\uFF0C\u53EF\u65E0\u5948\u8FD9\u9897\u8424\u77F3\u5DF2\u7ECF\u8BA4\u5979\u505A\u4E86\u4E3B\u4EBA\u3002</p><p>\u5BB9\u660A\u627E\u5230\u5C0F\u5170\u82B1\uFF0C\u8BF7\u5979\u5E2E\u81EA\u5DF1\u4E00\u4E2A\u5FD9\u3002\u5C0F\u5170\u82B1\u6709\u4E9B\u4E3A\u96BE\uFF0C\u5979\u533A\u533A\u4E00\u4ECB\u6CD5\u529B\u4F4E\u5FAE\u7684\u5C0F\u82B1\u7CBE\uFF0C\u8FDE\u4E0A\u4ED9\u90FD\u529E\u4E0D\u4E86\u7684\u4E8B\u60C5\uFF0C\u5979\u7740\u5B9E\u6CA1\u6709\u4FE1\u5FC3\u5B8C\u6210\u3002\u5BB9\u660A\u62FF\u51FA\u4E00\u682A\u67AF\u840E\u4E86\u7684\u51B0\u706F\u7389\u9732\uFF0C\u8BA9\u5C0F\u5170\u82B1\u770B\u770B\u80FD\u4E0D\u80FD\u6551\u6D3B\u3002\u8FD9\u4E5F\u7B97\u662F\u5C0F\u5170\u82B1\u529B\u6240\u80FD\u53CA\uFF0C\u5979\u4FBF\u63A5\u8FC7\u51B0\u706F\u7389\u9732\uFF0C\u7EC6\u7EC6\u67E5\u770B\u4E86\u4E00\u756A\uFF0C\u51B3\u5B9A\u8BD5\u4E00\u8BD5\u3002\u53EA\u4E00\u4F1A\u513F\u529F\u592B\uFF0C\u5C0F\u5170\u82B1\u5C31\u6551\u6D3B\u4E86\u51B0\u706F\u7389\u9732\u3002\u9762\u5BF9\u5BB9\u660A\u7684\u611F\u8C22\uFF0C\u5C0F\u5170\u82B1\u53D7\u5BA0\u82E5\u60CA\uFF0C\u8D76\u5FD9\u627E\u501F\u53E3\u79BB\u5F00\u3002\u671B\u7740\u5C0F\u5170\u82B1\u8FDC\u53BB\u7684\u80CC\u5F71\uFF0C\u5BB9\u660A\u8138\u4E0A\u4E0D\u7531\u5F97\u6D6E\u8D77\u4E86\u7B11\u610F\u3002\u5728\u8FD9\u4E16\u4E0A\uFF0C\u53EA\u6709\u4E00\u4E2A\u4EBA\u80FD\u591F\u51C0\u5316\u795F\u6C14\uFF0C\u90A3\u4FBF\u662F\u606F\u5C71\u795E\u5973\u3002</p><p>\u81EA\u4ECE\u8D64\u5730\u5973\u5B50\u6218\u6B7B\u4E4B\u540E\uFF0C\u8FD9\u4E09\u754C\u5BF9\u4E8E\u5BB9\u660A\u800C\u8A00\u6CA1\u6709\u4EFB\u4F55\u7559\u5FF5\uFF0C\u4ED6\u53EA\u89C9\u5F97\u75DB\u82E6\u3002\u4E5F\u8BB8\u76F8\u6BD4\u4E8E\u5E08\u7236\u8FD9\u4E09\u4E07\u5E74\u6765\u5728\u4EBA\u95F4\u53D7\u7684\u82E6\uFF0C\u4ED6\u8FD9\u4E9B\u75DB\u82E6\u7B97\u4E0D\u5F97\u4EC0\u4E48\u3002\u6240\u4EE5\u4ED6\u65E9\u5C31\u6697\u4E0B\u51B3\u5B9A\uFF0C\u5C31\u7B97\u503E\u8986\u8FD9\u4E09\u754C\uFF0C\u4E5F\u4E00\u5B9A\u8981\u5C06\u5E08\u7236\u6551\u56DE\u6765\u3002</p>',13),r=[o];function _(n,s){return e(),t("div",null,r)}const h=p(c,[["render",_],["__file","7.html.vue"]]);export{h as default};
