import{_ as p}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as e,c as t,f as a}from"./app.dcc3c1cf.js";const c={},o=a('<h1 id="\u7B2C7\u96C6-\u4E01\u6625\u59B9\u5931\u8E2A\u7591\u4F3C\u9047\u4E0D\u6D4B-\u8BF8\u591A\u7EBF\u7D22\u7275\u626F\u5361\u6069\u96C6\u56E2" tabindex="-1"><a class="header-anchor" href="#\u7B2C7\u96C6-\u4E01\u6625\u59B9\u5931\u8E2A\u7591\u4F3C\u9047\u4E0D\u6D4B-\u8BF8\u591A\u7EBF\u7D22\u7275\u626F\u5361\u6069\u96C6\u56E2" aria-hidden="true">#</a> \u7B2C7\u96C6\uFF1A\u4E01\u6625\u59B9\u5931\u8E2A\u7591\u4F3C\u9047\u4E0D\u6D4B \u8BF8\u591A\u7EBF\u7D22\u7275\u626F\u5361\u6069\u96C6\u56E2</h1><p>\u201C\u5361\u6069\u96C6\u56E2\u8D44\u91D1\u94FE\u65AD\u88C2\uFF0C\u539F\u6C5F\u6F6D\u5316\u80A5\u5382\u505C\u5DE5\u4FEE\u5EFA\u201D\uFF0C\u4E00\u5219\u91CD\u78C5\u6D88\u606F\u7ECF\u7531\u6C5F\u6F6D\u665A\u62A5\u53D1\u5E03\uFF0C\u7387\u5148\u5438\u5F15\u4E25\u826F\u6CE8\u610F\u3002\u91CD\u6848\u7EC4\u5728\u8F66\u7BA1\u6240\u8C03\u53D6\u9ED1\u8272\u6851\u5854\u7EB3\u8BB0\u5F55\uFF0C\u56E0\u8F66\u724C\u53F7\u540E\u56DB\u4F4D\u6CA1\u6709\u770B\u6E05\uFF0C\u6240\u4EE5\u5168\u5E02\u5171\u6709\u51E0\u5343\u8F86\u7B26\u5408\u7279\u5F81\uFF0C\u5F88\u96BE\u5728\u6781\u77ED\u65F6\u95F4\u5185\u5C06\u5176\u9501\u5B9A\u3002</p><p>\u4EFB\u73A5\u5A77\u8BA9\u7ECF\u4FA6\u79D1\u8C03\u67E5\u5361\u6069\u96C6\u56E2\u7ECF\u6D4E\u72B6\u51B5\uFF0C\u987E\u4E00\u9E23\u7B49\u4EBA\u5219\u5E26\u7740\u6731\u4F1F\u7167\u7247\u5206\u53D1\u5404\u5927\u5C0F\u533A\uFF0C\u5168\u57CE\u641C\u5BFB\u3002\u800C\u5728\u67D0\u5904\u9634\u6697\u5730\u4E0B\u8F66\u5E93\u91CC\uFF0C\u674E\u5EFA\u56FD\u548C\u80E1\u4E00\u6D6A\u79D8\u5BC6\u78B0\u9762\uFF0C\u5546\u8BAE\u65E9\u4E8E\u8B66\u65B9\u627E\u5230\u6731\u4F1F\uFF0C\u5426\u5219\u4FBF\u4F1A\u5904\u4E8E\u88AB\u52A8\u72B6\u6001\uFF0C\u5982\u540C\u5669\u68A6\u91CC\u7684\u753B\u9762\uFF0C\u53EA\u80FD\u8BA9\u5BF9\u65B9\u627C\u4F4F\u5589\u5499\uFF0C\u65E0\u6CD5\u6323\u8131\u3002</p><p>\u4E03\u5E74\u524D\u6731\u4F1F\u524D\u5F80\u5916\u7701\u6267\u884C\u4EFB\u52A1\uFF0C\u6C5F\u9633\u6253\u4ED6\u7535\u8BDD\u59CB\u7EC8\u5173\u673A\uFF0C\u6070\u597D\u4E0B\u73ED\u8DEF\u8FC7\u5361\u6069\u96C6\u56E2\u65F6\uFF0C\u66FE\u89C1\u9EC4\u6BDB\u62B1\u7740\u4E01\u6625\u59B9\u7684\u513F\u5B50\uFF0C\u4E8E\u662F\u8003\u8651\u5230\u4E8C\u4EBA\u5173\u7CFB\uFF0C\u7ACB\u9A6C\u8D76\u5F80\u4E01\u6625\u59B9\u5BB6\uFF0C\u600E\u6599\u6B64\u65F6\u4E01\u6625\u59B9\u7A81\u7136\u5931\u8E2A\uFF0C\u5979\u7684\u79BB\u5F00\u4E3A\u8FD9\u8D77\u6848\u4EF6\u589E\u6DFB\u8BF8\u591A\u7591\u70B9\u3002</p><p>\u867D\u7136\u5148\u524D\u4E01\u6625\u59B9\u6D89\u5ACC\u8BEC\u9677\uFF0C\u4F46\u5728\u4E3B\u89C2\u4E0A\u5E76\u672A\u6599\u5230\u4FAF\u8D35\u5E73\u4F1A\u88AB\u5BB3\u6B7B\u7684\u7ED3\u679C\uFF0C\u5E76\u4E14\u4E8B\u540E\u6709\u4E3B\u52A8\u4EA4\u4EE3\u7684\u4ECE\u5BBD\u60C5\u8282\uFF0C\u53EA\u8981\u613F\u610F\u51FA\u5EAD\u4F5C\u8BC1\u4FBF\u53EF\u51CF\u5211\uFF0C\u6CA1\u5FC5\u8981\u754F\u7F6A\u6F5C\u9003\u3002\u6731\u4F1F\u8BA4\u4E3A\u4E8B\u51FA\u8E4A\u8DF7\uFF0C\u7D22\u6027\u5E26\u4E0A\u9648\u660E\u7AE0\u4E0E\u6C5F\u9633\u6C47\u5408\uFF0C\u4E09\u4EBA\u53D1\u73B0\u5C0F\u5356\u90E8\u5C4B\u5916\u88AB\u4EBA\u6253\u626B\u8FC7\uFF0C\u5C4B\u5185\u6B8B\u7559\u955C\u5B50\u788E\u7247\u548C\u8840\u8FF9\uFF0C\u901A\u8FC7\u8D70\u8BBF\u5468\u56F4\u7FA4\u4F17\uFF0C\u6545\u800C\u63A8\u6D4B\u4E01\u6625\u59B9\u51F6\u591A\u5409\u5C11\u3002</p><p>\u6731\u4F1F\u6293\u4F4F\u9EC4\u6BDB\uFF0C\u6CA1\u80FD\u95EE\u51FA\u6709\u7528\u4FE1\u606F\uFF0C\u601D\u53CA\u674E\u5EFA\u56FD\u5C06\u4ED6\u8C03\u5230\u5916\u7701\u6267\u884C\u4EFB\u52A1\uFF0C\u4EFF\u4F5B\u662F\u6709\u610F\u4E3A\u4E4B\u3002\u5982\u4ECA\u4E01\u6625\u59B9\u4E0B\u843D\u4E0D\u660E\uFF0C\u9648\u660E\u7AE0\u8BA4\u4E3A\u8C03\u67E5\u884C\u8E2A\u5DF2\u88AB\u66B4\u9732\uFF0C\u597D\u5728\u6731\u4F1F\u5C06\u8BA1\u5C31\u8BA1\uFF0C\u6545\u610F\u5C06\u9EC4\u6BDB\u5355\u72EC\u9694\u79BB\uFF0C\u518D\u8BA9\u624B\u4E0B\u5192\u5145\u793E\u4F1A\u5173\u7CFB\u5F52\u8FD8\u624B\u673A\u3002</p><p>\u679C\u7136\u9EC4\u6BDB\u4E2D\u8BA1\uFF0C\u76F4\u63A5\u7ED9\u80E1\u4E00\u6D6A\u6253\u53BB\u7535\u8BDD\uFF0C\u6B64\u4EBA\u662F\u5361\u6069\u96C6\u56E2\u7684\u526F\u603B\u7ECF\u7406\uFF0C\u4E5F\u662F\u5B59\u4F20\u798F\u7684\u52A9\u7406\uFF0C\u4ED6\u5728\u63A5\u5230\u9EC4\u6BDB\u7535\u8BDD\u540E\u6CA1\u8FC7\u591A\u4E45\uFF0C\u518D\u7528\u624B\u673A\u62E8\u901A\u4E00\u4E2A\u795E\u79D8\u53F7\u7801\uFF0C\u53F7\u7801\u4E3B\u4EBA\u6B63\u662F\u5B59\u4F20\u798F\u672C\u4EBA\uFF0C\u674E\u5EFA\u56FD\u53D7\u4ED6\u59D4\u6258\uFF0C\u8D23\u4EE4\u6731\u4F1F\u7ACB\u523B\u91CA\u653E\u9EC4\u6BDB\u3002</p><p>\u4E13\u6848\u7EC4\u901A\u8FC7\u5BF9\u5361\u6069\u96C6\u56E2\u7684\u8C03\u67E5\uFF0C\u7275\u626F\u51FA\u8463\u4E8B\u957F\u5B59\u4F20\u798F\u7684\u53D1\u5BB6\u80CC\u666F\uFF0C\u4ED6\u5728\u5E74\u8F7B\u65F6\u7B79\u94B1\u6536\u8D2D\u4E00\u5BB6\u8001\u56FD\u8425\u9020\u7EB8\u5382\uFF0C\u5E76\u5C06\u9020\u7EB8\u5382\u6539\u540D\u5361\u6069\u7EB8\u4E1A\u3002\u968F\u7740\u5382\u91CC\u6536\u76CA\u8D8A\u505A\u8D8A\u597D\uFF0C\u9010\u6E10\u6210\u4E3A\u5E73\u5EB7\u53BF\u8D22\u653F\u652F\u67F1\uFF0C\u4E0D\u4F46\u89E3\u51B3\u51E0\u5343\u4EBA\u5C31\u4E1A\u95EE\u9898\uFF0C\u66F4\u662F\u6E05\u5DDE\u5E02\u7B2C\u4E00\u5BB6\u4E0A\u5E02\u516C\u53F8\uFF0C\u4E4B\u540E\u4FBF\u6210\u7ACB\u5361\u6069\u96C6\u56E2\uFF0C\u65D7\u4E0B\u5171\u6709\u4E03\u516B\u5BB6\u5B50\u516C\u53F8\uFF0C\u6D89\u53CA\u623F\u5730\u4EA7\u3001\u7EB8\u4E1A\u3001\u670D\u88C5\u3001\u98DF\u54C1\u548C\u5236\u9020\u52A0\u5DE5\u7B49\u3002</p><p>\u4E25\u826F\u901A\u8FC7\u5C0F\u5403\u644A\u7684\u63D0\u793A\uFF0C\u77AC\u95F4\u60F3\u901A\u70C2\u5C3E\u697C\u7684\u7206\u70B8\u539F\u7406\u3002\u5C0F\u9A6C\u901A\u8FC7\u591A\u5BB6\u8D70\u8BBF\u8C03\u67E5\uFF0C\u4E5F\u8DDF\u9646\u5382\u957F\u53D6\u5F97\u8054\u7CFB\uFF0C\u843D\u5B9E\u8001\u5E74\u6276\u624B\u51FA\u81EA\u4ED6\u5BB6\u3002\u7531\u4E8E\u4EA7\u54C1\u4ECE2008\u5E74\u540E\u5C31\u5C06\u751F\u4EA7\u6279\u53F7\u4EE5\u94A2\u5370\u5F62\u5F0F\u6253\u5165\u94A2\u7BA1\u5185\u90E8\uFF0C\u6240\u4EE5\u60F3\u8981\u51C6\u786E\u751F\u4EA7\u65E5\u671F\uFF0C\u5FC5\u987B\u63D0\u4F9B\u63A5\u53E3\u5904\u5185\u90E8\u7167\u7247\u3002</p><p>\u968F\u540E\u5C0F\u9A6C\u8DDF\u4E25\u826F\u524D\u5F80\u5F20\u8D85\u5BB6\uFF0C\u6CA1\u60F3\u5230\u674E\u9759\u91CD\u65B0\u6536\u62FE\u623F\u95F4\uFF0C\u5E76\u8BA9\u5DE5\u4EBA\u62C6\u6389\u8001\u5E74\u6276\u624B\u3002\u4FE9\u4EBA\u5306\u5FD9\u8D76\u5230\u5783\u573E\u5904\u7406\u5382\u627E\u56DE\u8001\u5E74\u6276\u624B\uFF0C\u9646\u5382\u957F\u6839\u636E\u5185\u90E8\u6279\u53F7\u67E5\u5230\u8FD9\u6279\u4EA7\u54C1\u662F\u57282009\u5E746\u6708\u751F\u4EA7\uFF0C\u56E0\u4E3A\u4EA7\u54C1\u4F9B\u4E0D\u5E94\u6C42\uFF0C\u6240\u4EE5\u5230\u8FBE\u5BA2\u6237\u624B\u91CC\u9700\u5F97\u4E00\u4E24\u4E2A\u6708\u4EE5\u540E\u3002</p><p>\u6B21\u65E5\u4E00\u65E9\uFF0C\u6731\u4F1F\u5BB9\u5149\u7115\u53D1\u5730\u5F00\u7740\u8B66\u8F66\u505C\u5728\u68C0\u5BDF\u9662\u95E8\u53E3\uFF0C\u7D27\u63A5\u6253\u7535\u8BDD\u628A\u6C5F\u9633\u4ECE\u5355\u4F4D\u91CC\u53EB\u51FA\u6765\uFF0C\u8FEB\u4E0D\u53CA\u5F85\u5730\u62C9\u4ED6\u53BB\u6293\u80E1\u4E00\u6D6A\u548C\u5B59\u4F20\u798F\u3002\u6C5F\u9633\u5341\u5206\u9075\u5B88\u7A0B\u5E8F\u6B63\u4E49\uFF0C\u56E0\u6B64\u5E76\u4E0D\u770B\u597D\u6731\u4F1F\u7684\u8D38\u7136\u884C\u52A8\uFF0C\u6CA1\u60F3\u5230\u6731\u4F1F\u4E8B\u5148\u641E\u5B9A\u4F20\u5524\u8BC1\uFF0C\u4EE4\u4ED6\u8BDD\u5728\u5634\u8FB9\u53EA\u80FD\u54BD\u4E0B\u3002</p><p>\u6765\u5230\u5361\u6069\u96C6\u56E2\u5927\u53A6\uFF0C\u6731\u4F1F\u5E26\u4EBA\u95EF\u5165\u9AD8\u5C42\u529E\u516C\u533A\uFF0C\u76F4\u63A5\u6253\u5F00\u8463\u4E8B\u957F\u529E\u516C\u5927\u95E8\u3002\u80E1\u4E00\u6D6A\u8DBE\u9AD8\u6C14\u6602\uFF0C\u654F\u9510\u53D1\u73B0\u4F20\u5524\u8BC1\u5E76\u672A\u5199\u7740\u5B59\u4F20\u798F\u7684\u540D\u5B57\uFF0C\u6731\u4F1F\u53EA\u5F97\u4F9D\u7AE0\u529E\u4E8B\uFF0C\u6682\u4E14\u5C06\u4ED6\u5148\u62BC\u5F80\u8B66\u5C40\uFF0C\u600E\u6599\u5B59\u4F20\u798F\u8981\u6C42\u968F\u540C\u524D\u5F80\u3002</p><p>\u6731\u4F1F\u6CA1\u529E\u6CD5\u79C1\u4E0B\u5BA1\u8BAF\uFF0C\u4F46\u53C8\u4E0D\u60F3\u8FDE\u7D2F\u6C5F\u9633\uFF0C\u4E8E\u662F\u8BA9\u4ED6\u5148\u56DE\u68C0\u5BDF\u9662\uFF0C\u7D27\u63A5\u4E00\u8138\u9634\u6C89\u5730\u6765\u5230\u8B66\u5C40\u3002\u79E6\u53BF\u957F\u548C\u5C40\u91CC\u51E0\u4F4D\u9886\u5BFC\u7B49\u5019\u591A\u65F6\uFF0C\u9762\u5BF9\u4E00\u5E72\u4EBA\u7B49\u65A5\u8D23\u7684\u773C\u795E\uFF0C\u6731\u4F1F\u4F9D\u65E7\u89C6\u82E5\u65E0\u7779\uFF0C\u5429\u5490\u624B\u4E0B\u53BB\u7ED9\u80E1\u4E00\u6D6A\u505A\u7B14\u5F55\uFF0C\u7EE7\u800C\u518D\u53BB\u4F1A\u8BAE\u5BA4\u542C\u5B59\u4F20\u798F\u201C\u544A\u72B6\u201D\uFF0C\u540C\u65F6\u79E6\u53BF\u957F\u4E5F\u5BF9\u6731\u4F1F\u8868\u8FBE\u4E0D\u6EE1\u770B\u6CD5\u3002</p><p>\u6574\u4E2A\u4E0B\u5348\u7684\u5BA1\u8BAF\u6CA1\u6709\u534A\u70B9\u4F5C\u7528\uFF0C\u6B63\u526F\u5C40\u957F\u5148\u540E\u7ED9\u6731\u4F1F\u6253\u7535\u8BDD\uFF0C\u8981\u6C42\u4ED6\u7ACB\u9A6C\u653E\u4EBA\u3002\u6731\u4F1F\u788D\u4E8E\u8BC1\u636E\u4E0D\u8DB3\uFF0C\u53EA\u80FD\u653E\u4EBA\uFF0C\u6C14\u5F97\u76F4\u63A5\u53BB\u8DDF\u6C5F\u9633\u8BC9\u82E6\uFF0C\u4F4E\u5934\u5408\u8BA1\u534A\u54CD\uFF0C\u6253\u7B97\u8FDE\u591C\u5E26\u7740\u6C5F\u9633\u8D76\u5F80\u82D7\u9AD8\u4E61\u627E\u9EC4\u6BDB\u3002</p>',14),r=[o];function _(n,s){return e(),t("div",null,r)}const h=p(c,[["render",_],["__file","7.html.vue"]]);export{h as default};
