import{_ as p}from"./_plugin-vue_export-helper.cdc0426e.js";import{o,c as e,f as t}from"./app.1218f0f6.js";const r={},c=t('<h1 id="\u4E1C\u9B4F\u897F\u9B4F-\u5C40\u52BF" tabindex="-1"><a class="header-anchor" href="#\u4E1C\u9B4F\u897F\u9B4F-\u5C40\u52BF" aria-hidden="true">#</a> \u4E1C\u9B4F\u897F\u9B4F \u5C40\u52BF</h1><blockquote><p>\u6B22\u4E4B\u81EA\u7389\u58C1\u5F52\u4E5F\uFF0C\u519B\u4E2D\u8BB9\u8A00\u97E6\u5B5D\u5BBD\u4EE5\u5B9A\u529F\u5F29\u5C04\u6740\u4E1E\u76F8\uFF1B\u9B4F\u4EBA\u95FB\u4E4B\uFF0C\u56E0\u4E0B\u4EE4\u66F0\uFF1A\u201C\u52B2\u5F29\u4E00\u53D1\uFF0C\u51F6\u8EAB\u81EA\u9668\u3002\u201D\u6B22\u95FB\u4E4B\uFF0C\u52C9\u5750\u89C1\u8BF8\u8D35\uFF0C\u4F7F\u659B\u5F8B\u91D1\u4F5C\u300A\u6555\u52D2\u6B4C\u300B\uFF0C\u6B22\u81EA\u548C\u4E4B\uFF0C\u54C0\u611F\u6D41\u6D95\u3002</p></blockquote><p><strong>\u300A\u8D44\u6CBB\u901A\u9274\xB7\u5377\u4E00\u4E94\u4E5D\xB7\u6881\u7EAA\u5341\u4E94\u300B</strong></p><p><code>\u5B5D\u6B66\u5E1D</code>\u897F\u5954\u540E\uFF0C<strong>\u5317\u9B4F</strong>\u5206\u88C2\u4E3A\u4E1C\u897F\u9B4F\u2014\u2014<strong>\u4E1C\u9B4F</strong>\u6210\u4E86<code>\u9AD8\u6B22</code>\u7684\u5730\u76D8\uFF0C<strong>\u897F\u9B4F</strong>\u5219\u5C3D\u5728<code>\u5B87\u6587\u6CF0</code>\u638C\u63E1\u4E4B\u4E2D\u3002\u8FD9\u4E24\u4EBA\u95F4\u7684\u77DB\u76FE\u4E5F\u8FC5\u901F\u5347\u7EA7\uFF0C\u4ECE\u201C\u4EBA\u6C11\u5185\u90E8\u77DB\u76FE\u201D\u6F14\u53D8\u6210\u201C\u654C\u6211\u77DB\u76FE\u201D\u3002\u5728\u6B64\u540E\u7684\u5341\u51E0\u5E74\u95F4\uFF0C\u4E24\u56FD\u8FDB\u884C\u4E86\u4E94\u6B21\u89C4\u6A21\u5E9E\u5927\u7684\u6218\u4E89\uFF0C\u4E92\u6709\u80DC\u8D1F\u3002</p><p>\u6700\u7EC8\u7684\u7ED3\u679C\uFF0C\u662F\u4E24\u56FD\u8C01\u4E5F\u5948\u4F55\u4E0D\u4E86\u5BF9\u65B9\uFF0C\u7EC8\u7A76\u8FD8\u662F\u5E73\u5206\u79CB\u8272\u4E4B\u5C40\u3002<strong>\u4F46\u4E0E\u6218\u524D\u4E0D\u540C\u7684\u662F\uFF0C\u4E24\u56FD\u7684\u56FD\u529B\u6B64\u6D88\u5F7C\u957F</strong>\uFF1A\u897F\u9B4F\uFF08\u5317\u5468\uFF09\u6700\u521D\u52BF\u5F31\uFF0C\u4F46\u7ECF\u8FC7\u4E94\u573A\u6218\u4E89\u4E4B\u540E\uFF0C\u5DF2\u7ECF\u80FD\u591F\u7AD9\u7A33\u811A\u8DDF\uFF1B\u800C\u4E1C\u9B4F\uFF08\u5317\u9F50\uFF09\uFF0C\u5219\u6C38\u8FDC\u5931\u53BB\u4E86\u6D88\u706D\u897F\u9B4F\u7684\u80FD\u529B\u3002</p><p>\u8FD9\u4E94\u6B21\u6218\u5F79\u7684\u8FC7\u7A0B\uFF0C\u672C\u8EAB\u5E76\u65E0\u751A\u53EF\u8DB3\u79F0\u9053\u4E4B\u5904\uFF0C\u5176\u95F4\u65E2\u6CA1\u6709\u60CA\u5929\u52A8\u5730\u7684\u5927\u6218\uFF0C\u66F4\u6CA1\u6709\u540D\u5782\u9752\u53F2\u7684\u9006\u8F6C\uFF0C\u5728\u4E2D\u56FD\u6F2B\u957F\u7684\u519B\u4E8B\u5386\u53F2\u4E2D\uFF0C\u5B83\u4EEC\u5B9E\u5728\u6CA1\u6709\u4EC0\u4E48\u7279\u522B\u503C\u5F97\u8BB0\u5F55\u7684\u3002\u751A\u81F3\u4E8E\u8FD9\u4E94\u6B21\u6218\u4E89\u5BF9\u4E8E\u53CC\u65B9\u4E2D\u5206\u5317\u4E2D\u56FD\u7684\u73B0\u72B6\uFF0C\u4E5F\u6CA1\u6709\u9020\u6210\u4EC0\u4E48\u5B9E\u8D28\u6027\u7684\u5F71\u54CD\u548C\u6539\u53D8\u3002</p><p><strong>\u8FD9\u51E0\u573A\u6218\u4E89\u7684\u5173\u952E\uFF0C\u5176\u5B9E\u4E0D\u5728\u4E8E\u80DC\u8D1F</strong>\u2014\u2014\u6218\u4E89\u7684\u80DC\u8D1F\uFF0C\u603B\u4F1A\u5939\u6742\u5F88\u591A\u8FD0\u6C14\u7684\u6210\u5206\u5728\u5176\u4E2D\uFF0C\u800C\u662F\u5728\u4E8E\u897F\u9B4F\u5728\u524D\u4E09\u6B21\u83B7\u80DC\u800C\u7B2C\u56DB\u6B21\u5927\u8D25\u7684\u60C5\u51B5\u4E0B\uFF0C<strong>\u5B87\u6587\u6CF0\u88AB\u8FEB\u5728\u56FD\u5185\u8FDB\u884C\u4E86\u519B\u4E8B\u4F53\u5236\u7684\u6539\u9769\u3002\u7136\u540E\uFF0C\u624D\u6709\u4E86<code>\u5E9C\u5175\u5236</code>\u7684\u51FA\u73B0\u3002</strong></p><p>\u53CC\u65B9\u7684\u7B2C\u4E00\u6B21\u6218\u4E89\uFF0C\u662F<code>\u5C0F\u5173\u4E4B\u6218</code>\uFF0C\u7531\u4E1C\u9B4F\uFF08\u5317\u9F50\uFF09\u4E00\u65B9\u6311\u8D77\u3002</p><p>\u9AD8\u6B22\u7684\u7B56\u7565\u7B80\u5355\u800C\u76F4\u63A5\uFF1A\u5229\u7528\u4E24\u56FD\u56FD\u529B\u4E0A\u7684\u5DEE\u8DDD\uFF0C\u76F4\u63A5\u78BE\u538B\u5BF9\u624B\uFF0C\u4EE5\u5927\u6B3A\u5C0F\uFF0C\u4EE5\u77F3\u51FB\u5375\u3002</p><p>\u4E1C\u897F\u9B4F\u5206\u88C2\u65F6\uFF0C\u4E1C\u9B4F\u5360\u636E\u7684\u662F\u5317\u4E2D\u56FD\u7684\u4E1C\u90E8\uFF0C\u8FD9\u662F\u5F53\u65F6\u5929\u4E0B\u6700\u4E3A\u5BCC\u9976\u7684\u5730\u533A\u3002\u800C\u897F\u9B4F\u867D\u7136\u4E0E\u4E1C\u9B4F\u5E73\u5206\u5317\u4E2D\u56FD\uFF0C\u4F46\u771F\u6B63\u80FD\u501A\u9760\u7684\uFF0C\u5176\u5B9E\u8FD8\u662F\u5173\u4E2D\u4E4B\u5730\u3002</p><p><strong>\u4E24\u76F8\u5BF9\u6BD4\u4E4B\u4E0B\uFF0C\u897F\u9B4F\u7684\u56FD\u529B\u8981\u8FDC\u5F31\u4E8E\u4E1C\u9B4F\u3002</strong></p><p>\u6839\u636E\u540E\u4E16\u4F30\u8BA1\uFF0C\u5373\u4FBF\u5728\u51E0\u5341\u5E74\u4EE5\u540E\uFF0C\u5230\u4E86\u5317\u5468\u706D\u4EA1\u5317\u9F50\u4E4B\u65F6\uFF0C\u5317\u9F50\u867D\u7136\u7ECF\u5386\u4E86\u653F\u6CBB\u9ED1\u6697\u3001\u6C11\u4E0D\u804A\u751F\u7684\u4E71\u76F8\uFF0C\u4F46\u4EBA\u53E3\u6570\u76EE\u4F9D\u7136\u662F\u5317\u5468\u7684\u4E24\u500D\u4EE5\u4E0A\u3002\u800C\u5F80\u524D\u63A8\u51E0\u5341\u5E74\uFF0C\u4E24\u56FD\u7684\u8FD9\u79CD\u5B9E\u529B\u5DEE\u8DDD\u53EA\u4F1A\u66F4\u5927\u3002</p><p>\u66F4\u4F55\u51B5\uFF0C<strong>\u5B87\u6587\u6CF0\u4E0E\u9AD8\u6B22\u6240\u4F9D\u4ED7\u7684\u519B\u4E8B\u529B\u91CF\uFF0C\u5BF9\u6BD4\u4E5F\u662F\u60AC\u6B8A\u7684</strong>\uFF1A\u867D\u7136\u53CC\u65B9\u7684\u6E90\u5934\u90FD\u662F\u6765\u81EA\u4E8E\u5317\u9B4F\u7684\u516D\u9547\u519B\u6237\uFF0C\u4F46\u5176\u4E2D\u5B87\u6587\u6CF0\u6240\u5F97\u5230\u7684\uFF0C\u5927\u7EA6\u53EA\u6709\u6000\u6714\u4E00\u9547\u7684\u5175\u529B\u3002</p><p>\u53EF\u5C31\u662F\u5728\u8FD9\u6837\u7684\u60AC\u6B8A\u4E4B\u4E0B\uFF0C\u5C0F\u5173\u4E4B\u6218\uFF0C\u6700\u7EC8\u8FD8\u662F\u4EE5\u4E1C\u9B4F\u5931\u8D25\u800C\u544A\u7EC8\u3002</p><p>\u6574\u4E2A\u6218\u4E89\u8FC7\u7A0B\u6BD4\u8F83\u7B80\u5355\uFF1A\u9AD8\u6B22\u9886\u5175\u897F\u8FDB\uFF0C\u610F\u6B32\u5728\u9EC4\u6CB3\u4E0A\u67B6\u8BBE\u6D6E\u6865\uFF0C\u8FC7\u6CB3\u8FDB\u5165\u5173\u4E2D\u3002\u4F46\u5B87\u6587\u6CF0\u4E00\u773C\u5C31\u770B\u7A7F\u4E86\u9AD8\u6B22\u7684\u8BA1\u8C0B\uFF1A\u660E\u9762\u4E0A\u662F\u51C6\u5907\u6E21\u9EC4\u6CB3\uFF0C\u4F46\u5B9E\u9645\u4E0A\u5374\u662F\u51C6\u5907\u4EE5\u504F\u5E08\u5077\u88AD\u9646\u8DEF\u3002</p><p>\u56E0\u6B64\uFF0C\u5B87\u6587\u6CF0\u5728\u5C0F\u5173\u9644\u8FD1\u8BBE\u91CD\u5175\u62E6\u622A\uFF0C\u51FB\u9000\u4E86\u9AD8\u6B22\u9EBE\u4E0B\u7684\u7AA6\u6CF0\u90E8\u3002</p><p>\u9AD8\u6B22\u968F\u5373\u64A4\u519B\uFF0C\u5C0F\u5173\u4E4B\u6218\u4E5F\u5BA3\u544A\u7ED3\u675F\u3002</p><p>\u7AD9\u5728\u540E\u4E16\u6765\u63A8\u6F14\uFF0C\u6211\u4EEC\u5F88\u5BB9\u6613\u5C31\u80FD\u4ECE\u5C0F\u5173\u4E4B\u6218\u7684\u8FC7\u7A0B\u4E2D\u63A8\u51FA\u4E00\u4E9B\u7ED3\u8BBA\uFF1A\u76F8\u8F83\u4E8E\u8C0B\u7565\uFF0C\u9AD8\u6B22\u7684\u519B\u4E8B\u80FD\u529B\u662F\u8981\u5F31\u8BB8\u591A\u7684\u3002<strong>\u6240\u4EE5\uFF0C\u4ED6\u5F88\u5BB9\u6613\u5C31\u4F1A\u60F3\u8981\u53D1\u6325\u81EA\u5DF1\u7684\u4F18\u52BF\uFF0C\u4EE5\u5F25\u8865\u81EA\u5DF1\u7684\u77ED\u677F\u3002</strong></p><p>\u800C\u4EC5\u4EC5\u624D\u8FC7\u4E86\u51E0\u4E2A\u6708\uFF0C\u4E24\u56FD\u5C31\u53C8\u6380\u8D77\u4E86\u53E6\u5916\u4E00\u573A\u6218\u4E89\u3002</p><p>\u8FD9\u4E00\u5E74\uFF0C\u5173\u4E2D\u5927\u65F1\u3002\u5B87\u6587\u6CF0\u5728\u5173\u4E2D\u5B9E\u5728\u662F\u5F85\u4E0D\u4E0B\u53BB\u4E86\uFF0C\u53EA\u5F97\u5E26\u7740\u4E00\u4E07\u591A\u4EBA\u7684\u519B\u961F\u51FA\u6F7C\u5173\uFF0C\u5230\u4E1C\u9B4F\u7EDF\u6CBB\u4E0B\u7684\u6853\u519C\u8C37\u4ED3\u5C31\u98DF\u2014\u2014\u8FD9\u4E00\u5403\u5C31\u662F\u4E94\u5341\u591A\u5929\u7684\u65F6\u95F4\u3002</p><p>\u8FD9\u4E00\u6B21\uFF0C\u9AD8\u6B22\u7528\u4E86\u56F4\u9B4F\u6551\u8D75\u4E4B\u8BA1\uFF1A\u4ED6\u5148\u6D3E\u4EBA\u56F4\u653B\u6853\u519C\uFF0C\u7136\u540E\u81EA\u5DF1\u518D\u4EB2\u7387\u5927\u519B\u4ECE\u84B2\u5742\u6E21\u8FC7\u9EC4\u6CB3\uFF0C\u653B\u51FB\u5173\u4E2D\u3002\u4F46\u8FD9\u4E2A\u65F6\u5019\uFF0C\u5B87\u6587\u6CF0\u679C\u65AD\u4ECE\u6853\u519C\u64A4\u5175\uFF0C\u56DE\u5E08\u5173\u4E2D\u3002\u8D81\u7740\u4E1C\u9B4F\u7ACB\u8DB3\u672A\u7A33\uFF0C\u5B87\u6587\u6CF0\u53D1\u8D77\u653B\u51FB\uFF0C\u53D6\u5F97\u5927\u80DC\uFF0C\u53F2\u79F0<code>\u6C99\u82D1\u4E4B\u6218</code>\u3002</p><p>\u4E4B\u540E\uFF0C\u65F6\u9694\u4E0D\u5230\u4E00\u5E74\uFF0C\u53CC\u65B9\u7D27\u63A5\u7740\u53C8\u53D1\u751F\u4E86\u7B2C\u4E09\u6B21\u6218\u4E89\uFF1A<code>\u6CB3\u6865\u4E4B\u6218</code>\u3002\u897F\u9B4F\u5927\u7EDF\u56DB\u5E74\uFF0C\u5B87\u6587\u6CF0\u62A4\u9001\u897F\u9B4F\u7687\u5E1D<code>\u5143\u5B9D\u77E9</code>\u5165\u6D1B\u9633\u796D\u7956\u3002\u9AD8\u6B22\u95FB\u77E5\uFF0C\u53D1\u5175\u653B\u51FB\uFF0C\u4F46\u88AB\u5B87\u6587\u6CF0\u51FB\u9000\u3002\u5728\u6B64\u6218\u4E2D\uFF0C\u53CC\u65B9\u4E24\u8D25\u4FF1\u4F24\uFF0C\u5B87\u6587\u6CF0\u635F\u5175\u6298\u5C06\uFF0C\u4F46\u5E73\u5B89\u64A4\u56DE\u5230\u5173\u4E2D\u3002</p><p><strong>\u771F\u6B63\u5BF9\u5B87\u6587\u6CF0\u9020\u6210\u4E86\u51B2\u51FB\u7684\uFF0C\u662F\u7B2C\u56DB\u6B21\u7684<code>\u9099\u5C71\u4E4B\u6218</code></strong>\u3002\u5728\u6B64\u6218\u4E2D\uFF0C\u897F\u9B4F\u5148\u80DC\u540E\u8D25\uFF0C\u4E3B\u529B\u51E0\u4E4E\u635F\u5931\u6B86\u5C3D\uFF0C\u5B87\u6587\u6CF0\u4EC5\u4EE5\u8EAB\u514D\u2014\u2014\u4E5F\u5C31\u662F\u8BF4\uFF0C\u5728\u7ECF\u8FC7\u4E86\u957F\u8FBE\u5341\u51E0\u5E74\u7684\u6218\u4E89\u540E\uFF0C\u4E1C\u9B4F\u7EC8\u4E8E\u53D6\u5F97\u538B\u5012\u6027\u7684\u80DC\u5229\u3002</p><p><strong>\u4F46\u8FD9\u6837\u7684\u7ED3\u679C\uFF0C\u5E76\u6CA1\u6709\u7ED9\u9AD8\u6B22\u5E26\u6765\u673A\u9047\uFF0C\u53CD\u5012\u662F\u523A\u6FC0\u4E86\u5B87\u6587\u6CF0\u52A0\u901F\u8FDB\u884C\u56FD\u5185\u6539\u9769</strong>\u2014\u2014\u8FD9\u4E5F\u5C31\u662F<code>\u5E9C\u5175\u5236</code>\u7684\u7531\u6765\uFF0C\u5176\u5177\u4F53\u5185\u5BB9\uFF0C\u6211\u4EEC\u4F1A\u7559\u5230\u4E0B\u7BC7\u8BE6\u7EC6\u5C55\u5F00\u3002</p><p>\u4E4B\u540E\uFF0C\u53C8\u8FC7\u4E86\u4E94\u5E74\uFF0C\u9AD8\u6B22\u9886\u5175\u897F\u8FDB\uFF0C\u653B\u6253\u897F\u9B4F\u5728\u9EC4\u6CB3\u8FB9\u7684\u636E\u70B9\u7389\u74A7\uFF0C\u53F2\u79F0<code>\u7389\u74A7\u4E4B\u6218</code>\u3002\u7136\u800C\u4E1C\u9B4F\u5F3A\u653B\u56DB\u5341\u4E03\u5929\u5374\u6BEB\u65E0\u6218\u679C\uFF0C\u897F\u9B4F\u5B88\u5C06<code>\u97E6\u5B5D\u5BBD</code>\u56E0\u6B64\u6210\u540D\uFF0C\u5F97\u4EE5\u8DFB\u8EAB\u540D\u5C06\u4E4B\u5217\u3002</p><p>\u5728\u6B64\u6218\u4E2D\uFF0C\u9AD8\u6B22\u5DF2\u7ECF\u8EAB\u60A3\u91CD\u75BE\uFF0C\u5E76\u4E8E\u6B21\u5E74\u75C5\u901D\u3002</p><p>\u6B64\u540E\uFF0C\u4E24\u56FD\u8FDB\u5165\u4E86\u4E00\u6BB5\u76F8\u5BF9\u548C\u5E73\u7684\u65F6\u671F\u3002</p><p>\u5982\u679C\u7528\u73B0\u4EE3\u7684\u8BED\u8A00\u6765\u5F62\u5BB9\uFF0C\u90A3\u4E48\u9AD8\u6B22\u4E0E\u5B87\u6587\u6CF0\u7EDD\u5BF9\u662F\u201C\u5E08\u51FA\u540C\u95E8\uFF0C\u5144\u5F1F\u53CD\u76EE\u201D\u7684\u6700\u4F73\u6848\u4F8B\u3002</p><p>\u4E24\u4EBA\u90FD\u66FE\u5728<code>\u5C14\u6731\u8363</code>\u9EBE\u4E0B\u505A\u4E8B\uFF0C\u5728\u5C14\u6731\u8363\u6B7B\u540E\uFF0C\u53C8\u90FD\u662F\u501A\u4ED7\u7740\u5C14\u6731\u8363\u5F53\u5E74\u5E73\u5B9A\u516D\u9547\u4E4B\u4E71\u6536\u7F16\u7684\u519B\u961F\uFF0C\u8D70\u4E0A\u4E86\u4E89\u9738\u5929\u4E0B\u7684\u8DEF\u3002</p><p>\u4F46\u5728\u8FD9\u6761\u8DEF\u4E0A\uFF0C\u4E24\u4EBA\u7684\u7ED3\u5C40\u53C8\u51E0\u4E4E\u662F\u5FC5\u7136\u7684\uFF1A<strong>\u53CC\u65B9\u5175\u529B\u4E0D\u5BF9\u7B49\uFF0C\u5185\u90E8\u8D44\u6E90\u4E5F\u4E0D\u5BF9\u7B49\u3002</strong></p><p>\u5728\u53CC\u65B9\u5B9E\u529B\u5DEE\u8DDD\u5DE8\u5927\u7684\u524D\u63D0\u4E0B\uFF0C\u5F31\u52BF\u7684\u5B87\u6587\u6CF0\u6240\u80FD\u4F9D\u4ED7\u7684\uFF0C\u53EA\u6709\u81EA\u5DF1\u5728<code>\u519B\u4E8B\u80FD\u529B</code>\u65B9\u9762\u76F8\u8F83\u4E8E\u9AD8\u6B22\u7684\u4F18\u52BF\u3002</p><p>\u6B63\u662F\u9760\u7740\u8FD9\u4E00\u70B9\u70B9\u4F18\u52BF\u7684\u5B58\u5728\uFF0C\u897F\u9B4F\u624D\u80FD\u4FDD\u8BC1\u5728\u5C0F\u5173\u4E4B\u6218\u3001\u6C99\u82D1\u4E4B\u6218\u4E2D\u7684\u80DC\u5229\u3002</p><p>\u4F46\u5B9E\u529B\u4E0A\u7684\u5DEE\u8DDD\u4F9D\u65E7\u5BA2\u89C2\u5B58\u5728\uFF1A\u9AD8\u6B22\u5931\u8D25\u4E86\u8FD8\u80FD\u91CD\u5934\u518D\u6765\uFF0C\u4E00\u6B21\u4E24\u6B21\u8D25\u4E86\u2014\u2014\u5373\u4FBF\u662F\u5DE8\u5927\u7684\u6E83\u8D25\uFF0C\u4E5F\u603B\u6709\u518D\u6765\u4E00\u6B21\u7684\u673A\u4F1A\u3002</p><p>\u53EF\u5B87\u6587\u6CF0\u5374\u4E0D\u540C\u3002\u5373\u4FBF\u6709\u5C0F\u5173\u4E4B\u6218\u3001\u6C99\u82D1\u4E4B\u6218\u3001\u6CB3\u6865\u4E4B\u6218\u4E09\u6B21\u5DE8\u5927\u7684\u80DC\u5229\uFF0C\u4ED6\u6240\u80FD\u505A\u5230\u7684\uFF0C\u4E5F\u53EA\u662F\u628A\u6218\u7EBF\u63A8\u8FDB\u5230\u4E1C\u9B4F\u5883\u5185\u3002\u800C\u7B49\u5230\u9099\u5C71\u4E4B\u6218\u65F6\uFF0C\u4E00\u6B21\u5931\u8D25\uFF0C\u5C31\u8BA9\u4ED6\u51E0\u4E4E\u906D\u53D7\u5230\u4E86\u503E\u56FD\u4E4B\u5371\u3002</p><p>\u4E5F\u5C31\u662F\u8BF4\uFF0C<strong>\u5728\u540C\u4E00\u79CD\u6A21\u5F0F\u7684\u7ADE\u4E89\u4E4B\u4E0B\uFF0C\u56E0\u4E3A<code>\u521D\u59CB\u8D44\u6E90</code>\u4E0D\u540C\uFF0C\u5B87\u6587\u6CF0\u7684\u5931\u8D25\u51E0\u4E4E\u662F\u5FC5\u7136\u7684\u3002</strong></p><p>\u867D\u7136\u76F8\u8F83\u4E8E\u9AD8\u6B22\uFF0C\u5B87\u6587\u6CF0\u6709\u519B\u4E8B\u80FD\u529B\u4E0A\u7684\u4F18\u52BF\uFF0C\u4F46\u8FD9\u4E2A\u4F18\u52BF\u4E4B\u5F31\uFF0C\u8FDC\u4E0D\u8DB3\u4EE5\u5F25\u8865\u4E24\u56FD\u5728\u5B9E\u529B\u4E0A\u7684\u5DEE\u8DDD\u3002</p><p>\u81F3\u6B64\uFF0C<strong>\u5BF9\u5B87\u6587\u6CF0\u800C\u8A00\uFF0C\u771F\u6B63\u80FD\u8BA9\u80DC\u8D1F\u9006\u8F6C\u7684\uFF0C\u5C31\u53EA\u6709\u5728<code>\u5185\u90E8\u7EC4\u7EC7\u5236\u5EA6</code>\u4E0A\u6709\u6240\u521B\u65B0\u4E86\u3002</strong></p><p>\u8FD9\u79CD\u521B\u65B0\u96BE\u5417\uFF1F</p><p>\u5F53\u7136\u96BE\u3002\u4E3A\u4EC0\u4E48\u90FD\u8BF4\u5236\u5EA6\u521B\u65B0\u662F\u88AB\u903C\u51FA\u6765\u7684\uFF0C\u5C31\u662F\u56E0\u4E3A\u8FD9\u79CD\u5236\u5EA6\u521B\u65B0\u7684\u96BE\u5EA6\uFF0C\u662F\u6700\u5927\u7684\u3002</p><p>\u6211\u4EEC\u53EF\u4EE5\u7528\u4E00\u4E2A\u6700\u4E3A\u7B80\u5355\u7684\u4F8B\u5B50\u6765\u505A\u89E3\u91CA\uFF1A\u6BCF\u4E2A\u4EBA\u90FD\u89C9\u5F97\u81EA\u5DF1\u8EAB\u4E0A\u6709\u5F88\u591A\u574F\u6BDB\u75C5\u9700\u8981\u6539\u6389\uFF0C\u4F46\u82B8\u82B8\u4F17\u751F\uFF0C\u53C8\u6709\u51E0\u4EBA\u80FD\u771F\u6B63\u505A\u5230\u5462\uFF1F</p><p>\u800C\u5982\u679C\u662F\u4E00\u4E2A\u56FD\u5BB6\u8981\u8FDB\u884C\u5236\u5EA6\u521B\u65B0\uFF0C\u90A3\u96BE\u5EA6\u5C31\u66F4\u5927\u4E86\uFF1A\u9700\u8981\u8C03\u52A8\u6240\u6709\u4EBA\u53BB\u6539\u53D8\u81EA\u5DF1\u56FA\u6709\u7684\u884C\u4E3A\u4E60\u60EF\u3002</p><p>\u8FD9\u53C8\u6709\u591A\u96BE\uFF1F</p><p>\u4F46\u51E1\u6709\u522B\u7684\u8DEF\u53EF\u8D70\uFF0C\u90FD\u4E0D\u4F1A\u8D70\u8FD9\u6761\u8DEF\u3002</p><p>\u897F\u9B4F\u5982\u679C\u6CA1\u6709\u906D\u9047\u9099\u5C71\u4E4B\u6218\u7684\u5927\u8D25\uFF0C\u90A3\u4E48\u5B87\u6587\u6CF0\u4F1A\u4E00\u76F4\u8BA4\u4E3A\uFF0C\u81EA\u5DF1\u9760\u7740\u519B\u4E8B\u80FD\u529B\u4E0A\u7684\u4F18\u52BF\uFF0C\u662F\u80FD\u591F\u5F25\u8865\u81EA\u8EAB\u5B9E\u529B\u7684\u4E0D\u8DB3\u7684\uFF0C\u6CA1\u6709\u5FC5\u8981\u975E\u53BB\u8D70\u5236\u5EA6\u521B\u65B0\u7684\u8346\u68D8\u4E4B\u8DEF\u3002</p><p>\u53EA\u6709\u7B49\u9099\u5C71\u4E4B\u6218\u5927\u8D25\uFF0C\u897F\u9B4F\u5DF2\u7ECF\u65E0\u8DEF\u53EF\u8D70\uFF0C\u624D\u4F1A\u6700\u7EC8\u903C\u7740\u5B87\u6587\u6CF0\u53BB\u5BFB\u627E\u66F4\u597D\u7684\u8D44\u6E90\u6574\u5408\u529E\u6CD5\u3002\u6BD5\u7ADF\uFF0C\u6B64\u65F6\u56DB\u76EE\u8352\u91CE\uFF0C\u8FD9\u53EF\u80FD\u5DF2\u7ECF\u662F\u626D\u8F6C\u4E7E\u5764\u7684\u552F\u4E00\u6551\u547D\u7A3B\u8349\u4E86\u3002</p><p><strong>\u771F\u6B63\u7684\u5236\u5EA6\u521B\u65B0\uFF0C\u4E00\u822C\u90FD\u662F\u5728\u65AD\u4E86\u5176\u4ED6\u8DEF\u7684\u5FF5\u60F3\u4E4B\u540E\uFF0C\u624D\u4F1A\u60F3\u5230\u8FD9\u6761\u6700\u96BE\u7684\u8DEF\u3002</strong></p><p>\u4E0D\u8FC7\uFF0C\u5F53\u7136\uFF0C<strong>\u8FD9\u6761\u8DEF\u4E00\u65E6\u6210\u529F\uFF0C\u6240\u7206\u53D1\u51FA\u6765\u7684\u529B\u91CF\u4E5F\u662F\u65E0\u6BD4\u5DE8\u5927\u7684\u3002</strong></p><h2 id="\u5317\u5468-\u897F\u9B4F-\u80DC\u51FA\u5173\u952E\u70B9" tabindex="-1"><a class="header-anchor" href="#\u5317\u5468-\u897F\u9B4F-\u80DC\u51FA\u5173\u952E\u70B9" aria-hidden="true">#</a> \u5317\u5468\uFF08\u897F\u9B4F\uFF09\u80DC\u51FA\u5173\u952E\u70B9</h2><p>\u56DE\u5934\u770B\u5386\u53F2\uFF0C\u5DF2\u7ECF\u53EF\u4EE5\u786E\u8BA4\u5317\u5468\uFF08\u897F\u9B4F\uFF09\u6700\u7EC8\u80FD\u591F\u80DC\u51FA\uFF0C\u662F\u6E90\u81EA\u4E8E\u5E9C\u5175\u5236\u7684\u3002\u4F46\u662F\uFF0C\u53EA\u8981\u771F\u7684\u6DF1\u5165\u5230\u5F53\u5E74\u7684\u5386\u53F2\u4E4B\u4E2D\u5C31\u4F1A\u53D1\u73B0\uFF0C<strong>\u5E9C\u5175\u5236\u8FDC\u8FDC\u4E0D\u662F\u5F53\u65F6\u897F\u9B4F\uFF08\u5317\u5468\uFF09\u7531\u5F31\u8F6C\u5F3A\u7684\u5173\u952E\u3002</strong></p><p>\u5982\u679C\u4E00\u5B9A\u8981\u627E\u4E00\u4E2A\u5173\u952E\u53D8\u91CF\u7684\u8BDD\uFF0C\u90A3\u4E48\uFF0C\u5E94\u8BE5\u662F<strong>\u9AD8\u6B22\u4E4B\u6B7B</strong>\u3002</p><p>\u5728\u7B2C\u4E94\u6B21\u4E1C\u897F\u9B4F\u6218\u4E89\u2014\u2014\u4E5F\u5C31\u662F\u7389\u74A7\u4E4B\u6218\u53D1\u751F\u540E\u4E00\u5E74\uFF0C\u9AD8\u6B22\u56E0\u75C5\u53BB\u4E16\uFF0C\u4EAB\u5E7452\u5C81\uFF0C\u4E4B\u540E\uFF0C\u5B87\u6587\u6CF0\u53C8\u6D3B\u4E869\u5E74\u3002</p><p>\u6B63\u662F\u56E0\u4E3A\u9AD8\u6B22\u7684\u6B7B\uFF0C\u4E1C\u897F\u9B4F\u4E4B\u95F4\u6301\u7EED\u4E86\u5341\u51E0\u5E74\u7684\u6218\u4E89\u624D\u7EC8\u4E8E\u544A\u4E00\u6BB5\u843D\u3002\u800C\u8FD9\uFF0C\u7ED9\u9971\u53D7\u6218\u4E89\u4E4B\u82E6\u7684\u897F\u9B4F\u5E26\u6765\u4E86\u5598\u606F\u4E4B\u673A\u3002</p><p>\u53EF\u4EE5\u8BBE\u60F3\uFF0C**\u5982\u679C\u9AD8\u6B22\u665A\u6B7B\u5341\u5E74\uFF0C\u4E24\u56FD\u4E4B\u95F4\u7684\u6218\u4E89\u518D\u6301\u7EED\u5341\u5E74\uFF0C\u90A3\u4E48\u897F\u9B4F\u4E00\u5B9A\u4F1A\u88AB\u4E1C\u9B4F\u62D6\u57AE\u3002**\u6BD5\u7ADF\u518D\u597D\u7684\u5236\u5EA6\uFF0C\u8981\u771F\u6B63\u53D1\u6325\u51FA\u4F5C\u7528\uFF0C\u4E5F\u662F\u65E2\u9700\u8981\u5B89\u5B9A\u548C\u5E73\u7684\u5916\u90E8\u73AF\u5883\uFF0C\u53C8\u9700\u8981\u5145\u5206\u7684\u65F6\u95F4\u53BB\u843D\u5B9E\u9002\u5E94\u65B0\u53D8\u5316\u7684\u3002</p><p>\u5982\u679C\u9AD8\u6B22\u4E0D\u6B7B\uFF0C\u90A3\u4E48\u897F\u9B4F\u6839\u672C\u5C31\u6CA1\u6709\u8FD9\u6837\u7684\u73AF\u5883\u3002</p><p>\u800C\u4E14\uFF0C\u9AD8\u6B22\u7684\u6B7B\uFF0C\u8FD8\u5E26\u6765\u4E86\u53E6\u5916\u4E24\u4E2A\u610F\u5916\u7684\u7ED3\u679C\uFF0C\u88AB\u897F\u9B4F\uFF08\u5317\u5468\uFF09\u5145\u5206\u628A\u63E1\uFF1A</p><blockquote><p><strong>\u9996\u5148\uFF0C\u662F\u4FAF\u666F\u4E4B\u4E71\u3002</strong></p></blockquote><p><code>\u4FAF\u666F</code>\u6700\u521D\u6295\u9760\u9AD8\u6B22\uFF0C\u4F46\u5728\u9AD8\u6B22\u6B7B\u540E\uFF0C\u56E0\u4E3A\u4E0E\u7EE7\u4F4D\u7684<code>\u9AD8\u6F84</code>\u5F7C\u6B64\u4E0D\u4FE1\u4EFB\uFF0C\u53CD\u590D\u518D\u4E09\u7EC8\u4E8E\u6295\u9760\u4E86<code>\u6881\u671D</code>\uFF0C\u5E76\u5F15\u53D1\u4FAF\u666F\u4E4B\u4E71\u3002</p><p>\u5357\u6881\u51E0\u4E4E\u662F\u5728\u4E00\u591C\u4E4B\u95F4\u5954\u6E83\uFF0C\u8FD9\u7ED9\u4E86\u897F\u9B4F\u5DE8\u5927\u7684\u6218\u7565\u673A\u9047\uFF0C<strong>\u501F\u52A9\u4E8E\u5357\u6881\u7684\u5185\u4E71\uFF0C\u5B87\u6587\u6CF0\u5148\u540E\u5360\u636E\u4E86\u672C\u5C5E\u4E8E\u5357\u6881\u7684\u76CA\u5DDE\u548C\u8346\u5DDE\u3002</strong></p><p>\u8FD9\u4E24\u5730\uFF0C\u610F\u4E49\u5DE8\u5927\uFF1A\u5173\u4E2D\u52A0\u76CA\u5DDE\uFF0C\u8FD9\u5C31\u662F\u79E6\u706D\u516D\u56FD\u548C\u5218\u90A6\u5B9A\u5929\u4E0B\u7684\u6807\u51C6\u6A21\u677F\uFF1B\u81F3\u4E8E\u5360\u636E\u8346\u5DDE\uFF0C\u5219\u4E3A\u540E\u6765\u7684\u968B\u706D\u9648\u4E4B\u6218\u626B\u6E05\u4E86\u5DE8\u5927\u969C\u788D\u3002</p><blockquote><p><strong>\u7136\u540E\uFF0C\u662F\u4E1C\u9B4F\uFF08\u5317\u9F50\uFF09\u671D\u5EF7\u7684\u653F\u6CBB\u6597\u4E89\u3002</strong></p></blockquote><p>\u81EA\u9AD8\u6B22\u4EE5\u6765\uFF0C\u4E1C\u9B4F\uFF08\u5317\u9F50\uFF09\u7684\u7EDF\u6CBB\u6A21\u5F0F\u4E00\u76F4\u90FD\u5728\u8FDC\u79BB\u6C49\u5316\u3002\u81EA\u9AD8\u6B22\u6B7B\u540E\uFF0C\u4E1C\u9B4F\uFF08\u5317\u9F50\uFF09\u5BBF\u547D\u822C\u5730\u51FA\u73B0\u4E86\u65E0\u4F11\u6B62\u7684\u653F\u6CBB\u6597\u4E89\uFF0C\u8FD9\u53C8\u7ED9\u897F\u9B4F\uFF08\u5317\u5468\uFF09\u5E26\u6765\u4E86\u957F\u8FBE\u5341\u51E0\u5E74\u7684\u7A97\u53E3\u65F6\u95F4\u3002</p><p>\u5728\u9AD8\u6B22\u6B7B\u540E\u7684\u8FD9\u51E0\u5341\u5E74\u4E4B\u4E2D\uFF0C\u5B87\u6587\u6C0F\u84B8\u84B8\u65E5\u4E0A\uFF0C\u9AD8\u6C0F\u65E5\u8584\u897F\u5C71\u3002</p><p>\u6211\u4EEC\u540E\u4E16\u5728\u770B\u5386\u53F2\u65F6\uFF0C\u56E0\u4E3A\u5DF2\u7ECF\u9884\u77E5\u4E86\u7ED3\u679C\uFF0C\u6240\u4EE5\u4E0D\u81EA\u89C9\u7684\u4F1A\u628A\u5386\u53F2\u7684\u53D1\u5C55\u8BA4\u4E3A\u662F\u968F\u7740\u65F6\u95F4\u63A8\u79FB\u800C\u4F9D\u6B21\u53D1\u751F\u7684\u5FC5\u7136\u4E8B\u4EF6\u3002\u4F46\u4E8B\u5B9E\u4E0A\uFF0C<strong>\u5728\u771F\u6B63\u7684\u5386\u53F2\u4E2D\uFF0C\u5E76\u4E0D\u5B58\u5728\u8FD9\u6837\u4E00\u4E2A\u4E25\u683C\u7684\u56E0\u679C\u5173\u7CFB</strong>\uFF0C\u5F88\u591A\u65F6\u5019\uFF0C<strong>\u4E00\u4E2A<code>\u5076\u7136\u53D8\u91CF</code>\u7684\u51FA\u73B0\uFF0C\u5C31\u4F1A\u5BFC\u81F4\u540E\u6765\u7684\u4E8B\u53D1\u751F\u98A0\u8986\u6027\u6539\u53D8\u3002</strong></p>',63),n=[c];function s(d,g){return o(),e("div",null,n)}const i=p(r,[["render",s],["__file","70.html.vue"]]);export{i as default};
