import{_ as o}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as p,c as e,f as c}from"./app.f4897be6.js";const t={},d=c('<h1 id="\u9648\u5E86\u4E4B" tabindex="-1"><a class="header-anchor" href="#\u9648\u5E86\u4E4B" aria-hidden="true">#</a> \u9648\u5E86\u4E4B</h1><blockquote><p>\u5E1D\u4EE5\u9B4F\u5317\u6D77\u738B\u98A2\u4E3A\u9B4F\u738B\uFF0C\u9063\u4E1C\u5BAB\u76F4\u9601\u5C06\u519B\u9648\u5E86\u4E4B\u5C06\u5175\u9001\u4E4B\u8FD8\u5317\u3002</p></blockquote><p><strong>\u300A\u8D44\u6CBB\u901A\u9274\xB7\u5377\u4E00\u4E94\u4E8C\xB7\u6881\u7EAA\u516B\u300B</strong></p><p>\u5927\u7EA6\u5199\u5230\u4E86\u516C\u5143510\u5E74\u524D\uFF0C\u800C\u9648\u5E86\u4E4B\u5317\u4F10\uFF0C\u662F\u53D1\u751F\u5728\u516C\u5143528\u5E74\u7684\u4E8B\u3002\u4E3A\u4EC0\u4E48\u4E2D\u95F4\u4E00\u4E0B\u5B50\u8DF3\u4E86\u8FD9\u4E48\u5927\u4E00\u6BB5\uFF1F\u56E0\u4E3A\u5357\u671D\u5728\u8FD9\u8FD1\u4E8C\u5341\u5E74\u7684\u5C81\u6708\u91CC\uFF0C\u4E5F\u7B97\u5F97\u4E0A\u653F\u6CBB\u6E05\u5E73\uFF0C\u56FD\u6CF0\u6C11\u5B89\uFF0C\u5B9E\u5728\u6CA1\u6709\u4EC0\u4E48\u503C\u5F97\u8BF4\u7684\u3002</p><p>\u53CD\u5012\u662F<code>\u5317\u9B4F</code>\uFF0C\u8FD9\u4E9B\u5E74\u91CC\u4E00\u76F4\u90FD\u8FC7\u5F97\u4E0D\u592A\u5E73\u3002\u81EA<code>\u5BA3\u6B66\u5E1D</code>\u6B7B\u540E\uFF0C<code>\u80E1\u592A\u540E</code>\u6267\u653F\uFF0C\u5341\u51E0\u5E74\u95F4\uFF0C\u671D\u5EF7\u52A8\u8361\u4E0D\u5B89\uFF0C\u8FED\u906D\u5927\u4E71\uFF1A\u5148\u662F\u653F\u6CBB\u660F\u6697\uFF0C\u671D\u5802\u4E0A\u52FE\u5FC3\u6597\u89D2\uFF1B\u968F\u5373\uFF0C\u53C8\u7206\u53D1\u4E86\u516D\u9547\u4E4B\u4E71\uFF0C\u671D\u5EF7\u5E94\u5BF9\u5931\u5F53\uFF0C\u904D\u5730\u72FC\u70DF\u3002</p><p>\u6700\u7EC8\uFF0C\u516D\u9547\u4E4B\u4E71\u88AB<code>\u5C14\u6731\u8363</code>\u5E73\u5B9A\u3002\u4F46\u5C14\u6731\u8363\u7684\u51FA\u73B0\uFF0C\u52A0\u5267\u7684\u5374\u662F<code>\u5B5D\u660E\u5E1D</code>\u548C\u80E1\u592A\u540E\u4E4B\u95F4\u7684\u77DB\u76FE\u3002\u80E1\u592A\u540E\u56E0\u4E3A\u8F7B\u4FE1\u8C17\u8A00\uFF0C\u7ADF\u7136\u6BD2\u6740\u4E86\u4EB2\u5B50\u5B5D\u660E\u5E1D\uFF0C\u4F46\u8FD9\u5DF2\u7ECF\u65E0\u6CD5\u963B\u6B62\u5C14\u6731\u8363\u4ECE\u8086\u5DDE\u5357\u4E0B\u3002\u5728\u8FDB\u5165\u6D1B\u9633\u540E\uFF0C<strong>\u5C14\u6731\u8363\u6740\u80E1\u592A\u540E\u53CA\u4EE5\u4E0B\u6D1B\u9633\u516C\u537F\u4E24\u5343\u4F59\u4EBA\uFF0C\u662F\u4E3A<code>\u6CB3\u9634\u4E4B\u53D8</code></strong>\u3002</p><p>\u4E5F\u6B63\u662F\u5317\u9B4F\u7684\u8FD9\u4E00\u573A\u52A8\u4E71\uFF0C\u6700\u7EC8\u6210\u5C31\u4E86\u9648\u5E86\u4E4B\u7684\u5A01\u540D\u3002</p><p>\u516C\u5143528\u5E74\uFF0C\u5728\u5C14\u6731\u8363\u5236\u9020\u6CB3\u9634\u4E4B\u53D8\u4EE5\u540E\uFF0C<code>\u5317\u9B4F</code>\u5B97\u5BA4<code>\u5143\u98A2</code>\u9009\u62E9\u5357\u9003\uFF0C\u6295\u5954<code>\u8427\u6881</code>\u3002\u8427\u6881\u6B64\u65F6\u5F00\u56FD\u5DF2\u7ECF\u63A5\u8FD1\u4E09\u5341\u5E74\uFF0C<code>\u6881\u6B66\u5E1D\u8427\u884D</code>\u63A5\u53D7\u4E86\u5143\u98A2\u7684\u6765\u6295\uFF0C\u5E76\u5C01\u5176\u4E3A\u9B4F\u738B\u3002</p><p>\u4E0D\u4E45\u4EE5\u540E\uFF0C\u5143\u98A2\u89C1\u5317\u65B9\u5927\u4E71\uFF0C\u4E0A\u8868\u8427\u884D\uFF0C\u8BF7\u6C42\u5317\u5F52\u3002<strong>\u8427\u884D\u4E8E\u662F\u4FBF\u547D\u9648\u5E86\u4E4B\u9886\u5175<code>\u4E03\u5343</code>\uFF0C\u62A4\u9001\u5143\u98A2\u5317\u5F52\u79F0\u5E1D\u3002</strong></p><p>\u4E3A\u4EC0\u4E48\u662F\u9648\u5E86\u4E4B\u53BB\uFF1F</p><p>\u8FD9\u503C\u5F97\u8BF4\u9053\u8BF4\u9053\u3002</p><p>\u9648\u5E86\u4E4B\u51FA\u8EAB\u5BD2\u95E8\uFF0C\u6700\u65E9\u662F\u8427\u884D\u7684\u4F8D\u4ECE\u3002\u4ED6\u5F00\u59CB\u4E0A\u6218\u573A\u7684\u65F6\u5019\uFF0C\u5DF2\u7ECF40\u5C81\u4E86\u2014\u2014\u6240\u4EE5\u5E76\u4E0D\u662F\u767D\u8863\u767D\u888D\u7684\u7FE9\u7FE9\u5C11\u5E74\u5F62\u8C61\uFF0C\u800C\u662F\u4EBA\u5230\u4E2D\u5E74\u7684\u5927\u53D4\u8DEF\u7EBF\u3002</p><p>\u90A3\u4E00\u5E74\uFF0C\u9648\u5E86\u4E4B\u968F\u7740\u8C6B\u7AE0\u738B<code>\u8427\u7EFC</code>\u5165\u9547\u5F90\u5DDE\uFF0C\u7ED3\u679C\u8427\u7EFC\u7ADF\u7136\u6295\u964D\u4E86\u5317\u9B4F\u3002\u603B\u4E4B\uFF0C\u56E0\u6B64\u4EA7\u751F\u4E86\u4E00\u7CFB\u5217\u4E00\u8A00\u96BE\u5C3D\u7684\u4E8B\u6545\uFF0C\u8427\u6881\u4E00\u65B9\u906D\u5230\u5927\u8D25\uFF0C\u4F46\u9648\u5E86\u4E4B\u6240\u90E8\u6DF1\u9677\u5176\u4E2D\u5374\u505A\u5230\u4E86\u72EC\u5584\u5176\u8EAB\u3002</p><p>\u6B64\u540E\uFF0C\u9648\u5E86\u4E4B\u53C8\u968F\u519B\u51FA\u5F81\uFF0C\u9887\u6709\u4E00\u4E9B\u6218\u7EE9\uFF0C\u5E76\u56E0\u6B64\u5C01\u4FAF\u3002</p><p>\u4E5F\u5C31\u662F\u8BF4\uFF0C\u9648\u5E86\u4E4B\u5728\u6B63\u5F0F\u767B\u4E0A\u5386\u53F2\u7684\u821E\u53F0\u524D\uFF0C\u5DF2\u7ECF\u4E0A\u8FC7\u6218\u573A\uFF0C\u5E76\u4E14\u8FD8\u7ACB\u4E86\u4E9B\u4E0D\u5927\u4E0D\u5C0F\u7684\u529F\u52CB\u3002\u8427\u884D\u5982\u4ECA\u6D3E\u4ED6\u53BB\uFF0C\u770B\u4E2D\u7684\u53EF\u80FD\u4E5F\u5C31\u662F\u4ED6\u7684\u8FD9\u4E9B\u80FD\u529B\u3002\u8BF4\u7A7F\u4E86\uFF0C\u8FD9\u5E76\u4E0D\u662F\u4E2A\u96C5\u6D3B\uFF0C\u5E26\u7740\u4E03\u5343\u4EBA\u5C31\u60F3\u53BB\u5317\u9B4F\u5F53\u7687\u5E1D\uFF0C\u8FD9\u4E2A\u526F\u672C\u600E\u4E48\u770B\u90FD\u6CA1\u5565\u8BDA\u610F\u3002\u5373\u4FBF\u5317\u9B4F\u5982\u4ECA\u5DF2\u7ECF\u5185\u4E71\u8870\u5F31\uFF0C\u4F46\u7626\u6B7B\u7684\u9A86\u9A7C\u6BD4\u9A6C\u5927\uFF0C\u8FD9\u4E03\u5343\u4EBA\u4EBA\u5F88\u6709\u53EF\u80FD\u4E00\u53BB\u5C31\u662F\u7F8A\u5165\u864E\u53E3\uFF0C\u6709\u53BB\u65E0\u8FD4\u3002</p><p>\u8427\u884D\u5185\u5FC3\u7684\u60F3\u6CD5\u4F30\u8BA1\u662F\uFF0C\u54CE\uFF0C\u8FD9\u4E03\u5343\u4EBA\u5C31\u6743\u5F53\u662F\u6253\u72D7\u7684\u8089\u5305\u5B50\u4E86\u5427\u3002</p><p>\u4F46\u8D85\u4E4E\u6240\u6709\u4EBA\u7684\u60F3\u8C61\uFF0C\u9648\u5E86\u4E4B\u5728\u8FD9\u4E00\u6218\u4E2D\u7EFD\u653E\u51FA\u4E86\u8000\u773C\u7684\u5149\u8F89\uFF0C\u4E0D\u4EC5\u6311\u52A8\u4E86\u5929\u4E0B\u5C40\u52BF\uFF0C\u8FD8\u56E0\u8F89\u714C\u7684\u6218\u7EE9\u800C\u5149\u8000\u5386\u53F2\uFF0C\u8BA9\u540E\u4EBA\u8D5E\u53F9\u4E0D\u5DF2\u3002</p><p>\u5982\u679C\u8BF4\u5386\u53F2\u6709\u5267\u672C\uFF0C\u90A3\u4E48\u9648\u5E86\u4E4B\u5C31\u662F\u5728\u7F16\u5267\u4E0D\u66FE\u7559\u610F\u7684\u7A7A\u767D\u9875\u4E0A\uFF0C\u628A\u539F\u672C\u4E0D\u8D77\u773C\u7684\u526F\u7EBF\u8D70\u51FA\u4E86\u4E3B\u7EBF\u7684\u5F00\u6302\u611F\u3002</p><p>\u9648\u5E86\u4E4B\u62A4\u9001\u5143\u98A2\u5317\u5F52\uFF0C\u5143\u98A2\u79F0\u5E1D\u3002\u6B64\u540E\uFF0C<strong>\u5143\u98A2\u4E0E\u9648\u5E86\u4E4B\u4E00\u8DEF\u5317\u4E0A\uFF0C\u6A2A\u626B\u4E2D\u539F\uFF0C\u6700\u7EC8\u8FDB\u5165<code>\u6D1B\u9633</code></strong>\u3002</p><p>\u5728\u6B64\u671F\u95F4\uFF0C\u88AB\u8BB0\u5F55\u8FDB\u53F2\u4E66\u7684\u6218\u5F79\u4E00\u5171\u6709\u8FD9\u4E48\u51E0\u573A\u3002</p><p><strong>\u7B2C\u4E00\u573A\u5927\u6218\u53D1\u751F\u5728\u7762\u9633</strong>\u3002\u5F53\u65F6\uFF0C\u9B4F\u5C06<code>\u4E18\u5927\u5343</code>\u7387\u4F17<code>\u4E03\u4E07</code>\u9547\u5B88\uFF0C\u5E76\u7B51\u4E5D\u57CE\u62B5\u5FA1\u6881\u519B\uFF0C\u4F46\u9648\u5E86\u4E4B\u4E00\u65E5\u4E4B\u5185\u653B\u9677\u4E09\u57CE\uFF0C\u4E18\u5927\u5343\u6295\u964D\u3002</p><p><strong>\u7B2C\u4E8C\u6218\u662F\u5728\u8003\u57CE</strong>\u3002\u53F2\u8F7D\uFF0C\u9B4F\u6D4E\u9634\u738B<code>\u5143\u6656\u4E1A</code>\u9886\u5175<code>\u4E24\u4E07</code>\uFF0C\u88AB\u9648\u5E86\u4E4B\u5168\u6B7C\u3002</p><p><strong>\u7B2C\u4E09\u6218\u5728\u8365\u9633\uFF0C\u8FD9\u4E5F\u662F\u53F2\u6599\u8BB0\u5F55\u4E2D\u53CC\u65B9\u5B9E\u529B\u5BF9\u6BD4\u6700\u4E3A\u60AC\u6B8A\u7684\u4E00\u6218</strong>\u3002\u6218\u524D\uFF0C\u9648\u5E86\u4E4B\u662F\u8FD9\u4E48\u6FC0\u52B1\u58EB\u5352\u7684\uFF1A\u654C\u519B\u6709<code>\u4E09\u5341\u4E07</code>\uFF0C\u800C\u6211\u519B\u53EA\u6709<code>\u4E03\u5343\u4EBA</code>\uFF0C\u6240\u4EE5\u6211\u4EEC\u4E0D\u80FD\u8DDF\u654C\u4EBA\u7684\u9A91\u5175\u5728\u5E73\u539F\u91CE\u6218\uFF0C\u53EA\u6709\u653B\u7834\u4ED6\u4EEC\u7684\u57CE\u5792\u624D\u80FD\u6C42\u751F\u3002</p><p>\u5728\u6B64\u6218\uFF0C\u9648\u5E86\u4E4B\u4EB2\u81EA\u64C2\u9F13\u3002\u53EA\u4E00\u901A\u9F13\u6BD5\uFF0C\u6881\u519B\u4FBF\u767B\u4E0A\u4E86\u57CE\u5899\u3002</p><p>\u4E0D\u4E45\u540E\uFF0C\u5317\u9B4F\u6743\u81E3<code>\u5143\u5929\u7A46</code>\u9886\u5175\u56F4\u57CE\uFF0C\u88AB\u9648\u5E86\u4E4B\u5728\u57CE\u4E0B\u51FB\u7834\uFF0C\u5E76\u653B\u7834\u864E\u7262\u5173\u3002</p><p>\u864E\u7262\u5173\u65E2\u7834\uFF0C\u5175\u950B\u6240\u6307\u4E4B\u5904\uFF0C\u4FBF\u662F\u6D1B\u9633\u4E86\u3002\u6D1B\u9633\u57CE\u4E2D\u7684\u5317\u9B4F<code>\u5B5D\u5E84\u5E1D\u5143\u5B50\u6538</code>\u5728\u5927\u81E3\u7C07\u62E5\u4E4B\u4E0B\uFF0C\u4ED3\u7687\u6E21\u9EC4\u6CB3\u907F\u654C\u3002\u5143\u98A2\u8FDB\u5165\u6D1B\u9633\u3002</p><p><strong>\u7B2C\u56DB\u6218\u53D1\u751F\u5728\u5927\u6881</strong>\u3002\u9648\u5E86\u4E4B\u8FDB\u5165\u6D1B\u9633\u540E\uFF0C\u5317\u9B4F\u53CD\u653B\uFF0C\u518D\u6B21\u653B\u514B\u5927\u6881\u3001\u864E\u7262\u5173\u3002\u9648\u5E86\u4E4B\u4E8E\u662F\u7387\u90E8\u53CD\u51FB\uFF0C\u5728\u5927\u6881\u51FB\u8D25\u5143\u5929\u7A46\uFF0C\u5E76\u6536\u964D\u864E\u7262\u5173\u5B88\u5C06\u3002</p><blockquote><p><strong>\u53F2\u8F7D\uFF0C</strong></p></blockquote><blockquote><p><strong>\u9648\u5E86\u4E4B\u6240\u90E8\u4ECE\u94DA\u53BF\u51FA\u53D1\u81F3\u6D1B\u9633\uFF0C</strong></p></blockquote><blockquote><p><strong>\u524D\u540E\u4F5C\u6218<code>47</code>\u6B21\uFF0C</strong></p></blockquote><blockquote><p><strong>\u653B\u57CE<code>32</code>\u5EA7\uFF0C</strong></p></blockquote><blockquote><p><strong><code>\u6240\u8FC7\u7686\u7834</code>\u3002</strong></p></blockquote><p>\u53EF\u5149\u7206\u53D1\u7684\u65F6\u5019\u6709\u591A\u70AB\u76EE\uFF0C\u8F6C\u77AC\u6563\u706D\u7684\u65F6\u5019\u4E5F\u5C31\u6709\u591A\u9EEF\u6DE1\u3002\u5728\u5143\u98A2\u8FDB\u5165\u6D1B\u9633\u4EE5\u540E\uFF0C\u5317\u9B4F\u5F00\u59CB\u8FDB\u884C\u7EC4\u7EC7\u529B\u91CF\u53CD\u51FB\u3002<strong>\u9648\u5E86\u4E4B\u8C0B\u6C42\u4E0E\u9B4F\u519B\u4E3B\u529B\u3001\u5C14\u6731\u8363\u5927\u519B\u51B3\u6218\uFF0C\u4F46\u6700\u7EC8\u5374\u88AB\u51FB\u8D25\u3002\u4E0D\u4E45\u540E\uFF0C\u5143\u98A2\u906D\u64D2\u88AB\u6740\u3002</strong></p><p>\u9648\u5E86\u4E4B\u90E8\u56DE\u64A4\u8427\u6881\uFF0C\u5728\u9014\u4E2D\u906D\u9047\u5C71\u6D2A\uFF0C\u5168\u519B\u8986\u6CA1\u3002\u9648\u5E86\u4E4B\u5316\u88C5\u4E3A\u548C\u5C1A\uFF0C\u53EA\u8EAB\u9003\u56DE\u8427\u6881\u3002</p><p>\u56DE\u5230\u5357\u65B9\u540E\uFF0C\u8427\u884D\u5BF9\u9648\u5E86\u4E4B\u6B64\u884C\u5927\u52A0\u8D5E\u8D4F\u3002\u6B64\u540E\uFF0C\u9648\u5E86\u4E4B\u9547\u5B88\u8FB9\u7586\uFF0C\u76F4\u81F3\u53BB\u4E16\uFF0C\u4EAB\u5E7454\u5C81\u3002</p><p>\u8D5E\u540C\u8005\u8BA4\u4E3A\uFF0C\u9648\u5E86\u4E4B\u5317\u4F10\u6240\u8FC7\u7686\u7834\uFF0C\u8FD9\u662F\u7B2C\u4E00\u6D41\u7684\u53E4\u4EE3\u540D\u5C06\u7684\u6C34\u5E73\uFF1B\u4F46\u53CD\u5BF9\u8005\u5374\u8BA4\u4E3A\uFF0C\u9648\u5E86\u4E4B\u7684\u529F\u7EE9\uFF0C\u5176\u5B9E\u662F\u300A\u6881\u4E66\u300B\u6240\u4F2A\u9020\u7684\u3002</p><p>\u6240\u4EE5\uFF0C\u7A76\u7ADF\u5E94\u8BE5\u5982\u4F55\u6765\u8BC4\u4EF7\u5462\uFF1F</p><p>\u9996\u5148\uFF0C\u68B3\u7406\u9648\u5E86\u4E4B\u7684\u6574\u4E2A\u6218\u6597\u4EBA\u751F\uFF0C\u4F60\u4F1A\u53D1\u73B0\u4ED6\u5176\u5B9E\u5E76\u4E0D\u5982\u4F20\u8BF4\u4E2D\u4E00\u822C\u767E\u6218\u767E\u80DC\u3002<strong>\u4ED6\u4E0E\u5C14\u6731\u8363\u7684\u51B3\u6218\uFF0C\u4E8B\u5B9E\u4E0A\u5C31\u662F\u5931\u8D25\u4E86\u7684\u3002</strong></p><p>\u867D\u7136\u5728\u8FDB\u5165\u6D1B\u9633\u4E4B\u524D\uFF0C\u9648\u5E86\u4E4B\u6240\u8FC7\u7686\u7834\u3002\u4F46\u8FDB\u5165\u6D1B\u9633\u4EE5\u540E\uFF0C<strong>\u968F\u7740\u5317\u9B4F\u628A\u5168\u90E8\u7684\u6CE8\u610F\u529B\u548C\u5B9E\u529B\u90FD\u8C03\u8F6C\u8FC7\u6765\uFF0C\u9648\u5E86\u4E4B\u5176\u5B9E\u624D\u8FCE\u6765\u771F\u6B63\u7684\u8003\u9A8C\u3002</strong></p><p>\u4E5F\u5C31\u662F\u8BF4\uFF0C\u4E4B\u524D\u7684\u6218\u6597\u90FD\u662F\u5F00\u80C3\u83DC\u3002\u4E0E\u5C14\u6731\u8363\u5927\u519B\u7684\u51B3\u6218\uFF0C\u624D\u662F\u771F\u6B63\u7684\u91CD\u5934\u620F\u3002</p><p>\u5F53\u65F6\uFF0C\u9648\u5E86\u4E4B\u5E26\u5175\u6E21\u8FC7\u9EC4\u6CB3\uFF0C\u9A7B\u624E\u4E8E\u9EC4\u6CB3\u5317\u5CB8\uFF0C\u4EE5\u653B\u4E3A\u5B88\uFF0C\u4E3B\u52A8\u5BFB\u6C42\u4E0E\u5C14\u6731\u8363\u5927\u519B\u51B3\u6218\u3002</p><p>\u4F46\u5C14\u6731\u8363\u540C\u6837\u4E5F\u662F\u4E45\u7ECF\u6218\u4E89\u7684\u540D\u5C06\uFF0C\u800C\u4E14\u4ED6\u9EBE\u4E0B\u6B64\u523B\u6709\u6570\u5341\u4E07\u5927\u519B\uFF0C\u5B9E\u529B\u8FDC\u80DC\u4E8E\u9648\u5E86\u4E4B\u3002\u4ED6\u5E76\u6CA1\u6709\u6309\u7167\u9648\u5E86\u4E4B\u7684\u610F\u56FE\u4E0E\u4E4B\u51B3\u6218\uFF0C\u800C\u662F\u6D3E\u504F\u5E08\u7275\u5236\u9648\u5E86\u4E4B\uFF0C\u81EA\u5DF1\u5219\u4EB2\u7387\u4E3B\u529B\u6E21\u6CB3\uFF0C\u76F4\u653B\u6D1B\u9633\u3002</p><p>\u5143\u98A2\u6B64\u524D\u867D\u7136\u5DF2\u7ECF\u805A\u96C6\u4E86\u5341\u6570\u4E07\u519B\u961F\uFF0C\u4F46\u672A\u7ECF\u6218\u9635\uFF0C\u800C\u4E14\u65F6\u95F4\u5C1A\u77ED\uFF0C\u51E0\u4E4E\u6CA1\u6709\u4EFB\u4F55\u51DD\u805A\u529B\uFF0C\u6218\u6597\u529B\u8FDC\u8FDC\u6BD4\u4E0D\u4E0A\u5C14\u6731\u8363\u7684\u7CBE\u9510\u3002\u56E0\u6B64\u5728\u5C14\u6731\u8363\u4E3B\u529B\u6E21\u6CB3\u4E4B\u540E\uFF0C\u5143\u98A2\u5927\u519B\u51E0\u4E4E\u662F\u4E00\u89E6\u5373\u6E83\uFF0C\u5143\u98A2\u53EA\u80FD\u9003\u51FA\u6D1B\u9633\uFF0C\u4E0D\u4E45\u4FBF\u88AB\u64D2\u6740\u3002</p><p>\u800C\u6B64\u65F6\u8EAB\u5728\u5317\u5CB8\u7684\u9648\u5E86\u4E4B\uFF0C\u56E0\u4E3A\u5931\u53BB\u4E86\u540E\u65B9\u652F\u63F4\uFF0C\u4E5F\u88AB\u51FB\u8D25\u3002\u66F4\u4F55\u51B5\uFF0C\u968F\u7740\u5143\u98A2\u6E83\u8D25\uFF0C\u6881\u519B\u5728\u5317\u9B4F\u7684\u5B58\u5728\u5DF2\u7ECF\u53D8\u5F97\u6BEB\u65E0\u610F\u4E49\u3002\u9648\u5E86\u4E4B\u90E8\u4E8E\u662F\u5F00\u59CB\u5357\u64A4\uFF0C\u4F46\u5728\u9014\u4E2D\u4E0D\u5E78\u906D\u9047\u5C71\u6D2A\uFF0C\u5168\u519B\u8986\u6CA1\u3002</p><p>\u4E5F\u5C31\u662F\u8BF4\uFF0C\u9648\u5E86\u4E4B\u56FA\u7136\u6709\u8F89\u714C\u4E00\u6218\uFF0C\u4F46\u8FD9\u6070\u5982\u70DF\u82B1\uFF0C\u7EDA\u70C2\u8FC7\u540E\uFF0C\u4E86\u65E0\u8E2A\u8FF9\u3002<strong>\u4ED6\u7684\u5317\u4F10\u867D\u7136\u6405\u4E71\u4E86\u5317\u65B9\u5C40\u52BF\uFF0C\u4F46\u65E2\u6CA1\u6709\u5E2E\u52A9\u5143\u98A2\u5EFA\u7ACB\u5E1D\u4E1A\uFF0C\u4E5F\u65E0\u52A9\u4E8E\u8427\u6881\u5728\u5317\u65B9\u7684\u9886\u571F\u6269\u5F20\u3002</strong></p><p>\u800C\u4E14\uFF0C\u4F60\u53EA\u8981\u518D\u4ED4\u7EC6\u601D\u7D22\uFF0C\u5C31\u4F1A\u53D1\u73B0\u9648\u5E86\u4E4B\u7684\u5927\u80DC\uFF0C\u5176\u5B9E\u6709\u592A\u5927\u7684\u201C\u6C34\u5206\u201D\u2014\u2014\u6682\u4E14\u4E0D\u8BF4\u300A\u6881\u4E66\u300B\u5728\u4F24\u4EA1\u6570\u5B57\u4E0A\u7684\u9020\u5047\uFF0C<strong>\u5C31\u5355\u8BF4\u9648\u5E86\u4E4B\u653B\u514B\u7684\u90A332\u5EA7\u57CE\u6C60\uFF0C\u91CC\u9762\u6709\u591A\u5C11\u662F\u6253\u4E0B\u6765\u7684\uFF1F</strong></p><p>\u5BF9\u7EDD\u5927\u591A\u6570\u9762\u5BF9\u9648\u5E86\u4E4B\u7684\u5317\u9B4F\u5B98\u5458\u800C\u8A00\uFF0C<strong>\u4ED6\u4EEC\u60F3\u7684\u5E76\u4E0D\u662F\u5FA1\u654C\uFF0C\u800C\u662F<code>\u7AD9\u961F</code></strong>\u3002\u9648\u5E86\u4E4B\u867D\u7136\u662F\u4E2A\u6881\u4EBA\uFF0C\u4F46\u4ED6\u80CC\u540E\u7AD9\u7740\u7684\uFF0C\u53EF\u662F\u5317\u9B4F\u5B97\u5BA4\u3001\u5317\u6D77\u738B\u5143\u98A2\u554A\uFF01</p><p>\u6362\u4E2A\u89D2\u5EA6\u6765\u8BF4\uFF0C<strong>\u8FD9\u5176\u5B9E\u662F\u5317\u6D77\u738B\u5143\u98A2\u548C\u5B5D\u5E84\u5E1D\u5143\u5B50\u6538\u4E4B\u95F4\u7684\u5E1D\u4F4D\u4E4B\u4E89</strong>\u3002\u9648\u5E86\u4E4B\u5982\u679C\u8D25\u4E86\uFF0C\u90A3\u4E48\u5143\u98A2\u5C31\u662F\u5B5D\u5E84\u5E1D\u7684\u4E71\u81E3\u8D3C\u5B50\u3002\u4F46\u9648\u5E86\u4E4B\u5982\u679C\u51FB\u8D25\u4E86\u5C14\u6731\u8363\uFF0C\u90A3\u5143\u98A2\u5C31\u4F1A\u6210\u4E3A\u5317\u9B4F\u7684\u65B0\u7687\u5E1D\u3002</p><p>\u800C\u4E14\u5728\u6B64\u65F6\uFF0C\u5143\u98A2\u7684\u8D62\u9762\u53EF\u80FD\u8FD8\u8981\u66F4\u5927\u4E00\u70B9\u3002</p><p>\u6240\u4EE5\uFF0C\u5927\u5BB6\u4E3A\u4EC0\u4E48\u8981\u62B5\u6297\u5462\uFF1F</p><p>\u66F4\u5927\u7684\u53EF\u80FD\uFF0C\u53EA\u4F1A\u662F\u8FD9\u4E9B\u57CE\u6C60\u91CC\u7684\u7EDD\u5927\u591A\u6570\uFF0C\u90FD\u662F\u671B\u98CE\u800C\u964D\u7684\u3002<strong>\u9648\u5E86\u4E4B\u5317\u4F10\u9762\u4E34\u7684\u771F\u5B9E\u5C40\u9762\uFF0C\u5176\u5B9E\u662F\u4ED6\u5728\u5317\u9B4F\u56FD\u5185\u5927\u4E71\u4E4B\u9645\uFF0C\u8D81\u7740\u5317\u9B4F\u519B\u961F\u4E3B\u529B\u65E0\u6687\u987E\u53CA\u800C\u5927\u519B\u7A81\u8FDB\uFF0C\u4EE5\u8FC5\u96F7\u4E0D\u53CA\u63A9\u8033\u4E4B\u52BF\u5360\u9886\u4E86\u6D1B\u9633</strong>\u3002</p><p>\u8FD9\u4E2A\u65F6\u5019\uFF0C\u5317\u9B4F\u7684\u519B\u961F\u4E3B\u529B\u5728\u5E72\u561B\u5462\uFF1F\u4E00\u90E8\u5206\u662F\u5C14\u6731\u8363\u7684\u5AE1\u7CFB\u90E8\u961F\uFF0C\u4ED6\u4EEC\u6B63\u8DDF\u7740\u5C14\u6731\u8363\u5317\u4F10\uFF1B\u53E6\u4E00\u90E8\u5206\u5219\u88AB\u5143\u5929\u7A46\u5E26\u7740\uFF0C\u53BB\u4E86\u5982\u4ECA\u7684\u5C71\u4E1C\u4E00\u5E26\u5E73\u53DB\u3002</p><p>\u4E8B\u5B9E\u4E0A\uFF0C\u5317\u9B4F\u671D\u5802\u5728\u4E4B\u524D\u5C31\u6709\u8FC7\u8BA8\u8BBA\uFF1A\u662F\u5E94\u8BE5\u4F18\u5148\u653B\u51FB\u9648\u5E86\u4E4B\u7684\u5317\u4F10\u90E8\u961F\uFF0C\u8FD8\u662F\u5148\u5E73\u5B9A\u53DB\u4E71\uFF1F\u6700\u7EC8\uFF0C\u671D\u5EF7\u7684\u51B3\u7B56\u662F\u5E73\u53DB\u3002\u56E0\u4E3A\u53DB\u519B\u58F0\u52BF\u6D69\u5927\uFF0C\u53F2\u8F7D\u6709\u51E0\u5341\u4E07\u4EBA\u4E4B\u591A\uFF1B\u800C\u9648\u5E86\u4E4B\u53EA\u5E26\u7740\u4E03\u5343\u4EBA\u7684\u90E8\u961F\uFF0C\u7528\u4E0D\u7740\u592A\u8FC7\u91CD\u89C6\u3002</p><p>\u5317\u9B4F\u671D\u5EF7\u6CA1\u6709\u60F3\u5230\u7684\uFF0C\u53EA\u4E0D\u8FC7\u662F\u9648\u5E86\u4E4B\u8FD9\u533A\u533A\u51E0\u5343\u4EBA\u7684\u90E8\u961F\uFF0C\u7ADF\u7136\u80FD\u4E00\u8DEF\u6740\u5230\u6D1B\u9633\uFF0C\u52A8\u6447\u4E86\u6839\u672C\u3002</p><p>\u8FD9\u4E2A\u6218\u4E89\u4E4B\u5916\u7684\u653F\u6CBB\u56E0\u7D20\uFF0C\u53EF\u80FD\u624D\u662F\u9648\u5E86\u4E4B\u53D6\u5F97\u6218\u679C\u7684\u6700\u5927\u539F\u56E0\u3002</p><p>\u6211\u4EEC\u5728\u601D\u8003\u9648\u5E86\u4E4B\u5317\u4F10\u5927\u80DC\u7684\u539F\u56E0\u65F6\uFF0C\u4E0D\u5F97\u4E0D\u91CD\u89C6\u8FD9\u4E00\u70B9\uFF1A<strong>\u9762\u5BF9\u7684\u654C\u4EBA\u592A\u5F31\u592A\u4E0D\u4E13\u5FC3\uFF0C\u8FD9\u662F\u9648\u5E86\u4E4B\u80FD\u6709\u7EDD\u4E16\u6218\u529F\u7684\u4E00\u4E2A\u539F\u56E0\u3002\u751A\u81F3\u4E8E\uFF0C\u53EF\u80FD\u662F\u6700\u4E3B\u8981\u539F\u56E0\u3002</strong></p><p>\u4ED6\u7684\u6218\u529F\uFF0C\u662F\u5728\u7279\u5B9A\u65F6\u671F\u3001\u7279\u6B8A\u6761\u4EF6\u4E0B\u7684\u4EA7\u7269\u3002</p><p>\u6B64\u540E\uFF0C\u968F\u7740\u5317\u9B4F\u5F00\u59CB\u8BA4\u771F\u5BF9\u5F85\uFF0C\u5C14\u6731\u8363\u4EB2\u7387\u5927\u519B\u6765\u88AD\uFF0C\u9648\u5E86\u4E4B\u7684\u4E03\u5343\u4EBA\u6839\u672C\u5C31\u65E0\u4ECE\u65BD\u5C55\u3002</p><p><strong>\u5BA2\u89C2\u7684\u8BF4\uFF0C\u9648\u5E86\u4E4B\u5317\u4F10\u7684\u6210\u529F\uFF0C\u786E\u5B9E\u5C55\u793A\u4E86\u4ED6\u4E00\u4E2A\u4F5C\u4E3A\u540D\u5C06\u7684\u6F5C\u8D28\u3002\u4F46\u662F\uFF0C\u4ED6\u7684\u6218\u529F\u5374\u5E76\u4E0D\u8DB3\u4EE5\u8BC1\u660E\u4ED6\u80FD\u591F\u8DFB\u8EAB\u540D\u5C06\u4E4B\u5217\u3002</strong></p><p>\u6211\u4EEC\u4E0D\u59A8\u6362\u4E2A\u89D2\u5EA6\u6765\u770B\u5F85\u3002</p><p>\u5927\u5BB6\u5C3D\u53EF\u4EE5\u8BBE\u60F3\u4E00\u4E0B\uFF1A\u9648\u5E86\u4E4B\u7684\u5317\u4F10\u519B\u5982\u679C\u518D\u665A\u6765\u4E09\u5E74\uFF0C\u6B64\u65F6\u5317\u9B4F\u56E0\u5B5D\u5E84\u5E1D\u624B\u5203\u5C14\u6731\u8363\u5DF2\u7ECF\u5927\u4E71\u3002\u90A3\u4E48\uFF0C\u9648\u5E86\u4E4B\u53EA\u8981\u624B\u4E2D\u6709\u8DB3\u591F\u7684\u7B79\u7801\uFF0C\u5E94\u8BE5\u5C31\u80FD\u53D6\u5F97\u582A\u6BD4<code>\u6853\u6E29</code>\u3001<code>\u5218\u88D5</code>\u7684\u529F\u4E1A\u3002</p><p>\u53C8\u6216\u8005\uFF0C\u9648\u5E86\u4E4B\u7684\u5317\u4F10\u961F\u4F0D\u4E0D\u662F\u53EA\u6709\u533A\u533A\u4E03\u5343\u4EBA\uFF0C\u800C\u662F\u50CF\u4E8C\u5341\u5E74\u524D\u7684\u4E34\u5DDD\u738B<code>\u8427\u5B8F</code>\u4E00\u6837\uFF0C\u624B\u63E1\u51E0\u5341\u4E07\u4EBA\uFF0C\u90A3\u4E48\u6211\u4EEC\u4E5F\u5927\u53EF\u4EE5\u8BBE\u60F3\uFF1A\u5373\u4FBF\u5F7C\u65F6\u5317\u9B4F\u5C1A\u5C5E\u5F3A\u52BF\uFF0C\u4F46\u9648\u5E86\u4E4B\u5E94\u8BE5\u4E5F\u80FD\u53D6\u5F97\u4E0D\u4FD7\u7684\u6218\u7EE9\u3002</p><p>\u4F46\u95EE\u9898\u662F\uFF0C\u5386\u53F2\u6CA1\u6709\u5982\u679C\u3002</p><p>\u9648\u5E86\u4E4B\u6CA1\u6709\u8FC7\u90A3\u4E48\u5927\u7684\u6743\u529B\uFF0C\u4E5F\u6CA1\u6709\u8FC7\u90A3\u4E48\u597D\u7684\u673A\u4F1A\u3002\u4ED6\u7684\u821E\u53F0\u5B9E\u5728\u6709\u9650\uFF0C\u5E76\u6CA1\u6709\u7ED9\u4ED6\u592A\u591A\u7684\u65BD\u5C55\u7A7A\u95F4\u3002\u4ED6\u80FD\u591F\u5728\u8FD9\u8F6C\u77AC\u5373\u901D\u7684\u673A\u4F1A\u4E2D\u7559\u4E0B\u81EA\u5DF1\u7684\u8EAB\u5F71\uFF0C\u5DF2\u5C5E\u5E78\u8FD0\u3002</p><p>\u867D\u7136\u8FD9\u79CD\u5E78\u8FD0\u7684\u5E95\u8272\uFF0C\u53C8\u662F\u4E0D\u5E78\u7684\u3002</p><p><strong>\u4F46\u4EBA\u751F\u5927\u62B5\u5982\u6B64\u3002</strong></p><p>\u5982\u4ECA\u7F51\u7EDC\u4E0A\u7684\u9648\u5E86\u4E4B\uFF0C\u5176\u5F62\u8C61\u4E0E<code>\u7530\u4E2D\u82B3\u6811</code>\u5728\u300A\u5954\u6D41\u300B\u4E2D\u7684\u5851\u9020\u5927\u6709\u4EA4\u53E0\uFF1A\u767D\u888D\u767D\u9A6C\uFF0C\u5E26\u9886\u4E09\u767E\u767D\u9A6C\u9A91\u58EB\u4FBF\u5954\u5411\u949F\u79BB\uFF0C\u51E0\u4E4E\u9760\u7740\u4E00\u4EBA\u4E4B\u529B\u5C31\u51FB\u8D25\u4E86\u5317\u9B4F\u6D69\u6D69\u51E0\u5341\u4E07\u5927\u519B\uFF0C\u626D\u8F6C\u4E7E\u5764\u3002</p><p>\u8FD9\u79CD\u9C9C\u8863\u6012\u9A6C\u8E0F\u5C71\u6CB3\u7684\u5F62\u8C61\uFF0C\u4E0D\u5F97\u4E0D\u8BF4\uFF0C\u5B9E\u5728\u662F\u610F\u6C14\u98CE\u53D1\u3002</p><p>\u867D\u7136\u5728\u771F\u5B9E\u7684\u5386\u53F2\u4E2D\uFF0C\u9648\u5E86\u4E4B\u5E76\u6CA1\u6709\u53C2\u4E0E\u949F\u79BB\u4E4B\u6218\uFF1B\u8427\u6881\u7684\u83B7\u80DC\uFF0C\u4E5F\u4E0D\u662F\u9760\u7684\u4EE5\u5C11\u80DC\u591A\uFF0C\u800C\u662F\u5B9E\u529B\u76F8\u5F53\u4E4B\u4E0B\u7684\u5927\u80DC\u3002\u4F46\u4F60\u8FD8\u662F\u5FCD\u4E0D\u4F4F\u4F1A\u53BB\u76F8\u4FE1\u5C0F\u8BF4\u7684\u865A\u6784\uFF0C\u5728\u8111\u6D77\u91CC\u6784\u5EFA\u8D77\u90A3\u4E2A\u98CE\u4E91\u5377\u9EC4\u6C99\u7684\u753B\u9762\u3002</p><p><strong>\u56E0\u4E3A\u5728\u90A3\u4E2A\u573A\u666F\u91CC\uFF0C\u900F\u8FC7\u9648\u5E86\u4E4B\u6F47\u6D12\u7684\u8EAB\u5F71\uFF0C\u4F60\u770B\u5230\u7684\u662F\u81EA\u5DF1</strong>\uFF1A\u8C01\u5BB6\u7537\u513F\u4E0D\u5E0C\u671B\u6709\u8FD9\u6837\u4E00\u4E2A\u9AD8\u5149\u65F6\u5019\uFF0C\u8BA9\u539F\u672C\u5E73\u5EB8\u65E0\u5947\u7684\u4EBA\u751F\u8FF8\u53D1\u51FA\u7206\u88C2\u7684\u5149\u8292\u5462\uFF1F\u54EA\u6015\u53EA\u6709\u4E00\u77AC\uFF0C\u4F46\u53EA\u8981\u80FD\u8BA9\u522B\u4EBA\u770B\u5230\u81EA\u5DF1\u7684\u4E0E\u4F17\u4E0D\u540C\uFF0C\u80FD\u8BA9\u540E\u4E16\u7559\u4E0B\u8D5E\u53F9\u7684\u58F0\u58F0\u60CA\u547C\uFF0C\u5C31\u90FD\u662F\u503C\u5F97\u7684\u3002</p><p>\u4F60\u6211\u867D\u751F\u800C\u5E73\u51E1\uFF0C\u4F46\u90FD\u6709\u4E00\u9897\u4E0D\u7518\u5BC2\u5BDE\u7684\u5FC3\u3002</p><p>\u6240\u4EE5\uFF0C\u5373\u4FBF\u90A3\u662F\u865A\u6784\u7684\uFF0C\u53C8\u6709\u4F55\u59A8\uFF1F</p><p>\u867D\u7136\u90A3\u4E2A\u767D\u8272\u7684\u80CC\u5F71\u5DF2\u7ECF\u8FDC\u53BB\uFF0C\u4F46\u6211\u770B\u5728\u773C\u91CC\uFF0C\u770B\u5230\u7684\u5206\u660E\u662F\u6211\u81EA\u5DF1\u3002</p><p>\u65E2\u7136\u73B0\u5B9E\u5DF2\u7ECF\u5982\u6B64\u6B8B\u9177\uFF0C\u90A3\u8BA9\u6211\u505A\u4E00\u573A\u5927\u68A6\uFF0C\u53C8\u5982\u4F55\uFF1F</p>',74),r=[d];function n(s,g){return p(),e("div",null,r)}const u=o(t,[["render",n],["__file","77.html.vue"]]);export{u as default};
