import{_ as p}from"./_plugin-vue_export-helper.cdc0426e.js";import{o,c as t,f as e}from"./app.1218f0f6.js";const r={},n=e('<h1 id="\u8427\u7EDF" tabindex="-1"><a class="header-anchor" href="#\u8427\u7EDF" aria-hidden="true">#</a> \u8427\u7EDF</h1><blockquote><p>\u590F\uFF0C\u56DB\u6708\uFF0C\u4E59\u5DF3\uFF0C\u662D\u660E\u592A\u5B50\u7EDF\u5352\u3002\u592A\u5B50\u81EA\u52A0\u5143\u670D\uFF0C\u4E0A\u5373\u4F7F\u7701\u5F55\u671D\u653F\uFF0C\u767E\u53F8\u8FDB\u4E8B\uFF0C\u586B\u59D4\u4E8E\u524D\uFF0C\u592A\u5B50\u8FA8\u6790\u8BC8\u8C2C\uFF0C\u79CB\u6BEB\u5FC5\u7779\uFF0C\u4F46\u4EE4\u6539\u6B63\uFF0C\u4E0D\u52A0\u6848\u52BE\uFF0C\u5E73\u65AD\u6CD5\u72F1\uFF0C\u591A\u6240\u5168\u5BA5\uFF0C\u5BBD\u548C\u5BB9\u4F17\uFF0C\u559C\u6120\u4E0D\u5F62\u4E8E\u8272\u3002</p></blockquote><p><strong>\u300A\u8D44\u6CBB\u901A\u9274\xB7\u5377\u4E00\u4E94\u4E94\xB7\u6881\u7EAA\u5341\u4E00\u300B</strong></p><p>\u5982\u679C\u4E00\u5B9A\u8981\u8BF4<code>\u8427\u884D</code>\u6709\u4EC0\u4E48\u4EBA\u751F\u9057\u61BE\u7684\u8BDD\uFF0C\u90A3\u5C31\u662F\u6D3B\u5F97\u592A\u957F\u4E86\u3002</p><p>\u4ED6\u5982\u679C\u65E9\u6B7B\u51E0\u5E74\uFF0C\u4FAF\u666F\u4E4B\u4E71\u53EF\u80FD\u5C31\u4E0D\u4F1A\u53D1\u751F\uFF0C<code>\u8427\u6881</code>\u4E5F\u4E0D\u4F1A\u88AB\u706D\u56FD\uFF1B\u800C\u4ED6\u5982\u679C\u80FD\u65E9\u6B7B\u5341\u51E0\u5E74\uFF0C\u90A3\u4E48<code>\u662D\u660E\u592A\u5B50\u8427\u7EDF</code>\u53EF\u80FD\u5C31\u80FD\u987A\u5229\u7EE7\u4F4D\uFF0C\u8427\u6881\u7684\u56FD\u8FD0\u6216\u8BB8\u66F4\u4E3A\u957F\u4E45\u2014\u2014\u5728\u5386\u53F2\u4E0A\uFF0C\u8427\u7EDF\u4E00\u76F4\u90FD\u88AB\u8BA4\u4E3A\u6709\u6210\u4E3A\u4E00\u4EE3\u4EC1\u541B\u7684\u6F5C\u8D28\uFF0C\u53EA\u4E0D\u8FC7\u56E0\u4E3A\u65E9\u901D\uFF0C\u624D\u6CA1\u6709\u673A\u4F1A\u65BD\u5C55\u624D\u534E\u3002</p><p>\u8427\u7EDF\u7559\u5728\u53F2\u6599\u4E2D\u7684\u5F62\u8C61\uFF0C\u5F88\u6709\u53EF\u80FD\u53EA\u662F\u4E00\u526F\u5047\u9762\u3002</p><p><strong>\u9996\u5148\u662F\u5173\u4E8E\u4ED6\u7684<code>\u597D\u5B66</code></strong>\u3002</p><p>\u636E\u8BF4\u8427\u7EDF\u5B66\u4E60\u80FD\u529B\u5F88\u5F3A\uFF0C\u80FD\u4E00\u76EE\u5341\u884C\uFF0C\u8FC7\u76EE\u4E0D\u5FD8\u3002\u4ED6\u4E09\u5C81\u5F00\u59CB\u8BFB\u7ECF\uFF0C\u4E94\u5C81\u904D\u8BFB\u4E94\u7ECF\uFF0C\u4E5D\u5C81\u65F6\u5C31\u80FD\u7ED9\u4EBA\u8BB2\u300A\u5B5D\u7ECF\u300B\u4E86\u2014\u2014\u6CE8\u610F\uFF0C\u53E4\u4EBA\u7B97\u5E74\u7EAA\u662F\u6309\u865A\u5C81\u6765\u8BB2\u7684\u3002</p><p>\u800C\u4E14\u8427\u7EDF\u8FD8\u4E0D\u53EA\u662F\u201C\u6C89\u8FF7\u201D\u4E8E\u5112\u5BB6\u7ECF\u5178\u4E0D\u53EF\u81EA\u62D4\uFF0C\u4ED6\u5BF9\u4E8E\u4F5B\u6CD5\u4E5F\u5F88\u7CBE\u901A\uFF0C\u201C\u904D\u89C8\u4F17\u7ECF\u201D\u3002\u8FD9\u4E00\u70B9\uFF0C\u5927\u7EA6\u5C31\u662F\u5728\u6295\u8427\u884D\u7684\u559C\u597D\u4E86\u3002\u4F17\u6240\u5468\u77E5\uFF0C\u8427\u884D\u5BF9\u4F5B\u6CD5\u7684\u70ED\u7231\u5DF2\u7ECF\u5230\u4E86\u72C2\u70ED\u7684\u5730\u6B65\u3002</p><p>\u5728\u8FD9\u79CD\u73AF\u5883\u4E4B\u4E0B\uFF0C\u8427\u7EDF\u5F53\u7136\u4E5F\u8981\u663E\u5F97\u5BF9\u4F5B\u6CD5\u65E2\u70ED\u7231\u53C8\u7CBE\u901A\u3002\u53F2\u4E66\u4E2D\u4E13\u95E8\u715E\u6709\u4ECB\u4E8B\u7684\u5199\uFF1A\u56E0\u4E3A\u88AB\u8427\u7EDF\u8BB2\u89E3\u4F5B\u7ECF\u7684\u8D64\u8BDA\u6240\u611F\u67D3\uFF0C\u201C\u7518\u9732\u964D\u4E8E\u6167\u4E49\u6BBF\u201D\u3002</p><p>\u9664\u6B64\u4EE5\u5916\uFF0C\u300A\u6881\u4E66\xB7\u662D\u660E\u592A\u5B50\u4F20\u300B\u91CC\u8FD8\u8BB0\u8F7D\u4E86\u8FD9\u4E48\u51E0\u4E2A\u4E8B\uFF1A</p><p>\u8427\u884D\u7684\u5F1F\u5F1F\u3001\u8427\u7EDF\u7684\u53D4\u7236\u3001\u59CB\u5174\u738B<code>\u8427\u61BA</code>\u6B7B\u4E86\uFF0C\u8FD9\u79CD\u65F6\u5019\uFF0C\u8427\u7EDF\u5E94\u8BE5\u600E\u4E48\u5C3D\u793C\u5462\uFF1F\u4F20\u7EDF\u4E0A\u6765\u8BF4\uFF0C\u592A\u5B50\u867D\u7136\u662F\u665A\u8F88\uFF0C\u4F46\u4E0E\u81E3\u5B50\u4E4B\u95F4\u8FD8\u662F\u6709\u541B\u81E3\u540D\u5206\u7684\uFF0C\u6240\u4EE5\u5E94\u8BE5\u201C\u793C\u7EDD\u508D\u4EB2\u201D\uFF0C\u4E0D\u5C3D\u793C\u4E8E\u8427\u61BA\u3002</p><p>\u4F46\u8427\u7EDF\u5BF9\u6B64\u5374\u6709\u5F02\u8BAE\uFF0C\u4ED6\u547D\u4EBA\u4ECE\u300A\u5468\u793C\u300B\u5F15\u5178\uFF0C\u53C8\u4EB2\u81EA\u8FA9\u8BD8\uFF0C\u6700\u7EC8\u8FD8\u662F\u4EE5\u4EB2\u60C5\u4E3A\u5C0A\uFF0C\u5728\u670D\u9970\u4E0A\u5BF9\u53D4\u7236\u7684\u53BB\u4E16\u8868\u793A\u4E86\u4E00\u5B9A\u7684\u54C0\u60BC\u3002</p><p><strong>\u8BB0\u8F7D\u8FD9\u5219\u6545\u4E8B\uFF0C\u8BF4\u7684\u662F\u8427\u7EDF\u7CBE\u901A\u4E8E\u201C<code>\u793C</code>\u201D</strong>\u3002</p><p>\u7B2C\u4E8C\u4E2A\u6545\u4E8B\uFF0C\u8BB2\u7684\u5219\u662F\u8427\u7EDF\u5728\u6BCD\u4EB2<strong>\u4E01\u8D35\u5AD4</strong>\u4E34\u7EC8\u524D\u540E\u7684\u4E00\u7CFB\u5217\u5B5D\u884C\u3002</p><p>\u5F53\u65F6\uFF0C\u8427\u7EDF\u4F8D\u75C5\uFF0C\u8863\u4E0D\u89E3\u5E26\u3002\u5728\u4E01\u8D35\u5AD4\u75C5\u901D\u540E\uFF0C\u4ED6\u66F4\u662F\u60B2\u75DB\u6B32\u7EDD\uFF0C\u8336\u996D\u4E0D\u8FDB\u3002\u8427\u884D\u7279\u5730\u547D\u4EBA\u4F20\u65E8\uFF0C\u524D\u540E\u6570\u6B21\u547D\u8427\u7EDF\u8FDB\u98DF\uFF0C\u8427\u7EDF\u624D\u5F00\u59CB\u5403\u4E1C\u897F\u3002</p><p>\u4E00\u573A\u4E27\u793C\u8FC7\u540E\uFF0C\u8427\u7EDF\u7626\u4E86\u4E00\u5927\u5708\u3002\u6BCF\u6B21\u5165\u671D\uFF0C\u4E0D\u7BA1\u662F\u671D\u4E2D\u5927\u81E3\u8FD8\u662F\u5EB6\u6C11\u767E\u59D3\uFF0C\u5927\u5BB6\u770B\u5230\u592A\u5B50\u56E0\u4E3A\u6BCD\u4E27\u800C\u6D88\u7626\u81F3\u6B64\uFF0C\u65E0\u4E0D\u6F78\u7136\u6CEA\u4E0B\u3002</p><p><strong>\u8FD9\u662F\u7528\u6765\u8BF4\u660E\u8427\u7EDF\u7684\u201C<code>\u5B5D</code>\u201D\u7684</strong>\u3002</p><p>\u6B64\u5916\u8FD8\u6709\u7B2C\u4E09\u4E2A\u6545\u4E8B\uFF0C\u5173\u4E8E\u5434\u5174\u90E1\u7684\u52B3\u5F79\u3002\u5434\u5174\u90E1\u5F53\u65F6\u5C61\u906D\u6C34\u60A3\uFF0C\u56E0\u6B64\u6709\u5EFA\u8BAE\u758F\u901A\u6C34\u9053\uFF0C\u8427\u884D\u4E5F\u4E0B\u8BCF\u53D1\u5434\u5174\u90E1\u7B49\u4E09\u90E1\u7684\u6C11\u4E01\u670D\u5F79\u3002\u4F46\u8427\u7EDF\u5374\u4E0A\u4E66\u8427\u884D\uFF0C\u5BF9\u5F81\u53D1\u89C4\u6A21\u548C\u7AE3\u5DE5\u65F6\u9650\u7B49\u95EE\u9898\u59D4\u5A49\u5730\u8868\u793A\u53CD\u5BF9\u3002</p><p><strong>\u8FD9\u4E2A\u4E3B\u8981\u7528\u6765\u8BF4\u660E\u8427\u7EDF\u7684\u201C<code>\u7231\u6C11</code>\u201D</strong>\u3002</p><p>\u89C2\u5BDF\u548C\u8BC4\u4EF7\u4E00\u4E2A\u4EBA\uFF0C\u4E00\u822C\u800C\u8A00\uFF0C\u662F\u901A\u8FC7\u8FD9\u4E2A\u4EBA\u8EAB\u4E0A\u53D1\u751F\u7684\u4E8B\uFF0C\u53C8\u6216\u8005\u662F\u901A\u8FC7\u8FD9\u4E2A\u4EBA\u9762\u5BF9\u4E00\u4E9B\u4E8B\u65F6\u7684\u5904\u7406\u65B9\u5F0F\uFF0C\u6765\u7ED9\u51FA\u5224\u65AD\u3002</p><p>\u90A3\u6211\u4EEC\u5982\u679C\u7528\u8FD9\u4E2A\u6807\u51C6\u6765\u770B\u8BB0\u5F55\u5728\u8427\u7EDF\u540D\u4E0B\u7684\u8FD9\u4E9B\u4E8B\uFF0C\u53C8\u80FD\u770B\u51FA\u4EC0\u4E48\u6765\u5462\uFF1F</p><p>\u7B54\u6848\u662F\uFF1A<strong>\u5047\u7684</strong>\u3002</p><p>\u8FD9\u91CC\u7684\u5047\uFF0C\u5E76\u4E0D\u662F\u8BF4\u4E8B\u4F8B\u9020\u5047\uFF0C\u800C\u662F\u6211\u4EEC\u53EF\u4EE5\u5F88\u660E\u767D\u65E0\u8BEF\u7684\u786E\u8BA4\uFF1A<strong>\u300A\u662D\u660E\u592A\u5B50\u4F20\u300B\u91CC\u7684\u8FD9\u4E9B\u4E8B\u8FF9\uFF0C\u662F\u7ECF\u8FC7\u4ED4\u7EC6\u6311\u9009\u3001\u7CBE\u5FC3\u4FEE\u9970\u4EE5\u540E\uFF0C\u88AB\u523B\u610F\u653E\u5728\u8FD9\u91CC\u7684</strong>\u3002\u901A\u8FC7\u8FD9\u79CD\u5851\u9020\uFF0C\u8427\u7EDF\u5F62\u6210\u4E86\u7CBE\u4E8E\u5112\u5B66\u3001\u5B5D\u884C\u611F\u4EBA\u3001\u7231\u6C11\u5982\u5B50\u7684\u4EBA\u8BBE\u3002</p><p>\u901A\u8BFB\u300A\u662D\u660E\u592A\u5B50\u4F20\u300B\uFF0C\u6700\u5927\u7684\u611F\u53D7\uFF0C\u5C31\u662F\u91CC\u9762\u5448\u73B0\u51FA\u6765\u7684\u8427\u7EDF\uFF0C\u4E0D\u662F\u4E00\u4E2A\u4EBA\uFF0C\u800C\u66F4\u50CF\u5E99\u91CC\u7684\u4E00\u5C0A\u795E\u3002</p><p>\u8FD9\u5C0A\u795E\u8089\u8EAB\u6CE5\u5851\uFF0C\u4E0A\u9762\u660E\u6643\u6643\u53EA\u5199\u4E86\u4E00\u4E2A\u5B57\uFF1A<code>\u4EC1</code>\u3002</p><p>\u81EA\u4ECE\u300A\u6C49\u4E66\u300B\u4EE5\u540E\uFF0C\u4E2D\u56FD\u53F2\u4E66\u7684\u4EF7\u503C\u89C2\uFF0C\u5C24\u5176\u662F\u5BF9\u541B\u738B\u7684\u8BC4\u4EF7\uFF0C\u51E0\u4E4E\u4E5F\u5C31\u662F\u4F20\u7EDF\u5112\u5BB6\u5BF9\u4E8E\u201C\u4EC1\u653F\u201D\u8FFD\u6C42\uFF1A\u8282\u6B32\u3001\u4EC1\u6148\u3001\u5BBD\u5BB9\u3001\u7231\u6C11\uFF0C\u8FD9\u5C31\u662F\u4E00\u4E2A\u597D\u541B\u4E3B\u6240\u5E94\u6709\u7684\u6240\u6709\u54C1\u8D28\u3002</p><p>\u8BF4\u5F97\u6734\u5B9E\u4E00\u70B9\uFF0C\u8FD9\u4E5F\u662F\u4E00\u4E2A\u6807\u51C6\u610F\u4E49\u4E0A\u7684\u201C\u597D\u4EBA\u201D\u2014\u2014\u8FD9\u79CD\u4EF7\u503C\u89C2\u5373\u4FBF\u653E\u5230\u73B0\u4EE3\u793E\u4F1A\u4E5F\u662F\u5982\u6B64\u3002</p><p>\u8FD9\u4E2A\u6807\u51C6\u81EA\u7136\u6B63\u786E\u7684\u3002\u201C\u4EC1\u201D\u610F\u5473\u7740\u5BF9\u81EA\u5DF1\u6B32\u671B\u7684\u514B\u5236\uFF0C\u8FD9\u5BF9\u4E8E\u90A3\u4E9B\u7AD9\u5728\u6743\u529B\u5DC5\u5CF0\u7684\u4EBA\u6765\u8BF4\uFF0C\u5C24\u5176\u96BE\u80FD\u53EF\u8D35\u3002</p><p>\u5728\u524D\u73B0\u4EE3\u793E\u4F1A\u4E2D\uFF0C\u541B\u4E3B\u5982\u679C\u80FD\u514B\u5236\u4F4F\u81EA\u5DF1\u7684\u6B32\u671B\uFF0C\u5173\u5FC3\u4ED6\u6CBB\u4E0B\u5B50\u6C11\u7684\u751F\u8BA1\uFF0C\u90A3\u4E48\u65E0\u7591\u5C06\u4F1A\u662F\u6240\u6709\u767E\u59D3\u7684\u5E78\u8FD0\u3002\u8FD9\u80FD\u8BA9\u767E\u59D3\u5B89\u4E8E\u8015\u79CD\uFF0C\u5982\u6B64\uFF0C\u81EA\u7136\u5C31\u4F1A\u6709\u767E\u59D3\u6BB7\u5BCC\u3001\u671D\u653F\u6E05\u660E\u3001\u56FD\u6CF0\u6C11\u5B89\u7684\u666F\u8C61\u51FA\u73B0\u2014\u2014\u8FD9\u4E5F\u6B63\u662F\u5112\u5BB6\u8FFD\u6C42\u7684\u7406\u60F3\u793E\u4F1A\u3002</p><p>\u53EA\u662F\u5728\u5386\u53F2\u4E2D\uFF0C\u8FD9\u6837\u7684\u541B\u4E3B\u867D\u7136\u662F\u5112\u5BB6\u58EB\u5927\u592B\u5BF9\u4E8E\u7687\u5E1D\u7684\u57F9\u517B\u76EE\u6807\uFF0C\u4F46\u6700\u540E\u771F\u6B63\u80FD\u591F\u6210\u529F\u7684\uFF0C\u5374\u5C11\u4E4B\u53C8\u5C11\u3002</p><p>\u770B\u4E0A\u53BB\uFF0C\u8427\u7EDF\u4F3C\u4E4E\u5F88\u7B26\u5408\u8FD9\u5957\u6807\u51C6\u3002</p><p>\u5355\u770B\u300A\u662D\u660E\u592A\u5B50\u4F20\u300B\uFF0C\u6211\u4EEC\u4F3C\u4E4E\u770B\u4E0D\u51FA\u4EC0\u4E48\u95EE\u9898\u3002\u4F46\u4F60\u53EA\u8981\u628A\u76EE\u5149\u7A0D\u5FAE\u653E\u8FDC\u4E00\u70B9\uFF0C\u505A\u4E2A\u5BF9\u6BD4\uFF0C\u5F88\u5FEB\u5C31\u80FD\u53D1\u73B0\u4E00\u4E9B\u4E0D\u4E00\u6837\u7684\u5730\u65B9\u3002</p><p>\u8427\u7EDF\u751F\u4E8E\u516C\u5143500\u5E74\uFF0C\u5728\u6B64\u524D\u4E00\u5E74\uFF0C<code>\u5317\u9B4F</code>\u7684<code>\u5B5D\u6587\u5E1D\u5143\u5B8F</code>\u521A\u521A\u75C5\u901D\u3002\u5728\u4E00\u822C\u4EBA\u7684\u5370\u8C61\u91CC\uFF0C\u8FD9\u4E24\u4EBA\u4F3C\u4E4E\u5E76\u6CA1\u6709\u4EC0\u4E48\u53EF\u6BD4\u6027\uFF1A\u4E00\u4E2A\u662F\u96CD\u5BB9\u5927\u5EA6\u7684\u51C6\u541B\u4E3B\uFF0C\u53E6\u4E00\u4E2A\u5219\u662F\u9510\u610F\u8FDB\u53D6\u7684\u5E74\u8F7B\u5E1D\u738B\u3002</p><p>\u4F46\u4E8B\u5B9E\u4E0A\uFF0C<strong>\u9769\u65B0\u5E76\u4E0D\u662F\u5B5D\u6587\u5E1D\u552F\u4E00\u7684\u5173\u952E\u8BCD\u3002\u4ED6\u7684<code>\u4EC1\u541B</code>\u5F62\u8C61\uFF0C\u4E5F\u662F\u4E3A\u5386\u53F2\u6240\u516C\u8BA4\u7684</strong>\u3002</p><p>\u5B5D\u6587\u5E1D\u65E9\u5E74\u88AB<code>\u51AF\u592A\u540E</code>\u629A\u517B\u3002\u867D\u7136\u51AF\u592A\u540E\u66FE\u5BF9\u4ED6\u591A\u6709\u82DB\u8D23\uFF0C\u751A\u81F3\u8FD8\u6709\u8FC7\u5E9F\u5E1D\u7684\u52A8\u5FF5\uFF0C\u4F46\u5B5D\u6587\u5E1D\u5728\u4EB2\u653F\u4EE5\u540E\u5374\u6CA1\u6709\u53CD\u653B\u5012\u7B97\uFF0C\u53CD\u800C\u8FD8\u5BF9\u51AF\u592A\u540E\u7684\u8EAB\u540E\u4E8B\u6781\u5C3D\u5C0A\u5D07\uFF0C\u4E0D\u4EC5\u5B9A\u671F\u796D\u62DC\uFF0C\u56FD\u5BB6\u6BCF\u6709\u5927\u4E8B\u65F6\uFF0C\u8FD8\u5FC5\u7136\u8981\u544A\u796D\u4E8E\u51AF\u6C0F\u3002\u6B64\u5916\uFF0C\u5B5D\u6587\u5E1D\u5BF9\u592A\u540E\u7684\u5BB6\u4EBA\u4E5F\u6781\u4E3A\u7167\u987E\u3002</p><p>\u81F3\u4E8E\u5B5D\u6587\u5E1D\u5BF9\u5F85\u5927\u81E3\u548C\u4E0B\u5C5E\u7684\u6001\u5EA6\uFF0C\u53F2\u4E66\u4E2D\u662F\u8FD9\u4E48\u8BB0\u8F7D\u7684\uFF1A\u201C<strong>\u7528\u6CD5\u867D\u4E25\uFF0C\u4E8E\u5927\u81E3\u65E0\u6240\u5BB9\u8D37\uFF0C\u7136\u4EBA\u6709\u5C0F\u8FC7\uFF0C\u5E38\u591A\u9614\u7565</strong>\u3002\u201D\u2014\u2014\u5B5D\u6587\u5E1D\u7528\u6CD5\u68EE\u4E25\uFF0C\u5BF9\u5927\u81E3\u7684\u884C\u653F\u4E4B\u5931\u4ECE\u4E0D\u5BBD\u8D37\uFF1B\u4F46\u5982\u679C\u53EA\u662F\u4E2A\u4EBA\u7684\u8FC7\u9519\uFF0C\u5374\u76F8\u5F53\u5BBD\u5BB9\u3002</p><p>\u5B5D\u6587\u5E1D\u7684\u996D\u83DC\u91CC\uFF0C\u66FE\u7ECF\u5403\u51FA\u8FC7\u866B\u5B50\uFF1B\u8FD8\u6709\u4EBA\u5728\u8FDB\u732E\u7FB9\u6C64\u65F6\uFF0C\u70EB\u4F24\u4E86\u5B5D\u6587\u5E1D\u7684\u624B\u2026\u2026\u5BF9\u4E8E\u8FD9\u4E9B\u884C\u4E3A\u4E3E\u6B62\u4E0A\u7684\u5C0F\u8FC7\u9519\uFF0C\u5B5D\u6587\u5E1D\u4ECE\u6765\u90FD\u4E0D\u4E88\u8FFD\u7A76\u3002</p><p>\u76F8\u6BD4\u8D77\u8427\u7EDF\u6CA1\u6709\u539F\u5219\u7684\u653E\u7EB5\uFF0C\u5B5D\u6587\u5E1D\u7684\u8FD9\u79CD\u5BBD\u5BB9\uFF0C\u4F3C\u4E4E\u8981\u6765\u5F97\u66F4\u52A0\u9AD8\u660E\u4E00\u4E9B\u3002</p><p>\u800C\u4E14\uFF0C\u7531\u4E8E\u56FD\u5BB6\u8FDE\u5E74\u7528\u5175\uFF0C\u5B5D\u6587\u5E1D\u5728\u4E2A\u4EBA\u7528\u5EA6\u4E0A\u4E5F\u76F8\u5F53\u8282\u7701\u3002\u4ED6\u5BF9\u4E8E\u5BAB\u6BBF\u80FD\u4E0D\u4FEE\u5C31\u4E0D\u4FEE\uFF0C\u81EA\u5DF1\u7A7F\u7684\u8863\u670D\u4E5F\u6D17\u51FA\u4E86\u7834\u7EFD\u3002\u5728\u6DEE\u5357\u7528\u5175\u65F6\uFF0C\u519B\u961F\u6240\u8FC7\u4E4B\u5904\u79CB\u6BEB\u65E0\u72AF\uFF0C\u4ECE\u4E0D\u62A2\u63A0\uFF0C\u4E00\u5982\u5728\u56FD\u5185\u884C\u519B\u3002</p><p>\u5BF9\u4E8E\u4E3A\u541B\u4E4B\u6076\uFF0C\u5B5D\u6587\u5E1D\u4E5F\u60F3\u5F97\u5341\u5206\u6E05\u695A\u3002\u4ED6\u8DDF\u53F2\u5B98\u8BF4\uFF1A\u541B\u738B\u5A01\u798F\u4E4B\u5927\uFF0C\u4E16\u95F4\u6CA1\u6709\u4EC0\u4E48\u529B\u91CF\u80FD\u591F\u7EA6\u675F\u5B83\uFF1B\u53EA\u6709\u53F2\u5B98\u7684\u79C9\u7B14\u76F4\u4E66\uFF0C\u624D\u80FD\u8BA9\u541B\u738B\u4E4B\u6076\u6709\u6240\u5FCC\u60EE\uFF01</p><p>\u5B5D\u6587\u5E1D\u548C\u662D\u660E\u592A\u5B50\u8427\u7EDF\u4E4B\u95F4\uFF0C\u76F8\u4F3C\u4E4B\u5904\u5176\u5B9E\u9887\u591A\u3002</p><p>\u5B5D\u6587\u5E1D\u867D\u7136\u5E7C\u5E74\u767B\u57FA\uFF0C\u4F46\u4E00\u76F4\u90FD\u88AB\u7B3C\u7F69\u5728\u51AF\u592A\u540E\u7684\u9634\u5F71\u4E4B\u4E0B\u3002\u800C\u5728\u51AF\u592A\u540E\u53BB\u4E16\u524D\u7684\u5341\u51E0\u5E74\u91CC\uFF0C\u5B5D\u6587\u5E1D\u4E00\u76F4\u88AB\u7CBE\u5FC3\u57F9\u517B\u7740\u2014\u2014\u5728\u8FD9\u4E00\u70B9\u4E0A\uFF0C\u4ED6\u4E0E\u8427\u7EDF\u5E76\u65E0\u4E8C\u81F4\u3002</p><p>\u6211\u4EEC\u53EF\u4EE5\u8FD9\u4E48\u8BF4\uFF1A<strong>\u8FD9\u4E24\u4EBA\u5728\u5112\u5BB6\u6240\u63A8\u5D07\u7684\u201C\u4EC1\u5FB7\u201D\u4E0A\uFF0C\u90FD\u662F\u80FD\u6709\u6240\u5EFA\u6811\u7684</strong>\u3002</p><p>\u4F46\u662F\uFF0C\u4F60\u53EA\u8981\u518D\u4E00\u6BD4\uFF0C\u5C31\u80FD\u770B\u51FA\u4E24\u4EBA\u7684\u5185\u6838\u7EDD\u4E0D\u76F8\u540C\uFF1A<strong>\u5B5D\u6587\u5E1D\u7684\u5185\u91CC\uFF0C\u53EF\u80FD\u8981\u66F4\u52A0\u79EF\u6781\u8FDB\u53D6\u4E00\u4E9B\u3002\u800C\u5728\u8427\u7EDF\u7684\u4EBA\u751F\u4E2D\uFF0C\u4F60\u65E2\u770B\u4E0D\u51FA\u6740\u4F10\u51B3\u65AD\uFF0C\u4E5F\u770B\u4E0D\u51FA\u9510\u610F\u9769\u65B0\uFF0C\u66F4\u770B\u4E0D\u51FA\u4E00\u7EDF\u5929\u4E0B\u4E4B\u5FD7</strong>\u3002</p><p>\u8427\u7EDF\u6709\u7684\uFF0C\u53EA\u662F\u4E2D\u56FD\u4F20\u7EDF\u653F\u6CBB\u5BF9\u4E8E\u541B\u738B\u201C<code>\u5185\u5723</code>\u201D\u7684\u8FFD\u6C42\u3002</p><blockquote><p><strong>\u8FD9\u79CD\u5DEE\u522B\uFF0C</strong><strong>\u65E2\u662F\u4E2A\u4EBA\u7684\u5DEE\u522B\uFF0C</strong><strong>\u53C8\u662F\u65F6\u4EE3\u7684\u5DEE\u522B\u3002</strong></p></blockquote><p>\u5317\u9B4F\u3002\u5B83\u4ECE\u65E9\u671F\u7684\u91CE\u86EE\u9010\u6B65\u5411\u6587\u660E\u8F6C\u53D8\uFF0C\u4ECE\u65E9\u671F\u7684\u7C97\u653E\u7EDF\u6CBB\u53D1\u5C55\u5230\u540E\u6765\u7684\u653F\u6CBB\u5EFA\u8BBE\uFF0C\u4ECE\u521D\u5B66\u4E60\u4E2D\u539F\u5230\u5168\u9762\u63A8\u884C\u6C49\u5316\u2026\u2026<strong>\u53EF\u4EE5\u8BF4\u5317\u9B4F\u4E00\u767E\u591A\u5E74\u7684\u5386\u53F2\uFF0C\u5728\u4E00\u5B9A\u610F\u4E49\u4E0A\u5C31\u662F<code>\u80E1\u6C49\u878D\u5408</code>\u7684\u8FDB\u5316\u53F2</strong>\u3002\u671F\u95F4\u98CE\u4E91\u6FC0\u8361\uFF0C\u4E7E\u5764\u6597\u8F6C\uFF0C\u6709\u592A\u591A\u7684\u5185\u5BB9\u503C\u5F97\u540E\u4EBA\u8BB0\u5F55\u5206\u6790\uFF0C\u548F\u800C\u4F20\u4E4B\u3002</p><p><strong>\u53EF\u5357\u671D\u5374\u4E0D\u540C</strong>\u3002\u7FFB\u5F00\u53F2\u4E66\uFF0C\u6B6A\u6B6A\u659C\u659C\u6BCF\u4E00\u884C\u5C0F\u5B57\u6D6E\u8D77\u6765\uFF0C\u53CD\u53CD\u590D\u590D\u6F14\u7ECE\u7684\u90FD\u662F\u540C\u4E00\u4E2A\u5267\u672C\u3002\u7EB5\u7136\u65B0\u4EBA\u4E0D\u65AD\u8986\u65E7\u4EBA\uFF0C\u671D\u5EF7\u66F4\u66FF\u5F02\u5E38\u9891\u7E41\uFF0C\u53EF\u5386\u53F2\u5374\u59CB\u7EC8\u9677\u5728\u5185\u90E8\u6218\u4E71\u3001\u6539\u671D\u6362\u4EE3\u3001\u7687\u5E1D\u8352\u6DEB\u7684\u6B7B\u5FAA\u73AF\u91CC\uFF0C\u65E0\u4F11\u65E0\u6B62\u3002</p><p>\u5F88\u591A\u4EBA\u4E3A\u5386\u53F2\u5267\u6240\u5438\u5F15\uFF0C\u559C\u6B22\u5357\u671D\u7684\u98CE\u6D41\u6F47\u6D12\u3002<strong>\u4F46\u4E8B\u5B9E\u4E0A\uFF0C\u771F\u6B63\u4EBA\u7269\u98CE\u6D41\u7684\u9B4F\u664B\u65F6\u4EE3\uFF0C\u65E9\u5DF2\u8FDC\u53BB</strong>\u3002\u5357\u671D\u5982\u4ECA\u8FD8\u5269\u4E0B\u7684\uFF0C\u53EA\u6709\u4E16\u5BB6\u5927\u65CF\u5728\u5C0F\u5708\u5C42\u4E2D\u7684\u9010\u6E10\u5815\u843D\u3001\u5B9E\u529B\u5B97\u5BA4\u4E3A\u4E89\u593A\u6743\u4F4D\u800C\u53D1\u8D77\u7684\u65E0\u4F11\u6B62\u6B8B\u6740\uFF0C\u6574\u4E2A\u56FD\u5BB6\u6B63\u5728\u5185\u8017\u4E2D\u9010\u6E10\u8870\u843D\u3002</p><p>\u5373\u4FBF\u662F\u201C\u8427\u7EDF\u201D\u8FD9\u6837\u7ECF\u8FC7\u4E86\u7CBE\u5FC3\u4FEE\u9970\uFF0C\u901A\u8EAB\u6240\u9644\u7740\u7684\uFF0C\u4E5F\u4F9D\u7136\u662F\u90A3\u4E2A\u65F6\u4EE3\u548C\u73AF\u5883\u7684\u7279\u5F81\uFF1A<strong>\u4ED6\u786E\u7136\u6709\u81EA\u5DF1\u7684\u4F18\u52BF\uFF0C\u4F46\u90A3\u79CD\u4E2D\u5EB8\u5E73\u548C\u7684\u6027\u683C\uFF0C\u5DF2\u7ECF\u662F\u90A3\u4E2A\u65F6\u4EE3\u548C\u73AF\u5883\u91CC\u7684\u5929\u82B1\u677F\u4E86</strong>\u3002</p><p>\u5357\u671D\u7684\u6574\u4E2A\u5927\u73AF\u5883\u2014\u2014\u4E0D\u7BA1\u662F\u58EB\u65CF\u5BF9\u4E8E\u6743\u4F4D\u7684\u5784\u65AD\uFF0C\u8FD8\u662F\u53CD\u590D\u7684\u6743\u529B\u6597\u4E89\u6240\u9020\u5C31\u7684\u5F7C\u6B64\u731C\u5FCC\uFF0C\u90FD\u4E0D\u53EF\u80FD\u8BA9\u8427\u7EDF\u53D1\u5C55\u6216\u8005\u88AB\u57F9\u517B\u51FA\u9769\u65B0\u3001\u8FDB\u53D6\u7684\u7CBE\u795E\u8FFD\u6C42\u6765\u3002</p><p>\u53EA\u6709\u5728\u4E0A\u5347\u671F\u7684\u5317\u9B4F\uFF0C\u624D\u80FD\u53EC\u5524\u51FA\u50CF\u5B5D\u6587\u5E1D\u4E00\u6837\u7684\u4E00\u4EE3\u4EE4\u4E3B\u3002\u4ED6\u65E2\u7B26\u5408\u4F20\u7EDF\u5112\u5BB6\u5BF9\u4E8E\u541B\u738B\u201C\u5185\u5723\u201D\u7684\u8981\u6C42\uFF0C\u53C8\u80FD\u8131\u79BB\u79CD\u79CD\u7AA0\u81FC\uFF0C\u5F15\u9886\u56FD\u5BB6\u628A\u63E1\u4F4F\u53D8\u9769\u7684\u5386\u53F2\u673A\u9047\u3002\u4ED6\u65E2\u6709\u514B\u5DF1\u590D\u793C\u7684\u4FEE\u517B\uFF0C\u53C8\u6709\u9A70\u9A8B\u7586\u573A\u7684\u8FFD\u9010\uFF1B\u65E2\u6709\u629A\u80B2\u4E07\u6C11\u3001\u4E00\u7EDF\u5929\u4E0B\u7684\u81EA\u89C9\uFF0C\u66F4\u6709\u8EAB\u6000\u754F\u60E7\u3001\u4E0D\u64C5\u7528\u6743\u529B\u3001\u4E0D\u8BA9\u6743\u6B32\u4E4B\u6076\u541E\u566C\u81EA\u8EAB\u7684\u6E05\u9192\u3002</p><p>\u8FD8\u662F\u90A3\u53E5\u8BDD\uFF0C\u8FD9\u65E2\u662F\u4E2A\u4EBA\u7684\u5DEE\u522B\uFF0C\u53C8\u662F\u65F6\u4EE3\u7684\u5DEE\u522B\u3002</p><p>\u867D\u7136\u5927\u5BB6\u90FD\u662F\u201C\u5929\u9009\u4E4B\u5B50\u201D\uFF0C\u4F46\u5929\u4E0E\u5929\u672C\u5C31\u662F\u4E0D\u4E00\u6837\u7684\u3002</p><p>\u518D\u7CBE\u5FC3\u96D5\u9970\u7684\u8427\u7EDF\uFF0C\u4E5F\u6210\u4E0D\u4E86\u5B5D\u6587\u5E1D\u3002</p>',56),s=[n];function c(d,g){return o(),t("div",null,s)}const l=p(r,[["render",c],["__file","78.html.vue"]]);export{l as default};
