import{_ as p}from"./_plugin-vue_export-helper.cdc0426e.js";import{o,c as e,f as t}from"./app.2ed33847.js";const c={},r=t('<h1 id="\u8427\u6881\u4E4B\u672B" tabindex="-1"><a class="header-anchor" href="#\u8427\u6881\u4E4B\u672B" aria-hidden="true">#</a> \u8427\u6881\u4E4B\u672B</h1><blockquote><p>\u6563\u9A91\u5E38\u4F8D\u8D3A\u741B\u542F\u9648\u56DB\u4E8B\uFF1A\u5176\u4E00\u4EE5\u4E3A\uFF1A\u4ECA\u5317\u8FB9\u7A3D\u670D\uFF0C\u6B63\u662F\u751F\u805A\u6559\u8BAE\u4E4B\u65F6\uFF0C\u800C\u5929\u4E0B\u6237\u53E3\u51CF\u843D\uFF0C\u5173\u5916\u5F25\u751A\u3002</p></blockquote><p><strong>\u300A\u8D44\u6CBB\u901A\u9274\xB7\u5377\u4E00\u4E94\u4E5D\xB7\u6881\u7EAA\u5341\u4E94\u300B</strong></p><p><code>\u5357\u671D</code>\u7684\u5386\u53F2\uFF0C\u771F\u7684\u5F88\u5947\u5999\u3002</p><p>\u6709\u65F6\u5019\u662F\u201C\u4E71\u201D\uFF0C\u65F6\u5C40\u56E0\u4E3A\u7687\u5E1D\u66F4\u66FF\u9891\u7E41\u3001\u6743\u529B\u66F4\u66FF\u4E0D\u6B63\u5E38\u800C\u9891\u9891\u52A8\u8361\u3002\u4ECE<code>\u5B8B\u6587\u5E1D</code>\u5F00\u59CB\uFF0C\u4E00\u76F4\u5230<code>\u5357\u9F50\u4E1C\u660F\u4FAF</code>\uFF0C\u51E0\u5341\u5E74\u4E4B\u95F4\u7687\u5E1D\u6362\u6765\u6362\u53BB\uFF0C\u5B97\u5BA4\u3001\u6B66\u5C06\u5F81\u6218\u4E0D\u65AD\uFF0C\u767E\u59D3\u6D41\u79BB\u5931\u6240\u3002</p><p>\u597D\u4E0D\u5BB9\u6613\uFF0C\u51FA\u4E86\u4E00\u4E2A<code>\u6881\u6B66\u5E1D</code>\u3002\u6881\u6B66\u5E1D\u5728\u4F4D47\u5E74\uFF0C\u662F\u5357\u671D\u5728\u4F4D\u65F6\u95F4\u6700\u957F\u7684\u7687\u5E1D\u3002\u5728\u4ED6\u7684\u6CBB\u4E0B\uFF0C\u5357\u671D\u4E5F\u57FA\u672C\u53EF\u4EE5\u7B97\u5F97\u4E0A\u662F\u98CE\u8C03\u96E8\u987A\uFF0C\u56FD\u6CF0\u6C11\u5B89\u7684\u3002</p><p>\u800C\u4E14\u867D\u7136\u8BF4\u5728\u5386\u53F2\u4E0A\uFF0C<code>\u8427\u6881</code>\u662F\u56E0\u4E3A<code>\u4FAF\u666F\u4E4B\u4E71</code>\u800C\u51E0\u4E4E\u4E00\u591C\u5D29\u6E83\u7684\u3002\u4F46\u4E8B\u5B9E\u662F\uFF0C\u65E9\u5728\u4FAF\u666F\u4E4B\u4E71\u4EE5\u524D\uFF0C\u8427\u6881\u56FD\u5185\u7684\u653F\u6CBB\u98CE\u6C14\u5C31\u5DF2\u7ECF\u76F8\u5F53\u5F97\u5DEE\u3002\u767E\u59D3\u5728\u51E0\u5341\u5E74\u7684\u548C\u5E73\u5C81\u6708\u91CC\uFF0C\u8FC7\u7684\u4E5F\u5E76\u4E0D\u662F\u4EC0\u4E48\u592A\u5E73\u751F\u6D3B\uFF0C\u53CD\u800C\u8FD8\u77DB\u76FE\u91CD\u91CD\uFF0C\u5927\u6709\u9690\u5FE7\u3002</p><p><strong>\u6B64\u65F6\u7684\u8427\u6881\uFF0C\u4E0E\u5176\u8BF4\u6B63\u503C\u9F0E\u76DB\uFF0C\u8FD8\u4E0D\u5982\u8BF4\u6811\u5927\u4E2D\u7A7A\u3002</strong></p><p>\u8FD9\u4E9B\u95EE\u9898\uFF0C\u5728\u5F53\u65F6\u5C31\u6709\u4EBA\u770B\u4E86\u51FA\u6765\uFF0C\u800C\u4E14\u8FD8\u4E13\u95E8\u4E0A\u4E66\uFF0C\u7ED9\u6881\u6B66\u5E1D\u63D0\u5EFA\u8BAE\u3002</p><p>\u4F46\u7ED3\u679C\u6362\u56DE\u7684\uFF0C\u5374\u662F\u6881\u6B66\u5E1D\u5288\u5934\u76D6\u8138\u4E00\u987F\u75DB\u9A82\u3002</p><p>\u4F60\u6839\u672C\u5C31\u60F3\u4E0D\u5230\uFF0C\u4E00\u4E2A\u5DF2\u7ECF\u516B\u5341\u591A\u5C81\u9AD8\u9F84\u3001\u5728\u4F4D\u56DB\u5341\u591A\u5E74\u7684\u8001\u7687\u5E1D\uFF0C\u5C45\u7136\u4F1A\u5728\u7ED9\u81E3\u4E0B\u7684\u8BCF\u4E66\u4E2D\uFF0C\u5931\u6001\u800C\u53C8\u6FC0\u70C8\u5730\u8BA8\u8BBA\u8D77\u81EA\u5DF1\u7684\u6027\u751F\u6D3B\u95EE\u9898\u6765\u3002</p><p><code>\u8D3A\u741B</code>\u662F\u4E00\u4E2A\u559C\u6B22\u63D0\u610F\u89C1\u7684\u4EBA\u3002\u800C\u4E14\u56E0\u4E3A\u610F\u89C1\u63D0\u5F97\u597D\uFF0C\u5F88\u53D7\u6881\u6B66\u5E1D\u5C0A\u91CD\u2014\u2014\u8BF7\u6CE8\u610F\uFF0C\u4E0D\u662F\u559C\u6B22\uFF0C\u662F\u5C0A\u91CD\u3002</p><p>\u80FD\u6709\u5982\u6B64\u5F85\u9047\uFF0C\u80AF\u5B9A\u4E0D\u4F1A\u6D6A\u5F97\u865A\u540D\uFF0C\u800C\u662F\u6709\u771F\u4E1C\u897F\u80FD\u62FF\u5F97\u51FA\u624B\u7684\u3002\u8FC7\u5F80\u5982\u6B64\uFF0C\u8D3A\u741B\u81EA\u7136\u4E5F\u5C31\u60F3\u7740\u8981\u518D\u63A5\u518D\u5389\u624D\u884C\u3002\u601D\u6765\u60F3\u53BB\uFF0C\u4ED6\u7ED9\u6881\u6B66\u5E1D\u4E0A\u4E86\u4E00\u5C01\u594F\u758F\uFF0C\u628A\u81EA\u5DF1\u8FD9\u4E9B\u5E74\u89C2\u5BDF\u5230\u7684\u56FD\u5BB6\u79CD\u79CD\u4E71\u8C61\u90FD\u5199\u4E86\u51FA\u6765\uFF0C\u5E0C\u671B\u80FD\u5F53\u5934\u68D2\u559D\uFF0C\u8BA9\u6881\u6B66\u5E1D\u6709\u6240\u60CA\u9192\u3002</p><p>\u53EF\u6CA1\u60F3\u5230\u4EA4\u4E0A\u53BB\uFF0C\u6881\u6B66\u5E1D\u5374\u62A1\u7740\u5927\u68D2\u6325\u4E86\u8FC7\u6765\uFF0C\u4E00\u68D2\u515C\u5934\uFF0C\u76F4\u63A5\u5C31\u628A\u8D3A\u741B\u63CD\u6210\u4E86\u8111\u9707\u8361\u3002</p><p>\u8D3A\u741B\u4E00\u5171\u63D0\u4E86\u56DB\u4E2A\u95EE\u9898\uFF1A</p><p><strong>\u4E00\u662F\u201C\u6237\u53E3\u51CF\u843D\u201D\uFF0C\u4E5F\u5C31\u662F\u4EBA\u53E3\u51CF\u5C11\u3002</strong></p><p>\u4E3A\u4EC0\u4E48\u592A\u5E73\u76DB\u4E16\u51E0\u5341\u5E74\uFF0C\u4EBA\u53E3\u5374\u6709\u6240\u51CF\u5C11\u5462\uFF1F\u8D3A\u741B\u8BA4\u4E3A\uFF0C\u8FD9\u4E0D\u662F\u4EBA\u53E3\u5B66\u4E0A\u7684\u95EE\u9898\uFF0C\u800C\u662F\u7BA1\u7406\u4E0A\u7684\u539F\u56E0\u3002</p><p><strong>\u662F\u56E0\u4E3A<code>\u5F81\u53D1\u592A\u91CD</code>\uFF0C\u800C\u4E14\u8FD8\u4E0D\u662F\u5730\u65B9\u5B98\u5E9C\u7684\u5F81\u53D1\uFF0C\u662F<code>\u9010\u5C42\u4E0B\u538B</code></strong>\u3002\u53BF\u91CC\u53D7\u5236\u4E8E\u90E1\u91CC\uFF0C\u90E1\u53C8\u88AB\u5DDE\u6240\u63A7\uFF0C\u4E00\u5C42\u5C42\u644A\u6D3E\u4E0B\u6765\uFF0C\u5BFC\u81F4\u7684\u7ED3\u679C\u5C31\u662F\u5927\u91CF\u767E\u59D3\u629B\u8352\uFF0C\u6210\u4E3A\u6D41\u6C11\u6216\u8005\u9690\u6237\u3002</p><p><strong>\u4E8C\u662F\u201C\u5BB0\u5B88\u8D2A\u6B8B\u201D\uFF0C\u8FD9\u91CC\u53C8\u5177\u4F53\u5206\u4E3A\u4E24\u6761\uFF1A<code>\u5962\u4F88</code>\u548C<code>\u6DEB\u9038</code>\u3002</strong></p><p>\u8D3A\u741B\u8BF4\u8FD9\u4E24\u8005\u662F\u76F8\u4F34\u800C\u751F\u7684\uFF0C\u5B98\u5458\u4E4B\u95F4\u7ADE\u76F8\u5962\u534E\uFF0C\u5DF2\u7ECF\u4E60\u4EE5\u4E3A\u5E38\uFF0C\u5B98\u573A\u6587\u606C\u6B66\u5B09\uFF0C\u6E05\u5EC9\u6B63\u76F4\u7684\u4EBA\u96BE\u4EE5\u51FA\u5934\u3002</p><p><strong>\u4E09\u662F<code>\u9177\u540F</code>\u6A2A\u884C\u3002</strong></p><p>\u8D3A\u741B\u5148\u5938\u4E86\u4E00\u4E0B\u6881\u6B66\u5E1D\u201C\u5FE7\u5FF5\u56DB\u6D77\uFF0C\u4E0D\u60EE\u52E4\u52B3\u201D\uFF0C\u7136\u540E\u53C8\u8BF4\u7528\u4E8B\u7684\u5B98\u5458\u5374\u201C\u4E0D\u8BBA\u56FD\u4E4B\u5927\u4F53\u201D\uFF0C\u4E0D\u80FD\u201C\u5FC3\u5B58\u660E\u6055\u201D\uFF0C\u53CD\u800C\u8FD8\u5439\u6BDB\u6C42\u75B5\uFF0C\u201C\u4EE5\u6DF1\u523B\u4E3A\u80FD\uFF0C\u4EE5\u7EF3\u9010\u4E3A\u52A1\u201D\u3002</p><p>\u4EC0\u4E48\u610F\u601D\u5462\uFF1F<strong>\u5C31\u662F\u8BF4\u7687\u5E1D\u8EAB\u8FB9\u7684\u4EBA\u2014\u2014\u5C24\u5176\u662F\u90A3\u4E9B\u7BA1\u5B98\u7684\u5B98\uFF0C\u559C\u6B22\u76EF\u7740\u522B\u4EBA\u7684\u5C0F\u6BDB\u75C5\uFF0C\u70ED\u8877\u4E8E\u628A\u5C0F\u95EE\u9898\u505A\u6210\u5927\u6848\u5B50\u3002</strong></p><p>\u8D3A\u741B\u8BA4\u4E3A\uFF0C\u8FD9\u4E9B\u4EBA\u770B\u4E0A\u53BB\u662F\u5949\u516C\u65E0\u79C1\uFF0C\u4F46\u5B9E\u9645\u4E0A\u5374\u662F\u4E3A\u4E86\u81EA\u5DF1\u7684\u201C\u5A01\u798F\u201D\uFF0C\u957F\u6B64\u4EE5\u5F80\uFF0C\u5BF9\u653F\u6CBB\u98CE\u6C14\u7684\u7834\u574F\u5C06\u6781\u5176\u4E25\u91CD\u3002\u8D3A\u741B\u7531\u6B64\u5EFA\u8BAE\u6881\u6B66\u5E1D\uFF0C\u5E94\u8BE5\u9EDC\u9000\u8FD9\u4E9B\u5C0F\u4EBA\u4F5E\u81E3\uFF0C\u91CD\u5851\u516C\u5E73\u516C\u6B63\u7684\u653F\u6CBB\u98CE\u6C14\u3002</p><p><strong>\u8FD8\u6709\u7B2C\u56DB\u6761\uFF0C\u662F\u8BF4\u56FD\u5BB6<code>\u82B1\u94B1</code>\u82B1\u592A\u591A\u4E86\u3002</strong></p><p>\u8D3A\u741B\u5728\u8FD9\u91CC\u8868\u8FF0\u7684\u5F88\u59D4\u5A49\uFF0C\u53EA\u8BF4\u5E94\u8BE5\u201C\u606F\u8D39\u4F11\u6C11\u201D\uFF0C\u56E0\u4E3A\u56FD\u5BB6\u5DF2\u7ECF\u82B1\u4E86\u5F88\u591A\u4E0D\u8BE5\u82B1\u7684\u94B1\u3001\u505A\u4E86\u5F88\u591A\u4E0D\u8BE5\u505A\u7684\u4E8B\uFF0C\u6BD4\u5982\u8BF4\uFF0C\u6709\u4E9B\u4FEE\u5EFA\u7684\u4E1C\u897F\uFF0C\u5176\u5B9E\u662F\u53EF\u4EE5\u4E0D\u5EFA\u7684\u3002</p><p>\u6709\u54EA\u4E9B\u5462\uFF1F\u8D3A\u741B\u4E0D\u6562\u660E\u8BF4\uFF0C\u4F46\u5F53\u7136\u662F\u6881\u6B66\u5E1D\u559C\u6B22\u7684\u4F5B\u5BFA\u4E86\u3002</p><p>\u8FD9\u6837\u6D0B\u6D0B\u6D12\u6D12\u5199\u5B8C\uFF0C\u8D3A\u741B\u6EE1\u5FC3\u6B22\u559C\u7684\u628A\u594F\u758F\u4EA4\u4E86\u4E0A\u53BB\u3002</p><p>\u53EF\u6CA1\u60F3\u5230\uFF0C\u7B49\u6765\u7684\u5374\u662F\u5288\u5934\u76D6\u8138\u4E00\u987F\u5927\u9A82\u3002</p><p>\u5DF2\u7ECF\u5782\u4F4D\u56DB\u5341\u591A\u5E74\u7684\u6881\u6B66\u5E1D\u5341\u5206\u6124\u6012\u548C\u5931\u6001\uFF0C\u5C45\u7136\u4E00\u70B9\u9762\u5B50\u90FD\u6CA1\u7ED9\u8D3A\u741B\u7559\uFF0C\u76F4\u63A5\u5C31\u4E0B\u8BCF\u4E0E\u8D3A\u741B\u5BF9\u9A82\u3002</p><p>\u5728\u8BCF\u4E66\u91CC\uFF0C\u6881\u6B66\u5E1D\u5148\u662F\u9634\u9633\u602A\u6C14\u7684\u5938\u8D3A\u741B\uFF1A\u4F60\u8BF4\u5F97\u597D\u554A\uFF01\u96BE\u5F97\u4F60\u6709\u8FD9\u6837\u7684\u8BDA\u5FC3\u8DDF\u6211\u8BF4\u8FD9\u4E9B\u3002\u8BF4\uFF0C\u662F\u4F60\u7684\u8D23\u4EFB\uFF1B\u542C\uFF0C\u662F\u6211\u7684\u4E49\u52A1\u3002\u4F60\u65E2\u7136\u8BF4\u4E86\uFF0C\u90A3\u6211\u5F53\u7136\u5C31\u8981\u542C\u3002</p><p>\u7136\u540E\uFF0C\u6881\u6B66\u5E1D\u8BDD\u950B\u4E00\u8F6C\uFF0C\u5F00\u59CB\u6279\u5224\u8D3A\u741B\uFF1A</p><p>\u4F60\u8BF4\u201C\u6C11\u5931\u5B89\u5C45\uFF0C\u7267\u5B88\u4E4B\u8FC7\u201D\uFF0C\u6765\uFF0C\u4F60\u597D\u597D\u8DDF\u6211\u8BF4\u8BF4\u8FD9\u4E9B\u6709\u8FC7\u7684\u201C\u7267\u5B88\u201D\u90FD\u662F\u8C01\u3002\u4ED6\u4EEC\u59D3\u751A\u540D\u8C01\uFF0C\u5B98\u5C45\u4F55\u804C\uFF0C\u53C8\u6709\u4F55\u4E0D\u6CD5\u4E4B\u5904\uFF1F</p><p>\u4F60\u8BF4\u6211\u6D3E\u51FA\u53BB\u7684\u4EBA\u4FB5\u6270\u5730\u65B9\uFF0C\u90A3\u4E48\u8BF7\u95EE\uFF0C\u6211\u5982\u679C\u4E0D\u6D3E\u4EBA\u5404\u5904\u5DE1\u67E5\uFF0C\u53C8\u5982\u4F55\u7EA6\u675F\u5404\u7EA7\u5B98\u540F\uFF1F\u56FD\u5BB6\u5982\u4F55\u6CBB\u7406\uFF1F\u5BF9\u6B64\u4F60\u6709\u4EC0\u4E48\u597D\u7684\u529E\u6CD5\u6CA1\u6709\uFF1F\u4F60\u65E2\u7136\u8FD9\u4E48\u8BF4\u4E86\uFF0C\u90A3\u60F3\u6765\u662F\u6709\u7684\u3002\u4F60\u8981\u662F\u89C9\u5F97\u4E0D\u8BE5\u5F53\u4F17\u8BF4\uFF0C\u53EF\u4EE5\u79C1\u4E0B\u544A\u8BC9\u6211\u3002</p><p>\u518D\u6765\u8BF4\u4F60\u8BA4\u4E3A\u7684\u201C\u5BB0\u5B88\u8D2A\u6B8B\u201D\u3002\u5173\u4E8E\u8D2A\u8150\u548C\u5962\u4F88\uFF0C\u6211\u89C9\u5F97\u4F60\u5E94\u8BE5\u8FA9\u8BC1\u7740\u770B\uFF1A\u6211\u56DB\u5341\u591A\u5E74\u524D\u5F53\u7687\u5E1D\u7684\u65F6\u5019\uFF0C\u56FD\u5BB6\u4E00\u7A77\u4E8C\u767D\u4EC0\u4E48\u90FD\u6CA1\u6709\uFF0C\u5982\u4ECA\u662F\u56FD\u5BB6\u5BCC\u88D5\u4E86\uFF0C\u6240\u4EE5\u5B98\u5458\u624D\u6709\u8D2A\u8150\u7684\u53EF\u80FD\u554A\uFF01\u4F60\u4E0D\u80FD\u53EA\u770B\u5230\u95EE\u9898\u7684\u4E00\u65B9\u9762\uFF0C\u800C\u5FFD\u89C6\u53E6\u4E00\u65B9\u9762\u3002\u518D\u8BF4\u4E86\uFF0C\u54EA\u91CC\u6709\u5BCC\u88D5\u4E86\u8FD8\u80FD\u4FDD\u6301\u8270\u82E6\u594B\u6597\u4F5C\u98CE\u7684\u56FD\u5BB6\uFF1F\u6709\u7684\u8BDD\uFF0C\u9EBB\u70E6\u4F60\u544A\u8BC9\u6211\u554A\u3002</p><p>\u8D8A\u8BF4\u8D8A\u6765\u52B2\u7684\u6881\u6B66\u5E1D\uFF0C\u63A5\u4E0B\u6765\u7EC8\u4E8E\u8BF4\u5230\u4E86\u91CD\u70B9\uFF1A</p><p>\u4F60\u8BF4\u5B98\u5458\u8D2A\u8150\uFF0C\u6839\u6E90\u5728\u6211\uFF0C\u5173\u4E8E\u8FD9\u4E00\u70B9\uFF0C\u6211\u8981\u597D\u597D\u5730\u8DDF\u4F60\u8BF4\u9053\u8BF4\u9053\uFF1A\u9664\u4E86\u516C\u5BB4\uFF0C\u6211\u4ECE\u6765\u4E0D\u5403\u56FD\u5BB6\u4E00\u7C92\u7C73\uFF1B\u5BAB\u91CC\u517B\u7684\u5BAB\u4EBA\uFF0C\u4E5F\u6CA1\u82B1\u671D\u5EF7\u4E00\u5206\u94B1\uFF1B\u81F3\u4E8E\u8425\u9020\u697C\u9601\u5E99\u5B87\uFF0C\u90A3\u66F4\u662F\u6211\u81EA\u5DF1\u60F3\u529E\u6CD5\u627E\u7684\u94B1\uFF0C\u4E00\u5206\u4E5F\u6CA1\u82B1\u56FD\u5E93\u91CC\u7684\uFF01</p><p>\u4F60\u5982\u4ECA\u62D0\u7740\u5F2F\u529D\u6211\u8282\u4FED\uFF0C\u8BF7\u95EE\u6211\u54EA\u91CC\u82B1\u94B1\u4E86\uFF01\u54EA\u91CC\u82B1\u94B1\u4E86\uFF01</p><p>\u8FD9\u91CC\u9700\u8981\u89E3\u91CA\u4E00\u4E0B\uFF0C\u4E3A\u4EC0\u4E48\u6881\u6B66\u5E1D\u4F1A\u8BF4\u81EA\u5DF1\u6CA1\u6709\u82B1\u4E00\u5206\u94B1\uFF1A</p><p>**\u5728\u5E1D\u5236\u65F6\u4EE3\uFF0C\u7687\u5E1D\u7684\u94B1\u548C\u671D\u5EF7\u7684\u94B1\u662F\u5206\u5F00\u7684\u3002**\u56FD\u5BB6\u7684\u7A0E\u8D4B\uFF0C\u4E5F\u5C31\u662F\u4ECE\u767E\u59D3\u90A3\u91CC\u5F81\u6536\u5230\u7684\u7530\u8D4B\u548C\u4E01\u8D4B\uFF0C\u662F\u56FD\u5BB6\u7684\u6B63\u9879\u6536\u5165\uFF1B\u800C\u7687\u5E1D\u7684\u94B1\uFF0C\u5219\u4E3B\u8981\u6765\u81EA\u4E8E\u201C\u8D44\u6E90\u7A0E\u201D\uFF0C\u4EE5\u53CA\u4E00\u4E9B\u7687\u5BB6\u7684\u5730\u79DF\u3002\u6BD4\u5982\u5728\u6E05\u671D\uFF0C\u671D\u5EF7\u7684\u6536\u5165\u5C31\u5728\u6237\u90E8\uFF0C\u800C\u7687\u5E1D\u7684\u79C1\u4EBA\u8170\u5305\u5219\u5F52\u5185\u52A1\u5E9C\u3002</p><p>\u4E24\u8005\u4E4B\u95F4\u901A\u4E0E\u4E0D\u901A\uFF0C\u8FD9\u4E0D\u89C1\u5F97\u662F\u4E00\u4E2A\u5BA2\u89C2\u4E8B\u5B9E\u3002\u4F46\u5982\u679C\u7687\u5E1D\u975E\u8981\u8BF4\u201C\u4E0D\u662F\u201D\uFF0C\u4F60\u4E5F\u5C31\u4E0D\u80FD\u4E00\u53E3\u54AC\u5B9A\u8BF4\u201C\u662F\u201D\u4E86\u3002\u8FD8\u662F\u6BD4\u5982\u5728\u6E05\u671D\uFF0C\u5230\u4E86\u4E7E\u9686\u665A\u5E74\uFF0C\u548C\u73C5\u5C31\u8EAB\u517C\u6237\u90E8\u5C1A\u4E66\u548C\u5185\u52A1\u5E9C\u5927\u81E3\u3002</p><p>\u5982\u4ECA\u5728\u6881\u6B66\u5E1D\u8FD9\u91CC\uFF0C\u4ED6\u4E5F\u662F\u63E3\u7740\u660E\u767D\u88C5\u7CCA\u6D82\u3002</p><p>\u7136\u540E\uFF0C\u6881\u6B66\u5E1D\u53C8\u7EE7\u7EED\u8DDF\u8D3A\u741B\u8BA8\u8BBA\u6DEB\u9038\u7684\u95EE\u9898\uFF1A\u4F60\u8BF4\u5B98\u5458\u84C4\u5993\uFF0C\u800C\u4E14\u5962\u534E\uFF0C\u56FD\u5BB6\u5E94\u8BE5\u4ECE\u4F5C\u98CE\u95EE\u9898\u4E0A\u7740\u624B\uFF0C\u6B63\u4E86\u4E0A\u6881\uFF0C\u4E0B\u6881\u624D\u80FD\u4E0D\u6B6A\uFF0C\u6240\u4EE5\u4F60\u8FD9\u662F\u5728\u8BF4\u6211\u4E86\uFF1F</p><p>\u90A3\u6211\u662F\u84C4\u5993\u4E86\uFF1F\u8FD8\u662F\u5962\u534E\u4E86\uFF1F</p><p>\u8BF4\u5230\u8FD9\u91CC\uFF0C\u6881\u6B66\u5E1D\u7EC8\u4E8E\u5F7B\u5E95\u7206\u53D1\u4E86\uFF0C\u4ED6\u6C14\u6025\u8D25\u574F\u7684\u5486\u54EE\u9053\uFF1A</p><blockquote><p>\u6284\u5F55\u4E00\u6BB5\u539F\u6587</p></blockquote><p>\u537F\u4E91\u201C\u5439\u6BDB\u6C42\u75B5\u201D\uFF0C\u590D\u662F\u4F55\u4EBA\u6240\u5439\u4E4B\u75B5\uFF1F</p><p>\u201C\u64D8\u808C\u5206\u7406\u201D\uFF0C\u590D\u662F\u4F55\u4EBA\u4E4E\uFF1F</p><p>\u4E8B\u53CA\u201C\u6DF1\u523B\u201D\u201C\u7EF3\u9010\u201D\uFF0C\u5E76\u590D\u662F\u8C01\uFF1F</p><p>\u53C8\u4E91\u201C\u6CBB\u3001\u7F72\u3001\u90B8\u3001\u8086\u201D\uFF0C\u4F55\u8005\u5B9C\u9664\uFF1F\u4F55\u8005\u5B9C\u7701\uFF1F</p><p>\u201C\u56FD\u5BB9\u620E\u5907\u201D\uFF0C\u4F55\u8005\u5B9C\u7701\uFF1F\u4F55\u8005\u672A\u987B\uFF1F</p><p>\u201C\u56DB\u65B9\u5C6F\u4F20\u201D\uFF0C\u4F55\u8005\u65E0\u76CA\uFF1F\u4F55\u8005\u59A8\u6C11\uFF1F\u4F55\u5904\u5174\u9020\u800C\u662F\u5F79\u6C11\uFF1F\u4F55\u5904\u8D39\u8D22\u800C\u662F\u975E\u6025\uFF1F</p><p>\u82E5\u4E3A\u201C\u8BA8\u53EC\u201D\uFF1F\u82E5\u4E3A\u201C\u5F81\u8D4B\u201D\uFF1F\u671D\u5EF7\u4ECE\u6765\u65E0\u6709\u6B64\u4E8B\uFF0C\u9759\u606F\u4E4B\u65B9\u590D\u4F55\u8005\uFF1F</p><p>\u5B9C\u5404\u51FA\u5176\u4E8B\uFF0C\u5177\u4EE5\u594F\u95FB\u3002</p><p>\u53F8\u9A6C\u5149\u5728\u300A\u8D44\u6CBB\u901A\u9274\u300B\u91CC\u8BB0\u5F55\u5B8C\u8FD9\u6BB5\u541B\u81E3\u5BF9\u7B54\u540E\uFF0C\u53C8\u7279\u5730\u52A0\u4E86\u4E00\u6BB5\u8BC4\u4EF7\uFF1A</p><blockquote><p>\u6881\u6B66\u5E1D\u6CA1\u6709\u5584\u7EC8\uFF0C\u8FD9\u662F\u4ED6\u5E94\u5F97\u7684\u5427\uFF01</p><p>\u4E3A\u4EC0\u4E48\u5462\uFF1F<strong>\u56E0\u4E3A\u4ED6\u4E0D\u7EB3\u8C0F</strong>\u3002\u4EBA\u5BB6\u597D\u5FC3\u597D\u610F\u529D\u4ED6\uFF0C\u4E5F\u6CA1\u6709\u6307\u540D\u9053\u59D3\u7684\u76F4\u63A5\u63ED\u7A7F\u4ED6\uFF0C\u7B97\u662F\u5F88\u6536\u655B\u4E86\u3002\u53EF\u4ED6\u8EAB\u4E3A\u738B\u8005\uFF0C\u4E0D\u4EC5\u4E0D\u865A\u5FC3\u7EB3\u8C0F\uFF0C\u53CD\u800C\u8FD8\u5728\u9707\u6012\u4E4B\u4E0B\u4E0E\u81E3\u4E0B\u4E89\u8BBA\u3002</p><p>\u800C\u4E14\u4E89\u8BBA\u4E5F\u4E0D\u662F\u5C31\u4E8B\u8BBA\u4E8B\u7684\uFF0C\u662F\u201C<strong>\u62A4\u5176\u6240\u77ED\uFF0C\u77DC\u5176\u6240\u957F</strong>\u201D\uFF0C\u53EA\u6311\u5BF9\u81EA\u5DF1\u6709\u5229\u7684\u8BF4\uFF0C\u4E00\u70B9\u90FD\u6CA1\u6709\u738B\u8005\u7684\u96CD\u5BB9\u5927\u5EA6\u3002</p><p>\u6240\u4EE5\u4ED6\u6700\u540E\u843D\u5F97\u90A3\u6837\u7684\u4E0B\u573A\uFF0C\u53EF\u4E0D\u5C31\u662F\u5E94\u8BE5\u7684\u5417\uFF1F</p></blockquote><p>\u4EBA\u4E0D\u662F\u5168\u77E5\u5168\u80FD\u7684\uFF0C\u603B\u4F1A\u6709\u8FD9\u6837\u90A3\u6837\u7684\u7F3A\u70B9\u2014\u2014\u5C24\u5176\u662F\u4F60\u6211\u8FD9\u6837\u7684\u5E73\u51E1\u4EBA\u3002\u867D\u7136\u4E5F\u90FD\u77E5\u9053\u8981\u201C\u6709\u5219\u6539\u4E4B\uFF0C\u65E0\u5219\u52A0\u52C9\u201D\uFF0C\u4F46\u4E00\u822C\u800C\u8A00\uFF0C\u522B\u4EBA\u5728\u7ED9\u4F60\u63D0\u610F\u89C1\u7684\u65F6\u5019\uFF0C\u4F60\u5FC3\u91CC\u603B\u5F52\u8FD8\u662F\u4F1A\u611F\u53D7\u5230\u4E00\u4E9B\u4E0D\u8212\u670D\u7684\u3002\u4F60\u53EF\u80FD\u4E5F\u660E\u767D\uFF0C\u8FD9\u4E9B\u90FD\u662F\u4E3A\u4E86\u4F60\u597D\uFF0C\u4F46\u4F60\u4F9D\u7136\u4F1A\u89C9\u5F97\u81EA\u5DF1\u88AB\u5192\u72AF\u4E86\u3002</p><p>\u5BF9\u6B64\uFF0C\u4E0D\u540C\u7684\u4EBA\u6709\u4E0D\u540C\u7684\u53CD\u5E94\uFF0C\u6709\u7684\u80FD\u4E00\u7B11\u800C\u8FC7\uFF0C\u6709\u7684\u5374\u66B4\u8DF3\u5982\u96F7\u2014\u2014\u867D\u7136\u5728\u7406\u667A\u4E0A\uFF0C\u4F60\u4E5F\u660E\u77E5\u540E\u8005\u7684\u505A\u6CD5\u4E0D\u53EF\u53D6\uFF0C\u4F46\u4E0B\u610F\u8BC6\u5730\uFF0C\u4F60\u5C31\u662F\u4F1A\u4EA7\u751F\u6392\u65A5\u611F\u3002</p><p>\u4E3A\u4EC0\u4E48\u4F1A\u8FD9\u6837\uFF1F</p><p>\u539F\u56E0\u5176\u5B9E\u5728\u4E8E\uFF1A<strong>\u522B\u4EBA\u5BF9\u4F60\u7684\u6279\u8BC4\uFF0C\u963B\u65AD\u4E86\u4F60\u4E3B\u89C2\u4E16\u754C\u7684\u5411\u5916\u5EF6\u4F38\u3002</strong></p><p>\u6BCF\u4E2A\u4EBA\u5728\u81EA\u5DF1\u7684\u5185\u5FC3\u91CC\uFF0C\u90FD\u6709\u7740\u4E00\u4E2A\u4E3B\u89C2\u4E16\u754C\u7684\u5B58\u5728\u3002\u5728\u522B\u4EBA\u770B\u6765\uFF0C\u5B83\u53EF\u80FD\u6F0F\u6D1E\u767E\u51FA\uFF0C\u53EF\u80FD\u62B1\u6B8B\u5B88\u7F3A\uFF0C\u4F46\u5728\u4F60\u770B\u6765\uFF0C\u90A3\u5C31\u662F\u4F60\u5BF9\u4E8E\u4E16\u754C\u7684\u8BA4\u77E5\u2014\u2014\u800C\u4E14\u8FD8\u662F\u7EDD\u5BF9\u6B63\u786E\u7684\u8BA4\u77E5\u3002</p><p>\u8FD9\u79CD\u4E3B\u89C2\u4E16\u754C\u5728\u5411\u5916\u5EF6\u4F38\u7684\u8FC7\u7A0B\u4E2D\u53EA\u8981\u9047\u963B\uFF0C\u60F3\u505A\u800C\u4E0D\u80FD\u505A\uFF0C\u60F3\u5F97\u800C\u4E0D\u80FD\u5F97\uFF0C\u5C31\u4F1A\u751F\u51FA\u632B\u6298\u611F\u3002\u800C\u8FD9\u79CD\u632B\u6298\u611F\uFF0C\u53C8\u5F88\u81EA\u7136\u5730\u53EF\u80FD\u5728\u7B2C\u4E00\u65F6\u95F4\u8F6C\u5316\u4E3A\u6124\u6012\u3002</p><p>\u597D\u4E3A\u4EBA\u5E08\u7684\u4EBA\u4E3A\u4EC0\u4E48\u8BA8\u538C\uFF1F\u5E76\u4E0D\u662F\u56E0\u4E3A\u4F60\u89C9\u5F97\u4F60\u5728\u5BF9\u65B9\u9762\u524D\u4F4E\u4EBA\u4E00\u7B49\u4E86\uFF0C\u800C\u662F\u5BF9\u65B9\u5BF9\u4F60\u7684\u4E3B\u89C2\u4E16\u754C\u505A\u51FA\u4E86\u5426\u5B9A\uFF0C\u963B\u6B62\u4E86\u4F60\u81EA\u7531\u610F\u5FD7\u7684\u968F\u5FC3\u6240\u6B32\u3002</p><p>\u8DEF\u6012\u75C7\u4E5F\u662F\u4E00\u6837\u7684\u9053\u7406\u3002\u7406\u667A\u4E0A\u6765\u8BF4\uFF0C\u522B\u4EBA\u52A0\u585E\u5BF9\u4F60\u4EA7\u751F\u7684\u5B9E\u9645\u5F71\u54CD\u5FAE\u4E4E\u5176\u5FAE\uFF0C\u4F46\u662F\uFF0C\u4F60\u9762\u5BF9\u52A0\u585E\u65F6\u6240\u4EA7\u751F\u7684\u6124\u6012\uFF0C\u5374\u8FDC\u8FDC\u8981\u9AD8\u4E8E\u5B9E\u9645\u7684\u5F71\u54CD\uFF0C\u8FD9\u5C31\u662F\u56E0\u4E3A\u4F60\u9A7E\u9A6D\u7684\u5FEB\u4E50\u548C\u987A\u7545\u88AB\u7C97\u66B4\u6253\u65AD\u4E86\u3002</p><p>**\u4E0D\u540C\u7684\u4EBA\u5BF9\u5F85\u8FD9\u79CD\u963B\u788D\uFF0C\u4F1A\u6709\u4E0D\u540C\u7684\u53CD\u5E94\u3002**\u5E74\u8F7B\u4EBA\u5C3D\u7BA1\u60C5\u7EEA\u63A7\u5236\u80FD\u529B\u5DEE\u4E00\u4E9B\uFF0C\u4F46\u5C1A\u5904\u4E8E\u5B66\u4E60\u7CBE\u529B\u65FA\u76DB\u7684\u65F6\u671F\uFF0C\u8FD9\u79CD\u963B\u788D\u53CD\u800C\u80FD\u66F4\u5BB9\u6613\u5730\u8F6C\u5316\u4E3A\u5B66\u4E60\u65B0\u4E8B\u7269\u7684\u52A8\u529B\u3002</p><p>\u76F8\u53CD\uFF0C\u4E00\u4E2A\u4EBA\u5982\u679C\u5DF2\u7ECF\u5728\u81EA\u5DF1\u7684\u8212\u9002\u533A\u5F85\u5F97\u592A\u4E45\uFF0C\u9010\u6E10\u4E27\u5931\u4E86\u63A5\u53D7\u65B0\u4E8B\u7269\u7684\u80FD\u529B\uFF0C\u4E60\u60EF\u4E8E\u8BA9\u8FC7\u5F80\u7684\u81EA\u7531\u610F\u5FD7\u56DB\u5904\u5EF6\u4F38\uFF0C\u90A3\u4E48\u4E00\u65E6\u9047\u5230\u8FD9\u79CD\u632B\u6298\uFF0C\u624D\u66F4\u5BB9\u6613\u8868\u73B0\u51FA\u62D2\u7EDD\u548C\u6124\u6012\u3002</p><p>\u540E\u8005\u8FD9\u6837\u7684\u4EBA\uFF0C\u5728\u6211\u4EEC\u7684\u751F\u6D3B\u91CC\u5176\u5B9E\u5F88\u5E38\u89C1\uFF0C\u800C\u4E14\u4E00\u822C\u96C6\u4E2D\u5728\u4E24\u79CD\u4EBA\u8EAB\u4E0A\uFF1A<strong>\u9886\u5BFC\u3001\u8001\u4EBA</strong>\u3002</p><p>\u5982\u679C\u8BF4\u8001\u4EBA\u662F\u56E0\u4E3A\u63A5\u53D7\u65B0\u4E8B\u7269\u7684\u80FD\u529B\u5DEE\uFF0C\u90A3\u4E48\u9886\u5BFC\u5C31\u662F\u5728\u81EA\u5DF1\u7684\u8212\u9002\u5708\u91CC\u5F85\u5F97\u65F6\u95F4\u592A\u957F\u4E86\uFF0C\u5DF2\u7ECF\u4E60\u60EF\u4E86\u8BA9\u5916\u754C\u4EE5\u4ED6\u7684\u8981\u6C42\u6765\u6539\u53D8\u3001\u4EE5\u4ED6\u7684\u610F\u56FE\u6765\u9002\u5E94\u3002\u4E00\u65E6\u4ED6\u53D1\u73B0\u8FD9\u79CD\u8981\u6C42\u4E0D\u80FD\u88AB\u6EE1\u8DB3\uFF0C\u81EA\u7136\u800C\u7136\u5730\u5C31\u4F1A\u7206\u53D1\u51FA\u6D2A\u8352\u4E4B\u6012\u3002</p><p><strong>\u8427\u884D\uFF0C\u6070\u6070\u662F\u8FD9\u4E24\u8005\u7684\u7ED3\u5408\u4F53\u3002</strong></p><p>\u8FD9\u4E00\u5E74\uFF0C\u8427\u884D\u5DF2\u7ECF\u516B\u5341\u591A\u5C81\uFF0C\u505A\u4E86\u56DB\u5341\u591A\u5E74\u7684\u7687\u5E1D\u3002\u66FE\u7ECF\u7684\u96C4\u624D\u5927\u7565\u65E9\u5DF2\u8FDC\u53BB\uFF0C\u7559\u4E0B\u7684\u53EA\u6709\u591A\u5E74\u6765\u5728\u5949\u627F\u4E2D\u517B\u6210\u7684\u81EA\u5F97\u610F\u6EE1\uFF0C\u865A\u5984\u9A84\u50B2\u3002\u5F53\u6709\u4EBA\u5411\u4ED6\u6307\u51FA\u4E00\u7CFB\u5217\u7684\u7F3A\u70B9\u548C\u95EE\u9898\u65F6\uFF0C\u4ED6\u611F\u53D7\u5230\u7684\u4E0D\u662F\u60C5\u51B5\u7684\u771F\u5B9E\u4E0E\u5426\uFF0C\u800C\u662F\u8D3A\u741B\u6B63\u5728\u5411\u81EA\u5DF1\u65E0\u6BD4\u6B63\u786E\u7684\u4EBA\u751F\u53D1\u8D77\u91CD\u5927\u6311\u6218\u3002</p><p>\u8FD9\u662F\u4ED6\u65E0\u8BBA\u5982\u4F55\u90FD\u4E0D\u80FD\u63A5\u53D7\u7684\u3002</p><p>\u8FD9\u79CD\u6124\u6012\uFF0C\u6211\u4EEC\u53EF\u4EE5\u4ECE\u6881\u6B66\u5E1D\u6C14\u6025\u8D25\u574F\u7684\u8C29\u9A82\u4E2D\u660E\u767D\u65E0\u8BEF\u7684\u770B\u51FA\u6765\u3002<strong>\u800C\u5728\u6124\u6012\u4E4B\u4E0B\uFF0C\u6881\u6B66\u5E1D\u5DE7\u5999\u7684\u907F\u5F00\u4E86\u8D3A\u741B\u63D0\u51FA\u7684\u6240\u6709\u95EE\u9898\u3002</strong></p><p>\u4ED6\u907F\u91CD\u5C31\u8F7B\uFF0C\u53EA\u628A\u91CD\u70B9\u96C6\u4E2D\u5728\u5BF9\u81EA\u5DF1\u6709\u5229\u7684\u70B9\u4E0A\uFF0C\u8BF4\u81EA\u5DF1\u8282\u4FED\u6734\u7D20\u3001\u8BF4\u8D3A\u741B\u5927\u800C\u65E0\u5F53\u3001\u8FD8\u8BF4\u8D3A\u741B\u54D7\u4F17\u53D6\u5BA0\u2026\u2026\u8FD9\u4E9B\u5168\u90FD\u662F\u8BDB\u5FC3\u4E4B\u8BBA\uFF0C\u4F46\u5E76\u6CA1\u6709\u5B9E\u9645\u4E0A\u53CD\u9A73\u8D3A\u741B\u7684\u89C2\u70B9\u3002</p><p>\u4E3A\u4EC0\u4E48\uFF1F</p><p>\u56E0\u4E3A\u4ED6\u53CD\u9A73\u4E0D\u4E86\u3002</p><p>\u6240\u4EE5\u6211\u4EEC\u770B\u5230\u7684\uFF0C\u624D\u53EA\u662F\u62D2\u7EDD\u3001\u53EA\u662F\u5931\u6001\u3001\u53EA\u662F\u6124\u6012\uFF0C\u53EA\u6709\u907F\u91CD\u5C31\u8F7B\u7684\u53CD\u9A73\u548C\u4EE5\u52BF\u538B\u4EBA\u7684\u5426\u8BA4\u3002</p><p>\u53F8\u9A6C\u5149\u505A\u5BB0\u76F8\u7684\u6C34\u5E73\u53EF\u80FD\u5F88\u4E00\u822C\uFF0C\u4F46\u505A\u8C0F\u5B98\u7684\u6210\u7EE9\u5374\u662F\u4F18\u79C0\u7684\uFF0C\u6240\u4EE5\u4ED6\u624D\u80FD\u6E05\u695A\u65E0\u8BEF\u7684\u6233\u4E2D\u6881\u6B66\u5E1D\u7684\u75DB\u5904\uFF1A</p><p>\u4EBA\u90FD\u4F1A\u72AF\u9519\uFF0C\u5C24\u5176\u5F53\u4F60\u8EAB\u5904\u9AD8\u4F4D\u65F6\uFF0C\u5468\u56F4\u542C\u5230\u7684\u6C38\u8FDC\u662F\u522B\u4EBA\u57FA\u4E8E\u4E0D\u540C\u52A8\u673A\u7684\u5938\u5956\u548C\u80AF\u5B9A\u3002\u8FD9\u4E9B\u6EA2\u7F8E\u4E4B\u8BCD\uFF0C\u635F\u4F24\u7684\u4E0D\u4EC5\u4EC5\u662F\u8C0E\u8A00\u91CC\u88AB\u9690\u7792\u7684\u90A3\u4E9B\u4E8B\u5B9E\uFF0C\u66F4\u8BA9\u5E1D\u738B\u7684\u4E3B\u89C2\u4E16\u754C\u81EA\u7531\u8513\u5EF6\uFF0C\u7ED9\u4ED6\u4EEC\u9020\u6210\u4E00\u79CD\u81EA\u5DF1\u65E0\u6240\u4E0D\u80FD\uFF0C\u6C38\u8FDC\u6B63\u786E\u7684\u5E7B\u89C9\u3002</p><p><strong>\u201C\u5978\u4F5E\u5C45\u524D\u800C\u4E0D\u89C1\uFF0C\u5927\u8C0B\u98A0\u9519\u800C\u4E0D\u77E5\u3002\u201D</strong></p><p>\u8FD9\u624D\u662F\u4E00\u4E2A\u56FD\u5BB6\u771F\u6B63\u7684\u5927\u707E\u96BE\u3002</p>',80),n=[r];function s(d,g){return o(),e("div",null,n)}const l=p(c,[["render",s],["__file","79.html.vue"]]);export{l as default};
