import{_ as p}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as e,c as t,f as a}from"./app.cb44c984.js";const c={},o=a('<h1 id="\u7B2C8\u96C6-\u7389\u73E0\u6C5F\u6CB3\u88AB\u8FEB\u5206\u79BB" tabindex="-1"><a class="header-anchor" href="#\u7B2C8\u96C6-\u7389\u73E0\u6C5F\u6CB3\u88AB\u8FEB\u5206\u79BB" aria-hidden="true">#</a> \u7B2C8\u96C6\uFF1A\u7389\u73E0\u6C5F\u6CB3\u88AB\u8FEB\u5206\u79BB</h1><p>\u9A86\u7389\u73E0\u5FC3\u6025\u5982\u711A\uFF0C\u56DB\u5904\u5BFB\u627E\u9A86\u5927\u529B\u7684\u4E0B\u843D\u3002\u5374\u5230\u5904\u4E5F\u627E\u4E0D\u5230\uFF0C\u9A86\u7389\u73E0\u6B32\u54ED\u65E0\u6CEA\u3002\u6DF1\u89C9\u5BF9\u4E0D\u8D77\u9648\u6C5F\u6CB3\u3002\u6C5F\u6CB3\u88AB\u82F1\u6770\u4E00\u53E5\u8BDD\u70B9\u9192\uFF0C\u76F8\u4FE1\u7389\u73E0\u80AF\u5B9A\u662F\u6709\u4EC0\u4E48\u6765\u4E0D\u53CA\u89E3\u91CA\u7684\u82E6\u8877\u3002\u996D\u4E5F\u4E0D\u5403\uFF0C\u5230\u7389\u73E0\u5BB6\u95E8\u53E3\u5B88\u7740\u5979\uFF0C\u7ED3\u679C\u6574\u6574\u7B49\u4E86\u4E00\u591C\u90FD\u6CA1\u89C1\u5979\u56DE\u6765\u3002\u800C\u8FD9\u65F6\uFF0C\u7389\u73E0\u4E00\u6574\u591C\u90FD\u5728\u5916\u9762\u5BFB\u627E\u9A86\u5927\u529B\u3002</p><p>\u9648\u6C5F\u6CB3\u8FDE\u7EED\u51E0\u5929\u75AF\u4E86\u4E00\u6837\u7684\u5BFB\u627E\u9A86\u7389\u73E0\uFF0C\u800C\u9648\u91D1\u6C34\u5219\u5E26\u7740\u9648\u5BB6\u6751\u7684\u6751\u6C11\u8DDF\u5728\u540E\u9762\uFF0C\u8981\u628A\u9648\u6C5F\u6CB3\u5E26\u56DE\u9648\u5BB6\u6751\uFF0C\u9648\u6C5F\u6CB3\u5374\u575A\u6301\u4E00\u5B9A\u8981\u627E\u5230\u9A86\u7389\u73E0\u3002</p><p>\u7389\u73E0\u7EC8\u4E8E\u627E\u5230\u9A86\u5927\u529B\uFF0C\u4E24\u4E2ADu\u573A\u7684\u6253\u624B\u6B63\u5728\u6BB4\u6253\u9A86\u5927\u529B\uFF0C\u60F3\u62A2\u593A\u5927\u529B\u624B\u4E0A\u7684\u94B1\uFF0C\u6DF7\u4E71\u4E2D\uFF0C\u7389\u73E0\u88AB\u6253\u6655\u3002</p><p>\u6C5F\u6CB3\u7ECF\u8FC7\u7389\u73E0\u5BB6\u95E8\u53E3\uFF0C\u53D1\u73B0\u95E8\u5F00\u4E86\uFF0C\u672C\u4EE5\u4E3A\u662F\u7389\u73E0\u56DE\u6765\u4E86\u3002\u6CA1\u60F3\u5230\u5374\u662F\u516C\u5B89\u5C40\u7684\u4EBA\uFF0C\u8BF4\u662F\u6709\u4EBA\u62A5\u6848\u79F0\u9A86\u7389\u73E0\u6D89\u5ACC\u8BC8\u9A97\uFF0C\u516C\u5B89\u5C40\u5DF2\u7ECF\u7ACB\u6848\u8C03\u67E5\u3002\u9648\u5BB6\u6751\u91CC\uFF0C\u9648\u91D1\u6C34\u53EC\u96C6\u4F17\u4EBA\uFF0C\u6572\u7740\u684C\u5B50\u8D28\u95EE\u662F\u8C01\u62A5\u7684\u6848\u3002\u539F\u6765\u6B63\u662F\u67F1\u5B50\u3002\u4ED6\u62C5\u5FC3\u81EA\u5DF1\u7684\u94B1\u56DE\u4E0D\u6765\uFF0C\u4E8E\u662F\u544A\u53D1\u4E86\u9A86\u7389\u73E0\u3002\u9648\u91D1\u6C34\u62C5\u5FC3\u8FD9\u6837\u4F1A\u6BC1\u4E86\u6C5F\u6CB3\u7684\u524D\u7A0B\u3002</p><p>\u6C5F\u6CB3\u5411\u90B1\u82F1\u6770\u501F\u4E86\u81EA\u884C\u8F66\uFF0C\u8D76\u5F80\u9648\u5BB6\u6751\uFF0C\u521A\u8D70\u6CA1\u591A\u4E45\uFF0C\u5931\u8E2A\u51E0\u5929\u7684\u7389\u73E0\u51FA\u73B0\u5728\u53BF\u59D4\u95E8\u53E3\uFF0C\u539F\u6765\uFF0C\u7389\u73E0\u662F\u5E26\u7740\u4EC5\u5269\u7684\u94B1\u56DE\u6765\u627E\u9648\u6C5F\u6CB3\u7684\u3002\u90B1\u82F1\u6770\u5927\u60CA\uFF0C\u544A\u8BC9\u5979\u6C5F\u6CB3\u56DE\u9648\u5BB6\u6751\u4E86\u3002</p><p>\u6C5F\u6CB3\u56DE\u5230\u9648\u5BB6\u6751\uFF0C\u62FF\u51FA\u4E86\u81EA\u5DF1\u7684\u5168\u90E8\u5B58\u6B3E\uFF0C\u60F3\u8981\u5C06\u6240\u6709\u7684\u503A\u52A1\u90FD\u627F\u62C5\u4E0B\u6765\uFF0C\u8BF4\u8BDD\u95F4\uFF0C\u9648\u91D1\u6C34\u63A5\u5230\u4E86\u90B1\u82F1\u6770\u6253\u6765\u7684\u7535\u8BDD\uFF0C\u90B1\u82F1\u6770\u8BF7\u4ED6\u8F6C\u544A\u9648\u6C5F\u6CB3\uFF0C\u9A86\u7389\u73E0\u6765\u627E\u4ED6\u4E86\u3002\u9648\u91D1\u6C34\u542C\u5230\u8FD9\u4E2A\u6D88\u606F\uFF0C\u5FC3\u91CC\u5374\u6709\u4E86\u81EA\u5DF1\u7684\u8BA1\u5212\u3002</p><p>\u653E\u4E0B\u7535\u8BDD\uFF0C\u9648\u91D1\u6C34\u8F6C\u5934\u4FBF\u547D\u4EBA\u5C06\u9648\u6C5F\u6CB3\u5173\u8D77\u6765\u3002\u4F17\u4EBA\u867D\u7136\u5341\u5206\u4E0D\u89E3\u4E3A\u4EC0\u4E48\u9648\u91D1\u6C34\u7A81\u7136\u6539\u53D8\u4E86\u6001\u5EA6\uFF0C\u4F46\u8FD8\u662F\u5C06\u9648\u6C5F\u6CB3\u9501\u8FDB\u4E86\u5C4B\u5B50\u91CC\u3002</p><p>\u9A86\u7389\u73E0\u8D76\u5230\u9648\u5BB6\u6751\u662F\uFF0C\u53EA\u89C1\u5230\u4E86\u9648\u91D1\u6C34\uFF0C\u9648\u91D1\u6C34\u544A\u8BC9\u5979\uFF0C\u662F\u6C5F\u6CB3\u62A5\u7684\u8B66\u3002\u8FD8\u8BF4\u6C5F\u6CB3\u548C\u5DE7\u59D1\u51C6\u5907\u7ED3\u5A5A\u4E86\u3002\u7389\u73E0\u4E0D\u4FE1\uFF0C\u9648\u91D1\u6C34\u8F6F\u786C\u517C\u65BD\uFF0C\u8981\u7389\u73E0\u4E3A\u4E86\u6C5F\u6CB3\u7684\u524D\u7A0B\u5C06\u6240\u6709\u7684\u4E8B\u60C5\u90FD\u627F\u62C5\u4E0B\u6765\u3002\u5E76\u4E14\u6C38\u8FDC\u79BB\u5F00\u3002</p><p>\u9648\u91D1\u6C34\u628A\u81EA\u5BB6\u7684\u65B0\u623F\u5356\u7ED9\u4E86\u67F1\u5B50\uFF0C\u66FF\u9648\u6C5F\u6CB3\u8FD8\u4E0A\u4E86\u503A\u52A1\u3002\u903C\u8FEB\u6C5F\u6CB3\u5A36\u5DE7\u59D1\u4E3A\u59BB\u3002\u9648\u6C5F\u6CB3\u65E0\u8BBA\u5982\u4F55\u4E5F\u4E0D\u80AF\u7B54\u5E94\u3002\u4F46\u6267\u62D7\u7684\u9648\u91D1\u6C34\u4F9D\u7136\u5F00\u59CB\u7B79\u5907\u5A5A\u793C\u3002</p><p>\u665A\u4E0A\uFF0C\u8D1F\u8D23\u770B\u5B88\u9648\u6C5F\u6CB3\u7684\u67F1\u5B50\u6709\u4E8B\u79BB\u5F00\uFF0C\u4E34\u8D70\u524D\u5FD8\u8BB0\u4E86\u9501\u95E8\uFF0C\u6C5F\u6CB3\u987A\u5229\u7684\u79BB\u5F00\u623F\u95F4\uFF0C\u6B63\u5DE7\u63A5\u5230\u4E86\u90B1\u82F1\u6770\u7684\u7535\u8BDD\uFF0C\u8FD9\u624D\u77E5\u9053\u7389\u73E0\u66FE\u7ECF\u6765\u8FC7\u3002</p><p>\u9A86\u7389\u73E0\u6DF1\u591C\u8DEF\u8FC7\u9648\u5BB6\u6751\uFF0C\u542C\u5230\u6751\u6C11\u95F2\u804A\uFF0C\u8BF4\u8D77\u5DE7\u59D1\u548C\u6C5F\u6CB3\u7684\u5A5A\u4E8B\u3002\u7389\u73E0\u9707\u60CA\uFF0C\u51B2\u8FDB\u9648\u5BB6\u6751\uFF0C\u6CA1\u60F3\u5230\u5374\u4EB2\u773C\u770B\u5230\u5DE7\u59D1\u548C\u6C5F\u6CB3\u76F8\u62E5\u5728\u4E00\u8D77\uFF0C\u7B80\u76F4\u4E0D\u6562\u76F8\u4FE1\u81EA\u5DF1\u7684\u773C\u775B\u3002\u7389\u73E0\u5FC3\u7070\u610F\u51B7\uFF0C\u8F6C\u8EAB\u79BB\u5F00\u3002</p><p>\u5374\u539F\u6765\u53EA\u662F\u8BEF\u4F1A\uFF0C\u6C5F\u6CB3\u79BB\u5F00\u770B\u5B88\u4ED6\u7684\u5C4B\u5B50\uFF0C\u6B63\u9047\u5230\u8981\u51B3\u5FC3\u8981\u548C\u5927\u5149\u79C1\u5954\u7684\u5DE7\u59D1\u3002\u5DE7\u59D1\u548C\u6C5F\u6CB3\u62E5\u62B1\u53EA\u662F\u4E3A\u4E86\u544A\u522B\u3002\u6CA1\u60F3\u5230\u4F1A\u88AB\u7389\u73E0\u770B\u5728\u773C\u91CC\u3002</p><p>\u6C5F\u6CB3\u548C\u9648\u91D1\u6C34\u7EC8\u4E8E\u5927\u5435\u4E00\u67B6\uFF0C\u8F6C\u5934\u79BB\u5F00\u4E86\u9648\u5BB6\u6751\u3002\u4ED6\u8981\u8D76\u5FEB\u53BB\u627E\u5230\u7389\u73E0\u3002\u53EA\u662F\u4ED6\u6765\u5230\u96C6\u5E02\u4E0A\u624D\u77E5\u9053\u3002\u7389\u73E0\u5C06\u6240\u6709\u7684\u8D27\u7269\u90FD\u8F6C\u624B\u7ED9\u4E86\u51AF\u59D0\uFF0C\u5E76\u5C06\u6240\u6709\u7684\u94B1\u5C31\u7ED9\u4E86\u9648\u6C5F\u6CB3\uFF0C\u53EA\u8EAB\u79BB\u5F00\uFF0C\u6CA1\u6709\u4EBA\u77E5\u9053\u5979\u53BB\u4E86\u54EA\u91CC\u3002</p>',14),r=[o];function _(n,s){return e(),t("div",null,r)}const h=p(c,[["render",_],["__file","8.html.vue"]]);export{h as default};
