import{_ as e}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as t,c,a as p,b as r,d as n,f as a,r as s}from"./app.cb44c984.js";const l={},d=a('<h1 id="\u5218\u5907" tabindex="-1"><a class="header-anchor" href="#\u5218\u5907" aria-hidden="true">#</a> \u5218\u5907</h1><blockquote><p>\u521D\uFF0C\u8BFC\u90E1\u5218\u5907\uFF0C\u4E2D\u5C71\u9756\u738B\u4E4B\u540E\u4E5F\uFF0C\u5C11\u5B64\u8D2B\uFF0C\u4E0E\u6BCD\u4EE5\u8D29\u4E3A\u4E1A\uFF0C\u957F\u4E03\u5C3A\u4E94\u5BF8\uFF0C\u5782\u624B\u4E0B\u819D\uFF0C\u987E\u81EA\u513F\u5176\u8033\uFF1B\u6709\u5927\u5FD7\uFF0C\u5C11\u8BED\u8A00\uFF0C\u8881\u6012\u4E0D\u5F62\u4E8E\u8272\u3002</p></blockquote><p><strong>\u300A\u8D44\u6CBB\u901A\u9274 \xB7 \u5377\u516D\u5341 \xB7 \u6C49\u7EAA\u4E94\u5341\u4E8C\u300B</strong></p><p>\u5728\u66F9\u64CD\u4E0E\u5B59\u575A\u5DF2\u7ECF\u8868\u6F14\u4E86\u597D\u51E0\u573A\u4EE5\u540E\uFF0C<strong>\u5218\u5907\u7EC8\u4E8E\u4E5F\u767B\u573A\u4E86</strong>\u3002</p><p>\u53EA\u662F\uFF0C\u8FD9\u4E2A\u573A\u767B\u7684\u5B9E\u5728\u662F\u5C34\u5C2C\u3002\u53F8\u9A6C\u5149\u5927\u7EA6\u662F\u89C9\u5F97\uFF0C\u7687\u53D4\u600E\u4E48\u8BF4\u4E5F\u662F\u63A5\u4E0B\u6765\u7684\u4E3B\u4EBA\u516C\u4E4B\u4E00\uFF0C\u6545\u4E8B\u90FD\u5DF2\u7ECF\u8FDB\u884C\u5230\u4E86\u8FD9\u91CC\uFF0C\u8FD8\u4E0D\u9732\u9762\u6015\u662F\u8BF4\u4E0D\u8FC7\u53BB\uFF0C\u4E8E\u662F\u5C31\u5B89\u6392\u5218\u5907\u5728\u300A\u8D44\u6CBB\u901A\u9274\u300B\u516D\u5341\u5377\u91CC\u8DD1\u4E86\u4E2A\u9F99\u5957\u2014\u2014\u771F\u7684\u5C31\u53EA\u4E32\u4E86\u4E00\u573A\u620F\uFF0C\u5C31\u53BB\u9886\u76D2\u996D\u4E86\u3002</p><p>\u5728\u300A\u8D44\u6CBB\u901A\u9274\u300B\u4E2D\uFF0C\u5218\u5907\u6240\u5360\u7684\u7BC7\u5E45\u6BD4\u91CD\uFF0C\u548C\u300A\u4E09\u56FD\u6F14\u4E49\u300B\u5B8C\u5168\u6CA1\u6CD5\u6BD4\u3002\u4E3A\u4EC0\u4E48\uFF1F\u56E0\u4E3A\u5218\u5907\u8FD9\u65F6\u5019\u771F\u6CA1\u4EC0\u4E48\u53EF\u5199\u7684\uFF1A</p><p><strong>\u4ED6\u51FA\u8EAB\u5B9E\u5728\u592A\u5DEE\uFF0C\u6B64\u65F6\u8FD8\u5728\u8270\u96BE\u7684\u8FDB\u884C\u7740\u539F\u59CB\u79EF\u7D2F\u3002</strong></p><p>\u8BF4\u5230\u5218\u5907\uFF0C\u5C31\u4E0D\u5F97\u4E0D\u63D0\u53CA\u4ED6\u7684\u51FA\u8EAB\u3002\u5218\u5907\u7684\u65E9\u5E74\u5F88\u662F\u8F9B\u82E6\uFF0C\u6CA1\u6709\u542C\u8FC7\u8FC7\u4ED6\u6709\u4EC0\u4E48\u5144\u5F1F\u59D0\u59B9\uFF0C\u7236\u4EB2\u53C8\u65E9\u901D\uFF0C\u4ECE\u5C0F\u548C\u6BCD\u4EB2\u76F8\u4F9D\u4E3A\u547D\u5356\u8349\u978B\uFF0C\u662F\u771F\u6B63\u7684\u5B64\u513F\u5BE1\u6BCD\u3002\u8FD9\u79CD\u51FA\u8EAB\uFF0C\u6401\u5386\u4EE3\u7684\u5F00\u56FD\u7687\u5E1D\u4E2D\u90FD\u4E0D\u591A\u89C1\u2014\u2014\u4E0D\u662F\u8BF4\u53E4\u4EE3\u793E\u4F1A\u6B67\u89C6\u52B3\u52A8\u4EBA\u6C11\uFF0C\u800C\u662F\u54EA\u6015\u5230\u4E86\u73B0\u4EE3\u793E\u4F1A\uFF0C\u5927\u4F01\u4E1A\u7684CEO\u4E5F\u5F88\u5C11\u6709\u90A3\u79CD\u4ECE\u5C0F\u5C31\u8DDF\u7740\u9EBB\u9EBB\u7EC3\u644A\u7684\u3002</p><blockquote><p>\u5218\u5907\u5373\u4F4D\u4EE5\u540E\uFF0C\u6309\u7167\u60EF\u4F8B\u8981\u7ACB\u5B97\u5E99\u3002\u8FD9\u91CC\u53EF\u4EE5\u628A\u4ED6\u548C\u5218\u79C0\u505A\u4E00\u4E2A\u5BF9\u6BD4\uFF1A\u5218\u79C0\u4ECE\u4ED6\u7238\u5F00\u59CB\uFF0C\u5F80\u4E0A\u7ACB\u4E86\u56DB\u4EE3\u7956\u5148\u3002\u800C\u5218\u5907\u5462\uFF1F\u4E00\u4E2A\u90FD\u6CA1\u6709\uFF0C\u4ED6\u7684\u5B97\u5E99\u91CC\u7ACB\u7684\u662F\u4E24\u6C49\u7684\u5386\u4EE3\u7687\u5E1D\uFF0C\u6CA1\u6709\u4E00\u4E2A\u662F\u4ED6\u81EA\u5DF1\u7684\u76F4\u7CFB\u4EB2\u5C5E\u3002 \u8FD9\u4ECE\u53E6\u4E00\u4E2A\u4FA7\u9762\uFF0C\u53CD\u6620\u51FA\u5218\u5907\u5BB6\u9053\u4E2D\u843D\u5DF2\u7ECF\u5F88\u591A\u4EE3\u4E86\u3002\u6240\u4EE5\u4ED6\u53E3\u53E3\u58F0\u58F0\u53F7\u79F0\u81EA\u5DF1\u662F\u201C\u4E2D\u5C71\u9756\u738B\u4E4B\u540E\uFF0C\u5B5D\u666F\u7687\u5E1D\u9601\u4E0B\u7384\u5B59\u201D\uFF0C\u662F\u5E76\u6CA1\u6709\u4EC0\u4E48\u8BC1\u636E\u7684\u3002\u8FD9\u4E2A\u5B97\u5BA4\u7684\u8EAB\u4EFD\uFF0C\u6709\u4E0D\u5C11\u6C34\u5206\u5728\u91CC\u9762\u3002\uFF08\u4E2D\u5C71\u9756\u738B\u5218\u80DC\u662F\u6C49\u6B66\u5E1D\u7684\u5F02\u6BCD\u5144\uFF0C\u6709\u4E00\u767E\u4E8C\u5341\u591A\u4E2A\u513F\u5B50\u3002\uFF09</p></blockquote><p>\u7136\u800C\uFF0C\u518D\u7A77\u4E0D\u80FD\u7A77\u6559\u80B2\uFF0C\u518D\u82E6\u4E0D\u80FD\u82E6\u5B69\u5B50\u3002\u5728\u5218\u5907\u8EAB\u4E0A\uFF0C\u5218\u8001\u592A\u592A\u505A\u5F97\u6700\u6210\u529F\u7684\u4E00\u7B14\u6295\u8D44\uFF0C\u5E94\u8BE5\u662F\u9001\u5218\u5907\u53BB\u6C42\u5B66\uFF0C\u8FD9\u4E5F\u662F\u771F\u6B63\u6539\u53D8\u4E86\u5218\u5907\u4EBA\u751F\u547D\u8FD0\u7684\u51B3\u5B9A\u3002</p><p>\u5218\u5907\u6C42\u5B66\u7684\u5BF9\u8C61\uFF0C\u662F\u65F6\u4EFB\u4E5D\u6C5F\u592A\u5B88\u7684\u540C\u4E61\u524D\u8F88\u5362\u690D\u3002\u5728\u8FD9\u91CC\uFF0C\u4ED6\u7ED3\u4EA4\u4E86\u540E\u6765\u5BF9\u4ED6\u5E2E\u52A9\u5F88\u5927\u7684\u540C\u5B66\u2014\u2014<strong>\u516C\u5B59\u74D2</strong>\u3002</p><p>\u5218\u5907\u65E9\u5E74\u8DDF\u7740\u4E9B\u4E0D\u77E5\u540D\u7684\u4EBA\u6DF7\uFF0C\u540E\u6765\u8D70\u6295\u65E0\u8DEF\uFF0C\u5C31\u6295\u5954\u4E86\u516C\u5B59\u74D2\u3002\u516C\u5B59\u74D2\u6D3E\u4ED6\u8DDF\u7740\u9752\u5DDE\u523A\u53F2\u7530\u6977\u6253\u8881\u7ECD\uFF0C\u53EF\u80FD\u6218\u529F\u8FD8\u884C\uFF0C\u5C31\u59D4\u4EFB\u4ED6\u505A\u4E86\u5E73\u539F\u4EE4\u2014\u2014\u6CA1\u9519\uFF0C\u4F60\u5728\u5404\u79CD\u4E09\u56FD\u6E38\u620F\u91CC\u73A9\u7684\u5218\u5907\uFF0C\u4ED6\u7684\u5E73\u539F\u90E1\u5C31\u662F\u516C\u5B59\u74D2\u7ED9\u4ED6\u7684\u3002</p><p>\u5218\u5907\u771F\u6B63\u7684\u7684\u53D1\u8FF9\uFF0C\u4E5F\u6B63\u662F\u4ECE\u8FD9\u91CC\u5F00\u59CB\u3002\u5F53\u5E73\u539F\u4EE4\u7684\u65F6\u5019\uFF0C\u6709\u4E00\u6B21\uFF0C\u5317\u6D77\u592A\u5B88\u5B54\u878D\u53D7\u9EC4\u5DFE\u8D3C\u56F4\u56F0\uFF0C\u5411\u5218\u5907\u6C42\u6551\u3002\u5218\u5907\u4E00\u542C\uFF0C\u5F00\u5FC3\u5F97\u4E0B\u5DF4\u90FD\u8981\u6389\u4E0B\u6765\u4E86\uFF1A\u7EB3\u5C3C\uFF01\u5B54\u5317\u6D77\u5C45\u7136\u8FD8\u77E5\u9053\u4E16\u95F4\u6709\u6211\u5218\u5907\u8FD9\u4E48\u4E2A\u5C0F\u4EBA\u7269\uFF1F\uFF01</p><p>\u5B54\u878D\u51FA\u8EAB\u9AD8\u8D35\uFF0C\u53C8\u662F\u5C11\u5E74\u6210\u540D\uFF0C\u5F53\u65F6\u5DF2\u7ECF\u540D\u95FB\u5929\u4E0B\u4E86\u3002\u6240\u4EE5\u5218\u5907\u624D\u4F1A\u56E0\u4E3A\u5B54\u878D\u7684\u6C42\u6551\u800C\u6FC0\u52A8\u5F97\u65B9\u5BF8\u5927\u4E71\u3002\u8BD5\u60F3\u4E00\u4E0B\uFF0C\u601D\u806A\u6709\u5929\u8DDF\u4F60\u8BF4\u4ED6\u751F\u610F\u4E0A\u9047\u5230\u4E86\u70B9\u9EBB\u70E6\uFF0C\u9700\u8981\u4F60\u5E2E\u5FD9\uFF0C\u4F60\u662F\u4E0D\u662F\u4E5F\u4F1A\u6FC0\u52A8\u5F97\u4E0D\u8981\u4E0D\u8981\u7684\uFF1F</p><p>\u5218\u5907\u56E0\u6B64\u7ED3\u8BC6\u4E86\u5B54\u878D\uFF0C\u800C\u5B54\u878D\u540E\u6765\u4E5F\u9001\u4E86\u5218\u5907\u4E00\u4EFD\u5927\u793C\uFF1A\u51E0\u5E74\u4EE5\u540E\uFF0C\u66F9\u64CD\u5F81\u8BA8\u5F90\u5DDE\u7267\u9676\u8C26\uFF0C\u7530\u6977\u5E26\u7740\u5218\u5907\u7387\u5175\u63F4\u52A9\u3002\u7ECF\u5B54\u878D\u7684\u4E00\u5E2D\u529D\u544A\uFF0C\u9676\u8C26\u7559\u4E0B\u4E86\u5218\u5907\u3002</p><p>\u5E78\u798F\u6765\u5F97\u731D\u4E0D\u53CA\u9632\uFF0C\u8F6C\u5E74\uFF0C\u9676\u8C26\u75C5\u6B7B\uFF0C\u5218\u5907\u7EE7\u4EFB\u6210\u4E3A\u4E86\u5F90\u5DDE\u523A\u53F2\u3002\u867D\u7136\u662F\u81EA\u5C01\u7684\uFF0C\u4F46\u6BD5\u7ADF\u662F\u5C01\u7586\u5927\u540F\uFF0C\u800C\u4E14\u662F\u5B9E\u9886\uFF08\u638C\u63E1\u6709\u5B9E\u9645\u63A7\u5236\u6743\uFF09\u3002\u8FD9\u4E00\u5E74\uFF0C<strong>\u5218\u590735\u5C81\u3002\u81EA23\u5C81\u4ECE\u519B\u4EE5\u6765\uFF0C\u4E00\u76F4\u9ED8\u9ED8\u65E0\u95FB\u7684\u5218\u5907\u7EC8\u4E8E\u8FCE\u6765\u4E86\u4EBA\u751F\u7684\u771F\u6B63\u8F6C\u673A\u3002</strong></p><p>\u5218\u5907\u6361\u4E86\u8FD9\u4E48\u5927\u4E2A\u9985\u997C\uFF0C\u81EA\u7136\u4F1A\u5F15\u8D77\u6240\u6709\u4EBA\u7684\u773C\u7EA2\u3002\u5148\u662F\u8881\u672F\uFF0C\u7136\u540E\u662F\u5415\u5E03\uFF0C\u6CA1\u51E0\u5E74\uFF0C\u5218\u5907\u5C31\u88AB\u8D76\u51FA\u4E86\u5F90\u5DDE\u3002\u6700\u540E\uFF0C\u5218\u5907\u53EA\u597D\u6295\u9760\u66F9\u64CD\uFF0C<code>\u7B2C\u56DB\u6B21</code>\u8FC7\u8D77\u5BC4\u4EBA\u7BF1\u4E0B\u7684\u751F\u6D3B\uFF08\u4E2D\u95F4\u8FD8\u66FE\u77ED\u6682\u7684\u6295\u9760\u8FC7\u5415\u5E03\uFF09\u3002</p><p>\u66F9\u64CD\u548C\u5218\u5907\u7684\u5176\u4ED6\u524D\u4EFB\u4E00\u6837\uFF0C\u5BF9\u5218\u5907\u4E00\u5982\u65E2\u5F80\u7684\u597D\u3002\u5218\u5907\u540E\u6765\u4E00\u987E\u8305\u5E90\u7684\u65F6\u5019\uFF0C\u8BF4\u7684\u90A3\u4E00\u4E32\u5B98\u540D\u2014\u2014\u5DE6\u5C06\u519B\u5B9C\u57CE\u4EAD\u4FAF\u9886\u8C6B\u5DDE\u7267\uFF0C\u90FD\u662F\u66F9\u64CD\u9001\u4ED6\u7684\u3002\u5176\u4E2D\uFF0C\u5DE6\u5C06\u519B\u548C\u8C6B\u5DDE\u7267\u867D\u7136\u662F\u865A\u9886\u7684\uFF0C\u4F46\u5374\u662F\u671D\u5EF7\u5C01\u7684\uFF0C\u800C\u4E14\u8FD8\u90FD\u5F88\u5927\uFF0C\u76F8\u5F53\u4E8E\u4E1C\u90E8\u6218\u533A\u53F8\u4EE4\u5458\u517C\u6CB3\u5357\u7701\u59D4\u4E66\u8BB0\u3002</p><p>\u65E0\u5948\uFF0C\u66F9\u64CD\u867D\u7136\u5BF9\u5218\u5907\u4E00\u5F80\u60C5\u6DF1\uFF0C\u4F46\u5218\u5907\u79C1\u4E0B\u91CC\u5374\u548C\u522B\u4EBA\u7709\u6765\u773C\u53BB\uFF0C\u5077\u5077\u7684\u548C\u8F66\u9A91\u5C06\u519B\u8463\u627F\u5BC6\u8C0B\u6740\u66F9\u64CD\u3002\u716E\u9152\u8BBA\u82F1\u96C4\u540E\uFF0C\u5218\u5907\u77E5\u9053\u4E86\u66F9\u64CD\u8BA4\u4E3A\u4ED6\u4EEC\u4FE9\u662F\u5929\u4E0B\u552F\u4E8C\u7684\u82F1\u96C4\uFF0C\u76CA\u53D1\u4E0D\u81EA\u5B89\uFF0C\u4FBF\u627E\u4E86\u4E2A\u501F\u53E3\uFF0C\u5E26\u5175\u6E9C\u4E86\u51FA\u6765\uFF0C\u53C8\u628A\u5F90\u5DDE\u7ED9\u9738\u5360\u4E86\u3002</p><p>\u8FD9\u6B21\u66F9\u64CD\u662F\u771F\u7684\u751F\u6C14\u4E86\uFF0C\u51FA\u624B\u5C31\u628A\u5218\u5907\u6253\u5F97\u627E\u4E0D\u7740\u7259\uFF0C\u4E22\u4E86\u4E8C\u5F1F\u4E09\u5F1F\u4E0D\u8BF4\uFF0C\u4FE9\u8001\u5A46\u4E5F\u88AB\u66F9\u64CD\u4FD8\u864F\u4E86\u3002\u5218\u5907\u832B\u7136\u56DB\u987E\uFF0C\u548B\u6574\uFF1F\u5F97\u7F6A\u4E86\u66F9\u64CD\u53EF\u4E0D\u662F\u5C0F\u4E8B\u554A\uFF0C\u5FC5\u987B\u8D76\u7D27\u627E\u4E00\u4E2A\u66F9\u64CD\u60F9\u4E0D\u8D77\u7684\u4EBA\u6295\u9760\uFF01</p><p>\u627E\u8C01\u5462\uFF1F\u5F53\u7136\u662F\u8881\u7ECD<code>\uFF08\u5BC4\u4EBA\u7BF1\u4E0B+5\uFF09</code>\u3002</p><p>\u5218\u5907\u7684\u76EE\u7684\u5730\u662F\u9752\u5DDE\uFF0C\u56E0\u4E3A\u5F53\u65F6\u7684\u9752\u5DDE\u523A\u53F2\u662F\u8881\u7ECD\u7684\u513F\u5B50\u2014\u2014\u8881\u8C2D\u3002\u8881\u8C2D\u542C\u8BF4\u5218\u5907\u6765\u4E86\uFF0C\u5E26\u4E86\u597D\u591A\u4EBA\u9A6C\uFF0C\u51FA\u57CE\u597D\u8FDC\u53BB\u8FCE\u63A5\u3002</p><p>\u4E00\u5B9A\u6709\u7B52\u5B50\u4F1A\u7591\u60D1\uFF1A\u5218\u5907\u4E4B\u524D\u4E0D\u662F\u8DDF\u7740\u7530\u6977\u548C\u8881\u7ECD\u5E72\u67B6\u5417\uFF0C\u4E3A\u4EC0\u4E48\u8881\u8C2D\u5BF9\u5218\u5907\u4F1A\u662F\u8FD9\u4E2A\u6001\u5EA6\uFF1F</p><p>\u300A\u4E09\u56FD\u5FD7\u300B\u8BF4\u5230\u8FD9\u91CC\uFF0C\u52A0\u4E86\u8FD9\u4E48\u4E00\u53E5\u8BDD\uFF0C\u201C\u9752\u5DDE\u523A\u53F2\u8881\u8C2D\uFF0C\u5148\u4E3B\u6545\u8302\u624D\u4E5F\u201D\uFF0C\u4E5F\u5C31\u662F\u8BF4\uFF0C\u8881\u8C2D\u662F\u5218\u5907\u4E3E\u7684\u8302\u624D\u3002</p><p>\u4EC0\u4E48\u610F\u601D\uFF1F</p><blockquote><p>\u6C49\u4EE3\u7684\u5B98\u540F\u9009\u62D4\uFF0C\u5B9E\u884C\u201C\u5BDF\u4E3E\u5236\u201D\u3002\u7B80\u5355\u6765\u8BF4\uFF0C\u5C31\u662F\u4E00\u7EA7\u4E00\u7EA7\u7684\u5730\u65B9\u5B98\u901A\u8FC7\u4E00\u4E9B\u6807\u51C6\uFF0C\u4ECE\u666E\u901A\u8001\u767E\u59D3\u91CC\u9009\u62D4\u51FA\u4E00\u4E9B\u6770\u51FA\u7684\u4EBA\u624D\u53BB\u505A\u5B98\u3002\u8FD9\u4E9B\u9009\u62D4\u51FA\u6765\u7684\u4EBA\uFF0C\u6709\u7684\u53EB\u5B5D\u5EC9\uFF0C\u6709\u7684\u53EB\u79C0\u624D\u3002\u540E\u6765\u4E1C\u6C49\u4E3A\u907F\u5218\u79C0\u7684\u8BB3\uFF0C\u6539\u79C0\u624D\u4E3A\u8302\u624D\u3002</p></blockquote><blockquote><p>\u5728\u5F53\u65F6\uFF0C\u4E00\u4E2A\u4EBA\u60F3\u8981\u505A\u5B98\uFF0C\u82E5\u662F\u6CA1\u6709\u519B\u529F\u6216\u8005\u5148\u8F88\u4F20\u4E0B\u6765\u7684\u7235\u4F4D\uFF0C\u5C31\u90FD\u5F97\u8D70\u8FD9\u6761\u8DEF\u3002\u800C\u50CF\u8881\u6C0F\u8FD9\u6837\u7684\u9AD8\u95E8\u5927\u9600\uFF0C\u751A\u81F3\u662F\u770B\u4E0D\u4E0A\u90A3\u4E24\u79CD\u51FA\u8EAB\u7684\uFF0C\u4E00\u5B9A\u8981\u4E3E\u8302\u624D\uFF0C\u624D\u7B26\u5408\u5176\u8BD7\u793C\u4F20\u5BB6\u7684\u6C14\u8D28\u3002</p></blockquote><p>\u90A3\u8881\u6C0F\u548C\u5218\u5907\u53C8\u662F\u600E\u4E48\u626F\u4E0A\u8FD9\u5C42\u5173\u7CFB\u5462\uFF1F</p><p>\u5927\u5BB6\u5728\u5730\u56FE\u4E0A\u4E00\u6BD4\u5212\u5C31\u77E5\u9053\u4E86\uFF1A\u8881\u6C0F\u662F\u6C5D\u5357\u6C5D\u9633\u4EBA\uFF0C\u5728\u5730\u7406\u4E0A\u5C5E\u4E8E\u8C6B\u5DDE\u3002\u770B\u5230\u4E86\u5427\uFF0C\u5218\u7384\u5FB7\u90A3\u8C6B\u5DDE\u7267\u7684\u865A\u804C\uFF0C\u867D\u7136\u4E0D\u80FD\u76F4\u63A5\u5BF9\u5F53\u5730\u8FDB\u884C\u884C\u653F\u6CBB\u7406\uFF0C\u4F46\u5374\u63E1\u6709\u9009\u62D4\u4EBA\u624D\u7684\u6743\u529B\u3002<strong>\u5218\u5907\u5728\u66F9\u64CD\u90A3\u91CC\uFF0C\u4E00\u65B9\u9762\u5403\u7740\u66F9\u64CD\u7684\u996D\uFF0C\u4E00\u65B9\u9762\u62C6\u7740\u66F9\u64CD\u7684\u53F0</strong>\uFF0C\u79C1\u4E0B\u91CC\u548C\u8463\u627F\u6697\u901A\u6B3E\u66F2\u4E0D\u8BF4\uFF0C\u8FD8\u5356\u4E86\u8881\u7ECD\u8FD9\u4E48\u4E2A\u4EBA\u60C5\uFF0C\u771F\u662F\u516B\u9762\u73B2\u73D1\u3002</p><p>\u5218\u5907\u8DDF\u7740\u8881\u8C2D\uFF0C\u4E00\u8DEF\u5411\u90BA\u57CE\u8FC7\u53BB\u2014\u2014\u90A3\u91CC\u662F\u8881\u7ECD\u5F53\u65F6\u7684\u5927\u672C\u8425\u3002\u8881\u7ECD\u5F85\u5218\u5907\u4E5F\u7279\u522B\u5BA2\u6C14\uFF0C\u51FA\u57CE\u4E24\u767E\u91CC\u8FCE\u63A5\u3002\u7B80\u5355\u6362\u7B97\u4E00\u4E0B\uFF0C\u5927\u7EA6\u5C31\u76F8\u5F53\u4E8E\uFF0C\u4E00\u4E2A\u4ECE\u524D\u548C\u4F60\u6253\u8FC7\u67B6\u7684\u4EBA\u8DD1\u5230\u4E0A\u6D77\u6765\u770B\u4F60\uFF0C\u4F60\u4E13\u95E8\u8DD1\u5230\u82CF\u5DDE\u53BB\u8FCE\u63A5\u4ED6\u2014\u2014\u800C\u4E14\u8FD8\u4E0D\u662F\u5750\u9AD8\u94C1\u54E6\uFF0C\u662F\u9A91\u9A6C\u3002</p><p>\u7136\u800C\uFF0C\u5218\u5907\u5728\u4ED6\u7684\u8FD9\u7B2C\u4E94\u4E2A\u8001\u677F\u8FD9\u91CC\uFF0C\u5E76\u6CA1\u6709\u5F85\u591A\u957F\u65F6\u95F4\u3002\u5F88\u6709\u53EF\u80FD\u4ED6\u5DF2\u7ECF\u9884\u6599\u5230\u4E86\u8881\u7ECD\u5C06\u4F1A\u8D25\u4EA1\uFF0C\u6240\u4EE5\u53CA\u65E9\u62BD\u8EAB\uFF0C\u53BB\u4E86\u5218\u8868\u90A3\u91CC\uFF0C\u5F00\u59CB\u4E86\u81EA\u5DF1\u4EBA\u751F\u7684<code>\u7B2C\u516D\u6B21</code>\u5BC4\u4EBA\u7BF1\u4E0B\u3002</p><p>\u51E0\u5E74\u4EE5\u540E\uFF0C\u5728\u8FD9\u91CC\uFF0C\u5218\u5907\u7EC8\u4E8E\u7FFB\u76D8\u3002</p><p>\u56DE\u987E\u5218\u5907\u7684\u594B\u6597\u53F2\uFF0C\u53EF\u4EE5\u53D1\u73B0\u4E00\u4E2A\u5F88\u6709\u610F\u601D\u7684\u73B0\u8C61\uFF0C\u5C31\u662F\u4F60\u5B8C\u5168\u770B\u4E0D\u5230\u5218\u5907\u6709\u4EC0\u4E48\u7279\u522B\u4E4B\u5904\uFF0C\u78B0\u5230\u66F9\u64CD\u66F4\u662F\u6BCF\u6218\u5FC5\u8D25\uFF0C\u4F46\u662F\uFF0C\u5927\u5BB6\u5BF9\u4ED6\u5C31\u662F\u7279\u522B\u7684\u5BA2\u6C14\uFF0C\u4ECE\u5B54\u878D\u5F00\u59CB\uFF0C\u4E4B\u540E\u7684\u9676\u8C26\u3001\u5415\u5E03\u3001\u66F9\u64CD\u3001\u9A6C\u817E\u3001\u8881\u7ECD\u3001\u5218\u8868......\u8FD9\u4E9B\u4E2A\u4E2A\u90FD\u662F\u5F53\u65F6\u5929\u4E0B\u7684\u82F1\u96C4\uFF0C\u5218\u5907\u4E0D\u8FC7\u4E00\u4ECB\u8D2B\u6C11\u51FA\u8EAB\uFF0C\u5374\u7ADF\u7136\u80FD\u540C\u65F6\u5F97\u5230\u8FD9\u4E48\u591A\u5F53\u65F6\u8C6A\u6770\u7684\u5782\u9752\u3002</p><p>\u4E0D\u5F97\u4E0D\u8BF4\uFF0C\u5728\u90A3\u6837\u7684\u4E71\u4E16\u4E2D\uFF0C\u5218\u5907\u6700\u7EC8\u80FD\u591F\u4E09\u5206\u5929\u4E0B\uFF0C\u5FC5\u7136\u4E5F\u6709\u4ED6\u7684\u8FC7\u4EBA\u4E4B\u5904\u3002</p><blockquote><p>\u5E2E\u5B54\u878D\u7684\u90A3\u5173\u952E\u4E00\u8DC3\uFF0C\u8BA9\u4ED6\u4EE5\u4E00\u4E2A\u5C0F\u519B\u5934\u5165\u4E86\u4E0A\u6D41\u793E\u4F1A\u4EBA\u58EB\u7684\u6CD5\u773C\u3002</p></blockquote><blockquote><p>\u5B54\u878D\u5FD7\u5927\u624D\u758F\uFF0C\u5218\u5907\u8F6C\u800C\u8DF3\u5230\u9676\u8C26\u90A3\u91CC\uFF0C\u5F97\u5230\u4E86\u5F90\u5DDE\u3002</p></blockquote><blockquote><p>\u540E\u6765\u867D\u7136\u5F90\u5DDE\u5931\u5229\uFF0C\u4F46\u5218\u5907\u5374\u5DF2\u7ECF\u4E0D\u518D\u662F\u4ECE\u524D\u7684\u5E73\u539F\u4EE4\uFF0C\u5F90\u5DDE\u523A\u53F2\u7684\u8EAB\u4EFD\uFF0C\u8BA9\u4ED6\u5728\u66F9\u64CD\u90A3\u91CC\u6709\u4E86\u4E00\u5E2D\u4E4B\u5730\u3002</p></blockquote><blockquote><p>\u800C\u66F9\u64CD\u7684\u770B\u91CD\uFF0C\u53C8\u8BA9\u4ED6\u5728\u8881\u7ECD\u90A3\u91CC\u6709\u4E86\u7528\u6B66\u4E4B\u5904\u3002</p></blockquote><blockquote><p>\u5230\u8FD9\u4E2A\u65F6\u5019\uFF0C\u4ED6\u4E0E\u8881\u7ECD\u7684\u8EAB\u4EFD\u5730\u4F4D\u5DF2\u7ECF\u5DEE\u4E0D\u591A\uFF0C\u800C\u4ED6\u53C8\u7ED9\u5BF9\u65B9\u9001\u4E86\u4E00\u4E2A\u8302\u624D\u8FC7\u53BB\uFF0C\u4EE5\u4E3A\u793A\u597D\uFF0C\u7B97\u662F\u7ED9\u81EA\u5DF1\u5907\u4E86\u4E00\u6761\u5BBD\u9614\u7684\u540E\u8DEF\u3002</p></blockquote><p>\u5218\u5907\u7684\u519B\u4E8B\u80FD\u529B\u867D\u7136\u4E0D\u662F\u5F88\u5F3A\u5927\uFF0C\u4F46\u4E0D\u662F\u6CA1\u6709\u3002\u5218\u5907\u7684\u5173\u952E\u4E00\u6B65\uFF0C\u662F\u5E2E\u5B54\u878D\u51FB\u9000\u4E86\u9EC4\u5DFE\u8D3C\uFF0C\u8FD9\u5C31\u4E0D\u662F\u4EA4\u9645\u5E94\u916C\u90A3\u4E48\u7B80\u5355\u7684\u4E8B\u3002</p><p>\u5218\u5907\u6B63\u662F\u56E0\u4E3A\u771F\u7684\u5E2E\u5230\u4E86\u5B54\u878D\uFF0C\u5B54\u878D\u8F6C\u8EAB\u624D\u4F1A\u628A\u4ED6\u63A8\u8350\u7ED9\u9676\u8C26\u3002\u800C\u5218\u5907\u4E5F\u770B\u6E05\u4E86\u5F62\u52BF\uFF0C\u679C\u65AD\u8131\u79BB\u7530\u6977\uFF0C\u6295\u5411\u9676\u8C26\u7684\u6000\u62B1\u3002\u7B49\u5230\u9676\u8C26\u6B7B\u65F6\uFF0C\u5218\u5907\u5DF2\u6DF1\u53D7\u9676\u8C26\u4FE1\u4EFB\u2014\u2014\u9676\u8C26\u4E3B\u52A8\u5C06\u5F90\u5DDE\u8BA9\u4E0E\u4E86\u5218\u5907\u3002\uFF08\u8C26\u75C5\u7B03\uFF0C\u8C13\u522B\u9A7E\u9E8B\u7AFA\u66F0\uFF1A\u201C\u975E\u5218\u5907\u4E0D\u80FD\u5B89\u6B64\u5DDE\u4E5F\u3002\u201D\uFF09</p><p>\u6B64\u540E\uFF0C\u5218\u5907\u5728\u5F90\u5DDE\u671F\u95F4\uFF0C\u53C8\u591A\u6B21\u51FB\u9000\u4E86\u76D7\u8D3C\u7684\u653B\u51FB\u3002</p><p>\u53EA\u662F\uFF0C\u4ED6\u7684\u519B\u4E8B\u80FD\u529B\u867D\u7136\u6709\uFF0C\u5374\u771F\u7684\u4E0D\u7B97\u7279\u522B\u5F3A\u5927\uFF0C\u5E94\u4ED8\u4E00\u4E0B\u666E\u901A\u76D7\u8D3C\u8FD8\u53EF\u4EE5\uFF0C\u78B0\u5230\u66F9\u64CD\u5C31\u6BCF\u6218\u5FC5\u8D25\u4E86\u3002</p><p>\u5218\u5907\u5982\u679C\u6280\u6B62\u4E8E\u6B64\uFF0C\u90A3\u4E48\u5927\u6982\u4E5F\u5C31\u53EA\u80FD\u548C\u5176\u4ED6\u4EBA\u4E00\u6837\uFF0C\u8981\u4E48\u5272\u636E\u79F0\u738B\uFF0C\u8981\u4E48\u5982\u5F20\u7EE3\u5F20\u9C81\u4E00\u822C\u88AB\u62DB\u964D\u4E86\u3002\u4F46\u662F\uFF01\u5218\u7687\u53D4\u53CA\u65F6\u8C03\u6574\u81EA\u5DF1\u7684\u4E1A\u52A1\u8303\u56F4\uFF0C\u5F00\u53D1\u51FA\u4E86\u65B0\u7684\u7ADE\u4E89\u4F18\u52BF\u2014\u2014</p><p>\u5218\u5907\u867D\u7136\u7ECF\u5E38\u88AB\u66F9\u64CD\u6253\u5F97\u6EE1\u5730\u627E\u7259\uFF0C\u4F46\u8BF7\u6CE8\u610F\uFF0C<strong>\u4ED6\u4E5F\u662F\u65E9\u5E74\u552F\u4E00\u4E00\u4E2A\u8DDF\u66F9\u64CD\u6597\u5374\u59CB\u7EC8\u6CA1\u6709\u88AB\u66F9\u64CD\u706D\u6389\u7684\u4EBA\u3002</strong></p><p>\u66F9\u64CD\u706D\u6389\u7684\u52BF\u529B\u6709\u591A\u5C11\uFF1F\u90A3\u662F\u4E00\u4E32\u5F88\u957F\u7684\u540D\u5355\uFF1A\u5F20\u9088\u3001\u5415\u5E03\u3001\u5F20\u626C\u3001\u674E\u5095\u3001\u90ED\u6C5C\u3001\u8881\u672F\u3001\u5F20\u7EE3\u3001\u8881\u7ECD\u3001\u8881\u8C2D\u3001\u8881\u5C1A\u3001\u9AD8\u5E72......\u4F46\u662F\uFF0C\u552F\u72EC\u6CA1\u6709\u5218\u5907\u3002</p><p>\u6240\u4EE5\u5218\u5907\u5728\u5F88\u65E9\u671F\u7684\u65F6\u5019\uFF0C\u5C31\u548C\u66F9\u64CD\u4E0D\u5BF9\u4ED8\u4E86\u3002\u4E8E\u662F\uFF0C\u4ED6\u5BF9\u81EA\u5DF1\u7684\u5B9A\u4F4D\u4E5F\u5C31\u6781\u5176\u7CBE\u51C6\u2014\u2014<code>\u6211\u5C31\u662F\u8981\u53CD\u66F9</code>\u3002</p><p>\u4ECE\u5728\u66F9\u64CD\u90A3\u91CC\u548C\u8463\u627F\u52FE\u642D\u5F00\u59CB\uFF0C\u5218\u5907\u5C31\u628A\u81EA\u5DF1\u6811\u7ACB\u6210\u4E86\u66F9\u64CD\u7684\u5BF9\u7ACB\u9762\u3002\u5C3D\u7BA1\u81EA\u5DF1\u5B9E\u529B\u5F31\u5C0F\uFF0C\u4ECE\u6765\u90FD\u6CA1\u5728\u66F9\u64CD\u90A3\u91CC\u5360\u5230\u8FC7\u4FBF\u5B9C\uFF0C\u4F46\u662F\u6211\u5218\u7384\u5FB7\u5C31\u662F\u8981\u548C\u4ED6\u66F9\u5B5F\u5FB7\u5BF9\u7740\u5E72\uFF01</p><blockquote><p>\u300A\u4E09\u56FD\u5FD7\u300B\u66FE\u5F15\u6CE8\u8FC7\u300A\u4E5D\u5DDE\u6625\u79CB\u300B\u91CC\u7684\u4E00\u6BB5\u5185\u5BB9\uFF1A\u5218\u5907\u8BF4\uFF0C\u4ECA\u6307\u4E0E\u543E\u4E3A\u6C34\u706B\u8005\uFF0C\u66F9\u64CD\u4E5F\uFF0C\u64CD\u4EE5\u6025\uFF0C\u543E\u4EE5\u5BBD\uFF1B\u64CD\u4EE5\u66B4\uFF0C\u543E\u4EE5\u4EC1\uFF1B\u64CD\u4EE5\u8C32\uFF0C\u543E\u4EE5\u5FE0\uFF1B\u6BCF\u4E0E\u64CD\u53CD\uFF0C\u4E8B\u4E43\u53EF\u6210\u8033\u3002</p></blockquote><p>\u6709\u4E86\u8FD9\u4E48\u4E2A\u8EAB\u4EFD\uFF0C<strong>\u5218\u5907\u81EA\u7136\u4E00\u76F4\u662F\u53CD\u66F9\u96C6\u56E2\u7684\u5EA7\u4E0A\u5BA2</strong>\uFF0C\u8881\u7ECD\u8FD9\u4E48\u5F85\u4ED6\uFF0C\u5218\u8868\u8FD9\u4E48\u5F85\u4ED6\uFF0C\u5B59\u6743\u4E5F\u8FD9\u4E48\u5F85\u4ED6\u3002</p><p>\u6700\u5F00\u59CB\uFF0C\u5218\u5907\u8BA4\u771F\u5730\u505A\u597D\u804C\u4E1A\u5C06\u9886\u7684\u672C\u804C\u5DE5\u4F5C\uFF0C\u5162\u5162\u4E1A\u4E1A\uFF1B\u7EC8\u4E8E\uFF0C\u4ED6\u4ECE\u7530\u6977\u90A3\u91CC\u8131\u9896\u800C\u51FA\uFF0C\u6C42\u5F97\u4E86\u5E73\u539F\u4E00\u90E1\u4E4B\u5730\u3002\u4EE5\u540E\uFF0C\u4ED6\u53C8\u51ED\u501F\u519B\u4E8B\u80FD\u529B\uFF0C\u83B7\u5F97\u4E86\u5F90\u5DDE\u4E0A\u5C42\u96C6\u56E2\u7684\u8BA4\u53EF\u3002\u81F3\u6B64\uFF0C\u4ED6\u9760\u81EA\u5DF1\u7684\u80FD\u529B\uFF0C\u6210\u529F\u6DF7\u5165\u4E86\u519B\u9600\u7684\u5708\u5B50\u4E2D\u3002</p><p>\u4F46\u519B\u9600\u4E5F\u6709\u5927\u5C0F\u4E4B\u5206\u3002\u5728\u519B\u4E8B\u80FD\u529B\u65E0\u4EE5\u4E3A\u7EE7\u7684\u60C5\u51B5\u4E0B\uFF0C\u5218\u5907\u654F\u9510\u5730\u53D1\u73B0\u673A\u4F1A\uFF0C\u5C06\u81EA\u5DF1\u6253\u9020\u6210\u4E86\u66F9\u64CD\u7684\u5BF9\u7ACB\u9762\uFF0C\u4ECE\u6B64\u6210\u4E86\u53CD\u66F9\u52BF\u529B\u7684\u5EA7\u4E0A\u5BBE\u3002\u5F53\u66F9\u64CD\u4E00\u8DEF\u9AD8\u6B4C\u731B\u8FDB\u3001\u6A2A\u626B\u5929\u4E0B\uFF0C\u5218\u5907\u4F5C\u4E3A\u53CD\u66F9\u7684\u6807\u5FD7\u4EBA\u7269\uFF0C\u81EA\u7136\u4E5F\u5C31\u6C34\u6DA8\u8239\u9AD8\uFF0C\u5907\u53D7\u4ED6\u4EBA\u9752\u7750\u4E86\u3002</p><blockquote><p>\u81EA\u62AC\u8EAB\u4EF7\uFF0C\u5F53\u5982\u662F\u4E5F\u3002</p></blockquote>',53),b={href:"https://mp.weixin.qq.com/s?__biz=MzAxNzg4MDMzMg==&mid=2247484123&idx=1&sn=094b336df3a7fdd2fafebb5361d8391a&chksm=9bdf86b8aca80faee204f82dc55b3010a9e0961a0305d791ed345e07aa47b6a0115bee7dcf80&cur_album_id=1572981576643215362&scene=189#wechat_redirect",target:"_blank",rel:"noopener noreferrer"};function u(k,_){const o=s("ExternalLinkIcon");return t(),c("div",null,[d,p("p",null,[p("a",b,[r("\u6587\u6848"),n(o)])])])}const f=e(l,[["render",u],["__file","8.html.vue"]]);export{f as default};
