import{_ as p}from"./_plugin-vue_export-helper.cdc0426e.js";import{o,c as e,f as c}from"./app.913fb917.js";const d={},t=c('<h1 id="\u6881\u9648\u7985\u4EE3" tabindex="-1"><a class="header-anchor" href="#\u6881\u9648\u7985\u4EE3" aria-hidden="true">#</a> \u6881\u9648\u7985\u4EE3</h1><blockquote><p>\u662F\u591C\uFF0C\u9738\u5148\u7F22\u6740\u50E7\u8FA9\u7236\u5B50\u3002\u65E2\u800C\u7ADF\u65E0\u9F50\u5175\uFF0C\u4EA6\u975E\u9738\u5148\u4E4B\u8C32\u4E5F\u3002</p></blockquote><p><strong>\u300A\u8D44\u6CBB\u901A\u9274\xB7\u5377\u4E00\u516D\u4E03\xB7\u6881\u7EAA\u4E8C\u5341\u4E8C\u300B</strong></p><p>\u4ECE<code>\u4E1C\u664B</code>\u5F00\u59CB\uFF0C<code>\u5B8B\u9F50\u6881\u9648</code>\u6BCF\u4E00\u6B21\u671D\u4EE3\u7684\u66F4\u66FF\uFF0C\u62FF\u7684\u90FD\u662F\u4E00\u6837\u7684\u5267\u672C\uFF1A\u6B66\u5C06\u901A\u8FC7\u6218\u4E89\u83B7\u5F97\u6743\u67C4\uFF0C\u903C\u8FEB\u7687\u5E1D\u9000\u4F4D\u3002\u7136\u540E\u8FC7\u51E0\u5341\u5E74\uFF0C\u518D\u6765\u4E00\u6B21\u3002</p><p>\u4F46\u5357\u671D\u7684\u795E\u5947\u4E4B\u5904\u5C31\u5728\u4E8E\uFF0C\u6BCF\u4E00\u4EE3\u76F8\u540C\u7684\u5267\u60C5\u90FD\u88AB\u73A9\u51FA\u4E86\u4E0D\u540C\u7684\u82B1\u6837\uFF1A<code>\u664B\u5B8B</code>\u7985\u4EE3\uFF0C<code>\u5218\u88D5</code>\u9760\u7684\u662F\u5B9E\u6253\u5B9E\u7684\u519B\u529F\uFF1B<code>\u5B8B\u9F50</code>\u7985\u4EE3\uFF0C<code>\u540E\u5E9F\u5E1D\u5218\u6631</code>\u786C\u751F\u751F\u628A<code>\u8427\u9053\u6210</code>\u903C\u5230\u6CA1\u6D3B\u8DEF\uFF1B<code>\u9F50\u6881</code>\u7985\u4EE3\uFF0C\u4E00\u65B9\u9762\u662F\u56E0\u4E3A<code>\u4E1C\u660F\u4FAF\u8427\u5B9D\u5377</code>\u5012\u884C\u9006\u65BD\uFF0C\u4F46\u53E6\u4E00\u65B9\u9762\uFF0C<code>\u8427\u884D</code>\u4E00\u76F4\u5728\u6697\u4E2D\u51C6\u5907\u8C0B\u53D6\u5927\u4F4D\u4E5F\u662F\u4E0D\u4E89\u7684\u4E8B\u5B9E\u3002</p><p>\u6309\u7167\u6B63\u5E38\u4EBA\u7684\u60F3\u8C61\uFF0C\u8FD9\u4E9B\u5927\u7EA6\u4E5F\u5C31\u662F\u6240\u6709\u7684\u5267\u60C5\u4E86\uFF0C\u4F46\u5982\u4ECA<code>\u6881\u9648</code>\u7985\u4EE3\uFF0C<code>\u9648\u9738\u5148</code>\u53C8\u73A9\u51FA\u4E86\u65B0\u82B1\u6837\uFF0C\u7AD6\u8D77\u4E86\u65B0\u6807\u6746\uFF1A<strong>\u4ED6\u901A\u8FC7\u5077\u88AD\u76DF\u53CB\u83B7\u5F97\u4E86\u6743\u67C4\uFF0C\u5E76\u6700\u7EC8\u5F97\u4EE5\u7BE1\u593A\u5E1D\u4E1A</strong>\u3002</p><p>\u8FD8\u662F\u5F97\u4ECE<code>\u4FAF\u666F\u4E4B\u4E71</code>\u8BF4\u8D77\u3002</p><p><code>\u4FAF\u666F</code>\u5F53\u521D\u56F4\u653B\u53F0\u57CE\u65F6\uFF0C<code>\u8427\u6881</code>\u5404\u5730\u8BF8\u4FAF\u66FE\u7EB7\u7EB7\u8D77\u5175\u52E4\u738B\u3002\u4F46\u96C6\u7ED3\u7684\u5927\u519B\u5728<code>\u67F3\u4EF2\u793C</code>\u7684\u7EDF\u5E05\u4E4B\u4E0B\uFF0C\u6BEB\u65E0\u4F5C\u4E3A\u3002\u800C\u4FAF\u666F\u5728\u653B\u7834\u53F0\u57CE\u4EE5\u540E\uFF0C\u5F88\u5FEB\u5C31\u4EE5<code>\u6881\u6B66\u5E1D</code>\u7684\u540D\u4E49\u4E0B\u8BCF\uFF0C\u201C\u89E3\uFF08\u6563\uFF09\u8BF8\u519B\u201D\u3002</p><p>\u8054\u519B\u672C\u6709\u5341\u51E0\u4E07\u4E4B\u4F17\uFF0C\u53EF\u5728\u67F3\u4EF2\u793C\u653E\u5F03\u62B5\u6297\u7684\u51B3\u5B9A\u4E4B\u4E0B\uFF0C\u4F17\u8425\u7686\u964D\u3002</p><p>\u8FD9\u4E9B\u5175\u529B\u88AB\u4FAF\u666F\u8FC5\u901F\u6536\u7F16\u3002\u81F3\u4E8E\u6BCF\u652F\u519B\u961F\u539F\u672C\u7684\u5C06\u9886\uFF0C\u4FAF\u666F\u5219\u51FA\u4E8E\u6536\u4E70\u4EBA\u5FC3\u7684\u8003\u8651\uFF0C\u9009\u62E9\u4E86\u5168\u90E8\u653E\u8D70\u3002</p><p>\u5176\u4E2D\u5C31\u6709\u7ADF\u9675\u592A\u5B88<code>\u738B\u50E7\u8FA9</code>\u3002</p><p>\u738B\u50E7\u8FA9\u5F53\u521D\u662F\u5E26\u7740\u4E00\u4E07\u4EBA\u4E1C\u4E0B\u52E4\u738B\u7684\uFF0C\u5982\u4ECA\u5374\u53EA\u80FD\u53EA\u8EAB\u8FD4\u56DE\u6765\u65F6\u7684\u5730\u65B9\u3002</p><p>\u4E0D\u8FC7\u8FD9\u4F4D\u7ADF\u9675\u592A\u5B88\u5F53\u521D\u4E5F\u5E76\u4E0D\u662F\u4ECE\u7ADF\u9675\u800C\u6765\u3002\u4ED6\u6765\u65F6\u7684\u5730\u65B9\uFF0C\u662F\u6E58\u4E1C\u738B<code>\u8427\u7ECE</code>\u6240\u5728\u7684\u6C5F\u9675\u3002</p><p>\u738B\u50E7\u8FA9\u867D\u7136\u59D3\u738B\uFF0C\u4F46\u5E76\u975E\u51FA\u81EA\u5357\u671D\u9AD8\u95E8\u7405\u740A\u738B\u6C0F\uFF0C\u800C\u662F\u6765\u81EA\u4E8E\u5317\u65B9\u5927\u65CF\u592A\u539F\u738B\u6C0F\u3002\u4ED6\u7684\u7236\u4EB2\u56E0\u6218\u4E71\u5357\u4E0B\uFF0C\u6295\u5954\u8427\u7ECE\uFF0C\u6B64\u540E\u5C31\u4E00\u76F4\u5728\u8427\u7ECE\u95E8\u4E0B\u3002\u738B\u50E7\u8FA9\u8FD9\u6B21\u6240\u7387\u9886\u7684\u52E4\u738B\u519B\u961F\uFF0C\u4E5F\u6B63\u662F\u8427\u7ECE\u7684\u90E8\u961F\u3002</p><p>\u4FAF\u666F\u987A\u5229\u63A7\u5236\u4F4F\u4E86\u8427\u6881\u671D\u5EF7\u3002</p><p>\u4E24\u4E2A\u6708\u4EE5\u540E\uFF0C\u6881\u6B66\u5E1D\u88AB\u997F\u6B7B\u3002\u4FAF\u666F\u6276\u7ACB\u592A\u5B50<code>\u8427\u7EB2</code>\u7EE7\u4F4D\uFF0C\u662F\u4E3A<code>\u7B80\u6587\u5E1D</code>\u3002</p><p>\u6E58\u4E1C\u738B\u8427\u7ECE\u5728\u5F97\u77E5\u8427\u884D\u53BB\u4E16\u7684\u6D88\u606F\u540E\uFF0C\u5E76\u4E0D\u627F\u8BA4\u4FAF\u666F\u6240\u518C\u7ACB\u7684\u7B80\u6587\u5E1D\uFF0C\u518D\u4E00\u6B21\u8D77\u5175\u8BA8\u4F10\u4FAF\u666F\u3002</p><p>\u6700\u521D\uFF0C\u8427\u7ECE\u9009\u62E9\u7684\u4E3B\u5C06\u662F<code>\u5F90\u6587\u76DB</code>\uFF0C\u4F46\u5F90\u6587\u76DB\u5374\u88AB\u4FAF\u666F\u5927\u519B\u6240\u51FB\u8D25\u3002\u8427\u7ECE\u4E0D\u5F97\u5DF2\uFF0C\u53EA\u80FD\u91CD\u65B0\u542F\u7528\u738B\u50E7\u8FA9\u3002</p><p>\u9762\u5BF9\u4FAF\u666F\u5927\u519B\u538B\u5883\u7684\u4E0D\u5229\u5C40\u9762\uFF0C\u738B\u50E7\u8FA9\u56FA\u5B88\u5DF4\u9675\uFF0C\u51FB\u9000\u4E86\u4FAF\u666F\u897F\u8FDB\u7684\u5927\u519B\uFF0C\u626D\u8F6C\u6218\u5C40\u3002</p><p>\u51E0\u4E4E\u662F\u540C\u65F6\uFF0C\u4EA4\u5DDE\u523A\u53F2<code>\u9648\u9738\u5148</code>\u4E5F\u9886\u5175\u5317\u4E0A\u3002\u5728\u51FB\u9000\u4E86\u4F9D\u9644\u4E8E\u4FAF\u666F\u7684\u52BF\u529B\u540E\uFF0C\u8FDB\u5165\u6C5F\u5DDE\u4E0E\u738B\u50E7\u8FA9\u6C47\u5408\u3002</p><p>\u5728\u6C5F\u5DDE\uFF0C\u738B\u50E7\u8FA9\u518D\u4E00\u6B21\u51FB\u8D25\u4FAF\u666F\u6240\u90E8\uFF0C\u4FAF\u666F\u8D25\u9000\u56DE\u5EFA\u5EB7\u2014\u2014\u8FD9\u4E00\u6218\uFF0C\u662F\u4FAF\u666F\u6700\u7EC8\u8D25\u4EA1\u7684\u5173\u952E\u6218\u5F79\u3002</p><p>\u6B21\u5E74\uFF0C\u8427\u7ECE\u547D\u738B\u50E7\u8FA9\u53D1\u52A8\u53CD\u653B\uFF0C\u6700\u7EC8\u653B\u5165\u5EFA\u5EB7\u3002\u4FAF\u666F\u9003\u5165\u5434\u4E2D\uFF0C\u4F46\u88AB\u90E8\u5C06\u6240\u6740\uFF0C\u6301\u7EED\u8FD1\u56DB\u5E74\u7684\u4FAF\u666F\u4E4B\u4E71\u7ED3\u675F\u3002</p><p>\u53EF\u4FAF\u666F\u7684\u88AB\u6740\u5E76\u4E0D\u662F\u6545\u4E8B\u7684\u7ED3\u675F\uFF0C\u800C\u53EA\u662F\u53E6\u4E00\u4E2A\u65F6\u4EE3\u7684\u5F00\u59CB\u3002</p><p>\u968F\u7740\u4FAF\u666F\u8EAB\u6B7B\uFF0C\u8427\u7ECE\u5728\u5927\u81E3\u7684\u53CD\u590D\u529D\u8FDB\u4E4B\u4E0B\u7EC8\u4E8E\u79F0\u5E1D\uFF0C\u662F\u4E3A<code>\u6881\u5143\u5E1D</code>\u3002\u4F46\u4ED6\u5374\u6B7B\u6D3B\u4E0D\u613F\u610F\u5230\u5EFA\u5EB7\u53BB\uFF0C\u53EA\u60F3\u7262\u7262\u5F85\u5728\u8001\u5DE2\u6C5F\u9675\u3002</p><p>\u8FD9\u5C31\u88AB<code>\u897F\u9B4F</code>\u6293\u4F4F\u4E86\u673A\u9047\u3002</p><p>\u5728\u6C5F\u9675\uFF0C\u6881\u5143\u5E1D\u906D\u5230\u4E86\u897F\u9B4F\u7684\u5077\u88AD\u3002\u8EAB\u5728\u4E0B\u6E38\u5EFA\u5EB7\u7684\u738B\u50E7\u8FA9\u548C\u9648\u9738\u5148\u751A\u81F3\u90FD\u8FD8\u6CA1\u6765\u5F97\u53CA\u6551\u63F4\uFF0C\u7687\u5E1D\u5C31\u5DF2\u7ECF\u88AB\u6740\u4E86\u3002</p><blockquote><p>\u6881\u5143\u5E1D\u7684\u5E74\u53F7\u201C\u627F\u5723\u201D\u7528\u4E86\u56DB\u5E74\uFF0C\u800C\u4ED6\u81EA\u5DF1\u5F53\u7687\u5E1D\u7684\u65F6\u95F4\u5374\u5927\u7EA6\u53EA\u6709\u4E24\u5E74\uFF1A\u516C\u5143552\u5E7411\u6708\u79F0\u5E1D\uFF0C\u8F6C\u5E74\u5341\u4E8C\u6708\u5373\u88AB\u897F\u9B4F\u4FD8\u864F\u6740\u6B7B\u3002</p></blockquote><p>\u7687\u4E0A\u88AB\u4EBA\u6740\u4E86\uFF0C\u56FD\u4E2D\u65E0\u4E3B\uFF0C\u8FD9\u4E00\u4E0B\u5B50\u641E\u5F97\u738B\u50E7\u8FA9\u548C\u9648\u9738\u5148\u5728\u4E0B\u6E38\u5F88\u5C34\u5C2C\u3002</p><p>\u897F\u9B4F\u5728\u6C5F\u9675\u53C8\u518C\u7ACB\u4E86<code>\u8427\u8A67</code>\u4E3A\u5080\u5121\u7687\u5E1D\uFF0C\u4F46\u738B\u9648\u4E8C\u4EBA\u5E76\u4E0D\u613F\u610F\u627F\u8BA4\uFF0C\u4ED6\u4EEC\u53E6\u7ACB\u6881\u5143\u5E1D\u4E4B\u5B50<code>\u8427\u65B9\u667A</code>\u4E3A\u5E1D\u3002</p><p>\u53EF\u8427\u65B9\u667A\u7684\u5C41\u80A1\u8FD8\u6CA1\u6709\u5750\u7A33\uFF0C<code>\u5317\u9F50</code>\u4E5F\u6765\u6A2A\u63D2\u4E00\u6760\u5B50\u4E86\uFF1A\u5317\u9F50\u6D3E\u5175\u62A4\u9001\u8427\u6881\u5B97\u5BA4<code>\u8427\u6E0A\u660E</code>\u5357\u5F52\uFF0C\u610F\u56FE\u6276\u7ACB\u8427\u6E0A\u660E\u4E3A\u5E1D\u3002</p><p>\u66FE\u7ECF\u8C8C\u4F3C\u5F3A\u5927\u7684\u8427\u6881\u738B\u671D\uFF0C\u5982\u4ECA\u5DF2\u662F\u56DB\u5206\u4E94\u88C2\uFF0C\u6447\u6447\u6B32\u5760\u3002</p><p>\u5373\u4FBF\u662F\u6881\u5143\u5E1D\u5728\u4F4D\u4E4B\u65F6\uFF0C\u6240\u8C13\u7684\u201C\u671D\u5EF7\u201D\uFF0C\u80FD\u591F\u53F7\u4EE4\u4E4B\u5730\u4E5F\u53EA\u6709\u6C5F\u9675\u4EE5\u4E0B\u7684\u6CBF\u957F\u6C5F\u4E00\u5E26\u3002\u800C\u4E14\u56E0\u4E3A\u957F\u671F\u6218\u4E71\uFF0C\u6237\u6570\u4E0D\u8FBE\u4E09\u4E07\u3002</p><p>\u66F4\u4E0D\u8981\u8BF4\u5982\u4ECA\uFF0C\u897F\u9B4F\u3001\u5317\u9F50\u53CC\u53CC\u5939\u51FB\uFF0C\u738B\u50E7\u8FA9\u5E94\u5BF9\u4E4F\u529B\u3002\u9762\u5BF9\u7740\u5317\u9F50\u7684\u5484\u5484\u903C\u4EBA\uFF0C\u738B\u50E7\u8FA9\u7EC8\u4E8E\u540C\u610F\u6539\u5949\u8427\u6E0A\u660E\u4E3A\u5E1D\u3002</p><p>\u4F46\u8FD9\u5374\u5F15\u8D77\u4E86\u9648\u9738\u5148\u7684\u4E0D\u6EE1\u3002</p><p>\u4E24\u4EBA\u4E4B\u524D\u66FE\u5171\u60A3\u96BE\uFF0C\u5F7C\u6B64\u95F4\u4E5F\u76F8\u4E92\u6B23\u8D4F\uFF0C\u751A\u81F3\u4E00\u5EA6\u8FD8\u60F3\u6210\u4E3A\u513F\u5973\u4EB2\u5BB6\u3002\u4F46\u968F\u7740\u6218\u4E89\u7ED3\u675F\uFF0C\u738B\u50E7\u8FA9\u5165\u4E3B\u5EFA\u5EB7\u3001\u4E0E\u9648\u9738\u5148\u5F62\u6210\u4E8B\u5B9E\u4E0A\u7684\u4E0A\u4E0B\u7EA7\u5173\u7CFB\uFF0C\u4E24\u4EBA\u7684\u5173\u7CFB\u5C31\u6709\u70B9\u5FAE\u5999\u4E86\u3002</p><p>\u4E0D\u5C11\u4EBA\u90FD\u529D\u738B\u50E7\u8FA9\uFF0C\u8BF4\u4F60\u5BF9\u9648\u9738\u5148\u8981\u6709\u6240\u63D0\u9632\u3002\u4F46\u738B\u50E7\u8FA9\u56E0\u4E3A\u987E\u53CA\u5F80\u65E5\u60C5\u5206\uFF0C\u7F6E\u82E5\u7F54\u95FB\u3002\u800C\u4E14\u770B\u4E0A\u53BB\uFF0C\u9648\u9738\u5148\u4E5F\u786E\u5B9E\u6CA1\u6709\u505A\u4EC0\u4E48\u503C\u5F97\u738B\u50E7\u8FA9\u731C\u5FCC\u7684\u4E8B\u3002</p><p>\u76F4\u5230\u738B\u50E7\u8FA9\u51C6\u5907\u8FCE\u7ACB\u8427\u6E0A\u660E\u3002</p><p>\u5F53\u65F6\uFF0C**\u738B\u50E7\u8FA9\u9A7B\u5EFA\u5EB7\uFF0C\u9648\u9738\u5148\u9A7B\u4EAC\u53E3\uFF0C\u4E24\u5730\u8DDD\u79BB\u4E0D\u5230\u767E\u91CC\u3002**\u4F46\u5C31\u662F\u8FD9\u4E00\u70B9\u70B9\u7684\u5DEE\u522B\uFF0C\u610F\u5473\u7740\u671D\u5EF7\u662F\u5728\u738B\u50E7\u8FA9\u7684\u63A7\u5236\u4E4B\u4E0B\u3002\u9648\u9738\u5148\u867D\u7136\u53CD\u5BF9\uFF0C\u4F46\u5230\u4E86\u771F\u6B63\u51B3\u7B56\u4E4B\u65F6\uFF0C\u56E0\u4E3A\u4EBA\u4E0D\u5728\u73B0\u573A\uFF0C\u6240\u4EE5\u5E76\u6CA1\u6709\u591A\u5C11\u6743\u91CD\u3002</p><p>\u9648\u9738\u5148\u66FE\u7ECF\u5148\u540E\u56DB\u6B21\u529D\u8BEB\u738B\u50E7\u8FA9\uFF0C\u4F46\u738B\u50E7\u8FA9\u90FD\u4E0D\u542C\u3002</p><p>\u800C\u9648\u9738\u5148\u5BF9\u6B64\u4E8B\u7684\u5904\u7406\u4E5F\u5341\u5206\u76F4\u622A\u4E86\u5F53\uFF1A<strong>\u90A3\u5C31\u628A\u738B\u50E7\u8FA9\u9664\u6389\u5427\u3002</strong></p><p>\u738B\u50E7\u8FA9\u5BF9\u4E8E\u9648\u9738\u5148\u8FD9\u6837\u7684\u60F3\u6CD5\u6BEB\u4E0D\u77E5\u60C5\u3002\u76F4\u5230\u88AB\u9648\u9738\u5148\u9886\u5175\u56F4\u5728\u4E86\u77F3\u5934\u57CE\uFF0C\u4ED6\u624D\u610F\u8BC6\u5230\u95EE\u9898\u7684\u4E25\u91CD\u6027\u3002\u800C\u6B64\u65F6\u4ED6\u8EAB\u8FB9\u4E0D\u8FC7\u6570\u5341\u4EBA\uFF0C\u6839\u672C\u5C31\u6CA1\u6709\u53CD\u6297\u7684\u80FD\u529B\u3002</p><p>\u738B\u50E7\u8FA9\u88AB\u6293\u4EE5\u540E\uFF0C\u9648\u9738\u5148\u9996\u5148\u8D28\u95EE\u4ED6\uFF1A\u4F60\u4E3A\u4EC0\u4E48\u8981\u548C\u5317\u9F50\u4E00\u8D77\u5BF9\u4ED8\u6211\uFF1F\uFF01</p><p>\u738B\u50E7\u8FA9\u8BF4\uFF0C\u6211\u6CA1\u6709\u554A\uFF01</p><p>\u9648\u9738\u5148\u53C8\u95EE\uFF1A\u5317\u9F50\u662F\u654C\u56FD\uFF0C\u4F60\u4E3A\u4EC0\u4E48\u4E0D\u6D3E\u519B\u961F\u9632\u5907\uFF1F</p><p>\u738B\u50E7\u8FA9\u8BF4\uFF0C\u4F60\u9A7B\u624E\u5728\u4EAC\u53E3\uFF0C\u804C\u8D23\u4E0D\u5C31\u662F\u9632\u5907\u5317\u9F50\u5417\uFF1F</p><p>\u5F53\u591C\uFF0C\u738B\u50E7\u8FA9\u4E0E\u957F\u5B50\u738B\u9820\u88AB\u6740\u3002</p><p>\u672C\u6765\uFF0C\u6309\u7167\u738B\u50E7\u8FA9\u548C\u9648\u9738\u5148\u7684\u5730\u4F4D\u5DEE\u522B\uFF0C\u5373\u4FBF\u8981\u4E0A\u6F14\u201C\u6743\u81E3\u7BE1\u4F4D\u201D\u7684\u620F\u7801\uFF0C\u4E5F\u53EA\u4F1A\u662F\u738B\u50E7\u8FA9\u5531\u4E3B\u89D2\uFF0C\u6CA1\u6709\u9648\u9738\u5148\u4EC0\u4E48\u4E8B\u3002\u5728\u6B63\u5E38\u7684\u5267\u672C\u91CC\uFF0C\u5E94\u8BE5\u662F\u738B\u50E7\u8FA9\u610F\u6B32\u7BE1\u4F4D\uFF0C\u6240\u4EE5\u9648\u9738\u5148\u8D77\u5175\u51FB\u8D25\u4ED6\u3002\u7136\u540E\uFF0C\u624D\u8F6E\u5230\u9648\u9738\u5148\u63A7\u5236\u671D\u653F\uFF0C\u6CBF\u7740\u524D\u4EFB\u4EEC\u7684\u8001\u8DEF\u8D70\u6D41\u7A0B\u3002</p><p>\u4F46\u8FD9\u4E2A\u8FC7\u7A0B\u4F1A\u5F88\u6F2B\u957F\uFF0C\u800C\u4E14\u8FD8\u5145\u6EE1\u4E86\u4E0D\u786E\u5B9A\u6027\uFF1A\u9996\u5148\uFF0C\u9648\u9738\u5148\u600E\u4E48\u5C31\u80FD\u80AF\u5B9A\u738B\u50E7\u8FA9\u4E00\u5B9A\u4F1A\u8D70\u4E0A\u7BE1\u4F4D\u7684\u9053\u8DEF\uFF1F\u5176\u6B21\uFF0C\u9648\u9738\u5148\u6562\u7B03\u5B9A\u81EA\u5DF1\u6709\u80FD\u529B\u5728\u6218\u573A\u4E0A\u51FB\u8D25\u738B\u50E7\u8FA9\u5417\uFF1F</p><p><strong>\u8FD9\u65F6\u5019\uFF0C\u6765\u4E00\u6B21\u7A81\u7834\u4E0B\u9650\u7684\u5077\u88AD\uFF0C\u5C31\u53D8\u5F97\u6709\u5FC5\u8981\u800C\u4E14\u6536\u83B7\u6781\u5927\u4E86\u3002</strong></p><p>\u4ECE\u53F2\u6599\u7684\u8BB0\u8F7D\u6765\u770B\uFF0C\u738B\u9648\u4E8C\u4EBA\u4E4B\u524D\u5E76\u65E0\u4EA4\u6076\u3002\u4E24\u4EBA\u4EC5\u6709\u7684\u610F\u89C1\u5206\u6B67\uFF0C\u5C31\u662F\u5173\u4E8E\u662F\u5426\u8981\u63A5\u7EB3\u5317\u9F50\u7684\u5080\u5121\u8427\u6E0A\u660E\u3002</p><p>\u53F2\u6599\u8BF4\uFF0C\u9648\u9738\u5148\u662F\u56E0\u4E3A\u738B\u50E7\u8FA9\u7684\u8F6F\u5F31\u800C\u5BFC\u81F4\u4E0D\u6EE1\uFF0C\u4F46\u95EE\u9898\u662F\uFF0C\u9648\u9738\u5148\u81EA\u5DF1\u4E5F\u4E0D\u662F\u4EC0\u4E48\u5584\u7537\u4FE1\u5973\u3002<strong>\u5728\u9664\u6389\u738B\u50E7\u8FA9\u3001\u83B7\u5F97\u8427\u6881\u7684\u6700\u9AD8\u6743\u67C4\u4EE5\u540E\uFF0C\u8427\u6881\u4F9D\u7136\u8D70\u4E0A\u4E86\u5BF9\u5317\u9F50\u79F0\u81E3\u7684\u8DEF\u3002</strong></p><p>\u6240\u8C13\u7684\u738B\u50E7\u8FA9\u8F6F\u5F31\uFF0C\u6839\u672C\u5C31\u53EA\u662F\u4E00\u4E2A\u501F\u53E3\uFF0C\u800C\u4E0D\u662F\u7406\u7531\u3002</p><p>\u66F4\u4F55\u51B5\uFF0C\u9648\u9738\u5148\u4ECE\u6765\u5C31\u6CA1\u6253\u7B97\u901A\u8FC7\u6B63\u5E38\u7684\u624B\u6BB5\u8FDB\u884C\u4EA4\u6D41\u548C\u63A7\u5236\u4E24\u4E2A\u4EBA\u7684\u5206\u6B67\uFF0C\u800C\u662F\u76F4\u63A5\u9886\u519B\u5077\u88AD\uFF0C\u5B9E\u65BD\u65A9\u9996\u884C\u52A8\uFF0C\u4E00\u62DB\u6BD9\u547D\u3002</p><p>\u738B\u50E7\u8FA9\u5728\u88AB\u9648\u9738\u5148\u6355\u83B7\u4EE5\u540E\uFF0C\u66FE\u7ECF\u6C42\u9976\u3002\u4F46\u5176\u5B9E\uFF0C\u4ED6\u53EA\u8981\u771F\u7684\u60F3\u6E05\u695A\u4E86\u9648\u9738\u5148\u6B64\u884C\u7684\u9634\u9669\u52A8\u673A\uFF0C\u5C31\u4E0D\u4F1A\u6709\u8FD9\u79CD\u4E3E\u52A8\u4E86\u3002</p><p><strong>\u9648\u9738\u5148\u5C31\u662F\u6765\u8981\u4ED6\u7684\u547D\u7684\u3002</strong></p><p>\u7AD9\u5728\u9648\u9738\u5148\u7684\u89D2\u5EA6\u4E0A\uFF0C<strong>\u4ED6\u4E00\u65E6\u4E00\u51FB\u6210\u529F\uFF0C\u6536\u76CA\u5C31\u5C06\u662F\u6574\u4E2A\u8427\u6881\u738B\u671D\u7684\u6743\u67C4\u3002</strong></p><p>\u90A3\u8FD9\u4E0E\u73AF\u5883\u53C8\u6709\u4EC0\u4E48\u5173\u7CFB\uFF1F</p><p>\u5173\u952E\u7684\u539F\u56E0\u5728\u4E8E\uFF1A<strong>\u7EF4\u6301\u7ADE\u4E89\u7684\u4E0B\u9650\u8981\u9760<code>\u5916\u529B</code>\uFF0C\u800C\u4E0D\u662F\u9760\u5F53\u4E8B\u4EBA\u7684\u81EA\u89C9\u3002</strong></p><p>\u5982\u679C\u53EA\u9760\u5F53\u4E8B\u4EBA\u7684\u81EA\u89C9\uFF0C\u90A3\u4E48\u6700\u7EC8\u80DC\u51FA\u7684\u4E00\u5B9A\u662F\u65E0\u8D56\uFF1A\u56E0\u4E3A\u53EA\u6709\u4ED6\u4EEC\u80FD\u8F7B\u6613\u7A81\u7834\u201C\u5EA6\u201D\uFF0C\u4E0D\u4EC5\u4E0D\u60DC\u4F7F\u7528\u964D\u7EF4\u653B\u51FB\uFF0C\u65E0\u4E0B\u9650\u5730\u505A\u65E0\u4E0B\u9650\u7684\u4E8B\uFF0C\u800C\u4E14\u8FD8\u80FD\u65E0\u6240\u4E0D\u7528\u5176\u6781\uFF0C\u4E3A\u4EBA\u6240\u4E0D\u80FD\u3002</p><p>\u9762\u5BF9\u7740\u8FD9\u79CD\u7ADE\u4E89\uFF0C\u81EA\u89C9\u7684\u6B63\u4EBA\u541B\u5B50\u6CE8\u5B9A\u5931\u8D25\u3002</p><p><strong>\u7ADE\u4E89\u4E00\u65E6\u6CA1\u6709\u7EA6\u675F\uFF0C\u5FC5\u7136\u4F1A\u7A81\u7834\u4E0B\u9650\u3002\u800C\u4E0B\u9650\u4E00\u65E6\u88AB\u7A81\u7834\uFF0C\u6700\u7EC8\u83B7\u80DC\u7684\u53EA\u4F1A\u662F\u5C0F\u4EBA\u3002</strong></p><p>\u9648\u9738\u5148\u5C31\u662F\u5178\u578B\u3002</p><p>\u5B8B\u9F50\u6881\u9648\uFF0C\u9648\u5DF2\u7ECF\u662F\u5357\u671D\u7684\u6700\u540E\u4E00\u4E2A\u671D\u4EE3\u3002\u4ECE\u9648\u9738\u5148\u79F0\u5E1D\u5F00\u59CB\u7B97\u8D77\uFF0C\u5B83\u4E00\u5171\u53EA\u6301\u7EED\u4E8632\u5E74\u3002<strong>\u516C\u5143589\u5E74\uFF0C\u968B\u519B\u653B\u5165\u5EFA\u5EB7\uFF0C\u9648\u706D\u4EA1\u3002</strong></p><p>\u5728\u968B\u519B\u4E4B\u4E2D\uFF0C\u6709\u4E00\u4F4D\u53EB<code>\u738B\u9881</code>\u7684\u5C06\u9886\uFF0C\u6B63\u662F\u738B\u50E7\u8FA9\u4E4B\u5B50\u3002\u5F53\u5E74\u6C5F\u9675\u88AB\u897F\u9B4F\u653B\u7834\uFF0C\u738B\u9881\u8FDB\u5165\u5173\u4E2D\uFF0C\u8F97\u8F6C\u6210\u4E3A\u968B\u519B\uFF0C\u5982\u4ECA\u53C8\u91CD\u56DE\u6C5F\u5357\u3002</p><p>\u653B\u706D\u9648\u4EE5\u540E\uFF0C\u738B\u9881\u53EC\u96C6\u7236\u4EB2\u5F53\u5E74\u7684\u6B8B\u90E8\uFF0C\u6240\u5F97\u4EC5\u5269\u5343\u4F59\u4EBA\u3002\u6C14\u6124\u96BE\u5E73\u7684\u738B\u9881\uFF0C\u5C06\u9648\u9738\u5148\u7684\u9675\u5893\u6398\u5F00\uFF0C\u711A\u5316\u9AA8\u9ABC\u4E3A\u7070\uFF0C\u559D\u5230\u809A\u5B50\u91CC\u3002</p><p><strong>\u771F\u6B63\u7684\u539F\u56E0\uFF0C\u662F\u5728\u4E8E\u5357\u5317\u5B9E\u529B\u7684<code>\u5DEE\u8DDD</code>\u3002</strong></p><p>\u81EA\u4FAF\u666F\u4E4B\u4E71\u5F00\u59CB\uFF0C\u5357\u65B9\u7684\u8870\u8D25\u662F\u8089\u773C\u53EF\u89C1\u7684\u3002\u4FAF\u666F\u4EC5\u4EC5\u4EE5\u4E00\u5343\u4F59\u4EBA\u7684\u8D44\u672C\uFF0C\u5C31\u53EF\u4EE5\u98A0\u8986\u4E00\u4E2A\u8C8C\u4F3C\u5F3A\u5927\u7684\u738B\u671D\uFF0C\u8FD9\u5176\u4E2D\u867D\u7136\u4E0D\u65E0\u8FD0\u6C14\u7684\u6210\u5206\uFF0C\u4F46\u540C\u65F6\uFF0C\u5357\u671D\u91D1\u7389\u5176\u5916\u3001\u6811\u5927\u4E2D\u7A7A\u7684\u73B0\u5B9E\u4E5F\u96BE\u4EE5\u5426\u8BA4\u3002</p><p>\u800C\u5317\u65B9\uFF0C\u867D\u7136\u5C1A\u5728\u4E24\u5206\u4E4B\u5C40\uFF0C**\u4F46\u897F\u9B4F\u8FD9\u4E9B\u5E74\u4E00\u76F4\u90FD\u5728\u6B63\u786E\u7684\u9053\u8DEF\u4E0A\u8D8A\u8D70\u8D8A\u5FEB\u3002**\u4E0D\u7BA1\u662F\u65E9\u4E9B\u5E74\u63A8\u884C\u5E9C\u5175\u5236\u6539\u9769\uFF0C\u8FD8\u662F\u540E\u6765\u501F\u4FAF\u666F\u4E4B\u4E71\u7684\u5951\u673A\u6781\u5927\u5730\u6269\u5F20\u9886\u571F\uFF0C\u8FD9\u4E9B\u90FD\u662F\u5B9E\u6253\u5B9E\u7684\u5B9E\u529B\u63D0\u5347\u3002</p><p>\u6B63\u662F\u56E0\u4E3A\u6709\u4E86\u897F\u9B4F\u7684\u5F3A\u5927\uFF0C\u624D\u4F1A\u6709\u5317\u65B9\u7EDF\u4E00\u5357\u65B9\u7684\u53EF\u80FD\u3002\u800C\u738B\u9881\u56E0\u7F18\u9645\u4F1A\u6D41\u843D\u5230\u5317\u65B9\uFF0C\u8FD9\u624D\u662F\u4ED6\u5F97\u4EE5\u590D\u4EC7\u7684\u6700\u5927\u5951\u673A\u3002</p><p>\u5728\u8FD9\u5F53\u4E2D\uFF0C\u738B\u9881\u505A\u4EC0\u4E48\u4E86\u5417\uFF1F</p><p>\u505A\u4E86\uFF0C\u4E5F\u6CA1\u505A\u3002</p><p>\u8BF4\u6CA1\u505A\uFF0C\u662F\u56E0\u4E3A\u4ED6\u81EA\u5DF1\u5728\u5927\u4EC7\u5F97\u62A5\u7684\u8FC7\u7A0B\u4E2D\uFF0C\u51FA\u529B\u751A\u5C0F\u3002\u4E0D\u7BA1\u6709\u4ED6\u6CA1\u4ED6\uFF0C\u9648\u90FD\u4F1A\u88AB\u968B\u6240\u706D\u3002</p><p>\u4F46\u4ED6\u4E5F\u505A\u4E86\uFF0C\u6240\u505A\u7684\uFF0C\u5C31\u662F\u642D\u4E0A\u897F\u9B4F\uFF08\u968B\uFF09\u8FD9\u4E2A\u84B8\u84B8\u65E5\u4E0A\u7684\u56FD\u5BB6\u7684\u4FBF\u8F66\u3002</p><p><strong>\u9009\u62E9\uFF0C\u771F\u7684\u6BD4\u52AA\u529B\u91CD\u8981\u3002</strong></p>',74),r=[t];function n(s,g){return o(),e("div",null,r)}const l=p(d,[["render",n],["__file","82.html.vue"]]);export{l as default};
