import{_ as p}from"./_plugin-vue_export-helper.cdc0426e.js";import{o,c as e,f as c}from"./app.eee000b7.js";const d={},t=c('<h1 id="\u9648\u9738\u5148" tabindex="-1"><a class="header-anchor" href="#\u9648\u9738\u5148" aria-hidden="true">#</a> \u9648\u9738\u5148</h1><blockquote><p>\u59CB\u5174\u592A\u5B88\u9648\u9738\u5148\u7ED3\u90E1\u4E2D\u8C6A\u6770\u6B32\u8BA8\u4FAF\u666F\uFF0C\u90E1\u4EBA\u4FAF\u5B89\u90FD\u3001\u5F20\u5072\u7B49\u5404\u5E05\u4F17\u5343\u9980\u4EBA\u5F52\u4E4B\u3002</p></blockquote><p><strong>\u300A\u8D44\u6CBB\u901A\u9274\xB7\u5377\u4E00\u516D\u4E8C\xB7\u6881\u7EAA\u5341\u516B\u300B</strong></p><p><strong>\u9648\u9738\u5148</strong></p><p>\u4E0D\u8BBA\u662F\u4ED6\u8FD9\u4E2A\u7687\u5E1D\u672C\u8EAB\uFF0C\u8FD8\u662F\u4ED6\u6240\u5F00\u521B\u7684\u671D\u4EE3\uFF0C\u5728\u5386\u53F2\u4E2D\u7684\u5B58\u5728\u611F\u90FD\u662F\u6BD4\u8F83\u5F31\u7684\uFF1A\u6BD5\u7ADF\uFF0C\u4F5C\u4E3A\u5357\u671D\u7684\u6700\u540E\u4E00\u4E2A\u671D\u4EE3\uFF0C<strong>\u9648\u9738\u5148\u6240\u521B\u7ACB\u7684<code>\u9648\u671D</code>\u4E0D\u7BA1\u662F\u5728\u56FD\u529B\uFF0C\u8FD8\u662F\u5728\u7586\u57DF\u4E0A\uFF0C\u90FD\u662F\u56DB\u671D\u4E4B\u672B\u3002</strong></p><p>\u800C\u5929\u4E0B\u5C40\u52BF\uFF0C\u6B64\u65F6\u5DF2\u7ECF\u65E5\u6E10\u660E\u6717\uFF0C**\u4EE5\u5317\u7EDF\u5357\u8D8B\u52BF\u5DF2\u6210\u3002**\u5728\u8FD9\u6837\u7684\u80CC\u666F\u4E0B\uFF0C\u9648\u671D\u6240\u8C13\u7684\u5F00\u56FD\u4E4B\u76DB\uFF0C\u4E0D\u8FC7\u53EA\u662F\u843D\u65E5\u4F59\u6656\u3002</p><p>\u4F46\u5386\u53F2\u7684\u5FC5\u7136\uFF0C\u5374\u4E0D\u610F\u5473\u7740\u4E2A\u4EBA\u6CA1\u6709\u9009\u62E9\u3002\u4EE5\u4FAF\u666F\u4E4B\u4E71\u4E3A\u5951\u673A\uFF0C\u5BD2\u95E8\u51FA\u8EAB\u7684\u6B66\u4EBA\u9648\u9738\u5148\u7ADF\u7136\u628A\u63E1\u4F4F\u4E86\u673A\u4F1A\uFF0C\u8F6C\u6218\u5929\u4E0B\uFF0C\u8FDB\u800C\u524A\u5E73\u56DB\u65B9\uFF0C\u8EAB\u5C45\u5927\u5B9D\uFF0C\u5B9E\u73B0\u4E86\u793E\u4F1A\u9636\u5C42\u7684\u5DE8\u5927\u8DC3\u8FC1\u2014\u2014\u8981\u77E5\u9053\uFF0C\u8FD9\u53EF\u53D1\u751F\u5728\u95E8\u7B2C\u4E4B\u89C1\u6700\u91CD\u7684\u5357\u671D\u3002</p><p>\u9648\u9738\u5148\u51FA\u8EAB\u8D2B\u5BD2\uFF0C\u53F2\u4E66\u8BB0\u8F7D\u4ED6\u201C<strong>\u5C11\u501C\u50A5\u6709\u5927\u5FD7\uFF0C\u4E0D\u6CBB\u751F\u4EA7\u3002\u65E2\u957F\uFF0C\u8BFB\u5175\u4E66\uFF0C\u591A\u6B66\u827A\uFF0C\u660E\u8FBE\u679C\u65AD\uFF0C\u4E3A\u5F53\u65F6\u6240\u63A8\u670D</strong>\u201D\u3002</p><p>\u5927\u610F\u662F\u8BF4\u9648\u9738\u5148\u5728\u5E74\u5C11\u65F6\u5C31\u80F8\u6000\u5927\u5FD7\uFF0C\u4E0D\u613F\u610F\u88AB\u751F\u4EA7\u52B3\u52A8\u6240\u9650\u5236\u3002\u957F\u5927\u4EE5\u540E\uFF0C\u559C\u6B22\u8BFB\u5175\u4E66\uFF0C\u6B66\u827A\u51FA\u4F17\uFF0C\u4E14\u660E\u4E8E\u4E8B\u7406\uFF0C\u4E3A\u4EBA\u679C\u65AD\uFF0C\u4E3A\u4F17\u4EBA\u6240\u4FE1\u670D\u3002</p><p>\u8FD9\u6BB5\u8BDD\u770B\u4E0A\u53BB\u5168\u662F\u8912\u4E49\u8BCD\uFF0C\u4F46\u4F60\u53EA\u8981\u8054\u7CFB\u4E00\u4E0B\u9648\u9738\u5148\u7684\u51FA\u8EAB\uFF0C\u5C31\u5927\u7EA6\u77E5\u9053\u5176\u4E2D\u6709\u51E0\u5206\u771F\u51E0\u5206\u5047\u4E86\u3002</p><p>\u9648\u9738\u5148\u662F\u5434\u5174\u4EBA\uFF0C\u5E74\u5C11\u65F6\u6B63\u597D\u8D76\u4E0A<code>\u8427\u6881</code>\u7684\u9EC4\u91D1\u5E74\u4EE3\uFF0C\u5F7C\u65F6\u53EF\u4EE5\u7B97\u662F\u5929\u4E0B\u592A\u5E73\u7684\u3002\u5728\u8FD9\u79CD\u65F6\u4EE3\u91CC\uFF0C\u4E61\u95F4\u201C\u4E0D\u6CBB\u751F\u4EA7\u201D\u7684\u5C11\u5E74\uFF0C\u5927\u6982\u4E5F\u5C31\u662F\u4E61\u91CC\u4E00\u9738\u4E86\u3002\u6240\u8C13\u7684\u201C\u501C\u50A5\u6709\u5927\u5FD7\u201D\uFF0C\u4E0D\u8FC7\u53EA\u662F\u6210\u529F\u4EE5\u540E\u5BF9\u201C\u4E0D\u6CBB\u751F\u4EA7\u201D\u7684\u6258\u8BCD\u3002\u60F3\u60F3\u4F60\u4EEC\u6751\u90A3\u4E9B\u4E0D\u613F\u610F\u5230\u5DE5\u5382\u6253\u5DE5\uFF0C\u6210\u5929\u53EA\u60F3\u7740\u6DF7\u793E\u4F1A\u8D5A\u5927\u94B1\u7684\u6DF7\u6DF7\uFF0C\u662F\u4E0D\u662F\u4E5F\u8FD9\u79CD\u5FB7\u6027\uFF1F</p><p>\u5F53\u6DF7\u6DF7\uFF0C\u80AF\u5B9A\u5C31\u5F97\u4F1A\u6253\u67B6\u3002\u8FD9\u4E00\u70B9\uFF0C\u53F2\u4E66\u91CC\u4E5F\u5199\u5F97\u5F88\u6E05\u695A\uFF1A\u201C\u591A\u6B66\u827A\u201D\u3002\u8FD9\u4E2A\u6B66\u827A\uFF0C\u80AF\u5B9A\u4E0D\u662F\u88AB\u6B66\u6797\u9AD8\u624B\u8C03\u6559\u8FC7\u7684\u90A3\u79CD\u201C\u6B66\u827A\u201D\uFF0C\u800C\u662F\u7ED3\u7ED3\u5B9E\u5B9E\u6253\u51FA\u6765\u7684\u7ECF\u9A8C\u3002\u4F46\u53F2\u4E66\u603B\u5F52\u8FD8\u662F\u8981\u7F8E\u5316\u4E00\u4E0B\u7684\uFF0C\u6240\u4EE5\u52A0\u4E86\u201C\u8BFB\u5175\u4E66\u201D\u4E09\u4E2A\u5B57\u3002\u6709\u8FD9\u4E09\u5B57\u5728\u524D\u9762\u6491\u7740\uFF0C\u201C\u591A\u6B66\u827A\u201D\u5C31\u4E0D\u662F\u7F3A\u70B9\u4E86\uFF0C\u53CD\u800C\u8FD8\u663E\u5F97\u8FD9\u5B8C\u5168\u5C31\u662F\u6CA1\u6709\u5D2D\u9732\u5934\u89D2\u7684\u201C\u540D\u5C06\u201D\uFF01\u518D\u52A0\u4E0A\u201C\u501C\u50A5\u6709\u5927\u5FD7\u201D\uFF0C\u600E\u4E48\u6837\uFF1F\u662F\u4E0D\u662F\u611F\u89C9\u672A\u6765\u738B\u8005\u7684\u5F62\u8C61\u5DF2\u7ECF\u8DC3\u7136\u6B32\u51FA\uFF1F</p><p>\u8499\u4EBA\u8FD9\u79CD\u4E8B\uFF0C\u679C\u7136\u8FD8\u662F\u8BFB\u4E66\u4EBA\u6700\u62FF\u624B\u3002</p><p>\u4E0D\u8FC7\u5728\u53F2\u4E66\u7684\u8FD9\u6BB5\u63CF\u8FF0\u4E2D\uFF0C\u9648\u9738\u5148\u771F\u6B63\u7684\u4F18\u70B9\u4E5F\u8FD8\u771F\u6709\uFF1A\u201C<code>\u660E\u8FBE\u679C\u65AD</code>\u201D\uFF0C\u660E\u4E8E\u4E8B\u7406\uFF0C\u4E3A\u4EBA\u679C\u65AD\u3002</p><p>\u518D\u8054\u7CFB\u4E00\u4E0B\u201C\u591A\u6B66\u827A\u201D\uFF0C\u8FD9\u5C31\u4E0D\u662F\u4E00\u4E2A\u5C0F\u6DF7\u6DF7\uFF0C\u800C\u662F\u5C0F\u6DF7\u6DF7\u7684\u5934\u4E86\u3002</p><p>\u4EC0\u4E48\u6837\u7684\u4EBA\u80FD\u6210\u6DF7\u6DF7\u5934\uFF1F\u4E00\u5B9A\u662F\u90A3\u79CD\u80FD\u6253\u67B6\u3001\u6562\u6253\u67B6\uFF0C\u65E2\u61C2\u9053\u7406\uFF0C\u53C8\u505A\u4E8B\u679C\u65AD\u7684\u4EBA\u3002</p><p><strong>\u800C\u201C\u61C2\u9053\u7406\u201D\u3001\u505A\u4E8B\u679C\u65AD\uFF0C\u8FD9\u53C8\u662F\u628A\u4EBA\u4ECE\u82B8\u82B8\u4F17\u751F\u4E2D\u533A\u5206\u53D6\u6765\u7684\u5173\u952E\u54C1\u683C\u3002</strong></p><p>\u5982\u679C\u8BF4\u8FD9\u79CD\u54C1\u8D28\u662F\u9648\u9738\u5148\u80FD\u8D70\u4E0A\u6210\u529F\u9053\u8DEF\u7684\u7B2C\u4E00\u4E2A\u4F18\u52BF\uFF0C\u90A3\u4E48\u63A5\u4E0B\u6765\u7684\uFF0C\u5C31\u662F\u6210\u529F\u8DEF\u4E0A\u7684\u7B2C\u4E8C\u4E2A\u91CD\u8981\u652F\u70B9\u4E86\uFF1A<code>\u8D35\u4EBA\u76F8\u52A9</code>\u3002</p><p>\u770B\u4E0A\u4E86\u9648\u9738\u5148\u7684\uFF0C\u662F\u5434\u5174\u592A\u5B88<strong>\u8427\u668E</strong>\u3002</p><p>\u8427\u668E\u5BF9\u9648\u9738\u5148\u7684\u559C\u7231\u4E0D\u662F\u4E00\u661F\u534A\u70B9\uFF0C\u7B80\u76F4\u5230\u4E86\u7231\u4E0D\u91CA\u624B\u7684\u5730\u6B65\u3002\u4ED6\u9022\u4EBA\u5C31\u5938\uFF0C\u8BF4\u9648\u9738\u5148\u5C06\u6765\u5FC5\u5B9A\u524D\u9014\u8FDC\u5927\u3002</p><p>\u540E\u6765\u8427\u668E\u8C03\u4EFB\u5E7F\u5DDE\u523A\u53F2\uFF0C\u628A\u9648\u9738\u5148\u4E5F\u5E26\u53BB\u4E86\u5E7F\u5DDE\u3002\u6B64\u65F6\u7684\u9648\u9738\u5148\u5927\u6982\u5DF2\u7ECF\u5FEB\u56DB\u5341\u5C81\u4E86\uFF0C\u771F\u4E0D\u77E5\u9053\u8427\u668E\u662F\u4ECE\u4EC0\u4E48\u5730\u65B9\u770B\u51FA\u6765\u4ED6\u4E0E\u4F17\u4E0D\u540C\u7684\u3002</p><p><strong>\u800C\u771F\u6B63\u8BA9\u9648\u9738\u5148\u51FA\u4EBA\u5934\u5730\u7684\uFF0C\u6070\u597D\u5C31\u662F\u8FD9\u6B21\u8C03\u52A8\u3002</strong></p><p>\u8427\u668E\u5230\u5E7F\u5DDE\u4EE5\u540E\uFF0C\u6709\u5F53\u5730\u571F\u8C6A\u4E0D\u670D\u671D\u5EF7\u7684\u7BA1\u7406\uFF0C\u8D77\u5175\u4F5C\u4E71\uFF0C\u8427\u668E\u4E8E\u662F\u6D3E\u9648\u9738\u5148\u9886\u5175\u5E73\u5B9A\u3002\u8FD9\u6B21\u5E73\u53DB\uFF0C\u5B8C\u5168\u6539\u53D8\u4E86\u9648\u9738\u5148\u7684\u4EBA\u751F\u65B9\u5411\u3002</p><p>\u51E0\u5E74\u4EE5\u540E\uFF0C\u4EA4\u5DDE\u53C8\u53D1\u751F\u4E86\u4E00\u6B21\u66F4\u5927\u7684\u53DB\u4E71\uFF0C\u5F53\u5730\u571F\u8C6A<code>\u674E\u8D32</code>\u8D77\u5175\u4F5C\u4E71\uFF0C<code>\u6881\u6B66\u5E1D</code>\u547D\u5C06\u9886\u5F81\u8BA8\u3002\u540C\u65F6\uFF0C\u6881\u671D\u5C5E\u56FD\u6797\u9091\u56FD\u56FD\u738B\u4E5F\u8D77\u5175\uFF0C\u540E\u80CC\u540E\u653B\u51FB\u674E\u8D32\u3002\u4F46\u4E0D\u6599\u674E\u8D32\u80FD\u5F81\u60EF\u6218\uFF0C\u7ADF\u7136\u51FB\u8D25\u4E86\u6797\u9091\u738B\u548C\u6881\u519B\uFF0C\u8FDB\u800C\u5317\u4E0A\u3002\u5728\u51FB\u8D25\u6881\u519B\u7684\u63F4\u519B\u4EE5\u540E\uFF0C\u674E\u8D32\u56F4\u653B\u5E7F\u5DDE\uFF0C\u5C06\u8427\u668E\u56F4\u5728\u4E86\u5E7F\u5DDE\u57CE\u5185\u3002</p><p>\u9648\u9738\u5148\u95FB\u8BAF\u706B\u901F\u9A70\u63F4\uFF0C\u4E0D\u4EC5\u51FB\u8D25\u674E\u8D32\uFF0C\u89E3\u56F4\u5E7F\u5DDE\u57CE\uFF0C\u540C\u65F6\u8FD8\u53C8\u6D88\u706D\u4E86\u53E6\u4E00\u652F\u53DB\u4E71\u7684\u961F\u4F0D\u3002</p><p>\u6218\u62A5\u4F20\u5230\u5EFA\u5EB7\uFF0C\u6881\u6B66\u5E1D\u5BF9\u9648\u9738\u5148\u5927\u4E3A\u8D5E\u8D4F\uFF0C\u4E0D\u4EC5\u52A0\u5B98\u8FDB\u7235\uFF0C\u8FD8\u7279\u5730\u547D\u753B\u5DE5\u753B\u4E86\u9648\u9738\u5148\u7684\u8096\u50CF\u6765\u89C2\u770B\u3002\u8FD9\u4E2A\u5BD2\u95E8\u51FA\u8EAB\u7684\u5927\u5175\uFF0C\u7A81\u7136\u5C31\u8DC3\u5165\u4E86\u6881\u6B66\u5E1D\u7684\u89C6\u91CE\uFF0C\u5E76\u4E14\u5907\u53D7\u9752\u7750\u3002</p><p>\u4E00\u5E74\u4EE5\u540E\uFF0C\u8427\u668E\u5728\u5E7F\u5DDE\u75C5\u901D\uFF0C\u9648\u9738\u5148\u6276\u9675\u9001\u846C\u4E8E\u5EFA\u5EB7\u3002\u5728\u8FD4\u56DE\u5E7F\u5DDE\u7684\u9014\u4E2D\uFF0C\u86F0\u4F0F\u7684\u674E\u8D32\u518D\u6B21\u4F5C\u4E71\uFF0C\u6881\u6B66\u5E1D\u5373\u547D\u9648\u9738\u5148\u518D\u6B21\u5F81\u8BA8\u3002\u6B64\u540E\uFF0C\u7ECF\u5386\u4E09\u5E74\u591A\u65F6\u95F4\uFF0C\u9648\u9738\u5148\u7EC8\u4E8E\u6D88\u706D\u4E86\u674E\u6C0F\u7684\u53DB\u4E71\u519B\u961F\uFF0C\u8BDB\u6740\u674E\u8D32\u5144\u5F1F\u3002</p><p>\u8FD9\u4E00\u5E74\u5DF2\u7ECF\u662F\u592A\u6E05\u5143\u5E74\uFF0C\u8DDD\u79BB\u9648\u9738\u5148\u5230\u5E7F\u5DDE\u5DF2\u7ECF\u8FC7\u53BB\u4E86\u4E03\u5E74\u3002\u5728\u8FD9\u4E03\u5E74\u4E2D\uFF0C\u9648\u9738\u5148\u5176\u5B9E\u5C31\u505A\u4E86\u4E00\u4EF6\u4E8B\uFF1A\u5E73\u53DB\u3002\u4F46\u5C31\u662F\u8FD9\u4EF6\u4E8B\uFF0C\u8BA9\u4ED6\u4ECE\u8427\u668E\u8EAB\u8FB9\u4E00\u4E2A\u65E0\u8DB3\u8F7B\u91CD\u7684\u5C06\u9886\uFF0C\u53D8\u6210\u4E86\u5E1D\u56FD\u9547\u5B88\u5357\u7586\u7684\u67F1\u77F3\u3002</p><p>\u4E00\u4ECB\u5BD2\u95E8\uFF0C\u9760\u81EA\u5DF1\u7684\u594B\u6597\u6709\u4E86\u663E\u8D6B\u5B98\u7235\uFF0C\u8FD8\u5165\u4E86\u6881\u6B66\u5E1D\u7684\u6CD5\u773C\u3002\u5728\u5F53\u5E74\u4ECE\u5434\u5174\u5230\u5E7F\u5DDE\u7684\u8DEF\u4E0A\uFF0C\u9648\u9738\u5148\u5927\u6982\u505A\u68A6\u90FD\u4E0D\u6562\u60F3\u81EA\u5DF1\u80FD\u62FF\u5230\u8FD9\u6837\u7684\u5267\u672C\u3002</p><p>\u4E3A\u4EC0\u4E48\u8BF4\u8427\u668E\u662F\u9648\u9738\u5148\u7684\u8D35\u4EBA\uFF1F\u5E76\u4E0D\u662F\u56E0\u4E3A\u8427\u668E\u8BA9\u9648\u9738\u5148\u98DE\u9EC4\u817E\u8FBE\u4E86\uFF0C\u800C\u5728\u4E8E\u4ED6\u7ED9\u4E86\u9648\u9738\u5148\u4E00\u4E2A\u673A\u4F1A\uFF0C\u4E00\u4E2A\u8BA9\u9648\u9738\u5148\u5F97\u4EE5\u5145\u5206\u65BD\u5C55\u81EA\u5DF1\u624D\u534E\u7684\u673A\u4F1A\u3002</p><p>\u6240\u4EE5\uFF0C\u4EC0\u4E48\u624D\u662F<code>\u8D35\u4EBA</code>\uFF1F</p><p><strong>\u662F\u90A3\u79CD\u7ED9\u4F60\u673A\u4F1A\uFF0C\u8BA9\u4F60\u5728\u7ADE\u4E89\u8005\u4E2D\u80FD\u591F\u8FC5\u901F\u8131\u9896\u800C\u51FA\u7684\u4EBA\u3002</strong></p><p>\u53E4\u5F80\u4ECA\u6765\uFF0C\u6709\u591A\u5C11\u80FD\u4EBA\u5FD7\u58EB\uFF0C\u7F3A\u7684\u5E76\u4E0D\u662F\u80FD\u529B\uFF0C\u800C\u662F\u8BA9\u4ED6\u4EEC\u7684\u51FA\u4F17\u80FD\u529B\u5F97\u4EE5\u65BD\u5C55\u7684\u5E73\u53F0\u3002\u5982\u4ECA\u8427\u668E\u7ED9\u9648\u9738\u5148\u7684\uFF0C\u5C31\u662F\u8FD9\u4E48\u4E00\u4E2A\u673A\u4F1A\u3002</p><p>\u5982\u679C\u9648\u9738\u5148\u5F53\u5E74\u7559\u5728\u5434\u5174\uFF0C\u90A3\u4E48\u4ED6\u5927\u7EA6\u662F\u6CA1\u6709\u673A\u4F1A\u7ECF\u5386\u8FD9\u4E48\u4E00\u573A\u9886\u5175\u5E73\u53DB\u7684\u5927\u6218\u7684\u3002</p><p>\u800C\u5E73\u53DB\u4E4B\u540E\uFF0C\u9648\u9738\u5148\u53C8\u62E5\u6709\u4E86\u6210\u529F\u8DEF\u4E0A\u7684\u7B2C\u4E09\u9879\u56E0\u7D20\uFF1A<code>\u8FD0\u6C14</code>\u3002</p><p>\u5E73\u5B9A\u674E\u6C0F\u53DB\u4E71\u7684\u90A3\u4E00\u5E74\u662F\u592A\u6E05\u5143\u5E74\uFF08\u516C\u5143547\u5E74\uFF09\u3002\u8FD9\u4E00\u5E74\uFF0C\u9648\u9738\u514845\u5C81\uFF0C\u6B63\u662F\u5E74\u5BCC\u529B\u5F3A\u7684\u65F6\u5019\u3002\u7B2C\u4E8C\u5E74\uFF0C<code>\u4FAF\u666F</code>\u8D77\u5175\u4F5C\u4E71\u3002</p><p>\u547D\u8FD0\u7684\u5B89\u6392\u5C31\u662F\u8FD9\u4E48\u5DE7\u5999\u3002\u516C\u5143547\u5E74\u5E74\u521D\uFF0C<code>\u9AD8\u6B22</code>\u75C5\u901D\uFF0C\u4FAF\u666F\u4E0D\u5B89\u4E8E<code>\u4E1C\u9B4F</code>\uFF0C\u968F\u5373\u53CD\u53DB\uFF0C\u8F93\u8BDA\u4E8E<code>\u8427\u6881</code>\u3002\u4F46\u5728\u540E\u6765\u7684\u4E00\u7CFB\u5217\u5916\u4EA4\u5F81\u6218\u8FC7\u7A0B\u4E2D\uFF0C\u4FAF\u666F\u53C8\u5BF9\u8427\u6881\u751F\u51FA\u7591\u5FC3\uFF0C\u9042\u8D77\u5175\u4F5C\u4E71\u3002</p><p><strong>\u5BF9\u9648\u9738\u5148\u6765\u8BF4\uFF0C\u547D\u8FD0\u4E4B\u795E\u7684\u5782\u9752\uFF0C\u5728\u4E8E\u521A\u521A\u597D</strong>\uFF1A\u5982\u679C\u4FAF\u666F\u65E9\u4E00\u5E74\u8D77\u5175\uFF0C\u9648\u9738\u5148\u5E73\u53DB\u672A\u679C\uFF0C\u8427\u6881\u56F0\u4E8E\u4FAF\u666F\u538B\u529B\uFF0C\u8FB9\u754C\u7684\u75A5\u7663\u4E4B\u75BE\u52BF\u5FC5\u4E0D\u4F1A\u88AB\u91CD\u89C6\u3002\u674E\u8D32\u5F88\u6709\u53EF\u80FD\u5377\u571F\u91CD\u6765\uFF0C\u9006\u8F6C\u5F62\u52BF\uFF0C\u9648\u9738\u5148\u5C06\u518D\u65E0\u8FD8\u624B\u4E4B\u529B\u3002</p><p>\u800C\u5982\u679C\u4FAF\u666F\u665A\u4E00\u5E74\u8D77\u5175\uFF0C\u90A3\u4E48\u6B66\u4EBA\u51FA\u8EAB\u7684\u9648\u9738\u5148\u5FC5\u7136\u4F1A\u5728\u4E00\u7CFB\u5217\u590D\u6742\u7684\u4EBA\u4E8B\u5B89\u6392\u4E2D\u9010\u6E10\u88AB\u8FB9\u7F18\u5316\uFF0C\u4E0D\u53EF\u80FD\u6709\u771F\u5B9E\u5386\u53F2\u4E2D\u90A3\u4E48\u5927\u7684\u5F71\u54CD\u529B\u3002</p><p>\u53EA\u6709\u73B0\u5982\u4ECA\uFF0C\u4EA4\u5E7F\u4E00\u5E26\u5C40\u52BF\u752B\u5B9A\uFF0C\u9648\u9738\u5148\u4E5F\u6709\u519B\u804C\u5728\u8EAB\uFF0C\u6709\u4E86\u8D77\u5175\u5E73\u53DB\u7684\u8D44\u672C\u548C\u6761\u4EF6\u3002</p><p>\u4E0D\u8FC7\u73B0\u5B9E\u603B\u662F\u9AA8\u611F\u7684\uFF0C\u9648\u9738\u5148\u867D\u6709\u4E00\u8154\u5E73\u53DB\u4E4B\u5FC3\uFF0C\u4F46\u4EA4\u5E7F\u4E00\u5E26\uFF0C\u8BF8\u4FAF\u5404\u6000\u9B3C\u80CE\u3002\u5927\u5BB6\u60F3\u7740\u7684\u5E76\u4E0D\u662F\u5982\u4F55\u5C3D\u5FEB\u8D77\u5175\uFF0C\u51FB\u8D25\u4FAF\u666F\u53DB\u4E71\uFF0C\u800C\u662F\u600E\u4E48\u5404\u81EA\u5360\u5C71\u4E3A\u738B\uFF0C\u62DB\u5175\u4E70\u9A6C\uFF0C\u9759\u89C2\u5C40\u52BF\u3002</p><p>\u9648\u9738\u5148\u5373\u4FBF\u60F3\u9886\u5175\u5317\u4E0A\uFF0C\u73B0\u5B9E\u4E2D\u4E5F\u56F0\u96BE\u91CD\u91CD\uFF0C\u5B8C\u5168\u6CA1\u6709\u60F3\u8C61\u4E2D\u90A3\u79CD\u201C\u541B\u7236\u6709\u96BE\uFF0C\u5929\u4E0B\u52E4\u738B\u201D\u7684\u5C40\u9762\u3002</p><p>\u592A\u6E05\u5143\u5E74\uFF08\u516C\u5143548\u5E74\uFF09\u4FAF\u666F\u53DB\u4E71\u5F00\u59CB\u65F6\uFF0C\u9648\u9738\u5148\u5373\u610F\u6B32\u8D77\u5175\u5317\u4E0A\uFF0C\u4F46\u76F4\u5230\u4E09\u5E74\u4EE5\u540E\u7684\u5927\u5B9D\u4E8C\u5E74\uFF08\u516C\u5143551\u5E74\uFF09\uFF0C\u52E4\u738B\u519B\u961F\u624D\u8D70\u51FA\u5927\u5EBE\u5CAD\uFF0C\u8FDB\u5165\u6C5F\u5DDE\uFF0C\u6EAF\u8D63\u6C5F\u5317\u4E0A\u3002</p><p>\u8FD9\u4E09\u5E74\u4E2D\uFF0C\u9648\u9738\u5148\u5E94\u8BE5\u8FC7\u5F97\u76F8\u5F53\u59D4\u5C48\uFF0C\u81EA\u5DF1\u660E\u660E\u4E00\u8154\u70ED\u8840\u62A5\u6548\u671D\u5EF7\uFF0C\u8EAB\u8FB9\u7684\u4EBA\u5374\u603B\u662F\u7AD9\u5728\u5BF9\u7ACB\u9762\u4E0A\u963B\u6270\u884C\u4E8B\u3002</p><p>\u9996\u5148\u662F\u5E7F\u5DDE\u523A\u53F2<code>\u5143\u666F\u4EF2</code>\u3002</p><p>\u4FAF\u666F\u4E4B\u4E71\u7684\u6D88\u606F\u4F20\u5230\u5E7F\u5DDE\u540E\uFF0C\u5143\u666F\u4EF2\u5C31\u5728\u79C1\u4E0B\u91CC\u56E4\u79EF\u7CAE\u8349\uFF0C\u5E76\u4E14\u4F3A\u673A\u9664\u6389\u9648\u9738\u5148\uFF0C\u5272\u636E\u4E00\u65B9\u3002</p><p>\u9648\u9738\u5148\u5F97\u77E5\u6D88\u606F\u540E\uFF0C\u5148\u53D1\u5236\u4EBA\uFF0C\u51FB\u8D25\u4E86\u5143\u666F\u4EF2\u3002\u968F\u540E\uFF0C\u9648\u9738\u5148\u8FCE\u5949\u8427\u6881\u5B97\u5BA4<code>\u8427\u52C3</code>\u4E3A\u5E7F\u5DDE\u523A\u53F2\uFF0C\u53C8\u51FB\u8D25\u4E86\u53D1\u751F\u5728\u59CB\u5174\u90E1\u7684\u53DB\u4E71\uFF0C\u51C6\u5907\u51FA\u5175\u5317\u4F10\u3002</p><p>\u4F46\u8427\u52C3\u4E5F\u4E0D\u914D\u5408\u3002</p><p>\u6B64\u65F6<code>\u6881\u6B66\u5E1D</code>\u5DF2\u6B7B\uFF0C\u4FAF\u666F\u63A7\u5236\u4E86<code>\u7B80\u6587\u5E1D</code>\uFF0C\u5404\u65B9\u8BF8\u4FAF\u9664\u6E58\u4E1C\u738B<code>\u8427\u7ECE</code>\u5916\uFF0C\u5728\u660E\u9762\u4E0A\u8FD8\u90FD\u5C0A\u5D07\u5EFA\u5EB7\u671D\u5EF7\u7684\u53F7\u4EE4\u3002\u9648\u9738\u5148\u867D\u7136\u60F3\u8BA8\u4F10\u4FAF\u666F\uFF0C\u4F46\u5E08\u51FA\u65E0\u540D\uFF0C\u56E0\u6B64\u53EA\u5F97\u6D3E\u4EBA\u5411\u8FDC\u5728\u6C5F\u9675\u7684\u6E58\u4E1C\u738B\u8427\u7ECE\u8F93\u8BDA\uFF0C\u53D7\u5176\u8282\u5236\u3002</p><p>\u5728\u5F97\u5230\u8427\u7ECE\u7684\u6388\u6743\u540E\uFF0C\u9648\u9738\u5148\u7EC8\u4E8E\u53EF\u4EE5\u8D77\u5175\u5317\u4E0A\u3002\u4F46\u5728\u5927\u5EBE\u5CAD\uFF0C\u4ED6\u53C8\u88AB\u8427\u52C3\u90E8\u4E0B\u963B\u62E6\u3002</p><p>\u51FB\u8D25\u4E86\u963B\u62E6\u519B\u961F\u540E\uFF0C\u752B\u5165\u6C5F\u5DDE\uFF0C\u9648\u9738\u5148\u53C8\u53D7\u5230\u5FE0\u4E8E\u4FAF\u666F\u7684\u5C06\u9886\u7684\u963B\u51FB\u3002\u7ECF\u8FC7\u4E86\u5927\u7EA6\u4E00\u5E74\u591A\u7684\u7F20\u6597\uFF0C\u9648\u9738\u5148\u624D\u5F97\u4EE5\u51FB\u8D25\u540E\u8005\uFF0C\u6EAF\u8D63\u6C5F\u5317\u4E0A\u3002</p><p>\u4E5F\u5C31\u662F\u8BF4\uFF0C\u82B1\u4E86\u8FD1\u4E09\u5E74\u65F6\u95F4\uFF0C\u9648\u9738\u5148\u624D\u771F\u6B63\u6709\u4E86\u201C\u52E4\u738B\u201D\u7684\u673A\u4F1A\u3002</p><p>\u4F46\u547D\u8FD0\u4E4B\u795E\u53C8\u4E00\u6B21\u5782\u9752\u4E8E\u9648\u9738\u5148\uFF1A<strong>\u7B49\u4ED6\u5317\u4E0A\u65F6\uFF0C\u4FAF\u666F\u7684\u4E3B\u529B\u90E8\u961F\u5DF2\u88AB<code>\u738B\u50E7\u8FA9</code>\u51FB\u8D25\u3002</strong></p><p>\u5982\u679C\u4ECE\u4F5C\u6218\u7684\u89D2\u5EA6\u770B\uFF0C\u8FD9\u662F\u5178\u578B\u7684\u201C\u8D77\u4E86\u4E2A\u5927\u65E9\uFF0C\u8D76\u4E86\u4E2A\u665A\u96C6\u201D\u3002\u4F46\u5BF9\u9648\u9738\u5148\u6765\u8BF4\uFF0C\u8FD9\u5374\u662F\u5929\u5927\u7684\u597D\u4E8B\uFF1A\u4ED6\u907F\u514D\u4E86\u4E0E\u4FAF\u666F\u4E3B\u529B\u4EA4\u6218\uFF0C\u770B\u4E0A\u53BB\u5012\u50CF\u4E2A\u6458\u6843\u5B50\u5403\u7684\u4EBA\u2014\u2014\u53C8\u662F\u4E00\u4E2A\u521A\u521A\u597D\u3002</p><p>\u6B64\u540E\u7684\u8FC7\u7A0B\uFF0C\u6CE2\u6F9C\u4E0D\u60CA\u3002\u738B\u3001\u9648\u8054\u519B\u4E00\u8DEF\u6CBF\u957F\u6C5F\u4E1C\u4E0B\uFF0C\u6700\u7EC8\u653B\u5165\u5EFA\u5EB7\uFF0C\u5E73\u5B9A\u4E86\u4FAF\u666F\u4E4B\u4E71\u3002\u6218\u540E\u8BBA\u529F\u884C\u8D4F\uFF0C\u9648\u9738\u5148\u4E0D\u8BBA\u5B98\u804C\u8FD8\u662F\u7235\u4F4D\uFF0C\u90FD\u5728\u738B\u50E7\u8FA9\u4E4B\u4E0B\u3002</p><p>\u5982\u679C\u6545\u4E8B\u5C31\u6B64\u622A\u6B62\uFF0C\u90A3\u4E48\u9648\u9738\u5148\u7559\u5728\u5386\u53F2\u4E2D\u7684\u5F62\u8C61\uFF0C\u53EF\u80FD\u4E5F\u5C31\u662F\u4E00\u4E2A\u8F85\u653F\u5927\u81E3\u2014\u2014\u867D\u7136\u4EE5\u5357\u671D\u5BF9\u8F85\u653F\u5927\u81E3\u7684\u82DB\u523B\uFF0C\u4ED6\u5E76\u4E0D\u4E00\u5B9A\u80FD\u5584\u7EC8\u3002</p><p>\u4F46\u662F\uFF0C<strong>\u9648\u9738\u5148\u5374\u7528<code>\u5173\u952E\u65F6\u523B\u7684\u4E00\u6B21\u9009\u62E9</code>\uFF0C\u5F7B\u5E95\u6539\u53D8\u4E86\u6B64\u540E\u7684\u4EBA\u751F\u2014\u2014\u800C\u8FD9\uFF0C\u4E5F\u6B63\u662F\u4ED6\u6210\u529F\u7684\u7B2C\u56DB\u4E2A\u56E0\u7D20\u3002</strong></p><p>\u51FB\u8D25\u4FAF\u666F\u540E\uFF0C\u8427\u7ECE\u6700\u7EC8\u79F0\u5E1D\u3002\u4F46\u4ED6\u4E00\u76F4\u5F85\u5728\u81EA\u5DF1\u7684\u8001\u5DE2\u6C5F\u9675\uFF0C\u4E0D\u613F\u610F\u5230\u5EFA\u5EB7\u53BB\uFF1B\u800C\u4E14\u5BF9<code>\u897F\u9B4F</code>\uFF0C\u4E5F\u4E00\u76F4\u758F\u4E8E\u9632\u5907\u3002\u6700\u540E\uFF0C\u897F\u9B4F\u8D81\u8427\u7ECE\u4E0D\u5907\uFF0C\u7ADF\u7136\u653B\u7834\u4E86\u6C5F\u9675\uFF0C\u8BDB\u6740\u8427\u7ECE\u3002</p><p>\u6B64\u65F6\uFF0C\u738B\u50E7\u8FA9\u4E0E\u9648\u9738\u5148\u9A7B\u4E8E\u5EFA\u5EB7\u3002\u4E24\u4EBA\u5546\u91CF\u540E\uFF0C\u51B3\u5B9A\u7ACB\u8427\u7ECE\u4E4B\u5B50<code>\u8427\u65B9\u667A</code>\u4E3A\u5E1D\u3002\u4F46\u4E8B\u60C5\u5C1A\u672A\u6210\u884C\uFF0C<code>\u5317\u9F50</code>\u5927\u519B\u5C31\u5357\u4E0B\u800C\u6765\uFF0C\u5E0C\u671B\u6276\u7ACB<code>\u8427\u6E0A\u660E</code>\u4E3A\u5E1D\u3002</p><p>\u8FEB\u4E8E\u538B\u529B\uFF0C\u738B\u50E7\u8FA9\u66F4\u6539\u4E86\u521D\u8877\uFF0C\u4F46\u8FD9\u906D\u5230\u9648\u9738\u5148\u7684\u53CD\u5BF9\u3002\u800C\u4E14\u9648\u9738\u5148\u7684\u53CD\u5BF9\u65B9\u6CD5\u5341\u5206\u76F4\u63A5\uFF1A<strong>\u8D81\u738B\u50E7\u8FA9\u4E0D\u5907\uFF0C\u88AD\u51FB\u738B\u50E7\u8FA9\u3002\u5F97\u624B\u540E\uFF0C\u9648\u9738\u5148\u8FDE\u591C\u6740\u6389\u4E86\u738B\u50E7\u8FA9\uFF0C\u4ECE\u6B64\u638C\u63E1\u6881\u671D\u5B9E\u6743\u3002</strong></p><p>\u6B64\u540E\uFF0C\u9648\u9738\u5148\u5148\u540E\u51FB\u8D25\u4E86\u5FE0\u4E8E\u738B\u50E7\u8FA9\u7684\u52BF\u529B\u7684\u6570\u6B21\u53CD\u6297\uFF0C\u4EE5\u53CA\u5317\u9F50\u7684\u8FDB\u653B\uFF0C\u6700\u7EC8\u903C\u8FEB<code>\u6881\u656C\u5E1D</code>\u9000\u4F4D\uFF0C\u767B\u57FA\u79F0\u5E1D\u3002</p>',61),r=[t];function n(s,g){return o(),e("div",null,r)}const i=p(d,[["render",n],["__file","84.html.vue"]]);export{i as default};
