import{_ as o}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as p,c as e,f as c}from"./app.21634df6.js";const t={},d=c('<h1 id="\u5317\u5468" tabindex="-1"><a class="header-anchor" href="#\u5317\u5468" aria-hidden="true">#</a> \u5317\u5468</h1><blockquote><p>\u4E1E\u76F8\u6CF0\u81EA\u5F18\u519C\u4E3A\u6865\uFF0C\u6D4E\u6CB3\uFF0C\u81F3\u5EFA\u5DDE\u3002\u4E19\u5BC5\uFF0C\u9F50\u4E3B\u81EA\u5C06\u51FA\u987F\u4E1C\u57CE\u3002\u6CF0\u95FB\u5176\u519B\u5BB9\u4E25\u76DB\uFF0C\u53F9\u66F0\uFF1A\u201C\u9AD8\u6B22\u4E0D\u6B7B\u77E3\uFF01\u201D\u4F1A\u4E45\u96E8\uFF0C\u81EA\u79CB\u53CA\u51AC\uFF0C\u9B4F\u519B\u755C\u4EA7\u591A\u6B7B\uFF0C\u4E43\u81EA\u84B2\u962A\u8FD8\u3002\u4E8E\u662F\u6CB3\u5357\u81EA\u6D1B\u9633\uFF0C\u6CB3\u5317\u81EA\u5E73\u9633\u5DF2\u4E1C\uFF0C\u7686\u5165\u4E8E\u9F50\u3002</p></blockquote><p><strong>\u300A\u8D44\u6CBB\u901A\u9274\xB7\u5377\u4E00\u516D\u4E09\xB7\u6881\u7EAA\u5341\u4E5D\u300B</strong></p><p>\u867D\u7136\u8FD9\u4E2A\u671D\u4EE3\u7684\u77E5\u540D\u5EA6\u5728\u666E\u7F57\u5927\u4F17\u4E2D\u5E76\u4E0D\u9AD8\uFF0C\u4F46\u662F\uFF0C\u5B83\u5728\u5386\u53F2\u4E2D\u7684\u5F71\u54CD\u5374\u5206\u91CF\u5341\u8DB3\uFF1A</p><blockquote><p>\u81EA\u4E1C\u6C49\u672B\u5E74\u4EE5\u6765\uFF0C\u8FD1\u56DB\u767E\u5E74\u5206\u88C2\u5C40\u9762\u7684\u7EC8\u7ED3\uFF0C\u5176\u5B9E\u8087\u59CB\u4E8E\u5317\u5468\uFF1B \u5F71\u54CD\u4E2D\u56FD\u5317\u65B9\u8FD1\u4E09\u767E\u5E74\u7684\u6C11\u65CF\u95EE\u9898\uFF0C\u6700\u540E\u4E5F\u7EC8\u7ED3\u4E8E\u5317\u5468\uFF1B \u800C\u81EA\u79E6\u6C49\u7B2C\u4E00\u5E1D\u56FD\u5D29\u6E83\u4E4B\u540E\uFF0C\u968B\u5510\u7B2C\u4E8C\u5E1D\u56FD\u7684\u5D1B\u8D77\uFF0C\u540C\u6837\u8FD8\u662F\u53D1\u8F6B\u4E8E\u5317\u5468\u3002</p></blockquote><p>\u5F53\u7136\uFF0C\u6211\u4EEC\u4E0D\u80FD\u628A\u6240\u6709\u7684\u529F\u52B3\u90FD\u5F52\u56E0\u4E8E\u5317\u5468\u548C\u5B87\u6587\u6C0F\u3002\u6BD5\u7ADF\uFF0C\u4E00\u4E2A\u9AD8\u60AC\u4E86\u51E0\u767E\u5E74\u7684\u5927\u95EE\u9898\u80FD\u591F\u5F97\u5230\u89E3\u51B3\uFF0C\u4E00\u5B9A\u662F\u51E0\u4EE3\u4EBA\u751A\u81F3\u51E0\u5341\u4EE3\u4EBA\u4E0D\u65AD\u63A2\u7D22\u8FFD\u6C42\u7684\u7ED3\u679C\u3002\u5230\u5B87\u6587\u6C0F\u548C\u5317\u5468\u8FD9\u91CC\uFF0C\u767E\u91CC\u8DEF\u5DF2\u884C\u4E5D\u5341\uFF0C\u5982\u4ECA\u4E0D\u8FC7\u662F\u4E5D\u4EDE\u9AD8\u5C71\uFF0C\u60DF\u5DEE\u4E00\u7BD1\u3002</p><p>\u4F46\u5B87\u6587\u6C0F\u5728\u5386\u53F2\u4E2D\u7EFD\u653E\u7684\u5149\u8292\u4F9D\u7136\u662F\u8000\u773C\u7684\u3002\u4ED6\u4EEC\u7684\u529F\u7EE9\uFF0C\u5728\u4E8E\u8865\u9F50\u4E86\u6700\u540E\u70B9\u775B\u7684\u90A3\u4E00\u7B14\u3002\u81EA\u6B64\uFF0C\u6F5C\u9F99\u7EC8\u9192\uFF0C\u817E\u6D77\u76F4\u4E0A\u800C\u7FD4\u4E8E\u4E5D\u5929\u3002</p><p>\u867D\u7136\u4E4B\u540E\u7684\u6545\u4E8B\u4EE4\u4EBA\u559F\u53F9\uFF1A\u5B87\u6587\u6C0F\u5B8C\u6210\u4E86\u8FD9\u4E00\u7B14\uFF0C\u5374\u6700\u7EC8\u6CA1\u80FD\u591F\u5750\u4EAB\u5176\u6210\u3002\u4F46\u662F\u8FD9\u5E76\u4E0D\u5F71\u54CD\u6211\u4EEC\u4ECA\u5929\u7FFB\u5F00\u8FD9\u6BB5\u5386\u53F2\uFF0C\u53BB\u542C\u90A3\u9F99\u9E23\u4E8E\u5929\u7684\u7A7F\u4E91\u88C2\u77F3\u4E4B\u58F0\u3002</p><p>\u4ECE<code>\u9AD8\u6D0B</code>\u7684\u79F0\u5E1D\u5F00\u59CB\u8BF4\u8D77\u3002</p><p>\u9AD8\u6D0B\u5F53\u521D\u5728<code>\u4E1C\u9B4F</code>\u51C6\u5907\u79F0\u5E1D\u65F6\uFF0C\u51E0\u4E4E\u906D\u5230\u4E86\u6240\u6709\u4EBA\u7684\u53CD\u5BF9\u3002\u5927\u5BB6\u7684\u7406\u7531\u4E0D\u4E00\u800C\u8DB3\uFF0C\u4F46\u5176\u4E2D\u6709\u4E00\u6761\u5374\u662F\u5173\u4E8E<code>\u897F\u9B4F</code>\u7684\uFF1A\u5982\u4ECA\u4E1C\u897F\u9B4F\u5E76\u4E89\u6B63\u7EDF\uFF0C\u9AD8\u6D0B\u5982\u679C\u8D38\u7136\u5E9F\u9664\u4E1C\u9B4F\u7687\u5E1D\uFF0C\u53EA\u6015\u4F1A\u5931\u53BB\u6B63\u7EDF\u6240\u5728\uFF0C\u5F15\u8D77\u5929\u4E0B\u58EB\u4EBA\u7684\u79BB\u5FC3\u79BB\u5FB7\u3002</p><p>\u9AD8\u6D0B\u5F53\u65F6\u6B63\u5728\u5174\u5934\u4E0A\uFF0C\u8FDE\u4EB2\u5A18\u7684\u53CD\u5BF9\u610F\u89C1\u90FD\u4E0D\u653E\u5728\u773C\u91CC\uFF0C\u66F4\u4F55\u51B5\u662F\u9762\u5BF9\u8FD9\u79CD\u865A\u65E0\u7F25\u7F08\u7684\u7591\u8651\u3002\u79CD\u79CD\u53CD\u5BF9\uFF0C\u4E00\u70B9\u90FD\u6CA1\u6709\u5F71\u54CD\u4ED6\u7684\u767B\u57FA\u5927\u8BA1\uFF0C\u4ED6\u4F9D\u65E7\u903C\u8FEB<code>\u5B5D\u9759\u5E1D</code>\u9000\u4F4D\uFF0C\u81EA\u5DF1\u767B\u57FA\u79F0\u5E1D\uFF0C\u6539\u56FD\u53F7\u4E3A<code>\u9F50</code>\uFF0C\u53F2\u79F0<code>\u5317\u9F50</code>\u3002</p><p>\u5728\u4ED6\u767B\u57FA\u4E4B\u540E\uFF0C\u5317\u9F50\u56FD\u5185\u58EB\u65CF\u7684\u6C11\u5FC3\u662F\u5426\u6709\u6240\u6CE2\u52A8\uFF0C\u53F2\u6599\u9619\u5982\uFF0C\u6211\u4EEC\u5DF2\u7ECF\u4E0D\u5F97\u800C\u77E5\u3002\u4F46\u4E0D\u610F\u5916\u7684\u662F\uFF0C\u897F\u9B4F\u90A3\u8FB9\u679C\u7136\u8FD8\u662F\u52A8\u4E86\u52A8\u7684\u3002</p><p>\u9AD8\u6D0B\u4E94\u6708\u79F0\u5E1D\uFF0C\u6D88\u606F\u4F20\u5230\u957F\u5B89\u7684<code>\u5B87\u6587\u6CF0</code>\u8033\u4E2D\u65F6\uFF0C\u5DF2\u7ECF\u662F\u4E03\u6708\uFF0C\u5B87\u6587\u6CF0\u968F\u5373\u51B3\u5B9A\u5F15\u5175\u4E1C\u5F81\u2014\u2014\u60F3\u6765\uFF0C\u558A\u51FA\u7684\u53E3\u53F7\u5927\u6982\u4E5F\u5C31\u662F\u201C\u8BA8\u4E0D\u81E3\u201D\u4E4B\u7C7B\u7684\u3002</p><p>\u4F46\u4E4B\u540E\u53C8\u8FC7\u4E86\u4E24\u4E2A\u6708\uFF0C\u5DF2\u7ECF\u5230\u4E86\u4E5D\u6708\uFF0C\u5927\u519B\u624D\u7B97\u662F\u771F\u7684\u51FA\u4E86\u957F\u5B89\uFF0C\u5230\u8FBE\u6F7C\u5173\u3002\u800C\u4E14\u5230\u4E86\u6F7C\u5173\u4E5F\u6CA1\u6709\u7EE7\u7EED\u524D\u884C\uFF0C\u636E\u8BF4\u662F\u56E0\u4E3A\u5F53\u65F6\u5929\u6C14\u6076\u52A3\uFF0C\u519B\u961F\u4E2D\u7684\u725B\u9A6C\u6B7B\u4F24\u65E0\u7B97\u3002</p><p>\u5317\u9F50\u95FB\u542C\u897F\u9B4F\u884C\u52A8\u540E\uFF0C\u8FC5\u901F\u4F5C\u51FA\u53CD\u5E94\u3002\u4F46\u897F\u9B4F\u8FD9\u8FB9\u5462\uFF0C<strong>\u5B87\u6587\u6CF0\u542C\u8BF4\u9AD8\u6D0B\u519B\u5BB9\u9F50\u6574\u540E\uFF0C\u53D1\u4E86\u53E5\u611F\u6168\uFF1A\u8FD9\u4EFF\u4F5B\u662F\u9AD8\u6B22\u8FD8\u6CA1\u6B7B\u554A\uFF01\u968F\u5373\u5C31\u9000\u4E86\u519B\u3002</strong></p><p>\u201C\u542C\u8BF4\u201D\u3002</p><p>\u610F\u601D\u5C31\u662F\uFF0C\u5B87\u6587\u6CF0\u538B\u6839\u5C31\u6CA1\u6709\u4E0A\u5230\u524D\u7EBF\uFF0C\u4E5F\u6CA1\u6709\u4E0E\u5317\u9F50\u7684\u519B\u961F\u6253\u8FC7\u4ED7\u3002</p><p>\u800C\u636E\u53F2\u6599\u8BB0\u8F7D\uFF0C<strong>\u8FD9\u4E00\u201C\u4ED7\u201D\u4E0B\u6765\uFF0C\u897F\u9B4F\u8FD8\u635F\u5931\u4E86\u4E0D\u5C11\u5730\u76D8\u3002</strong></p><p>\u53F2\u4E66\u5BF9\u6B64\u6CA1\u6709\u7ED9\u51FA\u89E3\u91CA\uFF0C\u4F46\u6211\u4EEC\u4E0D\u59A8\u6765\u63A8\u6D4B\u4E00\u4E0B\u3002</p><p><strong>\u9996\u5148\uFF0C\u8FD9\u4E00\u4ED7\uFF0C\u5B87\u6587\u6CF0\u662F\u80AF\u5B9A\u4E0D\u60F3\u6253\u7684\u3002</strong></p><p>\u4ED6\u8981\u662F\u60F3\u6253\uFF0C\u603B\u5F52\u4E5F\u8981\u5230\u524D\u7EBF\u53BB\u770B\u4E00\u770B\u3002\u4E0D\u80FD\u9760\u4E00\u4E2A\u201C\u542C\u8BF4\u201D\uFF0C\u5C31\u76F4\u63A5\u9000\u4E86\u56DE\u53BB\u3002</p><p>\u800C\u4ED6\u4E0D\u60F3\u6253\u7684\u539F\u56E0\uFF0C\u65E0\u5916\u4E4E\u4E5F\u5C31\u662F\u5916\u56E0\u548C\u5185\u56E0\u3002</p><p><strong>\u6240\u8C13<code>\u5916\u56E0</code>\uFF0C\u662F\u5317\u9F50\u7684\u5B9E\u529B\u3002</strong></p><p>\u5B87\u6587\u6CF0\u53EF\u80FD\u4F1A\u60F3\u770B\u4E00\u4E0B\u5317\u9F50\u5728\u7ECF\u5386\u4E86\u201C\u9AD8\u6F84-\u9AD8\u6D0B\u201D\u7684\u6743\u529B\u8F6C\u79FB\u4EE5\u53CA\u671D\u4EE3\u7684\u66F4\u8FED\u540E\uFF0C\u662F\u600E\u6837\u6DF7\u4E71\u7684\u5C40\u9762\u3002\u4F46\u4ED6\u5982\u4ECA\u770B\u5230\u7684\uFF0C\u5374\u662F\u5317\u9F50\u53CD\u5E94\u8FC5\u901F\uFF1B\u7136\u540E\uFF0C\u53C8\u542C\u8BF4\u5317\u9F50\u519B\u961F\u519B\u5BB9\u9F50\u6574\u3002\u5F88\u660E\u663E\uFF0C\u4ED6\u60F3\u8981\u4E58\u4E71\u53D6\u5229\u7684\u60F3\u6CD5\u53EA\u80FD\u662F\u843D\u7A7A\u4E86\u3002</p><p>\u81F3\u4E8E<code>\u5185\u56E0</code>\uFF0C\u5219\u5E94\u8BE5\u5305\u62EC\u4E24\u4E2A\u65B9\u9762\uFF1A<strong>\u897F\u9B4F\u5B71\u5F31\u7684\u5B9E\u529B\u548C\u4E00\u65F6\u672A\u7ADF\u7684\u5185\u90E8\u4E8B\u52A1\u3002</strong></p><p>\u5F53\u5E74\u9099\u5C71\u4E4B\u6218\u5931\u8D25\u540E\uFF0C\u897F\u9B4F\u4E3B\u529B\u5168\u519B\u8986\u6CA1\uFF0C\u4E00\u76F4\u90FD\u6CA1\u6709\u6062\u590D\u3002\u8FD9\u4E9B\u5E74\u6765\uFF0C\u897F\u9B4F\u9762\u5BF9\u7740<code>\u9AD8\u6B22</code>\u7684\u519B\u4E8B\u51CC\u903C\uFF0C\u9632\u5B88\u6709\u4F59\uFF0C\u8FDB\u653B\u4E0D\u8DB3\u3002\u867D\u7136\u51E0\u5E74\u524D\u4E5F\u66FE\u5728\u6218\u573A\u4E0A\u628A\u9AD8\u6B22\u903C\u9000\uFF0C\u4F46\u90A3\u66F4\u591A\u7684\u662F\u9760\u4E86\u5730\u5229\uFF0C\u4EE5\u53CA<code>\u97E6\u5B5D\u5BBD</code>\u7684\u9632\u5B88\u4E4B\u529F\u3002</p><p>\u5982\u4ECA\u4E1C\u897F\u5F00\u6218\uFF0C\u4E1C\u65B9\u7684\u5317\u9F50\u76DB\u51B5\u4F9D\u65E7\uFF0C\u897F\u9B4F\u867D\u7136\u60F3\u62AB\u575A\u6267\u9510\uFF0C\u5728\u6218\u573A\u4E0A\u6709\u6240\u8FDB\u53D6\uFF0C\u4F46\u5948\u4F55\u6709\u5FC3\u65E0\u529B\u3002</p><p>\u800C\u6240\u8C13\u5185\u90E8\u4E8B\u52A1\uFF0C\u5219\u662F\u6307\u5B87\u6587\u6CF0\u5728\u56FD\u5185\u7684<strong>\u5E9C\u5175\u5236\u6539\u9769</strong>\u8FD8\u672A\u7ADE\u5168\u529F\u3002</p><p>**\u76F8\u6BD4\u4E8E\u5728\u6218\u573A\u4E0A\u4E0E\u5317\u9F50\u4E89\u80DC\uFF0C\u89E3\u51B3\u5185\u90E8\u4E8B\u52A1\u5BF9\u897F\u9B4F\u6765\u8BF4\uFF0C\u624D\u662F\u66F4\u4E3A\u91CD\u8981\u7684\u4E8B\u60C5\u3002**\u6BD5\u7ADF\u5982\u4ECA\u4E1C\u897F\u5B9E\u529B\u5DEE\u8DDD\u5DE8\u5927\uFF0C\u897F\u9B4F\u5373\u4FBF\u5728\u6218\u573A\u4E0A\u83B7\u80DC\u4E86\uFF0C\u6240\u5F97\u4E5F\u4F9D\u7136\u6709\u9650\u3002\u4E0E\u5176\u5982\u6B64\uFF0C\u8FD8\u4E0D\u5982\u628A\u66F4\u591A\u7684\u5FC3\u601D\u82B1\u5728\u56FD\u5185\u2014\u2014\u540E\u6765\u7684\u5386\u53F2\u4E5F\u8BC1\u660E\uFF0C\u5E9C\u5175\u5236\u5728\u6210\u529F\u4EE5\u540E\uFF0C\u6240\u7206\u53D1\u51FA\u6765\u7684\u80FD\u91CF\u662F\u5DE8\u5927\u7684\u3002</p><p>\u4F46\u5728\u6B64\u65F6\uFF0C\u897F\u9B4F\u7684\u6539\u9769\u8FD8\u5728\u6301\u7EED\u63A8\u8FDB\u4E2D\u3002</p><p>\u6240\u4EE5\u5B87\u6587\u6CF0\u5E26\u7740\u4EBA\u8D70\u5230\u524D\u7EBF\u770B\u4E86\u770B\uFF0C\u53D1\u73B0\u5360\u4E0D\u5230\u5BF9\u624B\u4EC0\u4E48\u4FBF\u5B9C\uFF0C\u5C31\u56DE\u53BB\u4E86\u3002</p><p>\u5012\u53CD\u662F\u9AD8\u6D0B\uFF0C\u8FD9\u6B21\u5360\u4E86\u4E0D\u5C11\u571F\u5730\u4E0A\u7684\u4FBF\u5B9C\u3002</p><p>\u4E0D\u8FC7\u5317\u9F50\u4E4B\u540E\u4E5F\u6CA1\u6709\u518D\u5F80\u897F\u8FDB\u2014\u2014\u90A3\u6837\u7684\u8BDD\uFF0C\u5317\u9F50\u5C31\u53EF\u80FD\u8981\u9762\u5BF9\u897F\u9B4F\u7684\u5F3A\u529B\u53CD\u51FB\u3002\u4ED6\u4EEC\u4E5F\u6CA1\u6709\u505A\u597D\u6253\u5927\u4ED7\u7684\u51C6\u5907\uFF0C\u56E0\u6B64\u540C\u6837\u7F62\u5175\u56DE\u56FD\u3002</p><p>\u5982\u679C\u5355\u7EAF\u53EA\u8BFB\u53F2\u4E66\u4E2D\u672C\u7EAA\u7684\u65F6\u95F4\u7EBF\uFF0C\u90A3\u4E48\u4F60\u4F1A\u89C9\u5F97\u5728\u4E1C\u5F81\u56DE\u5F52\u4EE5\u540E\uFF0C\u5B87\u6587\u6CF0\u4F3C\u4E4E\u5E76\u6CA1\u6709\u505A\u4EC0\u4E48\u4E8B\u3002\u4F46\u662F\uFF0C\u4F60\u53EA\u8981\u628A\u76EE\u5149\u653E\u5F97\u66F4\u957F\u8FDC\u4E00\u70B9\uFF0C\u5C31\u4F1A\u53D1\u73B0\u4E00\u4E9B\u5176\u4ED6\u7684\u7AEF\u502A\uFF1A</p><p>\u4ECE\u4E1C\u65B9\u524D\u7EBF\u56DE\u6765\u540E\u7684\u7B2C\u4E8C\u5E74\uFF0C\u5B87\u6587\u6CF0\u5373\u6D3E\u4EBA\u5357\u5F81\uFF0C<strong>\u76EE\u6807\u662F\u6C49\u6C34\u6D41\u57DF\u4E00\u5E26\uFF0C\u5175\u950B\u76F4\u6307<code>\u8700\u4E2D</code></strong>\u3002</p><p>\u7136\u540E\uFF0C\u5B87\u6587\u6CF0\u7684\u89C6\u91CE\u4FBF\u4E00\u76F4\u505C\u7559\u5728\u897F\u65B9\u548C\u5357\u65B9\u3002</p><p>\u5728\u897F\u65B9\uFF0C\u5B87\u6587\u6CF0\u517B\u7CBE\u84C4\u9510\uFF0C\u8D81\u673A\u88AD\u51FB\u4E86<code>\u5410\u8C37\u6D51</code>\u3002\u5728\u6B64\u6218\u4E2D\uFF0C<strong>\u5410\u8C37\u6D51\u5927\u8D25\uFF0C\u540E\u6765\u4E00\u6BB5\u65F6\u95F4\u5185\u518D\u6CA1\u6709\u5BF9\u5317\u5468\u5F62\u6210\u5A01\u80C1</strong>\u3002</p><p>\u7136\u540E\uFF0C\u5B87\u6587\u6CF0\u53C8\u5168\u529B\u7ECF\u8425\u8700\u4E2D\u3002\u8D81\u7740\u5357\u65B9<code>\u4FAF\u666F</code>\u4F5C\u4E71\uFF0C<code>\u8427\u6881</code>\u5185\u4E71\u7684\u65F6\u673A\uFF0C<strong>\u897F\u9B4F\u5360\u636E\u4E86\u8700\u4E2D\uFF0C\u4ECE\u6B64\u5BF9\u5357\u65B9\u653F\u6743\u5F62\u6210\u5730\u5229\u4F18\u52BF\u3002</strong></p><p>\u5230<code>\u8427\u7ECE</code>\u51FB\u8D25\u4FAF\u666F\u540E\uFF0C\u5B87\u6587\u6CF0\u53C8\u53CA\u65F6\u51FA\u51FB\uFF0C<strong>\u653B\u7834\u6C5F\u9675\uFF0C\u64D2\u6740\u8427\u7ECE\uFF0C\u8FD9\u518D\u6B21\u5F15\u53D1\u4E86\u5357\u671D\u7684\u5185\u4E71</strong>\u2014\u2014\u751A\u81F3\u5230\u9648\u9738\u5148\u53BB\u4E16\u4EE5\u540E\u90FD\u8FD8\u5728\u6301\u7EED\u3002\u800C\u5230\u6B64\u65F6\uFF0C\u5360\u636E\u6C5F\u9675\u7684<code>\u897F\u6881</code>\u5DF2\u7ECF\u6210\u4E3A\u4E86\u5317\u5468\u7684\u9644\u5EB8\u2014\u2014\u8FD9\u91CC\uFF0C\u540E\u6765\u6210\u4E3A<code>\u968B</code>\u706D<code>\u9648</code>\u65F6\uFF0C\u519B\u961F\u5357\u4E0B\u7684\u53C8\u4E00\u4E2A\u8DF3\u677F\u3002</p><p>\u6B64\u5916\uFF0C\u5728\u671D\u5EF7\u4E0A\uFF0C\u5B87\u6587\u6CF0\u8FD8\u5904\u7406\u4E86\u4E00\u4E9B\u201C\u5C0F\u4E8B\u201D\u3002\u6BD4\u5982\uFF0C\u7687\u5BA4\u6210\u5458\u3001\u5C1A\u4E66<code>\u5143\u70C8</code>\u4F5C\u4E71\u88AB\u8BDB\uFF1B\u7B2C\u4E8C\u5E74\uFF0C\u897F\u9B4F<code>\u5E9F\u5E1D\u5143\u94A6</code>\u88AB\u5B87\u6587\u6CF0\u5E9F\u9664\u3002</p><p>\u8FD9\u6BB5\u65F6\u95F4\u91CC\uFF0C\u9AD8\u6D0B\u5F53\u7136\u4E5F\u6CA1\u6709\u95F2\u7740\u3002\u968F\u7740\u5B87\u6587\u6CF0\u64A4\u519B\uFF0C\u5317\u9F50\u519B\u961F\u8FDB\u903C\u81F3\u6F7C\u5173\u4E5F\u4E0D\u518D\u524D\u884C\u3002\u64A4\u56DE\u4EE5\u540E\uFF0C\u4ED6\u4EEC\u540C\u6837\u628A\u76EE\u5149\u6295\u5411\u4E86\u5357\u65B9\uFF0C\u8D81\u7740\u5357\u671D\u52A8\u4E71\uFF0C<strong>\u5317\u9F50\u88AD\u53D6\u4E86\u672C\u5C5E\u4E8E\u8427\u6881\u7684\u6DEE\u5357\u4E4B\u5730\u3002</strong></p><p>\u6211\u4EEC\u4ECE\u540E\u4E16\u770B\u8FC7\u53BB\uFF0C\u90FD\u77E5\u9053\u5317\u65B9\u7684\u4E1C\u897F\u4E4B\u4E89\uFF0C\u6700\u7EC8\u4EE5\u5317\u5468\u80DC\u51FA\uFF1B\u800C\u5317\u5468\u4E4B\u6240\u4EE5\u80FD\u80DC\u51FA\uFF0C\u5173\u952E\u53C8\u5728\u4E8E\u5E9C\u5175\u5236\u3002</p><p>\u8FD9\u4E2A\u89C2\u70B9\u5F53\u7136\u6CA1\u6709\u9519\uFF0C\u4F46\u5B83\u5E76\u4E0D\u662F\u5168\u90E8\u3002</p><p>\u201C\u5E9C\u5175\u5236\u201D\u7684\u5B9E\u65BD\uFF0C\u786E\u7136\u662F\u5317\u5468\u5F3A\u5927\u7684\u539F\u56E0\u3002\u4F46\u5E76\u4E0D\u662F\u8BF4\uFF0C\u4EC5\u4EC5\u9760\u7740\u201C\u5E9C\u5175\u5236\u201D\u8FD9\u4E00\u4E2A\u5148\u8FDB\u7684\u5236\u5EA6\uFF0C\u5317\u5468\u5C31\u53EF\u4EE5\u7A33\u5750\u9493\u9C7C\u53F0\uFF0C\u7B11\u770B\u672A\u6765\u7684\u6210\u529F\u3002</p><p><strong>\u9996\u5148\uFF0C\u662F\u4E00\u5F00\u59CB\u63D0\u5230\u7684\u4E1C\u5F81\u3002</strong></p><p>\u65E9\u4E9B\u5E74\u4E1C\u897F\u5206\u88C2\u7684\u65F6\u5019\uFF0C\u9AD8\u6B22\u5BF9\u5B87\u6587\u6CF0\u4E00\u76F4\u4FDD\u6301\u7740\u519B\u4E8B\u9AD8\u538B\uFF0C\u5B87\u6587\u6CF0\u88AB\u903C\u5F97\u75B2\u4E8E\u5954\u547D\uFF0C\u51E0\u8FD1\u5D29\u6E83\u3002\u4F46\u5982\u4ECA\u5374\u4E0D\u540C\uFF0C\u4E1C\u56FD\u653F\u6CBB\u51FA\u73B0\u9707\u8361\uFF0C\u5B87\u6587\u6CF0\u867D\u7136\u9886\u5175\u5230\u4E86\u524D\u7EBF\uFF0C\u5374\u7518\u613F\u5931\u5730\u800C\u8FD8\u3002</p><p>\u4E3A\u4EC0\u4E48\uFF1F</p><p><strong>\u56E0\u4E3A\u4ED6\u5728\u4E1C\u65B9\u4EE5\u5916\u53D1\u73B0\u4E86\u673A\u4F1A\u3002</strong></p><p>\u867D\u7136\u672C\u6765\u51C6\u5907\u8D81\u7740\u4E1C\u9B4F\uFF08\u5317\u9F50\uFF09\u653F\u5C40\u6DF7\u4E71\u800C\u8D81\u673A\u6E14\u5229\uFF0C\u4F46\u5728\u53D1\u73B0\u53EF\u80FD\u6027\u4E0D\u5927\u540E\uFF0C\u5B87\u6587\u6CF0\u679C\u65AD\u64A4\u5175\uFF0C\u540C\u65F6\u6536\u7F29\u524D\u7EBF\u3002\u5982\u6B64\uFF0C<strong>\u897F\u9B4F\u624D\u53EF\u4EE5\u4E13\u5FC3\u56FA\u5B88\u901A\u5173\u9632\u7EBF\uFF0C\u8FDB\u800C\u628A\u7CBE\u529B\u66F4\u591A\u7684\u653E\u5230\u89E3\u51B3\u5176\u4ED6\u4E8B\u60C5\u4E0A\u3002</strong></p><p>\u5728\u653E\u5F03\u4E0E\u5317\u9F50\u4E89\u96C4\u7684\u8FD9\u4E9B\u5E74\u4E2D\uFF0C\u5B87\u6587\u6CF0\u89E3\u51B3\u7684\u5176\u4ED6\u4E8B\u60C5\u5305\u62EC\u4F46\u4E0D\u9650\u4E8E\uFF1A<strong>\u51FB\u8D25<code>\u5410\u8C37\u6D51</code>\uFF0C\u5C06<code>\u897F\u6881</code>\u6536\u4E3A\u9644\u5EB8\uFF0C\u8FDB\u5360<code>\u8700\u4E2D</code></strong>\u3002</p><p>\u8FD9\u4E9B\u90FD\u662F\u5B9E\u5B9E\u5728\u5728\u80FD\u770B\u5F97\u5230\u6210\u679C\u7684\u6536\u83B7\u3002\u800C\u4E14\uFF0C\u5B83\u4EEC\u529F\u4E0D\u6B62\u5728\u5F53\u4EE3\uFF1A<strong>\u5728\u5F53\u65F6\uFF0C\u8FD9\u4E9B\u6218\u4E89\u662F\u5F00\u7586\u62D3\u571F\u4E4B\u4E3E\uFF1B\u4E8E\u540E\u4E16\uFF0C\u5B83\u4EEC\u662F\u968B\u671D\u80FD\u4E00\u7EDF\u5929\u4E0B\u7684\u91CD\u8981\u6761\u4EF6</strong>\u3002</p><p><strong>\u540C\u65F6\uFF0C\u8FD8\u6709\u4E00\u4E9B\u662F\u5F53\u65F6\u770B\u4E0D\u5230\u6210\u6548\u7684\u52AA\u529B</strong>\u3002\u6BD4\u5982\uFF0C<strong>\u5728\u56FD\u5185\u8FDB\u884C\u7684\u5E9C\u5175\u5236\u6539\u9769\uFF0C\u4EE5\u53CA\u5BF9\u5176\u4ED6\u653F\u6CBB\u52BF\u529B\u7684\u5B89\u629A\u4E0E\u540C\u5316</strong>\u3002\u8FD9\u4E9B\u4E8B\u60C5\uFF0C\u5B87\u6587\u6CF0\u4E00\u76F4\u90FD\u5728\u6301\u7EED\u63A8\u8FDB\u2014\u2014\u4E00\u76F4\u8981\u5230\u5468<code>\u6B66\u5E1D\u5B87\u6587\u9095</code>\u65F6\u4EE3\u624D\u80FD\u5B8C\u5168\u6210\u529F\u3002</p><p>\u6240\u6709\u8FD9\u4E9B\u52AA\u529B\uFF0C\u90FD\u662F\u5B87\u6587\u6CF0\u5728\u653E\u5F03\u4E86\u4E0E\u5317\u9F50\u4E89\u96C4\uFF0C\u4EE5\u53CA\u4E1C\u65B9\u7684\u4E00\u4E9B\u571F\u5730\u4E4B\u540E\uFF0C\u624D\u83B7\u5F97\u7684\u673A\u9047\u3002</p><p>\u9009\u62E9\u672C\u5C31\u6709\u5F97\u6709\u5931\u3002\u6709\u6240\u5931\uFF0C\u624D\u80FD\u6709\u6240\u5F97\u3002</p><p>\u5728<code>\u4E1C\u9B4F\u5B5D\u9759\u5E1D</code>\u88AB\u5E9F\u9664\u4E03\u5E74\u540E\uFF0C\u897F\u9B4F\u7684\u6700\u540E\u4E00\u4F4D\u7687\u5E1D\u2014\u2014<code>\u897F\u9B4F\u606D\u5E1D\u5143\u5ED3</code>\uFF0C\u4E5F\u5373\u5C06\u9762\u4E34\u88AB\u5E9F\u9664\u7684\u547D\u8FD0\u3002</p><p>\u4ED6\u88AB\u5E9F\u9664\u7684\u539F\u56E0\uFF0C\u8DDF\u5B5D\u9759\u5E1D\u51E0\u4E4E\u4E00\u6837\uFF1A<strong>\u88AB\u6743\u81E3\u7528\u6765\u7ACB\u5A01</strong>\u3002</p><p>\u897F\u9B4F\u606D\u5E1D\u4E09\u5E74\uFF08\u516C\u5143556\u5E74\uFF09\uFF0C\u65F6\u5E74\u4E94\u5341\u5C81\u7684\u897F\u9B4F\u5B9E\u9645\u63A7\u5236\u8005\u5B87\u6587\u6CF0\u7A81\u7136\u75C5\u5371\uFF0C\u6B64\u65F6\u55E3\u5B50<code>\u5B87\u6587\u89C9</code>\u53EA\u670916\u5C81\u3002\u4E34\u7EC8\u4E4B\u9645\uFF0C\u5B87\u6587\u6CF0\u6025\u62DB<code>\u5B87\u6587\u62A4</code>\u6258\u5B64\u3002\u4EA4\u4EE3\u5B8C\u540E\u4E8B\uFF0C\u5B87\u6587\u6CF0\u4FBF\u6E98\u7136\u957F\u901D\u3002</p><p>\u6B64\u65F6\uFF0C\u6446\u5728\u5B87\u6587\u62A4\u9762\u524D\u7684\u9EBB\u70E6\uFF0C\u6BD4\u8D77\u9AD8\u6D0B\u6765\uFF0C\u4E0D\u77E5\u9053\u8981\u5927\u591A\u5C11\u500D\uFF01</p><p>\u5F53\u521D\u9AD8\u6F84\u7A81\u7136\u53BB\u4E16\uFF0C\u9AD8\u6D0B\u4E5F\u9700\u8981\u6536\u4F0F\u5317\u9F50\u7684\u9C9C\u5351\u52CB\u8D35\u3002<strong>\u4F46\u4ED6\u7684\u201C\u6536\u4F0F\u201D\uFF0C\u66F4\u591A\u7684\u53EA\u662F\u4E00\u4E2A\u5229\u76CA\u4EA4\u6362\u8FC7\u7A0B</strong>\uFF1A\u4F60\u7ED9\u6211\u4EC0\u4E48\u597D\u5904\uFF0C\u6362\u53D6\u6211\u5BF9\u4F60\u7684\u652F\u6301\uFF1F</p><p><strong>\u9AD8\u6B22\u8FD8\u5728\u4E16\u65F6\uFF0C\u5BF9\u5317\u9F50\u5883\u5185\u5B9E\u529B\u96C6\u56E2\u7684\u6574\u5408\u5C31\u65E9\u5DF2\u5B8C\u6210</strong>\u3002\u9AD8\u6D0B\u4E4B\u540E\u9762\u5BF9\u7684\u95EE\u9898\uFF0C\u53EA\u662F\u81EA\u5DF1\u4F5C\u4E3A\u7A81\u7136\u51FA\u73B0\u7684\u63A5\u73ED\u4EBA\uFF0C\u8981\u5982\u4F55\u8BA9\u522B\u4EBA\u63A5\u53D7\u7684\u95EE\u9898\u3002</p><p>\u800C\u5B87\u6587\u62A4\u5219\u5B8C\u5168\u4E0D\u540C\u3002<strong>\u5B87\u6587\u6CF0\u5728\u8EAB\u524D\uFF0C\u5BF9\u5173\u4E2D\u52BF\u529B\u96C6\u56E2\u7684\u6574\u5408\u8FDC\u672A\u5B8C\u6210</strong>\u3002\u751A\u81F3\u4E8E\uFF0C\u5E9C\u5175\u5236\u4E0B\u7684\u516B\u67F1\u56FD\u867D\u7136\u90FD\u5C0A\u5949\u5B87\u6587\u6CF0\u4E3A\u5C0A\uFF0C\u4F46\u9762\u548C\u5FC3\u4E0D\u548C\u7684\u5927\u6709\u4EBA\u5728\u2014\u2014\u6240\u8C13\u7684\u5173\u9647\u96C6\u56E2\uFF0C\u5B9E\u9645\u4E0A\u662F\u7ECF\u8FC7\u5B87\u6587\u6CF0\u3001\u5B87\u6587\u62A4\u3001\u5B87\u6587\u9095\u4E09\u4EE3\u4EBA\u7684\u52AA\u529B\u624D\u5171\u540C\u9020\u5C31\u7684\u3002</p><p>\u5B87\u6587\u62A4\u5982\u4ECA\u63A5\u624B\u7684\uFF0C\u53EF\u4EE5\u8BF4\u662F\u4E00\u4E2A\u5927\u70C2\u644A\u5B50\u3002<strong>\u5728\u4ED6\u9762\u524D\u7684\uFF0C\u90FD\u662F\u897F\u9B4F\u7684\u5F00\u56FD\u5143\u8001\uFF0C\u6709\u4E9B\u4EBA\u7684\u8D44\u5386\u751A\u81F3\u5728\u5B87\u6587\u6CF0\u4E4B\u4E0A\uFF0C\u800C\u4E14\u4E4B\u524D\u5C31\u5BF9\u5B87\u6587\u6CF0\u9887\u6709\u6028\u8A00</strong>\u3002\u5982\u4ECA\u4ED6\u8981\u53BB\u7EDF\u5FA1\u8FD9\u6837\u7684\u5927\u4F6C\uFF0C\u591A\u5C11\u6709\u4E9B\u540D\u4E0D\u6B63\u8A00\u4E0D\u987A\u3002</p><p>\u8FD9\u4E2A\u65F6\u5019\uFF0C\u5B87\u6587\u62A4\u8FEB\u5207\u9700\u8981\u505A\u4E00\u4EF6\u4E8B\u6765\u6811\u7ACB\u5A01\u4FE1\uFF0C\u4E8E\u662F\uFF0C\u4ED6\u628A\u76EE\u5149\u8F6C\u5411\u4E86\u7687\u4F4D\u4E0A\u7684\u90A3\u5C0A\u6CE5\u80CE\u6728\u5076\uFF1A\u897F\u9B4F\u606D\u5E1D\u5143\u5ED3\u3002</p><p><strong>\u6301\u7EED\u4E00\u767E\u4E03\u5341\u591A\u5E74\u7684\u9B4F\u738B\u671D\uFF0C\u5C31\u6B64\u753B\u4E0A\u53E5\u53F7\u3002</strong></p><p>\u9065\u60F3\u5F53\u5E74\uFF0C<code>\u9053\u6B66\u5E1D\u62D3\u8DCB\u73EA</code>\u5F00\u56FD\u7684\u5951\u673A\uFF0C\u8FD8\u662F<code>\u524D\u79E6</code>\u5728\u6DDD\u6C34\u4E4B\u6218\u6218\u8D25\u540E\u6240\u5F15\u53D1\u7684\u4E00\u7CFB\u5217\u8FDE\u9501\u53CD\u5E94\u3002\u800C\u7B49\u5317\u9B4F\u771F\u6B63\u7AD9\u4F4D\u811A\u8DDF\uFF0C\u5DF2\u7ECF\u8981\u5230 <strong><code>\u540E\u71D5</code>\u6155\u5BB9\u5782</strong>\u75C5\u901D\u4E4B\u540E\u4E86\u3002</p><p>\u4F46\u5982\u4ECA\uFF0C\u5B83\u4E5F\u50CF\u5386\u53F2\u4E2D\u7684\u5176\u4ED6\u671D\u4EE3\u4E00\u6837\uFF0C\u5373\u5C06\u70DF\u6D88\u4E91\u6563\u3002</p><p>\u4ECE\u8F88\u5206\u7B97\u4E0B\u6765\uFF0C\u4E1C\u9B4F\u5B5D\u9759\u5E1D\u5143\u5584\u89C1\u4E0E\u897F\u9B4F\u606D\u5E1D\u5143\u5ED3\u8FD8\u662F\u540C\u8F88\uFF0C\u4E24\u4EBA\u90FD\u662F<code>\u5B5D\u6587\u5E1D\u5143\u5B8F</code>\u7684\u66FE\u5B59\u3002</p><p>\u5B5D\u9759\u5E1D\u5F53\u5E74\u88AB\u903C\u9000\u4F4D\u65F6\uFF0C\u5176\u5B9E\u65E9\u5C31\u5DF2\u7ECF\u505A\u597D\u4E86\u51C6\u5907\u3002\u5728\u88AB\u5927\u81E3\u901A\u77E5\u81EA\u5DF1\u5C06\u8981\u7985\u4F4D\u4E8E\u9AD8\u6D0B\u65F6\uFF0C\u4ED6\u5F88\u5766\u7136\u5730\u541F\u8BF5\u4E86<code>\u8303\u6654</code>\u5728\u300A\u540E\u6C49\u4E66\xB7\u732E\u5E1D\u7EAA\u300B\u4E2D\u7684\u8D5E\u8BCD\uFF1A</p><p><strong>\u732E\u5750\u4E0D\u8FB0\uFF0C\u8EAB\u64AD\u56FD\u5C6F\uFF0C</strong></p><p><strong>\u7EC8\u6211\u56DB\u767E\uFF0C\u6C38\u4F5C\u865E\u5BBE \u3002</strong></p><p>\u6743\u529B\u7684\u6E38\u620F\u6C38\u4E0D\u7EC8\u7ED3\uFF0C\u4F46\u5143\u6C0F\uFF08\u62D3\u8DCB\u6C0F\uFF09\u8FD9\u4E00\u9875\uFF0C\u5DF2\u7ECF\u6C38\u8FDC\u5730\u7FFB\u4E86\u8FC7\u53BB\u3002</p>',71),r=[d];function n(s,g){return p(),e("div",null,r)}const l=o(t,[["render",n],["__file","87.html.vue"]]);export{l as default};
