import{_ as p}from"./_plugin-vue_export-helper.cdc0426e.js";import{o,c as t,f as r}from"./app.cd90e563.js";const n={},s=r('<h1 id="\u706D\u664B" tabindex="-1"><a class="header-anchor" href="#\u706D\u664B" aria-hidden="true">#</a> \u706D\u664B</h1><blockquote><p>\u6625\uFF0C\u6B63\u6708\uFF0C\u4E01\u4E11\u6714\uFF0C\u6C49\u4E3B\u806A\u5BB4\u7FA4\u81E3\u4E8E\u5149\u6781\u6BBF\uFF0C\u4F7F\u6000\u5E1D\u8457\u9752\u8863\u884C\u9152\u3002\u5EBE\u73C9\u3001\u738B\u4FCA\u7B49\u4E0D\u80DC\u60B2\u6124\uFF0C\u56E0\u53F7\u54ED\uFF1B\u806A\u6076\u4E4B\u3002</p></blockquote><p><strong>\u300A\u8D44\u6CBB\u901A\u9274\u2022\u5377\u516B\u5341\u516B\u2022\u664B\u7EAA\u5341\u300B</strong></p><p>\u8FD9\u662F\u4E2D\u56FD\u5386\u53F2\u4E0A\u7684\u5C48\u8FB1\u4E00\u5E55\uFF1A\u6C38\u5609\u4E94\u5E74\uFF0C\u516C\u5143311\u5E74\uFF0C\u5308\u5974\u653B\u7834\u6D1B\u9633\uFF0C<code>\u664B\u6000\u5E1D\u53F8\u9A6C\u70BD</code>\u4EE5\u53CA\u6570\u5341\u540D\u671D\u81E3\u906D\u4FD8\uFF0C\u4E4B\u540E\u88AB\u9001\u5F80\u5E73\u9633\u3002</p><p>\u7B2C\u4E09\u5E74\u6B63\u6708\uFF0C<code>\u5308\u5974\u7687\u5E1D\u5218\u806A</code>\u5927\u5BB4\u7FA4\u81E3\uFF0C\u4E3A\u4E86\u8BA9\u73B0\u573A\u6B22\u4E50\u7965\u548C\u7684\u6C1B\u56F4\u55E8\u4E0A\u4E00\u4E2A\u65B0\u9AD8\u6F6E\uFF0C<strong>\u5218\u806A\u547D\u6000\u5E1D\u8EAB\u7740\u9752\u8863\uFF0C\u4F8D\u9152\u4E8E\u5BB4\u4F1A\u4E4B\u4E0A</strong>\u3002</p><p>\u6614\u65E5\u7684\u4E5D\u4E94\u4E4B\u5C0A\u6CA6\u843D\u81F3\u6B64\uFF0C\u8FD9\u662F\u81EA\u79E6\u59CB\u7687\u4EE5\u6765\u6C49\u4EBA\u7687\u5E1D\u4ECE\u672A\u6709\u8FC7\u7684\u5947\u803B\u5927\u8FB1\u3002\u5728\u5EA7\u7684\u897F\u664B\u65E7\u81E3\u5EBE\u73C9\u3001\u738B\u4FCA\u7B49\u4EBA\u4E00\u65F6\u60B2\u4E0D\u81EA\u80DC\uFF0C\u568E\u5555\u5927\u54ED\u3002</p><blockquote><p><strong>\u8FD9\u79CD\u5C48\u8FB1\uFF0C\u5176\u5B9E\u81EA\u6000\u5E1D\u88AB\u4FD8\u4EE5\u6765\u4FBF\u4E0D\u66FE\u6D88\u505C</strong>\u3002\u6000\u5E1D\u4E0E\u5308\u5974\u7687\u5E1D\u5218\u806A\u66FE\u7ECF\u6709\u8FC7\u4E00\u6BB5\u5BF9\u8BDD\uFF0C\u88AB\u300A\u4E16\u8BF4\u65B0\u8BED\u300B\u8BB0\u5F55\u4E86\u4E0B\u6765\uFF1A</p><p>\u5218\u806A\u95EE\u6000\u5E1D\uFF1A\u6211\u5F53\u5E74\u548C\u738B\u6D4E\u4E00\u8D77\u53BB\u62DC\u8BBF\u4F60\uFF0C\u738B\u6D4E\u8FD8\u5411\u4F60\u5938\u6211\u7684\u4E8B\uFF0C\u4F60\u8FD8\u8BB0\u5F97\u5417\uFF1F</p><p>\u8FD9\u53E5\u767D\u8BDD\u770B\u8D77\u6765\u5E73\u5E73\u65E0\u5947\uFF0C\u4F46\u5982\u679C\u4F60\u518D\u5BF9\u7167\u539F\u6587\uFF0C\u5C31\u4F1A\u4E0D\u5FCD\u5352\u8BFB\uFF1A</p><p>\u537F\u6614\u4E3A\u8C6B\u7AE0\u738B\uFF0C\u6715\u4E0E\u738B\u6B66\u5B50\u9020\u537F\uFF0C\u6B66\u5B50\u79F0\u6715\u4E8E\u537F\uFF0C\u537F\u8A00\u95FB\u5176\u540D\u4E45\u77E3\uFF0C\u8D60\u6715\u67D8\u5F13\u94F6\u7814\uFF0C\u537F\u9887\u8BB0\u5426\uFF1F</p><p>\u7231\u537F\u4F60\u5F53\u5E74\u505A\u8C6B\u7AE0\u738B\u7684\u65F6\u5019\uFF0C\u6715\u548C\u738B\u6B66\u5B50\uFF08\u738B\u6D4E\uFF09\u9020\u8BBF\u4E8E\u4F60\u3002\u738B\u6B66\u5B50\u4E8E\u537F\u7684\u9762\u524D\u5938\u8D5E\u6715\u7684\u4E8B\u60C5\uFF0C\u7231\u537F\u4F60\u8FD8\u8BB0\u5F97\u5417\uFF1F</p><p>\u4E00\u53E5\u8BDD\u91CC\uFF0C\u5218\u806A\u7528\u4E86\u4E94\u6B21\u201C\u537F\u201D\u6307\u4EE3\u6000\u5E1D\uFF0C\u7528\u4E86\u4E09\u6B21\u201C\u6715\u201D\u79F0\u547C\u81EA\u5DF1\u3002\u8FD9\u4E48\u505A\uFF0C\u81EA\u7136\u662F\u4E3A\u4E86\u5728\u8FD9\u4E2A\u6614\u65E5\u7687\u5E1D\u7684\u9762\u524D\u5F70\u663E\u5C0A\u5D07\u3002</p><p>\u6000\u5E1D\u6E05\u695A\u65E0\u8BEF\u5730\u542C\u51FA\u4E86\u8FD9\u756A\u5C0F\u5FC3\u601D\uFF0C\u4F46\u65E0\u5948\u4E16\u4E8B\u903C\u4EBA\uFF0C\u4E5F\u53EA\u80FD\u5C48\u4ECE\uFF1A\u201C\u81E3\u5F53\u7136\u8BB0\u5F97\uFF0C\u53EA\u662F\u9057\u61BE\u4E0D\u80FD\u65E9\u65E5\u5F97\u89C1\u965B\u4E0B\u7684\u9F99\u989C\uFF01\u201D</p><p>\u5218\u806A\u4E8E\u662F\u53C8\u95EE\uFF0C\u7231\u537F\uFF0C\u4F60\u4EEC\u5BB6\u600E\u4E48\u9AA8\u8089\u76F8\u6B8B\u81F3\u6B64\u5440\uFF01</p><p>\u6000\u5E1D\u606D\u987A\u5730\u56DE\u7B54\uFF1A\u8FD9\u662F\u5927\u6C49\u4E0A\u5E94\u5929\u547D\uFF0C\u965B\u4E0B\u5C06\u8981\u541B\u4E34\u5929\u4E0B\uFF0C\u56E0\u6B64\u8BA9\u6211\u5BB6\u81EA\u76F8\u6B8B\u6740\uFF0C\u4EE5\u4E3A\u738B\u8005\u524D\u9A71\u3002</p><p>\u5218\u806A\u542C\u7F62\uFF0C\u5F88\u662F\u53D7\u7528\u3002\u518D\u60F3\u60F3\u81EA\u5DF1\u662F\u4EE5\u5F02\u65CF\u7684\u8EAB\u4EFD\uFF0C\u4E00\u671D\u4FD8\u864F\u4E86\u4E2D\u539F\u738B\u671D\u7684\u7687\u5E1D\uFF0C\u5FC3\u91CC\u7B80\u76F4\u723D\u7FFB\u5929\uFF0C\u968F\u624B\u5C31\u5C06\u81EA\u5DF1\u7684\u4E00\u4E2A\u8D35\u4EBA\u8D50\u7ED9\u4E86\u6000\u5E1D\u3002</p></blockquote><p>\u5929\u5BB6\u5A01\u4E25\u6CA6\u4E27\u81F3\u6B64\uFF0C\u897F\u664B\u65E7\u81E3\u4EEC\u60B2\u4ECE\u4E2D\u6765\uFF0C\u5341\u5206\u53EF\u4EE5\u7406\u89E3\u3002\u7136\u800C\uFF0C\u8FD9\u79CD\u5185\u5FC3\u60B2\u75DB\u7684\u6D41\u9732\uFF0C\u6700\u7EC8\u7ADF\u7136\u65AD\u9001\u4E86\u664B\u6000\u5E1D\u53F8\u9A6C\u70BD\u7684\u6027\u547D\u3002</p><p>\u81EA\u88AB\u4FD8\u4EE5\u540E\uFF0C\u53F8\u9A6C\u70BD\u6DF1\u77E5\u5F62\u52BF\u9669\u6076\uFF0C\u6545\u800C\u523B\u610F\u4F4E\u58F0\u4E0B\u6C14\uFF0C\u66F2\u610F\u9022\u8FCE\uFF0C\u4EE5\u535A\u5218\u806A\u597D\u611F\u3002\u53EF\u5982\u4ECA\u4F17\u5927\u81E3\u9762\u5BF9\u201C\u9752\u8863\u884C\u9152\u201D\u4E00\u4E8B\u6240\u8868\u73B0\u51FA\u6765\u7684\u53CD\u5E94\uFF0C\u5374\u8BA9\u5218\u806A\u5FC3\u4E2D\u5FCC\u6068\u3002\u4E00\u4E2A\u6708\u4EE5\u540E\uFF0C\u53F8\u9A6C\u70BD\u4EE5\u53CA\u5EBE\u73C9\u3001\u738B\u4FCA\u7B49\u4EBA\u4FBF\u88AB\u5904\u6B7B\u3002</p><p>\u8FD9\u4E00\u5E74\u53F8\u9A6C\u70BD\u53EA\u6709\u4E09\u5341\u5C81\uFF0C\u540E\u4E16\u7ED9\u4ED6\u4E0A\u7684\u8C25\u53F7\u662F\u201C<code>\u6000</code>\u201D\uFF1A<code>\u4EC1\u6148\u77ED\u6298\u66F0\u6000\uFF0C\u5931\u4F4D\u800C\u6B7B\u66F0\u6000</code>\u3002</p><blockquote><p>\u664B\u6000\u5E1D\u53F8\u9A6C\u70BD\u591A\u821B\u7684\u4EBA\u751F\uFF0C\u540C\u65F6\u4E5F\u662F\u897F\u664B\u8FD9\u4E2A\u77ED\u547D\u738B\u671D\u7684\u7F29\u5F71\u3002</p></blockquote><p>\u53F8\u9A6C\u70BD\u662F\u6B66\u5E1D\u53F8\u9A6C\u708E\u7684\u7B2C\u4E8C\u5341\u4E94\u5B50\uFF0C\u664B\u60E0\u5E1D\u7684\u4EB2\u5F1F\u5F1F\u3002\u6401\u6B63\u5E38\u671D\u4EE3\uFF0C\u8FD9\u4E2A\u8EAB\u4EFD\u7684\u5F52\u5BBF\u5FC5\u7136\u662F\u5F53\u4E00\u4E2A\u592A\u5E73\u738B\u7237\uFF0C\u4F46\u56E0\u7F18\u9645\u4F1A\uFF0C\u53F8\u9A6C\u70BD\u5374\u88AB\u63A8\u5230\u5386\u53F2\u7684\u524D\u53F0\uFF0C\u5F00\u59CB\u4E86\u591A\u707E\u591A\u96BE\u7684\u4EBA\u751F\u3002</p><p>\u5230\u60E0\u5E1D\u6B7B\u65F6\uFF0C<strong>\u53F8\u9A6C\u70BD\u662F\u7687\u592A\u5F1F</strong>\u3002\u8FD9\u4E2A\u8EAB\u4EFD\u770B\u4F3C\u5C0A\u5D07\uFF0C\u6709\u7740\u50A8\u541B\u7684\u610F\u5473\uFF0C\u4F46\u5176\u5B9E\u5371\u673A\u56DB\u4F0F\uFF1A\u7B97\u6765\uFF0C\u4ED6\u5DF2\u7ECF\u662F\u60E0\u5E1D\u671D\u7684\u7B2C\u516D\u4F4D\u50A8\u541B\u4E86\u3002\u524D\u9762\u7684\u4E94\u4F4D\u50A8\u541B\uFF0C\u5DF2\u7ECF\u6B7B\u4E86\u56DB\u4E2A\uFF1A\u7687\u592A\u5B50\u53F8\u9A6C\u9079\uFF0C\u7687\u592A\u5B59\u53F8\u9A6C\u81E7\u3001\u53F8\u9A6C\u5C1A\uFF0C\u7687\u592A\u5F1F\u53F8\u9A6C\u9896\u2014\u2014\u4ED6\u4EEC\u867D\u7136\u4EBA\u524D\u98CE\u5149\uFF0C\u5374\u51E0\u4E4E\u5168\u90FD\u6B7B\u4E8E\u88AB\u6740\u3002</p><p>\u56E0\u6B64\uFF0C\u5F53\u53F8\u9A6C\u70BD\u88AB\u7ACB\u4E3A\u7687\u592A\u5F1F\u65F6\uFF0C\u4ED6\u4E0B\u610F\u8BC6\u662F\u62D2\u7EDD\u7684\uFF0C\u8FD9\u4E0D\u662F\u8D70\u5411\u8F89\u714C\u4EBA\u751F\u7684\u5EB7\u5E84\u5927\u9053\uFF0C\u800C\u662F\u901A\u5F80\u9B3C\u95E8\u5173\u7684\u5FEB\u901F\u6377\u5F84\u3002</p><p>\u4F46\u6B66\u5E1D\u8840\u8109\u4E0E\u751F\u4FF1\u6765\u7684\u8D23\u4EFB\u611F\uFF0C\u7EC8\u7A76\u8FD8\u662F\u523A\u6FC0\u4ED6\u6311\u8D77\u4E86\u8FD9\u4EFD\u91CD\u62C5\u3002\u5728\u4ED6\u51C6\u5907\u9000\u7F29\u65F6\uFF0C\u6709\u4EBA\u529D\u4ED6\uFF1A\u5982\u4ECA\u56FD\u5BB6\u591A\u96BE\uFF0C\u8BBA\u4EB2\u8BBA\u8D24\uFF0C\u5927\u738B\u90FD\u662F\u4E0D\u4E8C\u4EBA\u9009\uFF0C\u96BE\u9053\u5927\u738B\u5FCD\u5FC3\u770B\u7740\u5148\u5E1D\u7684\u57FA\u4E1A\u5C31\u8FD9\u4E48\u6BC1\u4E8E\u4E00\u65E6\u5417\uFF1F\u53F8\u9A6C\u70BD\u542C\u540E\uFF0C\u6BC5\u7136\u63A5\u53D7\u518C\u5C01\u3002</p><p>\u6B64\u65F6\u5DF2\u662F\u516B\u738B\u4E4B\u4E71\u7684\u672B\u671F\u3002\u6B64\u540E\u4E0D\u4E45\uFF0C<strong>\u4E1C\u6D77\u738B\u8D8A</strong>\u4ECE\u6700\u540E\u7684\u5BF9\u51B3\u4E2D\u83B7\u80DC\uFF0C\u4E3A\u524D\u540E\u6301\u7EED\u4E86\u8FD1\u4E8C\u5341\u5E74\u7684\u516B\u738B\u4E4B\u4E71\u62C9\u4E0B\u5E37\u5E55\u2014\u2014\u867D\u7136\u7559\u7ED9\u8FD9\u4F4D\u83B7\u80DC\u8005\u7684\uFF0C\u53EA\u6709\u6EE1\u76EE\u75AE\u75CD\u7684\u5C40\u52BF\u3002</p><p>\u800C\u4E1C\u6D77\u738B\u8D8A\u6267\u653F\u540E\u6CA1\u51E0\u4E2A\u6708\uFF0C\u671D\u5EF7\u5C31\u8FCE\u6765\u4E86\u5927\u8003\uFF1A<strong>\u60E0\u5E1D\u4E2D\u6BD2\u8EAB\u4EA1</strong>\u3002</p><p>\u540E\u5BAB\u7387\u5148\u884C\u52A8\u8D77\u6765\uFF0C\u60E0\u5E1D<strong>\u7F8A\u540E</strong>\u6025\u53EC\u524D\u592A\u5B50\u3001\u6E05\u6CB3\u738B\u8983\u5165\u5BAB\u3002\u7F8A\u540E\u6B64\u65F6\u662F\u7126\u8651\u7684\uFF1A\u5982\u679C\u53F8\u9A6C\u70BD\u7EE7\u4F4D\uFF0C\u90A3\u4E48\u7F8A\u540E\u4E3A\u5AC2\uFF0C\u52BF\u5FC5\u5C06\u6C38\u8FDC\u6DE1\u51FA\u653F\u6CBB\u821E\u53F0\u3002\u6240\u4EE5\u5979\u624D\u7740\u6025\u7B79\u5212\u8BA9\u665A\u60E0\u5E1D\u4E00\u8F88\u7684\u6E05\u6CB3\u738B\u8983\u7EE7\u4F4D\uFF0C\u53EA\u6709\u5982\u6B64\uFF0C\u5979\u624D\u80FD\u987A\u7406\u6210\u7AE0\u7684\u5347\u4EFB\u592A\u540E\uFF0C\u83B7\u5F97\u5BF9\u671D\u653F\u7684\u53D1\u8A00\u6743\u3002</p><p>\u4F46\u4F8D\u4E2D\u534E\u6DF7\u5DF2\u5C06\u6D88\u606F\u4F20\u51FA\u5BAB\u5916\uFF0C\u4E1C\u6D77\u738B\u8D8A\u4E8E\u662F\u6D3E\u4EBA\u5C06\u53F8\u9A6C\u70BD\u53EC\u5165\u5BAB\u4E2D\u3002\u6E05\u6CB3\u738B\u8983\u6B64\u65F6\u5DF2\u7ECF\u5230\u4E86\u5C1A\u4E66\u9601\uFF0C\u4F46\u4ED6\u53D1\u89C9\u60C5\u5F62\u4E0D\u5BF9\uFF0C\u53C8\u8D76\u7D27\u9000\u51FA\u5BAB\u53BB\u3002</p><p><strong>\u53F8\u9A6C\u70BD\u7EC8\u4E8E\u6210\u529F\u7EE7\u4F4D\uFF0C\u662F\u4E3A\u664B\u6000\u5E1D</strong>\u3002\u81F3\u4E8E\u7F8A\u540E\uFF0C\u5219\u6309\u7167\u60EF\u4F8B\u88AB\u5C01\u4E3A\u60E0\u7687\u540E\uFF0C\u5C45\u5F18\u8BAD\u5BAB\uFF0C\u4ECE\u6B64\u5F7B\u5E95\u6325\u522B\u671D\u653F\u3002</p><p><strong>\u6000\u5E1D\u5373\u4F4D\u540E\uFF0C\u5982\u4F55\u4E0E\u4E1C\u6D77\u738B\u8D8A\u76F8\u5904\uFF0C\u4FBF\u6210\u4E86\u7ED5\u4E0D\u5F00\u7684\u96BE\u9898</strong>\u3002</p><p>\u4E1C\u6D77\u738B\u8D8A\u4F5C\u4E3A\u516B\u738B\u4E4B\u4E71\u7684\u6700\u7EC8\u83B7\u80DC\u8005\uFF0C\u6267\u653F\u5728\u6240\u96BE\u514D\u3002\u521A\u5F00\u59CB\uFF0C\u4E1C\u6D77\u738B\u8D8A\u8FD8\u662F\u91CA\u653E\u4E86\u81EA\u5DF1\u7684\u8BDA\u610F\u7684\uFF1A\u9996\u5148\uFF0C\u6000\u5E1D\u7684\u7687\u592A\u5F1F\u5E76\u4E0D\u662F\u4E1C\u6D77\u738B\u8D8A\u6240\u7ACB\uFF0C\u4F46\u4ED6\u613F\u610F\u627F\u8BA4\uFF0C\u8FD9\u672C\u8EAB\u5C31\u4EE3\u8868\u4E86\u4E00\u79CD\u6001\u5EA6\u3002\u5176\u6B21\uFF0C\u5728\u6000\u5E1D\u7EE7\u4F4D\u540E\u4E0D\u4E45\uFF0C\u6E05\u6CB3\u738B\u8983\u4FBF\u88AB\u6740\uFF0C\u8FD9\u4E5F\u53EF\u4EE5\u7B97\u505A\u662F\u4E1C\u6D77\u738B\u8D8A\u7684\u79EF\u6781\u793A\u597D\u3002</p><blockquote><p>\u4F46\u95EE\u9898\u662F\uFF0C\u4E1C\u6D77\u738B\u8D8A\u60F3\u505A\u4E00\u4E2A\u597D\u6743\u81E3\uFF0C\u6000\u5E1D\u5374\u5E76\u4E0D\u60F3\u505A\u4E00\u4E2A\u597D\u5080\u5121\u3002</p></blockquote><p>\u6000\u5E1D\u4E0E\u60E0\u5E1D\u4E0D\u540C\uFF0C\u4ED6\u60F3\u8981\u53D1\u6325\u81EA\u5DF1\u5728\u671D\u653F\u4E0A\u7684\u5F71\u54CD\uFF0C\u751A\u81F3\u4E8E\u91CD\u65B0\u5149\u5927\u664B\u5BA4\u7684\u57FA\u4E1A\uFF0C\u6062\u590D\u5148\u7956\u7684\u8363\u5149\u3002\u6BD5\u7ADF\uFF0C\u664B\u671D\u7684\u5F00\u521B\u4E4B\u541B\u6B66\u5E1D\u53F8\u9A6C\u708E\uFF0C\u5C31\u662F\u4ED6\u7684\u751F\u7236\u3002</p><p>\u4E24\u4EBA\u4E4B\u95F4\u7684\u77DB\u76FE\uFF0C\u5929\u7136\u4E0D\u53EF\u907F\u514D\u3002</p><p>\u4F46\u773C\u7785\u7740\u51B2\u7A81\u4E00\u89E6\u5373\u53D1\uFF0C\u8001\u5929\u7237\u5374\u53C8\u8F7B\u8F7B\u6309\u4E0B\u4E86\u6682\u505C\u952E\uFF1A<strong>\u6C5F\u6DEE\u4E00\u5E26\uFF0C\u5C40\u52BF\u7A81\u4E71\uFF0C\u5982\u679C\u4EFB\u7531\u4E8B\u6001\u53D1\u5C55\uFF0C\u5C06\u4F1A\u5F71\u54CD\u5230\u6D1B\u9633\u7684\u7269\u8D44\u4F9B\u5E94</strong>\u3002\u6743\u8861\u4E4B\u4E0B\uFF0C\u4E1C\u6D77\u738B\u8D8A\u5E26\u5175\u51FA\u9547\u8BB8\u660C\uFF0C\u4EE5\u4FDD\u8BC1\u4E1C\u65B9\u7684\u7A33\u5B9A\u548C\u7CDF\u8FD0\u7684\u7545\u901A\u3002</p><p>\u8FD9\u5B9E\u5728\u662F\u6000\u5E1D\u7406\u987A\u6D1B\u9633\u671D\u653F\u548C\u7981\u519B\u529B\u91CF\u7684\u7EDD\u4F73\u673A\u4F1A\u3002</p><p>\u6C38\u5609\u5143\u5E74\u4E09\u6708\uFF0C\u4E1C\u6D77\u738B\u8D8A\u6B63\u5F0F\u51FA\u4EAC\u3002\u81EA\u6B64\u4EE5\u540E\uFF0C\u4ED6\u4E0E\u6000\u5E1D\u5F00\u59CB\u5404\u5FD9\u5404\u7684\uFF0C\u4E00\u4E2A\u7126\u5934\u70C2\u989D\u4E8E\u65E5\u76CA\u58EE\u5927\u7684\u5308\u5974\u4EBA\u52BF\u529B\uFF0C\u53E6\u4E00\u4E2A\u5219\u5728\u671D\u4E2D\u91CD\u6574\u6743\u67C4\u3002</p><p>\u4F46\u4E0D\u540C\u7684\u662F\uFF0C\u671D\u5802\u5728\u6000\u5E1D\u7684\u52AA\u529B\u7ECF\u8425\u4E0B\u65E5\u89C1\u8D77\u8272\uFF0C\u5929\u4E0B\u5C40\u52BF\u5374\u5728\u4E1C\u6D77\u738B\u8D8A\u7684\u201C\u7528\u5FC3\u7ECF\u8425\u201D\u4E0B\u9010\u6E10\u7CDC\u70C2\u3002</p><p>\u6C38\u5609\u4E8C\u5E74\u4E09\u6708\uFF0C\u4E1C\u6D77\u738B\u8D8A\u4ECE\u8BB8\u660C\u79FB\u9547\u9104\u57CE\u3002\u56DB\u6708\uFF0C\u8BB8\u660C\u88AB\u5DF2\u7ECF\u6295\u9760\u5308\u5974\u4EBA\u7684\u6D41\u5BC7\u738B\u5F25\u7B2C\u4E00\u6B21\u653B\u7834\u3002</p><p><strong>\u4E94\u6708\uFF0C\u738B\u5F25\u7387\u519B\u8FDB\u653B\u6D1B\u9633\uFF0C\u6D1B\u9633\u5927\u9707</strong>\u3002\u81EA\u4ECE\u5EFA\u5B89\u5143\u5E74\u66F9\u64CD\u6536\u590D\u6D1B\u9633\u4EE5\u6765\uFF0C\u6D1B\u9633\u5DF2\u7ECF\u6709\u4E00\u767E\u591A\u5E74\u6CA1\u6709\u7ECF\u5386\u8FC7\u6218\u4E89\u4E86\u3002</p><p>\u81EA\u516B\u738B\u4E4B\u4E71\u4EE5\u6765\uFF0C\u738B\u5F25\u5DF2\u7ECF\u5728\u9752\u5F90\u4E00\u5E26\u6D41\u7A9C\u4E86\u6570\u5E74\u4E4B\u4E45\u3002\u4E1C\u6D77\u738B\u8D8A\u4E4B\u6240\u4EE5\u51FA\u9547\u8BB8\u660C\uFF0C\u76EE\u7684\u4E4B\u4E00\u4E5F\u662F\u8981\u9632\u5907\u738B\u5F25\u6240\u90E8\u3002\u4F46\u5343\u9632\u4E07\u9632\uFF0C\u738B\u5F25\u7ADF\u7136\u7A9C\u5230\u4E86\u773C\u76AE\u5B50\u5E95\u4E0B\uFF0C\u654C\u4EBA\u7684\u56A3\u5F20\u7A0B\u5EA6\u53EF\u89C1\u4E00\u6591\u3002</p><p>\u6240\u5E78\u7684\u662F\uFF0C\u738B\u5F25\u6B64\u65F6\u8FD8\u5E76\u6CA1\u6709\u653B\u4E0B\u6D1B\u9633\u7684\u5B9E\u529B\uFF0C\u5728\u57CE\u5916\u9A9A\u6270\u4E86\u4E00\u756A\u540E\u5373\u4FBF\u64A4\u8D70\u3002</p><p>\u4F46\u5BF9\u4E8E\u5308\u5974\u4EBA\u800C\u8A00\uFF0C\u8FD9\u4F9D\u7136\u662F\u91CD\u5927\u7A81\u7834\u3002\u4E4B\u540E\u738B\u5F25\u56DE\u5230\u5E73\u9633\uFF0C**\u5308\u5974\u7687\u5E1D\u5218\u6E0A\uFF08\u5218\u806A\u4E4B\u7236\uFF09**\u4EB2\u81EA\u524D\u6765\u8FCE\u63A5\u3002</p><p>\u4E2D\u539F\u5982\u6B64\uFF0C\u6CB3\u4E1C\u3001\u6CB3\u5317\u5E26\u7684\u5C40\u9762\u4E5F\u662F\u4E00\u56E2\u4E71\u9EBB\uFF0C\u81EA\u5218\u6E0A\u5728\u6CB3\u4E1C\u8D77\u4E8B\uFF0C\u5E76\u5DDE\u4E4B\u5730\u4ECE\u6B64\u6218\u706B\u4E0D\u606F\u3002\u540E\u6765\uFF0C\u671D\u5EF7\u4EFB\u547D\u5218\u7428\u4E3A\u5E76\u5DDE\u523A\u53F2\uFF0C\u4F46\u5218\u7428\u5355\u67AA\u5339\u9A6C\u4E0A\u4EFB\uFF0C\u81F3\u591A\u53EA\u80FD\u4FDD\u8BC1\u9996\u5E9C\u664B\u9633\u4E0D\u5931\uFF0C\u5728\u53CD\u653B\u5308\u5974\u7684\u4E8B\u60C5\u4E0A\u6839\u672C\u5C31\u6709\u5FC3\u65E0\u529B\u3002</p><p>\u6B64\u540E\uFF0C\u5218\u6E0A\u5DF2\u7ECF\u9010\u6B65\u6E05\u9664\u5230\u6D1B\u9633\u4E4B\u95F4\u7684\u664B\u671D\u52BF\u529B\uFF0C\u5C06\u6218\u706B\u70E7\u5230\u4E86\u9EC4\u6CB3\u5CB8\u8FB9\u3002</p><p>\u6CB3\u5317\u4E00\u5E26\uFF0C\u9632\u5FA1\u4E00\u76F4\u4EE5\u90BA\u57CE\u4E3A\u4E2D\u5FC3\uFF0C\u8FD9\u5EA7\u4ECE\u8881\u7ECD\u65F6\u4EE3\u5C31\u5174\u8D77\u7684\u5927\u57CE\uFF0C\u671F\u95F4\u4E5F\u53CD\u590D\u88AB\u5308\u5974\u4EBA\u653B\u7834\uFF0C\u867D\u7136\u65F6\u6709\u6536\u590D\uFF0C\u4F46\u5728\u5218\u6E0A\u90E8\u5C06\u77F3\u52D2\u8FDB\u5165\u6CB3\u5317\u4EE5\u540E\uFF0C\u6700\u7EC8\u5931\u9677\u3002<strong>\u81F3\u6B64\uFF0C\u9EC4\u6CB3\u4EE5\u5317\uFF0C\u664B\u5BA4\u518D\u65E0\u53EF\u4F9D\u4ED7\u7684\u52BF\u529B</strong>\u3002</p><p>\u8EAB\u5728\u5916\u5730\u7684\u4E1C\u6D77\u738B\u8D8A\u7EC8\u4E8E\u518D\u4E5F\u5F85\u4E0D\u4F4F\u4E86\uFF0C\u53EA\u5F97\u8FD4\u56DE\u6D1B\u9633\u3002</p><p>\u56DE\u5230\u6D1B\u9633\u4EE5\u540E\uFF0C\u4E1C\u6D77\u738B\u8D8A\u6240\u505A\u7684\u7B2C\u4E00\u4EF6\u4E8B\uFF0C\u5374\u662F\u5F00\u59CB\u6E05\u9664\u6000\u5E1D\u7684\u515A\u7FBD\u3002</p><p><strong>\u4E1C\u6D77\u738B\u8D8A\u6B64\u6B21\u524D\u524D\u540E\u540E\u6740\u4E86\u671D\u81E3\u51E0\u5341\u4EBA\uFF0C\u5E76\u5C06\u7981\u536B\u519B\u9A71\u9010\u4E00\u7A7A</strong>\u3002\u9762\u5BF9\u7740\u4ED6\u6BD5\u9732\u7684\u7360\u7259\uFF0C\u6000\u5E1D\u6B32\u54ED\u65E0\u6CEA\u3002\u4E71\u4E16\u4E4B\u4E2D\uFF0C\u519B\u6743\u624D\u662F\u4E00\u5207\u6743\u529B\u7684\u6839\u672C\u3002\u6CA1\u6709\u519B\u4E8B\u5B9E\u529B\u7684\u6000\u5E1D\uFF0C\u5373\u4FBF\u5728\u671D\u5802\u4E0A\u58F0\u97F3\u518D\u5927\uFF0C\u4E5F\u7EC8\u7A76\u53EA\u662F\u4E1C\u6D77\u738B\u8D8A\u624B\u4E2D\u53EF\u4EE5\u88AB\u968F\u610F\u62FF\u634F\u7684\u6CE5\u5076\u3002</p><blockquote><p>\u53EA\u662F\uFF0C\u7559\u7ED9\u664B\u5BA4\u7684\u65F6\u95F4\uFF0C\u5DF2\u7ECF\u8D8A\u6765\u8D8A\u5C11\u4E86\u3002</p></blockquote><p>\u6C38\u5609\u4E09\u5E74\u516B\u6708\uFF0C\u5218\u806A\u4ECE\u5E73\u9633\u5357\u4E0B\uFF0C\u8FD9\u662F\u5308\u5974\u7B2C\u4E00\u6B21\u4ECE\u5317\u65B9\u6E21\u8FC7\u9EC4\u6CB3\uFF0C\u8FDB\u653B\u6D1B\u9633\u3002\u8FD9\u6B21\u8FDB\u653B\u5E76\u4E0D\u6210\u529F\uFF0C\u5218\u806A\u8D25\u8D70\u3002\u4F46\u624D\u8FC7\u4E86\u4E24\u4E2A\u6708\uFF0C\u5218\u806A\u5C31\u91CD\u6574\u65D7\u9F13\u518D\u6B21\u5357\u4E0B\u3002</p><p>\u8FD9\u4E00\u6B21\uFF0C\u8F7B\u8F66\u719F\u8DEF\u7684\u5308\u5974\u5927\u519B\u957F\u9A71\u76F4\u4E0B\uFF0C\u5F88\u5BB9\u6613\u5C31\u5230\u4E86\u6D1B\u9633\u57CE\u4E0B\u3002\u4F46\u6D1B\u9633\u57CE\u9632\u575A\u56FA\uFF0C\u5218\u806A\u6700\u7EC8\u8FD8\u662F\u9000\u8FD8\u3002</p><p>\u53C8\u9694\u4E86\u4E00\u5E74\uFF0C\u5308\u5974\u4EBA\u518D\u4E00\u6B21\u5357\u4E0B\u8FDB\u653B\u6D1B\u9633\u3002\u5728\u7ECF\u5386\u4E86\u524D\u4E24\u6B21\u7684\u5931\u8D25\u540E\uFF0C\u5308\u5974\u5927\u519B\u8FD9\u6B21\u7EC8\u4E8E\u627E\u5230\u4E86\u597D\u529E\u6CD5\uFF1A<strong>\u56F4\u653B</strong>\u3002</p><p>\u5386\u7ECF\u591A\u5E74\u6218\u4E71\uFF0C\u4E2D\u539F\u4E00\u5E26\u65E9\u5DF2\u6210\u4E3A\u8352\u5730\u3002\u8FD9\u4E9B\u5E74\u671D\u5EF7\u4E4B\u6240\u4EE5\u8FD8\u80FD\u7EF4\u6301\u4F4F\u6D1B\u9633\u7684\u575A\u633A\uFF0C\u5176\u5B9E\u9760\u7684\u662F\u4E1C\u5357\u4E00\u5E26\u7684\u6C34\u8DEF\u7269\u8D44\u2014\u2014\u8FD9\u4E5F\u662F\u4E4B\u524D\u4E1C\u6D77\u738B\u8D8A\u8981\u51FA\u9547\u8BB8\u660C\u7684\u91CD\u8981\u539F\u56E0\u3002</p><p><strong>\u4F46\u4F34\u968F\u7740\u738B\u5F25\u4E4B\u524D\u5BF9\u6D1B\u9633\u7684\u88AD\u6270\uFF0C\u5308\u5974\u4EBA\u5DF2\u7ECF\u77E5\u9053\u4E86\u664B\u671D\u7684\u865A\u5B9E\uFF0C\u4ED6\u4EEC\u5F00\u59CB\u4ECE\u5357\u65B9\u76F4\u63A5\u5207\u65AD\u6D1B\u9633\u7684\u8865\u7ED9</strong>\u3002\u738B\u5F25\u3001\u77F3\u52D2\u5148\u540E\u8FDB\u5165\u6D1B\u9633\u5357\u90E8\uFF0C\u81EA\u6B64\u4EE5\u540E\uFF0C\u4ECE\u8346\u5DDE\u3001\u626C\u5DDE\u4E24\u4E2A\u65B9\u5411\u5230\u8FBE\u6D1B\u9633\u7684\u8054\u7EDC\u88AB\u5B8C\u5168\u4E2D\u65AD\u3002</p><blockquote><p>\u4E0D\u8FC7\u624D\u4E24\u5E74\u4E4B\u95F4\uFF0C\u5308\u5974\u4EBA\u5C31\u5B8C\u6210\u4E86\u5BF9\u6D1B\u9633\u7684\u6218\u7565\u5305\u56F4\u3002\u6000\u5E1D\u541B\u81E3\uFF0C\u5BF9\u5750\u5B64\u57CE\u3002</p></blockquote><p>\u4E1C\u6D77\u738B\u8D8A\u6B64\u523B\u4E5F\u614C\u4E86\u795E\uFF0C\u4ED6\u4EE5\u6000\u5E1D\u7684\u540D\u4E49\u4E0B\u8BCF\uFF0C\u4EE4\u5929\u4E0B\u8BF8\u4FAF\u52E4\u738B\u3002\u6000\u5E1D\u4EB2\u81EA\u5BF9\u4F7F\u8005\u4EEC\u8BF4\uFF1A\u544A\u8BC9\u5929\u4E0B\u8BF8\u4FAF\uFF0C\u5982\u4ECA\u6551\u63F4\u5C1A\u4E14\u6765\u5F97\u53CA\uFF0C\u518D\u62D6\u5C31\u6CA1\u6709\u673A\u4F1A\u4E86\uFF01</p><p>\u7136\u800C\u6839\u672C\u5C31\u6CA1\u6709\u4EBA\u6765\u3002\u8BF8\u738B\u4EEC\u6709\u7684\u5750\u89C2\u6210\u8D25\uFF0C\u6709\u7684\u529B\u6240\u4E0D\u53CA\u3002\u4EC5\u6709\u7684\u4E00\u4E9B\u6551\u5175\uFF0C\u6839\u672C\u8FC7\u4E0D\u4E86\u5308\u5974\u4EBA\u7684\u9632\u7EBF\u3002</p><p>\u6218\u9F13\u58F0\u6E10\u8FDB\uFF0C\u4E1C\u6D77\u738B\u8D8A\u4E8E\u662F\u53C8\u5411\u6000\u5E1D\u63D0\u8BAE\uFF1A\u6211\u51C6\u5907\u5E26\u5175\u51FA\u5C6F\u8BB8\u660C\uFF0C\u4E0E\u654C\u519B\u5BFB\u6C42\u51B3\u6218\uFF01</p><p>\u4E8B\u5B9E\u5F53\u7136\u4E0D\u50CF\u4E1C\u6D77\u738B\u8D8A\u6240\u8BF4\u7684\u90A3\u4E48\u597D\u542C\u3002\u51E0\u5E74\u4EE5\u6765\uFF0C\u5C40\u52BF\u7CDC\u70C2\uFF0C\u5982\u4ECA\u4E1C\u5357\u7684\u8865\u7ED9\u53C8\u5DF2\u4E2D\u65AD\uFF0C<strong>\u6D1B\u9633\u5931\u9677\u53EA\u662F\u65F6\u95F4\u95EE\u9898</strong>\u3002\u56E0\u6B64\u4E1C\u6D77\u738B\u8D8A\u5634\u4E0A\u8BF4\u7740\u8981\u6253\u51FA\u53BB\uFF0C\u5FC3\u91CC\u7684\u5C0F\u7B97\u76D8\u5374\u662F\u8981\u5728\u5C1A\u6709\u9000\u8DEF\u4E4B\u65F6\u9003\u79BB\uFF0C\u5426\u5219\u5B88\u7740\u6D1B\u9633\u53EA\u6709\u6B7B\u8DEF\u4E00\u6761\u3002</p><p><strong>\u81F3\u4E8E\u6000\u5E1D\uFF0C\u4E1C\u6D77\u738B\u8D8A\u81EA\u7136\u4E0D\u4F1A\u5E26\u8D70</strong>\u3002\u628A\u6000\u5E1D\u8FD9\u4E2A\u5927\u5E03\u5076\u7559\u4E0B\uFF0C\u6B63\u597D\u53EF\u4EE5\u5438\u5F15\u5308\u5974\u4EBA\u7684\u706B\u529B\u653B\u51FB\uFF0C\u8FD9\u6837\u81EA\u5DF1\u7684\u64A4\u9000\u8FD8\u80FD\u4ECE\u5BB9\u4E9B\u3002</p><p>\u6000\u5E1D\u4E5F\u77E5\u9053\u4E1C\u6D77\u738B\u8D8A\u7684\u8FD9\u4E9B\u5FC3\u601D\uFF0C\u7136\u800C\u65F6\u4E8B\u5982\u6B64\uFF0C\u4ED6\u4E0D\u5F97\u4E0D\u653E\u4F4E\u8EAB\u6BB5\u54C0\u6C42\uFF1A\u5982\u4ECA\u671D\u5EF7\u7684\u5BB6\u5E95\u5C31\u8FD9\u4E48\u591A\uFF0C\u4F60\u5168\u5E26\u8D70\u4E86\uFF0C\u6D1B\u9633\u600E\u4E48\u529E\u554A\uFF01</p><p>\u4E1C\u6D77\u738B\u8D8A\u56DE\u9053\uFF0C\u965B\u4E0B\u83AB\u614C\uFF0C\u6211\u8FD9\u5C31\u51FA\u4EAC\u5BFB\u6C42\u4E0E\u654C\u519B\u51B3\u6218\uFF01\u5982\u679C\u771F\u7684\u80FD\u591F\u7834\u8D3C\uFF0C\u90A3\u4E48\u8FDC\u597D\u8FC7\u5982\u4ECA\u56F0\u5B88\u5B64\u57CE\u7684\u5C40\u9762\uFF01</p><p>\u968F\u540E\uFF0C<strong>\u4E1C\u6D77\u738B\u8D8A\u5E26\u7740\u56DB\u4E07\u5927\u519B\u5F00\u8D74\u8BB8\u660C</strong>\uFF0C\u8FD9\u51E0\u4E4E\u5DF2\u7ECF\u662F\u6D1B\u9633\u6240\u6709\u7684\u6B66\u88C5\u529B\u91CF\u3002</p><p>\u6D1B\u9633\u987F\u65F6\u9677\u5165\u6DF7\u4E71\uFF0C\u57CE\u5185\u5DF2\u7ECF\u65AD\u7CAE\uFF0C\u76D7\u8D3C\u6A2A\u884C\uFF0C\u751A\u81F3\u4E8E\u5BAB\u95E8\u65E0\u4EBA\u770B\u5B88\uFF0C\u6BBF\u5185\u5C38\u4F53\u4EA4\u6A2A\u3002</p><p>\u4EFB\u8C01\u90FD\u77E5\u9053\uFF0C\u8FD9\u6837\u7684\u6D1B\u9633\u662F\u65E0\u6CD5\u56FA\u5B88\u7684\u3002\u5728\u9752\u5DDE\u3001\u5156\u5DDE\u4E00\u5E26\u5C1A\u6709\u5B9E\u529B\u7684<strong>\u82DF\u665E</strong>\u5EFA\u8BAE\u6000\u5E1D\u8FC1\u90FD\u3002\u6000\u5E1D\u4E5F\u5F88\u60F3\u8D70\uFF0C\u53EF\u65E0\u5948\u5927\u81E3\u4EEC\u820D\u4E0D\u5F97\u6D1B\u9633\u7684\u4EA7\u4E1A\uFF0C\u4E0D\u540C\u610F\u3002</p><p>\u72B9\u8C6B\u826F\u4E45\uFF0C\u76F4\u5230\u6D1B\u9633\u57CE\u8FDE\u5403\u7684\u90FD\u6CA1\u6709\u4E86\uFF0C\u5927\u81E3\u4EEC\u624D\u7EC8\u4E8E\u70B9\u5934\u3002\u4F46\u6B64\u65F6\u5DF2\u7ECF\u8FDE\u8F66\u90FD\u627E\u4E0D\u5230\u4E86\uFF0C\u6000\u5E1D\u53EA\u80FD\u6B65\u884C\u64A4\u79BB\u3002</p><p>\u51FA\u4E86\u7687\u5BAB\u540E\uFF0C\u6D1B\u9633\u8857\u4E0A\u76D7\u8D3C\u6A2A\u884C\u3002\u6000\u5E1D\u4E00\u884C\u88AB\u62A2\u63A0\u4E86\u4E00\u756A\uFF0C\u4E0D\u5F97\u5DF2\u53EA\u80FD\u53C8\u9000\u56DE\u5BAB\u4E2D\u2014\u2014\u8FD9\u4E5F\u602A\u4E0D\u5F97\u76D7\u8D3C\uFF0C\u6B64\u65F6\u5C31\u8FDE\u6000\u5E1D\u81EA\u5DF1\u5403\u7684\uFF0C\u90FD\u662F\u5927\u81E3\u4EEC\u62A2\u6765\u8FDB\u732E\u7684\u3002</p><p>\u4E00\u8FB9\u662F\u671D\u5EF7\u7684\u8FC1\u5EF6\u4E0D\u51B3\uFF0C\u53E6\u4E00\u8FB9\u5374\u662F\u5308\u5974\u94C1\u9A91\u7684\u52A0\u901F\u63A8\u8FDB\u3002\u5728\u671D\u5EF7\u7684\u72B9\u8C6B\u626F\u76AE\u4E2D\uFF0C\u5308\u5974\u4EBA\u5DF2\u7ECF\u6E21\u8FC7\u4E86\u9EC4\u6CB3\u3002</p><p>\u9996\u5148\u5230\u8FBE\u6D1B\u9633\u57CE\u4E0B\u7684\uFF0C\u662F\u5218\u806A\u7684\u5927\u5C06\u547C\u5EF6\u664F\u3002\u547C\u5EF6\u664F\u5E76\u6CA1\u6709\u7ACB\u5373\u653B\u57CE\uFF0C\u53EA\u662F\u5728\u6D1B\u9633\u57CE\u5916\u62A2\u63A0\u4E86\u4E00\u756A\uFF0C\u5E76\u987A\u4FBF\u70E7\u4E86\u6D1B\u6C34\u4E0A\u7684\u8239\u2014\u2014\u8FD9\u4E9B\u8239\u90FD\u662F\u82DF\u665E\u9001\u6765\u7ED9\u6000\u5E1D\u51C6\u5907\u64A4\u9000\u7684\u3002</p><p>\u51E0\u5929\u4EE5\u540E\uFF0C\u738B\u5F25\u3001\u5218\u66DC\u90E8\u4E5F\u5230\u8FBE\u57CE\u4E0B\uFF0C\u6D1B\u9633\u968F\u5373\u7834\u57CE\u3002\u8FDB\u57CE\u540E\uFF0C\u5308\u5974\u4EBA\u8F7B\u800C\u6613\u4E3E\u5C31\u64D2\u83B7\u4E86\u8FDE\u8F66\u90FD\u6CA1\u6709\u7684\u6000\u5E1D\u3002</p><p>\u5C31\u5728\u6D1B\u9633\u57CE\u9677\u843D\u524D\u5915\uFF0C\u4E1C\u6D77\u738B\u8D8A\u7684\u751F\u547D\u4E5F\u8D70\u5230\u4E86\u5C3D\u5934\u3002</p><p>\u4ED6\u4E8E\u6C38\u5609\u56DB\u5E74\u5341\u4E00\u6708\u7387\u90E8\u79BB\u5F00\u6D1B\u9633\uFF0C\u5165\u9A7B\u8BB8\u660C\u3002\u4E4B\u540E\u672A\u505A\u8FC7\u591A\u505C\u7559\uFF0C\u53C8\u4E00\u8DEF\u4E1C\u884C\uFF0C\u5728\u8D70\u5230\u9879\u57CE\u65F6\u75C5\u901D\uFF0C\u6B64\u65F6\u662F\u6C38\u5609\u4E94\u5E74\u4E09\u6708\u2014\u2014\u8DDD\u79BB\u6D1B\u9633\u57CE\u7834\uFF0C\u8FD8\u6709\u4E09\u4E2A\u6708\u7684\u65F6\u95F4\u3002</p><p>\u4E34\u7EC8\u524D\uFF0C\u4E1C\u6D77\u738B\u8D8A\u5C06\u6307\u6325\u6743\u4EA4\u4E0E\u592A\u5C09<strong>\u738B\u884D</strong>\uFF0C\u5E76\u8981\u6C42\u4ED6\u7EE7\u7EED\u4E1C\u884C\uFF0C\u6700\u7EC8\u62B5\u8FBE\u81EA\u5DF1\u7684\u5C01\u56FD\u2014\u2014\u4E1C\u6D77\u3002</p><p>\u6309\u7167\u4E1C\u6D77\u738B\u8D8A\u7684\u8BBE\u60F3\uFF0C\u4E0E\u5176\u548C\u5308\u5974\u4EBA\u5728\u62C9\u952F\u6218\u4E2D\u6D88\u8017\u5B9E\u529B\uFF0C\u8FD8\u4E0D\u5982\u9000\u907F\u4E09\u820D\uFF0C\u7559\u7740\u6000\u5E1D\u5728\u6D1B\u9633\u7275\u5236\u5308\u5974\u4EBA\u3002\u6BD5\u7ADF\u4E71\u4E16\u4E4B\u4E2D\uFF0C\u4FDD\u5B58\u5B9E\u529B\u624D\u662F\u6700\u91CD\u8981\u7684\u3002</p><p>\u8DDF\u4E1C\u6D77\u738B\u8D8A\u60F3\u6CD5\u4E00\u81F4\u7684\u4EBA\u4E0D\u5C11\uFF0C\u56E0\u6B64\u5728\u6D1B\u9633\u5C06\u7834\u4E4B\u9645\uFF0C\u5927\u91CF\u7684\u738B\u516C\u8D35\u65CF\u90FD\u8DDF\u7740\u519B\u961F\u64A4\u51FA\u4E86\u6D1B\u9633\uFF0C\u4E00\u8DEF\u4E1C\u884C\u3002</p><p><strong>\u4F46\u8FD9\u6B21\u4ED6\u4EEC\u5374\u5931\u7B97\u4E86</strong>\u3002\u77F3\u52D2\u5728\u8FDB\u636E\u8BB8\u660C\u540E\uFF0C\u5E76\u6CA1\u6709\u5411\u6D1B\u9633\u51FA\u5175\uFF0C\u53CD\u800C\u662F\u8C03\u8F6C\u67AA\u5934\uFF0C\u4E00\u8DEF\u671D\u7740\u4E1C\u6D77\u738B\u8D8A\u8FFD\u8FC7\u6765\u3002\u5728\u4E1C\u6D77\u738B\u8D8A\u75C5\u901D\u540E\u4E00\u4E2A\u6708\uFF0C\u77F3\u52D2\u7EC8\u4E8E\u8FFD\u4E0A\u4E86\u8FD9\u652F\u4EBA\u9A6C\u3002</p><p>\u63A5\u4E0B\u6765\u53D1\u751F\u7684\u4E8B\uFF0C<strong>\u53EA\u80FD\u7528\u5C60\u6740\u6765\u5F62\u5BB9</strong>\u3002\u5931\u53BB\u4E86\u57CE\u9632\u4FDD\u62A4\u7684\u4E1C\u6D77\u56FD\u5175\u5B8C\u5168\u6CA1\u6709\u9632\u5B88\u4E4B\u529B\uFF0C\u6B7B\u4E8E\u4E71\u519B\u4E4B\u4E2D\u7684\u4EBA\u591A\u8FBE\u5341\u4F59\u4E07\u3002</p><p>\u6B64\u540E\uFF0C\u77F3\u52D2\u53C8\u5835\u622A\u4E86\u4ECE\u6D1B\u9633\u64A4\u51FA\u6765\u7684\u53E6\u4E00\u6CE2\u738B\u516C\u5927\u81E3\uFF0C\u5E76\u5C06\u4ED6\u4EEC\u51E0\u4E4E\u5168\u90E8\u6740\u6B7B\u3002\u8FD9\u5176\u4E2D\uFF0C<strong>\u5305\u62EC\u4E86\u5B97\u5BA4\u56DB\u5341\u516B\u4EBA</strong>\u3002</p><p>\u5728\u8FD9\u4E4B\u540E\uFF0C\u77F3\u52D2\u53C8\u5228\u5F00\u4E86\u4E1C\u6D77\u738B\u8D8A\u7684\u68FA\u6728\uFF0C\u5C06\u5176\u632B\u9AA8\u626C\u7070\uFF0C\u201C<strong>\u4E71\u5929\u4E0B\u8005\u6B64\u4EBA\u4E5F\uFF0C\u543E\u4E3A\u5929\u4E0B\u62A5\u4E4B\uFF0C\u6545\u711A\u5176\u9AA8\u4EE5\u544A\u5929\u5730</strong>\u3002\u201D</p><p>\u4F5C\u4E3A\u4E00\u4E2A\u7687\u5E1D\uFF0C\u6000\u5E1D\u7684\u4E00\u751F\u5145\u6EE1\u4E86\u65E0\u529B\u7684\u82CD\u51C9\u611F\u3002<strong>\u4ED6\u5DF2\u5728\u529B\u6240\u80FD\u53CA\u7684\u8303\u56F4\u5185\u628A\u4E8B\u60C5\u505A\u5230\u6700\u597D\uFF0C\u4F46\u4F9D\u7136\u4E8E\u4E8B\u65E0\u8865</strong>\u3002</p><p>\u516B\u738B\u4E4B\u4E71\u6301\u7EED\u8FD1\u4E8C\u5341\u5E74\uFF0C\u65E9\u5DF2\u5C06\u5927\u664B\u671D\u7684\u7EDF\u6CBB\u6839\u57FA\u63C9\u5F97\u7C89\u788E\uFF1B\u5F02\u65CF\u53C8\u5D1B\u8D77\u4E8E\u585E\u5185\uFF0C\u8FD9\u662F\u4E2D\u539F\u738B\u671D\u4ECE\u6765\u90FD\u6CA1\u6709\u9762\u5BF9\u8FC7\u7684\u9EBB\u70E6\uFF1B\u4ED6\u4E5F\u66FE\u8BD5\u56FE\u4ECE\u4E1C\u6D77\u738B\u8D8A\u7684\u624B\u4E2D\u593A\u53D6\u6743\u529B\uFF0C\u4F46\u5374\u4EE5\u5931\u8D25\u544A\u7EC8\u3002</p><p>\u800C\u90A3\u4E2A\u4E0E\u4ED6\u7F81\u7ECA\u534A\u751F\u3001\u4E00\u76F4\u5728\u6458\u679C\u5B50\u7684\u4E1C\u6D77\u738B\u8D8A\uFF0C\u504F\u504F\u53C8\u662F\u4E2A\u80FD\u529B\u5E73\u5EB8\u4E4B\u8F88\u3002<strong>\u6B63\u662F\u7531\u4E8E\u4ED6\u5728\u6267\u653F\u7684\u51E0\u5E74\u5185\u758F\u4E8E\u90E8\u7F72\uFF0C\u624D\u8BA9\u5308\u5974\u4EBA\u9010\u6E10\u5750\u5927\uFF0C\u5E76\u6700\u7EC8\u653B\u7834\u6D1B\u9633</strong>\u3002</p><p>\u706F\u67AF\u6CB9\u5C3D\uFF0C\u897F\u664B\u7684\u56FD\u8FD0\u7EC8\u88AB\u8017\u5B8C\u3002</p><p>\u6574\u4F53\u4E0A\u800C\u8A00\uFF0C\u6000\u5E1D\u53EF\u4EE5\u7B97\u4F5C\u662F\u4E00\u4E2A\u4E0D\u9519\u7684\u7687\u5E1D\u3002\u4ED6\u662F\u6B66\u5E1D\u7855\u679C\u4EC5\u5B58\u7684\u513F\u5B50\uFF0C\u5929\u7136\u6709\u7EE7\u4F4D\u7684\u5408\u7406\u6027\uFF1B\u4ED6\u65E9\u5E74\u8DDF\u968F\u60E0\u5E1D\u98A0\u6C9B\u6D41\u79BB\uFF0C\u6709\u8FC7\u5728\u7981\u519B\u4E2D\u4EFB\u804C\u7684\u7ECF\u5386\uFF0C\u8FD9\u4E3A\u4ED6\u540E\u6765\u63A7\u5236\u7981\u519B\u6253\u4E0B\u4E86\u826F\u597D\u57FA\u7840\uFF1B\u5373\u4F4D\u540E\uFF0C\u4ED6\u80FD\u5728\u77ED\u671F\u5185\u53D6\u5F97\u671D\u4E2D\u5927\u81E3\u7684\u652F\u6301\uFF1B\u751A\u81F3\u4E8E\u9634\u5DEE\u9633\u9519\u95F4\uFF0C\u4E1C\u6D77\u738B\u8D8A\u8FD8\u7559\u7ED9\u4ED6\u4E00\u5E74\u591A\u7684\u65F6\u95F4\u4E13\u5FC3\u7ECF\u8425\u3002</p><p>\u4F46\u5373\u4FBF\u5982\u6B64\uFF0C\u5C40\u52BF\u4F9D\u7136\u65E0\u6CD5\u633D\u56DE\u3002\u4E00\u4E2A\u7A7A\u58F3\u7687\u5E1D\u6240\u80FD\u505A\u7684\u4E8B\u6781\u4E3A\u6709\u9650\uFF0C\u4ED6\u65E2\u65E0\u6CD5\u4ECE\u4E1C\u6D77\u738B\u8D8A\u624B\u4E2D\u593A\u56DE\u6743\u529B\uFF0C\u4E5F\u65E0\u6CD5\u6539\u53D8\u5308\u5974\u4EBA\u5F3A\u5927\u7684\u4E8B\u5B9E\uFF0C\u751A\u81F3\u4E8E\uFF0C\u4ED6\u8FDE\u81EA\u5DF1\u7684\u5B89\u5168\u548C\u5C0A\u4E25\u90FD\u65E0\u6CD5\u4FDD\u8BC1\u3002</p><p>\u8FD9\u4E00\u5207\u90FD\u4E0D\u662F\u4ED6\u7684\u9519\uFF0C\u4ED6\u4E5F\u60F3\u52AA\u529B\u7684\u6539\u53D8\uFF0C\u4F46\u5374\u65E0\u80FD\u4E3A\u529B\u3002\u4E0A\u4E00\u4EE3\u917F\u6210\u7684\u82E6\u9152\uFF0C\u6700\u540E\u88AB\u4ED6\u4E00\u996E\u800C\u5C3D\u3002</p><p>\u8FD9\u4E0D\u662F\u4ED6\u7684\u9519\uFF0C\u5374\u7531\u4ED6\u627F\u62C5\u4E86\u6700\u75DB\u7684\u540E\u679C\u3002</p><h2 id="\u4E1C\u6D77\u738B\u8D8A\u672B\u8DEF" tabindex="-1"><a class="header-anchor" href="#\u4E1C\u6D77\u738B\u8D8A\u672B\u8DEF" aria-hidden="true">#</a> \u4E1C\u6D77\u738B\u8D8A\u672B\u8DEF</h2><p>\u4E1C\u6D77\u738B\u8D8A\u4E4B\u6240\u4EE5\u8981\u56DE\u5C01\u56FD\uFF0C\u5E76\u5728\u75C5\u901D\u524D\u5C06\u519B\u961F\u6307\u6325\u6743\u4EA4\u7ED9\u592A\u5C09\u738B\u884D\uFF0C\u662F\u6709\u8FC7\u6DF1\u601D\u719F\u8651\u7684\u3002</p><p>\u8D81\u7740\u8FD9\u51E0\u5E74\u6267\u653F\uFF0C\u4E1C\u6D77\u738B\u8D8A\u5DF2\u7ECF\u628A\u540E\u8DEF\u5B89\u6392\u5F97\u59A5\u59A5\u7684\u3002\u4E1C\u6D77\u56FD\u5C5E\u5F90\u5DDE\uFF0C\u5F90\u5DDE\u523A\u53F2\u5DF2\u7ECF\u662F\u4E1C\u6D77\u738B\u8D8A\u7684\u4EB2\u4FE1\u3002\u540C\u65F6\uFF0C\u90FD\u7763\u5F90\u5DDE\u519B\u4E8B\u7684\u53C8\u662F\u9A7B\u624E\u5728\u5EFA\u4E1A\u7684\u7405\u740A\u738B<strong>\u53F8\u9A6C\u777F</strong>\uFF0C\u8FD9\u4E5F\u662F\u4E1C\u6D77\u738B\u8D8A\u4EFB\u547D\u7684\u3002\u81F3\u4E8E\u5EFA\u4E1A\u6240\u5728\u7684\u626C\u5DDE\uFF0C\u523A\u53F2\u5219\u662F<strong>\u738B\u6566</strong>\u3002</p><p>\u800C\u738B\u6566\uFF0C\u5C31\u662F\u738B\u884D\u7684\u4ECE\u5F1F\u3002</p><p>\u8FD8\u5728\u6C38\u5609\u5143\u5E74\u7684\u65F6\u5019\uFF0C\u65F6\u4EFB\u53F8\u5F92\u7684\u738B\u884D\u5C31\u5411\u6267\u653F\u7684\u4E1C\u6D77\u738B\u8D8A\u8FDB\u8A00\uFF0C\u8BF4\u5982\u4ECA\u5929\u4E0B\u5927\u4E71\uFF0C\u5E94\u8BE5\u6D3E\u6587\u6B66\u517C\u5907\u7684\u91CD\u4EFB\u62C5\u5F53\u56DB\u65B9\u4E4B\u4EFB\u3002</p><p>\u8D81\u7740\u8FD9\u4E2A\u673A\u4F1A\uFF0C\u738B\u884D\u628A\u81EA\u5DF1\u7684\u5F1F\u5F1F\u4EEC\u5B89\u6392\u5F97\u5F88\u662F\u59A5\u5E16\uFF1A\u738B\u6F84\u5230\u8346\u5DDE\uFF0C\u738B\u6566\u5230\u9752\u5DDE\uFF08\u540E\u8F6C\u626C\u5DDE\uFF09\u653E\u5916\u4EFB\u3002\u81F3\u4E8E\u738B\u5BFC\uFF0C\u5F53\u65F6\u540D\u671B\u5C1A\u8F7B\uFF0C\u4E0D\u8DB3\u4EE5\u5B88\u7267\u5730\u65B9\uFF0C\u4FBF\u9001\u5230\u5B89\u4E1C\u5C06\u519B\u3001\u7405\u740A\u738B\u53F8\u9A6C\u777F\u7684\u5E55\u5E9C\u91CC\u505A\u4E86\u53F8\u9A6C\u3002</p><blockquote><p>\u7405\u740A\u738B\u53F8\u9A6C\u777F\uFF0C\u518D\u52A0\u4E0A\u738B\u6566\u3001\u738B\u5BFC\uFF0C\u5230\u8FD9\u91CC\uFF0C\u76F8\u4FE1\u4F60\u5E94\u8BE5\u5DF2\u7ECF\u770B\u51FA\u6765\u4E86\uFF0C\u8FD9\u5C31\u662F\u4E1C\u664B\u7684\u57FA\u672C\u73ED\u5E95\uFF0C\u6211\u4EEC\u4E60\u60EF\u4E0A\u79F0\u4F5C\u201C\u738B\u4E0E\u9A6C\uFF0C\u5171\u5929\u4E0B\u201D\u3002</p></blockquote><p>\u5BF9\u6B64\uFF0C\u738B\u884D\u66FE\u7ECF\u5F97\u610F\u7684\u8DDF\u738B\u6F84\u4E0E\u738B\u6566\u8BF4\uFF1A\u5982\u4ECA\u5929\u4E0B\u5C06\u4E71\uFF0C\u4F60\u4E8C\u4EBA\u4E00\u5728\u8346\u5DDE\u3001\u4E00\u5728\u9752\u5DDE\uFF0C\u6211\u5728\u671D\u4E2D\uFF0C\u6211\u4EEC\u738B\u6C0F\u53EF\u4EE5\u79F0\u5F97\u4E0A\u662F\u72E1\u5154\u4E09\u7A9F\u554A\uFF01\u54C8\u54C8\u54C8\uFF01</p><p>\u4F60\u770B\uFF0C\u5F53\u51DB\u51AC\u5C06\u81F3\uFF0C\u8FD9\u5C31\u662F\u4E00\u4E2A\u540D\u76D6\u5929\u4E0B\u7684\u671D\u5EF7\u91CD\u81E3\u7684\u771F\u5B9E\u60F3\u6CD5\u3002</p><p>\u800C\u8FD9\u4F4D\u738B\u884D\u5728\u88AB\u77F3\u52D2\u6293\u83B7\u540E\uFF0C\u8868\u73B0\u4E5F\u5B9E\u5728\u8BA9\u4EBA\u53F9\u4E3A\u89C2\u6B62\u3002\u9762\u5BF9\u7740\u77F3\u52D2\uFF0C\u4ED6\u4E0D\u505C\u7684\u8BC9\u82E6\uFF0C\u8BF4\u81EA\u5DF1\u6DF1\u9677\u5BA6\u6D77\uFF0C\u9887\u4E3A\u65E0\u5948\uFF1B\u53C8\u8BF4\u81EA\u5DF1\u4E0D\u61C2\u5B98\u573A\uFF0C\u4E0D\u95EE\u4E16\u4E8B\u3002\u5230\u6700\u540E\uFF0C\u4E3A\u4E86\u6D3B\u547D\uFF0C\u4ED6\u7ADF\u7136\u529D\u77F3\u52D2\u79F0\u5E1D\u3002</p><p>\u77F3\u52D2\u90FD\u88AB\u4ED6\u6C14\u7B11\u4E86\uFF1A\u4F60\u738B\u884D\u5C11\u5E74\u51FA\u4ED5\uFF0C\u540D\u76D6\u56DB\u6D77\uFF0C\u8EAB\u5C45\u91CD\u4EFB\uFF0C\u7ADF\u7136\u8BF4\u4E0D\u61C2\u5B98\u573A\uFF1F\u5929\u4E0B\u7CDC\u70C2\u5982\u6B64\uFF0C\u4F60\u96BE\u8F9E\u5176\u548E\uFF01</p><p>\u5F53\u591C\uFF0C\u738B\u884D\u88AB\u5175\u58EB\u4EEC\u6D3B\u57CB\u3002</p><p>\u6000\u5E1D\u4E00\u751F\u6781\u5C3D\u8C26\u5351\uFF0C\u4F46\u6700\u7EC8\u8FD8\u662F\u6CA1\u80FD\u9003\u8FC7\u52AB\u6570\uFF0C\u4E8E\u4E09\u5341\u5C81\u4FBF\u88AB\u5218\u806A\u6740\u6B7B\u3002</p><p>\u800C\u8FD9\u5E76\u4E0D\u610F\u5473\u7740\u6545\u4E8B\u7684\u7ED3\u675F\u3002</p><p>\u6000\u5E1D\u9047\u5BB3\u540E\uFF0C<strong>\u8EAB\u5728\u957F\u5B89\u7684\u7687\u592A\u5B50\u53F8\u9A6C\u90BA\u5373\u4F4D\uFF0C\u662F\u4E3A\u664B\u610D\u5E1D</strong>\u3002\u8FD9\u4E00\u5E74\uFF0C\u610D\u5E1D\u53EA\u6709\u5341\u4E09\u5C81\uFF0C\u4E14\u9762\u5BF9\u7684\u5C40\u52BF\u6BD4\u6000\u5E1D\u65F6\u66F4\u4E3A\u9669\u6076\u3002</p><p>\u5728\u5173\u4E2D\u575A\u6301\u4E86\u51E0\u5E74\u540E\uFF0C\u610D\u5E1D\u906D\u9047\u4E86\u8DDF\u6000\u5E1D\u4E00\u6837\u7684\u547D\u8FD0\uFF1A\u88AB\u4FD8\u3002</p><p>\u5218\u806A\u4F9D\u65E7\u559C\u6B22\u70AB\u8000\u81EA\u5DF1\u7684\u6210\u5C31\u611F\u3002\u51FA\u57CE\u6253\u730E\u65F6\uFF0C\u4ED6\u547D\u610D\u5E1D\u8EAB\u7740\u620E\u88C5\uFF0C\u6267\u621F\u5F00\u9053\uFF1B\u5927\u5BB4\u7FA4\u81E3\u65F6\uFF0C\u610D\u5E1D\u4E0D\u4EC5\u8981\u50CF\u6000\u5E1D\u4E00\u6837\u884C\u9152\uFF0C\u540C\u65F6\u8FD8\u591A\u4E86\u201C\u6D17\u7235\u201D\u7684\u6D3B\u2014\u2014\u7ED9\u5927\u81E3\u4EEC\u6D17\u9152\u676F\uFF0C\u6D17\u5B8C\u540E\u8FD8\u5F97\u6362\u8863\u670D\u6267\u4F1E\u76D6\u3002</p><p>\u5728\u5750\u7684\u664B\u81E3\u4EEC\u518D\u6B21\u5931\u58F0\u800C\u6CE3\u3002\u800C\u8FD9\u6837\u7684\u573A\u666F\uFF0C\u53C8\u518D\u4E00\u6B21\u60F9\u6012\u4E86\u5218\u806A\uFF0C\u610D\u5E1D\u968F\u5373\u88AB\u6740\uFF0C\u65F6\u5E74\u53EA\u6709\u5341\u516B\u5C81\u3002</p><p>\u664B\u81E3\u4EEC\u7684\u54ED\u4E27\uFF0C\u603B\u662F\u6BD4\u6B63\u5E38\u60C5\u51B5\u8981\u6765\u5F97\u65E9\u4E00\u4E9B\u3002</p>',98),g=[s];function e(c,a){return o(),t("div",null,g)}const u=p(n,[["render",e],["__file","9.html.vue"]]);export{u as default};
