import{_ as e}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as p,c as t,f as a}from"./app.f3808378.js";const c={},o=a('<h1 id="\u7B2C9\u96C6-\u88F4\u559C\u541B\u8DDF\u5362\u51CC\u98CE\u5357\u4E0B" tabindex="-1"><a class="header-anchor" href="#\u7B2C9\u96C6-\u88F4\u559C\u541B\u8DDF\u5362\u51CC\u98CE\u5357\u4E0B" aria-hidden="true">#</a> \u7B2C9\u96C6\uFF1A\u88F4\u559C\u541B\u8DDF\u5362\u51CC\u98CE\u5357\u4E0B</h1><p>\u2003\u2003\u641C\u5BFB\u4E00\u5708\uFF0C\u5362\u51CC\u98CE\u5728\u4E00\u95F4\u5C4B\u5185\u53D1\u73B0\u5218\u5341\u516B\u6B63\u5728\u711A\u716E\u4EBA\u5934\uFF0C\u800C\u711A\u716E\u7684\u4EBA\u5934\u5C31\u662F\u4E8E\u90FD\u5C09\u548C\u5D14\u65E0\u5FCC\u3002\u6628\u591C\u88F4\u559C\u541B\u4E00\u76F4\u4F4F\u5728\u5DE6\u4E0A\u623F\uFF0C\u5E76\u672A\u542C\u5230\u4EFB\u4F55\u52A8\u9759\u3002\u82CF\u65E0\u540D\u67E5\u95EE\u5218\u5341\u516B\u5982\u4F55\u6740\u4EBA\uFF0C\u4E8E\u90FD\u5C09\u7B49\u4EBA\u5C38\u8EAB\u73B0\u5728\u4F55\u5904\u3002\u5218\u5341\u516B\u8868\u793A\u4ED6\u4E4B\u6240\u4EE5\u6740\u4EBA\u5C31\u662F\u56E0\u4E3A\u4ED6\u4EEC\u6740\u4E86\u4ED6\u7684\u5C0F\u767D\uFF0C\u4E5F\u5C31\u662F\u90A3\u6761\u767D\u8272\u5DE8\u87D2\u3002</p><p>\u2003\u2003\u76F4\u5230\u6B64\u65F6\uFF0C\u5218\u5341\u516B\u624D\u77E5\u9053\u539F\u6765\u767D\u8272\u5DE8\u87D2\u662F\u5362\u51CC\u98CE\u6240\u6740\uFF0C\u4E3A\u4E86\u67E5\u5230\u5269\u4E0B\u6240\u6709\u4EBA\u7684\u8E2A\u8FF9\uFF0C\u82CF\u65E0\u540D\u8BA9\u4EBA\u653E\u5F00\u5218\u5341\u516B\uFF0C\u54EA\u77E5\u5218\u5341\u516B\u4E00\u58F0\u54E8\u97F3\u53EC\u5524\u6765\u65E0\u6570\u6761\u87D2\u86C7\u3002\u5728\u88F4\u559C\u541B\u548C\u82CF\u65E0\u540D\u7684\u529D\u8BF4\u4E0B\uFF0C\u5362\u51CC\u98CE\u5E26\u4EBA\u64A4\u79BB\uFF0C\u5C06\u5218\u5341\u516B\u4EA4\u7ED9\u82CF\u53BF\u5C09\u8C03\u67E5\u3002\u968F\u540E\uFF0C\u82CF\u65E0\u540D\u544A\u522B\u82CF\u53BF\u5C09\uFF0C\u5E76\u540C\u610F\u88F4\u559C\u541B\u8DDF\u968F\u4ED6\u4EEC\u4E00\u540C\u5357\u4E0B\u524D\u5F80\u5357\u5DDE\u8D74\u4EFB\u3002</p><p>\u2003\u2003\u51E0\u4EBA\u5B89\u6392\u4E00\u756A\u4E00\u8D77\u8D76\u8DEF\uFF0C\u6700\u7EC8\u6765\u5230\u8D74\u4EFB\u5730\u5357\u5DDE\uFF0C\u82CF\u65E0\u540D\u5E26\u7740\u4F17\u4EBA\u6765\u5230\u523A\u53F2\u5E9C\u62DC\u89C1\u5357\u5DDE\u523A\u53F2\u718A\u5343\u5E74\uFF0C\u5357\u5DDE\u523A\u53F2\u718A\u5343\u5E74\u5BF9\u4ED6\u4EEC\u5F02\u5E38\u70ED\u60C5\uFF0C\u4E0D\u4EC5\u56E0\u4E3A\u82CF\u65E0\u540D\u72C4\u516C\u5F1F\u5B50\u7684\u540D\u53F7\uFF0C\u66F4\u56E0\u4E3A\u4ED6\u662F\u516C\u4E3B\u5B89\u6392\u800C\u6765\u3002\u718A\u5343\u5E74\u8BBE\u5BB4\u6B3E\u5F85\u4F17\u4EBA\uFF0C\u8D39\u9E21\u5E08\u4E5F\u5306\u5306\u8D76\u6765\u3002\u8D39\u9E21\u5E08\u65E9\u5230\u534A\u6708\uFF0C\u5C06\u82CF\u65E0\u540D\u548C\u5362\u51CC\u98CE\u7684\u8EAB\u4EFD\u6765\u5386\u544A\u77E5\u523A\u53F2\uFF0C\u88AB\u523A\u53F2\u597D\u610F\u62DB\u5F85\u3002</p><p>\u2003\u2003\u88F4\u559C\u541B\u63A8\u8F9E\u6389\u523A\u53F2\u7684\u5BB4\u8BF7\uFF0C\u5E26\u7740\u859B\u73AF\u6765\u5230\u53F8\u9A6C\u5E9C\uFF0C\u4E3A\u8868\u8FBE\u81EA\u5DF1\u5BF9\u859B\u73AF\u7684\u8C22\u610F\uFF0C\u60F3\u8981\u4EB2\u81EA\u966A\u7740\u859B\u73AF\u5403\u7B2C\u4E00\u987F\u6765\u81EA\u5357\u5DDE\u7684\u996D\u98DF\u3002\u859B\u73AF\u8DDF\u7740\u88F4\u559C\u541B\u4E00\u8DEF\u800C\u6765\uFF0C\u89C1\u8BC6\u5230\u82CF\u65E0\u540D\u7684\u535A\u5B66\u548C\u5362\u51CC\u98CE\u7684\u5FE0\u52C7\uFF0C\u4ED6\u5DF2\u7ECF\u4E0D\u865A\u6B64\u884C\u3002\u542C\u95FB\u859B\u73AF\u4F69\u670D\u5362\u51CC\u98CE\u6B66\u827A\uFF0C\u88F4\u559C\u541B\u8868\u793A\u81EA\u5DF1\u5E2E\u4ED6\u51C6\u5907\u62DC\u5E08\u793C\u8BA9\u4ED6\u53BB\u62DC\u5362\u51CC\u98CE\u4E3A\u5E08\u5B66\u827A\u3002</p><p>\u2003\u2003\u5BB4\u5E2D\u7A7A\u9699\uFF0C\u82CF\u65E0\u540D\u6253\u542C\u6545\u4EA4\u989C\u5143\u592B\u7684\u4E0B\u843D\uFF0C\u523A\u53F2\u9762\u9732\u4E3A\u96BE\u4E4B\u8272\uFF0C\u539F\u6765\u989C\u5143\u592B\u5DF2\u7ECF\u53BB\u4E16\uFF0C\u4ECA\u65E5\u521A\u597D\u51FA\u6BA1\u3002\u4E03\u65E5\u524D\u989C\u5143\u592B\u75C5\u6545\uFF0C\u4ECA\u65E5\u5357\u5DDE\u4E09\u5B50\u4E3A\u5176\u53D1\u4E27\u3002\u8336\u9053\u9AD8\u58EB\u949F\u4F2F\u671F\u4E3A\u5176\u6276\u68FA\uFF0C\u53E4\u7434\u5723\u624B\u8DEF\u516C\u590D\u4E3A\u5176\u5F39\u7434\uFF0C\u8BD7\u4EBA\u51B7\u7C4D\u4E3A\u5176\u6492\u7EB8\u94B1\u3002</p><p>\u2003\u2003\u7434\u5E08\u6797\u5B9D\u62E6\u8DEF\u60F3\u8981\u62DC\u8DEF\u516C\u590D\u4E3A\u5E08\uFF0C\u4ED6\u5DF2\u7ECF\u662F\u7B2C\u4E5D\u6B21\u62DC\u5E08\uFF0C\u53EF\u5982\u6B64\u60C5\u666F\uFF0C\u8DEF\u516C\u590D\u600E\u4F1A\u7B54\u5E94\u6536\u5F92\uFF0C\u547D\u4EBA\u5C06\u5176\u8D76\u8D70\u3002\u8DEF\u516C\u590D\u544A\u8BC9\u6797\u5B9D\uFF0C\u81EA\u5DF1\u4E4B\u6240\u4EE5\u4E0D\u613F\u6536\u4ED6\u4E3A\u5F92\uFF0C\u4E0D\u662F\u56E0\u4E3A\u4ED6\u66FE\u5728\u9752\u697C\u5F39\u594F\uFF0C\u800C\u662F\u56E0\u4E3A\u4ED6\u6BEB\u65E0\u5929\u8D4B\u53EF\u8A00\u3002\u6797\u5B9D\u5F53\u4F17\u906D\u53D7\u5982\u6B64\u5927\u8FB1\uFF0C\u6000\u6068\u5728\u5FC3\uFF0C\u4E0B\u51B3\u5FC3\u5FC5\u62A5\u6B64\u4EC7\u3002</p><p>\u2003\u2003\u82CF\u65E0\u540D\u8DDF\u968F\u523A\u53F2\u524D\u6765\uFF0C\u60F3\u8981\u9001\u6545\u4EA4\u4E00\u7A0B\u3002\u4ED6\u4E0E\u989C\u5143\u592B\u5728\u957F\u5B89\u4E00\u522B\uFF0C\u54EA\u60F3\u5230\u5DF2\u7ECF\u6210\u4E3A\u6C38\u522B\u3002\u523A\u53F2\u8868\u793A\uFF0C\u4ED6\u5F53\u521D\u542C\u989C\u5143\u592B\u81EA\u8C6A\u611F\u6168\uFF0C\u4E4B\u6240\u4EE5\u5F03\u957F\u5B89\u56DE\u5357\u5DDE\uFF0C\u5C31\u662F\u56E0\u4E3A\u5357\u5DDE\u56DB\u5B50\u540C\u8FDB\u540C\u9000\u3001\u7F3A\u4E00\u4E0D\u53EF\u3002\u6709\u4EBA\u8FD8\u5C06\u5357\u5DDE\u56DB\u5B50\u7ED8\u5165\u753B\u4E2D\uFF0C\u6B64\u753B\u540D\u4E3A\u201C\u77F3\u6865\u56FE\u201D\u3002\u201C\u77F3\u6865\u56FE\u201D\u88AB\u9152\u697C\u4E3B\u4EBA\u6B27\u9633\u6CC9\u6536\u85CF\u3002</p><p>\u2003\u2003\u5357\u5DDE\u5546\u4EBA\u6B27\u9633\u6CC9\u4E00\u76F4\u89C6\u989C\u5143\u592B\u4E3A\u77E5\u5DF1\uFF0C\u4E00\u76F4\u60F3\u4EE5\u9152\u4ED9\u4E4B\u540D\u52A0\u5165\u5357\u5DDE\u56DB\u5B50\uFF0C\u53EF\u4E00\u76F4\u672A\u80FD\u5B9E\u73B0\u6240\u613F\uFF0C\u5982\u4ECA\u53EA\u597D\u5728\u5BB6\u4E2D\u796D\u62DC\u989C\u5143\u592B\u3002\u9646\u79BB\u4E00\u76F4\u60F3\u8981\u8D2D\u4E70\u201C\u77F3\u6865\u56FE\u201D\uFF0C\u53EF\u4E00\u76F4\u4E5F\u88AB\u6B27\u9633\u6CC9\u62D2\u7EDD\u3002\u9646\u79BB\u8BA4\u4E3A\u5357\u5DDE\u53EA\u6709\u4E00\u4EF6\u534A\u5B9D\u8D1D\uFF0C\u201C\u77F3\u6865\u56FE\u201D\u7B97\u4E00\u4EF6\u5B9D\u8D1D\uFF0C\u53E6\u5916\u534A\u4EF6\u662F\u8DEF\u516C\u590D\u624B\u4E0A\u7684\u4E09\u56FD\u53E4\u7434\uFF0C\u6BD5\u7ADF\u53E4\u7434\u4E0E\u8DEF\u516C\u590D\u76F8\u6620\u751F\u8F89\uFF0C\u53EA\u80FD\u7B97\u534A\u4EF6\u5B9D\u8D1D\u3002</p><p>\u2003\u2003\u88F4\u559C\u541B\u5C06\u6240\u753B\u7684\u82CF\u65E0\u540D\u548C\u5362\u51CC\u98CE\u80CC\u5F71\u753B\u50CF\u9001\u7ED9\u4ED6\u4EEC\uFF0C\u82CF\u65E0\u540D\u5BF9\u6B64\u753B\u8D5E\u4E0D\u7EDD\u53E3\u3002\u88F4\u559C\u541B\u968F\u540E\u6311\u9009\u4E86\u914D\u5200\u9001\u7ED9\u5362\u51CC\u98CE\uFF0C\u5362\u51CC\u98CE\u672C\u4E0D\u613F\u63A5\u53D7\u6B64\u793C\u7269\uFF0C\u5728\u8D39\u9E21\u5E08\u548C\u82CF\u65E0\u540D\u7684\u4E00\u756A\u8A00\u8BBA\u8C03\u4F83\u4E0B\uFF0C\u53EA\u597D\u6682\u65F6\u6536\u4E0B\u793C\u7269\u3002\u591C\u665A\u5362\u51CC\u98CE\u62FF\u51FA\u914D\u5200\u4E60\u7EC3\u6B66\u827A\uFF0C\u859B\u73AF\u5728\u4E00\u4FA7\u5077\u770B\u88AB\u53D1\u73B0\uFF0C\u5362\u51CC\u98CE\u8BE2\u95EE\u624D\u77E5\u5BF9\u65B9\u60F3\u8981\u5B66\u6B66\uFF0C\u53EF\u8BA4\u4E3A\u4ED6\u6CA1\u6709\u5927\u62B1\u8D1F\u4E0D\u914D\u62DC\u81EA\u5DF1\u4E3A\u5E08\u3002\u859B\u73AF\u5374\u8868\u793A\u597D\u9AD8\u9A9B\u8FDC\u8005\u6216\u8BB8\u4E00\u8F88\u5B50\u90FD\u662F\u5E9F\u7269\uFF0C\u80FD\u968F\u9047\u800C\u5B89\u7684\u4EBA\u624D\u662F\u4E86\u4E0D\u8D77\u7684\u4EBA\u3002\u859B\u73AF\u6307\u8D23\u5362\u51CC\u98CE\u4E3A\u4EBA\u9AD8\u50B2\uFF0C\u770B\u4E0D\u8D77\u5357\u5DDE\u7684\u4E00\u5207\uFF0C\u7A7A\u6709\u62B1\u8D1F\u53C8\u6709\u4F55\u7528\u3002\u859B\u73AF\u6B64\u8A00\u8BA9\u5362\u51CC\u98CE\u522E\u76EE\u76F8\u770B\uFF0C\u51B3\u5B9A\u6536\u4ED6\u4E3A\u5F92\u6559\u6388\u4ED6\u6B66\u827A\u3002</p><p>\u2003\u2003\u523A\u53F2\u718A\u5343\u5E74\u56E0\u4E3A\u5362\u51CC\u98CE\u7684\u8EAB\u4EFD\u4E3A\u96BE\uFF0C\u4E0D\u77E5\u8BE5\u5B89\u6392\u4ED6\u4EC0\u4E48\u516C\u52A1\uFF0C\u82CF\u65E0\u540D\u8868\u793A\u5362\u51CC\u98CE\u8DDF\u968F\u81EA\u5DF1\u800C\u6765\uFF0C\u5C31\u662F\u4E3A\u4E86\u78E8\u70BC\uFF0C\u8BA9\u523A\u53F2\u968F\u610F\u5B89\u6392\u3002\u523A\u53F2\u8868\u793A\u5357\u5DDE\u76EE\u524D\u6355\u8D3C\u7684\u53F8\u6CD5\u53C2\u519B\u6B63\u597D\u7A7A\u7F3A\uFF0C\u53EF\u4EE5\u8BA9\u5362\u51CC\u98CE\u6682\u4EE3\u8BE5\u804C\u3002\u5362\u51CC\u98CE\u62C5\u5FE7\u81EA\u5DF1\u6234\u7F6A\u4E4B\u8EAB\u88AB\u53D1\u73B0\u540E\u8FDE\u7D2F\u523A\u53F2\uFF0C\u82CF\u65E0\u540D\u4EE5\u6848\u5377\u79EF\u538B\u592A\u591A\uFF0C\u4E0D\u53EF\u4E00\u65E5\u65E0\u4EBA\u4E3A\u7531\uFF0C\u8BA9\u5362\u51CC\u98CE\u6682\u4EE3\uFF0C\u5E76\u8868\u793A\u81EA\u5DF1\u613F\u610F\u627F\u62C5\u4E4B\u540E\u7684\u8D23\u4EFB\u3002\u5362\u51CC\u98CE\u7B54\u5E94\u4EFB\u804C\uFF0C\u4F46\u5E0C\u671B\u82CF\u65E0\u540D\u80FD\u9001\u88F4\u559C\u541B\u56DE\u957F\u5B89\u3002</p>',11),r=[o];function _(n,s){return p(),t("div",null,r)}const h=e(c,[["render",_],["__file","9.html.vue"]]);export{h as default};
