import{_ as p}from"./_plugin-vue_export-helper.cdc0426e.js";import{o,c as t,f as e}from"./app.1218f0f6.js";const c={},r=e('<h1 id="\u8D3E\u8BE9-\u4E0E-\u6768\u4FEE" tabindex="-1"><a class="header-anchor" href="#\u8D3E\u8BE9-\u4E0E-\u6768\u4FEE" aria-hidden="true">#</a> \u8D3E\u8BE9 \u4E0E \u6768\u4FEE</h1><blockquote><p>\u8BA8\u864F\u6821\u5C09\u6B66\u5A01\u8D3E\u8BE9\u66F0\uFF1A\u201C\u8BF8\u541B\u82E5\u5F03\u519B\u5355\u884C\uFF0C\u5219\u4E00\u4EAD\u957F\u80FD\u675F\u541B\u77E3\u3002\u4E0D\u5982\u76F8\u7387\u800C\u897F\uFF0C\u4EE5\u653B\u957F\u5B89\uFF0C\u4E3A\u8463\u516C\u62A5\u4EC7\u3002\u4E8B\u6D4E\uFF0C\u5949\u56FD\u5BB6\u4EE5\u6B63\u5929\u4E0B\uFF1B\u82E5\u5176\u4E0D\u5408\uFF0C\u8D70\u672A\u540E\u4E5F\u3002\u201D</p></blockquote><p><strong>\u300A\u8D44\u6CBB\u901A\u9274 \xB7 \u5377\u516D\u5341 \xB7 \u6C49\u7EAA\u4E94\u5341\u4E8C\u300B</strong></p><p>\u6211\u4EEC\u5728\u5B66\u300A\u6768\u4FEE\u4E4B\u6B7B\u300B\u65F6\uFF0C\u8BFE\u672C\u91CC\u66FE\u7ECF\u7ED9\u8FC7\u4E00\u4E2A\u6807\u51C6\u8BC4\u4EF7\uFF0C\u8BF4\u66F9\u64CD\u8FD9\u662F\u5AC9\u8D24\u5992\u80FD\uFF0C\u5AC9\u5992\u6768\u4FEE\u6BD4\u4ED6\u806A\u660E\u3002\u6D41\u5E74\u541B\u5F53\u65F6\u5E74\u9F7F\u5C1A\u5E7C\uFF0C\u4E8E\u662F\u4E5F\u5C31\u89C9\u5F97\uFF1A\u66F9\u64CD\u8FD9\u4E2A\u4EBA\uFF0C\u771F\u662F\u9F20\u809A\u9E21\u80A0\u554A\uFF01\u76F4\u5230\u5E74\u7EAA\u7A0D\u957F\u4EE5\u540E\uFF0C\u624D\u6162\u6162\u611F\u89C9\u5230\uFF1A\u6768\u4FEE\u8FD9\u4E2A\u4EBA\uFF0C\u5B9E\u5728\u662F\u592A\u4E48\u65E0\u804A\u900F\u9876\u4E86\u3002</p><blockquote><p>\u66F9\u64CD\u5728\u5927\u95E8\u91CC\u5199\u4E2A\u201C\u6D3B\u201D\u5B57\uFF0C\u5DE5\u5320\u4EEC\u4E0D\u61C2\u5411\u4F60\u8BF7\u6559\uFF0C\u4F60\u5E76\u6CA1\u6709\u4E00\u5B9A\u8981\u56DE\u7B54\u7684\u9053\u7406\uFF0C\u4F55\u5FC5\u4E8E\u53BB\u63ED\u7A7F\u66F9\u64CD\u7684\u628A\u620F\uFF1F</p></blockquote><blockquote><p>\u66F9\u64CD\u5728\u76D2\u5B50\u4E0A\u5199\u4E86\u201C\u4E00\u5408\u9165\u201D\u4E09\u4E2A\u5927\u5B57\uFF0C\u4F60\u7BA1\u4ED6\u4EC0\u4E48\u610F\u601D\uFF0C\u4F55\u81F3\u4E8E\u76F4\u63A5\u5C31\u62FF\u8FC7\u6765\u5206\u7ED9\u5927\u5BB6\u5403\u5462\uFF1F\u5403\u5B8C\u4E86\u8FD8\u632F\u632F\u6709\u8BCD\uFF0C\u8BF4\u4E0A\u9762\u5199\u7684\u662F\u201C\u4E00\u4EBA\u4E00\u53E3\u9165\u201D\uFF0C\u8FD9\u79CD\u884C\u4E3A\u7684\u76EE\u7684\u53C8\u662F\u4EC0\u4E48\uFF1F</p></blockquote><blockquote><p>\u66F9\u64CD\u6015\u6709\u4EBA\u6697\u5BB3\u4ED6\uFF0C\u8C0E\u79F0\u4F1A\u201C\u68A6\u4E2D\u6740\u4EBA\u201D\uFF0C\u4F60\u504F\u504F\u8981\u63ED\u7A7F\uFF0C\u4F60\u8FD9\u65F6\u5019\u53C8\u662F\u5728\u8868\u8FBE\u4E00\u4E2A\u4EC0\u4E48\u6837\u7684\u8BC9\u6C42\u5462\uFF1F</p></blockquote><p>\u4E8E\u662F\u76F4\u5230<code>\u201C\u9E21\u808B\u201D\u4E8B\u4EF6</code>\uFF0C\u66F9\u64CD\u7EC8\u4E8E\u5FCD\u65E0\u53EF\u5FCD\uFF0C\u5904\u7406\u4E86\u6768\u4FEE\u3002\u800C\u4E14\uFF0C\u7406\u7531\u8FD8\u6B63\u5927\u5149\u660E\uFF1A\u519B\u961F\u8FD8\u5728\u6253\u4ED7\uFF0C\u4F60\u5C31\u5F00\u59CB\u6536\u62FE\u884C\u674E\uFF0C\u5E76\u56DB\u5904\u6563\u64AD\u5373\u5C06\u64A4\u5175\u7684\u6D88\u606F\uFF01\u8FD9\u79CD\u884C\u4E3A\uFF0C\u4E0A\u53F8\u54EA\u6015\u5C31\u662F\u8DDF\u4F60\u6CA1\u6709\u8FC7\u8282\uFF0C\u4E5F\u4F1A\u6740\u4E4B\u3002</p><p>\u4ECA\u5929\uFF0C\u6211\u4EEC\u5C31\u6765\u770B\u770B\u53E6\u4E00\u4E2A<strong>\u5728\u66F9\u64CD\u9762\u524D\u5145\u5206\u65BD\u5C55\u4E86\u624D\u667A\uFF0C\u5374\u4ECE\u6765\u6CA1\u6709\u88AB\u66F9\u64CD\u5AC9\u6068\u8FC7\u7684\u806A\u660E\u4EBA\u2014\u2014\u8D3E\u8BE9\u3002</strong><code>\u8FD9\u662F\u4E00\u4E2A\u4E09\u56FD\u65F6\u671F\u667A\u529B\u7B2C\u4E00\u6863\u4E2D\u7684\u4E00\u6D41\u4EBA\u7269\u3002</code></p><p>\u8D3E\u8BE9\u56E0\u8463\u5353\u4E4B\u6B7B\u800C\u53D1\u8FF9\u3002\u8463\u5353\u6B7B\u540E\uFF0C\u6709\u6D41\u8A00\u8BF4\u738B\u5141\u8981\u628A\u6240\u6709\u7684\u51C9\u5DDE\u4EBA\u90FD\u6740\u5149\uFF0C\u4E00\u65F6\u4EBA\u5FC3\u60F6\u60F6\u3002\u674E\u5095\u7B49\u8463\u5353\u65E7\u90E8\u5411\u738B\u5141\u6C42\u9976\uFF0C\u8BF7\u6C42\u8D66\u514D\uFF0C\u6CA1\u60F3\u5230\u738B\u5141\u6BEB\u4E0D\u5BA2\u6C14\u5730\u56DE\u7EDD\u4E86\uFF1A\u4ECA\u5E74\u6740\u4E86\u8463\u5353\uFF0C\u666E\u5929\u540C\u5E86\uFF0C\u5DF2\u7ECF\u5927\u8D66\u5929\u4E0B\u4E86\uFF0C\u4F60\u4EEC\u5E76\u4E0D\u5728\u8D66\u514D\u4E4B\u5217\uFF0C\u800C\u4E14\u4E5F\u4E0D\u4F1A\u6709\u7B2C\u4E8C\u6B21\u5927\u8D66\u4E86\uFF01</p><p>\u8FD9\u4E2A\u56DE\u590D\u5B9E\u5728\u662F\u8BA9\u4EBA\u7EDD\u671B\u3002\u674E\u5095\u7B49\u51E0\u4E2A\u9AD8\u7EA7\u5C06\u9886\u4E00\u756A\u5546\u91CF\uFF0C\u51C6\u5907\u5C31\u5730\u6563\u4F19\uFF0C\u5404\u56DE\u5404\u5BB6\u5404\u627E\u5404\u5988\u3002</p><p>\u8D3E\u8BE9\u4E5F\u662F\u51C9\u5DDE\u4EBA\uFF0C\u5F53\u65F6\u505A\u7740\u4E00\u4E2A\u4E0D\u5927\u4E0D\u5C0F\u7684\u6B66\u5B98\u3002\u773C\u89C1\u7740\u4EBA\u5FC3\u6563\u4E86\uFF0C\u81EA\u5DF1\u53EF\u80FD\u4E5F\u4F1A\u6709\u6740\u8EAB\u4E4B\u7978\uFF0C\u4FBF\u8DD1\u8FC7\u53BB\u5411\u674E\u5095\u8FDB\u8A00\uFF0C\u8BF4\u4E0D\u80FD\u6563\u554A\uFF01\u73B0\u5728\u671D\u5EF7\u5BF9\u6211\u4EEC\u8FD8\u6CA1\u6709\u4E0B\u624B\uFF0C\u5C31\u662F\u56E0\u4E3A\u90E8\u961F\u8FD8\u5728\uFF0C\u5403\u4E0D\u52A8\u6211\u4EEC\u3002\u4E00\u65E6\u6563\u4E86\uFF0C\u90A3\u5230\u65F6\u5019\u6D3E\u4E2A\u4EAD\u957F\u5C31\u80FD\u6765\u6740\u6211\u4EEC\u4E86\uFF01</p><p>\u674E\u5095\u4E00\u542C\uFF0C\u89C9\u5F97\u5F88\u6709\u9053\u7406\uFF0C\u5C31\u7EE7\u7EED\u542C\u7740\u8D3E\u8BE9\u5FFD\u60A0\uFF1A\u6211\u4EEC\u4E0D\u5982\u4E00\u8D77\u53BB\u653B\u6253\u957F\u5B89\uFF0C\u4E3A\u8463\u5353\u62A5\u4EC7\u5427\uFF01\u6210\u529F\u4E86\uFF0C\u5C31\u8FCE\u5949\u6C49\u732E\u5E1D\uFF0C\u505A\u56FD\u5BB6\u7684\u5FE0\u76F4\u5927\u81E3\uFF1B\u5931\u8D25\u4E86\uFF0C\u5230\u65F6\u5019\u518D\u6765\u8BF4\u6563\u4F19\uFF0C\u4E5F\u4E0D\u8FDF\u3002</p><p>\u5E73\u5FC3\u800C\u8BBA\uFF0C\u8FD9\u4E2A\u8BA1\u7B56\u7684\u6C34\u5E73\u5F88\u662F\u4E00\u822C\uFF0C\u4F46\u95EE\u9898\u662F\u674E\u5095\u613F\u610F\u76F8\u4FE1\u554A\u3002\u53EA\u8981\u674E\u5095\u76F8\u4FE1\u4E86\uFF0C\u90E8\u961F\u5C31\u4E0D\u81F3\u4E8E\u6563\u4F19\u3002\u679C\u7136\uFF0C\u674E\u5095\u5E26\u7740\u519B\u961F\u4E00\u8DEF\u5411\u897F\uFF0C\u7ADF\u7136\u771F\u7684\u653B\u4E0B\u4E86\u957F\u5B89\uFF0C\u6740\u4E86\u738B\u5141\uFF0C\u8D76\u8DD1\u4E86\u5415\u5E03\u3002</p><p>\u4E8B\u540E\uFF0C\u674E\u5095\u8BBA\u529F\u884C\u8D4F\uFF0C\u8D3E\u8BE9\u5BF9\u4E8E\u9AD8\u5B98\u5C01\u7235\u4E00\u6982\u63A8\u8F9E\uFF0C\u53EA\u63A5\u53D7\u4E86\u5C1A\u4E66\u7684\u804C\u4F4D\u3002\u7528\u4ED6\u81EA\u5DF1\u7684\u8BDD\u6765\u8BF4\uFF0C\u8FD9\u53EA\u662F\u4E00\u6761\u6551\u547D\u4E4B\u8BA1\uFF0C\u54EA\u6709\u56E0\u6B64\u5347\u5B98\u53D1\u8D22\u7684\u9053\u7406\u3002</p><p>\u6B64\u65F6\u7684\u957F\u5B89\u57CE\u5DF2\u7ECF\u4E71\u6210\u4E86\u4E00\u9505\u7CA5\uFF0C\u674E\u5095\u548C\u90ED\u6C5C\u4E92\u76F8\u770B\u4E0D\u987A\u773C\uFF0C\u4E3E\u5175\u76F8\u4E89\uFF0C\u751A\u81F3\u4E8E\u6263\u62BC\u767E\u5B98\u4EE5\u4E3A\u4EBA\u8D28\u3002\u4E0D\u8FC7\u4E24\u4EBA\u5BF9\u8D3E\u8BE9\u5012\u90FD\u8FD8\u5356\u70B9\u9762\u5B50\u3002\u800C\u8D3E\u8BE9\u5462\uFF0C\u5BF9\u6C49\u732E\u5E1D\u53C8\u591A\u65B9\u56DE\u62A4\uFF0C\u6240\u4EE5\u5728\u671D\u5EF7\u516C\u537F\u770B\u6765\uFF0C<strong>\u8D3E\u8BE9\u5927\u6982\u662F\u5F53\u65F6\u552F\u4E00\u7684\u4E00\u4E2A\u51C9\u5DDE\u597D\u4EBA\u4E86\u3002</strong></p><p>\u6CA1\u591A\u4E45\uFF0C\u56E0\u4E3A\u6BCD\u4EB2\u75C5\u901D\uFF0C\u8D3E\u8BE9\u56DE\u5BB6\u5954\u4E27\u3002\u5728\u8FD9\u671F\u95F4\uFF0C\u5929\u4E0B\u5C40\u52BF\u9762\u76EE\u5168\u975E\uFF0C\u674E\u5095\u90ED\u6C5C\u56E0\u76F8\u4E89\u52BF\u529B\u6E10\u5F31\uFF0C\u6700\u7EC8\u88AB\u6740\uFF1B\u66F9\u64CD\u8FCE\u6C49\u732E\u5E1D\u4E8E\u8BB8\u660C\uFF0C\u4ECE\u6B64\u5F00\u59CB\u540D\u6B63\u8A00\u987A\u5730\u53D1\u53F7\u65BD\u4EE4\u3002</p><p>\u8EAB\u4E3A\u51C9\u5DDE\u4EBA\u7684\u8D3E\u8BE9\u4E3E\u76EE\u65E0\u4EB2\uFF0C\u6700\u540E\u53EA\u5F97\u6295\u9760\u4E86\u5357\u9633\u519B\u9600\u5F20\u7EE3\u3002\u8D3E\u8BE9\u548C\u5F20\u7EE3\u7684\u5173\u7CFB\u5176\u5B9E\u9887\u4E3A\u758F\u8FDC\u2014\u2014\u5F20\u7EE3\u7684\u90E8\u961F\uFF0C\u662F\u53D4\u53D4\u5F20\u6D4E\u7559\u7ED9\u4ED6\u7684\u3002\u800C\u5F20\u6D4E\uFF0C\u5219\u662F\u8463\u5353\u7684\u65E7\u90E8\u3002</p><p>\u5BF9\u5F20\u7EE3\uFF0C\u8D3E\u8BE9\u732E\u51FA\u7684\u6700\u91CD\u8981\u7684\u4E00\u6761\u8BA1\u7B56\u662F\u5728\u5B98\u6E21\u4E4B\u6218\u4E2D\uFF0C\u8BA9\u5F20\u7EE3\u6295\u9760\u66F9\u64CD\u2014\u2014\u8FD9\u91CC\u9762\uFF0C\u6709\u4E00\u4E2A\u5F88\u66F2\u6298\u7684\u6545\u4E8B\u3002</p><blockquote><p>\u5F20\u7EE3\u4E4B\u524D\u662F\u6295\u9760\u8FC7\u66F9\u64CD\u7684\uFF0C\u53EA\u4E0D\u8FC7\u6CA1\u51E0\u5929\u5C31\u53CD\u6C34\u4E86\u3002\u5F53\u7136\uFF0C\u539F\u56E0\u51FA\u5728\u66F9\u64CD\u8EAB\u4E0A\uFF1A\u66F9\u64CD\u559C\u6B22\u4EBA\u59BB\uFF0C\u5728\u5357\u9633\u671F\u95F4\uFF0C\u5C31\u60F3\u8BA9\u5F20\u7EE3\u5BE1\u5C45\u7684\u5A76\u6BCD\uFF0C\u4E5F\u5C31\u662F\u5F20\u6D4E\u7684\u8001\u5A46\u6765\u966A\u4ED6\uFF0C\u4E0D\u77E5\u4E0D\u89C9\u4E2D\u5360\u4E86\u5F20\u7EE3\u4E00\u4E2A\u5927\u4FBF\u5B9C\u3002</p></blockquote><blockquote><p>\u5F20\u7EE3\u6012\u4E0D\u53EF\u904F\uFF0C\u6124\u7136\u9020\u53CD\u3002\u6700\u540E\uFF0C\u66F9\u64CD\u53EA\u80FD\u7070\u6E9C\u6E9C\u5730\u9003\u8D70\uFF0C\u800C\u4E14\u8FD8\u642D\u4E0A\u4E86\u957F\u5B50\u66F9\u6602\u3001\u4F84\u5B50\u66F9\u5B89\u6C11\u3001\u5927\u5C06\u5178\u97E6\u4E09\u6761\u4EBA\u547D\u3002</p></blockquote><p>\u6240\u4EE5\u73B0\u5728\u8BA9\u5F20\u7EE3\u518D\u6295\u5954\u66F9\u64CD\uFF0C\u4ED6\u662F\u5F88\u6709\u5FC3\u91CC\u969C\u788D\u7684\u3002\u4E00\u6765\u81EA\u5DF1\u5DEE\u70B9\u5F04\u6B7B\u4E86\u66F9\u64CD\uFF1B\u4E8C\u6765\u66F9\u64CD\u7684\u6B63\u59BB\u4E01\u6C0F\uFF0C\u56E0\u4E3A\u8001\u516C\u5AD6\u5A3C\u5BB3\u6B7B\u513F\u5B50\u7684\u4E8B\uFF0C\u5DF2\u7ECF\u548C\u66F9\u64CD\u79BB\u5A5A\u4E86\u3002\u8FD9\u79CD\u65F6\u5019\u8FD8\u8DD1\u8FC7\u53BB\u4F9D\u9644\u66F9\u64CD\uFF0C\u641E\u4E0D\u597D\u4E0D\u5C31\u7F8A\u5165\u864E\u53E3\u4E86\u5417\uFF1F</p><p>\u5F53\u65F6\uFF0C\u8881\u7ECD\u548C\u66F9\u64CD\u6D3E\u51FA\u7684\u4F7F\u8005\u90FD\u5230\u4E86\u5357\u9633\uFF0C\u7531\u8D3E\u8BE9\u8D1F\u8D23\u63A5\u5F85\u3002\u8D3E\u8BE9\u5F88\u793C\u8C8C\u5730\u628A\u66F9\u64CD\u7684\u4F7F\u8005\u5B89\u987F\u4E86\u4E0B\u6765\uFF0C\u7136\u540E\uFF0C\u8F6C\u8EAB\u8DDF\u8881\u7ECD\u7684\u4F7F\u8005\u8BF4\uFF1A\u4F60\u4E2B\u6EDA\u56DE\u53BB\u5427\uFF0C\u8FDE\u81EA\u5DF1\u5144\u5F1F\u90FD\u5BB9\u4E0D\u4E0B\u7684\u4EBA\uFF0C\u52B3\u8D44\u770B\u4E0D\u4E0A\uFF01</p><p>\u5F20\u7EE3\u5DEE\u70B9\u88AB\u5413\u6B7B\u3002\u8D3E\u8BE9\u5374\u4E0D\u614C\u4E0D\u5FD9\u5730\u7ED9\u4ED6\u89E3\u91CA\u5230\uFF1A<strong>\u9996\u5148</strong>\uFF0C\u66F9\u64CD\u5949\u5929\u5B50\u4EE4\u8BF8\u4FAF\uFF0C\u6211\u4EEC\u5E94\u8BE5\u542C\u4ED6\u7684\uFF1B<strong>\u5176\u6B21</strong>\uFF0C\u8881\u7ECD\u52BF\u5927\uFF0C\u6211\u4EEC\u6295\u5954\u4ED6\uFF0C\u4E8E\u4ED6\u800C\u8A00\u53EA\u662F\u9526\u4E0A\u6DFB\u82B1\uFF0C\u4F46\u82E5\u662F\u6295\u9760\u66F9\u64CD\uFF0C\u90A3\u5C31\u662F\u96EA\u4E2D\u9001\u70AD\u4E86\uFF1B<strong>\u7B2C\u4E09</strong>\uFF0C\u66F9\u64CD\u662F\u60F3\u4E89\u593A\u5929\u4E0B\u7684\u4EBA\uFF0C\u5C06\u519B\u6295\u9760\u4E86\u4ED6\u4EE5\u540E\uFF0C\u4ED6\u5FC5\u5B9A\u4F1A\u539A\u5F85\u5C06\u519B\uFF0C\u56E0\u4E3A\u53EA\u6709\u8FD9\u6837\u624D\u80FD\u663E\u5F97\u51FA\u4ED6\u7684\u5BBD\u5B8F\u5927\u91CF\u3002</p><p>\u6700\u7EC8\uFF0C\u4E0E\u5176\u8BF4\u5F20\u7EE3\u662F\u88AB\u8BF4\u670D\u4E86\uFF0C\u8FD8\u4E0D\u5982\u8BF4\u662F\u56E0\u4E3A\u5DF2\u7ECF\u6CA1\u6709\u5176\u4ED6\u9009\u9879\u4E86\uFF0C\u5F20\u7EE3\u6700\u7EC8\u53EA\u80FD\u6295\u9760\u4E86\u66F9\u64CD\u3002\u800C\u66F9\u64CD\u5BF9\u5F85\u5F20\u7EE3\u7684\u6001\u5EA6\uFF0C\u4E5F\u679C\u7136\u5982\u8D3E\u8BE9\u6240\u6599\u3002</p><p>\u6295\u5954\u66F9\u64CD\u540E\uFF0C\u8D3E\u8BE9\u8868\u73B0\u5F97\u76F8\u5F53\u4F4E\u8C03\u3002\u5728\u300A\u4E09\u56FD\u5FD7\xB7\u8D3E\u8BE9\u4F20\u300B\u4E2D\uFF0C\u4E00\u5171\u53EA\u8BB0\u5F55\u4E86\u8D3E\u8BE9\u4E3A\u66F9\u64CD\u51FA\u7684\u4E09\u6761\u8BA1\u7B56\uFF1A</p><p>\u7B2C\u4E00\u662F\u529D\u8BEB\uFF0C\u529D\u66F9\u64CD\u4E0D\u8981\u5357\u5F81\u8346\u5DDE\uFF0C\u7ED3\u679C\u66F9\u64CD\u4E0D\u542C\uFF0C\u6700\u7EC8\u8D25\u4E8E\u8D64\u58C1\uFF1B</p><p>\u7B2C\u4E8C\u662F\u66F9\u64CD\u5728\u897F\u5F81\u97E9\u9042\u9A6C\u8D85\u65F6\uFF0C\u5199\u4FE1\u95EE\u8BA1\u4E8E\u8D3E\u8BE9\u3002\u8D3E\u8BE9\u5EFA\u8BAE\u79BB\u95F4\u97E9\u9A6C\u4E8C\u4EBA\uFF0C\u66F9\u64CD\u4F9D\u8BA1\u65BD\u884C\uFF0C\u679C\u7136\u4E8B\u534A\u529F\u500D\u3002</p><p>\u7B2C\u4E09\u6B21\u5219\u662F\u66F9\u64CD\u5728\u7ACB\u50A8\u95EE\u9898\u4E0A\u72B9\u8C6B\u4E0D\u51B3\uFF0C\u54A8\u8BE2\u8D3E\u8BE9\u7684\u610F\u89C1\u3002\u8D3E\u8BE9\u6C89\u9ED8\u4E0D\u8BED\uFF0C\u66F9\u64CD\u5F88\u662F\u5947\u602A\uFF0C\u95EE\u8D3E\u8BE9\u4E3A\u4EC0\u4E48\u4E0D\u8BF4\u8BDD\uFF0C\u8D3E\u8BE9\u8BF4\u6211\u5728\u60F3\u8881\u7ECD\u548C\u5218\u8868\u7684\u4E8B\u3002\u66F9\u64CD\u542C\u7F62\uFF0C\u54C8\u54C8\u5927\u7B11\uFF0C\u6700\u7EC8\u7ACB\u66F9\u4E15\u4E3A\u55E3\u5B50\uFF08\u66F9\u4E15\u79F0\u5E1D\u540E\uFF0C\u916C\u529F\u5C01\u8D3E\u8BE9\u4E3A\u4E09\u516C\u4E4B\u9996\u7684\u592A\u5C09\uFF09\u3002</p><p>\u4ECE\u8FD9\u4E09\u4EF6\u4E8B\u6765\u770B\uFF0C\u66F9\u64CD\u5BF9\u8D3E\u8BE9\u662F\u9887\u4E3A\u4FE1\u4EFB\u7684\u3002\u4ED6\u56E0\u4F55\u80FD\u5F97\u5230\u66F9\u64CD\u7684\u4FE1\u4EFB\uFF0C\u53F2\u4E66\u5E76\u672A\u660E\u8A00\uFF0C\u4F46\u901A\u8FC7\u4E00\u4E9B\u7EC6\u679D\u672B\u8282\u7684\u63CF\u5199\uFF0C\u6211\u4EEC\u6216\u8BB8\u80FD\u7AA5\u5F97\u5230\u5176\u4E2D\u7684\u4E00\u4E8C\u95E8\u9053\u3002</p><blockquote><p>\u6768\u4FEE\u548C\u8D3E\u8BE9\uFF0C\u90FD\u901A\u8FC7\u5177\u4F53\u7684\u4E8B\u8FF9\u5C55\u73B0\u4E86\u81EA\u5DF1\u7684\u9AD8\u667A\u5546\uFF0C\u4F46\u4E24\u4EBA\u884C\u4E8B\u4E4B\u95F4\uFF0C\u98CE\u683C\u5DEE\u522B\u6781\u5927\u3002</p></blockquote><p><strong>\u9996\u5148</strong>\uFF0C\u8D3E\u8BE9\u7684\u806A\u660E\uFF0C\u90FD\u7528\u5728\u4E86\u89E3\u51B3\u5B9E\u9645\u95EE\u9898\u4E0A\u3002\u800C\u6768\u4FEE\u7684\u806A\u660E\uFF0C\u5219\u5B8C\u5168\u53EA\u662F\u4E3A\u4E86\u5C55\u793A\u806A\u660E\u672C\u8EAB\u3002</p><p>\u8D3E\u8BE9\u7684\u7B79\u8C0B\u5212\u7B56\uFF0C\u5E76\u4E0D\u89C1\u5F97\u6709\u591A\u4E48\u6B63\u4E49\u51DB\u7136\uFF0C\u53CD\u5012\u662F\u6709\u4E9B\u8BA1\u7B56\uFF0C\u5982\u5506\u4F7F\u674E\u5095\u653B\u6253\u957F\u5B89\u4E00\u4E8B\uFF0C\u7978\u5BB3\u6781\u5927\u3002\u4F46\u7EB5\u89C2\u8D3E\u8BE9\u7684\u8BA1\u8C0B\uFF0C\u6BCF\u4E00\u6B21\u90FD\u662F\u9488\u5BF9\u5177\u4F53\u7684\u95EE\u9898\u5C55\u5F00\u2014\u2014\u4ED6\u60F3\u8981\u505A\u7684\uFF0C\u662F\u89E3\u51B3\u8FD9\u4E2A\u95EE\u9898\u3002\u4ED6\u4E5F\u662F\u901A\u8FC7\u5BF9\u95EE\u9898\u7684\u89E3\u51B3\uFF0C\u6765\u663E\u793A\u81EA\u5DF1\u7684\u9AD8\u667A\u5546\u3002</p><p>\u518D\u53CD\u89C2\u6768\u4FEE\uFF0C\u51E0\u4E4E\u6240\u6709\u7684\u8868\u6F14\u90FD\u53EA\u662F\u5728\u8BC1\u660E\uFF0C\u4F60\u4EEC\u5FEB\u770B\uFF0C\u6211\u7684\u667A\u5546\u5F88\u9AD8\uFF01\u662F\u5178\u578B\u7684\u4E3A\u4E86\u79C0\u800C\u79C0\u3002</p><p><strong>\u5176\u6B21</strong>\uFF0C\u8D3E\u8BE9\u7684\u8BA1\u8C0B\u4ECE\u4E0D\u9488\u5BF9\u5177\u4F53\u7684\u4EBA\uFF0C\u800C\u662F\u5C31\u4E8B\u8BBA\u4E8B\u3002</p><p>\u4E0D\u50CF\u6768\u4FEE\uFF0C\u9488\u5BF9\u7684\u76EE\u6807\u6781\u4E3A\u660E\u786E\uFF1A\u4F60\u662F\u4E1E\u76F8\u53C8\u80FD\u600E\u6837\uFF0C\u6211\u5C31\u662F\u6BD4\u4F60\u806A\u660E\uFF01\u4E00\u4E2A\u6210\u5E74\u4EBA\uFF0C\u505A\u8FD9\u79CD\u65E0\u804A\u7684\u4E8B\uFF0C\u4F60\u5B8C\u5168\u65E0\u6CD5\u7406\u89E3\u610F\u4E49\u4F55\u5728\u3002</p><p><strong>\u6700\u540E</strong>\uFF0C\u8D3E\u8BE9\u7684\u9AD8\u667A\u5546\u5E76\u6CA1\u6709\u8BA9\u4ED6\u773C\u9AD8\u4E8E\u9876\uFF0C\u76DB\u6C14\u51CC\u4EBA\u3002\u76F8\u53CD\uFF0C\u4ED6\u8C26\u548C\u4F4E\u8C03\uFF0C\u4E0E\u4EBA\u4E3A\u5584\u3002</p><p>\u6C49\u732E\u5E1D\u5728\u957F\u5B89\u65F6\uFF0C\u65AF\u6587\u626B\u5730\uFF0C\u6574\u4E2A\u51C9\u5DDE\u96C6\u56E2\u53EA\u6709\u8D3E\u8BE9\u5BF9\u4ED6\u4EEC\u5C1A\u5B58\u5584\u610F\uFF0C\u8FD9\u662F\u4E00\u79CD\u96EA\u4E2D\u9001\u70AD\u4F3C\u7684\u7684\u7167\u987E\uFF0C\u8BA9\u6EE1\u671D\u516C\u537F\u90FD\u5BF9\u8D3E\u8BE9\u9887\u6709\u597D\u611F\u3002</p><p>\u6768\u4FEE\u5462\uFF0C\u867D\u7136\u4E5F\u53F7\u79F0\u662F\u5728\u5E2E\u52A9\u5DE5\u5320\u4EEC\u89E3\u8C1C\uFF0C\u4F46\u662F\u8FD9\u79CD\u5FD9\u5374\u5E2E\u5F97\u8FDE\u9526\u4E0A\u6DFB\u82B1\u90FD\u7B97\u4E0D\u4E0A\uFF0C\u53EA\u80FD\u662F\u8BA9\u66F9\u64CD\u89C9\u5F97\u6768\u4FEE\u8FD9\u4E2A\u4EBA\u53EF\u6076\u5F02\u5E38\u3002</p><p><code>\u8D3E\u8BE9\u806A\u660E\uFF0C\u4F46\u4ECE\u6765\u4E0D\u8BA9\u65C1\u4EBA\u5BF9\u4ED6\u7684\u806A\u660E\uFF0C\u611F\u89C9\u5230\u4E0D\u8212\u670D\u3002</code></p><p>\u667A\u5546\u9AD8\uFF0C\u5E76\u4E0D\u610F\u5473\u7740\u4F60\u5C31\u80FD\u505A\u4E00\u4E2A\u806A\u660E\u4EBA\u3002\u901A\u8FC7\u5BF9\u6BD4\uFF0C\u4F60\u53EF\u4EE5\u770B\u5230\u8D3E\u8BE9\u3001\u6768\u4FEE\u4E8C\u4EBA\u540C\u6837\u662F\u9AD8\u667A\u5546\u65CF\u7FA4\uFF0C\u4F46\u884C\u4E8B\u98CE\u683C\u5374\u5929\u5DEE\u5730\u522B\uFF1A\u8D3E\u8BE9\u8C26\u865A\u4F4E\u8C03\uFF0C\u5C31\u4E8B\u8BBA\u4E8B\uFF0C\u806A\u660E\u624D\u667A\u53EA\u7528\u4E8E\u89E3\u51B3\u5B9E\u9645\u95EE\u9898\uFF0C\u95F7\u58F0\u53D1\u5927\u8D22\uFF1B\u800C\u6768\u4FEE\u5374\u751F\u6027\u5F20\u626C\uFF0C\u597D\u54D7\u4F17\u53D6\u5BA0\uFF0C\u5F97\u610F\u4E8E\u81EA\u5DF1\u7684\u806A\u660E\u624D\u667A\u5374\u4E0D\u77E5\u5185\u655B\uFF0C\u6700\u7EC8\u81EA\u53D6\u8EAB\u4EA1\u3002</p><p><strong>\u5E76\u4E0D\u662F\u6240\u6709\u7684\u806A\u660E\uFF0C\u90FD\u662F\u667A\u6167\u3002</strong></p><p>\u5386\u53F2\u4E2D\u7684\u6768\u4FEE\u4E5F\u662F\u88AB\u66F9\u64CD\u6240\u6740\uFF0C\u4F46\u5374\u4E0E\u5356\u5F04\u624D\u534E\u65E0\u5173\uFF0C\u800C\u662F\u56E0\u4E3A\u6CC4\u9732\u673A\u5BC6\u3002</p><p>\u6768\u4FEE\u4E00\u76F4\u5728\u505A\u4E1E\u76F8\u5E9C\u7684\u4E3B\u8584\uFF0C\u4E5F\u5C31\u662F\u8D1F\u8D23\u7BA1\u7406\u4E1E\u76F8\u5E9C\u6587\u4E66\u7684\u5B98\u5458\uFF0C\u76F8\u5F53\u4E8E\u5982\u4ECA\u7684\u56FD\u52A1\u9662\u603B\u7406\u79D8\u4E66\u3002\u7531\u4E8E\u8FD9\u79CD\u8EAB\u4EFD\uFF0C\u66F9\u4E15\u4E0E\u66F9\u690D\u65E9\u5E74\u5747\u5BF9\u6768\u4FEE\u9752\u773C\u76F8\u52A0\uFF0C\u6DF1\u76F8\u7ED3\u7EB3\u3002\u6700\u7EC8\uFF0C\u6768\u4FEE\u6295\u9760\u4E86\u66F9\u690D\u4E00\u65B9\uFF0C\u4F46\u66F9\u690D\u5728\u7ADE\u4E89\u4E2D\u843D\u8D25\uFF0C\u6240\u4EE5\u6768\u4FEE\u81EA\u7136\u4E5F\u9887\u4E3A\u5931\u610F\u3002</p><p>\u7136\u800C\u6768\u4FEE\u7684\u95EE\u9898\u4E0D\u6B62\u4E8E\u6B64\uFF0C\u66F4\u5173\u952E\u7684\u5728\u4E8E\u4ED6\u5411\u66F9\u690D\u900F\u9732\u66F9\u64CD\u7684\u4E00\u4E9B\u610F\u5411\uFF0C\u5411\u66F9\u690D\u4F20\u9012\u4FE1\u606F\uFF0C\u5E76\u6559\u6388\u66F9\u690D\u5E94\u5BF9\u66F9\u64CD\u8003\u95EE\u4E4B\u7B56\u3002</p><p>\u8FD9\u79CD\u4E8B\u6700\u7EC8\u5F15\u8D77\u4E86\u66F9\u64CD\u7684\u731C\u5FCC\u3002\u5728\u51B3\u610F\u653E\u5F03\u66F9\u690D\u4EE5\u540E\uFF0C\u66F9\u64CD\u7EC8\u4E8E\u627E\u673A\u4F1A\uFF0C\u5904\u6B7B\u4E86\u6768\u4FEE\u3002</p>',46),n=[r];function s(l,g){return o(),t("div",null,n)}const d=p(c,[["render",s],["__file","9.html.vue"]]);export{d as default};
