import{_ as p}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as e,c as t,f as a}from"./app.1218f0f6.js";const c={},o=a('<h1 id="\u7B2C9\u96C6-\u5927\u5C71\u7389\u73E0\u6B63\u5F0F\u7ED3\u5A5A" tabindex="-1"><a class="header-anchor" href="#\u7B2C9\u96C6-\u5927\u5C71\u7389\u73E0\u6B63\u5F0F\u7ED3\u5A5A" aria-hidden="true">#</a> \u7B2C9\u96C6\uFF1A\u5927\u5C71\u7389\u73E0\u6B63\u5F0F\u7ED3\u5A5A</h1><p>\u77E5\u9053\u5973\u513F\u79C1\u5954\u7684\u9648\u91D1\u6C34\u518D\u6B21\u75C5\u5012\uFF0C\u6C5F\u6CB3\u5E26\u7740\u7389\u73E0\u7559\u4E0B\u7684\u94B1\u56DE\u5230\u9648\u5BB6\u6751\uFF0C\u4EA4\u7ED9\u9648\u91D1\u6C34\u3002\u5631\u5490\u9648\u91D1\u6C34\u597D\u597D\u517B\u75C5\u540E\u8F6C\u8EAB\u8981\u8D70\uFF0C\u9648\u91D1\u6C34\u60F3\u8981\u7528\u591A\u5E74\u7684\u6069\u60C5\u548C\u7236\u5B50\u60C5\u6765\u7559\u4E0B\u9648\u6C5F\u6CB3\uFF0C\u9648\u6C5F\u6CB3\u811A\u6B65\u505C\u987F\u4E86\u4E00\u4E0B\uFF0C\u4F9D\u7136\u8FD8\u662F\u8F6C\u8EAB\u79BB\u5F00\u3002\u6C5F\u6CB3\u56DB\u5904\u6253\u542C\u7389\u73E0\u7684\u4E0B\u843D\u3002 \u4ED6\u53D6\u56DE\u4E86\u5F53\u521D\u4E8C\u4EBA\u7684\u5408\u5F71\uFF0C\u624D\u53D1\u73B0\u7389\u73E0\u5728\u7167\u7247\u4E0A\u5199\u7684\u662F\u751F\u65E5\u7559\u5FF5\u3002</p><p>\u706B\u8F66\u4E0A\uFF0C\u7389\u73E0\u641C\u5149\u4E86\u8EAB\u4E0A\u6240\u6709\u7684\u94B1\u4E70\u4E86\u4E00\u5F20\u5230\u6C5F\u897F\u8D63\u5DDE\u7684\u8F66\u7968\u3002\u8FD9\u51E0\u4E4E\u662F\u7389\u73E0\u73B0\u5728\u7684\u5168\u90E8\u8D22\u4EA7\u3002\u5979\u73B0\u5728\u5DF2\u7ECF\u8EAB\u65E0\u5206\u6587\uFF0C\u8FDE\u4E70\u4E00\u5F20\u70E7\u997C\u5145\u9965\u7684\u94B1\u90FD\u6CA1\u6709\u4E86\u3002\u4E0B\u4E86\u706B\u8F66\uFF0C\u7389\u73E0\u53EA\u80FD\u9760\u5403\u522B\u4EBA\u7684\u5269\u996D\u5145\u9965\u3002</p><p>\u6C5F\u6CB3\u56DB\u5904\u6253\u63A2\u7389\u73E0\u7684\u4E0B\u843D\uFF0C\u4ED6\u5750\u4E0A\u706B\u8F66\uFF0C\u6765\u5230\u889C\u5382\uFF0C\u5E0C\u671B\u80FD\u627E\u5230\u7389\u73E0\uFF0C\u4F46\u889C\u5382\u7684\u5DE5\u4EBA\u4EEC\u4E5F\u5F88\u4E45\u6CA1\u6709\u89C1\u8FC7\u7389\u73E0\u4E86\u3002\u6C5F\u6CB3\u5B88\u5728\u889C\u5382\u9644\u8FD1\uFF0C\u5E0C\u671B\u6709\u4E00\u5929\u7389\u73E0\u4F1A\u51FA\u73B0\u3002</p><p>\u7389\u73E0\u53C8\u7D2F\u53C8\u997F\uFF0C\u7EC8\u4E8E\u4F53\u529B\u4E0D\u652F\uFF0C\u6655\u5012\u5728\u94C1\u8F68\u4E2D\u592E\u3002</p><p>\u6C5F\u6CB3\u5C06\u7389\u73E0\u7684\u7167\u7247\u52A0\u5370\u4E86\u5F88\u591A\u5F20\uFF0C\u7ED9\u5168\u56FD\u5404\u5730\u7684\u66FE\u7ECF\u7684\u751F\u610F\u4F19\u4F34\u4EEC\u5BC4\u53BB\uFF0C\u8BF7\u6C42\u4ED6\u4EEC\u5E2E\u52A9\u4ED6\u5BFB\u627E\u9A86\u7389\u73E0\u7684\u4E0B\u843D\u3002</p><p>\u7389\u73E0\u518D\u6B21\u9192\u6765\u65F6\uFF0C\u5DF2\u7ECF\u4E0D\u5728\u51B0\u51B7\u7684\u94C1\u8F68\u4E0A\uFF0C\u800C\u662F\u8EBA\u5728\u4E00\u5F20\u5E8A\u4E0A\u3002\u539F\u6765\u662F\u4E00\u4E2A\u7537\u4EBA\u6551\u4E86\u5979\u7684\u547D\u3002\u8FD9\u4E2A\u6C89\u9ED8\u5BE1\u8A00\u7684\u7537\u4EBA\u4E0D\u58F0\u4E0D\u54CD\u7684\u5C06\u5C4B\u5B50\u7559\u7ED9\u7389\u73E0\u5C45\u4F4F\uFF0C\u81EA\u5DF1\u62B1\u8D77\u88AB\u5B50\uFF0C\u53BB\u4E86\u522B\u5904\u3002</p><p>\u7B2C\u4E8C\u5929\u4E00\u65E9\u7389\u73E0\u5C31\u79BB\u5F00\u4E86\uFF0C\u665A\u4E0A\uFF0C\u7537\u4EBA\u542C\u5230\u6572\u95E8\u58F0\uFF0C\u6253\u5F00\u95E8\u4E00\u770B\uFF0C\u5C45\u7136\u662F\u9A86\u7389\u73E0\u53C8\u56DE\u6765\u4E86\u3002\u539F\u6765\u662F\u7389\u73E0\u5B9E\u5728\u627E\u4E0D\u5230\u4F4F\u7684\u5730\u65B9\u4E86\u3002\u7537\u4EBA\u518D\u6B21\u5C06\u81EA\u5DF1\u7684\u5C4B\u5B50\u8BA9\u7ED9\u4E86\u5979\u3002</p><p>\u7389\u73E0\u575A\u6301\u8981\u53BB\u770B\u770B\u7537\u4EBA\u4F4F\u7684\u5730\u65B9\uFF0C\u51C6\u5907\u957F\u671F\u79DF\u4E0B\u6765\uFF0C\u5728\u8FD9\u4E2A\u964C\u751F\u7684\u57CE\u5E02\u91CD\u65B0\u5F00\u59CB\u3002\u7389\u73E0\u8FD9\u65F6\u624D\u77E5\u9053\uFF0C\u7537\u4EBA\u628A\u5C4B\u5B50\u8BA9\u7ED9\u5979\uFF0C\u81EA\u5DF1\u4F4F\u7684\u662F\u56DB\u9762\u900F\u98CE\u7684\u8349\u68DA\u3002\u7389\u73E0\u5F88\u611F\u6FC0\uFF0C\u4E2D\u5348\u505A\u597D\u4E86\u70ED\u817E\u817E\u7684\u996D\u83DC\u3002\u4ED6\u8FD9\u624D\u77E5\u9053\u8FD9\u4E2A\u7537\u4EBA\u662F\u94C1\u8DEF\u6273\u9053\u5DE5\uFF0C\u53EB\u738B\u5927\u5C71\u3002</p><p>\u7389\u73E0\u505A\u4E86\u548C\u8D27\u90CE\u62C5\uFF0C\u51C6\u5907\u91CD\u65B0\u5F00\u59CB\u3002\u4E00\u5929\uFF0C\u5929\u964D\u5927\u96E8\uFF0C\u7389\u73E0\u5728\u5ECA\u4E0B\u907F\u96E8\u65F6\uFF0C\u9047\u5230\u4E86\u90BB\u6751\u7684\u540C\u4E61\uFF0C\u7389\u73E0\u8FD8\u62B1\u6709\u4E00\u4E1D\u5E0C\u671B\uFF0C\u5411\u4ED6\u6253\u542C\u5DE7\u59D1\uFF0C\u5374\u542C\u8BF4\u5DE7\u59D1\u548C\u5979\u7537\u4EBA\u51FA\u6765\u5356\u732A\u76AE\u624B\u5957\uFF0C\u8FD8\u8BF4\u5979\u7537\u4EBA\u4E5F\u59D3\u9648\uFF0C\u8FD8\u662F\u540C\u4E61\u7B49\u7B49\u3002\u7389\u73E0\u8BEF\u4F1A\u4ED6\u8BF4\u7684\u662F\u9648\u6C5F\u6CB3\u3002\u611F\u5230\u5341\u5206\u7EDD\u671B\u3002</p><p>\u7389\u73E0\u7AD9\u5728\u66B4\u96E8\u4E2D\u62D4\u901A\u4E86\u9648\u5BB6\u6751\u7684\u7535\u8BDD\uFF0C\u5374\u4E45\u4E45\u4E0D\u6562\u5F00\u53E3\u3002\u7389\u73E0\u5728\u96E8\u4E2D\u98A4\u6296\u7740\uFF0C\u4E00\u8BED\u4E0D\u53D1\uFF0C\u6302\u65AD\u4E86\u7535\u8BDD\u3002\u56DE\u5230\u738B\u5927\u5C71\u5BB6\uFF0C\u7389\u73E0\u95EE\u738B\u5927\u5C71\u613F\u4E0D\u613F\u610F\u5A36\u81EA\u5DF1\u4E3A\u59BB\u3002</p><p>\u9648\u6C5F\u6CB3\u4E00\u76F4\u5728\u889C\u5382\u9644\u8FD1\u5F98\u5F8A\u3002\u889C\u5382\u7A81\u7136\u8D77\u4E86\u5927\u706B\uFF0C\u9648\u6C5F\u6CB3\u5FD9\u51B2\u8FDB\u53BB\uFF0C\u5E2E\u52A9\u889C\u5382\u5DE5\u4EBA\u4EEC\u62A2\u6551\u7269\u8D44\u3002\u5927\u706B\u7184\u706D\u540E\uFF0C\u9648\u6C5F\u6CB3\u5728\u889C\u5382\u7684\u5899\u4E0A\u53D1\u73B0\u4E86\u4E00\u5E45\u753B\uFF0C\u539F\u6765\u6B63\u662F\u5F53\u5E74\u5C0F\u7389\u73E0\u753B\u7684\u5979\u548C\u5988\u5988\u5728\u889C\u5382\u5DE5\u4F5C\u7684\u65E5\u5B50\u3002\u9648\u6C5F\u6CB3\u629A\u6478\u7740\u90A3\u526F\u753B\uFF0C\u5931\u58F0\u75DB\u54ED\u3002</p><p>\u889C\u5382\u5382\u957F\u5341\u5206\u611F\u6FC0\u9648\u6C5F\u6CB3\uFF0C\u9648\u6C5F\u6CB3\u8BE2\u95EE\u889C\u5382\u662F\u5426\u8FD8\u62DB\u4EBA\uFF0C\u5382\u957F\u5F53\u5373\u540C\u610F\uFF0C\u4E8E\u662F\uFF0C\u9648\u6C5F\u6CB3\u5C31\u7559\u5728\u4E86\u889C\u5382\u3002</p><p>\u7389\u73E0\u548C\u738B\u5927\u5C71\u7ED3\u5A5A\u7684\u5F53\u5929\u665A\u4E0A\uFF0C\u7389\u73E0\u5F00\u53E3\u5411\u738B\u5927\u5C71\u501F\u94B1\u3002\u738B\u5927\u5C71\u4EC0\u4E48\u4E5F\u6CA1\u95EE\uFF0C\u638F\u51FA\u81EA\u5DF1\u7684\u5168\u90E8\u8EAB\u5BB6\uFF0C\u4EA4\u5230\u4E86\u7389\u73E0\u624B\u91CC\u3002\u7389\u73E0\u51B3\u5FC3\u597D\u597D\u548C\u738B\u5927\u5C71\u8FC7\u65E5\u5B50\uFF0C\u53EA\u662F\u4F9D\u7136\u5FCD\u4E0D\u4F4F\u5077\u5077\u7684\u54ED\u6CE3\u3002\u8FD9\u4E9B\u90FD\u88AB\u738B\u5927\u5C71\u770B\u5728\u773C\u91CC\u3002\u4F46\u5374\u4EC0\u4E48\u4E5F\u6CA1\u8BF4\u3002</p><p>\u4ECE\u6B64\uFF0C\u9648\u6C5F\u6CB3\u5728\u889C\u5382\u5DE5\u4F5C\uFF0C\u800C\u7389\u73E0\u5219\u5728\u8D63\u5DDE\u5F00\u59CB\u4E86\u65B0\u7684\u4EBA\u751F\u3002\u9664\u4E86\u6BCF\u6B21\u5C06\u8D5A\u5230\u7684\u94B1\u5BC4\u56DE\u9648\u5BB6\u6751\uFF0C\u4F3C\u4E4E\u4E0E\u8FC7\u53BB\u7684\u751F\u6D3B\u518D\u4E5F\u6CA1\u6709\u4E86\u5176\u4ED6\u7684\u8054\u7CFB\u3002</p>',15),r=[o];function _(n,s){return e(),t("div",null,r)}const h=p(c,[["render",_],["__file","9.html.vue"]]);export{h as default};
