import{_ as p}from"./_plugin-vue_export-helper.cdc0426e.js";import{o,c as e,f as c}from"./app.2ed33847.js";const d={},t=c('<h1 id="\u5317\u9F50\u7684\u8986\u706D" tabindex="-1"><a class="header-anchor" href="#\u5317\u9F50\u7684\u8986\u706D" aria-hidden="true">#</a> \u5317\u9F50\u7684\u8986\u706D</h1><blockquote><p>\uFF08\u80E1\uFF09\u592A\u540E\u6B32\u7ACB\u80E1\u662D\u4EEA\uFF0C\u529B\u4E0D\u80FD\u9042\uFF0C\u4E43\u5351\u8F9E\u539A\u793C\u4EE5\u6C42\u4EE4\u8431\uFF0C\u7ED3\u4E3A\u59CA\u59B9\u3002</p></blockquote><p><strong>\u300A\u8D44\u6CBB\u901A\u9274\xB7\u5377\u4E00\u4E03\u4E00\xB7\u9648\u7EAA\u4E94\u300B</strong></p><p>\u516C\u5143547\u5E74\uFF0C<code>\u4E1C\u9B4F</code>\u6743\u81E3<strong>\u9AD8\u6B22</strong>\u75C5\u901D\uFF0C\u4E16\u5B50<strong>\u9AD8\u6F84</strong>\u55E3\u4F4D\u3002\u4E24\u5E74\u4EE5\u540E\uFF0C\u9AD8\u6F84\u88AB\u6740\uFF0C\u5176\u5F1F<code>\u9AD8\u6D0B</code>\u55E3\u4F4D\u3002\u968F\u5373\uFF0C<strong>\u9AD8\u6D0B\u903C\u8FEB\u4E1C\u9B4F<code>\u5B5D\u9759\u5E1D\u5143\u5584\u89C1</code>\u7985\u4F4D\uFF0C\u4E1C\u9B4F\u706D\u4EA1\uFF0C\u5317\u9F50\u5EFA\u7ACB\u3002</strong></p><p>\u9AD8\u6D0B\u5728\u4F4D\u5341\u5E74\uFF0C\u524D\u671F\u5C1A\u53EF\uFF0C\u4F46\u5230\u4E86\u540E\u671F\u5374\u7CBE\u795E\u9519\u4E71\uFF0C\u884C\u4E3A\u4E56\u5F20\u3002\u5176\u540E\uFF0C\u9AD8\u6D0B\u75C5\u6545\uFF0C\u4F20\u4F4D\u4E8E\u592A\u5B50<code>\u9AD8\u6BB7</code>\u3002\u4F46\u4EC5\u534A\u5E74\u540E\uFF0C\u9AD8\u6B22\u7684\u7B2C\u516D\u5B50\u3001\u9AD8\u6D0B\u4E4B\u5F1F<code>\u9AD8\u6F14</code>\u5373\u7BE1\u4F4D\u3002</p><p>\u9AD8\u6F14\u5728\u4F4D\u4EC5\u4E8C\u5E74\uFF0C\u4E34\u7EC8\u524D\u5E76\u6CA1\u6709\u5C06\u7687\u4F4D\u4F20\u4E8E\u592A\u5B50<code>\u9AD8\u767E\u5E74</code>\uFF0C\u800C\u662F\u4EA4\u7ED9\u4E86\u4E5D\u5F1F<code>\u9AD8\u6E5B</code>\u3002\u5728\u8FD9\u4E4B\u540E\uFF0C\u5317\u9F50\u53C8\u5B58\u5728\u4E8617\u5E74\uFF0C\u8FD917\u5E74\u4E2D\uFF0C\u5317\u9F50\u4F9D\u65E7\u6301\u7EED\u6027\u7684\u9677\u5728\u671D\u653F\u6DF7\u4E71\u3001\u7531\u76DB\u8F6C\u8870\u7684\u8FF7\u5C40\u91CC\u3002</p><p>**\u56DE\u770B\u5317\u9F50\u7684\u6574\u4E2A\u671D\u653F\u4E4B\u5931\uFF0C\u7AEF\u502A\u5728\u9AD8\u6F14\u767B\u57FA\u65F6\u5373\u5DF2\u6D6E\u73B0\u3002**\u5F53\u521D\uFF0C\u4E3A\u4E86\u7BE1\u4F4D\uFF0C\u9AD8\u6F14\u66FE\u8BDB\u6740\u5927\u81E3<code>\u6768\u6114</code>\uFF0C\u4EE5<code>\u8D75\u5F66\u6DF1</code>\u4E3A\u66FF\u3002\u65F6\u4EBA\u6709\u8BC4\uFF0C\u8FD9\u662F\u201C\u5C06\u6D89\u5343\u91CC\uFF0C\u6740\u9A90\u9A4E\u800C\u7B56\u8E47\u9A74\uFF0C\u53EF\u60B2\u4E4B\u751A\u4E5F\uFF01\u201D\u2014\u2014\u5728\u5C06\u8981\u8DCB\u5C71\u6D89\u6C34\u3001\u5343\u91CC\u800C\u884C\u7684\u65F6\u5019\uFF0C\u5374\u6740\u4E86\u826F\u9A6C\u53BB\u6362\u4E00\u5934\u7B28\u9A74\uFF0C\u5509\uFF0C\u5B9E\u5728\u662F\u53EF\u60B2\u554A\uFF01</p><p>\u4ECE\u9AD8\u6E5B\u767B\u57FA\u540E\u5F00\u59CB\u8BF4\u8D77\u3002</p><p>\u9AD8\u6E5B\u7684\u7B2C\u4E00\u4E2A\u6743\u81E3\uFF0C\u662F<code>\u548C\u58EB\u5F00</code>\u3002\u548C\u58EB\u5F00\u4E4B\u6240\u4EE5\u80FD\u8BA8\u9AD8\u6E5B\u6B22\u5FC3\uFF0C\u662F\u56E0\u4E3A\u4ED6\u5584\u4E8E\u63E1\u69CA\uFF08\u4E00\u79CD\u68CB\u7C7B\u6E38\u620F\uFF09\u548C\u5F39\u7435\u7436\u2014\u2014\u5728\u8FD9\u4E00\u70B9\u4E0A\uFF0C\u53E4\u5F80\u4ECA\u6765\u90FD\u662F\u4E00\u6837\u7684\u3002\u80FD\u548C\u9886\u5BFC\u6253\u4E52\u4E53\u7403\u4E0E\u724C\u7684\u4EBA\uFF0C\u603B\u6BD4\u771F\u6B63\u505A\u4E8B\u7684\u4EBA\u8981\u6765\u5F97\u4EB2\u8FD1\u3002</p><p>\u548C\u58EB\u5F00\u80FD\u6709\u591A\u725B\u5462\uFF1F\u4ED6\u5F53\u653F\u7684\u65F6\u5019\uFF0C\u628A<code>\u9AD8\u5143\u6D77</code>\u8D76\u51FA\u4E86\u671D\u5EF7\u3002</p><p>\u9AD8\u5143\u6D77\u53EF\u4E0D\u662F\u4E00\u822C\u4EBA\uFF0C\u4ED6\u4E4B\u4E8E\u9AD8\u6E5B\uFF0C\u662F\u80FD\u5728\u5173\u952E\u65F6\u523B\u63D0\u4F9B\u51B3\u7B56\u4F9D\u636E\u7684\u4EBA\u3002\u5F53\u5E74\u9AD8\u6F14\u5728\u4F4D\uFF0C\u731C\u5FCC\u9AD8\u6E5B\u3002\u9AD8\u6E5B\u5F88\u4E0D\u81EA\u5B89\uFF0C\u751F\u6015\u9AD8\u6F14\u5148\u4E0B\u624B\u4E3A\u5F3A\u6740\u6389\u81EA\u5DF1\uFF0C\u4E13\u95E8\u95EE\u8BA1\u4E8E\u9AD8\u5143\u6D77\u3002</p><p>\u9AD8\u5143\u6D77\u672C\u4E0D\u613F\u610F\u7275\u6D89\u8FDB\u8FD9\u79CD\u5BAB\u5EF7\u4E89\u6597\u91CC\uFF0C\u4E00\u5F00\u59CB\u5E76\u4E0D\u63D0\u5EFA\u8BAE\u3002\u9AD8\u6E5B\u4E3A\u4E86\u903C\u4ED6\u5F00\u53E3\uFF0C\u751A\u81F3\u628A\u4ED6\u5173\u4E86\u8D77\u6765\u3002\u9AD8\u5143\u6D77\u4E0D\u5F97\u5DF2\uFF0C\u624D\u63D0\u51FA\u4E0A\u4E2D\u4E0B\u4E09\u7B56\u3002\u53EA\u4E0D\u8FC7\u540E\u6765\u9AD8\u6E5B\u6B63\u51C6\u5907\u4F9D\u8BA1\u884C\u4E8B\uFF0C\u9AD8\u6F14\u7A81\u7136\u75C5\u5371\uFF0C\u4F20\u4F4D\u4E8E\u9AD8\u6E5B\uFF0C\u4E00\u573A\u5927\u7978\u624D\u5316\u4E8E\u65E0\u5F62\u3002</p><p>\u4F46\u5982\u4ECA\uFF0C\u9AD8\u6E5B\u597D\u4E86\u4F24\u75A4\u5FD8\u4E86\u75BC\uFF0C\u7ADF\u7136\u56E0\u4E3A\u548C\u58EB\u5F00\u7684\u8C17\u8A00\u5C31\u8D76\u8D70\u4E86\u9AD8\u5143\u6D77\u2014\u2014\u5F53\u7136\uFF0C\u9AD8\u5143\u6D77\u4E5F\u4E0D\u662F\u4EC0\u4E48\u7701\u6CB9\u7684\u706F\uFF0C\u5C06\u6765\u4ED6\u8FD8\u4F1A\u56DE\u6765\u7684\uFF0C\u8FD9\u4E2A\u6211\u4EEC\u5148\u6309\u4E0B\u4E0D\u8868\u3002</p><p>\u5BF9\u9AD8\u6E5B\u800C\u8A00\uFF0C\u548C\u58EB\u5F00\u7684\u529F\u80FD\u6BD4\u8F83\u5355\u4E00\uFF0C\u552F\u4E00\u7684\u4F5C\u7528\u5C31\u662F\u966A\u7740\u73A9\u3002\u8FD9\u5F53\u7136\u4F1A\u60F9\u4EBA\u773C\u7EA2\u5FC3\u70ED\uFF0C\u4F46\u773C\u7EA2\u5F52\u773C\u7EA2\uFF0C\u60F3\u201C\u53D6\u800C\u4EE3\u4E4B\u201D\u7684\u5FF5\u5934\u4E00\u822C\u4EBA\u8FD8\u662F\u4E0D\u6562\u52A8\u7684\u3002</p><p>\u53EF<code>\u7956\u73FD</code>\u5C31\u4E0D\u5728\u4E00\u822C\u4EBA\u4E4B\u5217\u3002</p><p>\u7956\u73FD\u624D\u534E\u6A2A\u6EA2\uFF0C\u53EA\u4E0D\u8FC7\u6709\u4E2A\u5C0F\u7F3A\u70B9\uFF1A\u624B\u811A\u5F88\u4E0D\u5E72\u51C0\u3002\u4ED6\u5012\u4E5F\u4E0D\u662F\u559C\u6B22\u5077\u94B1\uFF0C\u800C\u662F\u559C\u6B22\u5077\u4E66\u2014\u2014\u5F53\u7136\uFF0C\u8BFB\u4E66\u4EBA\u7684\u4E8B\uFF0C\u600E\u4E48\u80FD\u7B97\u5077\u5462\uFF1F\u7956\u73FD\u4E5F\u662F\u5982\u6B64\uFF0C\u9AD8\u6D0B\u8FD8\u5728\u4F4D\u65F6\uFF0C\u8EAB\u4E3A\u79D8\u4E66\u4E1E\u7684\u4ED6\u5C31\u66FE\u76D1\u5B88\u81EA\u76D7\uFF0C\u5077\u7A83\u671D\u5EF7\u85CF\u4E66\uFF0C\u5E76\u56E0\u6B64\u88AB\u8D2C\u5B98\u4E0B\u72F1\u3002\u540E\u6765\uFF0C\u9AD8\u6D0B\u8FD8\u662F\u56E0\u4E3A\u7231\u60DC\u4ED6\u7684\u624D\u534E\uFF0C\u624D\u53C8\u8BA9\u4ED6\u91CD\u65B0\u505A\u5B98\u3002</p><p>\u8FD9\u4E2A\u7956\u73FD\uFF0C\u5F53\u7136\u4E5F\u4E0D\u662F\u90A3\u79CD\u5929\u751F\u7518\u4E8E\u5BC2\u5BDE\u7684\u4EBA\u3002\u5982\u4ECA\u9AD8\u6E5B\u5728\u4F4D\uFF0C\u6743\u81E3\u5F53\u9053\uFF0C\u6B63\u662F\u51FA\u4EBA\u5934\u5730\u7684\u597D\u65F6\u673A\uFF0C\u4ED6\u548C\u58EB\u5F00\u80FD\u505A\u6743\u81E3\uFF0C\u6211\u7956\u73FD\u600E\u4E48\u5C31\u505A\u4E0D\u5F97\uFF1F</p><p>\u6781\u5584\u4E13\u8425\u7684\u7956\u73FD\uFF0C\u65E9\u5728\u9AD8\u6E5B\u521D\u5373\u4F4D\u65F6\u5C31\u5DF2\u7ECF\u6DF7\u8FDB\u4E86\u9AD8\u6E5B\u4E0E\u548C\u58EB\u5F00\u7684\u201C\u5A31\u4E50\u5708\u201D\uFF0C\u53EA\u4E0D\u8FC7\u56E0\u4E3A\u88AB\u548C\u58EB\u5F00\u6240\u5FCC\uFF0C\u51FA\u4E3A\u5B89\u5FB7\u592A\u5B88\u3002</p><p>\u521D\u6B21\u4EA4\u950B\u5931\u8D25\uFF0C\u7956\u73FD\u5E76\u6CA1\u6709\u6C14\u9981\uFF0C\u800C\u662F\u518D\u63A5\u518D\u5389\uFF1A\u65E2\u7136\u548C\u58EB\u5F00\u5BF9\u81EA\u5DF1\u6709\u6240\u9632\u5907\uFF0C\u90A3\u4E48\u5C31\u5E94\u8BE5\u5148\u8BA8\u597D\u548C\u58EB\u5F00\u3002\u800C\u7956\u73FD\u7684\u529E\u6CD5\u662F\uFF0C\u7ED9\u548C\u58EB\u5F00\u63D0\u4E86\u4E00\u4E2A\u8BA8\u597D\u9AD8\u6E5B\u7684\u597D\u4E3B\u610F\u3002</p><p>\u7956\u73FD\u8DDF\u548C\u58EB\u5F00\u8BF4\uFF1A\u6211\u6559\u4F60\u4E00\u4E2A\u8BA8\u597D\u7687\u5E1D\u7684\u597D\u529E\u6CD5\uFF01\u4F60\u770B\uFF0C\u4ECE\u4E16\u5B97\uFF08\u9AD8\u6F84\uFF09\u3001\u663E\u7956\uFF08\u9AD8\u6D0B\uFF09\u5230\u8083\u5B97\uFF08\u9AD8\u6F14\uFF09\uFF0C\u5C31\u6CA1\u6709\u4E00\u4E2A\u4EBA\u7684\u592A\u5B50\u80FD\u591F\u987A\u5229\u7EE7\u4F4D\u3002\u5982\u4ECA\u7687\u4E0A\u6625\u79CB\u6B63\u76DB\uFF0C**\u4F60\u4E0D\u5982\u5EFA\u8BAE\u7687\u4E0A\u4F20\u4F4D\u4E8E\u592A\u5B50\uFF0C\u8FD9\u6837\u624D\u80FD\u786E\u4FDD\u592A\u5B50\u53EF\u4EE5\u987A\u5229\u7EE7\u4F4D\u3002**\u800C\u4E14\u8FD9\u4E9B\u8FD8\u53EA\u662F\u660E\u9762\u4E0A\u7684\u597D\u5904\uFF0C\u80CC\u5730\u91CC\uFF0C\u628A\u671D\u653F\u4E0A\u4E71\u4E03\u516B\u7CDF\u7684\u4E8B\u90FD\u4EA4\u7ED9\u592A\u5B50\uFF0C\u7687\u4E0A\u81EA\u5DF1\u4E0D\u5C31\u80FD\u7A7A\u51FA\u5927\u628A\u65F6\u95F4\u4E86\u561B\uFF01</p><p>\u548C\u58EB\u5F00\u4F9D\u8A00\u628A\u8FD9\u4E2A\u5EFA\u8BAE\u63D0\u7ED9\u4E86\u9AD8\u6E5B\uFF0C\u9AD8\u6E5B\u4E00\u542C\uFF0C\u987F\u89C9\u5999\u4E0D\u53EF\u8A00\uFF0C\u4E0D\u4E45\u5C31\u4F20\u4F4D\u4E8E\u5341\u5C81\u7684\u592A\u5B50\u9AD8\u7EAC\u3002</p><p>\u7ECF\u6B64\u4E00\u4E8B\uFF0C\u7956\u73FD\u91CD\u65B0\u8FDB\u5165\u51B3\u7B56\u5C42\u3002</p><p>\u4E0D\u4E45\u540E\uFF0C\u7956\u73FD\u5F97\u610F\u5FD8\u5F62\uFF0C\u5F00\u59CB\u8C0B\u5212\u5982\u4F55\u628A\u548C\u58EB\u5F00\u641E\u8D70\uFF0C\u81EA\u5DF1\u72EC\u64C5\u6743\u5A01\u3002</p><p>\u4F46\u7956\u73FD\u7684\u5934\u4E0D\u591F\u94C1\uFF0C\u4ED6\u6307\u4F7F\u4ED6\u4EBA\u8BEC\u544A\u548C\u58EB\u5F00\uFF0C\u5374\u88AB\u548C\u58EB\u5F00\u53CD\u54AC\u4E00\u53E3\uFF0C\u5165\u9AA8\u4E09\u5206\u3002\u800C\u5728\u9762\u5BF9\u7740\u9AD8\u6E5B\u7684\u8D28\u95EE\u65F6\uFF0C\u4E0D\u77E5\u9053\u662F\u51FA\u4E8E\u4EC0\u4E48\u5FC3\u6001\uFF0C\u7956\u73FD\u7ADF\u7136\u548C\u81EA\u5DF1\u4E00\u5FC3\u60F3\u8BA8\u597D\u7684\u9AD8\u6E5B\u5927\u5435\u4E86\u4E00\u67B6\u3002</p><p>\u53CB\u8C0A\u7684\u5C0F\u8239\u5C31\u6B64\u6C89\u6CA6\u3002</p><p>\u5435\u4E0D\u8FC7\u7956\u73FD\u7684\u9AD8\u6E5B\uFF0C\u547D\u4EBA\u6252\u5F00\u7956\u73FD\u7684\u5634\u5F80\u91CC\u9762\u585E\u571F\uFF0C\u4F46\u8FD9\u4F9D\u7136\u963B\u6B62\u4E0D\u4E86\u7956\u73FD\u8D8A\u5435\u8D8A\u5174\u594B\uFF0C\u4E00\u8FB9\u5410\u4E00\u8FB9\u9A82\u3002\u6700\u7EC8\uFF0C\u8FDB\u5165\u66B4\u8D70\u6A21\u5F0F\u7684\u7956\u73FD\u88AB\u6316\u4E86\u4E2A\u5751\u76F4\u63A5\u6254\u8FDB\u53BB\u3002</p><p>\u771F\u7684\u5C31\u662F\u4E2A\u571F\u5751\uFF0C\u7956\u73FD\u88AB\u6254\u5728\u91CC\u9762\uFF0C\u4E00\u5173\u5C31\u662F\u597D\u51E0\u5E74\uFF0C\u800C\u4E14\u56E0\u4E3A\u7167\u660E\u5149\u7EBF\u4E0D\u597D\uFF0C\u5C31\u6B64\u5931\u660E\u3002</p><p>\u8981\u6401\u4E00\u822C\u4EBA\u8EAB\u4E0A\uFF0C\u8FD9\u5267\u672C\u5C31\u7B97\u662F\u73A9\u5B8C\u4E86\uFF0C\u4F46\u7956\u73FD\u4E0D\u4E00\u6837\uFF0C\u4ED6\u7ADF\u7136\u53C8\u590D\u8D77\u4E86\u3002</p><p>\u5728\u88AB\u5173\u4E86\u4E24\u5E74\u4EE5\u540E\uFF0C\u9AD8\u6E5B\u75C5\u901D\uFF0C\u5F53\u4E86\u4E09\u5E74\u7687\u5E1D\u7684\u9AD8\u7EAC\u771F\u6B63\u63A5\u624B\u6743\u529B\u3002\u65B0\u7687\u5E1D\u8FD9\u65F6\u5019\u60F3\u8D77\u4E86\u7956\u73FD\u7684\u597D\uFF1A\u4ED6\u7684\u7687\u4F4D\u80FD\u6765\u5F97\u5982\u6B64\u5B89\u7A33\uFF0C\u7956\u73FD\u5F53\u5E74\u7684\u201C\u998A\u4E3B\u610F\u201D\u529F\u4E0D\u53EF\u6CA1\u3002\u8981\u4E0D\u662F\u4ED6\u6559\u548C\u58EB\u5F00\u529D\u8BF4\u9AD8\u6E5B\u63D0\u524D\u7985\u4F4D\uFF0C\u81EA\u5DF1\u7684\u7687\u4F4D\u4E0D\u89C1\u5F97\u5982\u6B64\u7A33\u56FA\u3002</p><p>\u4E0D\u4E45\u540E\uFF0C\u7956\u73FD\u88AB\u9AD8\u7EAC\u91CA\u653E\uFF0C\u968F\u5373\u91CD\u56DE\u671D\u5EF7\u3002</p><p>\u4E4B\u540E\u53C8\u8FC7\u4E86\u4E24\u5E74\uFF0C\u7956\u73FD\u7EC8\u4E8E\u51FB\u8D25\u548C\u58EB\u5F00\uFF0C\u72EC\u9738\u671D\u653F\u3002\u800C\u5728\u8FD9\u91CC\u9762\uFF0C\u53C8\u662F\u53E6\u4E00\u4E2A\u590D\u6742\u7684\u6545\u4E8B\u4E86\u3002</p><p>\u548C\u58EB\u5F00\u5F53\u521D\u73A9\u5F97\u6709\u70B9\u5927\uFF0C\u4E0D\u5149\u662F\u966A\u9AD8\u6E5B\u73A9\uFF0C\u8FD8\u966A\u9AD8\u6E5B\u7684\u7687\u540E\u73A9\uFF0C\u73A9\u7740\u73A9\u7740\u5C31\u73A9\u4E0A\u4E86\u5E8A\u3002\u5728\u9AD8\u6E5B\u6B7B\u540E\uFF0C\u80E1\u7687\u540E\u53D8\u6210\u80E1\u592A\u540E\uFF0C\u4E24\u4EBA\u66F4\u52A0\u8086\u65E0\u5FCC\u60EE\u2014\u2014\u5F53\u7136\uFF0C\u80E1\u592A\u540E\u4E5F\u4E0D\u6B62\u548C\u58EB\u5F00\u8FD9\u4E00\u4E2A\u9762\u9996\u3002</p><p>\u9AD8\u6E5B\u624D\u6B7B\uFF0C\u671D\u5EF7\u5927\u81E3\u5C31\u66FE\u60F3\u8981\u628A\u548C\u58EB\u5F00\u8D76\u51FA\u671D\u5EF7\uFF0C\u4F46\u548C\u58EB\u5F00\u8DD1\u5230\u540E\u4E3B\u9AD8\u7EAC\u548C\u80E1\u592A\u540E\u9762\u524D\u4E00\u756A\u54ED\u8BC9\uFF0C\u6700\u7EC8\u8F6C\u5371\u4E3A\u5B89\uFF0C\u53CD\u5012\u662F\u529B\u4E3B\u9A71\u9010\u548C\u58EB\u5F00\u7684\u5B97\u5BA4<code>\u9AD8\u777F</code>\u88AB\u6740\u3002</p><p>\u800C\u4E14\uFF0C\u548C\u58EB\u5F00\u8FD8\u642D\u4E0A\u4E86<code>\u9646\u4EE4\u8431</code>\u8FD9\u6761\u7EBF\u2014\u2014\u5BF9\uFF0C\u5C31\u662F\u5F53\u5E74\u5927\u70ED\u7684\u90A3\u90E8\u5BAB\u6597\u620F\u7684\u539F\u578B\u4EBA\u7269\u3002</p><p>\u9646\u4EE4\u8431\u672C\u6765\u662F\u540E\u4E3B\u9AD8\u7EAC\u7684\u4E73\u6BCD\u3002\u9AD8\u7EAC\u4ECE\u5C0F\u8DDF\u7740\u9646\u6C0F\uFF0C\u5F53\u4E86\u7687\u5E1D\u4E5F\u79BB\u4E0D\u5F00\u5979\uFF0C**\u8FD9\u5C31\u5BFC\u81F4\u9AD8\u6E5B\u6B7B\u540E\u671D\u653F\u7684\u4E00\u5927\u53D8\u5C40\uFF0C\u662F\u9646\u4EE4\u8431\u7684\u52BF\u529B\u5F00\u59CB\u6025\u5267\u81A8\u80C0\u3002**\u5F53\u65F6\u751A\u81F3\u6709\u4EBA\u62CD\u9A6C\u5C41\uFF0C\u60F3\u628A\u9646\u6C0F\u4E5F\u518C\u7ACB\u4E3A\u592A\u540E\uFF0C\u9646\u6C0F\u7684\u6743\u5A01\u53EF\u89C1\u4E00\u6591\u3002</p><p>\u671D\u653F\u5982\u6B64\uFF0C\u642D\u4E0A\u4E86\u8FD9\u6761\u7EBF\u7684\u548C\u58EB\u5F00\uFF0C\u81EA\u7136\u4E5F\u66F4\u52A0\u8086\u65E0\u5FCC\u60EE\u3002</p><p>\u4F46\u6CA1\u5230\uFF0C<strong>\u5C31\u5728\u6700\u5982\u65E5\u4E2D\u5929\u7684\u65F6\u5019\uFF0C\u548C\u58EB\u5F00\u7ADF\u7136\u88AB\u4EBA\u7ED9\u6740\u4E86\u3002</strong></p><p>\u540E\u4E3B\u9AD8\u7EAC\u7684\u5F1F\u5F1F\u3001\u7405\u740A\u738B<code>\u9AD8\u4FE8</code>\uFF0C\u56E0\u4E3A\u770B\u548C\u58EB\u5F00\u4E0D\u723D\uFF0C\u5C31\u8FD9\u4E48\u8D81\u81EA\u5DF1\u7BA1\u7406\u9996\u90FD\u90BA\u57CE\u9A7B\u519B\u4E4B\u5229\uFF0C\u6740\u4E86\u548C\u58EB\u5F00\u3002</p><p>\u9AD8\u4FE8\u4E3A\u6B64\u642D\u4E0A\u4E86\u81EA\u5DF1\u7684\u4E00\u6761\u547D\uFF0C\u4F46\u4ED6\u7684\u767D\u7ED9\u5374\u5E2E\u7956\u73FD\u5F00\u8F9F\u4E86\u4E00\u6761\u5EB7\u5E84\u5927\u9053\uFF1A<strong>\u5728\u548C\u58EB\u5F00\u6B7B\u540E\u4E0D\u4E45\uFF0C\u7956\u73FD\u5373\u7528\u4E8B\uFF0C\u6743\u503E\u671D\u91CE</strong>\u2014\u2014\u4E00\u4EE3\u540D\u5C06<code>\u659B\u5F8B\u5149</code>\uFF0C\u4E5F\u5C31\u662F\u5728\u6B64\u65F6\u88AB\u7956\u73FD\u6784\u9677\u800C\u6B7B\u7684\u3002</p><p>\u5317\u9F50\u7684\u671D\u653F\u683C\u5C40\uFF0C\u5982\u4ECA\u53D8\u5F97\u66F4\u52A0\u6666\u6697\uFF1A\u5728\u671D\u5EF7\u4E2D\uFF0C\u7956\u73FD\u4E3B\u653F\uFF1B\u5728\u540E\u4E3B\u9AD8\u7EAC\u7684\u80CC\u540E\uFF0C\u53C8\u6709\u9646\u4EE4\u8431\u9690\u9690\u63D0\u7EBF\uFF1B\u4E24\u4EBA\u5185\u5916\u52FE\u7ED3\uFF0C\u63A7\u5236\u4F4F\u4E86\u671D\u653F\u3002</p><p>\u524D\u6587\u6240\u8BF4\u7684\u518C\u7ACB\u9646\u6C0F\u4E3A\u592A\u540E\u4E00\u4E8B\uFF0C\u5982\u4ECA\u518D\u6B21\u88AB\u7956\u73FD\u63D0\u4E86\u51FA\u6765\u3002\u800C\u9646\u6C0F\u4E3A\u7B79\u5176\u529F\uFF0C\u53C8\u7ED9\u7956\u73FD\u8981\u5230\u4E86\u4EC6\u5C04\u7684\u804C\u4F4D\u3002</p><p>\u81F3\u4E8E\u80E1\u592A\u540E\u5462\uFF1F</p><p>\u5979\u88AB\u4EB2\u513F\u5B50\u9AD8\u7EAC\u5E7D\u7981\u4E86\u2014\u2014\u5979\u7684\u9762\u9996\u591A\u5230\u4EE4\u9AD8\u7EAC\u6124\u6012\u3002\u5728\u5904\u6B7B\u8FD9\u4E9B\u4EBA\u540E\uFF0C\u9AD8\u7EAC\u5E7D\u7981\u4E86\u80E1\u592A\u540E\u3002</p><p>\u9AD8\u5143\u6D77\u4F5C\u4E3A\u7956\u73FD\u7684\u5E2E\u624B\uFF0C\u8FC7\u4E86\u8FD9\u4E48\u591A\u5E74\u4EE5\u540E\uFF0C\u4ED6\u7EC8\u4E8E\u4E5F\u56DE\u6765\u4E86\u2014\u2014\u5173\u952E\u5728\u4E8E\u4ED6\u62DC\u5BF9\u4E86\u7801\u5934\uFF1A\u4ED6\u7684\u540E\u59BB\u662F\u9646\u4EE4\u8431\u7684\u5916\u7525\u3002\u7531\u6B64\uFF0C\u4ED6\u642D\u4E0A\u4E86\u9646\u6C0F\u8FD9\u6761\u7EBF\u3002</p><p>\u53EF\u4ED6\u548C\u7956\u73FD\u5E76\u4E0D\u5BF9\u4ED8\u3002\u4E0D\u4E45\u540E\uFF0C\u7956\u73FD\u5C31\u53C8\u8BBE\u6CD5\u628A\u4ED6\u8D76\u51FA\u4E86\u671D\u5EF7\uFF0C\u4ECE\u6B64\u72EC\u4E13\u671D\u653F\u3002</p><p>\u7956\u73FD\u4E0D\u662F\u548C\u58EB\u5F00\uFF0C\u6BD5\u7ADF\u662F\u6709\u6587\u5316\u7684\u3002\u5728\u5F53\u653F\u4EE5\u540E\uFF0C\u4ED6\u4E00\u5EA6\u6574\u987F\u671D\u653F\uFF0C\u4EE4\u65F6\u5C40\u76F8\u8F83\u4E8E\u548C\u58EB\u5F00\u65F6\u671F\u6709\u4E86\u6781\u5927\u8D77\u8272\u3002</p><p>**\u4F46\u7956\u73FD\u8FD9\u4E2A\u4EBA\u6709\u4E2A\u5927\u95EE\u9898\uFF1A\u603B\u89C1\u4E0D\u5F97\u6709\u4EBA\u9A91\u5230\u81EA\u5DF1\u5934\u4E0A\u3002**\u5F53\u5E74\u4ED6\u901A\u8FC7\u548C\u58EB\u5F00\u722C\u4E0A\u6765\uFF0C\u7FFB\u8138\u5C31\u4E0D\u8BA4\u4EBA\uFF0C\u5FC3\u5FC3\u5FF5\u5FF5\u53EA\u60F3\u7740\u628A\u548C\u58EB\u5F00\u8D76\u8D70\u3002\u5982\u4ECA\u901A\u8FC7\u9646\u6C0F\u5F97\u4F4D\uFF0C\u5C31\u53C8\u89C9\u5F97\u9646\u6C0F\u548C\u5979\u7684\u540C\u515A\u4EEC\u788D\u4E8B\u4E86\uFF0C\u60F3\u7740\u628A\u8FD9\u4E9B\u4EBA\u8D76\u8D70\u3002</p><p>\u53EF\u4ED6\u7684\u5934\u4F9D\u7136\u4E0D\u591F\u94C1\uFF0C\u6CA1\u649E\u5F97\u52A8\u9646\u6C0F\u4E00\u4F19\uFF0C\u53CD\u800C\u628A\u81EA\u5DF1\u7ED9\u5F39\u4E86\u51FA\u53BB\u3002</p><p>\u5F53\u521D\uFF0C\u7956\u73FD\u9760\u7740\u9646\u4EE4\u8431\u4E0A\u4F4D\u3002\u4F46\u5F53\u653F\u540E\uFF0C\u4ED6\u53C8\u89C9\u5F97\u9646\u4EE4\u8431\u7684\u722A\u7259\u4EEC\u8D2A\u8150\u8D25\u574F\u671D\u653F\uFF0C\u800C\u6839\u5B50\u5C31\u5728\u4E8E\u9646\u4EE4\u8431\uFF0C\u4E8E\u662F\u4FBF\u6307\u4F7F\u4EB2\u4FE1\u5F39\u52BE\u3002</p><p>\u53EF\u4EB2\u4FE1\u5374\u4E0D\u6562\u505A\u5F97\u7F6A\u9646\u6C0F\u7684\u4E8B\uFF0C\u4E00\u6765\u4E8C\u53BB\uFF0C\u88AB\u9646\u6C0F\u6240\u77E5\u3002\u9646\u6C0F\u5148\u4E0B\u624B\u4E3A\u5F3A\uFF0C\u6307\u4F7F\u4ED6\u4EBA\u5F39\u52BE\u7956\u73FD\uFF0C\u7956\u73FD\u5C31\u6B64\u88AB\u8D2C\uFF0C\u6B64\u540E\u5728\u53F2\u4E66\u4E2D\u5931\u53BB\u8BB0\u8F7D\u3002</p><p>\u76F8\u6BD4\u4E4B\u4E0B\uFF0C\u80E1\u592A\u540E\u5C31\u8BC6\u76F8\u5F97\u591A\u3002\u81EA\u4ECE\u88AB\u513F\u5B50\u5E7D\u7981\u540E\uFF0C\u5979\u75DB\u5B9A\u601D\u75DB\uFF0C\u8BA4\u6E05\u5F62\u52BF\uFF0C\u653E\u4E0B\u4E86\u5305\u88B1\uFF0C\u79EF\u6781\u8FFD\u6C42\u6539\u53D8\u5883\u51B5\uFF0C\u7ADF\u7136\u4F4E\u8EAB\u4E0B\u6C14\u5730\u6073\u6C42\u548C\u9646\u4EE4\u8431\u7ED3\u4E3A\u59CA\u59B9\u3002</p><p>\u540C\u65F6\uFF0C\u80E1\u592A\u540E\u53C8\u628A\u81EA\u5DF1\u7684\u4F84\u5973\u9001\u5230\u513F\u5B50\u8EAB\u8FB9\uFF0C\u5E0C\u671B\u80FD\u88AB\u7ACB\u4E3A\u7687\u540E\u3002\u8003\u8651\u5230\u81EA\u5DF1\u7684\u529B\u91CF\u53EF\u80FD\u4E0D\u591F\uFF0C\u8FD8\u662F\u5F97\u9646\u4EE4\u8431\u5E2E\u5FD9\uFF0C\u5802\u5802\u592A\u540E\u7ADF\u7136\u5411\u4E00\u4E2A\u4E73\u6BCD\u4F4E\u5934\uFF0C\u8BF7\u6C42\u7ACB\u81EA\u5DF1\u7684\u4F84\u5973\u4E3A\u540E\u3002\u8FD9\u8BA9\u9646\u4EE4\u8431\u8EAB\u5FC3\u5F97\u5230\u4E86\u6781\u5927\u7684\u6EE1\u8DB3\uFF0C\u70B9\u5934\u540C\u610F\u3002\u4E0D\u4E45\u540E\uFF0C\u80E1\u592A\u540E\u7684\u4F84\u5973\u80E1\u6C0F\u88AB\u7ACB\u4E3A\u7687\u540E\u3002</p><p>\u53EF\u4E0D\u4E45\uFF0C\u9646\u4EE4\u8431\u5C31\u53CD\u6094\u4E86\uFF0C\u5979\u4E5F\u60F3\u6276\u6301\u4E00\u4E2A\u7687\u540E\u3002\u4E24\u76F8\u6298\u4E2D\u4E4B\u4E0B\uFF0C\u7687\u540E\u4E00\u4E0B\u5B50\u6709\u4E86\u4E24\u4E2A\uFF1A\u9646\u4EE4\u8431\u652F\u6301\u7684\u7A46\u6C0F\u4E3A\u53F3\u7687\u540E\uFF0C\u80E1\u6C0F\u4E3A\u5DE6\u7687\u540E\u3002</p><p>\u4F46\u9646\u4EE4\u8431\u8FD8\u662F\u89C9\u5F97\u522B\u626D\uFF0C\u4E8E\u662F\u5C31\u627E\u5230\u80E1\u592A\u540E\u642C\u5F04\u662F\u975E\uFF1A\u4F60\u4F84\u5973\u6210\u5929\u5728\u7687\u5E1D\u9762\u524D\u8BF4\u4F60\u7684\u574F\u8BDD\uFF01</p><p>\u80E1\u592A\u540E\u6B64\u65F6\u7279\u522B\u5BB3\u6015\u513F\u5B50\u5BF9\u81EA\u5DF1\u6709\u574F\u5370\u8C61\uFF0C\u542C\u4E86\u8FD9\u4E9B\u8BDD\u52C3\u7136\u5927\u6012\uFF0C\u7ADF\u7136\u7ACB\u5373\u5C31\u628A\u80E1\u540E\u8D76\u51FA\u4E86\u7687\u5BAB\u3002</p><p>\u9646\u4EE4\u8431\u6743\u5A01\u4E4B\u76DB\uFF0C\u4EE5\u81F3\u4E8E\u65AF\u3002</p><p>\u6B64\u540E\uFF0C\u9646\u4EE4\u8431\u53CA\u5176\u540C\u515A<code>\u7A46\u63D0\u5A46</code>\u3001<code>\u9AD8\u963F\u90A3\u80B1</code>\u3001<code>\u97E9\u957F\u9E3E</code>\u7528\u4E8B\uFF0C\u5317\u9F50\u671D\u653F\u4ECE\u6B64\u6C5F\u6CB3\u65E5\u4E0B\u3002</p><p>\u8FD9\u65F6\u5DF2\u662F\u672B\u4E16\uFF0C\u4E0B\u4E00\u5E74\uFF0C\u5317\u5468<code>\u6B66\u5E1D\u5B87\u6587\u9095</code>\u5373\u5C06\u4F10\u9F50\u3002\u518D\u8FC7\u4E24\u5E74\uFF0C\u5317\u9F50\u5C06\u6B63\u5F0F\u706D\u4EA1\u3002</p><p><strong>\u5317\u9F50\u7684\u8870\u843D\uFF0C\u662F\u4E00\u4E2A\u770B\u5F97\u89C1\u7684\u3001\u957F\u671F\u7684\u8FC7\u7A0B\u3002</strong></p><p>\u53F2\u4E66\u4E2D\u8BB0\u8F7D\u8FC7\u8FD9\u6837\u4E00\u4EF6\u4E8B\uFF1A\u5F53\u521D\u4E1C\u897F\u9B4F\u5E76\u5CD9\uFF0C\u53CC\u65B9\u4EE5\u5982\u4ECA\u5C71\uFF08\u897F\uFF09\u9655\uFF08\u897F\uFF09\u4E4B\u95F4\u7684\u9EC4\u6CB3\u4E3A\u754C\uFF0C\u4E1C\u5F3A\u897F\u5F31\u3002\u5230\u4E86\u51AC\u5929\uFF0C\u897F\u9B4F\uFF08\u5317\u5468\uFF09\u7ECF\u5E38\u6D3E\u4EBA\u6572\u788E\u6CB3\u4E0A\u7684\u51B0\uFF0C\u4EE5\u9632\u6B62\u4E1C\u9B4F\uFF08\u5317\u9F50\uFF09\u8D81\u7740\u9EC4\u6CB3\u7ED3\u51B0\u800C\u6E21\u6CB3\u3002</p><p>\u5982\u4ECA\u51E0\u5341\u5E74\u8FC7\u53BB\uFF0C\u5230\u4E86\u51AC\u5929\uFF0C\u6CB3\u4E0A\u7684\u51B0\u4F9D\u7136\u4F1A\u88AB\u6572\u788E\u3002\u4F46\u6572\u51B0\u7684\u4E00\u65B9\uFF0C\u5DF2\u7ECF\u53D8\u6210\u4E86\u5317\u9F50\u3002\u5F53\u5E74\u9AD8\u6B22\u5728\u4E16\u65F6\u5BF9\u897F\u9B4F\uFF08\u5317\u5468\uFF09\u7684\u538B\u5012\u6027\u4F18\u52BF\uFF0C\u65E9\u5DF2\u70DF\u6D88\u4E91\u6563\uFF0C\u5317\u9F50\u5982\u4ECA\u5DF2\u7ECF\u6CA6\u843D\u5230\u8981\u9760\u5730\u5229\u624D\u80FD\u52C9\u5F3A\u7EF4\u6301\u5747\u52BF\u7684\u5730\u6B65\u3002</p><p><strong>\u5230\u5317\u65B9\u4E1C\u897F\u5E76\u5CD9\u7684\u540E\u671F\uFF0C\u5728\u5317\u9F50\u88AB\u706D\u4EA1\u4E4B\u524D\uFF0C\u4E24\u56FD\u66FE\u7ECF\u6709\u8FC7\u4E09\u6B21\u89C4\u6A21\u6BD4\u8F83\u5927\u7684\u6218\u4E89</strong>\uFF0C\u6BCF\u4E00\u6B21\u90FD\u662F\u5317\u5468\u5360\u4E0A\u98CE\uFF0C\u800C\u5317\u9F50\u5219\u56E0\u4E3A\u5404\u79CD\u610F\u5916\uFF0C\u6CA1\u6709\u906D\u53D7\u592A\u5927\u635F\u5931\u3002</p><p>\u7B2C\u4E00\u6B21\u662F\u516C\u5143563\u5E74\uFF0C\u4E5F\u5C31\u662F\u9AD8\u6E5B\u7EE7\u4F4D\u540E\u7684\u7B2C\u4E09\u5E74\uFF0C\u5317\u5468\u8054\u5408\u7A81\u53A5\u5F81\u8BA8\u5317\u9F50\u3002\u8054\u519B\u884C\u81F3\u664B\u9633\u57CE\u4E0B\uFF0C\u53D7\u56F0\u4E8E\u5929\u6C14\u5BD2\u51B7\u53CA\u5317\u9F50\u51C6\u5907\u5145\u5206\uFF0C\u5317\u5468\u6CA1\u6709\u53D6\u5F97\u4E1D\u6BEB\u6218\u679C\u2014\u2014\u4E5F\u6B63\u662F\u4ECE\u8FD9\u6B21\u5165\u4FB5\u540E\uFF0C\u5317\u9F50\u4E3A\u9632\u6B62\u5317\u5468\u51AC\u5B63\u5077\u88AD\uFF0C\u624D\u5F00\u59CB\u5728\u9EC4\u6CB3\u4E0A\u51FF\u51B0\u3002</p><p>\u7B2C\u4E8C\u6B21\u7684\u610F\u5916\u56E0\u7D20\uFF0C\u5219\u7B97\u5F97\u4E0A\u662F\u5317\u9F50\u7684\u201C\u5916\u4EA4\u80DC\u5229\u201D\u3002\u81EA\u4E1C\u897F\u9B4F\u706D\u4EA1\u540E\uFF0C\u6A2A\u4E98\u5728\u4E24\u56FD\u4E4B\u95F4\u7684\u201C\u610F\u8BC6\u5F62\u6001\u4E0A\u7684\u654C\u6211\u77DB\u76FE\u201D\u4E0D\u590D\u5B58\u5728\uFF0C\u4E24\u56FD\u5173\u7CFB\u5F00\u59CB\u6709\u7F13\u548C\u8FF9\u8C61\uFF0C\u4E8E\u662F\u8FDB\u884C\u4E86\u4E00\u4E9B\u5916\u4EA4\u63A5\u89E6\u3002</p><p>\u5BF9\u5317\u5468\u800C\u8A00\uFF0C\u5916\u4EA4\u63A5\u89E6\u7684\u76EE\u7684\u7B80\u5355\u76F4\u63A5\uFF1A\u5C31\u662F\u4E3A\u56FD\u5185\u7684\u653F\u6CBB\u6539\u9769\u548C\u6597\u4E89\u8D62\u5F97\u65F6\u95F4\u3002\u800C\u5BF9\u5317\u9F50\u6765\u8BF4\uFF0C\u7F13\u548C\u540C\u6837\u4E5F\u5177\u6709\u73B0\u5B9E\u610F\u4E49\uFF1A\u5317\u9F50\u6B66\u5C06\u6743\u91CD\uFF0C\u5982\u679C\u5F00\u6218\uFF0C\u671D\u5EF7\u5BF9\u6B66\u5C06\u7684\u63A7\u5236\u80FD\u529B\u52BF\u5FC5\u51CF\u5F31\u3002\u5728\u8FD9\u65B9\u9762\uFF0C\u9AD8\u6C0F\u7684\u7687\u5E1D\u4EEC\u65E2\u6CA1\u6709\u8FD9\u4E2A\u80FD\u529B\uFF0C\u4E5F\u6CA1\u6709\u8FD9\u4E2A\u201C\u7CBE\u529B\u201D\u2014\u2014\u7956\u5148\u7559\u4E0B\u504C\u5927\u7684\u57FA\u4E1A\u662F\u62FF\u6765\u4EAB\u4E50\u7684\uFF0C\u53C8\u4E0D\u662F\u7528\u6765\u594B\u6597\u7684\uFF01</p><p>\u57FA\u4E8E\u4E24\u56FD\u5404\u81EA\u6240\u6000\u7684\u201C\u9B3C\u80CE\u201D\uFF0C\u51B2\u7A81\u53D8\u6210\u4E86\u6682\u65F6\u7684\uFF0C\u548C\u5E73\u6210\u4E3A\u4E86\u65F6\u4EE3\u7684\u201C\u4E3B\u6D41\u201D\u3002\u90A3\u4E9B\u77ED\u6682\u7684\u51B2\u7A81\uFF0C\u56E0\u4E3A\u6574\u4F53\u8D8B\u6696\u7684\u201C\u56FD\u9645\u5927\u73AF\u5883\u201D\uFF0C\u53CD\u5012\u663E\u5F97\u6709\u4E9B\u201C\u975E\u4E3B\u6D41\u201D\u3002</p><p>\u56E0\u6B64\uFF0C\u5728\u516C\u5143564\u5E74\uFF0C\u5317\u9F50\u624D\u9760\u7740\u201C\u5916\u4EA4\u201D\u4E0A\u7684\u4E00\u573A\u80DC\u5229\uFF0C\u6210\u529F\u907F\u514D\u4E86\u4E00\u573A\u5927\u6218\u3002</p><p>\u5F53\u65F6\uFF0C\u5317\u9F50\u56E0\u4E3A\u524D\u4E00\u5E74\u88AB\u5317\u5468\u56F4\u653B\u2014\u2014\u867D\u7136\u53EA\u662F\u865A\u60CA\u4E00\u573A\uFF0C\u5F00\u59CB\u5207\u5207\u5B9E\u5B9E\u5730\u8003\u8651\u8D77\u81EA\u6211\u9632\u536B\u95EE\u9898\u3002\u9664\u5F00\u51FF\u51B0\u8FD9\u4E2A\u529E\u6CD5\u5916\uFF0C\u5317\u9F50\u541B\u81E3\u4EEC\u8FD8\u60F3\u51FA\u4E86\u4E00\u4E2A\u201C\u91DC\u5E95\u62BD\u85AA\u201D\u7684\u529E\u6CD5\u3002</p><p>\u5317\u5468\u56FD\u5185\u6B64\u65F6\u662F\u5B87\u6587\u62A4\u5F53\u653F\u3002\u5B87\u6587\u62A4\u65E9\u5E74\u4E0E\u6BCD\u4EB2\u5931\u6563\uFF0C\u5176\u6BCD\u960E\u6C0F\u5982\u4ECA\u6B63\u597D\u5728\u5317\u9F50\u56FD\u5185\u3002\u5317\u9F50\u4EE5\u6B64\u4E3A\u5951\u673A\uFF0C\u9001\u960E\u6C0F\u56DE\u5317\u5468\uFF0C\u4FC3\u6210\u4E86\u4E24\u56FD\u77ED\u6682\u7684\u201C\u871C\u6708\u671F\u201D\u3002</p><p>\u8FD9\u6B21\u201C\u5916\u4EA4\u52AA\u529B\u201D\uFF0C\u4EA7\u751F\u7684\u6548\u679C\u7ACB\u7AFF\u89C1\u5F71\uFF1A\u7A81\u53A5\u56E0\u524D\u4E00\u5E74\u5728\u8054\u5408\u51FA\u5175\u4E2D\u906D\u9047\u5931\u8D25\uFF0C\u7B2C\u4E8C\u5E74\u53C8\u4E3B\u52A8\u6765\u7EA6\u5317\u5468\u518D\u6B21\u5171\u540C\u51FA\u51FB\u3002\u5B87\u6587\u62A4\u788D\u4E8E\u60C5\u9762\uFF0C\u4E0D\u5F97\u4E0D\u51FA\u5175\uFF0C\u4F46\u51FA\u5175\u4E0D\u5C3D\u529B\uFF0C\u6700\u7EC8\u5BFC\u81F4\u4E24\u56FD\u8054\u519B\u94E9\u7FBD\u800C\u5F52\u3002</p><p>\u81F3\u4E8E\u7B2C\u4E09\u6B21\uFF0C\u5219\u662F\u5728\u516C\u5143575\u5E74\uFF0C\u4E5F\u5C31\u662F\u5317\u5468\u53D1\u52A8\u706D\u5317\u9F50\u4E4B\u6218\u7684\u524D\u4E00\u5E74\uFF0C\u5B87\u6587\u9095\u9886\u5927\u519B\u4E1C\u5F81\uFF0C\u6240\u5411\u62AB\u9761\uFF0C\u4F46\u7531\u4E8E\u4E2D\u9014\u67D3\u75C5\uFF0C\u53EA\u5F97\u73ED\u5E08\u56DE\u671D\uFF0C\u5317\u9F50\u56E0\u6B64\u7EED\u547D\u4E00\u5E74\u3002</p>',71),r=[t];function n(s,a){return o(),e("div",null,r)}const i=p(d,[["render",n],["__file","90.html.vue"]]);export{i as default};
