import{_ as p}from"./_plugin-vue_export-helper.cdc0426e.js";import{o,c as e,f as c}from"./app.cd90e563.js";const d={},t=c('<h1 id="\u5B87\u6587\u9095" tabindex="-1"><a class="header-anchor" href="#\u5B87\u6587\u9095" aria-hidden="true">#</a> \u5B87\u6587\u9095</h1><blockquote><p>\u4E94\u6708\uFF0C\u5DF1\u4E11\uFF0C\u5468\u9AD8\u7956\u5E05\u8BF8\u519B\u4F10\u7A81\u53A5\u2026\u2026\u7678\u5DF3\uFF0C\u5E1D\u4E0D\u8C6B\uFF0C\u7559\u6B62\u4E91\u9633\u5BAB\uFF1B\u4E19\u7533\uFF0C\u8BCF\u505C\u8BF8\u519B\u2026\u2026\u516D\u6708\uFF0C\u4E01\u9149\u6714\uFF0C\u5E1D\u75BE\u751A\uFF0C\u8FD8\u957F\u5B89\uFF1B\u662F\u5915\u6B82\uFF0C\u5E74\u4E09\u5341\u516D\u3002</p></blockquote><p><strong>\u300A\u8D44\u6CBB\u901A\u9274\xB7\u5377\u4E00\u4E03\u4E09\xB7\u9648\u7EAA\u4E03\u300B</strong></p><p><strong>\u5B87\u6587\u9095\u5728\u5386\u53F2\u4E2D\u540D\u58F0\u4E0D\u663E\uFF0C\u4F46\u5B9E\u9645\u4E0A\uFF0C\u5374\u662F\u6C49\u5510\u4E4B\u95F4\u627F\u524D\u542F\u540E\u7684\u5E1D\u738B\u3002</strong></p><p>\u897F\u65B9\u6C49\u5B66\u754C\u8BA4\u4E3A\uFF0C<code>\u968B\u6587\u5E1D\u6768\u575A</code>\u662F\u4E2D\u56FD\u5386\u53F2\u4E2D\u4EC5\u6B21\u4E8E<code>\u79E6\u59CB\u7687</code>\u7684\u4F1F\u5927\u541B\u738B\u2014\u2014\u8FD9\u4E3B\u8981\u662F\u9488\u5BF9\u4E8E\u968B\u6587\u5E1D\u7ED3\u675F\u4E86\u81EA\u6C49\u672B\u4EE5\u6765\u56DB\u767E\u4F59\u5E74\u7684\u52A8\u4E71\uFF0C\u4F7F\u4E2D\u534E\u5E1D\u56FD\u91CD\u5F52\u4E00\u7EDF\uFF0C\u6CA1\u6709\u590D\u5236\u897F\u65B9\u7F57\u9A6C\u5E1D\u56FD\u8870\u8D25\u540E\u7684\u5206\u88C2\u3002</p><p>\u4F46\u4E8B\u5B9E\u4E0A\uFF0C\u6768\u575A\u7684\u8FD9\u4EFD\u529F\u52B3\uFF0C\u672C\u6765\u662F\u5B87\u6587\u9095\u7684\u3002</p><p>\u6216\u8005\u53EF\u4EE5\u8FD9\u4E48\u8BF4\uFF0C\u5728\u91CD\u5F52\u4E00\u7EDF\u7684\u4E00\u767E\u91CC\u8DEF\u4E0A\uFF0C\u5B87\u6587\u9095\u5DF2\u7ECF\u8D70\u5B8C\u4E86\u524D\u9762\u4E5D\u5341\u91CC\u3002\u5269\u4E0B\u6768\u575A\u8D70\u7684\u5341\u91CC\uFF0C\u4E0D\u8FC7\u662F\u6C34\u5230\u6E20\u6210\uFF0C\u987A\u5229\u6210\u7AE0\u3002</p><p>\u53EA\u4E0D\u8FC7\uFF0C\u56E0\u4E3A\u5B87\u6587\u9095\u610F\u5916\u65E9\u6B7B\uFF0C\u4EE5\u53CA\u4ED6\u8EAB\u540E\u7684\u4E00\u7CFB\u5217\u4EBA\u4E8B\u52A8\u8361\uFF0C\u624D\u8BA9\u6768\u575A\u654F\u9510\u5730\u628A\u63E1\u4F4F\u673A\u4F1A\uFF0C\u5B8C\u6210\u4E86\u7687\u6743\u7985\u4EE3\u3002\u5176\u540E\uFF0C\u6768\u575A\u6B65\u5B87\u6587\u9095\u524D\u8DEF\uFF0C\u8D70\u5B8C\u6700\u540E\u4E00\u7A0B\uFF0C\u6210\u5C31\u4E86\u81EA\u5DF1\u5728\u5386\u53F2\u4E2D\u7684\u4EE4\u540D\u3002</p><p>\u800C\u4ECA\u5929\uFF0C\u4E3B\u8981\u8981\u6765\u4ECB\u7ECD\u5B87\u6587\u9095\u5728\u653B\u706D\u5317\u9F50\u4E4B\u540E\u7684\u4E00\u4E2A\u9009\u62E9\u2014\u2014\u4E00\u4E2A\u4ED6\u4E0E\u6768\u575A\u5B8C\u5168\u4E0D\u540C\u7684\u9009\u62E9\u3002</p><p>\u516C\u5143577\u5E74\uFF0C<code>\u5317\u5468</code>\u5927\u519B\u5728\u5B87\u6587\u9095\u7684\u4EB2\u7387\u4E4B\u4E0B\uFF0C\u653B\u706D<code>\u5317\u9F50</code>\u3002\u5206\u79BB\u52A8\u8361\u957F\u8FBE\u8FD1\u534A\u4E2A\u4E16\u7EAA\u7684\u5317\u4E2D\u56FD\uFF0C\u7EC8\u4E8E\u53C8\u5408\u4E8C\u4E3A\u4E00\u3002</p><p>\u4F46\u5728\u653B\u706D\u5317\u9F50\u540E\u7684\u7B2C\u4E8C\u5E74\uFF0C\u5B87\u6587\u9095\u505A\u51FA\u7684\u51B3\u5B9A\u5374\u662F\uFF1A<strong>\u5148\u51FA\u5175\u653B\u6253\u6614\u65E5\u7684\u76DF\u53CB\uFF0C\u7A81\u53A5\u3002</strong></p><p>\u4E4B\u540E\u7684\u5386\u53F2\u8FDB\u7A0B\u6709\u4E9B\u6025\u8F6C\u76F4\u4E0B\uFF1A\u5B87\u6587\u9095\u5728\u5317\u4F10\u51FA\u5175\u540E\u4E0D\u4E45\u7A81\u7136\u60A3\u75C5\uFF0C\u800C\u4E14\u4ECE\u53F2\u6599\u7684\u8BB0\u8F7D\u6765\u770B\uFF0C\u75C5\u60C5\u8FD8\u76F8\u5F53\u7684\u91CD\uFF0C\u5728\u6025\u51B2\u51B2\u5730\u8D76\u56DE\u957F\u5B89\u540E\uFF0C\u5F53\u591C\u5373\u4FBF\u53BB\u4E16\u3002</p><p>\u4E0D\u8FC7\u8FD9\u5E76\u4E0D\u59A8\u788D\u6211\u4EEC\u63D0\u51FA\u7591\u60D1\uFF1A**\u5982\u679C\u7EAF\u4EE5\u7EDF\u4E00\u7684\u89D2\u5EA6\u6765\u770B\uFF0C\u90A3\u4E48\u5B87\u6587\u9095<code>\u5148\u653B\u7A81\u53A5</code>\u4F3C\u4E4E\u6709\u4E9B\u672C\u672B\u5012\u7F6E\u4E86\u3002**\u5F81\u7A81\u53A5\u5728\u5F53\u65F6\u770B\u6765\uFF0C\u4F3C\u4E4E\u7B97\u4E0D\u4E0A\u4EC0\u4E48\u6025\u4E8B\uFF0C\u76F8\u53CD\uFF0C\u5F53\u65F6\u7684\u4E2D\u534E\u6545\u571F\u8FD8\u6709<code>\u897F\u6881</code>\u3001<code>\u5357\u9648</code>\u672A\u706D\u3002</p><p>\u5B87\u6587\u9095\u5982\u6B64\u51B3\u7B56\uFF0C\u662F\u4E0D\u662F\u6709\u70B9\u820D\u672C\u9010\u672B\u4E86\uFF1F</p><p>\u4F46\u5176\u5B9E\uFF0C\u8FD9\u79CD\u89C2\u5FF5\u662F\u73B0\u4EE3\u4EBA\u7AD9\u5728\u5982\u4ECA\u7684\u5F53\u7136\u7ACB\u573A\u4E0A\u6765\u8003\u8651\u7684\u3002\u6211\u4EEC\u8981\u60F3\u89E3\u9664\u7591\u60D1\uFF0C\u5C31\u9700\u8981\u8BBE\u8EAB\u5904\u5730\u7684\u7AD9\u5728\u5B87\u6587\u9095\u7684\u7ACB\u573A\u4E0A\uFF0C\u6765\u770B\u770B\u5B87\u6587\u9095\u662F\u600E\u4E48\u60F3\u95EE\u9898\u7684\u3002</p><p>\u5728\u8BDB\u6740\u6743\u81E3<code>\u5B87\u6587\u62A4</code>\u4EB2\u653F\u540E\uFF0C\u5B87\u6587\u9095\u5148\u7ECF\u8FC7\u4E86\u51E0\u5E74\u7684\u5185\u653F\u5EFA\u8BBE\uFF0C\u7136\u540E\u624D\u5C06\u89C6\u7EBF\u8F6C\u5411\u56FD\u5916\u3002\u4EE5\u5317\u5468\u5F53\u65F6\u7684\u89D2\u5EA6\u6765\u770B\uFF0C\u5929\u4E0B\u52BF\u529B\u4E2D\uFF0C<strong><code>\u897F\u6881</code>\u662F\u9644\u5EB8\uFF0C<code>\u5357\u9648</code>\u662F\u76DF\u53CB\uFF0C<code>\u5317\u9F50</code>\u662F\u654C\u4EBA</strong>\u3002</p><p>\u5317\u5468\u5F53\u7136\u4F1A\u9009\u62E9\u5148\u5BF9\u4ED8\u7F20\u6597\u4E86\u56DB\u5341\u5E74\u4E4B\u4E45\u7684\u5317\u9F50\u3002</p><p>\u800C\u5728\u5317\u9F50\u88AB\u653B\u706D\u540E\uFF0C\u5317\u5468\u4E0E\u5317\u8FB9\u7684\u7A81\u53A5\u4E00\u4E0B\u5B50\u5C31\u6CA1\u6709\u4E86\u7F13\u51B2\u3002</p><p>\u867D\u7136\u8BF4\u5317\u5468\u4E0E\u7A81\u53A5\u662F\u4E00\u5B9A\u7A0B\u5EA6\u4E0A\u7684\u76DF\u53CB\uFF0C\u4F46\u53CC\u65B9\u7684\u5173\u7CFB\u5176\u5B9E\u6BD4\u8F83\u590D\u6742\uFF1A\u5F53\u5E74\u7A81\u53A5\u5D1B\u8D77\uFF0C\u5728\u8499\u53E4\u8349\u539F\u4E0A\u4E0E\u67D4\u7136\u4E89\u9738\uFF0C\u5317\u5468\u4E0E\u5317\u9F50\u5404\u7AD9\u4E00\u65B9\uFF0C\u5317\u9F50\u4E0E\u67D4\u7136\u8054\u59FB\uFF0C\u5317\u5468\u5219\u4E0E\u7A81\u53A5\u7ED3\u76DF\uFF0C\u6700\u7EC8\u7A81\u53A5\u83B7\u80DC\u3002\u540E\u6765\uFF0C\u56E0\u4E3A\u5317\u9F50\u7684\u654C\u5BF9\u6001\u5EA6\uFF0C\u7A81\u53A5\u8FD8\u66FE\u7EA6\u5317\u5468\u5171\u540C\u653B\u6253\u5317\u9F50\u2014\u2014\u8FD9\u65F6\u5019\u5317\u5468\u56FD\u5185\u8FD8\u662F\u5B87\u6587\u62A4\u5F53\u653F\u3002</p><p>\u4F46\u662F\uFF0C\u7A81\u53A5\u81EA\u4ECE\u6210\u4E3A\u8349\u539F\u9738\u4E3B\u540E\uFF0C\u5BF9\u4E2D\u539F\u56FD\u5BB6\u7684\u6001\u5EA6\u5C31\u5F00\u59CB\u6709\u6240\u8F6C\u53D8\uFF0C\u4E0D\u518D\u5728\u628A\u5B9D\u5168\u90FD\u62BC\u5317\u5468\u8EAB\u4E0A\uFF0C\u800C\u662F\u5DE6\u53F3\u9022\u6E90\uFF0C\u4E24\u5934\u901A\u5403\u3002</p><p>\u8FD9\u5728\u5B87\u6587\u9095\u770B\u6765\uFF0C\u5C31\u662F\u80CC\u53DB\u3002</p><p>\u5F53\u7136\uFF0C\u56FD\u5BB6\u4E4B\u95F4\u7684\u5229\u76CA\u51B2\u7A81\uFF0C\u4E0D\u80FD\u7B80\u5355\u7528\u4E2A\u4EBA\u597D\u6076\u7684\u6846\u67B6\u5957\u7528\u3002\u800C\u5B87\u6587\u9095\u5BF9\u7A81\u53A5\u7684\u6001\u5EA6\uFF0C\u8FD8\u4E0E\u5F53\u65F6\u7684\u5929\u4E0B\u5927\u5C40\u6709\u5173\u3002</p><p>\u5317\u5468\u5728\u7EDF\u4E00\u5317\u4E2D\u56FD\u4EE5\u540E\uFF0C\u63A5\u4E0B\u6765\u5175\u950B\u6240\u6307\uFF0C\u5C31\u8981\u505A\u9009\u62E9\u9898\u4E86\uFF1A<strong>\u5148\u5F80<code>\u5357</code>\uFF0C\u8FD8\u662F\u5148\u5F80<code>\u5317</code>\uFF1F</strong></p><p>\u5F80\u5357\uFF0C\u662F\u5357\u9648\u3002\u6B64\u65F6\u7684\u5357\u9648\u662F<code>\u9648\u5BA3\u5E1D\u9648\u987C</code>\u5728\u4F4D\uFF0C\u867D\u7136\u653F\u6CBB\u8FD8\u4E0D\u50CF\u540E\u6765\u7684<code>\u540E\u4E3B\u9648\u53D4\u5B9D</code>\u65F6\u671F\u90A3\u4E48\u660F\u6697\uFF0C\u4F46\u4E24\u56FD\u7684\u5B9E\u529B\u5BF9\u6BD4\u5DF2\u7ECF\u5341\u5206\u660E\u663E\uFF0C\u5317\u5468\u6B64\u65F6\u5357\u4E0B\uFF0C\u5357\u9648\u4E0D\u89C1\u5F97\u80FD\u652F\u6301\u591A\u4E45\u3002</p><p>\u5F80\u5317\uFF0C\u5219\u662F\u7A81\u53A5\u3002\u7A81\u53A5\u7684\u5B9E\u529B\u6BD4\u4E4B\u5357\u9648\uFF0C\u8981\u5F3A\u4E0A\u592A\u591A\u3002\u800C\u4E14\u585E\u5916\u82E6\u5BD2\uFF0C\u5317\u5468\u7684\u7EDF\u6CBB\u8005\u867D\u7136\u662F\u9C9C\u5351\u4EBA\uFF0C\u4F46\u65E9\u5DF2\u6C49\u5316\uFF0C\u585E\u5916\u5DF2\u7ECF\u4E0D\u9002\u5408\u4ED6\u4EEC\u5C45\u4F4F\u3002</p><p>\u603B\u4F53\u6765\u770B\uFF0C<strong>\u6253\u7A81\u53A5\u662F\u5178\u578B\u7684\u6295\u8D44\u6536\u76CA\u4E0D\u6210\u6B63\u6BD4\u3002</strong></p><p>\u8FD9\u6837\u8FDB\u4E00\u6B65\u6982\u62EC\u4E4B\u540E\uFF0C\u4F60\u4F1A\u53D1\u73B0\uFF0C\u95EE\u9898\u5176\u5B9E\u5C31\u662F\uFF1A<strong><code>\u5148\u96BE\u540E\u6613</code>\uFF0C\u8FD8\u662F<code>\u5148\u6613\u540E\u96BE</code>\uFF1F</strong></p><p>\u8FD9\u4E2A\u95EE\u9898\uFF0C\u5728\u51E0\u767E\u5E74\u4EE5\u540E\uFF0C\u8FD8\u4F1A\u88AB\u53E6\u4E00\u4E2A\u4EBA\u9047\u4E0A\uFF1A<strong><code>\u540E\u5468</code>\u4E16\u5B97\u67F4\u8363\u3002</strong></p><p>**\u67F4\u8363\u505A\u4E86\u4E0E\u5B87\u6587\u9095\u4E00\u6837\u7684\u9009\u62E9\uFF1A\u5148\u96BE\u540E\u6613\uFF0C\u9886\u5175\u5317\u4E0A\u4E0E\u5951\u4E39\u5F00\u6218\u3002**\u800C\u7ED3\u679C\u4E5F\u4E0E\u5B87\u6587\u9095\u60CA\u4EBA\u5730\u76F8\u4F3C\uFF1A\u67F4\u8363\u4E5F\u5728\u9014\u4E2D\u67D3\u75C5\uFF0C\u968F\u5373\u75C5\u4EA1\u3002\u4E0D\u4E45\u540E\uFF0C<code>\u8D75\u5321\u80E4</code>\u7BE1\u4F4D\uFF0C<code>\u5317\u5B8B</code>\u5EFA\u7ACB\u3002</p><p>\u6B64\u540E\uFF0C\u5317\u5B8B\u6539\u53D8\u540E\u5468\u201C\u5148\u5317\u540E\u5357\u201D\u7684\u7B56\u7565\uFF0C\u8F6C\u800C\u5411\u5357\uFF0C\u5148\u5E73\u5B9A\u5357\u65B9\u5404\u56FD\uFF0C\u7EDF\u4E00\u4E2D\u539F\u2014\u2014\u8FD9\u53C8\u4E0E\u5317\u5468\u548C\u968B\u671D\u7684\u60C5\u5F62\u9AD8\u5EA6\u76F8\u4F3C\u3002\u6768\u575A\u5728\u7BE1\u4E86\u5317\u5468\u7684\u5929\u4E0B\u540E\uFF0C\u505A\u51FA\u7684\u9009\u62E9\u4E0E\u8D75\u5321\u80E4\u5927\u4F53\u65E0\u5DEE\u3002</p><p>\u5386\u53F2\u4E2D\u771F\u5B9E\u7684\u60C5\u51B5\u662F\uFF1A\u4E0D\u7BA1\u662F\u6B64\u524D\u8FD8\u662F\u6B64\u540E\uFF0C\u7AD9\u5728\u4E2D\u539F\u6C49\u4EBA\u56FD\u5BB6\u7684\u89D2\u5EA6\u770B\u8FC7\u53BB\uFF0C\u8349\u539F\u6C11\u65CF\u90FD\u662F\u6C38\u8FDC\u201C\u6253\u4E0D\u6B7B\u201D\u7684\uFF0C\u706D\u4E86\u4E00\u4E2A\u8FD8\u4F1A\u6709\u4E0B\u4E00\u4E2A\uFF0C\u5308\u5974\u3001\u9C9C\u5351\u3001\u67D4\u7136\u3001\u7A81\u53A5\u3001\u5951\u4E39\u3001\u5973\u771F\u3001\u8499\u53E4......\u7B49\u7B49\u7B49\u7B49\uFF0C\u65E0\u4E0D\u8BC1\u660E\u7740\u8FD9\u4E00\u70B9\u3002\u800C\u4E14\u957F\u57CE\u4EE5\u5916\uFF0C\u65E0\u6CD5\u8015\u79CD\uFF0C\u96BE\u4EE5\u5B88\u5FA1\u3002\u6240\u4EE5\uFF0C\u4E0E\u5176\u82B1\u8D39\u5DE8\u5927\uFF0C\u8FD8\u4E0D\u5982\u5BFB\u6C42\u4E00\u79CD\u4E0E\u8349\u539F\u9738\u4E3B\u5171\u5B58\u7684\u65B9\u5F0F\u3002</p><p>\u2014\u2014\u8FD9\u79CD\u5206\u6790\uFF0C\u8FD8\u662F\u6BD4\u8F83\u4E50\u89C2\u7684\u4E00\u79CD\u4E86\u3002\u5176\u6210\u7ACB\u7684\u57FA\u7840\uFF0C\u662F\u4E2D\u539F\u738B\u671D\u80FD\u591F\u6253\u5F97\u8FC7\u8349\u539F\u6C11\u65CF\u3002</p><p>\u5982\u679C\u8D38\u7136\u5F00\u6218\uFF0C\u53C8\u6253\u4E0D\u8FC7\u5462\uFF1F</p><p><code>\u660E\u82F1\u5B97</code>\u5C31\u7ED9\u540E\u6765\u4EBA\u4E0A\u4E86\u751F\u52A8\u4E00\u8BFE\u3002</p><p>\u6240\u4EE5\uFF0C\u6240\u8C13\u201C\u6C49\u5BB6\u9752\u53F2\u4E0A\uFF0C\u8BA1\u62D9\u662F\u548C\u4EB2\u201D\u4E4B\u7C7B\u7684\u8BDD\uFF0C\u5B8C\u5168\u5C31\u662F\u4E66\u751F\u4E4B\u8BBA\uFF0C\u4E0D\u5F53\u5BB6\u4E0D\u77E5\u67F4\u7C73\u8D35\u3002\u8D44\u6E90\u662F\u6709\u9650\u7684\uFF0C\u82B1\u5728\u5BF9\u5916\u6218\u4E89\u4E0A\uFF0C\u5C31\u610F\u5473\u7740\u56FD\u5185\u7684\u8282\u8863\u7F29\u98DF\uFF0C\u6A2A\u5F81\u66B4\u655B\u3002\u6C49\u6B66\u5E1D\u5F0F\u7684\u5F00\u7586\u62D3\u571F\u56FA\u7136\u5FEB\u610F\uFF0C\u4F46\u4EE3\u4EF7\u5374\u662F\u6237\u53E3\u51CF\u534A\uFF0C\u8FD9\u5728\u4EFB\u4F55\u4E00\u4E2A\u6B63\u5E38\u7684\u5E1D\u738B\u770B\u6765\uFF0C\u65E0\u8BBA\u5982\u4F55\u90FD\u662F\u4E0D\u503C\u5F97\u7684\u3002</p><p>\u73B0\u5B9E\u4E2D\uFF0C\u5728\u591A\u6570\u60C5\u51B5\u4E0B\uFF0C\u4F60\u4E0D\u80FD\u6307\u671B\u6240\u6709\u7684\u7687\u5E1D\u90FD\u50CF<code>\u6C49\u6B66\u5E1D</code>\u3001<code>\u5510\u592A\u5B97</code>\u3001<code>\u660E\u6210\u7956</code>\u4E00\u6837\uFF0C\u6216\u8005\u6709\u8D85\u5353\u7684\u6BC5\u529B\uFF0C\u6216\u8005\u6709\u7528\u4EBA\u7684\u9B44\u529B\uFF0C\u53C8\u6216\u8005\u6709\u5F81\u6218\u7684\u80FD\u529B\u3002\u7EDD\u5927\u90E8\u5206\u7687\u5E1D\uFF0C\u4E0D\u5177\u5907\u4EE5\u4E0A\u4E09\u8005\u4E4B\u4E00\uFF0C\u56E0\u6B64\u5BF9\u4ED6\u4EEC\u6765\u8BF4\uFF0C\u8D38\u7136\u51FA\u5F81\u7EDD\u4E0D\u662F\u4E00\u4E2A\u597D\u9009\u62E9\u3002</p><p>\u5C24\u5176\u662F\u6B64\u65F6\u7684\u7A81\u53A5\uFF0C\u521A\u521A\u6D88\u706D\u4E86\u67D4\u7136\uFF0C\u6B63\u5904\u4E8E\u5B9E\u529B\u7684\u4E0A\u5347\u671F\u3002</p><p>\u4F46\u662F\uFF0C\u4EE5\u4E0A\u8FD9\u4E9B\u53EA\u662F\u201C\u4E00\u822C\u60C5\u51B5\u201D\u3002\u5BF9\u5B87\u6587\u9095\u800C\u8A00\uFF0C\u9009\u62E9\u53C8\u6709\u4E0D\u540C\u3002</p><p><strong>\u5317\u4F10\u4E4B\u96BE\uFF0C\u4E8E\u522B\u4EBA\u662F\u963B\u529B\uFF1B\u4E8E\u4ED6\uFF0C\u5374\u662F\u8D70\u4E0A\u8FD9\u6761\u8DEF\u7684\u6700\u4E3B\u8981\u539F\u56E0\u3002</strong></p><p>\u7AD9\u5728\u5B87\u6587\u9095\u7684\u89D2\u5EA6\u770B\u8FC7\u53BB\uFF0C\u653B\u706D\u5317\u9F50\u4EE5\u540E\u79FB\u5E08\u653B\u7A81\u53A5\uFF0C\u65E2\u662F\u5BF9\u65E2\u5F80\u7A81\u53A5\u201C\u80CC\u53DB\u201D\u884C\u4E3A\u7684\u62A5\u590D\uFF0C<strong>\u53C8\u662F\u8D81\u7740\u519B\u961F\u58EB\u6C14\u5982\u8679\uFF0C\u53EF\u4EE5\u6025\u51B2\u786C\u4E0A\u7684\u7EDD\u597D\u673A\u4F1A\u3002</strong></p><p>\u5426\u5219\uFF0C\u7B49\u4ED6\u79FB\u5E08\u5411\u5357\uFF0C\u653B\u706D\u4E86\u5357\u9648\uFF0C\u628A\u6C5F\u5357\u818F\u8174\u4E4B\u5730\u5C3D\u5165\u56CA\u4E2D\uFF0C\u5230\u65F6\u5019\uFF0C\u53C8\u6709\u591A\u5C11\u4EBA\u8FD8\u613F\u610F\u91CD\u62AB\u6218\u7532\uFF0C\u518D\u8D74\u5F81\u7A0B\uFF0C\u5230\u585E\u5317\u82E6\u5BD2\u4E4B\u5730\u53AE\u6740\u5462\uFF1F</p><p>**\u6700\u5927\u7684\u679C\u5B9E\u8981\u7559\u5230\u6700\u540E\uFF0C\u8FD9\u6837\u624D\u6709\u6FC0\u52B1\u4EBA\u5FC3\u3001\u9A71\u4F7F\u7B56\u52A8\u7684\u4F5C\u7528\u3002**\u5357\u9648\u53EA\u6709\u653E\u5728\u7A81\u53A5\u4E4B\u540E\uFF0C\u624D\u6709\u9F13\u821E\u5C06\u58EB\u4E4B\u529F\u3002</p><p>\u5426\u5219\uFF0C\u9014\u4E2D\u5C31\u5DF2\u7ECF\u628A\u6700\u5927\u7684\u5B9D\u7BB1\u5F00\u4E86\uFF0C\u8C01\u8FD8\u6709\u5FC3\u601D\u53BB\u6253\u5927boss\uFF1F</p><p>\u800C\u4E14\uFF0C\u4ECE\u53E6\u4E00\u4E2A\u89D2\u5EA6\u770B\u8FC7\u53BB\uFF0C\u5BF9\u7A81\u53A5\u4E5F\u6709\u5FC5\u4F10\u7684\u7406\u7531\u3002\u4E2D\u539F\u5206\u88C2\u8FD9\u4E9B\u5E74\uFF0C\u7A81\u53A5\u5DF2\u7ECF\u653B\u706D\u67D4\u7136\uFF0C\u4E0D\u65AD\u5750\u5927\u3002\u5982\u4ECA\u5B87\u6587\u9095\u8D81\u7740\u56FD\u529B\u5F3A\u76DB\uFF0C\u6C14\u52BF\u5982\u8679\uFF0C\u53EF\u4EE5\u4E0E\u7A81\u53A5\u4E89\u96C4\u6C99\u573A\u3002<strong>\u8FD9\u4EF6\u5927\u4E8B\u5982\u679C\u4E0D\u5728\u81EA\u5DF1\u624B\u91CC\u505A\u5B8C\uFF0C\u7559\u7ED9\u540E\u4E16\u5B50\u5B59\uFF0C\u53EA\u6015\u4F1A\u5B9E\u529B\u5BF9\u6BD4\u60AC\u6B8A\uFF0C\u5F81\u4F10\u65E0\u529B\u3002</strong></p><p>\u5728\u5B87\u6587\u9095\u7684\u89C4\u5212\u91CC\uFF0C\u7A81\u53A5\u662F\u4E00\u4E2A\u654C\u4EBA\uFF0C\u5357\u9648\u4E5F\u662F\u4E00\u4E2A\u654C\u4EBA\uFF0C\u54EA\u4E00\u4E2A\u654C\u4EBA\u8BE5\u4F18\u5148\u8003\u8651\uFF0C\u662F\u8003\u9A8C\u4E00\u4E2A\u6301\u56FD\u8005\u7684\u6218\u7565\u7D20\u8D28\u7684\u5927\u95EE\u9898\u3002</p><p>\u90A3\u4E3A\u4EC0\u4E48\u6768\u575A\u548C\u8D75\u5321\u80E4\u53C8\u9009\u62E9\u4E86\u4E0D\u4E00\u6837\u7684\u8DEF\u5462\uFF1F</p><p>\u5E76\u4E0D\u662F\u56E0\u4E3A\u4E24\u4EBA\u7684\u5927\u5C40\u89C6\u91CE\u4E0D\u5982\u5B87\u6587\u9095\u6216\u8005\u67F4\u8363\uFF0C\u800C\u662F\u73B0\u5B9E\u4F7F\u7136\uFF1A<strong>\u968B\u4E0E\u5B8B\u5F97\u56FD\u4E0D\u6B63\uFF0C\u653F\u6CBB\u4E0A\u7684\u4E0D\u7A33\u56FA\uFF0C\u4F7F\u5F97\u6768\u575A\u4E0E\u8D75\u5321\u80E4\u4E0D\u80FD\u8F7B\u6613\u5192\u9669</strong>\u2014\u2014\u8FD9\u4E00\u70B9\uFF0C\u662F\u4E24\u4EBA\u505A\u6240\u6709\u51B3\u7B56\u7684\u6700\u5927\u524D\u63D0\u3002</p><p>\u5728\u8FD9\u79CD\u60C5\u51B5\u4E0B\uFF0C\u67FF\u5B50\u5148\u62E3\u8F6F\u7684\u634F\uFF0C\u81EA\u7136\u5C31\u6210\u4E86\u4E24\u4E2A\u4EBA\u7684\u6700\u4F18\u89E3\u3002\u5357\u65B9\u5BCC\u9976\u800C\u53EF\u6B3A\uFF0C\u653B\u706D\u4EE5\u540E\uFF0C\u4E0D\u4EC5\u80FD\u5145\u5B9E\u56FD\u529B\uFF0C\u8FD8\u80FD\u5927\u5927\u5927\u5927\u5927\u5927\u589E\u52A0\u653F\u6743\u7684\u5408\u6CD5\u6027\u3002</p><p>\u800C\u5F80\u5317\u6253\u5462\uFF1F\u80DC\u4E86\u5C31\u7B97\u4E86\uFF0C\u4E00\u65E6\u8D25\u4E86\uFF0C\u5C31\u4E07\u52AB\u4E0D\u590D\u4E86\u3002</p><p>\u540E\u4E16\u8BC4\u4EF7\u5317\u5B8B\u4EA1\u56FD\uFF0C\u8BA4\u4E3A\u8D75\u5321\u80E4\u3001\u8D75\u666E\u541B\u81E3\u5728\u9009\u62E9\u76EE\u6807\u65F6\uFF0C\u5148\u6613\u540E\u96BE\u7684\u7B56\u7565\u662F\u4E00\u5927\u539F\u56E0\u3002\u5357\u65B9\u5DF2\u5E73\uFF0C\u5175\u7532\u5DF2\u8001\uFF0C\u5951\u4E39\u672A\u706D\uFF0C\u8FD9\u662F\u4E3A\u5B50\u5B59\u5F92\u7559\u9057\u61BE\u3002<strong>\u4F46\u8FD9\u79CD\u8BBA\u8FF0\uFF0C\u5176\u5B9E\u662F\u4EE5\u4E8B\u540E\u770B\u4E8B\u5148\uFF0C\u5929\u7136\u5730\u5047\u8BBE\u8D75\u5321\u80E4\u5317\u4F10\u80FD\u591F\u6210\u529F\u3002</strong></p><p>\u53EF\u4E07\u4E00\u5931\u8D25\u4E86\u5462\uFF1F</p><p>\u5982\u679C\u8D75\u5321\u80E4\u5F53\u5E74\u5F81\u5951\u4E39\u5931\u8D25\uFF0C\u53EF\u80FD\u201C\u4E94\u4EE3\u201D\u5C31\u8981\u53D8\u6210\u201C\u516D\u4EE3\u201D\u201C\u4E03\u4EE3\u201D\u4E86\u3002</p><p><strong>\u6301\u56FD\u8005\u505A\u51B3\u7B56\uFF0C\u4E0D\u5E94\u4EE5\u5192\u9669\u4E3A\u80FD\u4E8B\uFF0C\u800C\u662F\u8981\u5728\u6709\u7EA6\u675F\u7684\u6761\u4EF6\u4E0B\uFF0C\u4E89\u53D6\u6548\u679C\u6700\u4F18\u3002</strong></p><p>\u5317\u5468\u4E0E\u968B\u7684\u7EA6\u675F\u4E0D\u540C\u3002\u540C\u6837\uFF0C\u540E\u5468\u4E0E\u5317\u5B8B\u7684\u7EA6\u675F\u4E5F\u4E0D\u540C\u3002</p><p>\u53F2\u6599\u8BB0\u8F7D\uFF0C\u5B87\u6587\u9095\u5F81\u4F10\u7A81\u53A5\u7684\u76F4\u63A5\u8BF1\u56E0\uFF0C\u662F\u7A81\u53A5\u63A5\u7EB3\u5317\u9F50\u964D\u81E3\uFF0C\u53C8\u5C61\u6B21\u6270\u8FB9\u3002\u5B87\u6587\u9095\u56E0\u6B64\u5927\u6012\uFF0C\u51B3\u5B9A\u51FA\u5F81\u3002</p><p>\u6211\u4EEC\u53EA\u8981\u60F3\u6E05\u695A\u4E86\u5B87\u6587\u9095\u7684\u5927\u5C40\u89C2\uFF0C\u5C31\u4F1A\u77E5\u9053\u8FD9\u53EA\u662F\u5B87\u6587\u9095\u7684\u501F\u53E3\u3002\u7A81\u53A5\u4E4B\u524D\u5C31\u4E24\u9762\u4E09\u5200\u8FC7\uFF0C\u5B87\u6587\u9095\u4E5F\u5FCD\u4E86\uFF0C\u600E\u4E48\u73B0\u5728\u7A81\u7136\u5C31\u5FCD\u4E0D\u4E86\u4E86\u5462\uFF1F</p><p>\u4E00\u6837\u7684\u4E8B\uFF0C\u6768\u575A\u4E5F\u78B0\u5230\u8FC7\u3002\u516C\u5143583\u5E74\uFF0C\u56E0\u4E3A\u7A81\u53A5\u5C61\u6B21\u6270\u8FB9\uFF0C\u6768\u575A\u5927\u6012\uFF0C\u547D\u5927\u5C06\u9886\u5175\u51FA\u5F81\u3002\u4F46\u7ED3\u679C\uFF0C\u4EC5\u4EC5\u662F\u628A\u7A81\u53A5\u8D76\u56DE\u53BB\u5C31\u56DE\u6765\u4E86\u2014\u2014\u56E0\u4E3A\u6768\u575A\u8FD8\u6709\u66F4\u91CD\u8981\u7684\u4E8B\u8981\u505A\u3002\u6B64\u65F6\u968B\u671D\u5EFA\u7ACB\u4EC5\u4EC5\u4E09\u5E74\uFF0C\u56FD\u5185\u653F\u5C40\u4E0D\u7A33\uFF0C\u5357\u9648\u5C1A\u5728\uFF0C\u6768\u575A\u4E00\u65F6\u987E\u4E0D\u4E0A\u7A81\u53A5\u7684\u75A5\u7663\u4E4B\u75BE\u3002</p><p>\u4F46\u5341\u51E0\u5E74\u540E\uFF0C\u60C5\u52BF\u5927\u53D8\u3002\u8FD9\u4E9B\u5E74\u6765\uFF0C\u968B\u706D\u9648\uFF0C\u4E00\u7EDF\u4E2D\u56FD\uFF0C\u56FD\u529B\u7A7A\u524D\u5F3A\u76DB\u3002\u800C\u540C\u65F6\uFF0C\u7A81\u53A5\u5185\u90E8\u653F\u5C40\u4E0D\u7A33\u3002\u6768\u575A\u547D\u957F\u5B59\u665F\u3001\u88F4\u77E9\u5165\u7A81\u53A5\u79BB\u95F4\uFF0C\u5F3A\u76DB\u4E00\u65F6\u7684\u8349\u539F\u9738\u4E3B\u7ADF\u7136\u5206\u88C2\u4E3A\u4E24\u90E8\uFF0C\u5B9E\u529B\u5927\u635F\u3002\u8349\u539F\u53C8\u56DE\u5230\u4E86\u4E89\u9738\u7684\u5C40\u9762\u3002</p><p>\u8FD9\u624D\u662F\u4E2D\u539F\u738B\u671D\u6700\u613F\u610F\u770B\u5230\u7684\u7ED3\u679C\u3002</p>',59),r=[t];function s(n,g){return o(),e("div",null,r)}const i=p(d,[["render",s],["__file","93.html.vue"]]);export{i as default};
