import{_ as o}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as p,c as e,f as t}from"./app.52cb12a8.js";const r={},c=t('<h1 id="\u5357\u5317\u671D\u7EC8\u7ED3" tabindex="-1"><a class="header-anchor" href="#\u5357\u5317\u671D\u7EC8\u7ED3" aria-hidden="true">#</a> \u5357\u5317\u671D\u7EC8\u7ED3</h1><blockquote><p>\u4E94\u6708\uFF0C\u5DF1\u4E11\uFF0C\u5468\u9AD8\u7956\u5E05\u8BF8\u519B\u4F10\u7A81\u53A5\u2026\u2026\u7678\u5DF3\uFF0C\u5E1D\u4E0D\u8C6B\uFF0C\u7559\u6B62\u4E91\u9633\u5BAB\uFF1B\u4E19\u7533\uFF0C\u8BCF\u505C\u8BF8\u519B\u2026\u2026\u516D\u6708\uFF0C\u4E01\u9149\u6714\uFF0C\u5E1D\u75BE\u751A\uFF0C\u8FD8\u957F\u5B89\uFF1B\u662F\u5915\u6B82\uFF0C\u5E74\u4E09\u5341\u516D\u3002</p></blockquote><p><strong>\u300A\u8D44\u6CBB\u901A\u9274\xB7\u5377\u4E00\u4E03\u4E09\xB7\u9648\u7EAA\u4E03\u300B</strong></p><p>\u867D\u7136\uFF0C\u5728 <strong><code>\u5317\u5468</code>\u6B66\u5E1D\u5B87\u6587\u9095</strong>\u53BB\u4E16\u7684\u8FD9\u4E00\u5E74\uFF0C\u8DDD\u79BB<code>\u968B</code>\u706D<code>\u9648</code>\u7EDF\u4E00\u5357\u5317\u8FD8\u6709\u4E8C\u5341\u5E74\uFF0C\u4F46\u4E8B\u5B9E\u4E0A\uFF0C\u7EDF\u4E00\u7684\u5951\u673A\u5728\u6B64\u524D\u4E00\u5E74\u5C31\u5DF2\u7ECF\u5960\u5B9A\uFF1A<strong>\u81EA\u5317\u5468\u6D88\u706D<code>\u5317\u9F50</code>\uFF0C\u5B87\u6587\u9095\u8FDB\u5165\u90BA\u57CE\u7684\u90A3\u4E00\u523B\u5F00\u59CB\uFF0C\u4E2D\u56FD\u5927\u5730\u7684\u91CD\u5F52\u4E00\u7EDF\u5DF2\u7ECF\u52BF\u4E0D\u53EF\u6321\uFF0C\u6C34\u5230\u6E20\u6210\u3002</strong></p><p>\u867D\u7136\u81EA\u897F\u664B\u672B\u5E74\u7684\u6218\u4E71\u4EE5\u6765\uFF0C\u8FD9\u5DF2\u7ECF\u662F\u5317\u4E2D\u56FD\u7684\u7B2C\u4E09\u6B21\u7EDF\u4E00\uFF0C<strong>\u4F46\u8FD9\u4E00\u6B21\uFF0C\u7684\u786E\u662F\u4E0E\u4F17\u4E0D\u540C\u7684\u3002</strong></p><p><strong><code>\u9EC4\u4EC1\u5B87</code>\u5148\u751F\u628A\u4E2D\u534E\u7684\u7EDF\u4E00\u738B\u671D\u5206\u4E3A\u4E09\u4E2A\u9636\u6BB5</strong>\uFF0C\u6BCF\u4E00\u4E2A\u9636\u6BB5\u7684\u5E1D\u56FD\uFF0C\u90FD\u6709\u4E0D\u540C\u7684\u6CBB\u7406\u6A21\u5F0F\uFF0C\u5B58\u5728\u4E0D\u540C\u7684\u793E\u4F1A\u7ED3\u6784\uFF0C\u4E5F\u90FD\u8981\u9762\u5BF9\u4E0D\u540C\u7684\u56F0\u5883\u3002</p><p>**<code>\u79E6\u6C49</code>\u4E3A\u7B2C\u4E00\u5E1D\u56FD\u3002**\u5F53\u4E2D\u534E\u5E1D\u56FD\u5EF6\u7EED\u5230\u6C49\u672B\uFF0C\u56FD\u5BB6\u7684\u7EDF\u6CBB\u80FD\u529B\u5DF2\u7ECF\u65E0\u6CD5\u7EF4\u6301\u5176\u5E9E\u5927\u7684\u7EDF\u6CBB\uFF0C\u77DB\u76FE\u7684\u7206\u53D1\u4E0D\u53EF\u907F\u514D\u3002</p><p>\u95EE\u9898\u4F55\u5728\uFF1F\u7B80\u800C\u8A00\u4E4B\u5C31\u662F\u4E24\u6761\uFF1A<strong>\u4EBA\u53E3\u6ECB\u751F\uFF0C\u58EB\u65CF\u5750\u5927\u3002</strong></p><p>\u4EBA\u53E3\u6ECB\u751F\u540E\uFF0C\u5FC5\u7136\u4F34\u968F\u4EBA\u591A\u5730\u5C11\uFF0C\u7ADE\u4E89\u52A0\u5267\u3002\u8FD9\u6240\u5BFC\u81F4\u7684\u56FD\u5BB6\u52A8\u4E71\uFF0C\u5728\u524D\u73B0\u4EE3\u793E\u4F1A\u57FA\u672C\u4E0A\u662F\u201C\u4E0D\u6CBB\u4E4B\u75C7\u201D\uFF0C\u51E0\u4E4E\u53EA\u80FD\u901A\u8FC7\u6218\u4E71\u6216\u8005\u761F\u75AB\u6765\u89E3\u51B3\u2014\u2014\u8FD9\u4E5F\u5C31\u662F\u9EC4\u5DFE\u4E4B\u4E71\u7684\u8D77\u56E0\u3002</p><p>\u6B64\u540E\uFF0C\u58EB\u65CF\u5750\u5927\u6240\u5F15\u8D77\u7684\u5730\u65B9\u79BB\u5FC3\u529B\uFF0C\u88AB\u9EC4\u5DFE\u4E4B\u4E71\u540E\u7684\u6218\u4E71\u52A0\u901F\u653E\u5927\u3002</p><p>\u4E1C\u6C49\u672B\u5E74\u7684\u519B\u9600\uFF0C\u51E0\u4E4E\u90FD\u6709\u5730\u65B9\u58EB\u65CF\u80CC\u666F\u3002\u800C\u4E1C\u6C49\u672B\u5E74\u6240\u8C13\u7684\u7FA4\u96C4\u4E89\u9738\uFF0C\u6B63\u662F\u5728\u5730\u65B9\u58EB\u65CF\u52BF\u529B\u6025\u5267\u81A8\u80C0\uFF0C\u671D\u5EF7\u7EDF\u6CBB\u5931\u5E8F\u540E\u51FA\u73B0\u7684\u3002</p><p>\u6B64\u5916\uFF0C\u7531\u4E8E\u7B2C\u4E00\u5E1D\u56FD\u5D29\u6E83\uFF0C\u585E\u5916\u80E1\u4EBA\u8FDB\u5165\u4E2D\u539F\uFF0C\u8FD9\u53C8\u5F15\u53D1\u4E86<strong>\u80E1\u6C49\u878D\u5408</strong>\u7684\u95EE\u9898\u2014\u2014\u80E1\u4EBA\u8FDB\u5165\u6C49\u5730\uFF0C\u8FD9\u4E5F\u662F\u4ECE\u6C49\u672B\u5F00\u59CB\u7684\u3002</p><p><strong>\u7B2C\u4E00\u5E1D\u56FD\u7684\u5D29\u6E83\uFF0C\u56E0\u4E3A<code>\u4EBA\u53E3\u6ECB\u751F</code>\u800C\u5F15\u53D1\uFF0C\u5728\u53E0\u52A0<code>\u58EB\u65CF\u5750\u5927</code>\u3001<code>\u80E1\u6C49\u95EE\u9898</code>\u540E\uFF0C\u7EC8\u4E8E\u53D8\u5F97\u96BE\u4EE5\u6536\u573A\u3002</strong></p><p>\u672C\u6765\uFF0C<code>\u897F\u664B</code>\u7684\u7EDF\u4E00\u662F\u4E00\u4E2A\u5F88\u597D\u7684\u5951\u673A\u3002\u7ECF\u8FC7\u4E09\u56FD\u957F\u8FBE\u8FD1\u767E\u5E74\u7684\u6218\u4E71\uFF0C\u4EBA\u53E3\u9510\u51CF\uFF0C\u4EBA\u53E3\u95EE\u9898\u5DF2\u7ECF\u57FA\u672C\u89E3\u51B3\u3002</p><p>\u6B64\u540E\uFF0C\u5982\u679C\u6709\u4E00\u4E2A\u76F8\u5BF9\u548C\u5E73\u7684\u73AF\u5883\uFF0C\u90A3\u4E48\u58EB\u65CF\u5750\u5927\u7684\u79BB\u5FC3\u56E0\u7D20\u53EF\u80FD\u5E76\u4E0D\u4F1A\u6709\u591A\u5927\u7834\u574F\u4F5C\u7528\u3002</p><p>\u4F46\u504F\u504F\uFF0C\u56E0\u4E3A\u897F\u664B\u672B\u5E74\u7684<code>\u516B\u738B\u4E4B\u4E71</code>\uFF0C\u534E\u590F\u5927\u5730\u91CD\u71C3\u6218\u706B\uFF0C\u585E\u5916\u80E1\u4EBA\u8D81\u673A\u8FDB\u5165\u4E2D\u539F\u3002</p><p>\u539F\u6765\u7684\u5C0F\u6BDB\u75C5\uFF0C\u771F\u6B63\u53D8\u6210\u4E86\u5927\u95EE\u9898\u3002</p><p>\u80E1\u4EBA\u8FDB\u5165\u4E2D\u539F\u4EE5\u540E\uFF0C\u82E5\u5355\u7EAF\u4EE5\u519B\u4E8B\u80FD\u529B\u8BBA\uFF08\u65E2\u6709\u90E8\u843D\u7EC4\u7EC7\u5F62\u5F0F\u7684\u4F18\u52BF\uFF0C\u53C8\u6709\u9A91\u5175\u5BF9\u6B65\u5175\u7684\u78BE\u538B\uFF09\uFF0C\u6C49\u4EBA\u6839\u672C\u4E0D\u662F\u5BF9\u624B\u3002\u56E0\u6B64\uFF0C\u6211\u4EEC\u770B\u5230\u7684\u8868\u8C61\u662F\uFF0C<strong>\u81EA\u897F\u664B\u706D\u4EA1\u4EE5\u540E\uFF0C\u5317\u65B9\u51E0\u4E4E\u90FD\u662F\u80E1\u4EBA\u5EFA\u7ACB\u7684\u653F\u6743</strong>\u3002</p><p><strong>\u4F46\u80E1\u4EBA\u653F\u6743\u7684\u5BFF\u547D\uFF0C\u5F80\u5F80\u5E76\u4E0D\u957F\u4E45\u3002</strong></p><p>\u5360\u9886\u4E00\u4E2A\u56FD\u5BB6\u548C\u6CBB\u7406\u4E00\u4E2A\u56FD\u5BB6\uFF0C\u4E4B\u95F4\u5DEE\u522B\u6781\u5927\uFF1A\u80E1\u4EBA\u80FD\u9760\u519B\u4E8B\u5F81\u670D\u6C49\u5730\u653F\u6743\uFF0C\u4F46\u5374\u6CA1\u6709\u529E\u6CD5\u7EDF\u6CBB\u6C49\u5730\u3002\u800C\u4E14\uFF0C\u5EFA\u7ACB\u738B\u671D\u4EE5\u540E\uFF0C\u80E1\u4EBA\u5BF9\u4E8E\u56FD\u5BB6\u6743\u529B\u7684\u8FD0\u7528\u4E5F\u5E76\u4E0D\u719F\u7A14\u3002</p><p>\u56E0\u6B64\uFF0C\u80E1\u4EBA\u653F\u6743\u5F80\u5F80\u4F1A\u5728\u7B2C\u4E00\u4EE3\u519B\u4E8B\u5F3A\u4EBA\u901D\u53BB\u540E\u8FC5\u901F\u5D29\u6E83\u2014\u2014\u5728\u8FD9\u65B9\u9762\uFF0C<code>\u540E\u8D75</code>\u53EF\u4EE5\u7B97\u662F\u5178\u578B\u3002<code>\u77F3\u52D2</code>\u4EE5\u5974\u96B6\u51FA\u8EAB\uFF0C\u51E0\u4E4E\u7EDF\u4E00\u4E86\u6574\u4E2A\u5317\u65B9\u3002\u4F46\u5728\u4ED6\u6B7B\u540E\u4E0D\u5230\u4E8C\u5341\u5E74\uFF0C\u540E\u8D75\u5373\u5DF2\u4EA1\u56FD\u3002</p><p>\u76F4\u5230<code>\u82FB\u575A</code>\u51FA\u73B0\u3002</p><p>\u82FB\u575A\u5E76\u4E0D\u662F\u4E00\u4E2A\u5178\u578B\u7684\u80E1\u4EBA\u541B\u4E3B\uFF0C\u800C\u66F4\u50CF\u4E00\u4E2A\u6C49\u4EBA\u7687\u5E1D\u3002\u4ED6\u719F\u6089\u5112\u5BB6\u7ECF\u5178\uFF0C\u7EC8\u8EAB\u575A\u5B88\u4EC1\u4E49\uFF0C\u8BD5\u56FE\u5728\u5DF2\u7ECF\u6218\u4E71\u4E86\u8FD1\u516D\u5341\u5E74\u7684\u5317\u65B9\u5927\u5730\u91CD\u65B0\u6062\u590D\u79E9\u5E8F\u3002</p><p>\u4F46\u4ED6\u6700\u7EC8\u8FD8\u662F\u5931\u8D25\u4E86\u3002</p><p>\u5982\u679C\u5206\u6790\u82FB\u575A\u5931\u8D25\u7684\u539F\u56E0\uFF0C\u6211\u4EEC\u4E0D\u96BE\u770B\u51FA\u5176\u4E2D\u95EE\u9898\u6240\u5728\uFF1A\u82FB\u575A\u5F00\u51FA\u7684\u836F\u65B9\uFF0C\u6CBB\u4E0D\u4E86\u5F53\u65F6\u7684\u75C5\u3002\u9762\u5BF9\u7740\u5317\u65B9\u6C11\u65CF\u4E4B\u95F4\u7684\u6DF7\u6218\uFF0C\u82FB\u575A\u6307\u671B\u7528\u81EA\u5DF1\u7684\u4EC1\u653F\uFF0C\u6765\u5EFA\u7ACB\u4E00\u4E2A\u8D85\u8D8A\u79CD\u65CF\u7684\u56FD\u5BB6\u3002</p><p>\u4F46\u4E8B\u5B9E\u4E0A\uFF0C<strong>\u4ED6\u7684\u6743\u529B\uFF0C\u662F\u67B6\u8BBE\u5728\u519B\u4E8B\u5F81\u670D\u4E4B\u4E0A\u7684</strong>\u3002</p><p>\u56E0\u6B64\u4E00\u65E6\u6DDD\u6C34\u5931\u8D25\uFF0C\u5E1D\u56FD\u4FBF\u8FC5\u901F\u5D29\u3002</p><p>\u5728\u8FD9\u4E00\u70B9\u4E0A\uFF0C\u6211\u4EEC\u53EA\u8981\u5BF9\u6BD4\u8D64\u58C1\u4E4B\u6218\uFF0C\u5C31\u53EF\u4EE5\u770B\u5F97\u6E05\u6E05\u695A\u695A\uFF1A\u8D64\u58C1\u4E4B\u6218\u5931\u8D25\u540E\uFF0C<code>\u66F9\u64CD</code>\u5B9E\u529B\u5927\u635F\uFF0C\u4F46\u5317\u65B9\u4F9D\u7136\u5728\u5176\u7EDF\u6CBB\u4E4B\u4E0B\u3002</p><p>\u82FB\u575A\u5219\u4E0D\u540C\uFF0C\u867D\u7136\u4ED6\u4E0D\u613F\u610F\u627F\u8BA4\u81EA\u5DF1\u7684\u80E1\u4EBA\u8EAB\u4EFD\uFF0C\u4F46\u4E8B\u5B9E\u4E0A\uFF0C**\u4ED6\u7684\u56FD\u5BB6\u6700\u7EC8\u4F9D\u9760\u7684\uFF0C\u8FD8\u662F\u4ED6\u81EA\u5DF1\u7684\u65CF\u4EBA\u3002**\u5F53\u6C10\u65CF\u7CBE\u9510\u5728\u6DDD\u6C34\u4E4B\u6218\u524D\u7EBF\u5168\u519B\u8986\u6CA1\uFF0C\u65CF\u4EBA\u53C8\u5DF2\u7ECF\u88AB\u6563\u4E8E\u56DB\u65B9\uFF0C\u82FB\u575A\u6839\u672C\u5C31\u65E0\u529B\u53EF\u501F\u3002</p><p>\u5230\u8FD9\u91CC\uFF0C\u903B\u8F91\u5DF2\u7ECF\u6253\u4E86\u4E00\u4E2A\u6B7B\u7ED3\uFF1A\u4E00\u65B9\u9762\uFF0C\u82FB\u575A\u5E0C\u671B\u5EFA\u7ACB\u8D77\u4E00\u4E2A\u8D85\u8D8A\u79CD\u65CF\u7684\u56FD\u5BB6\uFF1B\u53E6\u4E00\u65B9\u9762\uFF0C\u4ED6\u6240\u4F9D\u9760\u7684\u5B9E\u529B\u53C8\u90FD\u57FA\u4E8E\u79CD\u65CF\u3002<strong>\u82FB\u575A\u60F3\u8981\u7528\u81EA\u5DF1\u7684<code>\u4EC1\u653F</code>\u5C31\u6D88\u5F2D\u6389\u6C11\u65CF\u4E4B\u95F4\u7684\u5DEE\u8DDD\uFF0C\u8FD9\u51E0\u4E4E\u662F\u4E00\u4E2A\u4E0D\u53EF\u80FD\u5B8C\u6210\u7684\u4EFB\u52A1\u3002</strong></p><p>\u8FD9\u662F\u89E3\u51B3\u80E1\u6C49\u878D\u5408\u95EE\u9898\u7684<strong>\u7B2C\u4E00\u6B21\u5C1D\u8BD5</strong>\u3002</p><p>\u6DDD\u6C34\u4E4B\u6218\u540E\uFF0C\u5317\u65B9\u518D\u6B21\u9677\u5165\u5206\u88C2\u3002\u6B64\u540E\u4E94\u5341\u591A\u5E74\u95F4\uFF0C<code>\u9C9C\u5351\u62D3\u8DCB\u6C0F</code>\u51ED\u501F\u5F3A\u5927\u7684\u6B66\u529B\u4EE5\u53CA\u4E0D\u4E16\u51FA\u7684\u519B\u4E8B\u5929\u624D<code>\u62D3\u8DCB\u7118</code>\uFF0C\u91CD\u65B0\u7EDF\u4E00\u5317\u65B9\u3002</p><p>\u800C\u62D3\u8DCB\u6C0F\u5728\u57FA\u672C\u7ED3\u675F\u5BF9\u5916\u6218\u4E89\u4EE5\u540E\uFF0C\u5C31\u5F00\u59CB\u7740\u624B\u89E3\u51B3\u56FD\u5BB6\u6B63\u5E38\u5316\u7684\u95EE\u9898\u2014\u2014\u5176\u95F4\u79CD\u79CD\u4E3E\u63AA\uFF0C\u4EE5\u53CA <strong><code>\u5317\u9B4F</code>\u5B5D\u6587\u5E1D</strong>\u7684\u6C49\u5316\u6539\u9769\uFF0C\u90FD\u53EF\u4EE5\u88AB\u5F52\u7EB3\u4E3A\u5E7F\u4E49\u4E0A\u7684<code>\u201C\u592A\u548C\u6539\u5236\u201D</code>\u3002</p><p>\u4ECE\u62D3\u8DCB\u7118\u7EDF\u4E00\u5317\u65B9\uFF0C\u5230\u5B5D\u6587\u5E1D\u5143\u5B8F\u8FDB\u884C\u6C49\u5316\u6539\u9769\uFF0C\u671F\u95F4\u516D\u5341\u591A\u5E74\u3002<strong>\u5728\u5317\u9B4F\u65F6\u671F\uFF0C\u5317\u65B9\u7684\u4E16\u5BB6\u5927\u65CF\u5DF2\u7ECF\u5F00\u59CB\u4E0E\u80E1\u4EBA\u653F\u6743\u5408\u4F5C</strong>\u2014\u2014\u8FD9\u5728\u6B64\u524D\u7684\u80E1\u4EBA\u653F\u6743\u4E2D\uFF0C\u662F\u6BD4\u8F83\u7F55\u89C1\u7684\u3002\u5F53\u521D\uFF0C\u5373\u4FBF\u4EE5\u82FB\u575A\u7684\u7684\u5BBD\u4EC1\uFF0C\u524D\u79E6\u671D\u5EF7\u4E2D\u4E5F\u51E0\u4E4E\u6CA1\u6709\u58EB\u65CF\u4EBA\u7269\u3002</p><p>\u7AD9\u5728\u5317\u65B9\u58EB\u65CF\u7684\u89D2\u5EA6\u4E0A\uFF0C\u8FD9\u79CD\u8F6C\u53D8\u53EF\u4EE5\u7406\u89E3\u3002\u80E1\u4EBA\u4EE5\u4E2D\u539F\u4E3A\u6218\u573A\uFF0C\u5931\u8D25\u7684\u4E00\u65B9\u56FA\u7136\u60E8\u70C8\uFF0C\u4F46\u58EB\u65CF\u8D56\u4EE5\u751F\u5B58\u7684\u571F\u5730\uFF0C\u4E00\u6837\u906D\u53D7\u5200\u5175\u4E4B\u7978\u3002\u56E0\u6B64\uFF0C\u4E0E\u5176\u5750\u89C2\u6210\u8D25\uFF0C\u8FD8\u4E0D\u5982\u5BFB\u6C42\u4E0E\u80E1\u4EBA\u653F\u6743\u5408\u4F5C\u3002\u901A\u8FC7\u6539\u53D8\u80E1\u4EBA\uFF0C\u672A\u5C1D\u4E0D\u80FD\u91CD\u5EFA\u79E9\u5E8F\u2014\u2014\u4E00\u79CD\u58EB\u65CF\u9636\u5C42\u6240\u5E0C\u671B\u7684\u79E9\u5E8F\u3002</p><p>\u8FD9\u79CD\u5408\u4F5C\uFF0C\u5E26\u6765\u7684\u53D8\u5316\u7F13\u6162\u5374\u4E5F\u660E\u663E\uFF1A<strong>\u80E1\u4EBA\u653F\u6743\u7684\u7EDF\u6CBB\uFF0C\u5F00\u59CB\u8131\u79BB\u7B80\u5355\u7684\u5F81\u670D/\u63A0\u593A\u6A21\u5F0F\uFF0C\u6162\u6162\u53D8\u5F97\u50CF\u4E00\u4E2A\u6B63\u5E38\u7684\u56FD\u5BB6\u3002</strong></p><p>\u5C42\u6B21\u9012\u8FDB\u4E4B\u540E\uFF0C\u592A\u548C\u6539\u5236\u7EC8\u4E8E\u8FCE\u6765\u9AD8\u6F6E\uFF1A<strong>\u5B5D\u6587\u5E1D\u7684\u6C49\u5316\u6539\u9769\u3002</strong></p><p>\u5B5D\u6587\u5E1D\u7684\u6539\u9769\uFF0C\u6838\u5FC3\u5728\u4E8E\u201C\u6C49\u5316\u201D\u3002\u5176\u903B\u8F91\u662F\uFF1A\u65E2\u7136\u9C9C\u5351\u4EBA\u5DF2\u7ECF\u7EDF\u6CBB\u4E2D\u539F\u8179\u5730\u8FD1\u767E\u5E74\uFF0C\u63A5\u53D7\u534E\u590F\u6587\u5316\uFF0C\u90A3\u4E48\uFF0C\u5B9E\u884C\u6C49\u5236\u6709\u4F55\u4E0D\u53EF\uFF1F</p><p>**\u4ECE\u897F\u664B\u672B\u5E74\u81F3\u6B64\u65F6\uFF0C\u65F6\u95F4\u5DF2\u7ECF\u8FC7\u53BB\u4E86\u8FD1\u4E24\u767E\u5E74\u3002**\u80E1\u6C49\u4E4B\u95F4\uFF0C\u65E9\u5C31\u4E0D\u662F\u6B64\u524D\u8840\u6D77\u6DF1\u4EC7\u4E0D\u591F\u6234\u5929\u7684\u90A3\u79CD\u72B6\u6001\u3002\u7ECF\u8FC7\u8FD9\u4E48\u591A\u5E74\u7684\u53AE\u6740\uFF0C\u6C49\u4EBA\u5DF2\u7ECF\u63A5\u53D7\u4E86\u4E0D\u53EF\u80FD\u518D\u628A\u80E1\u4EBA\u8D76\u51FA\u53BB\u7684\u73B0\u5B9E\uFF0C\u80E1\u4EBA\u4E5F\u5F00\u59CB\u5B66\u4F1A\u5C0A\u91CD\u548C\u63A5\u53D7\u6C49\u4EBA\u65E7\u6709\u7684\u6587\u5316\u3002</p><p><strong>\u4F46\u5B5D\u6587\u5E1D\u7684\u6539\u9769\uFF0C\u6700\u7EC8\u8FD8\u662F\u5931\u8D25\u4E86\u3002</strong></p><p>\u5B5D\u6587\u5E1D\u56E0\u4E3A\u65E9\u901D\u800C\u672A\u7ADE\u5168\u529F\uFF0C\u8FD9\u56FA\u7136\u662F\u4E00\u4E2A\u65B9\u9762\u56E0\u7D20\u3002\u4F46\u540C\u65F6\uFF0C\u4E5F\u662F\u56E0\u5185\u653F\u3001\u519B\u4E8B\u65B9\u9762\u51FA\u73B0\u7684\u4E00\u7CFB\u5217\u95EE\u9898\uFF0C\u624D\u6700\u7EC8\u846C\u9001\u4E86\u5317\u9B4F\u5728\u80E1\u6C49\u878D\u5408\u4E0A\u7684\u52AA\u529B\u3002</p><p>\u8FD9\u662F\u89E3\u51B3\u80E1\u6C49\u878D\u5408\u95EE\u9898\u7684<strong>\u7B2C\u4E8C\u6B21\u5C1D\u8BD5\u3002</strong></p><p>\u5728\u5357\u5317\u671D\u5206\u88C2\u65F6\u671F\u7684\u4E24\u6B21\u878D\u5408\u52AA\u529B\u4E2D\uFF0C\u6211\u4EEC\u90FD\u770B\u5230\u4E86\u8FD9\u6837\u7684\u73B0\u8C61\uFF1A<strong>\u4E00\u4E2A\u770B\u4E0A\u53BB\u5F88\u6709\u5E0C\u671B\u7684\u8FDB\u7A0B\uFF0C\u56E0\u4E3A\u4E00\u4E9B\u610F\u5916\u56E0\u7D20\u7684\u6253\u4E71\u800C\u6025\u8F6C\u76F4\u4E0B\uFF0C\u4EE5\u81F3\u4E8E\u65E0\u6CD5\u633D\u6551\u3002</strong></p><p>\u524D\u79E6\u6DDD\u6C34\u4E4B\u6218\u7684\u5D29\u6E83\u5982\u662F\uFF0C\u5B5D\u6587\u5E1D\u7684\u65E9\u901D\u4EA6\u5982\u662F\u3002</p><p>**\u4E0D\u7BA1\u662F\u5728\u524D\u79E6\uFF0C\u8FD8\u662F\u5728\u5317\u9B4F\uFF0C\u5317\u65B9\u7684\u6C11\u65CF\u878D\u5408\u8FC7\u7A0B\uFF0C\u90FD\u662F\u5728\u4EBA\u4E3A\u63D0\u901F\u7684\u3002**\u524D\u79E6\u65F6\u671F\uFF0C\u6C11\u65CF\u755B\u57DF\u4E4B\u5206\u5C1A\u5728\uFF0C\u82FB\u575A\u5F3A\u884C\u4EE5\u4EC1\u653F\u6D88\u9664\uFF0C\u6548\u679C\u4E0D\u5F70\u3002\u800C\u81F3\u4E8E\u5317\u9B4F\uFF0C\u5373\u4FBF\u62D3\u8DCB\u6C0F\u7684\u6C49\u5316\u52AA\u529B\u5DF2\u7ECF\u5353\u6709\u6210\u6548\uFF0C\u4F46\u5728\u65BD\u884C\u6C49\u5316\u6539\u9769\u4EE5\u540E\uFF0C\u65E7\u6709\u7684\u80E1\u4EBA\u519B\u4E8B\u6A21\u5F0F\u4E5F\u968F\u4E4B\u6D88\u5931\uFF0C\u5317\u9B4F\u7684\u519B\u4E8B\u4F18\u52BF\u4E0D\u518D\u3002</p><p>\u4E5F\u5C31\u662F\u8BF4\uFF0C\u5B5D\u6587\u5E1D\u7684\u6C49\u5316\uFF0C\u5176\u5B9E\u662F<code>\u5355\u4E00\u7EF4\u5EA6</code>\u7684\u6C49\u5316\u3002</p><p>\u5B5D\u6587\u5E1D\u501F\u52A9\u4E24\u767E\u5E74\u6765\u7684\u80E1\u6C49\u878D\u5408\u4E4B\u52BF\uFF0C\u8BD5\u56FE\u4EE5\u56FD\u5BB6\u7684\u529B\u91CF\uFF0C\u5F3A\u884C\u6D88\u9664\u80E1\u6C49\u4E4B\u95F4\u7684\u9694\u9602\u3002\u8FD9\u79CD\u52AA\u529B\uFF0C\u5E94\u8BE5\u662F\u6210\u529F\u7684\uFF0C\u4F46\u540C\u65F6\uFF0C<strong>\u5317\u9B4F\u4E5F\u4E22\u6389\u4E86\u80E1\u4EBA\u65E2\u6709\u7684\u90E8\u843D\u5F62\u5F0F\u4E0B\u7684\u519B\u4E8B\u4F18\u52BF\u3002</strong></p><p>\u968F\u7740\u5B5D\u6587\u5E1D\u65E9\u901D\uFF0C\u540E\u7EE7\u65E0\u4EBA\uFF0C\u4E4B\u540E\u7684<code>\u5BA3\u6B66\u5E1D</code>\u548C<code>\u80E1\u592A\u540E</code>\u53C8\u6CA1\u80FD\u591F\u627E\u5230\u5408\u9002\u7684\u6B66\u529B\u8F93\u51FA\u6A21\u5F0F\uFF0C\u4EE5\u6B66\u529B\u5F3A\u76DB\u7ACB\u56FD\u7684\u5317\u9B4F\uFF0C\u6700\u7EC8\u7ADF\u7136\u56E0\u4E3A\u7F3A\u4E4F\u519B\u4E8B\u5B9E\u529B\u800C\u9010\u6E10\u8870\u843D\uFF0C\u6700\u7EC8\u706D\u4EA1\u3002</p><p>\u4EE5\u5317\u9B4F\u672B\u5E74\u7684\u519B\u4E8B\u5B9E\u529B\uFF0C\u6211\u4EEC\u751A\u81F3\u53EF\u4EE5\u8FD9\u4E48\u5047\u8BBE\uFF1A\u5373\u4FBF\u5B83\u80FD\u653B\u706D\u5357\u671D\uFF0C\u7EDF\u4E00\u4E2D\u56FD\uFF0C\u4F46\u9762\u5BF9\u7740\u67D4\u7136\u7684\u519B\u4E8B\u538B\u529B\uFF0C\u5B83\u4E5F\u4E0D\u53EF\u80FD\u6301\u4E45\u3002</p><p>\u770B\u4E0A\u53BB\uFF0C**\u5317\u9B4F\u662F\u89E3\u51B3\u4E86\u6C11\u65CF\u878D\u5408\u7684\u95EE\u9898\uFF0C\u4F46\u8FD9\u662F\u4EE5\u653E\u5F03\u6B66\u529B\u8F93\u51FA\u4E3A\u524D\u63D0\u7684\u3002**\u56E0\u6B64\u53EA\u80FD\u8BF4\uFF0C\u95EE\u9898\u53EA\u662F\u6682\u65F6\u88AB\u63A9\u76D6\u4E86\uFF0C\u800C\u975E\u88AB\u89E3\u51B3\u4E86\u3002<strong>\u6B64\u65F6\u4E00\u65E6\u5916\u90E8\u7684\u7EA6\u675F\u4F5C\u7528\u5931\u53BB\uFF0C\u6C11\u65CF\u95EE\u9898\u5C31\u4F1A\u91CD\u65B0\u7206\u53D1\u51FA\u6765\u3002</strong></p><p>\u5317\u9B4F\u7684\u706D\u4EA1\uFF0C\u8087\u59CB\u4E8E\u516D\u9547\u4E4B\u4E71\u3002\u800C\u5728\u516D\u9547\u4E4B\u4E71\u51E0\u5E74\u540E\uFF0C\u5317\u65B9\u53C8\u56DE\u5230\u4E86\u53CC\u96C4\u5E76\u5CD9\u7684\u5C40\u9762\uFF1A<strong><code>\u9AD8\u6C0F</code>\u653F\u6743\u4E86\u5360\u636E\u5317\u4E2D\u56FD\u7684\u4E1C\u90E8\uFF0C\u800C\u897F\u90E8\u5219\u4E3A<code>\u5B87\u6587\u6C0F</code>\u6240\u6709\u3002</strong></p><p>\u9AD8\u6C0F\u548C\u5B87\u6587\u6C0F\u7684\u5171\u540C\u70B9\uFF0C\u662F\u53CC\u65B9\u90FD\u662F\u9C9C\u5351\u4EBA\u2014\u2014\u5F53\u7136\uFF0C\u5B87\u6587\u6C0F\u53EF\u80FD\u8981\u66F4\u7EAF\u7CB9\u4E00\u70B9\uFF0C\u9AD8\u6C0F\u662F\u9C9C\u5351\u5316\u7684\u6C49\u4EBA\u3002</p><p>\u4EC0\u4E48\u53EB\u9C9C\u5351\u5316\u7684\u6C49\u4EBA\uFF1F\u5C31\u662F\u8BF4\uFF0C\u9AD8\u6B22\u7684\u8840\u7EDF\u867D\u7136\u662F\u6C49\u4EBA\u8840\u7EDF\uFF0C\u4F46\u4ED6\u7684\u6574\u4E2A\u6210\u957F\u73AF\u5883\uFF0C\u5374\u662F\u5728\u9C9C\u5351\u4EBA\u7684\u7FA4\u4F53\u4E2D\u3002\u4EE5\u6211\u4EEC\u5982\u4ECA\u7684\u6C11\u65CF\u89C2\u5FF5\uFF0C\u9AD8\u6B22\u65E0\u7591\u662F\u6C49\u4EBA\uFF0C\u4F46\u5728\u5F53\u65F6\uFF0C\u9AD8\u6B22\u5219\u662F\u9C9C\u5351\u4EBA\u3002</p><p>\u9AD8\u6B22\u4E0E\u5B87\u6587\u6CF0\u540C\u65F6\u90FD\u4F9D\u9760\u4E86\u9C9C\u5351\u4EBA\u7684\u6B66\u529B\u2014\u2014\u516D\u9547\u4E4B\u4E71\u7684\u6B8B\u4F59\u52BF\u529B\uFF0C\u53EA\u4E0D\u8FC7\u9AD8\u6B22\u5F97\u5230\u4E86\u5927\u90E8\u5206\uFF0C\u5B87\u6587\u6CF0\u5219\u53EA\u5206\u5230\u4E00\u5C0F\u90E8\u5206\u3002\u501F\u7740\u8FD9\u4E9B\u5B9E\u529B\uFF0C\u4E24\u4EBA\u5E73\u5206\u5317\u4E2D\u56FD\u3002</p><p>\u4F46\u5728\u6B64\u540E\u7684\u5386\u53F2\u4E2D\uFF0C\u53CC\u65B9\u8D70\u51FA\u7684\u5374\u662F\u622A\u7136\u4E0D\u540C\u7684\u8DEF\u3002<strong>\u5B87\u6587\u6C0F\u65E0\u610F\u4E2D\u8E5A\u51FA\u7684\u4E00\u6761\u9053\uFF0C\u771F\u6B63\u89E3\u51B3\u4E86\u56F0\u6270\u5317\u4E2D\u56FD\u51E0\u767E\u5E74\u7684\u6C11\u65CF\u95EE\u9898\u3002</strong></p><p><strong>\u5E9C\u5175\u5236</strong></p><p>\u800C\u5E9C\u5175\u5236\u7684\u7ED3\u679C\uFF0C\u662F\u6700\u7EC8\u89E3\u51B3\u4E86\u56F0\u6270\u5317\u65B9\u591A\u5E74\u7684\u80E1\u6C49\u95EE\u9898\uFF1A<strong>\u5728\u5E9C\u5175\u5236\u4E0B\uFF0C\u65E2\u6D88\u9664\u4E86\u80E1\u6C49\u4E4B\u95F4\u7684\u9694\u9602\u95EE\u9898\uFF0C\u53C8\u4FDD\u6301\u4E86\u5F3A\u5927\u7684\u6B66\u529B\u8F93\u51FA\u3002</strong></p><p>\u6B64\u5916\uFF0C\u51E0\u767E\u5E74\u7684\u6218\u4E71\uFF0C\u8FD8\u5E26\u6765\u4E86\u53E6\u4E00\u4E2A\u540E\u679C\uFF1A<strong>\u5730\u65B9\u79BB\u5FC3\u4E3B\u4E49\u7684\u5F7B\u5E95\u7834\u4EA7\u3002</strong></p><p>\u81EA\u6C49\u672B\u4EE5\u6765\uFF0C\u5730\u65B9\u5927\u65CF\u56FF\u4E8E\u81EA\u5DF1\u7684\u52BF\u529B\u8303\u56F4\uFF0C\u4E0D\u613F\u610F\u63A5\u53D7\u66F4\u8FDC\u66F4\u5927\u7684\u4E2D\u592E\u671D\u5EF7\u7684\u7EDF\u6CBB\u3002\u4F46\u7ECF\u8FC7\u51E0\u767E\u5E74\u7684\u6218\u4E71\uFF0C\u5730\u65B9\u58EB\u65CF\u8BA4\u8BC6\u5230\uFF0C\u53EA\u6709\u7EDF\u4E00\u7684\u4E2D\u539F\u738B\u671D\uFF0C\u624D\u6709\u51DD\u805A\u5B9E\u529B\u3001\u62B5\u6297\u585E\u5916\u80E1\u4EBA\u7684\u80FD\u529B\u3002\u5426\u5219\uFF0C\u8986\u5DE2\u4E4B\u4E0B\uFF0C\u5B89\u6709\u5B8C\u5375\u3002</p><p>\u5BF9\u4E16\u5BB6\u5927\u65CF\u800C\u8A00\uFF0C\u4E0E\u80FD\u591F\u8F93\u51FA\u6B66\u529B\u7684\u7EDF\u4E00\u653F\u6743\u5408\u4F5C\uFF0C\u662F\u6700\u597D\u7684\u751F\u5B58\u65B9\u5F0F\u3002</p><p>\u81EA\u5317\u5468\u8FDB\u884C\u5E9C\u5175\u5236\u6539\u9769\uFF0C\u5173\u9647\u96C6\u56E2\u6210\u578B\u4EE5\u540E\uFF0C\u76F4\u81F3<code>\u968B\u5510</code>\uFF0C\u6211\u4EEC\u53EF\u4EE5\u770B\u5230\u5927\u91CF\u7684\u5173\u4E1C\u58EB\u65CF\u613F\u610F\u4E0E\u672C\u662F\u80E1\u4EBA\u7684\u671D\u5EF7\u52CB\u8D35\u8054\u59FB\u2014\u2014\u8981\u77E5\u9053\uFF0C\u653E\u5728\u51E0\u767E\u5E74\u4EE5\u524D\uFF0C\u8BA9\u58EB\u65CF\u4E0E\u80E1\u4EBA\u901A\u5A5A\uFF0C\u7B80\u76F4\u662F\u5929\u65B9\u591C\u8C2D\u3002</p><p>\u867D\u7136\u4E16\u5BB6\u5927\u65CF\u5DF2\u7ECF\u53EA\u6709\u51E0\u767E\u5E74\u7684\u5BFF\u547D\uFF0C\u4F46\u6B64\u540E\uFF0C\u4ED6\u4EEC\u518D\u4E5F\u6CA1\u6709\u8FC7\u79BB\u5FC3\u503E\u5411\u3002</p><p>\u81F3\u6B64\uFF0C<strong>\u6C49\u672B\u4EE5\u6765\u7B2C\u4E00\u5E1D\u56FD\u79EF\u7D2F\u7684\u79CD\u79CD\u95EE\u9898\uFF0C\u7EC8\u4E8E\u5168\u90E8\u88AB\u6D88\u9664\u3002</strong></p>',63),n=[c];function s(d,g){return p(),e("div",null,n)}const i=o(r,[["render",s],["__file","94.html.vue"]]);export{i as default};
