import{_ as o}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as p,c as e,f as c}from"./app.8a7d9ef5.js";const d={},t=c('<h1 id="\u9648\u671D" tabindex="-1"><a class="header-anchor" href="#\u9648\u671D" aria-hidden="true">#</a> \u9648\u671D</h1><blockquote><p>\uFF08\u592A\u5EFA\u5341\u4E09\u5E74\uFF09\u6625\uFF0C\u6B63\u6708\uFF0C\u5DF1\u9149\uFF0C\u4E0A\u4E0D\u8C6B\uFF0C\u592A\u5B50\uFF08\u9648\u53D4\u5B9D\uFF09\u4E0E\u59CB\u5174\u738B\u53D4\u9675\u3001\u957F\u6C99\u738B\u53D4\u575A\u5E76\u5165\u4F8D\u75BE\u3002\u2026\u2026\u4E01\u5DF3\uFF0C\u592A\u5B50\u5373\u7687\u5E1D\u4F4D\uFF0C\u5927\u8D66\u3002</p></blockquote><p><strong>\u300A\u8D44\u6CBB\u901A\u9274\xB7\u5377\u4E00\u4E03\u4E94\xB7\u9648\u7EAA\u4E5D\u300B</strong></p><p>\u4ECE557\u5E74<code>\u9648\u9738\u5148</code>\u79F0\u5E1D\uFF0C\u5230\u516C\u5143589\u5E74<code>\u968B</code>\u706D\u9648\uFF0C\u9648\u671D\u5171\u4F20\u4E94\u5E1D\u538632\u5E74\u3002</p><p>\u9648\u671D\u5728\u5386\u53F2\u4E2D\u7684\u5B58\u5728\u611F\u5F88\u5F31\uFF0C\u800C\u4E14\u56E0\u4E3A\u662F\u5357\u671D\u7684\u7EC8\u5C40\uFF0C\u6240\u4EE5\u7EDD\u5927\u90E8\u5206\u4EBA\u5BF9\u9648\u671D\u7684\u611F\u89C9\uFF0C\u53EA\u6709\u65E0\u80FD\u4E0E\u6CA1\u843D\u3002</p><p>\u4F46\u5B9E\u9645\u5E76\u975E\u5982\u6B64\u3002</p><p>\u5982\u679C\u7EB5\u5411\u5BF9\u6BD4\u7740\u770B\uFF0C\u9648\u671D\u7684\u8FD9\u51E0\u4F4D\u7687\u5E1D\uFF0C\u4E0D\u7BA1\u5728\u6CBB\u56FD\u80FD\u529B\u8FD8\u662F\u5728\u4E2A\u4EBA\u64CD\u5B88\u4E0A\uFF0C\u90FD\u6BD4\u5357\u671D\u7684\u7EDD\u5927\u90E8\u5206\u7687\u5E1D\u8981\u5F3A\u3002\u751A\u81F3\u53EF\u4EE5\u8FD9\u4E48\u8BF4\uFF0C\u9664\u53BB<code>\u9648\u540E\u4E3B\u9648\u53D4\u5B9D</code>\u5916\uFF0C\u9648\u671D\u7687\u5E1D\u7684\u5E73\u5747\u7D20\u8D28\u90A3\u662F\u76F8\u5F53\u7684\u9AD8\u3002</p><p>\u4F46\u5386\u53F2\u7684\u65E0\u5948\u4E5F\u5C31\u5728\u4E8E\u6B64\uFF0C\u5357\u5317\u5206\u88C2\u7684\u5C40\u9762\u6B64\u65F6\u5DF2\u8FD1\u5C3E\u58F0\uFF0C\u4E0D\u7BA1\u524D\u9762\u51E0\u4EE3\u9648\u671D\u7687\u5E1D\u5982\u4F55\u7684\u52B1\u7CBE\u56FE\u6CBB\uFF0C\u57FA\u672C\u4E0A\u90FD\u4E0D\u53EF\u80FD\u6539\u53D8\u5357\u671D\u88AB\u5317\u671D\u7EDF\u4E00\u7684\u5386\u53F2\u5927\u52BF\u3002</p><p>\u5F53\u65F6\uFF0C\u9648\u9738\u5148\u80CC\u76DF\u88AD\u6740<code>\u738B\u50E7\u8FA9</code>\uFF0C\u8FD9\u662F\u4ED6\u5F97\u4EE5\u521B\u7ACB\u9648\u671D\u662F\u5173\u952E\u6B65\u9AA4\u4E4B\u4E00\u3002</p><p>\u4F46\u8FD9\u53EA\u662F\u6B65\u9AA4\u4E4B\u4E00\uFF0C\u8FDC\u8FDC\u4E0D\u662F\u5168\u90E8\u3002\u4ECE\u6740\u6389\u738B\u50E7\u8FA9\u5230\u9648\u671D\u7ACB\u56FD\u771F\u6B63\u7A33\u56FA\uFF0C\u671F\u95F4\u5927\u7EA6\u7528\u4E86\u4E94\u5E74\u65F6\u95F4\u3002</p><p>\u5728\u8FD9\u4E94\u5E74\u4E2D\uFF0C<strong><code>\u9648\u9738\u5148</code>\u3001<code>\u9648\u84A8</code>\u4E24\u4EE3\u7687\u5E1D\u4E1C\u5F81\u897F\u8BA8\uFF0C\u526A\u9664\u5F02\u5DF1\uFF0C\u7EC8\u4E8E\u5F97\u4EE5\u6210\u4E3A\u5357\u671D<code>\u6B63\u7EDF</code></strong>\u2014\u2014\u8FD9\u4E5F\u5C31\u662F<code>\u5415\u601D\u52C9</code>\u5148\u751F\u6240\u8BF4\u7684\u9648\u201C\u5F97\u56FD\u4E4B\u6B63\u201D\u3002\u51E0\u4E4E\u53EF\u4EE5\u8FD9\u4E48\u8BF4\uFF0C\u76F8\u8F83\u4E8E\u5357\u671D\u7684\u5176\u4ED6\u671D\u4EE3\uFF0C\u9648\u7684\u51E0\u4E4E\u6BCF\u4E00\u5BF8\u571F\u5730\uFF0C\u90FD\u662F\u9760\u81EA\u5DF1\u6253\u4E0B\u6765\u7684\u3002</p><p>\u9648\u9738\u5148\u5728\u5EFA\u5EB7\u79F0\u5E1D\u65F6\uFF0C\u653F\u4EE4\u4E0D\u51FA\u4E09\u5434\u5730\u533A\uFF08\u4ECA\u82CF\u5357\u6D59\u5317\u4E00\u5E26\uFF09\u3002\u4ED6\u751A\u81F3\u90FD\u4E0D\u662F\u957F\u6C5F\u4EE5\u5357\u6700\u5927\u7684\u52BF\u529B\uFF0C<code>\u8427\u6881</code>\u6E58\u5DDE\u523A\u53F2<code>\u738B\u7433</code>\u5C31\u4E0D\u613F\u610F\u63A5\u53D7\u9648\u9738\u5148\u5EFA\u5EB7\u671D\u5EF7\u7684\u53F7\u4EE4\uFF0C\u9A7B\u624E\u4E8E\u5EFA\u5EB7\u4E0A\u6E38\u7684\u6C5F\u5DDE\u3002\u5C24\u5176\u662F\u5728\u9648\u9738\u5148\u7BE1\u4F4D\u4EE5\u540E\uFF0C\u738B\u7433\u66F4\u662F\u4E3E\u8D77\u53CD\u53DB\u7684\u5927\u65D7\u3002\u4E00\u76F4\u5230\u9648\u9738\u5148\u53BB\u4E16\uFF0C\u738B\u7433\u90FD\u662F\u5EFA\u5EB7\u671D\u5EF7\u6700\u4E3A\u5934\u75DB\u7684\u9EBB\u70E6\u3002</p><p>\u516C\u5143559\u5E74\uFF0C57\u5C81\u7684\u9648\u9738\u5148\u75C5\u901D\uFF0C\u7ED9<code>\u9648\u6587\u5E1D\u9648\u84A8</code>\u7559\u4E0B\u7684\u5C31\u662F\u8FD9\u4E48\u4E00\u526F\u70C2\u644A\u5B50\u3002</p><p>\u800C\u4E14\u9648\u84A8\u7684\u9EBB\u70E6\u8FD8\u4E0D\u6B62\u5982\u6B64\uFF1A<strong>\u4ED6\u4E0D\u662F\u9648\u9738\u5148\u7684\u513F\u5B50\uFF0C\u53EA\u662F\u4F84\u5B50\u3002</strong></p><p>\u9648\u9738\u5148\u65E9\u5148\u5728<code>\u6881\u5143\u5E1D\u8427\u7ECE</code>\u9EBE\u4E0B\uFF0C\u552F\u4E00\u5728\u4E16\u7684\u513F\u5B50<code>\u9648\u660C</code>\u56E0\u6B64\u88AB\u7559\u5728\u6C5F\u9675\u505A\u4EBA\u8D28\u3002\u4F46\u4E4B\u540E\u6C5F\u9675\u88AB<code>\u897F\u9B4F</code>\u653B\u7834\uFF0C\u9648\u660C\u4E5F\u6210\u4E86\u897F\u9B4F\u7684\u4FD8\u864F\u3002</p><p>\u9648\u9738\u5148\u79F0\u5E1D\u540E\uFF0C\u66FE\u53CD\u590D\u4E0E\u897F\u9B4F\uFF08\u5317\u5468\uFF09\u4EA4\u6D89\uFF0C\u5E0C\u671B\u80FD\u6362\u56DE\u513F\u5B50\u3002\u4F46\u5929\u4E0D\u9042\u4EBA\u613F\uFF0C\u9648\u9738\u5148\u7EC8\u7A76\u6CA1\u80FD\u7B49\u5230\u8FD9\u4E00\u5929\uFF0C\u75C5\u5371\u4E4B\u9645\uFF0C\u53EA\u80FD\u4F20\u4F4D\u4E8E\u4F84\u5B50\u9648\u84A8\u3002</p><p>\u800C\u9648\u84A8\u7EE7\u4F4D\u540E\u4E0D\u4E45\uFF0C\u897F\u9B4F\u9A6C\u4E0A\u5C31\u653E\u5F52\u4E86\u9648\u660C\uFF0C\u610F\u56FE\u663E\u7136\u662F\u8981\u6311\u8D77\u9648\u671D\u7684\u5185\u4E71\u3002</p><p>\u5BF9\u6B64\uFF0C\u9648\u84A8\u7684\u89E3\u51B3\u624B\u6BB5\u5E72\u8106\u5229\u843D\uFF1A\u5728\u5F97\u77E5\u9648\u660C\u56DE\u56FD\u4E4B\u9645\uFF0C\u4ED6\u5C01\u9648\u660C\u4E3A\u8861\u9633\u738B\uFF0C\u4EE5\u6B64\u6765\u662D\u793A\u81EA\u5DF1\u7684\u7687\u4F4D\u3002\u540C\u65F6\uFF0C\u53C8\u5728\u63A5\u9648\u660C\u56DE\u5EFA\u5EB7\u7684\u9014\u4E2D\uFF0C\u547D\u4EBA\u51FF\u6C89\u4E86\u9648\u660C\u7684\u5750\u8239\u3002\u968F\u7740\u9648\u660C\u6EBA\u4EA1\uFF0C\u9648\u84A8\u4E00\u52B3\u6C38\u9038\u5730\u89E3\u51B3\u4E86\u6240\u6709\u95EE\u9898\u3002</p><p><strong>\u9648\u6587\u5E1D\u9648\u84A8\u5728\u4F4D\u4E03\u5E74\uFF0C\u5BF9\u9648\u671D\u505A\u51FA\u7684\u6700\u5927\u8D21\u732E\uFF0C\u662F\u524A\u5E73\u4E86\u9648\u671D\u7684\u6240\u6709\u5185\u4E71</strong>\uFF1A\u7EE7\u4F4D\u7B2C\u4E8C\u5E74\uFF0C\u51FB\u8D25\u738B\u7433\uFF1B\u968F\u540E\uFF0C\u8FDB\u519B\u5DF4\u4E18\uFF0C\u903C\u9000<code>\u5317\u5468</code>\u5165\u4FB5\u7684\u519B\u961F\uFF1B\u4E4B\u540E\uFF0C\u53C8\u6536\u670D\u4E4B\u524D\u4E00\u76F4\u5904\u4E8E\u534A\u72EC\u7ACB\u72B6\u6001\u7684\u51E0\u4E2A\u5272\u636E\u52BF\u529B\u3002\u81F3\u6B64\uFF0C<strong>\u5357\u65B9\u9664<code>\u897F\u6881</code>\u4F9D\u6258\u4E8E\u5317\u5468\u4EE5\u5916\uFF0C\u5168\u90E8\u90FD\u7EB3\u5165\u4E86\u9648\u7684\u7EDF\u6CBB\uFF0C\u9648\u6210\u4E3A\u5357\u671D\u6B63\u7EDF\u3002</strong></p><p>\u516C\u5143566\u5E74\uFF0C\u9648\u6587\u5E1D\u9648\u84A8\u53BB\u4E16\uFF0C\u4E34\u7EC8\u524D\u4F20\u4F4D\u4E8E\u592A\u5B50<code>\u9648\u4F2F\u5B97</code>\uFF0C\u4EE5\u6587\u5E1D\u4E4B\u5F1F<code>\u9648\u987C</code>\u8F85\u653F\u3002\u4E09\u5E74\u540E\uFF0C<strong>\u9648\u987C\u5E9F\u9648\u4F2F\u5B97\u79F0\u5E1D\uFF0C\u662F\u4E3A<code>\u9648\u5BA3\u5E1D</code>\u3002</strong></p><p>\u9648\u5BA3\u5E1D\u5728\u4F4D\u671F\u95F4\uFF0C\u4E8E\u519B\u4E8B\u4E0A\u7684\u6700\u5927\u52A8\u4F5C\uFF0C\u662F\u5173\u4E8E<code>\u6DEE\u5357\u4E4B\u5730</code>\u7684\u4E89\u593A\uFF1A\u516C\u5143573\u5E74\uFF0C\u9648\u5BA3\u5E1D\u4EE5<code>\u5434\u660E\u5F7B</code>\u4E3A\u5E05\uFF0C\u6536\u590D\u6DEE\u5357\u5730\u3002578\u5E74\uFF0C\u5434\u660E\u5F7B\u4E89\u593A\u6DEE\u5317\u672A\u679C\uFF0C\u88AB\u5317\u5468\u6240\u4FD8\u3002<strong>579\u5E74\uFF0C\u5317\u5468\u653B\u5165\u6DEE\u5357\uFF0C\u9648\u671D\u518D\u6B21\u5931\u53BB\u957F\u6C5F\u4EE5\u5317\u7684\u6240\u6709\u9886\u571F\u3002</strong></p><p>\u81F3\u6B64\uFF0C<strong>\u9648\u671D\u5176\u5B9E\u4E27\u5931\u4E86\u8D56\u4EE5\u4E0E\u5317\u671D\u5E73\u5206\u5929\u4E0B\u7684\u6700\u5927\u5730\u5229\u3002</strong></p><p>\u5357\u65B9\u6240\u8C13\u7684\u201C\u957F\u6C5F\u5929\u9669\u201D\uFF0C\u5173\u952E\u5728\u4E8E\u6DEE\u6CB3\u9632\u7EBF\u3002<strong>\u6709<code>\u6DEE\u6CB3</code>\u5728\uFF0C\u957F\u6C5F\u624D\u80FD\u771F\u6B63\u6210\u4E3A\u5929\u9669</strong>\u3002\u81EA\u4E1C\u664B\u4EE5\u6765\uFF0C\u5357\u65B9\u671D\u5EF7\u65E0\u4E0D\u56FA\u5B88\u6DEE\u6CB3\u9632\u7EBF\u3002\u4F46\u5728\u4FAF\u666F\u4E4B\u4E71\u540E\uFF0C<code>\u5317\u9F50</code>\u8D81\u5357\u65B9\u5185\u4E71\uFF0C\u8D81\u673A\u593A\u53D6\u6DEE\u5357\u4E4B\u5730\u3002</p><p>\u9648\u671D\u6536\u590D\u6DEE\u5357\u5730\u65F6\uFF0C\u5317\u9F50\u6B63\u503C\u540E\u4E3B<code>\u9AD8\u7EAC</code>\u5728\u4F4D\uFF0C\u671D\u653F\u6DF7\u4E71\uFF0C\u519B\u4E8B\u65E0\u80FD\u3002\u4F46\u51E0\u5E74\u4EE5\u540E\uFF0C\u968F\u7740\u5317\u5468\u653B\u706D\u5317\u9F50\uFF0C\u9648\u9762\u5BF9\u7740\u5F3A\u5927\u7684\u5BF9\u624B\u65E0\u80FD\u4E3A\u529B\uFF0C\u53EA\u80FD\u5750\u770B\u5730\u5229\u4E27\u5931\u3002</p><p>\u53C8\u8FC7\u4E86\u4E09\u5E74\uFF0C\u9648\u5BA3\u5E1D\u9648\u987C\u75C5\u901D\uFF0C\u592A\u5B50<code>\u9648\u53D4\u5B9D</code>\u7EE7\u4F4D\uFF0C\u662F\u4E3A<code>\u9648\u540E\u4E3B</code>\u3002</p><p>\u56E0\u4E3A\u4EA1\u56FD\uFF0C\u6240\u4EE5\u4F5C\u4E3A\u7687\u5E1D\u7684\u9648\u53D4\u5B9D\u662F\u6CA1\u6709\u83B7\u8C25\u7684\u3002\u4F46\u5728\u9648\u4EA1\u540E\uFF0C\u4F5C\u4E3A\u968B\u671D\u7684\u957F\u5B89\u53BF\u516C\uFF0C\u9648\u53D4\u5B9D\u662F\u6709\u968B\u671D\u7ED9\u4ED6\u7684\u8C25\u53F7\u7684\uFF0C\u66F0\u201C<code>\u7080</code>\u201D\u2014\u2014\u5F53\u65F6\u968B\u671D\u5728\u4F4D\u7684\u7687\u5E1D\uFF0C\u6B63\u662F<code>\u6768\u5E7F</code>\u3002</p><p>\u867D\u7136\u8FD9\u4E2A\u6076\u610F\u6EE1\u6EE1\u7684\u5B57\u6700\u7EC8\u6210\u4E86\u6768\u5E7F\u7684\u4E13\u5C5E\u54C1\uFF0C\u4F46\u5355\u5C31\u8C25\u53F7\u672C\u8EAB\u800C\u8A00\uFF0C\u201C\u7080\u201D\u5176\u5B9E\u662F\u5F88\u9002\u5408\u9648\u53D4\u5B9D\u7684\u3002<strong>\u201C\u597D\u5185\u8FDC\u793C\u66F0\u7080\u201D</strong>\uFF0C\u9648\u53D4\u5B9D\u5728\u5F53\u7687\u5E1D\u7684\u51E0\u5E74\u4E2D\uFF0C\u6240\u4F5C\u6240\u4E3A\u53EF\u4EE5\u8BF4\u90FD\u56F4\u7740\u8FD9\u4E2A\u5B57\u5C55\u5F00\u3002<strong>\u8EAB\u4E3A\u7687\u5E1D\uFF0C\u4ED6\u786E\u5B9E\u8352\u5E9F\u4E86\u671D\u653F\uFF0C\u803D\u4E8E\u9152\u8272\uFF0C\u9189\u5FC3\u4E8E\u8BD7\u6587\u97F3\u4E50\u3002</strong></p><p>\u4F46\u7EC6\u770B\u4ED6\u5B9A\u683C\u5728\u5386\u53F2\u4E2D\u7684\u6700\u540E\u8EAB\u5F71\uFF0C\u4ED6\u5168\u90E8\u7684\u9519\uFF0C\u53EF\u80FD\u4E5F\u5C31\u4EC5\u4EC5\u4E8E\u6B64\u4E86\u3002</p><p>\u5E73\u5FC3\u800C\u8BBA\u7684\u8BF4\u4E00\u53E5\uFF0C**\u9648\u53D4\u5B9D\u7684\u4F5C\u4E3A\u786E\u5B9E\u8352\u6DEB\uFF0C\u4F46\u8FDC\u8FDC\u4E0D\u81F3\u4E8E\u4EA1\u56FD\u3002**\u5982\u679C\u6A2A\u5411\u5BF9\u6BD4\uFF0C\u90A3\u4E48\u4ED6\u6BD4\u5317\u9F50\u540E\u4E3B<code>\u9AD8\u7EAC</code>\u3001\u5317\u5468<code>\u5BA3\u5E1D\u5B87\u6587\u8D5F</code>\uFF0C\u8FD8\u8981\u5F3A\u4E0A\u4E0D\u5C11\u3002</p><p>\u56DE\u987E\u5B8C\u9648\u77ED\u6682\u7684\u5386\u53F2\uFF0C\u4F60\u4F1A\u53D1\u73B0\u5B83\u7684\u706D\u4EA1\u4E0E\u5176\u8BF4\u662F\u5185\u5FE7\u6240\u81F4\uFF0C\u8FD8\u4E0D\u5982\u8BF4\u662F\u5916\u60A3\u592A\u5F3A\u3002</p><p><strong>\u6700\u5927\u7684\u5916\u60A3\uFF0C\u662F\u968B\u671D\u3002\u800C\u968B\u6BD4\u9648\u8981\u5F3A\u4E0A\u592A\u591A\u3002</strong></p><p>\u6211\u4EEC\u56DE\u770B\u9648\u671D\u51E0\u4F4D\u7687\u5E1D\u5728\u4F4D\u671F\u95F4\u7684\u4F5C\u4E3A\uFF0C\u5747\u53EF\u4EE5\u79F0\u5F97\u4E0A\u662F\u53EF\u5708\u53EF\u70B9\u7684\u2014\u2014\u9664\u4E86\u9648\u53D4\u5B9D\u3002\u6240\u4EE5\uFF0C\u6211\u4EEC\u4E0D\u59A8\u6765\u505A\u8FD9\u4E48\u4E00\u4E2A\u5047\u8BBE\uFF1A\u5047\u5982\u968B\u706D\u9648\u65F6\uFF0C\u9047\u5230\u7684\u9648\u671D\u7687\u5E1D\u4E0D\u662F\u9648\u53D4\u5B9D\uFF0C\u800C\u662F\u8F83\u4E3A\u5F00\u660E\u7684\u9648\u5BA3\u5E1D\u9648\u987C\uFF0C\u90A3\u4E48\u7ED3\u679C\u4F1A\u5982\u4F55\uFF1F</p><p>\u6700\u5927\u7684\u53EF\u80FD\u662F\uFF0C<strong>\u968B\u706D\u9648\u7684\u6218\u4E89\u5C06\u4F1A\u6253\u5F97\u66F4\u60E8\u70C8\u4E00\u4E9B\uFF0C\u4F46\u6700\u540E\u7684\u7ED3\u679C\u5374\u4E0D\u4F1A\u53D8\uFF0C\u9648\u671D\u4F9D\u65E7\u6539\u53D8\u4E0D\u4E86\u88AB\u706D\u4EA1\u7684\u7ED3\u5C40\u3002</strong></p><p>\u56E0\u4E3A\u9648\u671D\u6240\u80FD\u4F9D\u4ED7\u7684\uFF0C\u5927\u6982\u4E5F\u5C31\u53EA\u5269\u6307\u671B\u7740\u201C\u541B\u81E3\u4E00\u5FC3\uFF0C\u5171\u514B\u65F6\u8270\u201D\u8FD9\u4E00\u6761\u4E86\u3002</p><p>\u4F46\u5F88\u96BE\u60F3\u8C61\uFF0C\u8FD9\u79CD\u7CBE\u795E\u7684\u529B\u91CF\u771F\u7684\u80FD\u591F\u62B5\u6D88\u6389\u5B9E\u529B\u4E0A\u7684\u5DEE\u8DDD\u3002</p><p>\u9648\u671D\u5373\u4FBF\u671D\u653F\u6E05\u660E\uFF0C\u767E\u59D3\u5B89\u5B9A\uFF0C\u5728\u9762\u5BF9\u968B\u671D\u7684\u201C\u4FB5\u7565\u201D\u65F6\uFF0C\u4E0E\u771F\u5B9E\u7684\u5386\u53F2\u76F8\u6BD4\uFF0C\u4ED6\u4EEC\u80FD\u505A\u5230\u7684\uFF0C\u53EF\u80FD\u4E5F\u5C31\u662F\u575A\u6301\u5F97\u66F4\u4E45\u4E00\u4E9B\u3002</p><p>\u968B\u706D\u9648\u4EE5\u540E\uFF0C\u4EC5\u5F97\u6237\u4E94\u5341\u4E07\uFF0C\u53E3\u4E8C\u767E\u4F59\u4E07\u3002\u800C\u5341\u51E0\u5E74\u524D\uFF0C\u5317\u5468\u706D\u5317\u9F50\u65F6\uFF0C\u6536\u6237\u53E3\u4E00\u5343\u4F59\u4E07\u3002\u518D\u4E4B\u540E\u968B\u4EE3\u5317\u5468\uFF0C\u4EBA\u53E3\u6570\u5E94\u8BE5\u5DF2\u7ECF\u8FBE\u5230\u4E86\u4E24\u5343\u4E07\u4EE5\u4E0A\u3002</p><p>\u8FD9\u79CD\u5DEE\u8DDD\u662F\u6570\u91CF\u7EA7\u7684\u3002</p><p>\u4E3A\u4EC0\u4E48\u9648\u7684\u6237\u53E3\u6570\u5982\u6B64\u4E4B\u5C11\uFF1F\u53EA\u6709\u4E24\u79CD\u53EF\u80FD\uFF1A\u4E0D\u662F\u771F\u7684\u5C11\uFF0C\u5C31\u662F\u88AB\u4E16\u5BB6\u5927\u65CF\u5360\u6709\u7684\u9690\u6237\u8FC7\u591A\u3002\u4F46\u4E0D\u7BA1\u54EA\u4E2A\u539F\u56E0\uFF0C\u6307\u5411\u7684\u7ED3\u679C\u90FD\u662F\u4E00\u6837\u7684\uFF1A<strong>\u9648\u671D\u7684<code>\u8D44\u6E90\u83B7\u53D6\u80FD\u529B</code>\u592A\u5F31</strong>\u3002</p><p>\u800C\u4E0E\u6B64\u540C\u65F6\uFF0C\u968B\u7684\u4F18\u52BF\u4E0D\u6B62\u5728\u7EDD\u5BF9\u6570\u91CF\u4E0A\uFF0C\u66F4\u5728\u5236\u5EA6\u5C42\u9762\u4E0A\uFF1A\u7ECF\u8FC7\u5317\u5468\u7684\u5E9C\u5175\u5236\u6539\u9769\u540E\uFF0C\u968B\u7684\u5F81\u5175\u5236\u5EA6\u5DF2\u7ECF\u6BD4\u9648\u671D\u4F20\u81EA\u6C49\u671D\u7684\u4E34\u65F6\u62DB\u52DF\u65B9\u6CD5\u6709\u4E86\u8D28\u7684\u63D0\u5347\u2014\u2014\u7528\u73B0\u4EE3\u7684\u8BED\u8A00\u6765\u9610\u8FF0\uFF0C\u5C31\u662F\u8FD9\u662F\u7BA1\u7406\u80FD\u529B\u4E0A\u7684\u63D0\u5347\uFF0C\u662F\u5F53\u4E0B\u4F01\u4E1A\u5728\u540C\u4E1A\u7ADE\u4E89\u4E2D\u5F97\u4EE5\u80DC\u51FA\u7684\u6700\u4E3B\u8981\u624B\u6BB5\u3002</p><p>\u786C\u5B9E\u529B\u4E0A\u7684\u78B0\u649E\u4ECE\u6765\u90FD\u4E0D\u63BA\u6C34\u5206\uFF0C\u9648\u51E0\u4E4E\u6CA1\u6709\u83B7\u80DC\u7684\u53EF\u80FD\u3002</p><p>\u9648\u636E\u6709\u6DEE\u5357\u4E4B\u5730\u5927\u7EA6\u53EA\u6709\u56DB\u5E74\uFF08\u516C\u5143573\u5E74-577\u5E74\uFF09\u65F6\u95F4\uFF0C\u5F97\u4E4B\u4E8E\u5317\u9F50\uFF0C\u5931\u4E4B\u4E8E\u5317\u5468\u3002\u800C\u5F97\u4E0E\u5931\u4E4B\u65F6\u7684\u4E3B\u5C06\uFF0C\u90FD\u662F\u5434\u660E\u5F7B\u3002</p><p>\u5982\u679C\u5355\u7EAF\u53EA\u770B\u8FD9\u6B21\u6218\u4E89\uFF0C\u770B\u4E0D\u51FA\u591A\u5927\u95EE\u9898\u3002\u4F46\u4F60\u53EA\u8981\u56DE\u6EAF\u5386\u53F2\uFF0C\u5C31\u80FD\u53D1\u73B0\u5176\u4E2D\u7684\u5DEE\u5F02\uFF1A\u65F6\u95F4\u518D\u5F80\u56DE\u5012\u4E03\u5341\u591A\u5E74\uFF0C\u90A3\u65F6\u7684\u5929\u4E0B\u8FD8\u662F<code>\u5317\u9B4F</code>\u4E0E<code>\u8427\u6881</code>\u76F8\u5BF9\u5CD9\u3002\u4ECE\u5357\u9F50\u672B\u5E74\u5F00\u59CB\uFF0C\u5357\u5317\u671D\u5728\u6DEE\u5357\u5730\u7684\u4E89\u593A\u6301\u7EED\u4E86\u5341\u51E0\u5E74\uFF0C\u6700\u7EC8\u4EE5\u5357\u65B9\u83B7\u80DC\u800C\u544A\u7EC8\u3002</p><p>\u5357\u671D\u83B7\u80DC\u7684\u6218\u5F79\uFF0C\u53F2\u79F0\u949F\u79BB\u4E4B\u6218\u3002\u6B64\u6218\u8427\u6881\u80FD\u591F\u83B7\u80DC\uFF0C\u56FA\u7136\u6709<code>\u66F9\u666F\u5B97</code>\u3001<code>\u97E6\u777F</code>\u7684\u4E2A\u4EBA\u4F5C\u7528\uFF0C<strong>\u4F46\u8427\u6881\u7684\u56FD\u529B\u5F3A\u76DB\uFF0C\u624D\u662F\u66F4\u4E3A\u5173\u952E\u7684\u539F\u56E0\u4E4B\u4E00</strong>\u3002</p><p>\u5728\u949F\u79BB\u4E4B\u6218\u4E4B\u524D\uFF0C\u4E24\u56FD\u5DF2\u7ECF\u6709\u8FC7\u4E00\u6B21\u5927\u6218\uFF0C\u5F53\u65F6\u8427\u6881\u60E8\u8D25\uFF0C\u53F7\u79F0\u201C\u79EF\u84C4\u4E3A\u4E4B\u4E00\u7A7A\u201D\uFF0C\u4F46\u8F6C\u773C\u51E0\u5E74\uFF0C\u5C31\u53C8\u80FD\u62C9\u51FA\u4E00\u652F\u519B\u961F\uFF0C\u518D\u6B21\u4F1A\u6218\u3002</p><p>\u800C\u9648\u671D\u5219\u4E0D\u540C\uFF0C\u5728\u5434\u660E\u5F7B\u5931\u8D25\u540E\uFF0C\u9648\u518D\u4E5F\u65E0\u529B\u4E89\u593A\u6DEE\u5357\u3002</p><p>\u5730\u5229\u7684\u4E27\u5931\uFF0C\u8868\u9762\u4E0A\u770B\u8D77\u6765\u662F\u56E0\u4E3A\u4F5C\u6218\u5931\u5229\u3002\u4F46\u6DF1\u5C42\u6B21\u7684\u539F\u56E0\uFF0C\u5374\u662F\u56FD\u529B\u7684\u8870\u8D25\u3002</p><p><strong>\u8FD9\u4E2A\u5927\u52BF\uFF0C\u4E0D\u662F\u4E00\u4EE3\u4EBA\u7684\u52AA\u529B\uFF0C\u800C\u662F\u51E0\u767E\u5E74\u7684\u79EF\u7D2F\u3002</strong></p><p>\u547D\u8FD0\u7559\u7ED9\u9648\u6C0F\u7684\uFF0C\u53EA\u6709\u4F59\u6656\u3002\u4ED6\u4EEC\u7684\u8868\u6F14\u4ECE\u4E00\u5F00\u59CB\uFF0C\u5C31\u662F\u7528\u6765\u8FCE\u63A5\u8C22\u5E55\u7684\u3002</p>',49),r=[t];function n(s,g){return p(),e("div",null,r)}const i=o(d,[["render",n],["__file","97.html.vue"]]);export{i as default};
