import{_ as p}from"./_plugin-vue_export-helper.cdc0426e.js";import{o,c as e,f as c}from"./app.eee000b7.js";const d={},t=c('<h1 id="\u5173\u9647\u96C6\u56E2" tabindex="-1"><a class="header-anchor" href="#\u5173\u9647\u96C6\u56E2" aria-hidden="true">#</a> \u5173\u9647\u96C6\u56E2</h1><blockquote><p>\u7F8E\u9633\u516C\u82CF\u5A01\uFF0C\u7EF0\u4E4B\u5B50\u4E5F\u2026\u2026\u968B\u4E3B\u4E3A\u4E1E\u76F8\uFF0C\u9AD8\u988E\u8350\u4E4B\uFF0C\u968B\u4E3B\u53EC\u89C1\uFF0C\u4E0E\u8BED\uFF0C\u5927\u60A6\uFF1B\u5C45\u6708\u9980\uFF0C\u95FB\u5C06\u53D7\u7985\uFF0C\u9041\u5F52\u7530\u91CC\u3002\u988E\u8BF7\u8FFD\u4E4B\uFF0C\u968B\u4E3B\u66F0\uFF1A\u201C\u6B64\u4E0D\u6B32\u9884\u543E\u4E8B\u8033\uFF0C\u7F6E\u4E4B\u3002\u201D\u53CA\u53D7\u7985\uFF0C\u5F81\u62DC\u592A\u5B50\u5C11\u4FDD\uFF0C\u8FFD\u5C01\u5176\u7236\u4E3A\u90B3\u516C\uFF0C\u4EE5\u5A01\u88AD\u7235\u3002</p></blockquote><p><strong>\u300A\u8D44\u6CBB\u901A\u9274\xB7\u5377\u4E00\u4E03\u4E94\xB7\u9648\u7EAA\u4E5D\u300B</strong></p><p>\u5173\u9647\u96C6\u56E2\uFF0C\u6307\u7684\u662F\u4ECE\u5317\u5468\u5230\u5510\u521D\u638C\u63E1\u6743\u529B\u7684\u4E00\u4E2A\u653F\u6CBB\u5B9E\u4F53\uFF0C\u4F46\u8FD9\u4E2A\u6982\u5FF5\uFF0C\u8981\u523020\u4E16\u7EAA\u521D\uFF0C\u624D\u7531\u53F2\u5B66\u5927\u5E08<code>\u9648\u5BC5\u606A</code>\u63D0\u51FA\u2014\u2014\u8FD9\u91CC\u7684\u201C\u5927\u5E08\u201D\uFF0C\u4E0D\u662F\u4E00\u4E2A\u70C2\u5927\u8857\u7684\u606D\u7EF4\u4E4B\u8BCD\uFF0C\u800C\u662F\u5BF9\u9648\u5148\u751F\u627F\u524D\u542F\u540E\u3001\u5F00\u5B97\u7ACB\u6D3E\u5F0F\u7684\u8D21\u732E\u7684\u5C0A\u79F0\u3002</p><p>**\u9648\u5BC5\u606A\u5148\u751F\u5F71\u54CD\u6700\u5927\u7684\u6210\u5C31\uFF0C\u662F\u4E2D\u53E4\u53F2\uFF0C\u4E5F\u5C31\u662F\u5173\u4E8E<code>\u9B4F\u664B\u5357\u5317\u671D</code>\u76F4\u81F3<code>\u968B\u5510</code>\u7684\u5236\u5EA6\u6D41\u53D8\u3002**\u5982\u4ECA\u51E0\u4E4E\u6240\u6709\u8FD9\u65B9\u9762\u7684\u5B66\u672F\u89C2\u5FF5\uFF0C\u90FD\u8131\u79BB\u4E0D\u4E86\u4ED6\u5F53\u5E74\u5212\u5B9A\u7684\u6846\u67B6\u3002</p><p>\u8FD9\u5C31\u662F\u9648\u5BC5\u606A\u5148\u751F\u7684\u5B66\u672F\u5730\u4F4D\u3002</p><p>\u800C\u5728\u4ED6\u7684\u5B66\u672F\u6210\u679C\u4E2D\uFF0C\u201C\u5173\u9647\u96C6\u56E2\u201D\u662F\u7279\u522B\u91CD\u8981\u7684\u4E00\u5757\u5185\u5BB9\u3002\u8FD9\u4E00\u6982\u5FF5\u7684\u63D0\u51FA\uFF0C\u89E3\u91CA\u4E86\u4ECE\u5357\u5317\u671D\u7EC8\u7ED3\u5230\u968B\u5510\u5F62\u6210\u7684\u5173\u952E\u539F\u56E0\u3002</p><p>\u867D\u7136\u4EE5\u5982\u4ECA\u7684\u5B66\u672F\u89C4\u8303\u800C\u8BBA\uFF0C\u8FD9\u4E2A\u5DF2\u7ECF\u63D0\u51FA\u4E86\u4E00\u4E2A\u4E16\u7EAA\u7684\u6982\u5FF5\u5F88\u4E0D\u5B8C\u6574\uFF1A\u9648\u5BC5\u606A\u5148\u751F\u5E76\u6CA1\u6709\u660E\u786E\u5173\u9647\u96C6\u56E2\u7684\u6784\u6210\u8303\u56F4\u3001\u6210\u5458\u5206\u5E03\u7B49\uFF0C\u800C\u53EA\u662F\u6709\u4E00\u4E2A\u5927\u4F53\u7684\u8303\u56F4\u3002<strong>\u8FD9\u4E00\u7FA4\u4F53\uFF0C\u4E3B\u8981\u5305\u62EC\u51FA\u8EAB\u4E8E<code>\u5317\u9B4F</code>\u516D\u9547\u7684\u6B66\u5DDD\u9547\u6B66\u4EBA\uFF0C\u4EE5\u53CA\u8FD9\u4E9B\u4EBA\u5728<code>\u897F\u9B4F\uFF08\u5317\u5468\uFF09</code>\u65F6\u671F\u4EE5\u540E\u6240\u5438\u7EB3\u7684\u5173\u4E2D\u8C6A\u5F3A\u3002</strong></p><p><strong>\u8FD9\u4E9B\u4EBA\uFF0C\u53EF\u4EE5\u8BF4\u662F\u5317\u5468\u8FD9\u4E2A\u653F\u6743\u7684\u6838\u5FC3\u529B\u91CF\u3002</strong></p><p>\u4F46\u662F\uFF0C\u5728\u6768\u575A\u7BE1\u4F4D\u7684\u8FC7\u7A0B\u4E2D\uFF0C\u4F60\u53C8\u57FA\u672C\u4E0A\u770B\u4E0D\u5230\u4ED6\u4EEC\u7684\u8EAB\u5F71\u3002</p><p>\u6768\u575A\u5728\u7BE1\u4F4D\u65F6\u671F\uFF0C\u671D\u5EF7\u4E2D\u6700\u4E3A\u4F9D\u4ED7\u7684\u4E09\u4E2A\u4EBA\u5206\u522B\u662F<code>\u9AD8\u988E</code>\u3001<code>\u674E\u5FB7\u6797</code>\u3001<code>\u6768\u96C4</code>\u3002\u5176\u4E2D\uFF0C\u6768\u96C4\u4E0E\u6768\u575A\u662F\u540C\u65CF\uFF1B\u9AD8\u988E\u4E4B\u7236\u81EA<code>\u5317\u9F50</code>\u6765\u5954\uFF0C\u5728\u5317\u5468\u5E76\u65E0\u6839\u57FA\uFF1B\u81F3\u4E8E\u674E\u5FB7\u6797\uFF0C\u672C\u662F\u5317\u9F50\u5B98\u540F\uFF0C\u9F50\u4EA1\u540E\u624D\u4ED5\u5468\u3002</p><p>\u4E5F\u5C31\u662F\u8BF4\uFF0C<strong>\u6768\u575A\u59D4\u4EE5\u8179\u5FC3\u4E4B\u4EFB\u7684\uFF0C\u5168\u90FD\u4E0D\u662F\u5173\u9647\u8D35\u65CF\u7684\u6210\u5458</strong>\u3002</p><p>\u76F8\u53CD\uFF0C\u4ED6\u771F\u6B63\u501F\u52A9\u7684\uFF0C\u90FD\u662F\u6CA1\u6709\u5173\u9647\u8D35\u65CF\u80CC\u666F\uFF0C\u5C24\u5176\u662F\u5BD2\u95E8\u51FA\u8EAB\u7684\u4EBA\uFF0C\u6216\u8005\u539F\u6765\u5728\u5317\u9F50\u4E3A\u5B98\u7684\u4EBA\u3002</p><p>\u4E3A\u4EC0\u4E48\uFF1F</p><p>\u56E0\u4E3A\u53EA\u6709\u8FD9\u4E9B\u5BD2\u95E8\u51FA\u8EAB\u8005\u548C\u5317\u9F50\u65E7\u4EBA\uFF0C\u6768\u575A\u624D\u771F\u6B63\u7ED9\u5F97\u4E86\u4ED6\u4EEC\u5BCC\u8D35\uFF0C\u80FD\u7528\u6743\u4F4D\u6765\u9A71\u4F7F\u4ED6\u4EEC\u4E3A\u5DF1\u6240\u7528\u3002</p><p>\u5012\u662F\u4E5F\u6709\u4F8B\u5916\u3002</p><p>\u5728\u5173\u9647\u96C6\u56E2\u4E2D\uFF0C\u6768\u575A\u5012\u4E5F\u6709\u81EA\u5DF1\u7684\u5FE0\u5B9E\u62E5\u8DB8\uFF1A<strong>\u4EAC\u5146\u97E6\u6C0F</strong>\u3002</p><p>\u4E3B\u8981\u662F<code>\u97E6\u5B5D\u5BBD</code>\u3002</p><p>\u97E6\u5B5D\u5BBD\u6210\u540D\u591A\u5E74\uFF0C\u5F53\u5E74\u7684\u7389\u74A7\u4E4B\u6218\u4E00\u6218\u6210\u540D\uFF0C\u95F4\u63A5\u62D6\u6B7B\u4E86<code>\u9AD8\u6B22</code>\u3002</p><p>**\u4F46\u5373\u4FBF\u529F\u9AD8\u5982\u6B64\uFF0C\u97E6\u5B5D\u5BBD\u5728\u897F\u9B4F\uFF08\u5317\u5468\uFF09\u4E5F\u4E00\u76F4\u6CA1\u6709\u591A\u5C11\u8BDD\u8BED\u6743\u3002**\u4ED6\u4E00\u751F\u7684\u4E8B\u4E1A\uFF0C\u90FD\u662F\u5728\u8FB9\u7586\u9632\u5907\u5317\u9F50\uFF0C\u53EF\u771F\u7684\u5230\u5317\u5468\u706D\u9F50\u4E4B\u6218\u65F6\uFF0C\u4E0D\u7BA1\u662F\u5728\u524D\u671F\u7684\u89C4\u5212\u90E8\u7F72\u4E2D\uFF0C\u8FD8\u662F\u5728\u6218\u4E89\u7684\u8FC7\u7A0B\u91CC\uFF0C\u97E6\u5B5D\u5BBD\u90FD\u5168\u7A0B\u4E0D\u88AB\u91CD\u89C6\uFF0C\u8981\u4E0D\u5C31\u662F\u610F\u89C1\u4E0D\u88AB\u91C7\u7EB3\uFF0C\u8981\u4E0D\u5C31\u662F\u4E0A\u4E0D\u4E86\u524D\u7EBF\u3002</p><p>\u867D\u7136\u53F2\u6599\u4E2D\u6CA1\u6709\u660E\u786E\u89E3\u91CA\u97E6\u6C0F\u4E3A\u4EC0\u4E48\u4F1A\u575A\u5B9A\u7684\u7AD9\u5728\u6768\u575A\u4E00\u8FB9\uFF0C\u4F46\u5176\u5728\u5317\u5468\u7684\u88AB\u8FB9\u7F18\u5316\uFF0C\u5F88\u53EF\u80FD\u662F\u539F\u56E0\u4E4B\u4E00\u3002</p><p>\u603B\u4E4B\uFF0C\u5728\u6768\u575A\u6267\u653F\u4EE5\u540E\uFF0C\u97E6\u5B5D\u5BBD\u8FD9\u4E2A<code>\u5B87\u6587\u6CF0</code>\u65F6\u4EE3\u7684\u8001\u81E3\uFF0C\u6BEB\u4E0D\u72B9\u8C6B\u7684\u7AD9\u5230\u4E86\u6768\u575A\u4E00\u8FB9\u3002\u4ED6\u4EE5\u4E3B\u5C06\u7684\u8EAB\u4EFD\uFF0C\u4E3B\u6301\u5E73\u5B9A\u4E86<code>\u5C09\u8FDF\u8FE5</code>\u7684\u53DB\u4E71\u2014\u2014\u8981\u77E5\u9053\uFF0C\u6B64\u65F6\u8DDD\u79BB\u7389\u74A7\u4E4B\u6218\u5DF2\u7ECF\u8FC7\u53BB\u4E8634\u5E74\u3002\u5F53\u5E74\u97E6\u5B5D\u5BBD\u53EA\u670938\u5C81\uFF0C\u6B63\u503C\u58EE\u5E74\uFF0C\u800C\u5982\u4ECA\u7684\u4ED6\u5DF2\u7ECF72\u5C81\uFF0C\u5E73\u53DB\u540E\u4E00\u4E2A\u6708\u5373\u4FBF\u75C5\u901D\u3002</p><p>\u97E6\u5B5D\u5BBD\u8FD9\u4E00\u7ACB\u573A\uFF0C\u7ED9\u4EAC\u5146\u97E6\u6C0F\u5E26\u6765\u7684\u5B9E\u60E0\u5F53\u7136\u662F\u7ACB\u7AFF\u89C1\u5F71\u7684\u3002\u4EAC\u5146\u97E6\u6C0F\u867D\u7136\u53F7\u79F0\u662F\u5173\u4E2D\u671B\u65CF\uFF0C\u4E5F\u7B97\u662F\u5173\u9647\u8D35\u65CF\u7684\u6210\u5458\u4E4B\u4E00\uFF0C\u4F46\u4ED6\u4EEC\u8FDC\u8FDC\u4E0D\u662F\u6838\u5FC3\u6210\u5458\u3002\u5728\u5317\u5468\u65F6\u671F\uFF0C\u97E6\u6C0F\u5B50\u5F1F\u867D\u7136\u6709\u5728\u5B98\u8005\uFF0C\u4F46\u57FA\u672C\u4E0A\u90FD\u53EA\u5728\u5730\u65B9\u4E3A\u5B98\uFF0C\u800C\u5728\u671D\u8005\u6781\u5C11\uFF0C\u66F4\u8C08\u4E0D\u4E0A\u8FDB\u5165\u6743\u529B\u6838\u5FC3\u3002</p><p>\u53EF\u7ECF\u6B64\u4E00\u5F79\uFF0C\u4EAC\u5146\u97E6\u6C0F\u5728\u5173\u9647\u8D35\u65CF\u4E2D\u7684\u5730\u4F4D\u6709\u4E86\u6781\u5927\u63D0\u5347\uFF1A\u97E6\u5B5D\u5BBD\u7684\u5B50\u4F84\u8F88\uFF0C\u5728\u968B\u671D\u4E3A\u5B98\u8005\u4EBA\u6570\u5927\u589E\u3002\u51E0\u5341\u5E74\u4EE5\u540E\uFF0C\u5728\u968B\u5510\u5B17\u53D8\u7684\u8FC7\u7A0B\u4E2D\uFF0C\u97E6\u6C0F\u53C8\u4E00\u6B21\u6210\u529F\u7AD9\u961F\uFF0C\u6700\u7EC8\u8BA9\u4EAC\u5146\u97E6\u6C0F\u6210\u4E3A\u4E86\u5510\u671D\u7684\u9876\u7EA7\u58EB\u65CF\u4E4B\u4E00\u3002</p><p>\u57CE\u5357\u97E6\u675C\uFF0C\u53BB\u5929\u5C3A\u4E94\u3002</p><p><strong>\u53EF\u5927\u90E8\u5206\u7684\u5173\u9647\u8D35\u65CF\uFF0C\u5E76\u4E0D\u4F1A\u50CF\u97E6\u6C0F\u8FD9\u4E48\u505A\u3002</strong></p><p>\u4ED6\u4EEC\u7684\u5178\u578B\u505A\u6CD5\uFF0C\u53EF\u4EE5\u4EE5<code>\u82CF\u7EF0</code>\u7684\u513F\u5B50<code>\u82CF\u5A01</code>\u4E3A\u5178\u578B\u3002</p><p>\u82CF\u7EF0\u5728\u5317\u5468\u5F00\u56FD\u7684\u8FC7\u7A0B\u4E2D\u53D1\u6325\u4E86\u91CD\u8981\u4F5C\u7528\uFF0C\u867D\u7136\u6CA1\u6709\u6D3B\u5230\u5317\u5468\u5EFA\u7ACB\uFF0C\u4F46\u6240\u521B\u7ACB\u7684\u529F\u52CB\u5374\u662F\u65E0\u6CD5\u78E8\u706D\u7684\u3002\u82CF\u5A01\u5C11\u6709\u4EE4\u540D\uFF0C\u540C\u65F6\u53C8\u6709\u7236\u4EB2\u5149\u73AF\u7684\u52A0\u6301\uFF0C\u53EF\u8C13\u5E74\u5C11\u5F97\u610F\uFF0C\u524D\u7A0B\u65E0\u91CF\u3002</p><p>\u4F46\u82CF\u5A01\u5374\u5BF9\u5317\u5468\u7684\u6D3E\u7CFB\u6597\u4E89\u6781\u5EA6\u6392\u65A5\uFF1A<code>\u5B87\u6587\u62A4</code>\u5F53\u653F\u65F6\uFF0C\u4ED6\u66FE\u7ECF\u4E00\u5EA6\u8F9E\u5B98\u3002\u540E\u6765\u6768\u575A\u505A\u5927\u4E1E\u76F8\uFF0C\u66FE\u628A\u82CF\u5A01\u8BF7\u51FA\u6765\u505A\u5B98\uFF0C\u82CF\u5A01\u4E5F\u7B54\u5E94\u4E86\u3002\u4F46\u4E0D\u4E45\u4EE5\u540E\uFF0C\u82CF\u5A01\u5C31\u53C8\u8F9E\u5B98\u4E0D\u505A\uFF0C\u9690\u5C45\u5C71\u4E2D\u3002\u6709\u4EBA\u5411\u6768\u575A\u544A\u53D1\u6B64\u4E8B\uFF0C\u8981\u6C42\u60E9\u6CBB\u82CF\u5A01\uFF0C\u53EF\u6768\u575A\u6446\u4E86\u6446\u624B\u62D2\u7EDD\u9053\uFF1A\u8FD9\u662F\u4ED6\u4E0D\u60F3\u63BA\u548C\u6211\u4EEC\u5BB6\u7684\u5BB6\u4E8B\uFF0C\u7531\u4ED6\u53BB\u5427\u3002</p><p>\u4E4B\u540E\uFF0C\u6768\u5EFA\u7BE1\u4F4D\uFF0C\u518D\u8BF7\u82CF\u5A01\u51FA\u5C71\uFF0C\u82CF\u5A01\u53C8\u6109\u5FEB\u7684\u540C\u610F\u4E86\u3002</p><p>\u8FD9\u662F\u4EC0\u4E48\u4E2A\u610F\u601D\uFF1F</p><p>\u4F60\u8BA9\u6211\u5F53\u5B98\uFF0C\u53EF\u4EE5\uFF1B\u4F46\u4F60\u7BE1\u4F4D\uFF0C\u6307\u671B\u6211\u7ED9\u4F60\u7AD9\u53F0\uFF0C\u90A3\u4E0D\u884C\u3002</p><p><strong>\u5317\u5468\u4E0E\u968B\u7684\u7985\u4EE3\uFF0C\u5BF9\u5173\u9647\u8D35\u65CF\u800C\u8A00\uFF0C\u5E76\u4E0D\u662F\u56FD\u5BB6\u7684\u66F4\u66FF\uFF0C\u800C\u66F4\u50CF\u4E00\u5BB6\u80A1\u4EFD\u5236\u516C\u53F8\u5185\u90E8\u7684\u80A1\u4E1C\u5728\u4E89\u593A\u63A7\u5236\u6743\u3002</strong></p><p><strong>\u5173\u9647\u8D35\u65CF\u4F5C\u4E3A\u201C\u5176\u4ED6\u4E2D\u5C0F\u80A1\u4E1C\u201D\uFF0C\u5728\u8FD9\u79CD\u4E89\u6597\u4E2D\uFF0C\u662F\u88AB\u53CC\u65B9\u62C9\u62E2\u7684\u5BF9\u8C61\uFF0C\u6240\u4EE5\u4ED6\u4EEC\u5E76\u6CA1\u6709\u5FC5\u8981\u6301\u7279\u522B\u660E\u786E\u7684\u7ACB\u573A</strong>\uFF1A\u56E0\u4E3A\u4E0D\u7BA1\u8C01\u80DC\u8C01\u8D1F\uFF0C\u4ED6\u4EEC\u7684\u65E2\u6709\u6743\u5229\u90FD\u4E0D\u4F1A\u88AB\u524A\u5F31\uFF0C\u53CD\u800C\u8FD8\u4F1A\u56E0\u4E3A\u53CC\u65B9\u7684\u4E89\u76F8\u51FA\u4EF7\u800C\u6709\u6240\u63D0\u9AD8\u3002</p><p>\u76F8\u53CD\uFF0C\u5012\u662F\u9009\u8FB9\u7AD9\u624D\u6709\u98CE\u9669\u3002</p><p>\u5982\u679C\u7AD9\u5BF9\u4E86\uFF0C\u53EF\u80FD\u4F1A\u5728\u83B7\u80DC\u4E4B\u540E\u5206\u5F97\u90E8\u5206\u4EA7\u4E1A\uFF0C\u8FD9\u5927\u6982\u5C5E\u4E8E\u9526\u4E0A\u6DFB\u82B1\u3002\u4F46\u5982\u679C\u5931\u8D25\u4E86\uFF0C\u90A3\u4EE3\u4EF7\u5C31\u5927\u4E86\u53BB\u4E86\uFF0C\u52A8\u8F84\u5C31\u662F\u706D\u95E8\u4E4B\u7978\u3002\u5BF9\u5173\u9647\u8D35\u65CF\u6765\u8BF4\uFF0C\u8FD9\u662F\u4E00\u7B14\u5178\u578B\u7684\u98CE\u9669\u6536\u76CA\u4E0D\u6210\u6BD4\u4F8B\u7684\u6295\u8D44\u3002</p><p>\u6240\u4EE5\u6700\u597D\u7684\u529E\u6CD5\uFF0C\u5C31\u662F\u4F5C\u58C1\u4E0A\u89C2\u3002</p><p>\u90A3\u4E48\uFF0C\u5C31\u771F\u7684\u6CA1\u6709\u8EAB\u5904\u5173\u9647\u96C6\u56E2\u6838\u5FC3\uFF0C\u800C\u7AD9\u53F0\u5B87\u6587\u6C0F\u3001\u8DF3\u51FA\u6765\u65D7\u5E1C\u9C9C\u660E\u7684\u53CD\u5BF9\u6768\u575A\u7684\u4EBA\u5417\uFF1F</p><p>\u5F53\u7136\u4E5F\u6709\u2014\u2014\u867D\u7136\u5F88\u5C11\u3002</p><p>\u4EE3\u8868\u662F<code>\u5C09\u8FDF\u8FE5</code>\u3002</p><p>\u4F5C\u4E3A\u5B87\u6587\u6C0F\u4E00\u65B9\u7684\u5F97\u529B\u5E72\u5C06\uFF0C\u4ED6\u5C31\u6DF1\u5EA6\u4ECB\u5165\u5230\u4E86\u53CC\u65B9\u7684\u6597\u4E89\u4E2D\u6765\u3002</p><p>\u5C09\u8FDF\u8FE5\u7684\u6BCD\u4EB2\u662F\u5B87\u6587\u6CF0\u7684\u59D0\u59D0\uFF0C\u6240\u4EE5\u5C09\u8FDF\u8FE5\u662F\u5317\u5468\u6B63\u724C\u7684\u7687\u4EB2\u56FD\u621A\u3002\u4F5C\u4E3A\u5B87\u6587\u6CF0\u4E4B\u7525\uFF0C\u4ED6\u4ECE\u5C0F\u5C31\u8DDF\u968F\u5B87\u6587\u6CF0\uFF0C\u540E\u6765\u53C8\u62C5\u4EFB\u5E73\u5B9A\u8700\u5730\u7684\u4E3B\u5C06\u3002\u5728\u5B87\u6587\u9095\u4F10\u9F50\u671F\u95F4\uFF0C\u5C09\u8FDF\u8FE5\u4E0D\u4EC5\u5168\u7A0B\u53C2\u4E0E\uFF0C\u8FD8\u7ACB\u6709\u5927\u529F\u3002\u5230\u5B87\u6587\u9095\u6B7B\u540E\uFF0C\u5C09\u8FDF\u8FE5\u53C8\u4EFB\u76F8\u5DDE\u4E3B\u7BA1\uFF0C\u8FD9\u610F\u5473\u7740\u5317\u5468\u51E0\u4E4E\u628A\u5317\u9F50\u6545\u5730\u7684\u7BA1\u7406\u6743\u5168\u90E8\u4EA4\u7ED9\u4E86\u5C09\u8FDF\u8FE5\u3002</p><p><strong>\u5982\u679C\u8BF4\uFF0C\u5173\u9647\u8D35\u65CF\u4E0E\u5317\u5468\u8FD9\u4E2A\u653F\u6743\u662F\u6DF1\u5EA6\u7ED1\u5B9A\u7684\u3002\u90A3\u4E48\u5728\u6B64\u4E4B\u5916\uFF0C\u5C09\u8FDF\u8FE5\u5C31\u8FD8\u6709\u4E00\u5C42\u989D\u5916\u7684\u8EAB\u4EFD\u201C\u7ED1\u5B9A\u201D\uFF1A\u4E0E\u5B87\u6587\u6C0F\u7684\u6DF1\u5EA6\u7ED1\u5B9A\u3002</strong></p><p>\u5176\u4ED6\u7684\u5173\u9647\u8D35\u65CF\u6210\u5458\uFF0C\u6216\u662F\u51FA\u8EAB\u4E8E\u5317\u9B4F\u65F6\u671F\u7684\u6B66\u5DDD\u9547\uFF0C\u6216\u662F\u5173\u4E2D\u8C6A\u5F3A\uFF0C\u800C\u5C09\u8FDF\u6C0F\u5219\u54EA\u4E00\u5934\u90FD\u4E0D\u6CBE\u3002\u4ED6\u4EC5\u4EC5\u662F\u56E0\u4E3A\u5B87\u6587\u6C0F\u7684\u8D4F\u8BC6\uFF0C\u624D\u8FDB\u5165\u5230\u5317\u5468\u653F\u6743\u7684\u4E0A\u5C42\u3002</p><p>\u800C\u4E14\u59FB\u4EB2\u5173\u7CFB\u4E5F\u76F8\u5BF9\u7B80\u5355\uFF0C\u4E3B\u8981\u5C31\u662F\u5B87\u6587\u6C0F\u3002</p><p>\u6240\u4EE5\u5C09\u8FDF\u8FE5\u4E0D\u53EF\u80FD\u50CF\u5176\u4ED6\u7684\u5173\u9647\u8D35\u65CF\u6210\u5458\u4E00\u6837\u5750\u5C71\u89C2\u864E\u6597\uFF0C\u4ED6\u5929\u7136\u5C31\u7AD9\u5728\u6768\u575A\u7684\u53CD\u5BF9\u9762\u4E0A\u3002</p><p>\u4E8E\u516C\uFF0C\u4ED6\u53D7\u77E5\u9047\u4E8E\u5B87\u6587\u6CF0\uFF0C\u6709\u62A5\u7B54\u7684\u4E49\u52A1\u3002\u4E8E\u79C1\uFF0C\u5B87\u6587\u6C0F\u7684\u5931\u8D25\uFF0C\u5C31\u662F\u5C09\u8FDF\u6C0F\u7684\u5931\u8D25\uFF0C\u5C31\u7B97\u4ED6\u613F\u610F\u6295\u9760\u6768\u575A\uFF0C\u4EE5\u5C09\u8FDF\u6C0F\u548C\u5B87\u6587\u6C0F\u7684\u5173\u7CFB\uFF0C\u4E5F\u514D\u4E0D\u4E86\u65A9\u8349\u9664\u6839\u7684\u4E0B\u573A\u3002</p><p>\u5C09\u8FDF\u8FE5\u53CD\u6768\u575A\uFF0C\u8FD9\u662F\u677F\u4E0A\u9489\u9489\u7684\u4E8B\u3002</p><p>\u800C\u5C09\u8FDF\u8FE5\u4E5F\u7684\u786E\u6CA1\u6709\u8F9C\u8D1F\u5B87\u6587\u9095\u7684\u91CD\u6258\uFF0C\u5728\u6768\u575A\u6267\u653F\u540E\uFF0C\u4ED6\u7B2C\u4E00\u4E2A\u5C31\u7AD9\u51FA\u6765\u53CD\u5BF9\u6768\u575A\u3002</p><p>\u6070\u597D\uFF0C\u4ED6\u4E5F\u6709\u8FD9\u4E2A\u53CD\u5BF9\u7684\u80FD\u529B\u3002</p><p>\u4F46\u8FD9\u573A\u53CD\u53DB\uFF0C\u4EC5\u4EC5\u6301\u7EED\u4E8668\u5929\u2014\u2014\u5176\u4E2D\u5931\u8D25\u7684\u539F\u56E0\u6709\u5F88\u591A\uFF0C\u4F46\u5B83\u4EEC\u4E0D\u662F\u672C\u6587\u5173\u6CE8\u6240\u5728\uFF0C\u8FD9\u91CC\u5C31\u4E0D\u5C55\u5F00\u4E86\uFF0C\u6211\u4EEC\u53EA\u5173\u5FC3\u7ED3\u679C\uFF1A<strong>\u5728\u5C09\u8FDF\u8FE5\u5931\u8D25\u4EE5\u540E\uFF0C\u5C09\u8FDF\u6C0F\u51E0\u4E4E\u88AB\u8FDE\u6839\u62D4\u8D77\uFF0C\u518D\u65E0\u6839\u57FA\u3002</strong></p><p>\u5BF9\u522B\u7684\u5BB6\u65CF\u6765\u8BF4\uFF0C\u8FD9\u6837\u6781\u7AEF\u7684\u7ACB\u573A\uFF0C\u5B9E\u5728\u662F\u592A\u8FC7\u5371\u9669\u4E86\u3002</p><p>\u8FD8\u662F\u5B66\u82CF\u5A01\u7684\u4E0D\u542D\u58F0\u6BD4\u8F83\u597D\u3002</p><p>\u5982\u679C\u6309\u7167\u5FE0\u541B\u62A5\u56FD\u7684\u89C2\u5FF5\u6765\u7406\u89E3\u5173\u9647\u8D35\u65CF\u7684\u884C\u4E3A\uFF0C\u90A3\u4E48\u5F97\u51FA\u7684\u7ED3\u8BBA\u81EA\u7136\u662F\u8D1F\u9762\u7684\uFF1A\u8FD9\u4E9B\u4EBA\u4E0D\u80FD\u201C\u98DF\u541B\u4E4B\u7984\uFF0C\u5FE0\u541B\u4E4B\u4E8B\u201D\uFF0C\u662F\u5178\u578B\u7684\u5FD8\u6069\u8D1F\u4E49\u3002</p><p>\u4F46\u79CD\u8BBA\u65AD\uFF0C\u8FD9\u5E76\u4E0D\u7B26\u5408\u5F53\u65F6\u7684\u5B9E\u60C5\u3002</p><p>\u5F53\u65F6\u7684\u5B9E\u60C5\uFF0C\u5982\u679C\u8981\u8FFD\u6839\u6EAF\u6E90\uFF0C\u90A3\u4E48\u5E94\u8BE5\u4ECE\u516C\u5143578\u5E74\u8BF4\u8D77\uFF1A\u8FD9\u4E00\u5E74\uFF0C\u5317\u5468<code>\u6B66\u5E1D\u5B87\u6587\u9095</code>\u53BB\u4E16\uFF0C\u592A\u5B50<code>\u5B87\u6587\u8D5F</code>\u7EE7\u4F4D\uFF0C\u662F\u4E3A<code>\u5BA3\u5E1D</code>\u3002</p><p><strong>\u5BA3\u5E1D\u4ECE\u7EE7\u4F4D\u5F00\u59CB\uFF0C\u5C31\u6709\u610F\u8BC6\u7684\u6392\u65A5\u5B87\u6587\u6C0F\u7684\u5B97\u65CF\u52BF\u529B</strong>\uFF0C\u5C24\u5176\u662F\u53D4\u7236\u4E00\u8F88\uFF0C\u4EE5<code>\u9F50\u738B\u5B87\u6587\u5BAA</code>\u4E3A\u9996\uFF0C\u540E\u7EED\u5305\u62EC<code>\u8D75\u738B\u5B87\u6587\u62DB</code>\u3001<code>\u9648\u738B\u5B87\u6587\u7EAF</code>\u3001<code>\u8D8A\u738B\u5B87\u6587\u76DB</code>\u3001<code>\u4EE3\u738B\u5B87\u6587\u8FBE</code>\u3001<code>\u6ED5\u738B\u5B87\u6587\u900C</code>\u7B49\u4E94\u738B\uFF0C\u88AB\u9646\u7EED\u9063\u79BB\u957F\u5B89\u3002</p><p>\u5728\u5B87\u6587\u8D5F\u770B\u6765\uFF0C\u8FD9\u662F\u4ED6\u7EF4\u6301\u7687\u4F4D\u7684\u5FC5\u7136\u9009\u62E9\u3002\u6B64\u65F6\u7684\u4ED6\u53EA\u670920\u5C81\uFF0C\u800C\u5B87\u6587\u5BAA\u7B49\u53D4\u7236\u4EEC\uFF0C\u57FA\u672C\u4E0A\u90FD\u5904\u5728\u4E09\u5341\u591A\u5C81\u7684\u58EE\u5E74\u3002\u5982\u679C\u8BA9\u4ED6\u4EEC\u638C\u63E1\u4E86\u592A\u591A\u6743\u529B\uFF0C\u90A3\u4E48\u4EE5\u5357\u5317\u671D\u65F6\u671F\u5B97\u5BA4\u4F5C\u4E71\u7684\u653F\u6CBB\u98CE\u6C14\uFF0C\u65E0\u7591\u662F\u53D6\u4E71\u4E4B\u6E90\u3002</p><p>\u4F46\u5174\u4E00\u5229\u5FC5\u751F\u4E00\u5F0A\uFF0C\u5B87\u6587\u8D5F\u6B7B\u540E\u6768\u575A\u8FC5\u901F\u7BE1\u4F4D\u7684\u5386\u53F2\uFF0C\u5C31\u8BA9\u540E\u4E16\u89C1\u8BC6\u5230\u4E86\u7687\u65CF\u52BF\u529B\u8870\u5F31\u7684\u5DE8\u5927\u5371\u9669\uFF1A**\u5F53\u4E00\u4E2A\u738B\u671D\u771F\u7684\u5230\u4E86\u5371\u673A\u65F6\u523B\uFF0C\u53EA\u6709<code>\u540C\u59D3\u5B97\u5BA4</code>\u624D\u662F\u771F\u6B63\u7684\u67F1\u77F3\u91CD\u81E3\u3002**\u867D\u7136\u897F\u664B\u7684\u516B\u738B\u4E4B\u4E71\u662F\u5929\u4E0B\u5927\u4E71\u7684\u7F18\u8D77\uFF0C\u4F46\u540C\u59D3\u5B97\u5BA4\u7684\u4F5C\u7528\uFF0C\u5374\u53C8\u662F\u4E0D\u53EF\u6216\u7F3A\u7684\u3002</p><p>\u6709\u5F88\u591A\u4EBA\u6279\u8BC4\u5317\u5468\u6B66\u5E1D\u5B87\u6587\u9095\uFF0C\u8BA4\u4E3A\u5B87\u6587\u9095\u6B7B\u540E\u4E09\u5E74\u5317\u5468\u5373\u4FBF\u706D\u4EA1\uFF0C\u6240\u4EE5\u4ED6\u5BF9\u6B64\u8D1F\u6709\u4E0D\u53EF\u63A8\u5378\u7684\u8D23\u4EFB\u3002\u4F46\u4E8B\u5B9E\u4E0A\uFF0C<strong>\u5B87\u6587\u9095\u5728\u81EA\u5DF1\u7684\u4F53\u5236\u4E2D\uFF0C\u5BF9\u4E8E\u5B97\u5BA4\u7684\u91CD\u7528\uFF0C\u5929\u7136\u6709\u9632\u8303\u5F02\u59D3\u7BE1\u4F4D\u7684\u4F5C\u7528</strong>\u3002</p><p>\u5728\u7ECF\u8FC7\u4E09\u767E\u591A\u5E74\u7684\u6218\u4E71\u540E\uFF0C\u7687\u6743\u7684\u5A01\u4E25\u5982\u4ECA\u5DF2\u6D88\u5931\u6B86\u5C3D\uFF0C\u201C\u5F02\u79CD\u79F0\u738B\u201D\u7684\u4E8B\u4E0D\u80DC\u679A\u4E3E\u3002\u5728\u8FD9\u79CD\u60C5\u51B5\u4E0B\uFF0C\u5B97\u65CF\u52BF\u529B\u5BF9\u7687\u6743\u7684\u5E87\u4F51\u5FC5\u4E0D\u53EF\u5C11\u3002\u867D\u7136\u8FD9\u540C\u65F6\u53C8\u4F1A\u6709\u5B97\u5BA4\u6B8B\u6740\u7684\u524D\u8F66\u4E4B\u9274\uFF0C\u4F46\u5B87\u6587\u9095\u4EE5\u5F3A\u6743\u541B\u4E3B\u7684\u5A01\u671B\uFF0C\u81EA\u80FD\u5E94\u4ED8\u4E8E\u6B64\u3002</p><p>\u56DE\u987E\u5B87\u6587\u9095\u7684\u4EBA\u751F\uFF0C\u4ED6\u4ECE\u767B\u57FA\u4E3A\u5E1D\uFF0C\u5230\u6700\u7EC8\u6536\u56DE\u6743\u529B\uFF0C\u65E0\u4E0D\u7ECF\u5386\u4E86\u6B8B\u9177\u7684\u5B97\u5BA4\u6B8B\u6740\uFF1A\u4E24\u4F4D\u5144\u957F\u6B7B\u4E8E\u5802\u5144<code>\u5B87\u6587\u62A4</code>\uFF0C\u4ED6\u53C8\u8E0F\u7740\u5B87\u6587\u62A4\u7684\u5C38\u4F53\u767B\u4E0A\u6743\u529B\u7684\u5DC5\u5CF0\u3002\u6B64\u540E\uFF0C\u4ED6\u8FD8\u8BDB\u6740\u4E86\u4F01\u56FE\u7BE1\u4F4D\u7684<code>\u536B\u738B\u5B87\u6587\u76F4</code>\u3002</p><p>\u4F46\u6B64\u540E\uFF0C\u5728\u6709\u751F\u4E4B\u5E74\uFF0C<strong>\u5B87\u6587\u9095\u57FA\u672C\u4E0A\u7EF4\u6301\u4E86\u5B97\u5BA4\u7684\u548C\u5E73\uFF0C\u5E76\u4E14\u5728\u6CBB\u56FD\u548C\u519B\u4E8B\u4E0A\u6781\u529B\u4F9D\u4ED7\u5B97\u5BA4\u6210\u5458</strong>\u3002\u751A\u81F3\u4E8E\uFF0C\u76F4\u5230\u4E34\u7EC8\uFF0C\u4ED6\u8FD8\u7ED9\u5B87\u6587\u8D5F\u4E0B\u9057\u8BCF\uFF0C<strong>\u201C\u4E0D\u5F97\u6B8B\u6740\u5B97\u5BA4\u201D</strong>\u3002</p><p>\u53EF\u5B87\u6587\u8D5F\u5374\u5C06\u6B64\u629B\u4E4B\u4E8E\u8111\u540E\u3002\u5B87\u6587\u9095\u5C38\u9AA8\u672A\u5BD2\uFF0C\u4ED6\u4FBF\u6740\u6B7B\u4E86\u9F50\u738B\u5B87\u6587\u5BAA\uFF0C\u4E14\u5BF9\u8BF8\u738B\u7684\u731C\u5FCC\u65E5\u751A\u4E00\u65E5\uFF0C\u6700\u7EC8\u903C\u8D70\u4E94\u738B\uFF0C\u7ED9\u6768\u575A\u4EE5\u53EF\u4E58\u4E4B\u673A\u3002</p><p><strong>\u4E0D\u662F\u5B87\u6587\u9095\u8EAB\u524D\u601D\u8651\u672A\u53CA\uFF0C\u800C\u662F\u5B87\u6587\u8D5F\u4EB2\u624B\u7834\u574F\u4E86\u5B87\u6587\u9095\u5F53\u5E74\u7684\u89C4\u5219\u3002\u518D\u52A0\u4E4B\u5B87\u6587\u8D5F\u81EA\u5DF1\u53C8\u77ED\u5BFF\uFF0C\u624D\u7EC8\u4E8E\u8BA9\u8FD9\u4E2A\u6F0F\u6D1E\u7ED9\u4ED6\u4EBA\u4EE5\u53EF\u4E58\u4E4B\u673A\u3002</strong></p><p>\u5343\u5E74\u4EE5\u540E\uFF0C\u6211\u4EEC\u590D\u76D8\u6768\u575A\u7684\u7BE1\u4F4D\uFF0C\u8981\u770B\u5230\u662F\u592A\u591A\u5076\u7136\u56E0\u7D20\u7684\u5939\u6742\uFF0C\u624D\u8BA9\u6768\u575A\u5F97\u4EE5\u6210\u529F\u3002\u800C\u5317\u5468\u706D\u4EA1\u5F97\u5982\u6B64\u4E4B\u5FEB\uFF0C\u8FD9\u4E2A\u8D23\u4EFB\u65E0\u8BBA\u5982\u4F55\u90FD\u4E0D\u5E94\u8BE5\u8BA9\u5B87\u6587\u9095\u6765\u80CC\u3002</p><p><strong>\u5B87\u6587\u8D5F\u5BF9\u5B97\u5BA4\u7684\u731C\u5FCC\uFF0C\u624D\u662F\u771F\u6B63\u8BA9\u8FD9\u4E2A\u56FD\u5BB6\u201C\u6881\u67F1\u6467\u6298\u201D\u7684\u539F\u56E0\u3002</strong></p>',67),r=[t];function n(s,g){return o(),e("div",null,r)}const i=p(d,[["render",n],["__file","98.html.vue"]]);export{i as default};
