import{_ as e}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as r,c as n,a as p,b as o,d as s,f as c,r as a}from"./app.33477645.js";const l={},_=p("h1",{id:"\u5207\u5C14\u8BFA\u8D1D\u5229\u7684\u60B2\u9E23",tabindex:"-1"},[p("a",{class:"header-anchor",href:"#\u5207\u5C14\u8BFA\u8D1D\u5229\u7684\u60B2\u9E23","aria-hidden":"true"},"#"),o(" \u5207\u5C14\u8BFA\u8D1D\u5229\u7684\u60B2\u9E23")],-1),g=p("blockquote",null,[p("p",null,"\u201C\u4ED6\u7684\u80BA\u548C\u809D\u7684\u788E\u5757\u4ECE\u5634\u91CC\u6D8C\u51FA\u6765\u3002\u6211\u7528\u7EF7\u5E26\u5305\u7740\u624B\uFF0C\u4F38\u8FDB\u4ED6\u7684\u5634\u91CC\uFF0C\u62FF\u51FA\u90A3\u4E9B\u4E1C\u897F\uFF0C\u9632\u6B62\u4ED6\u88AB\u81EA\u5DF1\u7684\u5185\u810F\u545B\u5230\u3002\u201D")],-1),h=p("p",null,"\u6D88\u9632\u5458\u74E6\u897F\u91CC\xB7\u4F0A\u683C\u7EB3\u575A\u79D1\u7684\u59BB\u5B50\u5411\u300A\u5207\u5C14\u8BFA\u8D1D\u5229\u7684\u60B2\u9E23\u300B\u7684\u4F5C\u8005\u8BB2\u8FF0\u4E08\u592B\u662F\u5982\u4F55\u4E00\u6B65\u6B65\u8D70\u5411\u6B7B\u4EA1\u65F6\uFF0C\u65E9\u5DF2\u6CE3\u4E0D\u6210\u58F0\u3002",-1),u={href:"https://www.zhihu.com/search?hybrid_search_extra=%7B%22sourceType%22%3A%22answer%22%2C%22sourceId%22%3A2708359926%7D&hybrid_search_source=Entity&q=%E5%88%87%E5%B0%94%E8%AF%BA%E8%B4%9D%E5%88%A9%E6%A0%B8%E6%B3%84%E6%BC%8F&search_source=Entity&type=content",target:"_blank",rel:"noopener noreferrer"},i=c("<p>\u6838\u8F90\u5C04\u5BFC\u81F4\u4E4B\u540E\u7684\u4E8C\u5341\u5E74\u5185\uFF0C\u5171\u6709<strong>9.7\u4E07\u4EBA\u6B7B\u4EA1\uFF0C27\u4E07\u4EBA\u60A3\u4E0A\u764C\u75C7\uFF0C250\u4E07\u4EBA\u8EAB\u60A3\u5404\u79CD\u75BE\u75C5</strong>\uFF0C\u5176\u4E2D\u5305\u62EC47.3\u4E07\u540D\u513F\u7AE5\u3002</p><p>\u9762\u5BF9\u91CD\u5927\u707E\u96BE\uFF0C\u4E00\u4E2A\u56FD\u5BB6\u548C\u6C11\u65CF\u6240\u6D8C\u73B0\u51FA\u7684\u82F1\u96C4\uFF0C\u4EE3\u8868\u7740\u56FD\u5BB6\u548C\u6C11\u65CF\u7684\u7CBE\u795E\u3002</p><p>\u540C\u65F6\uFF0C\u4ECE\u4E00\u4E2A\u56FD\u5BB6\u548C\u6C11\u65CF\u5BF9\u5F85\u82F1\u96C4\u7684\u65B9\u5F0F\uFF0C\u53EF\u4EE5\u770B\u51FA\u8FD9\u4E2A\u56FD\u5BB6\u5230\u5E95\u662F\u4F1F\u5927\u8FD8\u662F\u5351\u52A3\u3002\u90A3\u4E48\uFF0C\u8FD9\u4E9B\u82CF\u8054\u82F1\u96C4\u4EEC\u6700\u7EC8\u53C8\u6709\u7740\u600E\u6837\u7684\u7ED3\u5C40\uFF1F</p><p><strong>\u5207\u5C14\u8BFA\u8D1D\u5229\u4E8B\u6545\u7684\u7ECF\u8FC7</strong></p><p>1986\u5E744\u670826\u65E5\uFF0C\u51CC\u66681\u70B923\u5206\u3002</p><p>\u82CF\u8054\u5883\u5185\uFF0C\u4E4C\u514B\u5170\u57FA\u8F85\u5DDE\u7684\u666E\u91CC\u76AE\u4E9A\u5B63\uFF0C\u4E00\u58F0\u5DE8\u5927\u7684\u7206\u70B8\u58F0\u5212\u7834\u5B81\u9759\u7684\u591C\u7A7A\u3002</p><p>\u4E09\u516C\u91CC\u5916\u7684\u5C0F\u9547\uFF0C\u4ECE\u7206\u70B8\u58F0\u4E2D\u60CA\u9192\u7684\u5C45\u6C11\u7EB7\u7EB7\u8D70\u51FA\u5BB6\u95E8\u67E5\u770B\uFF0C\u5374\u770B\u5230\u6B64\u751F\u4ECE\u672A\u89C1\u8FC7\u7684\u5947\u5F02\u666F\u8C61\u3002\u53EA\u89C1\u5207\u5C14\u8BFA\u8D1D\u5229\u6838\u7535\u7AD9\u4E0A\u65B9\u7684\u5929\u7A7A\u5DF2\u7ECF\u88AB\u706B\u7130\u7167\u5F97\u901A\u7EA2\uFF0C\u4E00\u9053\u51E0\u516C\u91CC\u957F\u7684\u84DD\u8272\u5149\u67F1\u4ECE\u706B\u5149\u4E2D\u76F4\u51B2\u4E91\u9704\u3002</p><p>\u6574\u4E2A\u591C\u7A7A\u8272\u5F69\u7F24\u7EB7\uFF0C\u5982\u540C\u6781\u5149\u822C\u7F8E\u4E3D\u3002</p><p>\u5C45\u6C11\u4EEC\u7EB7\u7EB7\u805A\u5728\u4E00\u8D77\uFF0C\u60CA\u5947\u5730\u8BA8\u8BBA\u5E76\u6B23\u8D4F\u7740\u591C\u7A7A\u4E2D\u7EDA\u4E3D\u7684\u7F8E\u666F\uFF0C\u6D51\u7136\u4E0D\u77E5\u6B64\u65F6\u5230\u5E95\u53D1\u751F\u4E86\u4EC0\u4E48\u3002</p><p>\u5929\u7A7A\u4E2D\uFF0C\u6F2B\u5929\u7070\u8272\u5C18\u57C3\u50CF\u96EA\u82B1\u822C\u56DB\u5904\u98D8\u843D\u3002</p><p>\u5C18\u57C3\u9897\u7C92\u6389\u843D\u5728\u5B69\u5B50\u4EEC\u7A1A\u5AE9\u7684\u8138\u988A\uFF0C\u5C45\u6C11\u4EEC\u7684\u53D1\u80A9\uFF0C\u98D8\u8FDB\u4ED6\u4EEC\u7684\u53E3\u9F3B\uFF0C\u6CBE\u67D3\u5728\u76AE\u80A4\u4E0A\u3002\u4ED6\u4EEC\u4E0D\u77E5\u9053\uFF0C\u90A3\u8BE1\u5F02\u7684\u84DD\u8272\u5149\u67F1\uFF0C\u90A3\u96EA\u7247\u822C\u7684\u5C18\u57C3\uFF0C\u90A3\u53D8\u5E7B\u83AB\u6D4B\u7684\u201C\u6781\u5149\u201D\uFF0C\u662F\u6765\u81EA\u5730\u72F1\u7684\u666F\u8272\u3002</p><p>\u6B64\u65F6\uFF0C\u4ED6\u4EEC\u6B63\u5728\u7ECF\u5386\u4E00\u573A\u4EBA\u7C7B\u5386\u53F2\u4E0A\u6700\u4E25\u91CD\u7684\u6838\u6CC4\u6F0F\u2014\u2014\u5207\u5C14\u8BFA\u8D1D\u5229\u6838\u4E8B\u6545\u3002</p><p>\u4E8B\u6545\u662F\u5982\u4F55\u53D1\u751F\u7684\u5462\uFF1F</p><p>\u5F53\u5929\u665A\u4E0A1\u70B923\u520604\u79D2\uFF0C\u5207\u5C14\u8BFA\u8D1D\u5229\u6838\u7535\u7AD9\u7B2C\u56DB\u53F7\u53CD\u5E94\u5806\uFF0C\u6B63\u5728\u8FDB\u884C\u5B89\u5168\u7CFB\u7EDF\u6D4B\u8BD5\u3002\u56E0\u8BBE\u8BA1\u7F3A\u9677\u548C\u6280\u672F\u4EBA\u5458\u64CD\u4F5C\u5931\u8BEF\uFF0C\u53CD\u5E94\u5806\u51FA\u73B0\u5F02\u5E38\u3002</p><p>1\u70B923\u520640\u79D2\uFF0C\u6280\u672F\u4EBA\u5458\u6309\u4E0B\u201C\u7D27\u6025\u505C\u673A\u201D\u6309\u94AE\u3002\u7D27\u6025\u505C\u673A\u542F\u52A8 7 \u79D2\u540E\uFF0C\u53CD\u5E94\u5806\u5185\u529F\u7387\u6025\u5267\u4E0A\u5347\uFF0C\u5806\u82AF\u6E29\u5EA6\u8FC5\u901F\u6500\u5347\uFF0C\u84B8\u6C7D\u538B\u529B\u5267\u589E\u3002</p><p>\u9AD8\u6E29\u4F7F\u5F97\u77F3\u58A8\u548C\u6C34\u53D1\u751F\u53CD\u5E94\uFF0C\u751F\u6210\u5927\u91CF\u6C22\u6C14\u548C\u4E00\u6C27\u5316\u78B3\uFF0C\u5E76\u5F15\u53D1\u5267\u70C8\u7206\u70B8\u3002</p><p>\u5DE8\u5927\u7684\u51B2\u51FB\u529B\u77AC\u95F4\u5C06\u53CD\u5E94\u5806\u4E0A\u65B92000\u5428\u91CD\u7684\u7A79\u9876\u76D6\u70B8\u98DE\u30023\u79D2\u949F\u4E4B\u540E\uFF0C\u7D27\u63A5\u7740\u53D1\u751F\u4E86\u7B2C\u4E8C\u8D77\u7206\u70B8\uFF0C\u53CD\u5E94\u5806\u5916\u58F3\u76F4\u63A5\u70B8\u788E\uFF0C\u56DB\u53F7\u673A\u5382\u623F\u70B8\u6BC1\u4E00\u534A\u3002</p><p>\u6838\u53CD\u5E94\u5806\u53D1\u51FA\u5267\u70C8\u7684\u706B\u5149\uFF0C\u76F4\u63A5\u66B4\u9732\u5728\u7A7A\u6C14\u4E2D\u3002\u71C3\u70E7\u7740\u7684\u6838\u539F\u6599\u4E0D\u65AD\u5411\u5916\u55B7\u6E85\uFF0C\u643A\u5E26\u5927\u91CF\u6838\u8F90\u5C04\u7C92\u5B50\u7684\u5C18\u57C3\u6D8C\u5165\u534A\u7A7A\uFF0C\u968F\u7740\u70ED\u6D6A\u98D8\u5411\u56DB\u5468\u5E7F\u88A4\u7684\u57CE\u5E02\u4E61\u6751\u3002</p><p>\u5F3A\u70C8\u7684\u8F90\u5C04\u8BA9\u7A7A\u6C14\u4EA7\u751F\u7535\u79BB\u73B0\u8C61\uFF0C\u6563\u53D1\u51FA\u5404\u79CD\u5947\u5F02\u7684\u5149\u8292\uFF0C\u800C\u90A3\u9053\u5C04\u5411\u591C\u7A7A\u7684\u84DD\u8272\u5149\u67F1\uFF0C\u6B63\u662F\u5806\u82AF\u66B4\u9732\u7684\u5927\u91CF\u653E\u5C04\u6027\u7269\u8D28\u53D1\u51FA\u7684\u5951\u4F26\u79D1\u592B\u8F90\u5C04\u3002</p><p>\u5207\u5C14\u8BFA\u8D1D\u5229\u57CE\uFF0C\u4E00\u5EA7\u82CF\u8054\u4E94\u661F\u7EA7\u7684\u6A21\u8303\u5C0F\u9547\uFF0C\u5728\u8FD9\u573A\u707E\u96BE\u4E2D\u6BC1\u4E8E\u4E00\u65E6\uFF0C\u6CA6\u4E3A\u88AB\u5E9F\u5F03\u7684\u201C\u9B3C\u57CE\u201D\u3002</p><p><strong>\u7B2C\u4E00\u6279\u82CF\u8054\u82F1\u96C4\uFF1A\u53C2\u52A0\u6551\u63F4\u706D\u706B\u7684\u6D88\u9632\u5458</strong></p><p>\u7206\u70B8\u53D1\u751F\u540E\uFF0C\u7EA6\u6709 50 \u5428\u6838\u71C3\u6599\u5316\u4F5C\u70DF\u5C18\u8FDB\u5165\u5927\u6C14\u5C42\uFF0C\u53E6\u6709 70 \u5428\u6838\u71C3\u6599\u548C 900 \u5428\u77F3\u58A8\u55B7\u6E85\u5230\u53CD\u5E94\u5806\u5468\u56F4\uFF0C\u5F15\u8D77 30 \u4F59\u5904\u706B\u707E\u3002</p><p>\u51CC\u66681\u70B935\u5206\uFF0C\u5728\u63A5\u5230\u706B\u707E\u62A5\u8B66\u76849\u5206\u949F\u540E\uFF0C\u666E\u91CC\u76AE\u4E9A\u5B63\u5E02\u72EC\u7ACB\u519B\u4E8B\u5316\u7B2C6\u6D88\u9632\u652F\u961F14\u540D\u6D88\u9632\u961F\u5458\u7B2C\u4E00\u65F6\u95F4\u8D76\u5230\u73B0\u573A\u6551\u63F4\u3002\u6B64\u540E\uFF0C\u9646\u7EED\u6709\u53E6\u591655\u540D\u6D88\u9632\u961F\u5458\u548C14\u53F0\u6D88\u9632\u8F66\u8D76\u8D74\u73B0\u573A\u3002</p><p>\u4EE4\u4EBA\u75DB\u5FC3\u7684\u662F\uFF0C\u6B64\u65F6\u6D88\u9632\u961F\u5458\u5B8C\u5168\u6CA1\u6709\u5BF9\u6838\u8F90\u5C04\u505A\u4EFB\u4F55\u51C6\u5907\uFF0C\u56E0\u4E3A\u4ED6\u4EEC\u5E76\u4E0D\u77E5\u9053\u81EA\u5DF1\u9762\u5BF9\u7684\u662F\u6838\u53CD\u5E94\u5806\u7206\u70B8\u7684\u95EE\u9898\u3002</p><p>\u4E3A\u4EC0\u4E48\u4ED6\u4EEC\u4E0D\u77E5\u9053\uFF1F\u56E0\u4E3A\u6838\u7535\u7AD9\u91CC\u7684\u67D0\u4E9B\u4EBA\uFF0C\u4E0D\u613F\u5BF9\u5916\u900F\u9732\u771F\u76F8\u3002</p><p>\u9762\u5BF9\u707E\u96BE\u7684\u53D1\u751F\uFF0C\u4EE5\u5382\u957F\u3001\u603B\u5DE5\u7A0B\u5E08\u3001\u526F\u603B\u5DE5\u7A0B\u5E08\u4E3A\u9996\u7684\u5B98\u50DA\u4EEC\uFF0C\u5728\u53D1\u73B0\u7206\u70B8\u73B0\u573A\u6563\u843D\u7684\u77F3\u58A8\u5757\uFF0C\u610F\u8BC6\u5230\u53CD\u5E94\u5806\u5DF2\u7ECF\u70B8\u6BC1\u7684\u60C5\u51B5\u4E0B\uFF0C\u9009\u62E9\u7684\u662F\u7AED\u529B\u63A8\u5378\u8D23\u4EFB\uFF0C\u5984\u56FE\u63A9\u76D6\u771F\u76F8\uFF0C\u633D\u6551\u81EA\u5DF1\u7684\u4ED5\u9014\u3002</p><p>\u5F53\u65F6\u73B0\u573A\u7684\u8F90\u5C04\u5F3A\u5EA6\u4E3A2\u4E07\u4F26\u7434\uFF0C\u53CD\u5E94\u5806\u5185\u90E8\u662F3\u4E07\u4F26\u7434\uFF0C\u800C1\u5C0F\u65F6500\u4F26\u7434\u7684\u8F90\u5C04\u91CF\u5C31\u80FD\u81F4\u4EBA\u6025\u6027\u6B7B\u4EA1\u3002</p><p>\u73B0\u573A\u4E00\u540D\u6D88\u9632\u5458\u7531\u4E8E\u597D\u5947\uFF0C\u7528\u624B\u6361\u8D77\u5730\u4E0A\u7684\u77F3\u58A8\u5757\u770B\u4E86\u770B\u3002\u5F88\u5FEB\uFF0C\u4ED6\u7684\u6574\u4E2A\u624B\u638C\u5F00\u59CB\u5267\u70C8\u75BC\u75DB\uFF0C\u76AE\u80A4\u50CF\u88AB\u70C8\u7130\u711A\u70E7\u8FC7\u4E00\u822C\u53D8\u6210\u8840\u7EA2\u8272\uFF0C\u5E76\u8FC5\u901F\u8D77\u6CE1\u6E83\u70C2\u3002</p><p>\u73B0\u573A\u6D88\u9632\u5458\u7684\u5634\u91CC\u90FD\u5C1D\u5230\u4E86\u6D53\u6D53\u7684\u91D1\u5C5E\u5473\u3002\u6B64\u65F6\uFF0C\u4ED6\u4EEC\u4E5F\u89C9\u5BDF\u5230\u4E86\u4E8B\u60C5\u7684\u4E25\u91CD\u6027\uFF0C\u4F46\u662F\u5E76\u6CA1\u6709\u9000\u5374\uFF0C\u800C\u662F\u9009\u62E9\u7528\u751F\u547D\u6765\u62EF\u6551\u82CF\u8054\u548C\u82CF\u8054\u4EBA\u6C11\u3002</p><p>\u4E00\u4F4D\u6D88\u9632\u5458\u56DE\u5FC6\u9053\uFF1A</p><p>\u201C\u8BB0\u5F97\u5F53\u65F6\u961F\u53CB\u4EEC\u8FD8\u5728\u5F00\u73A9\u7B11\uFF0C\u5982\u679C\u6211\u4EEC\u8C01\u90FD\u80FD\u6D3B\u5230\u660E\u5929\u65E9\u6668\uFF0C\u90A3\u4FBF\u662F\u975E\u5E38\u5E78\u8FD0\u7684\u3002\u201D</p><p>\u6D88\u9632\u5458\u4E0E\u5927\u706B\u6574\u6574\u6218\u6597\u4E86\u4E00\u4E2A\u5C0F\u65F6\uFF0C\u7EB7\u7EB7\u51FA\u73B0\u5F3A\u70C8\u7729\u6655\u548C\u5455\u5410\u75C7\u72B6\u3002\u6BEB\u65E0\u7591\u95EE\uFF0C\u4ED6\u4EEC\u6700\u540E\u90FD\u5C06\u6210\u4E3A\u727A\u7272\u8005\u3002</p><p>\u53C2\u4E0E\u6551\u63F4\u706D\u706B\u768428\u4EBA\u5728\u968F\u540E\u4E09\u4E2A\u6708\u5185\uFF0C\u6B7B\u4E8E\u6025\u6027\u653E\u5C04\u75C5\uFF0C\u5269\u4E0B\u7684\u7EDD\u5927\u591A\u6570\u4E5F\u4E8E\u6570\u6708\u6216\u6570\u5E74\u5185\u6B7B\u4E8E\u764C\u75C7\u6216\u5176\u4ED6\u56E0\u8F90\u5C04\u5BFC\u81F4\u7684\u4E25\u91CD\u75BE\u75C5\u3002</p><p><strong>\u90A3\u4E9B\u6551\u706B\u82F1\u96C4\u4EEC\u7684\u7ED3\u5C40</strong></p><p>\u5728\u300A\u5207\u5C14\u8BFA\u8D1D\u5229\u7684\u60B2\u9E23\u300B\u4E2D\uFF0C\u6D88\u9632\u5458\u74E6\u897F\u91CC\xB7\u4F0A\u683C\u7EB3\u575A\u79D1\u7684\u59BB\u5B50\u8BB2\u8FF0\u4E86\u4ED6\u4E08\u592B\u56E0\u6025\u6027\u653E\u5C04\u75C5\uFF0C\u5728\u533B\u9662\u6CBB\u7597\u76F4\u81F3\u6B7B\u4EA1\u7684\u7EC6\u8282\u3002</p><p>\u6BCF\u8FC7\u4E00\u5929\uFF0C\u5979\u7684\u4E08\u592B\u90FD\u5224\u82E5\u4E24\u4EBA\u3002</p><p>\u8FD9\u662F\u4E00\u6BB5\u6BB5\u4EE4\u4EBA\u89E6\u76EE\u60CA\u5FC3\u7684\uFF0C\u5E26\u8840\u7684\u6587\u5B57\uFF1A</p><p>\u7B2C\u4E00\u5929\uFF0C\u201C\u6211\u770B\u5230\u4E86\u4ED6\uFF0C\u5168\u8EAB\u80BF\u80C0\uFF0C\u51E0\u4E4E\u770B\u4E0D\u5230\u773C\u775B\u201D\u3002</p><p>\u8FD9\u662F\u56E0\u4E3A\u6838\u8F90\u5C04\u7684\u7167\u5C04\uFF0C\u8840\u7BA1\u5185\u76AE\u7EC6\u80DE\u53D7\u635F\uFF0C\u8840\u7BA1\u58C1\u901A\u900F\u6027\u4F1A\u589E\u5F3A\uFF0C\u5927\u91CF\u7684\u8840\u7BA1\u5185\u7684\u6DB2\u4F53\u6E17\u5230\u7EC4\u7EC7\u6DB2\u4E4B\u95F4\uFF0C\u5F62\u6210\u80BF\u80C0\u3002</p><p>\u201C\u540E\u6765\uFF0C\u707C\u4F24\u5F00\u59CB\u5728\u5916\u8868\u663E\u9732\uFF0C\u4ED6\u7684\u5634\u5DF4\u3001\u820C\u5934\u3001\u8138\u988A\uFF0C\u4E00\u5F00\u59CB\u662F\u5C0F\u4F24\u53E3\uFF0C\u540E\u6765\u6108\u53D8\u6108\u5927\u3002\u201D</p><p>\u4F24\u53E3\u7684\u6108\u5408\u9700\u8981\u7EC4\u7EC7\u518D\u751F\uFF0C\u800C\u8EAB\u4F53\u88AB\u5927\u91CF\u5C04\u7EBF\u7A7F\u900F\uFF0C\u67D3\u8272\u4F53\u5168\u90E8\u906D\u5230\u7834\u574F\uFF0C\u7EC6\u80DE\u589E\u6B96\u7684\u7B2C\u4E00\u6B65\u4FBF\u88AB\u963B\u65AD\u3002\u8001\u7684\u7EC6\u80DE\u6B7B\u53BB\uFF0C\u65B0\u7684\u7EC6\u80DE\u65E0\u6CD5\u8865\u5145\uFF0C\u4E00\u4E2A\u5C0F\u4F24\u53E3\u5C31\u4F1A\u53D8\u6210\u4E00\u4E2A\u5927\u6D1E\uFF0C\u6700\u540E\u8089\u4F53\u4F1A\u6162\u6162\u6D88\u5931\u3002</p><p>\u201C\u53EA\u8981\u4E00\u8F6C\u5934\uFF0C\u5C31\u53EF\u4EE5\u770B\u5230\u4E00\u7C07\u5934\u53D1\u7559\u5728\u6795\u5934\u4E0A\u3002\u201D</p><p>\u5728\u906D\u53D7\u6838\u8F90\u5C04\u540E\uFF0C\u4EBA\u7684\u76AE\u80A4\u3001\u6307\u7532\u3001\u6BDB\u53D1\u3001\u4E0A\u76AE\u7EC6\u80DE\u7B49\u662F\u66F4\u65B0\u5468\u671F\u6700\u77ED\u7684\u7EC6\u80DE\uFF0C\u4E5F\u662F\u6700\u5148\u51FA\u73B0\u95EE\u9898\u7684\u3002</p><p>\u201C\u6211\u6BCF\u5929\u66FF\u4ED6\u6362\u90A3\u7247\u5E03\uFF0C\u4E0A\u9762\u90FD\u662F\u8840\uFF08\u6CA1\u6709\u76AE\u80A4\u5C4F\u969C\uFF0C\u4F53\u6DB2\u8840\u6DB2\u6E17\u51FA\uFF09\u3002\u6211\u628A\u4ED6\u62AC\u8D77\u6765\uFF0C\u4ED6\u7684\u76AE\u80A4\u7C98\u5728\u6211\u624B\u4E0A\uFF08\u76AE\u80A4\u4E0D\u65AD\u5265\u843D\uFF0C\u65E0\u6CD5\u518D\u751F\uFF09\u3002\u201D</p><p>\u201C\u4ED6\u4E00\u5929\u6392\u4FBF\u4E8C\u5341\u4E94\u5230\u4E09\u5341\u6B21\uFF0C\u4F34\u968F\u7740\u8840\u6DB2\u548C\u9ECF\u6DB2\u3002\u201D \u56E0\u4E3A\u4E0D\u6B62\u662F\u8868\u9762\u76AE\u80A4\u51FA\u8840\uFF0C\u80A0\u9053\u9ECF\u819C\u8131\u843D\u540C\u6837\u4F1A\u5BFC\u81F4\u80A0\u9053\u51FA\u8840\u3002</p><p>\u201C\u4ED6\u7684\u80BA\u548C\u809D\u7684\u788E\u7247\u90FD\u4ECE\u5634\u91CC\u8DD1\u51FA\u6765\uFF0C\u4ED6\u88AB\u81EA\u5DF1\u7684\u5185\u810F\u545B\u5230\u3002\u201D</p><p>\u201C\u5728\u533B\u9662\u7684\u6700\u540E\u4E24\u5929\uFF0C\u6211\u62AC\u8D77\u4ED6\u7684\u624B\u81C2\uFF0C\u9AA8\u5934\u597D\u50CF\u6643\u6765\u6643\u53BB\uFF0C\u76AE\u80A4\u70C2\u5F97\u8981\u6389\u4E0B\u6765\uFF0C\u5230\u5904\u90FD\u662F\u9C9C\u8840\u548C\u7C98\u6DB2\uFF0C\u6574\u4E2A\u4EBA\u597D\u50CF\u8981\u878D\u5316\u4E86\u4E00\u6837\u2026\u2026\u201D</p><p>\u7531\u6B64\u53EF\u60F3\u800C\u77E5\uFF0C\u90A3\u4E9B\u8EBA\u5728\u75C5\u5E8A\u4E0A\u7684\u6D88\u9632\u5458\uFF0C\u4E34\u7EC8\u524D\u90FD\u906D\u53D7\u4E86\u600E\u6837\u7684\u6298\u78E8\u3002</p><p>\u4E3A\u4EC0\u4E48\u4E0D\u80FD\u76F4\u63A5\u5B89\u4E50\u6B7B\u5462\uFF1F\u56E0\u4E3A\u8FD9\u6837\u7A00\u6709\u7684\u75C5\u4F8B\u662F\u7814\u7A76\u6838\u8F90\u5C04\u6BD2\u7406\u53CD\u5E94\u6700\u73CD\u8D35\u7684\u5B9E\u9A8C\u6837\u672C\u3002</p><p><strong>\u4EBA\u7C7B\u533B\u5B66\u7684\u8FDB\u6B65\uFF0C\u6709\u65F6\u5019\u4E0D\u5F97\u4E0D\u901A\u8FC7\u5982\u6B64\u6B8B\u5FCD\u7684\u65B9\u5F0F\u6765\u83B7\u5F97\u3002</strong></p><p>\u867D\u7136\u5979\u7684\u4E08\u592B\u88AB\u6838\u8F90\u5C04\u6298\u78E8\u6210\u8FD9\u6837\uFF0C\u4F46\u662F\u4E08\u592B\u8FD8\u662F\u6DF1\u7231\u7740\u5979\uFF0C\u820D\u4E0D\u5F97\u79BB\u5F00\u5979\uFF0C\u5979\u53C8\u4F55\u5C1D\u4E0D\u662F\u5982\u6B64\u5462\u3002</p><p>\u6709\u4E00\u5929\uFF0C\u74E6\u897F\u91CC\u592B\u4EBA\u56DE\u5230\u533B\u9662\uFF0C\u770B\u5230\u5E8A\u8FB9\u684C\u4E0A\u6446\u7740\u4E00\u9897\u5927\u5927\u7684\u6A59\u5B50\uFF0C\u5979\u77AC\u95F4\u6CEA\u6D41\u6EE1\u9762\u3002\u56E0\u4E3A\u5979\u77E5\u9053\uFF0C\u4E08\u592B\u6BCF\u5929\u56DE\u5BB6\u90FD\u4F1A\u5E26\u7ED9\u5979\u4E00\u4E2A\u5C0F\u793C\u7269\uFF0C\u800C\u8FD9\u9897\u6A59\u5B50\u6B63\u662F\u4E08\u592B\u6551\u706B\u90A3\u5929\u7279\u610F\u4E3A\u5979\u4E70\u7684\u3002</p><p>\u74E6\u897F\u91CC\u5FAE\u7B11\u7740\u8BF4\uFF1A\u201C\u4EB2\u7231\u7684\uFF0C\u6211\u6CA1\u5FD8\u8BB0\u6211\u7684\u793C\u7269\u3002\u201D</p><p>\u8BF4\u5B8C\u540E\uFF0C\u74E6\u897F\u91CC\u95ED\u4E0A\u4E86\u773C\u775B\u3002\u62A4\u58EB\u4E00\u76F4\u66FF\u4ED6\u9EBB\u9189\uFF0C\u8BA9\u4ED6\u80FD\u5165\u7761\u3002</p><p>\u74E6\u897F\u91CC\u592B\u4EBA\u6D41\u7740\u6CEA\uFF0C\u9ED8\u9ED8\u62FF\u8D77\u90A3\u9897\u6A59\u5B50\u3002</p><p>\u62A4\u58EB\u5728\u5E37\u5E55\u5916\uFF0C\u5BF9\u5979\u6BD4\u624B\u52BF\u8BF4\u4E0D\u80FD\u5403\uFF0C\u5E76\u60CA\u6050\u5730\u51B2\u5979\u5927\u558A\uFF1A</p><p>\u201C\u4F60\u8981\u77E5\u9053\uFF0C\u90A3\u4E0D\u662F\u4F60\u7684\u4E08\u592B\u4E86\uFF0C\u4E0D\u662F\u4F60\u5FC3\u7231\u7684\u4EBA\u4E86\uFF0C\u800C\u662F\u6709\u5F3A\u70C8\u8F90\u5C04\u3001\u4E25\u91CD\u8F90\u5C04\u4E2D\u6BD2\u7684\u4EBA\u3002\u4F60\u5982\u679C\u6CA1\u6709\u81EA\u6740\u503E\u5411\uFF0C\u5C31\u8BF7\u7406\u667A\u4E00\u70B9\u3002\u201D</p><p>\u74E6\u897F\u91CC\u592B\u4EBA\u54ED\u558A\u9053\uFF1A\u201C\u4F46\u662F\u6211\u7231\u4ED6\uFF01\u6211\u7231\u4ED6\uFF01\u201D</p><p>\u533B\u62A4\u4EBA\u5458\u8BA4\u4E3A\u5979\u201C\u4E0D\u6B63\u5E38\u201D\u3002\u56E0\u4E3A\u6240\u6709\u4EBA\uFF0C\u5305\u62EC\u5979\u7684\u4E08\u592B\uFF0C\u90FD\u8BA9\u5979\u79BB\u5F00\u70B9\uFF0C\u5C0F\u5FC3\u8F90\u5C04\u3002\u5979\u50CF\u6CA1\u6709\u542C\u89C1\u4E00\u6837\uFF0C\u6CA1\u65E5\u6CA1\u591C\u6089\u5FC3\u7167\u987E\u81EA\u5DF1\u7684\u4E08\u592B\u3002</p><p>\u5979\u770B\u7740\u5468\u906D\u53D1\u751F\u7684\u4E00\u5207\uFF0C\u5DE8\u5927\u7684\u4E0D\u5B89\u4E4B\u4E0B\uFF0C\u5979\u7684\u76EE\u6807\u5F88\u7B80\u5355\uFF0C\u5C31\u662F\u4E00\u76F4\u8DDF\u7740\u5979\u7684\u4E08\u592B\u3002\u4ECE\u5207\u5C14\u8BFA\u8D1D\u5229\u5230\u83AB\u65AF\u79D1\uFF0C\u6000\u7740\u516D\u4E2A\u6708\u7684\u8EAB\u5B55\u7684\u5979\uFF0C\u59CB\u7EC8\u966A\u5728\u4E08\u592B\u8EAB\u65C1\u3002</p><p>\u53EF\u5979\u600E\u4E48\u4F1A\u60F3\u5230\uFF0C\u8FD9\u75C5\u5176\u5B9E\u6CBB\u4E0D\u597D\u3002</p><p>\u5728\u5979\u65E0\u5FAE\u4E0D\u81F3\u7684\u7167\u6599\u4E0B\uFF0C\u5728\u6EE1\u5FC3\u671F\u76FC\u7684\u6E34\u671B\u4E2D\uFF0C\u4E08\u592B\u5374\u4ECE\u91CC\u5230\u5916\uFF0C\u6162\u6162\u5730\u8150\u70C2\u6210\u4E00\u5177\u5C38\u4F53\u3002\u591A\u4E48\u4EE4\u4EBA\u7EDD\u671B\uFF01</p><p>\u56DB\u5468\u4E4B\u540E\uFF0C\u74E6\u897F\u91CC\u5728\u75DB\u82E6\u4E2D\u79BB\u5F00\u4E86\u4EBA\u4E16\u3002</p><p>\u201C\u4ED6\u4EEC\u60F3\u7ED9\u4ED6\u7A7F\u793C\u670D\uFF0C\u5934\u76D4\u548C\u6D88\u9632\u670D\u653E\u5728\u80F8\u524D\u3002\u4E5F\u4E0D\u80FD\u7A7F\u978B\u5B50\uFF0C\u56E0\u4E3A\u811A\u592A\u80BF\u4E86\uFF0C\u53CC\u817F\u66F4\u662F\u80BF\u5F97\u50CF\u70B8\u5F39\u3002\u6700\u540E\u793C\u670D\u4E5F\u526A\u5F00\u4E86\uFF0C\u76D6\u5728\u8EAB\u4E0A\uFF0C\u56E0\u4E3A\u7A7F\u4E0D\u8FDB\u53BB\u2026\u2026\u201D</p><p>\u4ED6\u4EEC\u6B7B\u540E\uFF0C\u82CF\u8054\u7279\u522B\u59D4\u5458\u4F1A\u5BF9\u4ED6\u4EEC\u7684\u5BB6\u4EBA\u8BF4\uFF1A</p><p>\u201C\u6211\u4EEC\u4E0D\u80FD\u5C06\u4F60\u4EEC\u7684\u4E08\u592B\uFF0C\u4F60\u4EEC\u7684\u513F\u5B50\u7684\u9057\u4F53\u4EA4\u7ED9\u4F60\u4EEC\uFF0C\u4ED6\u4EEC\u4F1A\u4EE5\u7279\u522B\u7684\u65B9\u5F0F\u846C\u5728\u83AB\u65AF\u79D1\u5893\u5730\u3002\u4F60\u4EEC\u5E94\u8BE5\u7B7E\u7F72\u8FD9\u4EFD\u6587\u4EF6\uFF0C\u9700\u8981\u4F60\u4EEC\u7684\u540C\u610F\u3002\u201D</p><p>\u4E0B\u846C\u7684\u90A3\u5929\uFF0C\u4ED6\u4EEC\u5750\u4E0A\u5927\u5DF4\uFF0C\u7531\u519B\u4EBA\u770B\u62A4\u5E76\u9A7E\u9A76\uFF0C\u6CBF\u7740\u73AF\u8DEF\u5728\u83AB\u65AF\u79D1\u8F6C\u60A0\u4E86\u4E24\u4E09\u5C0F\u65F6\uFF0C\u6700\u540E\u53C8\u8FD4\u56DE\u4E86\u83AB\u65AF\u79D1\u3002\u56E0\u4E3A\u5BF9\u8BB2\u673A\u4E2D\u8BF4\uFF0C\u6709\u4E00\u7FA4\u5916\u56FD\u8BB0\u8005\u5B88\u5019\u5728\u90A3\u91CC\uFF0C\u6B63\u5728\u7B49\u7740\u91C7\u8BBF\u3002</p><p>\u7236\u6BCD\u4EEC\u6C89\u9ED8\u4E0D\u8BED\uFF0C\u800C\u59BB\u5B50\u4EEC\u5219\u60C5\u7EEA\u6FC0\u52A8\uFF0C\u54ED\u558A\u9053\uFF1A</p><p>\u201C\u5E72\u561B\u8981\u85CF\u7740\u6211\u4EEC\u7684\u4E08\u592B\uFF1F\u4ED6\u4EEC\u662F\u8C01\uFF1F\u51F6\u624B\uFF1F\u7F6A\u72AF\uFF1F\u6211\u4EEC\u662F\u5728\u5B89\u846C\u8C01\uFF1F\u201D</p><p>\u5C3D\u7BA1\u6700\u540E\u4ED6\u4EEC\u5230\u8FBE\u4E86\u5893\u5730\uFF0C\u5374\u4E0D\u80FD\u505A\u6700\u540E\u7684\u9053\u522B\u3002\u6240\u6709\u9057\u4F53\u88AB\u5B89\u653E\u5728\u710A\u6B7B\u7684\u950C\u5236\u68FA\u6901\u91CC\uFF0C\u5E76\u6D47\u7B51\u4E0A\u539A\u539A\u7684\u6DF7\u51DD\u571F\uFF0C\u6DF1\u6DF1\u57CB\u4E8E\u5730\u5E95\u3002</p><p>\u727A\u7272\u7684\u6D88\u9632\u5458\u4EEC\uFF0C\u81EA\u7136\u88AB\u5F53\u6210\u201C\u82CF\u8054\u82F1\u96C4\u201D\u3002</p><p>\u74E6\u897F\u91CC\u662F\u4E00\u4F4D\u519C\u6C11\u7684\u513F\u5B50\uFF0C\u4ED6\u5F88\u559C\u6B22\u79CD\u5730\u3002\u5988\u5988\u5E0C\u671B\u4ED6\u9000\u4F0D\u540E\u56DE\u5BB6\u79CD\u5730\uFF0C\u751A\u81F3\u5E2E\u4ED6\u76D6\u4E86\u680B\u6728\u5934\u623F\u5B50\uFF0C\u53EF\u4ED6\u5374\u4E00\u5FC3\u60F3\u5F53\u6D88\u9632\u5458\u3002</p><p>\u82CF\u8054\u7684\u7537\u5B69\u603B\u6709\u4E9B\u82F1\u96C4\u4E3B\u4E49\uFF0C\u5728\u548C\u5E73\u5E74\u4EE3\u9009\u62E9\u5371\u9669\u7684\u5DE5\u4F5C\uFF0C\u6BCF\u4E00\u6B21\u884C\u52A8\u90FD\u662F\u5728\u4FDD\u536B\u56FD\u5BB6\u548C\u4EBA\u6C11\u3002</p><p>\u4ED6\u4EEC\u6700\u7EC8\u4E5F\u5982\u613F\u62A5\u56FD\uFF0C\u53EF\u603B\u89C9\u5F97\u5E94\u8BE5\u6709\u66F4\u597D\u7684\u65B9\u5F0F\u3002</p><p>\u5982\u679C\u56FD\u5BB6\u4E00\u5F00\u59CB\u5BF9\u8FD9\u4E9B\u5B69\u5B50\u5766\u8BDA\u4E9B\uFF0C\u76F8\u4FE1\u4ED6\u4EEC\u4F9D\u7136\u4F1A\u4E3A\u4E86\u56FD\u5BB6\u548C\u4EBA\u6C11\uFF0C\u6BEB\u4E0D\u72B9\u8C6B\u5730\u5949\u732E\u51FA\u81EA\u5DF1\u7684\u4E00\u5207\u751A\u81F3\u751F\u547D\u3002</p><p><strong>\u7136\u800C\u9690\u7792\u548C\u6B3A\u9A97\uFF0C\u5374\u8BA9\u4ED6\u4EEC\u7684\u727A\u7272\u53D8\u5F97\u5982\u6B64\u60E8\u75DB\u548C\u60B2\u58EE\u3002</strong></p><p><strong>\u7B2C\u4E8C\u6279\u82CF\u8054\u82F1\u96C4\uFF1A\u6838\u79D1\u5B66\u5BB6\u74E6\u83B1\u91CC\xB7\u5217\u52A0\u7D22\u592B</strong></p><p>\u7206\u70B8\u53D1\u751F\u4E09\u5C0F\u65F6\u540E\uFF0C\u6D88\u606F\u4F20\u5230\u4E86\u514B\u91CC\u59C6\u6797\u5BAB\u3002</p><p>\u4F46\u6208\u5C14\u5DF4\u4E54\u592B\u9664\u4E86\u77E5\u9053\u201C\u5207\u5C14\u8BFA\u8D1D\u5229\u6838\u7535\u7AD9\u53D1\u751F\u4E86\u4E00\u573A\u706B\u707E\u4E4B\u5916\u201D\uFF0C\u5E76\u6CA1\u6709\u83B7\u5F97\u592A\u591A\u7684\u771F\u5B9E\u4FE1\u606F\u3002\u4ED6\u8BE2\u95EE\u4E86\u5F53\u65F6\u82CF\u8054\u7684\u6700\u9AD8\u79D1\u5B66\u6743\u5A01\u2014\u2014\u82CF\u8054\u79D1\u5B66\u9662\u9662\u957F\u517C\u539F\u5B50\u80FD\u7814\u7A76\u6240\u6240\u957F\u538B\u529B\u5C71\u5FB7\u7F57\u592B\u9662\u58EB\u3002</p><p>\u8FD9\u4F4D\u5DF2\u7ECF83\u5C81\u9AD8\u9F84\uFF0C\u65E9\u5C31\u8131\u79BB\u4E00\u7EBF\u7684\u9662\u58EB\u81EA\u4FE1\u5730\u544A\u8BC9\u6208\u5C14\u5DF4\u4E54\u592B\uFF1A</p><blockquote><p>\u201C\u6838\u53CD\u5E94\u5806\u7EDD\u5BF9\u5B89\u5168\uFF0C\u751A\u81F3\u53EF\u4EE5\u88C5\u7F6E\u5728\u7EA2\u573A\u3002\u8FD0\u4F5C\u8FC7\u7A0B\u548C\u716E\u8336\u6CA1\u4E24\u6837\uFF0C\u5C31\u50CF\u5728\u7EA2\u573A\u6446\u4E86\u4E2A\u8336\u58F6\u3002\u201D</p></blockquote><p>\u6CA1\u6709\u4EBA\u628A\u5207\u5C14\u8BFA\u8D1D\u5229\u4E8B\u6545\u5F53\u56DE\u4E8B\u3002</p><p>26\u65E5\u6E05\u6668\uFF0C\u82CF\u8054\u90E8\u957F\u4F1A\u8BAE\u4E3B\u5E2D\u96F7\u65E5\u79D1\u592B\u5F97\u5230\u201C\u6838\u53CD\u5E94\u5806\u53D1\u751F\u4E86\u7206\u70B8\uFF0C\u9700\u8981\u7ACB\u5373\u91C7\u53D6\u7D27\u6025\u63AA\u65BD\u201D\u7684\u7535\u8BDD\u6C47\u62A5\u540E\uFF0C\u4ECD\u672A\u5C06\u4E8B\u6545\u7684\u4E25\u91CD\u6027\u544A\u77E5\u6208\u5C14\u5DF4\u4E54\u592B\u3002</p><p>26\u65E5\u4E0A\u5348\uFF0C\u82CF\u5171\u653F\u6CBB\u5C40\u5F00\u4F1A\u8BA8\u8BBA\u5C40\u52BF\uFF0C\u542C\u53D6\u6C47\u62A5\u7684\u9886\u5BFC\u8005\u4EEC\u575A\u51B3\u53CD\u5BF9\u6838\u53CD\u5E94\u5806\u5DF2\u7ECF\u53D1\u751F\u7206\u70B8\u7684\u8FD9\u79CD\u8BF4\u6CD5\u3002</p><p>\u4E3A\u4EC0\u4E48\uFF1F\u5F88\u53EF\u80FD\u662F\u56E0\u4E3A\u4ED6\u4EEC\u4E0D\u76F8\u4FE1\u3002\u6BD5\u7ADF\u5F53\u65F6\u6574\u4E2A\u82CF\u8054\u56FD\u5BB6\u7684\u5BA3\u4F20\uFF0C\u90FD\u5728\u5F3A\u8C03\u5207\u5C14\u8BFA\u8D1D\u5229\u662F\u5168\u4E16\u754C\u6700\u5B89\u5168\u7684\u6838\u7535\u7AD9\u3002</p><p>\u6B64\u65F6\uFF0C\u53C2\u52A0\u4F1A\u8BAE\u7684\u6838\u79D1\u5B66\u5BB6\u74E6\u83B1\u91CC\xB7\u5217\u52A0\u7D22\u592B\u6839\u636E\u73B0\u573A\u6C47\u62A5\u8D44\u6599\uFF0C\u610F\u8BC6\u5230\u4E8B\u6545\u7684\u4E25\u91CD\u6027\uFF0C\u4E0D\u987E\u5176\u4ED6\u4EBA\u7684\u53CD\u5BF9\uFF0C\u6BC5\u7136\u7AD9\u51FA\u6765\u9610\u8FF0\u4E86\u81EA\u5DF1\u7684\u770B\u6CD5\u3002</p><p>\u4F1A\u8BAE\u6700\u7EC8\u51B3\u5B9A\u7EC4\u7EC7\u4E00\u4E2A\u653F\u5E9C\u59D4\u5458\u4F1A\u6765\u5904\u7406\u4E8B\u6545\u540E\u679C\u3002\u968F\u540E\uFF0C\u5217\u52A0\u7D22\u592B\u9662\u58EB\u88AB\u514B\u91CC\u59C6\u6797\u5BAB\u5F81\u53EC\u6210\u4E3A\u201C\u5207\u5C14\u8BFA\u8D1D\u5229\u7279\u522B\u59D4\u5458\u4F1A\u201D\u7684\u4E3B\u4EFB\uFF0C\u8D1F\u8D23\u5904\u7406\u8FD9\u8D77\u82CF\u8054\u5386\u53F2\u4E0A\u6700\u5927\u89C4\u6A21\u7684\u6838\u4E8B\u6545\u3002</p><p>\u74E6\u83B1\u91CC\xB7\u5217\u52A0\u7D22\u592B\u751F\u4E8E1936\u5E74\uFF0C\u662F\u83AB\u65AF\u79D1\u56FD\u7ACB\u5927\u5B66\u7684\u6559\u6388\uFF0C\u5E93\u5C14\u6070\u6258\u592B\u539F\u5B50\u80FD\u7814\u7A76\u6240\u526F\u6240\u957F\uFF0C\u5E76\u4E14\u662F\u82CF\u8054\u5386\u53F2\u4E0A\u6700\u5E74\u8F7B\u7684\u79D1\u5B66\u9662\u9662\u58EB\u3002</p><p>\u8FD9\u4F4D\u539F\u672C\u53EF\u4EE5\u4E3A\u82CF\u8054\u79D1\u5B66\u4E8B\u4E1A\u505A\u51FA\u91CD\u5927\u8D21\u732E\u7684\u79D1\u5B66\u5BB6\uFF0C\u5374\u7528\u53E6\u5916\u4E00\u79CD\u65B9\u5F0F\u4E3A\u8FD9\u4E2A\u56FD\u5BB6\u8D21\u732E\u51FA\u81EA\u5DF1\u7684\u751F\u547D\uFF0C\u4E3A\u4E16\u4EBA\u6240\u94ED\u8BB0\u3002</p><p>\u5217\u52A0\u7D22\u592B\u9662\u58EB\u8D76\u8D74\u4E8B\u6545\u73B0\u573A\uFF0C\u638C\u63E1\u4E86\u7B2C\u4E00\u624B\u8D44\u6599\u3002\u4ED6\u4E58\u5750\u88C5\u7532\u8F66\uFF0C\u5192\u7740\u5F3A\u70C8\u7684\u8F90\u5C04\uFF0C\u4EB2\u81EA\u62B5\u4E344\u53F7\u53CD\u5E94\u5806\u8FD1\u8DDD\u79BB\u89C2\u5BDF\uFF0C\u53D1\u73B0\u53CD\u5E94\u5806\u5185\u90E8\u7684\u77F3\u58A8\u4ECD\u5728\u71C3\u70E7\uFF0C\u7206\u70B8\u53E3\u7684\u767D\u8272\u70DF\u67F1\u6B63\u5728\u5927\u91CF\u91CA\u653E\u653E\u5C04\u6027\u7269\u8D28\u3002</p><p>\u74E6\u83B1\u91CC\xB7\u5217\u52A0\u7D22\u592B\u610F\u8BC6\u5230\u653E\u5C04\u6027\u7269\u8D28\u6269\u6563\u7684\u5371\u5BB3\u6027\uFF0C\u575A\u6301\u7ACB\u5373\u64A4\u79BB\u666E\u91CC\u76AE\u4E9A\u5B63\u5E02\u7684\u6240\u6709\u5C45\u6C11\u3002\u4E3A\u6B64\uFF0C\u4ED6\u4E0D\u60DC\u5192\u72AF\u82CF\u8054\u6700\u9AD8\u9886\u5BFC\u5C42\uFF0C\u6700\u7EC8\u6210\u529F\u8BF4\u670D\u82CF\u8054\u5F53\u5C40\u5C06\u5207\u5C14\u8BFA\u8D1D\u5229\u6240\u5728\u7684\u5C0F\u9547\u7684\u6C11\u4F17\u5168\u90E8\u64A4\u79BB\u3002</p><p>4\u670827\u65E5\u4E0A\u534811\u70B9\uFF0C\u4E5F\u5C31\u662F\u7206\u70B8\u53D1\u751F34\u5C0F\u65F6\u540E\uFF0C\u8D85\u8FC71000\u8F86\u5927\u5DF4\u62B5\u8FBE\u5C0F\u9547\uFF0C\u82CF\u8054\u5F53\u5C40\u5F00\u59CB\u542F\u52A8\u9996\u6279\u64A4\u79BB\u3002\u4F46\u662F\u76F4\u5230\u4E0B\u5348\u4E24\u70B9\uFF0C\u5F53\u5C40\u624D\u5BA3\u5E03\u6240\u6709\u4EBA\u5F7B\u5E95\u79BB\u5F00\u8BE5\u9547\u3002</p><p>\u7ECF\u8FC7\u4E09\u4E2A\u591A\u5C0F\u65F6\u7684\u5DE5\u4F5C\uFF0C\u5927\u7EA6\u67094\u4E07\u591A\u5C45\u6C11\u88AB\u8F6C\u79FB\u5230\u5217\u65AF\u683C\u7EB3\u7B49\u9547\u3002</p><p>\u4E3A\u4E86\u907F\u514D\u6050\u614C\uFF0C\u5F53\u5C40\u9009\u62E9\u9690\u7792\u4E8B\u6545\u771F\u76F8\uFF0C\u544A\u8BC9\u5C45\u6C11\u6CA1\u6709\u53D1\u751F\u4E25\u91CD\u4E8B\u6545\uFF0C\u53EA\u8981\u5927\u5BB6\u52E4\u6D17\u624B\u5373\u53EF\u3002\u5C45\u6C11\u4EEC\u4EE5\u4E3A\u53EA\u662F\u6682\u65F6\u64A4\u79BB\uFF0C\u6709\u4E9B\u4EBA\u5728\u5BB6\u95E8\u53E3\u7559\u6709\u201C\u4E0D\u8981\u52A8\u6211\u7269\u54C1\u201D\u7684\u5B57\u6761\u3002</p><p>\u7136\u800C\uFF0C\u4ED6\u4EEC\u6CA1\u6709\u60F3\u5230\uFF0C\u8FD9\u4E00\u53BB\uFF0C\u4FBF\u6B64\u751F\u4E0D\u518D\u590D\u8FD4\u3002</p><p>\u4E8B\u6545\u53D1\u751F48\u5C0F\u65F6\u540E\uFF0C\u5F53\u5730\u653F\u5E9C\u624D\u5F00\u59CB\u758F\u656310\u516C\u91CC\u534A\u5F84\u8303\u56F4\u5185\u7684\u5C45\u6C11\u3002\u5F88\u591A\u4EBA\u56E0\u4E3A\u64A4\u79BB\u592A\u665A\uFF0C\u906D\u53D7\u8F90\u5C04\u5BFC\u81F4\u60A3\u4E0A\u7532\u72B6\u817A\u764C\uFF0C\u5A74\u513F\u7578\u5F62\u7387\u4E5F\u56E0\u6B64\u5927\u5E45\u5EA6\u63D0\u9AD8\u3002</p><p>\u6B64\u65F6\uFF0C\u5207\u5C14\u8BFA\u8D1D\u5229\u7684\u6838\u5C18\u57C3\u968F\u7740\u98CE\u98D8\u5230\u6B27\u6D32\u5176\u5B83\u56FD\u5BB6\uFF0C\u5176\u4E2D60%\u6C89\u964D\u5230\u767D\u4FC4\u7F57\u65AF\uFF0C\u7ED9\u5F53\u5730\u5E26\u6765\u6781\u5927\u7684\u707E\u5BB3\u3002\u6B27\u6D32\u5404\u56FD\u68C0\u6D4B\u5230\u6838\u8F90\u5C04\u4E25\u91CD\u5F02\u5E38\u7684\u60C5\u51B5\uFF0C\u5A92\u4F53\u5927\u8086\u62A5\u9053\u6B64\u4E8B\uFF0C\u5404\u56FD\u7EB7\u7EB7\u8D28\u8BE2\u82CF\u8054\u3002</p><p>4\u670828\u65E5\uFF0C\u82CF\u8054\u5F53\u5C40\u610F\u8BC6\u5230\u518D\u4E5F\u906E\u63A9\u4E0D\u4F4F\uFF0C\u4FBF\u5728\u5F53\u65E5\u665A\u4E0A9\u70B9\uFF0C\u901A\u8FC7\u7535\u89C6\u53F0\u63D2\u64AD\u4E00\u6761\u77ED\u8BAF\u62A5\u9053\u6B64\u4E8B\u3002\u6B64\u65F6\uFF0C\u8DDD\u79BB\u4E8B\u6545\u7684\u53D1\u751F\uFF0C\u5DF2\u7ECF\u8FC7\u53BB\u4E86\u6574\u657467\u4E2A\u5C0F\u65F6\u3002</p><p>\u82CF\u8054\u627F\u8BA4\u540E\uFF0C\u4E16\u754C\u4E3A\u4E4B\u9707\u60CA\uFF01</p><p>\u6B27\u6D32\u5404\u56FD\u7EB7\u7EB7\u91C7\u53D6\u5404\u79CD\u9632\u8303\u63AA\u65BD\uFF0C\u4E2D\u56FD\u4E5F\u4E3A\u6B64\u60CA\u51FA\u4E00\u8EAB\u51B7\u6C57\uFF0C\u201C\u5207\u5C14\u8BFA\u8D1D\u5229\u4E8B\u6545\u201D\u9042\u6210\u4E3A\u4E00\u4E2A\u7A81\u53D1\u7684\u4E16\u754C\u6027\u95EE\u9898\u3002</p><p>\u6B64\u65F6\u6700\u6DE1\u5B9A\u7684\u56FD\u5BB6\u5C45\u7136\u662F\u82CF\u8054\u3002\u7206\u70B8\u53D1\u751F\u51E0\u5929\u540E\uFF0C\u82CF\u8054\u5F53\u5C40\u4ECD\u7136\u9009\u62E9\u5C3D\u91CF\u9690\u7792\u6838\u6CC4\u6F0F\u4E8B\u6545\u7684\u4E25\u91CD\u7A0B\u5EA6\u3002</p><p>\u4EE4\u4EBA\u75DB\u5FC3\u7684\u662F\uFF0C\u7ECF\u74E6\u83B1\u91CC\xB7\u5217\u52A0\u7D22\u592B\u7AED\u529B\u529D\u8BF4\uFF0C\u4E5F\u672A\u80FD\u963B\u6B625\u5929\u4E4B\u540E\u5728\u4E4C\u514B\u5170\u9996\u90FD\u57FA\u8F85\u6240\u4E3E\u884C\u7684\u4E94\u4E00\u52B3\u52A8\u8282\u5E86\u5178\u6E38\u884C\u3002\u5904\u4E8E\u91CD\u707E\u533A\u7684\u4E4C\u514B\u5170\u4F9D\u65E7\u9F13\u52B1\u6C11\u4F17\u53C2\u52A05\u67081\u65E5\u7684\u52B3\u52A8\u8282\u5927\u578B\u5E86\u795D\u6D3B\u52A8\u3002</p><p>\u57FA\u8F85\u8DDD\u79BB\u5207\u5C14\u8BFA\u8D1D\u5229\u6838\u7535\u7AD9\u53EA\u6709600\u516C\u91CC\u3002\u5E86\u5178\u6D3B\u52A8\u4E2D\uFF0C\u65E0\u6570\u7684\u5B69\u5B50\u4E0E\u5E02\u6C11\u53D7\u5230\u6838\u8F90\u5C04\u6C61\u67D3\uFF0C\u5177\u4F53\u6570\u76EE\u81F3\u4ECA\u672A\u516C\u5E03\u3002</p><p>\u65F6\u4E3A\u4E4C\u514B\u5170\u7B2C\u4E00\u4E66\u8BB0\u7684\u96EA\u6BD4\u65AF\u57FA\u4E5F\u643A\u5E26\u5BB6\u4EBA\u548C\u5B59\u5B50\u53C2\u52A0\u5178\u793C\u3002\u96EA\u6BD4\u65AF\u57FA\u540E\u6765\u81EA\u6740\uFF0C\u8FD9\u5927\u6982\u662F\u4ED6\u4E00\u751F\u4E2D\u6700\u5149\u8F89\u7684\u4E00\u4EF6\u4E8B\u3002</p><p>\u74E6\u83B1\u91CC\xB7\u5217\u52A0\u7D22\u592B\u9662\u58EB\u4E5F\u56E0\u8FDE\u7EED\u5728\u4E8B\u6545\u73B0\u573A\u8D85\u957F\u65F6\u95F4\u7684\u5DE5\u4F5C\uFF0C\u906D\u53D7\u4E86\u6838\u8F90\u5C04\u7684\u635F\u5BB3\u3002\u6240\u6709\u7684\u6551\u63F4\u4EBA\u5458\u90FD\u5728\u8FDB\u884C\u8F6E\u6362\uFF0C\u4EE5\u907F\u514D\u8FC7\u91CF\u7684\u6838\u8F90\u5C04\uFF0C\u53EA\u6709\u4ED6\u59CB\u7EC8\u575A\u5B88\u73B0\u573A\u6307\u6325\u3002</p><p>\u5728\u5207\u5C14\u8BFA\u8D1D\u5229\u4E8B\u6545\u73B0\u573A\u5DE5\u4F5C\u534A\u4E2A\u6708\u4E4B\u540E\uFF0C\u74E6\u83B1\u91CC\xB7\u5217\u52A0\u7D22\u592B\u5DF2\u7ECF\u663E\u9732\u51FA\u75C5\u53D8\u7684\u5F81\u5146\uFF0C\u5F00\u59CB\u5927\u91CF\u8131\u53D1\u548C\u5267\u70C8\u54B3\u55FD\u3002</p><p><strong>\u74E6\u83B1\u91CC\xB7\u5217\u52A0\u7D22\u592B\u9662\u58EB\u7684\u7ED3\u5C40</strong></p><p>1986\u5E748\u6708\uFF0C\u74E6\u83B1\u91CC\xB7\u5217\u52A0\u7D22\u592B\u4F5C\u4E3A\u82CF\u8054\u653F\u5E9C\u5168\u6743\u4EE3\u8868\u524D\u5F80\u5965\u5730\u5229\u9996\u90FD\u7EF4\u4E5F\u7EB3\uFF0C\u5411\u56FD\u9645\u539F\u5B50\u80FD\u673A\u6784\u901A\u62A5\u201C\u5207\u5C14\u8BFA\u8D1D\u5229\u4E8B\u6545\u201D\u7684\u8BE6\u60C5\u3002</p><p>\u57285\u5C0F\u65F6\u7684\u542C\u8BC1\u4F1A\u4E0A\uFF0C\u5217\u52A0\u7D22\u592B\u9662\u58EB\u4EE5\u4E13\u4E1A\u548C\u8BDA\u6073\u7684\u6001\u5EA6\uFF0C\u89E3\u91CA\u548C\u627F\u8BA4\u4E86\u201C\u5207\u5C14\u8BFA\u8D1D\u5229\u201D\u4E8B\u6545\u4E2D\u7684\u9519\u8BEF\uFF0C\u5F97\u5230\u56FD\u9645\u793E\u4F1A\u540C\u884C\u4EEC\u7684\u5C0A\u656C\u5E76\u4E14\u4F7F\u5F97\u82CF\u8054\u653F\u5E9C\u514D\u4E8E\u90BB\u56FD\u7684\u7D22\u8D54\u3002\u8FD9\u4E0E\u82CF\u8054\u653F\u5E9C\u5927\u591A\u6570\u5F53\u6743\u8005\u7684\u6001\u5EA6\u5F62\u6210\u4E86\u9C9C\u660E\u5BF9\u6BD4\uFF0C\u4ED6\u4EEC\u8BD5\u56FE\u6781\u529B\u63A9\u76D6\u3001\u6DE1\u5316\u707E\u96BE\u7684\u4E25\u91CD\u7A0B\u5EA6\u3002</p><p>\u5728\u82CF\u8054\u56FD\u5185\u5173\u4E8E\u4E8B\u6545\u8C03\u67E5\u7684\u6CD5\u5EAD\u8046\u8BAF\u4E2D\uFF0C\u74E6\u83B1\u91CC\xB7\u5217\u52A0\u7D22\u592B\u51FA\u4E8E\u79D1\u5B66\u5BB6\u7684\u804C\u4E1A\u64CD\u5B88\uFF0C\u4E0D\u987E\u68C0\u5BDF\u5B98\u591A\u756A\u529D\u963B\uFF0C\u5728\u6CD5\u5EAD\u4E0A\u63ED\u9732\u4E86\u82CF\u8054\u6838\u5DE5\u4E1A\u4E2D\u7684\u79CD\u79CD\u5F0A\u7AEF\u3002</p><p>\u4ED6\u7684\u8FD9\u4E00\u8BDA\u5B9E\u800C\u6B63\u4E49\u7684\u4E3E\u52A8\uFF0C\u5374\u8BA9\u4ED6\u4ECE\u529F\u81E3\u53D8\u4E3A\u201C\u7F6A\u4EBA\u201D\uFF0C\u553E\u624B\u53EF\u5F97\u7684\u56FD\u5BB6\u52CB\u7AE0\u4E5F\u5316\u4E3A\u4E4C\u6709\u3002</p><p>\u6B64\u540E\uFF0C\u4ED6\u843D\u9009\u65B0\u4E00\u5C4A\u5E93\u5C14\u6070\u6258\u592B\u539F\u5B50\u80FD\u7814\u7A76\u6240\u79D1\u5B66\u7406\u4E8B\u4F1A\uFF0C\u5E76\u4E14\u662F\u6574\u4E2A\u201C\u5207\u5C14\u8BFA\u8D1D\u5229\u201D\u6551\u63F4\u56E2\u961F\u4E2D\u552F\u4E00\u6CA1\u6709\u88AB\u6388\u4E88\u201C\u793E\u4F1A\u4E3B\u4E49\u52B3\u52A8\u82F1\u96C4\u201D\u8363\u8A89\u7684\u6210\u5458\u3002\u540C\u65F6\uFF0C\u5217\u52A0\u7D22\u592B\u9662\u58EB\u7684\u8EAB\u4F53\u5065\u5EB7\u4E5F\u6025\u5267\u6076\u5316\uFF0C\u5DF2\u7ECF\u65E0\u6CD5\u6B63\u5E38\u8FDB\u98DF\uFF0C\u5E76\u4E14\u6BCF\u5929\u9700\u8981\u670D\u7528\u5927\u91CF\u5B89\u7720\u836F\u624D\u80FD\u5165\u7761\u3002</p><p>1988\u5E744\u670827\u65E5\uFF0C\u5207\u5C14\u8BFA\u8D1D\u5229\u4E8B\u6545\u4E24\u5468\u5E74\u7EAA\u5FF5\u65E5\u7684\u7B2C\u4E8C\u5929\uFF0C\u74E6\u83B1\u91CC\xB7\u5217\u52A0\u7D22\u592B\u9662\u58EB\u5728\u83AB\u65AF\u79D1\u7684\u516C\u5BD3\u4E2D\u81EA\u7F22\u8EAB\u4EA1\u3002</p><p>\u6B7B\u524D\uFF0C\u4ED6\u5E76\u6CA1\u6709\u7559\u4E0B\u4EFB\u4F55\u9057\u4E66\uFF0C\u4F46\u7559\u4E0B\u4E86\u7CFB\u5217\u5173\u4E8E\u201C\u5207\u5C14\u8BFA\u8D1D\u5229\u4E8B\u6545\u201D\u8C03\u67E5\u62A5\u544A\u7684\u5F55\u97F3\u5E26\u3002</p><p>\u5728\u5F55\u97F3\u5E26\u7684\u5F00\u5934\uFF0C\u5217\u52A0\u7D22\u592B\u9662\u58EB\u5982\u6B64\u8BF4\u9053\uFF1A</p><blockquote><p>\u201C\u8C0E\u8A00\u6709\u4F55\u540E\u679C\uFF1F\u5E76\u975E\u662F\u6211\u4EEC\u4F1A\u628A\u8C0E\u8A00\u8BEF\u8BA4\u4E3A\u771F\u76F8\uFF0C\u771F\u6B63\u5371\u9669\u7684\u662F\uFF1A\u6211\u4EEC\u542C\u591A\u4E86\u8C0E\u8A00\u4FBF\u4E0D\u518D\u80FD\u5206\u8FA8\u51FA\u771F\u76F8\u3002\u90A3\u65F6\u6211\u4EEC\u80FD\u505A\u4EC0\u4E48\uFF1F\u9664\u4E86\u653E\u5F03\u5BF9\u771F\u7406\u7684\u4FE1\u4EF0\uFF0C\u6EE1\u8DB3\u4E8E\u8C0E\u8A00\u5806\u780C\u7684\u6545\u4E8B\uFF0C\u6211\u4EEC\u8FD8\u5269\u4E0B\u4EC0\u4E48\uFF1F\u201D</p></blockquote><p>\u5728\u5F55\u97F3\u5E26\u4E2D\uFF0C\u5217\u52A0\u7D22\u592B\u9662\u58EB\u5BF9\u82CF\u8054\u653F\u5E9C\u8BD5\u56FE\u63A9\u76D6\u707E\u96BE\u4FE1\u606F\uFF0C\u89C6\u82CF\u8054\u4EBA\u6C11\u7684\u751F\u547D\u5982\u8349\u82A5\uFF0C\u6574\u4E2A\u5B98\u50DA\u4F53\u7CFB\u9EBB\u6728\u3001\u5931\u8D23\u800C\u8FDF\u949D\uFF0C\u8868\u8FBE\u4E86\u6DF1\u6DF1\u7684\u5931\u671B\u4E4B\u60C5\u3002</p><p>\u5173\u4E8E\u4ED6\u81EA\u6740\u7684\u539F\u56E0\uFF0C\u6709\u4EBA\u8BA4\u4E3A\u5217\u52A0\u7D22\u592B\u9662\u58EB\u5BF9\u6CA1\u6709\u88AB\u6388\u4E88\u82CF\u8054\u201C\u793E\u4F1A\u4E3B\u4E49\u52B3\u52A8\u82F1\u96C4\u201D\u79F0\u53F7\u7279\u522B\u5931\u671B\u3002\u4ED6\u662F\u6574\u4E2A\u6551\u707E\u56E2\u961F\u4E2D\u6700\u6709\u8D44\u683C\u4EAB\u6709\u8FD9\u4E00\u8363\u8A89\uFF0C\u5374\u4E5F\u662F\u552F\u4E00\u4E00\u4E2A\u6CA1\u6709\u88AB\u6388\u4E88\u8BE5\u56FD\u5BB6\u8363\u8A89\u7684\u4EBA\u3002\u4E5F\u6709\u4EBA\u8BA4\u4E3A\uFF0C\u5217\u52A0\u7D22\u592B\u4E3A\u5207\u5C14\u8BFA\u8D1D\u5229\u4E8B\u6545\u9020\u6210\u4EBA\u6C11\u7684\u75DB\u82E6\u800C\u81EA\u8D23\u3002</p><p>\u901A\u8FC7\u8FD9\u573A\u4E8B\u6545\uFF0C\u82CF\u8054\u4EBA\u6C11\u53D1\u73B0\uFF0C\u4ED6\u4EEC\u4E00\u76F4\u88AB\u5145\u65A5\u7740\u5404\u79CD\u653F\u6CBB\u8C0E\u8A00\u548C\u6F20\u89C6\u6C11\u4F17\u751F\u547D\u5065\u5EB7\u7684\u6743\u529B\u96C6\u56E2\u6240\u8499\u853D\u3002\u4ED6\u4EEC\u53D8\u5F97\u4E0D\u518D\u4FE1\u4EFB\u201C\u8001\u5927\u54E5\u201D\uFF0C\u4FE1\u5FF5\u4E5F\u5F00\u59CB\u5FEB\u901F\u5D29\u6E83\u3002</p><p>\u5728\u8FD9\u573A\u707E\u96BE\u4E2D\uFF0C\u82CF\u8054\u4EBA\u6C11\u8FD8\u662F\u82CF\u8054\u7684\u4F1F\u5927\u4EBA\u6C11\uFF0C\u53EF\u82CF\u8054\u65E9\u5DF2\u7ECF\u4E0D\u662F\u4EBA\u6C11\u7684\u82CF\u8054\u3002</p><p><strong>\u7B2C\u4E09\u6279\u82CF\u8054\u82F1\u96C4\uFF1A\u90A3\u4E9B\u4E3B\u52A8\u53C2\u52A0\u5FC5\u6B7B\u4EFB\u52A1\u7684\u82CF\u8054\u4EBA\u6C11</strong></p><p>5\u67082\u65E5\uFF0C\u82CF\u8054\u5F53\u5C40\u51B3\u5B9A\u64A4\u79BB\u65B9\u570630km\u5185\u7684\u5C45\u6C11\uFF0C\u5E76\u8BBE\u7ACB\u9694\u79BB\u533A\u3002\u6B64\u65F6\uFF0C\u6838\u7535\u7AD9\u5185\u51FA\u73B0\u4E86\u66F4\u52A0\u4E25\u91CD\u7684\u9669\u60C5\u3002</p><p>\u53CD\u5E94\u5806\u7684\u5927\u706B\u5BFC\u81F44\u53F7\u673A\u7EC4\u5185\u90E8\u4EA7\u751F\u4E86\u4E0A\u5343\u5428\u7684\u6E29\u5EA6\u9AD8\u8FBE1200\u5EA6\u7684\u5806\u82AF\u7194\u878D\u7269\u3002\u7194\u878D\u7269\u878D\u5316\u4E86\u706D\u706B\u7684\u6C99\u5B50\uFF0C\u50CF\u5CA9\u6D46\u822C\u56DB\u5904\u6D41\u6DCC\uFF0C\u4E00\u65E6\u70E7\u7A7F\u5E95\u5C42\u697C\u677F\uFF0C\u9047\u4E0A\u5730\u4E0B\u5BA4\u6C34\u6C60\u4E2D\u7684\u51B7\u5374\u6C34\uFF0C\u4EA7\u751F\u7684\u5DE8\u5927\u84B8\u6C7D\u4F1A\u9020\u6210\u53E6\u4E00\u6B21\u66F4\u4E25\u91CD\u7684\u7206\u70B8\u3002</p><p>\u540E\u679C\u4F1A\u6709\u591A\u4E25\u91CD\u5462\uFF1F\u5207\u5C14\u8BFA\u8D1D\u5229\u6838\u7535\u7AD9\u5C06\u4F1A\u5937\u4E3A\u5E73\u5730\uFF0C\u5176\u4F59\u4E09\u4E2A\u6838\u53CD\u5E94\u5806\u4E5F\u4F1A\u7206\u70B8\u3002\u7206\u70B8\u4EA7\u751F\u7684\u51B2\u51FB\u6CE2\u5C06\u6CE2\u53CA200\u516C\u91CC\uFF0C\u5269\u4F59\u7684\u6838\u8F90\u5C04\u539F\u6599\u5C06\u6269\u6563\u81F3\u6574\u4E2A\u6B27\u6D32\u5927\u9646\uFF0C\u4EA7\u751F\u4E0D\u582A\u8BBE\u60F3\u7684\u6BC1\u706D\u6027\u540E\u679C\u3002</p><p>\u552F\u4E00\u7684\u89E3\u51B3\u529E\u6CD5\u5C31\u662F\u6D3E\u4E09\u4E2A\u719F\u6089\u6838\u7535\u5382\u6C34\u7BA1\u6784\u9020\u7684\u5DE5\u4EBA\u8FDB\u5165\u6C34\u6C60\uFF0C\u6253\u5F00\u6C34\u6C60\u5E95\u90E8\u7684\u9600\u95E8\uFF0C\u8BA9\u51B7\u5374\u6C34\u6392\u51FA\u3002</p><p>\u8FD9\u662F\u4E00\u6B21\u5FC5\u6B7B\u7684\u4EFB\u52A1\uFF0C\u6240\u6709\u4EBA\u90FD\u6E05\u695A\u8FD9\u4E00\u70B9\u3002</p><p>\u65F6\u4EFB\u82CF\u8054\u90E8\u957F\u4F1A\u8BAE\u526F\u4E3B\u5E2D\u7684\u9C8D\u91CC\u65AF\xB7\u8C22\u5C14\u6BD4\u7EB3\u5BF9\u5DE5\u4EBA\u4EEC\u8BF4\u9053\uFF1A</p><blockquote><p>\u201C\u4F60\u4EEC\u8981\u4E0B\u6C34\uFF0C\u662F\u56E0\u4E3A\u52BF\u5728\u5FC5\u884C\uFF0C\u4E0D\u7136\u5C31\u4F1A\u6709\u6210\u767E\u4E0A\u5343\u4E07\u7684\u4EBA\u6B7B\u53BB\u3002\u6211\u4E0D\u76F8\u4FE1\u8FD9\u6837\u7684\u7406\u7531\u8FD8\u4E0D\u8DB3\u591F\u3002\u6211\u4EEC\u7684\u8840\u6DB2\u4E2D\u6D41\u6DCC\u7740\u4E0E\u751F\u4FF1\u6765\u7684\u727A\u7272\uFF0C\u6240\u4EE5\u6211\u4EEC\u624D\u5F3A\u5927\u3002\u6BCF\u4E00\u4E2A\u65F6\u4EE3\u90FD\u6709\u8981\u627F\u53D7\u7684\u82E6\u96BE\uFF0C\u6211\u553E\u5F03\u9020\u6210\u8FD9\u8D77\u4E8B\u6545\u7684\u4EBA\uFF0C\u6211\u8BC5\u5492\u6211\u4EEC\u4E0D\u5F97\u4E0D\u4ED8\u51FA\u7684\u4EE3\u4EF7\u2026\u2026\u4F46\u6211\u5DF2\u7ECF\u9009\u62E9\u63A5\u53D7\uFF0C\u73B0\u5728\u6362\u4F60\u4EEC\u4E86\u3002\u201D</p></blockquote><p>\u4E09\u4F4D\u5DE5\u4EBA\u7AD9\u4E86\u51FA\u6765\u3002</p><p>\u4ED6\u4EEC\u662F\u503C\u73ED\u957F\u9C8D\u91CC\u65AF\xB7\u5DF4\u62C9\u8BFA\u592B\uFF0C2\u53F7\u6DA1\u8F6E\u95F4\u9AD8\u7EA7\u5DE5\u7A0B\u5E08\u74E6\u5217\u91CC\xB7\u522B\u65AF\u5E15\u7F57\u592B\u548C2\u53F7\u53CD\u5E94\u5806\u9AD8\u7EA7\u673A\u68B0\u5DE5\u7A0B\u5E08\u963F\u5217\u514B\u8C22\xB7\u963F\u7EB3\u5E74\u79D1\u3002</p><p>\u6CA1\u6709\u4EFB\u4F55\u4FA5\u5E78\u5FC3\u7406\uFF0C\u6CA1\u6709\u81EA\u6B3A\u6B3A\u4EBA\uFF0C\u53EA\u6709\u8BA4\u6E05\u73B0\u5B9E\u540E\uFF0C\u5E73\u9759\u5730\u53BB\u63A5\u53D7\u3002</p><p>\u5F53\u4ED6\u4EEC\u7AD9\u51FA\u6765\u7684\u90A3\u4E00\u523B\uFF0C\u5C31\u6CE8\u5B9A\u8981\u627F\u53D7\u65E0\u5C3D\u7684\u82E6\u96BE\uFF0C\u627F\u62C5\u5DE8\u5927\u7684\u8D23\u4EFB\uFF0C\u4E14\u4F1A\u4EE5\u5F02\u5E38\u60B2\u58EE\u7684\u65B9\u5F0F\u6B7B\u53BB\u3002</p><p>\u53EF\u6BCF\u4E2A\u65F6\u4EE3\u90FD\u9700\u8981\u8FD9\u6837\u7684\u82F1\u96C4\u3002</p><p>\u6700\u7EC8\u4ED6\u4EEC\u4EE5\u81EA\u6211\u727A\u7272\u7684\u65B9\u5F0F\uFF0C\u7EC8\u4E8E\u5B8C\u6210\u4E86\u4EFB\u52A1\u3002</p><p>\u4E8B\u540E\uFF0C\u4EBA\u6C11\u4F1A\u6B4C\u9882\u4ED6\u4EEC\u7684\u82F1\u96C4\u4E8B\u8FF9\uFF0C\u4F1A\u8BB0\u4F4F\u4ED6\u4EEC\u90A3\u4E00\u523B\u7684\u5149\u8F89\u5F62\u8C61\uFF0C\u800C\u4ED6\u4EEC\u4E5F\u5C06\u7528\u5728\u75C5\u5E8A\u75DB\u82E6\u5730\u7B49\u5F85\u6B7B\u4EA1\u7684\u65B9\u5F0F\uFF0C\u6765\u9ED8\u9ED8\u66FF\u8FD9\u4E2A\u6C11\u65CF\u627F\u53D7\u82E6\u96BE\uFF0C\u66FF\u8FD9\u4E2A\u56FD\u5BB6\u8D1F\u91CD\u524D\u884C\u3002</p><p>\u5728\u8FD9\u8D77\u4E8B\u6545\u4E2D\uFF0C\u4F5C\u51FA\u5DE8\u5927\u727A\u7272\u7684\u82CF\u8054\u4EBA\u6C11\uFF0C\u4E5F\u8FDC\u8FDC\u4E0D\u6B62\u8FD9\u4E09\u4EBA\u3002\u6316\u6398\u5730\u4E0B\u901A\u9053\uFF0C\u6CF5\u5165\u6DB2\u6C2E\u51B7\u5374\u5730\u9762\u7684\u7164\u70AD\u5DE5\u4EBA\uFF1B3238\u540D\u5E73\u5747\u5E74\u9F84\u4E0D\u523025\u5C81\uFF0C\u6E05\u7406\u5C4B\u9876\u653E\u5C04\u6027\u77F3\u58A8\u70EF\u7684\u58EB\u5175\u2026\u2026</p><p>\u4ED6\u4EEC\u540C\u6837\u662F\u7236\u6BCD\u7684\u513F\u5B50\uFF0C\u59BB\u5B50\u7684\u4E08\u592B\uFF0C\u5B69\u5B50\u7684\u7236\u4EB2\uFF0C\u4F46\u662F\u5927\u591A\u6570\u4EBA\u6700\u7EC8\u4E0D\u5F97\u4E0D\u727A\u7272\uFF0C\u53D8\u6210\u4E8B\u6545\u6B7B\u4EA1\u540D\u5355\u3001\u4F24\u6B8B\u540D\u5355\u4E0A\u4E00\u4E2A\u4E2A\u51B0\u51B7\u7684\u6570\u5B57\u3002</p><p><strong>\u540E\u7EED\u51E0\u5341\u4E07\u5FD7\u613F\u8005</strong></p><p>1986\u5E74\u548C87 \u5E74\uFF0C\u5728\u4E8B\u6545\u7684\u540E\u7EED\u5904\u7406\u9636\u6BB5\uFF0C\u82CF\u8054\u5F53\u5C40\u5171\u7EC4\u7EC7\u4E86\u7EA6 24 \u4E07\u4EBA\u53C2\u52A0\u5207\u5C14\u8BFA\u8D1D\u5229\u4E8B\u6545\u7684\u540E\u7EED\u6E05\u7406\u5DE5\u4F5C\u3002</p><p>\u6838\u7535\u7AD9\u65B9\u5706100\u516C\u91CC\u7684\u5730\u8868\u571F\u5730\u5168\u90E8\u88AB\u94F2\u9664\uFF0C\u5E76\u91CD\u65B0\u7528\u6CE5\u571F\u8986\u76D6\uFF0C\u5B8C\u6210\u8FD9\u4E2A\u6E05\u7406\u5DE5\u4F5C\u4F1A\u727A\u7272\u51E0\u4E07\u4EBA\u7684\u751F\u547D\u3002</p><p>\u540E\u6765\u4ED6\u4EEC\u7684\u7ED3\u5C40\uFF1A</p><blockquote><p>\u201C\u4ED6\u4EEC\u4E2D\u7684\u5927\u591A\u6570\u4EBA\u90FD\u5F97\u4E86\u91CD\u75C5\uFF0C\u843D\u4E0B\u4E86\u6B8B\u75BE\uFF0C\u4F46\u6CA1\u6709\u653E\u5F03\u5DE5\u4F5C\uFF0C\u60F3\u90FD\u4E0D\u6562\u60F3\u3002\u4ED6\u4EEC\u6CA1\u6709\u9664\u4E86\u53CD\u5E94\u5806\u4E4B\u5916\u7684\u751F\u6D3B\u2014\u2014\u53CD\u5E94\u5806\u5C31\u662F\u4ED6\u4EEC\u7684\u751F\u6D3B\u3002\u4ECA\u5929\u5728\u5176\u4ED6\u5730\u65B9\uFF0C\u8FD8\u6709\u8C01\uFF0C\u8FD8\u6709\u4EC0\u4E48\u5355\u4F4D\u9700\u8981\u4ED6\u4EEC\u5462\uFF1F\u201D</p></blockquote><p>\u201C\u6B7B\u4EA1\u7ECF\u5E38\u53D1\u751F\uFF0C\u6B7B\u4EA1\u5C31\u5728\u5239\u90A3\u95F4\u3002\u4ED6\u4EEC\u5728\u4E0D\u77E5\u4E0D\u89C9\u4E2D\u6B7B\u53BB\u2014\u2014\u8D70\u7740\u8D70\u7740\u5C31\u5012\u4E0B\u4E86\uFF0C\u7761\u7740\u4E86\u4FBF\u518D\u4E5F\u6CA1\u6709\u9192\u6765\uFF0C\u53BB\u7ED9\u62A4\u58EB\u9001\u82B1\uFF0C\u5FC3\u810F\u5C31\u4E0D\u8DF3\u4E86\u3002\u4ED6\u4EEC\u6B63\u5728\u727A\u7272\uFF0C\u5374\u6CA1\u4EBA\u771F\u6B63\u5728\u610F\u3002\u4EBA\u4EEC\u4E0D\u60F3\u503E\u542C\u6B7B\u4EA1\uFF0C\u4E0D\u60F3\u503E\u542C\u6050\u60E7\u2026\u2026\u201D</p><p>\u4ED6\u4EEC\u88AB\u79F0\u4F5C\u5FD7\u613F\u8005\uFF0C\u4ED6\u4EEC\u4E5F\u90FD\u8BA4\u4E3A\uFF0C\u8FD9\u662F\u4E00\u4E2A\u5D07\u9AD8\u7684\u4EFB\u52A1\u3002\u5F53\u4ED6\u4EEC\u7ED3\u675F\u5DE5\u4F5C\uFF0C\u56DE\u5230\u5BB6\u4E4B\u540E\uFF0C\u81EA\u5DF1\u8FD8\u80FD\u6D3B\u51E0\u5E74\uFF0C\u5C31\u662F\u4ED6\u4EEC\u5728\u4F11\u606F\u65F6\u95F2\u8C08\u7684\u8BDD\u9898\u3002</p><p>\u5927\u707E\u96BE\u4E2D\u7684\u4F1F\u5927\u82CF\u8054\u4EBA\u6C11\uFF0C\u4F3C\u4E4E\u4E00\u4E2A\u4E2A\u90FD\u5316\u8EAB\u4E3A\u65E0\u540D\u7684\u201C\u4FDD\u5C14\xB7\u67EF\u5BDF\u91D1\u201D\uFF0C\u7528\u751F\u547D\u548C\u9C9C\u8840\u5728\u544A\u8BC9\u4E16\u4EBA\u201C\u94A2\u94C1\u662F\u600E\u6837\u70BC\u6210\u7684\u201D\u3002</p><p><strong>\u7279\u6B8A\u7684\u82CF\u8054\u82F1\u96C4\uFF1A\u6570\u4EE5\u767E\u4E07\u8BA1\u7684\u52A8\u7269</strong></p><p>\u5207\u5C14\u8BFA\u8D1D\u5229\u4E8B\u6545\uFF0C\u4E5F\u7ED9\u52A8\u7269\u4EEC\u5E26\u6765\u4E86\u65E0\u5C3D\u7684\u707E\u96BE\u3002</p><p>\u4EBA\u7C7B\u8FD8\u80FD\u81EA\u6211\u62EF\u6551\uFF0C\u4F46\u662F\u52A8\u7269\u4EEC\u4E0D\u80FD\u3002\u5728\u65E0\u6570\u4E0D\u4E3A\u4EBA\u77E5\u7684\u9634\u6697\u89D2\u843D\uFF0C\u5B83\u4EEC\u6C38\u8FDC\u4E5F\u4E0D\u77E5\u9053\u5230\u5E95\u53D1\u751F\u4E86\u4EC0\u4E48\uFF0C\u53EA\u77E5\u9053\u81EA\u5DF1\u7684\u540C\u4F34\uFF0C\u5C31\u8FD9\u6837\u4E00\u53EA\u53EA\u60B2\u60E8\u5730\u6B7B\u53BB\u3002</p><p>\u5F53\u4EBA\u4EEC\u64A4\u79BB\u4E4B\u540E\uFF0C\u58EB\u5175\u548C\u730E\u624B\u5F00\u8FDB\u6751\u5E84\uFF0C\u5C04\u6740\u4E86\u6240\u6709\u8FD8\u80FD\u6D3B\u52A8\u7684\u52A8\u7269\u3002</p><p>\u72D7\u3001\u732B\u3001\u9A6C\u3001\u725B\u3001\u7F8A\u2026\u2026\u5B83\u4EEC\u4EC0\u4E48\u90FD\u4E0D\u660E\u767D\uFF0C\u5B83\u4EEC\u6BEB\u65E0\u8FC7\u9519\uFF0C\u4F46\u662F\u4E3A\u4E86\u9632\u6B62\u5B83\u4EEC\u6CBE\u67D3\u7684\u8F90\u5C04\u5C18\u57C3\u53EF\u80FD\u5371\u5BB3\u5230\u4EBA\u7C7B\uFF0C\u5B83\u4EEC\u5FC5\u987B\u6B7B\u3002</p><p>\u6BCF\u5929\u90FD\u6709\u6570\u5341\u5361\u8F66\u7684\u52A8\u7269\u5C38\u4F53\u88AB\u96C6\u4E2D\u8FD0\u9001\u5230\u6307\u5B9A\u7684\u5730\u65B9\uFF0C\u57CB\u5165\u6DF1\u6DF1\u7684\u5927\u5751\u4E2D\uFF0C\u5E76\u6D47\u7B51\u4E0A\u539A\u539A\u7684\u6DF7\u51DD\u571F\u3002</p><p>\u6570\u767E\u4E07\u7684\u52A8\u7269\uFF0C\u88AB\u8FD9\u6837\u6709\u7EC4\u7EC7\u7684\u5C04\u6740\u3002\u56E0\u4E3A\u4EBA\u7C7B\u7684\u8FC7\u9519\uFF0C\u5B83\u4EEC\u6DF1\u57CB\u5728\u6DF7\u51DD\u4E2D\uFF0C\u7528\u7D2F\u7D2F\u5C38\u9AA8\u65E0\u58F0\u5730\u8BC9\u8BF4\u7740\u60B2\u60E8\u7684\u547D\u8FD0\u3002</p><p>30\u5E74\u540E\uFF0C\u5F53\u4EBA\u4EEC\u518D\u6B21\u8E0F\u4E0A\u5207\u5C14\u8BFA\u8D1D\u5229\u7684\u571F\u5730\u3002\u867D\u7136\u6838\u8F90\u5C04\u4F9D\u7136\u5F3A\u70C8\uFF0C\u4E0D\u9002\u5B9C\u4EBA\u7C7B\u5C45\u4F4F\uFF0C\u4F46\u662F\u987D\u5F3A\u5730\u5728\u6B64\u7E41\u884D\u751F\u606F\u7684\u52A8\u690D\u7269\u4EEC\uFF0C\u5374\u628A\u8FD9\u513F\u53D8\u6210\u4E86\u5B83\u4EEC\u7684\u4E4C\u6258\u90A6\u3002</p><p>\u8302\u5BC6\u8471\u90C1\u7684\u8349\u6728\u4E1B\u6797\u3001\u8272\u5F69\u6591\u6593\u7684\u9C9C\u82B1\u3001\u6C34\u679C\u3001\u6606\u866B\uFF0C\u65E0\u6570\u52A8\u7269\u4EEC\u5728\u6B64\u5B89\u5BB6\u3002</p><p>\u867D\u7136\u6709\u4EBA\u53D1\u73B0\uFF0C\u4E00\u4E9B\u52A8\u7269\u4EA7\u751F\u4E86\u57FA\u56E0\u53D8\u5F02\uFF0C\u4F46\u65E0\u8BBA\u5982\u4F55\uFF0C\u8FDC\u79BB\u4EBA\u7C7B\u7684\u6253\u6270\uFF0C\u8FD9\u513F\u5DF2\u7ECF\u6210\u4E86\u4E00\u4E2A\u52A8\u7269\u7684\u5929\u5802\u3002</p><p>\u5E9F\u5F03\u9AD8\u697C\u3001\u5DE5\u5382\u3001\u6C11\u5C45\u7684\u5E9F\u589F\u4E4B\u95F4\uFF0C\u968F\u7740\u4EBA\u7C7B\u7684\u6D88\u5931\uFF0C\u5927\u81EA\u7136\u8FC5\u901F\u63A5\u7BA1\u4E86\u5B83\uFF0C\u5E76\u7528\u5B83\u7684\u667A\u6167\u4E3A\u8FD9\u7247\u571F\u5730\u629A\u5E73\u4F24\u75D5\u3002\u65E0\u6570\u751F\u547D\u7684\u91CD\u65B0\u7EFD\u653E\uFF0C\u8BA9\u8FD9\u5EA7\u4EBA\u7C7B\u7684\u201C\u9B3C\u57CE\u201D\u6563\u53D1\u51FA\u4E00\u79CD\u5996\u5F02\u7684\u201C\u751F\u547D\u4E4B\u7F8E\u201D\u3002</p><p>\u5E0C\u671B\u4EBA\u7C7B\u4E0D\u8981\u518D\u53BB\u5F00\u53D1\u90A3\u7247\u571F\u5730\uFF0C\u8BA9\u5B83\u6C38\u8FDC\u6210\u4E3A\u52A8\u7269\u4EEC\u7684\u4E4C\u6258\u90A6\u5427\u3002</p><p><strong>\u540E\u8BB0</strong></p><p>1991\u5E7412\u670825\u65E5\uFF0C\u82CF\u8054\u56FD\u65D7\u4ECE\u514B\u91CC\u59C6\u6797\u5BAB\u4E0A\u7A7A\u7F13\u7F13\u964D\u4E0B\uFF0C\u66FE\u7ECF\u7684\u82CF\u8054\u8D70\u5230\u4E86\u5C3D\u5934\u3002\u5207\u5C14\u8BFA\u8D1D\u5229\u4E8B\u6545\uFF0C\u5728\u82CF\u8054\u4EBA\u6C11\u7684\u8EAB\u4E0A\u5212\u4E0B\u4E00\u9053\u6DF1\u6DF1\u7684\u4F24\u53E3\uFF0C\u6572\u54CD\u4E86\u82CF\u8054\u7684\u7B2C\u4E00\u58F0\u4E27\u949F\u3002</p><p>\u82CF\u8054\u7684\u89E3\u4F53\uFF0C\u5E76\u4E0D\u503C\u5F97\u6C11\u4F17\u60CB\u60DC\u3002</p><p>\u56E0\u4E3A\u6B64\u65F6\u7684\u82CF\u8054\u65E9\u5DF2\u4E0D\u662F\u4EBA\u6C11\u7684\u82CF\u8054\uFF01</p><p>\u5F53\u82CF\u8054\u5BF9\u5916\u5949\u884C\u5927\u56FD\u6C99\u6587\u4E3B\u4E49\uFF0C\u63D0\u51FA\u8981\u5728\u4E2D\u56FD\u5EFA\u519B\u4E8B\u57FA\u5730\u5E76\u9A7B\u519B\uFF0C\u751A\u81F3\u5A01\u80C1\u7528\u539F\u5B50\u5F39\u653B\u51FB\u540C\u4E3A\u793E\u4F1A\u4E3B\u4E49\u9635\u8425\u7684\u4E2D\u56FD\uFF1B\u5BF9\u5185\u611A\u5F04\u6B3A\u9A97\u81EA\u5DF1\u7684\u4EBA\u6C11\uFF0C\u5C06\u6C11\u4F17\u751F\u547D\u89C6\u5982\u8349\u82A5\uFF0C\u7528\u7F9E\u8FB1\u548C\u653F\u6CBB\u624B\u6BB5\u5BF9\u5F85\u81EA\u5DF1\u7684\u82F1\u96C4\u65F6\uFF0C\u82CF\u8054\u4FBF\u5DF2\u7ECF\u80CC\u53DB\u4E86\u5168\u4E16\u754C\u7684\u65E0\u4EA7\u9636\u7EA7\uFF0C\u80CC\u53DB\u4E86\u81EA\u5DF1\u7684\u4EBA\u6C11\uFF0C\u80CC\u53DB\u4E86\u82F1\u7279\u7EB3\u96C4\u8010\u5C14\u3002</p><p>\u66FE\u7ECF\u7684\u82CF\u8054\u6700\u7EC8\u53EA\u6B8B\u5B58\u7740\u4E00\u526F\u6563\u53D1\u7740\u81ED\u5473\u7684\u7A7A\u58F3\uFF0C\u6CA6\u4E3A\u5C11\u6570\u6743\u529B\u9636\u5C42\u4E89\u593A\u79C1\u5229\u4E0E\u6743\u52BF\u7684\u5DE5\u5177\u3002</p><p>\u800C\u82CF\u8054\u4EBA\u6C11\u4E0E\u540C\u6837\u7ECF\u5386\u8FC7\u793E\u4F1A\u4E3B\u4E49\u6C11\u4E3B\u9769\u547D\u6D17\u793C\u7684\u4E2D\u56FD\u4EBA\u6C11\u4E00\u6837\uFF0C\u5728\u91CD\u5927\u707E\u96BE\u9762\u524D\uFF0C\u603B\u6709\u65E0\u6570\u82F1\u96C4\u7AD9\u51FA\u6765\uFF0C\u4ED6\u4EEC\u82F1\u52C7\u575A\u5F3A\uFF0C\u4ED6\u4EEC\u52C7\u4E8E\u727A\u7272\uFF0C\u4ED6\u4EEC\u4E3A\u4E86\u56FD\u5BB6\u548C\u4EBA\u6C11\u7684\u5229\u76CA\u6562\u4E8E\u5949\u732E\u4E00\u5207\u3002</p><p>\u81F4\u656C\u82CF\u8054\u4F1F\u5927\u7684\u4EBA\u6C11\u548C\u4ED6\u4EEC\u8C31\u5199\u7684\u90A3\u4E9B\u53EF\u6B4C\u53EF\u6CE3\u7684\u6545\u4E8B\uFF01</p>",165);function d(b,k){const t=a("ExternalLinkIcon");return r(),n("div",null,[_,g,h,p("p",null,[o("1986\u5E74\u53D1\u751F\u7684"),p("a",u,[o("\u5207\u5C14\u8BFA\u8D1D\u5229\u6838\u6CC4\u6F0F"),s(t)]),o("\u662F\u4EBA\u7C7B\u5386\u53F2\u4E0A\uFF0C\u81F3\u4ECA\u4E3A\u6B62\u9020\u6210\u540E\u679C\u6700\u4E25\u91CD\uFF0C\u5F71\u54CD\u6700\u6DF1\u8FDC\u7684\u4E00\u573A\u6838\u6CC4\u6F0F\u4E8B\u6545\u3002\u4E8B\u6545\u9020\u6210\u7684\u76F4\u63A5\u548C\u95F4\u63A5\u7ECF\u6D4E\u635F\u5931\u9AD8\u8FBE\u4E24\u5343\u4EBF\u7F8E\u5143\u4EE5\u4E0A\u3002")]),i])}const m=e(l,[["render",d],["__file","chernobyl.html.vue"]]);export{m as default};
